INSERT INTO `broadcast_text_locale` VALUES ('1', 'deDE', 'Hilfe, Hilfe! Ich werde bedrängt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3', 'deDE', 'Kompanie... HALT!... Angetreten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4', 'deDE', 'Vorwärts... MARSCH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5', 'deDE', 'Kommt, vielleicht können wir bei einer Schlägerei mitmischen, wie zivilisierte Leute.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('7', 'deDE', 'Habt vielen Dank!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9', 'deDE', 'Folgt mir, $N. Ich bringe Euch zum Versteck der Defias. Aber Ihr solltet mich beschützen, denn sonst bin ich so gut wie tot.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10', 'deDE', 'Der Eingang ist hier in Moonbrook versteckt. Haltet Ausschau nach Dieben. Sie wollen meinen Tod.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11', 'deDE', 'Sagt Stoutmantle, dass sich die Bande der Defias hier verkrochen hat, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13', 'deDE', 'An die Arbeit, Abschaum!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('14', 'deDE', 'Grrrr! Idioten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('15', 'deDE', 'Nichtsnutzige Kobolde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('16', 'deDE', 'In Ordnung Leute, macht einen lebendigen Eindruck!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('17', 'deDE', 'Nun gut, Leute! Zeit aufzustehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('19', 'deDE', 'He, folgt mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('22', 'deDE', 'Noch eine Herde erfolgreich zurückgebracht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('23', 'deDE', 'Nun, ich bin hier. Und ich bin bereit, wieder einzutreten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('24', 'deDE', 'Ihr werdet Euch niemandem anschließen, Thorsen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('25', 'deDE', '$N, meine Wunden sind tief. Vergebt mir, dass ich so langsam bin, aber ich bin zu schwer verletzt, um schneller zu gehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('26', 'deDE', 'Ah, endlich frische Luft! Ich muss mich einen Moment ausruhen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('27', 'deDE', 'Die Blackrockplage hat sich in dieser Gegend stark ausgebreitet. Ich werde mein Bestes tun, um das Tempo zu halten, $N. Los, gehen wir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('28', 'deDE', 'So nah am gesegneten Seenhain. Möge das Licht mir beistehen, damit mich meine Beine noch ein wenig weiter tragen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('29', 'deDE', 'Marshal Marris, Sire. Korporal Keeshan vom 12. Säbelregiment zurück vom Kampf und meldet sich zum Dienst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('30', 'deDE', 'Ihr seid sehr tapfer, $C. Habt Dank für meine Rettung! Ich bin sicher, Marshal Marris wird Eure gute Tat belohnen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('33', 'deDE', 'Antonio Perellis Zauberelixiere zu verkaufen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('35', 'deDE', 'Kommt! Kommt alle! Perellis Elixiere bringen Euch zum Staunen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('36', 'deDE', 'Steinmetze... grr... Seid gewarnt, Defias: Heute Nacht sinkt der rostige Anker.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('37', 'deDE', 'Der Boss will, dass alle äußerst wachsam bleiben. Heute Nacht sinkt der rostige Anker.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('38', 'deDE', 'Ich bin wegen des Goldes hier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('39', 'deDE', 'Wohin ich auch schaue, sehe ich Diebe. Wir, die Volksmiliz, müssen dieses Land den Bauern zurückgeben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('40', 'deDE', 'Die Höfe müssen befreit werden. Sturmwind mag diese Leute ja im Stich gelassen haben, aber ich will verflucht sein, wenn ich das auch tue.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('41', 'deDE', null, 'Es wurde von Aktivitäten der Defias am Küstenhorizont berichtet. An der Späherkuppe nichts Außergewöhnliches.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('42', 'deDE', 'Es wird von reger Gnollaktivität im Goldküstensteinbruch und im Jangoschacht berichtet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('43', 'deDE', 'An der Späherkuppe ist alles klar, Lord Stoutmantle. Aber aus Moonbrook kommen Berichte über umfangreiche Bewegungen der Defias.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('44', 'deDE', 'Das Volk von Westfall wird die Fahnenflucht Sturmwinds niemals verzeihen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('45', 'deDE', 'Nichts, was beißt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('46', 'deDE', 'Hier Fischi, Fischi... kommt zu Papa.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('47', 'deDE', 'Nehmt Euch einen Stuhl und trinkt was!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('48', 'deDE', 'Die Flaschen sind staubig, doch der Schnaps reinigt die Kehle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('49', 'deDE', null, 'Hoffentlich lässt sich die Verstärkung aus Sturmwind bald blicken...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('50', 'deDE', 'Kommt Jungs, wir müssen diese Brücke wieder aufbauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('51', 'deDE', 'Wo bleibt denn nur die Verstärkung aus Sturmwind? Aus dem Osten kommen Berichte über umfangreiche Bewegungen der Blackrock.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('52', 'deDE', 'Vom 2. Bataillon aus Sturmwind ist noch immer nichts zu sehen. Aus dem Nordosten wird über Bewegungen der Schattenfellgnolle berichtet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('53', 'deDE', 'Marshal Marris erwartet jeden Augenblick die Reserve von Sturmwind...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('54', 'deDE', null, 'Keine Sorge, Graumähne, wir besorgen dir bald etwas zu essen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('55', 'deDE', null, 'Ich hätte nie gedacht, dass einmal der Tag kommen würde, an dem ich meine Heimat Westfall verlasse...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('56', 'deDE', 'Ich kann nicht glauben, dass der verdammte Wagen eine Panne hatte. In diesem Land hat man einfach kein Glück...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('57', 'deDE', 'Sobald ich diesen Wagen in Ordnung gebracht habe, verschwinden wir von hier...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('58', 'deDE', 'Wie soll ich bloß diesen mechanischen Schrott von meinen Feldern bekommen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('59', 'deDE', 'Ein Haufen Rohlinge und Diebe werden mich nicht dazu kriegen, mein Land zu verlassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('60', 'deDE', 'Sie mögen ja alle anderen Bauern vertrieben haben, doch die Saldeans werden Westfall niemals verlassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('61', 'deDE', null, 'Bleibt auf den Straßen. Im Wald wimmelt es von Untoten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('62', 'deDE', null, 'Fürchtet Euch nicht. Die Nachtwache wird das Land früh genug von dieser üblen Besudelung befreien.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('63', 'deDE', 'Untote kriechen überall durchs Land. Wo ist die Armee Sturmwinds?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('64', 'deDE', 'Die Nachtwache allein kann uns nicht ewig beschützen. Wir brauchen die Unterstützung der Armee Sturmwinds.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('65', 'deDE', 'Wir dürfen uns dem Bösen nicht ergeben, das unser Land überschattet. Die Nachtwache muss siegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('66', 'deDE', 'Im Wald wimmelt es nur so von Worgs und Ghuls. Es muss etwas geschehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('67', 'deDE', 'Wir brauchen mehr Unterstützung aus Sturmwind. Unsere Häuser fallen in die Hände der Untoten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('68', 'deDE', 'Der Rat des Dämmerwalds muss etwas unternehmen. Das Böse lauert in der Luft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('69', 'deDE', 'Das Volk von Dunkelhain erwartet mehr vom Rat. Wir können nicht zulassen, dass sie unter diesem gottlosen Zorn leiden müssen, der uns quält.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('70', 'deDE', 'Ständiges Gezänk bringt uns nicht weiter. Wir müssen etwas unternehmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('71', 'deDE', 'Die Nachrichten von Sturmwind verheißen nichts Gutes...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('72', 'deDE', 'Unsere Sache stößt hinter den dicken Steinwänden von Sturmwind auf taube Ohren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('73', 'deDE', null, 'Ich spüre, dass Böses sich zusammenbraut...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('74', 'deDE', null, 'Die Sterne sprechen zu mir. Sie flüstern mir von den Schrecken, die in der Nacht warten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('75', 'deDE', null, 'Die Zukunft hält viel Ungewissheit und Tod bereit, ähnlich wie die Vergangenheit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('76', 'deDE', null, 'Die Geister flüstern in mein Ohr. Sie sind ruhelos und voller Angst.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('77', 'deDE', 'Die Sterne werden heute Nacht die perfekte Konstellation haben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('79', 'deDE', 'Am Nachthimmel wirkt etwas viel Größeres als Magie. Eines Tages werden sie mir zuhören...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('80', 'deDE', 'Psssst. Wir haben eben etwas Fusel hereinbekommen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('81', 'deDE', 'Tretet vor und befeuchtet Eure Kehle.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('82', 'deDE', 'Diese dunkle Besudelung ist absolut schlecht fürs Geschäft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('83', 'deDE', 'Willkommen im Roten Raben. Heutzutage sollte man lieber im Haus bleiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('84', 'deDE', 'Möge Euer volles Glas nie leer werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('85', 'deDE', 'Willkommen im Gasthaus Zur Höhle des Löwen! Fühlt Euch wie zu Hause!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('86', 'deDE', 'So viel Arbeit, so viel Arbeit! Wo bleibt nur die Zeit?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('87', 'deDE', 'Wo war bloß das Buch über die Bräuche der Nachtelfen, nach dem ich gesucht habe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('88', 'deDE', 'Oh je! Seht nur, in welchem Zustand diese Bücher sind! Wer hat sie zurückgestellt, ohne sie alphabetisch zu ordnen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('89', 'deDE', 'Erhebt euch und greift zu den Waffen, Bürger von Dunkelhain! Eine Monstrosität der Untoten nähert sich auf der Straße!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('90', 'deDE', 'Die Monstrosität ist in das Lager der Nachtwache eingefallen! Schnell, wir müssen sie abfangen, bevor sie die Stadt erreicht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('91', 'deDE', 'Das ist nicht genug! Verteidiger, versammelt Euch in der Mitte der Stadt. Zusammen werden wir dem untoten Monster standhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('92', 'deDE', 'Die Monstrosität ist hier! Vorwärts!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('93', 'deDE', 'Die Bestie wurde erschlagen! Jetzt herrscht wieder Ruhe in Dunkelhain!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('94', 'deDE', 'Ich wusste nicht, dass er geheiratet hat! Ich wusste nicht einmal, dass er sich mit einem Mädchen traf! Das ist schrecklich! Jetzt kann er nachts nicht mehr arbeiten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('95', 'deDE', 'Na schön! Ihr habt die besseren Argumente, Ihr Meister der Ingenieurskunst! Macht mit der Tür, was immer Ihr wollt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('96', 'deDE', 'Das stimmt, wir sind dumm. Dumm wie ein Fuchs!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('97', 'deDE', 'Wo ist mein Speck?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('98', 'deDE', 'Ihr habt Euer eigenes Land zerstört, meines werdet ihr nicht auch noch zerstören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('99', 'deDE', '$N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('100', 'deDE', 'Danke, dass Ihr mich gerettet habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('101', 'deDE', 'Jetzt sterbt Ihr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('102', 'deDE', 'Zeit für meine Patrouille. Ich bin bald zurück.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('103', 'deDE', 'Jetzt, wo die Luft rein ist...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('104', 'deDE', 'Wer kommt da?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('105', 'deDE', 'Ich war lange Zeit fort... ich sollte lieber zurückgehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('106', 'deDE', 'Gefreiter Thorsen meldet sich von der Patrouille zurück. Nichts Neues zu berichten - da draußen ist es ziemlich ruhig...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('107', 'deDE', null, 'Es braucht mehr als einen verwesenden Kadaver, um uns aufzuhalten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('108', 'deDE', null, 'Sehr gut. Ihr seid entlassen, Gefreiter. Ruht Euch aus, aber macht es Euch nicht zu bequem. Kurzen wird uns nicht für immer in Ruhe lassen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('109', 'deDE', 'Was ist denn hier los? Rührt dieses Pulver nicht an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('110', 'deDE', 'Aber? Seid Ihr das...? Oh... Ich bin so hungrig, Aber! SO HUNGRIG!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('111', 'deDE', 'Wartet... Ihr seid nicht mein Ehemann. Aber er muss Euch geschickt haben. Und Ihr... seht... appetitlich aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('112', 'deDE', 'Mal sehen, ob ich diesmal ins Schwarze treffe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('113', 'deDE', 'Sieht so aus, als müsste ich noch etwas üben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('114', 'deDE', 's wird Zeit für meine Runde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('115', 'deDE', 'Nee, ich hab jetzt keine Zeit zum Trinken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('117', 'deDE', 'Was soll\'s! Schiebt mir ein Eisgekühltes rüber, Belm!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('118', 'deDE', 'Ahhh! Ich muss zurück an die Arbeit! Ich danke Euch, Belm.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('120', 'deDE', 'Hier bitte sehr, Eure Lieblingssorte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('121', 'deDE', 'Sagt Eurem Freund Tongus dort, wenn er hier seine Rechnung nicht begleicht, werde ich ihm mit dem alten Steelhead einen Besuch abstatten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('122', 'deDE', 'Belm sagt, Ihr sollt Eure Rechnung zahlen oder er wird Euch die Fresse polieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('123', 'deDE', 'Hoha! Der alte Schwachbauch soll nur kommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('124', 'deDE', 'Ich nehme alles, was Ihr habt! Und gebt noch eine Flasche dazu, zum Runterspülen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('125', 'deDE', 'He, Belm! Gib mir einen Becher Donnerbier und einen für meinen guten Freund $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('126', 'deDE', 'Und wie läuft das Fassbewachen...?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('127', 'deDE', 'Ah, das ist genau richtig! Ich glaube, ich hole mir noch ein paar... könnt Ihr für mich auf diese Fässer aufpassen, $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('128', 'deDE', 'Nun ja, ich muss mich wieder um die Arbeit kümmern. Aber so eine kleine Pause tut schon gut und so ein Donnerbier schmeckt immer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('129', 'deDE', 'Ich bin wieder da, wo ich angefangen habe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('130', 'deDE', 'Ich bin jetzt weit genug entfernt... dass ich das Fass nicht mehr sehen kann. Gift los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('131', 'deDE', 'Glaubt Ihr, dass da irgendwelche Fische drin sind?', 'Glaubt Ihr, dass da irgendwelche Fische drin sind?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('132', 'deDE', 'Letzte Woche habe ich einen großen gefangen, der hatte drei Augen!', 'Letzte Woche habe ich einen großen gefangen, der hatte drei Augen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('133', 'deDE', 'Mein Papa sagt, im Meer gibt es Fische, die so groß sind, dass sie ein ganzes Schiff verschlingen können.', 'Mein Papa sagt, im Meer gibt es Fische, die so groß sind, dass sie ein ganzes Schiff verschlingen können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('134', 'deDE', 'Mein Papa kann mehr Fische fangen als deiner!', 'Mein Papa kann mehr Fische fangen als deiner!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('135', 'deDE', 'Seht! Seht! Ich habe etwas gefangen! Ohhh... das ist nur ein stinkender, alter Stiefel.', 'Seht! Seht! Ich habe etwas gefangen! Ohhh... das ist nur ein stinkender, alter Stiefel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('136', 'deDE', 'Gar nicht.', 'Gar nicht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('138', 'deDE', 'Lügner!', 'Lügner!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('139', 'deDE', 'Ich hörte, dass es riesige Fische gibt, die zum Jagen an Land gehen und Leute fressen!', 'Ich hörte, dass es riesige Fische gibt, die zum Jagen an Land gehen und Leute fressen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('140', 'deDE', 'Und einmal im Lager habe ich einen Fisch gefangen, der größer war als ich selbst!!!', 'Und einmal im Lager habe ich einen Fisch gefangen, der größer war als ich selbst!!!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('141', 'deDE', 'Ich habe eine Geschichte über diesen goldenen Fisch gehört: Wenn man ihn fängt, hat man drei Wünsche frei!', 'Ich habe eine Geschichte über diesen goldenen Fisch gehört: Wenn man ihn fängt, hat man drei Wünsche frei!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('142', 'deDE', 'Wenn Ihr so gut fischen würdet, wie Ihr redet, dann gäbe es im ganzen Ozean längst keine Fische mehr.', 'Wenn Ihr so gut fischen würdet, wie Ihr redet, dann gäbe es im ganzen Ozean längst keine Fische mehr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('143', 'deDE', 'Pssst! Ihr verscheucht ja die Fische.', 'Pssst! Ihr verscheucht ja die Fische.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('144', 'deDE', 'Das habt Ihr Euch ausgedacht.', 'Das habt Ihr Euch ausgedacht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('145', 'deDE', 'Wenn Ihr einen Fisch fangen könntet, der so groß wie Euer Mundwerk ist, hätten wir für alle Zeiten genug zu essen.', 'Wenn Ihr einen Fisch fangen könntet, der so groß wie Euer Mundwerk ist, hätten wir für alle Zeiten genug zu essen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('146', 'deDE', 'Vielleicht sollten wir zur Brücke gehen und angeln.', 'Vielleicht sollten wir zur Brücke gehen und angeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('147', 'deDE', 'Wirklich?', 'Wirklich?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('149', 'deDE', 'Und das war die Geschichte, wie Lothar 36 Orcs mit bloßen Händen tötete!', 'Und das war die Geschichte, wie Lothar 36 Orcs mit bloßen Händen tötete!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('150', 'deDE', 'Und so standen die Ritter vor der angreifenden Horde und behaupteten ihre Stellung, während Tausende wütender Orcs durch das Tal nachströmten.', 'Und so standen die Ritter vor der angreifenden Horde und behaupteten ihre Stellung, während Tausende wütender Orcs durch das Tal nachströmten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('151', 'deDE', 'Es heißt, er könne sich von Zeit zu Zeit in einen Raben verwandeln.', 'Es heißt, er könne sich von Zeit zu Zeit in einen Raben verwandeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('152', 'deDE', 'Wisst Ihr, warum die Augen der Orcs rot glühen? Das liegt daran, dass sie Blut trinken!', 'Wisst Ihr, warum die Augen der Orcs rot glühen? Das liegt daran, dass sie Blut trinken!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('153', 'deDE', 'Den Löffel gibt es nicht.', 'Den Löffel gibt es nicht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('154', 'deDE', 'Ich schwöre es, man hat sie tatsächlich schon gesehen. Es gibt die Pandarenen wirklich!', 'Ich schwöre es, man hat sie tatsächlich schon gesehen. Es gibt die Pandarenen wirklich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('155', 'deDE', 'Und dann biss ihm der Hase einfach den Kopf ab... Ich schwöre es.', 'Und dann biss ihm der Hase einfach den Kopf ab... Ich schwöre es.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('156', 'deDE', 'Ihr wisst doch, dass es in den Kanälen Krokiliske gibt. Man hat sie als Tiere aus den Sümpfen mitgebracht, aber dann in die Kanäle geworfen.', 'Ihr wisst doch, dass es in den Kanälen Krokiliske gibt. Man hat sie als Tiere aus den Sümpfen mitgebracht, aber dann in die Kanäle geworfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('157', 'deDE', 'Wow.', 'Wow.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('158', 'deDE', 'Oh ja, davon hab ich gehört.', 'Oh ja, davon hab ich gehört.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('159', 'deDE', null, 'Komm zurück, Flauschie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('160', 'deDE', null, 'Warum läufst du vor mir weg, Flauschie?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('161', 'deDE', null, 'Komm her, Flauschie! Ich will dich doch nur baden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('162', 'deDE', null, 'Flauschie! Hör auf zu rennen, Flauschie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('164', 'deDE', null, 'Ein Jahr lang jeden Tag Karotten, wenn du nur endlich aufhörst!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('165', 'deDE', null, 'Zwing mich nicht, meinen Papa zu holen! Er fängt Hasen gern mit der Flinte!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('166', 'deDE', 'Aber bitte, das ist doch nicht wahr.', 'Aber bitte, das ist doch nicht wahr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('167', 'deDE', 'Klasse.', 'Klasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('168', 'deDE', 'Mein Vater sagt, das sei nur eine Mär.', 'Mein Vater sagt, das sei nur eine Mär.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('169', 'deDE', 'Ist das zu glauben?', 'Ist das zu glauben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('170', 'deDE', 'Das hört sich wohl eher wie eine von Billys Fischgeschichten an.', 'Das hört sich wohl eher wie eine von Billys Fischgeschichten an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('171', 'deDE', 'Mir war, als hätte ich was gehört.', 'Mir war, als hätte ich was gehört.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('172', 'deDE', 'Ich glaube, ich sehe da was.', 'Ich glaube, ich sehe da was.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('173', 'deDE', 'Iiieeh... das ist kein Fisch!', 'Iiieeh... das ist kein Fisch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('174', 'deDE', 'Ich habe Wurmgedärm auf meinen Schuhen.', 'Ich habe Wurmgedärm auf meinen Schuhen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('175', 'deDE', 'Irgendwas riecht eigenartig.', 'Irgendwas riecht eigenartig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('176', 'deDE', 'Ich hoffe, das war ein Fisch!', 'Ich hoffe, das war ein Fisch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('177', 'deDE', 'Ich bezweifle, dass es in diesen Kanälen Fische gibt.', 'Ich bezweifle, dass es in diesen Kanälen Fische gibt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('178', 'deDE', 'Wurm kommt an den Haken, Haken kommt ins Wasser. Fisch ist im Wasser, unser Fisch.', 'Wurm kommt an den Haken, Haken kommt ins Wasser. Fisch ist im Wasser, unser Fisch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('179', 'deDE', 'Hättet Ihr \'ne Münze übrig?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('181', 'deDE', 'Almosen für die Armen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('182', 'deDE', 'Gebt Ihr mir einen aus?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('183', 'deDE', 'Helft Ihr \'nem armen Kerl?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('184', 'deDE', 'Habt Ihr ein paar Münzen für einen Blinden...? Was meint Ihr damit, ich wäre nicht blind...? ICH BIN NICHT BLIND! ICH KANN SEHEN!!! Ein Wunder!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('185', 'deDE', 'Ich polier Eure Rüstung für \'n Stück Kupfer.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('186', 'deDE', 'Das ist alles nur die Schuld dieser dämlichen Armee der Allianz. Die mussten ihre Türme ja auch genau hinter meinem Hof errichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('187', 'deDE', 'Das ist alles nur die Schuld dieser dämlichen Orcs. Mussten die denn meinen Hof bis auf die Grundmauern niederbrennen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('188', 'deDE', 'Habt Ihr das gehört? Ich glaube, sie kommen näher...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('189', 'deDE', 'Wachdienst! Ich hasse Wachdienst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('190', 'deDE', 'Ich hörte, alle Kobolde, die sich dort unten befanden, seien tot.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('192', 'deDE', 'Tut mir leid, Huldar... Aber anscheinend habe ich Euch in die Irre geführt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('194', 'deDE', 'Dunkeleisenaktivitäten. Es wurden sogar einige Schiffsladungen gekapert...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('195', 'deDE', 'Ich bin sicher, dass an der Unfähigkeit oder Unachtsamkeit einiger Tagelöhner liegt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('197', 'deDE', 'Seid gegrüßt, $N! Verteidiger des Volkes!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('198', 'deDE', 'Das Volk von Westfall salutiert vor $N, einem mutigen und tapferen Verteidiger der Freiheit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('199', 'deDE', 'Ein dreifaches Hoch auf $N! Dieses Land wird wieder uns gehören!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('200', 'deDE', 'Habt Dank für das Fässlein Schnaps, $N. Ich glaube, ich genehmige mir gleich ein Gläschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('201', 'deDE', null, 'Wenn wir die Essenzströme umkehren, können wir vielleicht auch die Polarität ändern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('203', 'deDE', null, 'Die Magiefelder würden sich dann also in eine Kettenreaktion positiv aufgeladener Energien verwandeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('204', 'deDE', null, 'Das Portal würde sich leicht verlagern. Wenn Ihr zu diesem Zeitpunkt einen Bindezauberspruch sprecht, könnt Ihr es an Ort und Stelle verfestigen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('205', 'deDE', null, 'Wenn die positiv ausgerichteten Energien mit den negativ aufgeladenen Energien kollidieren, heben sie einander nicht auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('206', 'deDE', null, 'Maginor sagt, dass Wechselströme positiver Energie in der Tat einen viel stabileren Fluss erzeugen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('207', 'deDE', null, 'Die magischen Schutzzauber sollten dann durch den Energiestrom so weit aufgeladen sein, dass sie die Entität im Zaum halten können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('208', 'deDE', null, 'Warum können wir das Feld nicht einfach verlagern, um die Abweichung im Fluxus zu kompensieren?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('209', 'deDE', null, 'Zwölf Fokuszentren einzubringen könnte es den magischen Energien erlauben, sich stabiler zu formen, da sie die notwendige Stabilität einbringen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('210', 'deDE', null, 'Aber was passiert, wenn die resultierende Frequenzverschiebung eine magische Rückkopplung durch die Ströme schickt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('211', 'deDE', null, 'Würde das nicht bewirken, dass die Bindung sich löst, wenn sie nicht vor Beginn des Zauberns richtig verankert wurde?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('212', 'deDE', null, 'Würde das nicht ein Kreuzen der Strahlen bedeuten? Ist das nicht ziemlich schlimm?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('213', 'deDE', null, 'Die resultierenden Energien könnten natürlich kollabieren und das könnte wiederum einen Energiefluxus erzeugen, der Euch die nächsten Wochen eine Migräne bescheren würde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('214', 'deDE', null, 'Aber ist es nicht genau das, was die anfänglichen Probleme mit Adept Sylerias magischen Formeln verursachte?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('215', 'deDE', null, 'Ich denke nicht. Ich möchte nicht wieder hochgejagt werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('216', 'deDE', null, 'Das wird jedoch den magischen Fokus aus der Balance bringen und im Äther eine umgekehrte Vibration verursachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('217', 'deDE', null, 'Ich denke, dass es funktionieren könnte, wenn wir zum Zaubern zwanzig Leute zur Verfügung hätten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('218', 'deDE', null, 'Was wäre denn, wenn wir drei Fokusse nach tyreanischem Muster einsetzten? Das sollte die Lösung sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('219', 'deDE', null, 'Aber wenn wir es mit einem Ankergewinde auf angemessenem Energiefluss stabilisieren könnten, sollte es funktionieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('220', 'deDE', null, 'Seid nicht immer so negativ. Die Chancen, dass das eintritt, stehen zwischen Null und Nichts.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('221', 'deDE', null, 'Wenn wir eine geeignete Sequenz benutzen, sollte es funktionieren. Es werden vor Beginn nur sorgfältige Studien nötig sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('222', 'deDE', null, 'Hui, und das alles für einen Liebestrank. Hoffentlich ist er es wert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('223', 'deDE', null, 'Wenn wir die surianische Theorie benutzen, dann ja, aber nicht, wenn wir den y\'serianischen Ansatz verfolgen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('224', 'deDE', null, 'Zumindest wären wir dann nicht da, um sauber machen zu müssen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('225', 'deDE', null, 'Nur, wenn wir nicht die richtigen Initialisierungsprozeduren befolgen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('226', 'deDE', 'Ich schätze, Ihr habt Recht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('227', 'deDE', null, 'Hier ist meine Liste, Keldric. Die üblichen Sachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('228', 'deDE', null, 'Habt Dank, Keldric. Bis bald.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('229', 'deDE', 'Seid verflucht, $N! Eines Tages wird die Rache mein sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('230', 'deDE', 'Wir haben eben erfahren, dass eine Karawane Thelsamar verlassen hat. Es ist Zeit aufzubrechen! Los, los, los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('231', 'deDE', 'Wir kommen der Ausgrabungsstätte immer näher und müssen jetzt vorsichtig sein. Macht den Unterwasser-Atemapparat bereit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('232', 'deDE', 'He Stone, habt Ihr schon von der neuen Dampfmaschine des alten Tonkrend gehört?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('233', 'deDE', 'Ja doch. Ich habe alles über diese neumodischen Spielereien gehört. Die Panzerung des Gehäuses wurde entfernt, um das Gewicht zu verringern. Pah! Unsinn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('234', 'deDE', 'Denkt Ihr auch mal an etwas anderes als an Kämpfe? Auf der Laufbahn würde ich Euch um Längen schlagen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('235', 'deDE', 'Pah! Wenn Ihr in die Reichweite des alten Trollplows kommt, werdet Ihr zu Staub zermahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('236', 'deDE', 'Argh! Ihr seid ein hoffnungsloser Fall!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('237', 'deDE', 'He, Fizzer, habe ich Euch je davon erzählt, wie ich mal durch die Kanäle ritt und ein paar Goblins in Schredderrüstungen meinten, mir auflauern zu können?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('238', 'deDE', 'Ah, lasst mich mal sehen. Ja, annähernd hundert Mal, würde ich wetten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('239', 'deDE', 'Wahaha! Ihr Gesichtsausdruck, als die Kanone des alten Trollplows sich in ihre Richtung drehte! Ihre Sägen haben mich nicht einmal berührt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('240', 'deDE', 'Ich hätte einfach ein bisschen Immerschimmer in meinen Tank gefüllt und wäre zu Hause gewesen, bevor die überhaupt gemerkt hätten, dass ich weg war!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('241', 'deDE', 'Pah! Das macht doch keinen Spaß!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('242', 'deDE', 'He, Stone, wir sollten uns mal wieder auf der Laufbahn treffen. Könnten wir nicht ein paar Runden laufen und schauen, wer gewinnt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('244', 'deDE', 'Aber sicher doch. So lange, bis wir danach direkt in der Arena landen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('245', 'deDE', 'Nichts, was Ihr sagt, wird mich je dazu bewegen, dieser monströsen Maschine, die Ihr Dampfpanzer nennt, in der Arena entgegenzutreten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('246', 'deDE', 'Ha! Habt Ihr Angst, dass ich Euren kleinen, mickrigen Rennwagen zerstöre?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('247', 'deDE', 'Ha! Ich habe mehr Angst davor, dass diese alten Löcher und Spalten das Ding zur Explosion bringen und ich von den Splittern getroffen werden könnte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('248', 'deDE', 'Haha! Der gute alte Trollplow hat nur ein paar kleine Gefechtsnarben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('249', 'deDE', 'Nichts geht über den Klang einer Zwei-Zylinder-Dampfmaschine mit Gabelring, die mit reinem Feuerstein betrieben wird!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('250', 'deDE', 'Ha! Ich würde das Geräusch einer Kraterkanone XT, die ein Geschoss abfeuert, jederzeit allem anderen vorziehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('251', 'deDE', 'Pah! Ihr würdet ja eine großartige Dampfmaschine nicht einmal erkennen, wenn sie Euch auf den Kopf fiele!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('252', 'deDE', 'Mag sein, aber wenn mir das Ding auf den Kopf fallen würde, wüsste ich ganz genau, welche Kanone geeignet wäre, um es in seine Bestandteile aufzulösen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('253', 'deDE', null, 'Corbett, mein Teurer. Harlan benötigt so schnell wir können eine Ladung gestrickter Hemden und Hosen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('254', 'deDE', null, 'Corbett, seid Ihr da? Harlan benötigt noch eine Ladung Strickwaren. Könnt Ihr sie ihm bringen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('255', 'deDE', 'Es war mir ein Vergnügen, Zuckerschnute. Ich bin bald zurück...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('256', 'deDE', 'Die Geschäfte im Basar scheinen ja gut zu laufen. Ich werde ihm sofort Nachschub bringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('257', 'deDE', 'Hm... wenn ich das hier losgeworden bin, werde ich vielleicht in dieses Käsegeschäft gehen und mir dort einen kleinen Imbiss genehmigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('258', 'deDE', 'Ich müsste doch ein paar Münzen bei diesem Geschäft verdient haben. Vielleicht kaufe ich mir ein Mittagessen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('259', 'deDE', 'He, Harlan. Hier ist eine Ladung Strickstoff für Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('260', 'deDE', 'Uff! Hier ist eine neue Ladung an Vorräten, Harlan. Euer Geschäft scheint ja gut zu laufen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('261', 'deDE', 'Ah, das weiß ich sehr zu schätzen, Corbett. Wir werden alles sofort in die Regale räumen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('262', 'deDE', 'Ah ja, und pünktlich geliefert. Es ist wie immer ein Vergnügen, mit Euch Geschäfte zu machen, Corbett.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('263', 'deDE', 'Dann will ich mal wieder. Macht\'s gut, Harlan.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('264', 'deDE', 'Schön zu sehen, dass es Euch so gut geht, Harlan. Und ich hoffe, wir sehen uns bald wieder...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('265', 'deDE', 'Oberbürgermeister! Unsere Späher berichten von seltsamen Bewegungen im Westen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('266', 'deDE', 'Bürgermeister Ello! Unsere Friedhof-Späher kehren mit unheilvollen Berichten zurück...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('267', 'deDE', 'Grauenhaftes Gebrüll und Geknurre dringt aus der Hütte des alten Einsiedlers!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('268', 'deDE', 'Wölfe sind in diese Richtung unterwegs, als würden sie... vor etwas fliehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('269', 'deDE', 'Ja, Oberbürgermeister.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('270', 'deDE', 'Das sind schlimme Nachrichten und ich fürchte, dass sie etwas mit den Geschäften zu tun haben, die $N mit dem Einbalsamierer macht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('271', 'deDE', 'Behüter, informiert Althea, dass eine entsetzliche Bedrohung auf unsere Stadt zusteuert. Wir müssen Vorbereitungen treffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('272', 'deDE', 'Althea, ich habe gehört, dass auf dem Friedhof seltsame Dinge vor sich gehen sollen. Ello denkt, dass es Anzeichen für einen Angriff auf Dunkelhain sein könnten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('273', 'deDE', null, 'Das verheißt nichts Gutes. Schickt Späher aus, um mehr Informationen zu sammeln, und bleibt wachsam.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('275', 'deDE', 'Ja, Kommandant!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('276', 'deDE', 'Achtung! Achtung! Gefahr lauert in der Wildnis! Nachtwachen, seid auf der Hut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('277', 'deDE', 'DUNKELHAIN... ICH HABE HUNGER!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('278', 'deDE', 'RRRRRRRR!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('279', 'deDE', null, '... Neun... Zehn... Fertig oder nicht, ich komme!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('280', 'deDE', null, 'He! Wehe, du bist zu weit weg gelaufen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('281', 'deDE', null, 'Mama hat uns verboten, da noch mal hochzuklettern. Ich werd\'s sagen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('282', 'deDE', 'Danke, $N. Möge das Licht Euch segnen, $GBruder:Schwester; und Euch leiten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('283', 'deDE', 'Nun zu diesem Imbiss...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('284', 'deDE', 'Guten Tag, Elling! Hallo Elaine! Kann ich einen Laib Blaukäse bekommen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('285', 'deDE', 'Hallo, Triasklan! Eine Kugel von Eurem geräucherten Mozzarella, bitte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('286', 'deDE', null, 'Guten Tag, Corbett. Hier ist Euer Käse. Heute Morgen ganz frisch gemacht! Und wie läuft Euer Laden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('287', 'deDE', null, 'Hallo, Corbett! Bitte sehr! Sehe ich es richtig, dass Euer Tuchladen gut läuft...?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('288', 'deDE', 'Bis später, Corbett, und grüßt Eure bessere Hälfte von mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('289', 'deDE', 'Macht\'s gut, und grüßt Eure Familie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('290', 'deDE', 'Recht herzlichen Dank!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('291', 'deDE', 'Danke für den Käse!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('292', 'deDE', 'Ich sollte zurückgehen, bevor Rema sich Sorgen macht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('293', 'deDE', 'Zeit, wieder zurück in den Laden zu gehen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('294', 'deDE', 'Ich bin zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('295', 'deDE', 'Ja, gnädige Frau, das Geschäft läuft gut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('296', 'deDE', 'Testing 1', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('297', 'deDE', 'Testing 2', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('298', 'deDE', 'Testing 3', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('299', 'deDE', 'Ich habe das Gebäude wieder verlassen. Jetzt gehe ich noch einen Wegpunkt weiter...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('300', 'deDE', 'Ich bewege mich innerhalb des Gebäudes.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('301', 'deDE', 'Ich habe den letzten Wegpunkt erreicht! Juhu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('302', 'deDE', null, 'Mama, Jesse ist wieder den Windturm hochgeklettert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('303', 'deDE', 'Ihr verpetzt mich besser nicht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('304', 'deDE', 'Ich kann nicht runterkommen. Ich hab Angst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('305', 'deDE', 'Whoa!... Whoa!... Aaaaahhh!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('306', 'deDE', 'An die Arbeit, Ihr Hunde! Kniet Euch richtig rein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('307', 'deDE', null, 'Du kommst jetzt sofort da runter! Ich will nicht, dass du an diesem Ding hochkletterst. Es ist nicht sicher.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('308', 'deDE', null, 'Seht Ihr, was ich meine? Rein jetzt, auf der Stelle.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('310', 'deDE', 'Es wird eine Weile dauern, bis das warm ist. Zeit genug, sich einen Schluck zu genehmigen! Wie steht\'s mit Euch, Tongus?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('311', 'deDE', 'WÄHLT MICH!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('312', 'deDE', 'Angriff!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('313', 'deDE', 'Geht auf eure Positionen, Männer! Die Bestien sind gleich bei uns. Wartet auf mein Signal!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('314', 'deDE', 'Diese Länder werden gesäubert werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('316', 'deDE', 'Die Gräueltaten der Untoten werden ausgelöscht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('317', 'deDE', 'Der Scharlachrote Kreuzzug darf nicht scheitern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('318', 'deDE', 'Da draußen tobt das Meer. Die Meeresgeister sind wohl unruhig... hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('319', 'deDE', 'Ich habe einmal einen Meeresriesen gesehen. Ich kann darauf verzichten, noch mal einem zu begegnen. Rülps!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('320', 'deDE', 'Ich würde ja da raus gehen und mit diesen Gnollen aufräumen, aber dieses Bein hat schon bessere Tage erlebt und ich befürchte, ich würde nur als Toter enden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('321', 'deDE', 'Seht Euch da draußen vor. Schon so mancher Abenteuersuchende ist nicht aus dem Sumpf zurückgekehrt. Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('322', 'deDE', 'Es ist Zeit für meine Meditation, lasst mich allein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('323', 'deDE', 'Da seid Ihr ja. Was gibt es für Neuigkeiten aus Westfall?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('324', 'deDE', 'Van Cleef lässt mitteilen, dass die Pläne unterwegs seien. Doch er hat Gerüchte vernommen, dass irgendjemand herumschnüffelt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('325', 'deDE', 'Wohlan denn. Dann werde ich zurückkehren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('326', 'deDE', 'Hmm, das könnte dieser Schnüffler Shaw sein. Ich werde sehen, was ich herausfinden kann. Und nun fort mit Euch. Ich werde Euch bald wieder aufsuchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('327', 'deDE', '*seufz* Es ist an der Zeit, den Friedhof wieder zu überprüfen. Ich bin in ein paar Minuten zurück.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('328', 'deDE', 'Wir sollten diesen Zaun wirklich bald reparieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('329', 'deDE', 'Vrok sagte, dass die Visiere von Grumlars Schusswaffe ab sind. Ich sollte wohl mal nachsehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('330', 'deDE', 'Hmpf! Ich habe das verdammte Ziel nicht einmal getroffen! Dieser dickschädelige Zwerg hat die Schusswaffe wahrscheinlich wieder als StreitKolben benutzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('331', 'deDE', 'Hmmmm... Der hat jetzt seine Schulter gestreift. Grumlar hatte Recht, die Visiere sind ein bisschen verbogen. Aber das kann ich leicht reparieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('332', 'deDE', 'Ein fast perfekter Schuss! Er liegt ein bisschen daneben, aber ich kann mir vorstellen, wie Grumlar damit daneben treffen würde. Er würde selbst ein Scheunentor nicht treffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('333', 'deDE', 'Volltreffer! Dieser verdammte Zwerg weiß nicht, wovon er redet. Das Einzige, was mit Grumlars Schusswaffe nicht stimmt, ist Grumlars Zielkunst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('339', 'deDE', 'Only $3658E shopping minutes before Christmas!  But wait, is it Christmas?  Well then, there are only $3659E minutes left to party!  TEST TEST DO NOT LOCALIZE', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('340', 'deDE', 'Warg, seid Ihr bereit zum Essen? Ich fange jetzt damit an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('341', 'deDE', null, 'Khara summt während des Kochens eine alte Zwergenmelodie.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('342', 'deDE', 'Liebling, das Essen ist fertig. Komm, wasch dich und iss.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('343', 'deDE', 'Mmmm, das war gut. Aber jetzt ist es Zeit, wieder an die Arbeit zu gehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('344', 'deDE', 'Ahhh, das wurde aber auch Zeit! Mein Magen hat schon ganz laut geknurrt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('345', 'deDE', 'MMmmMMmmm. Riecht köstlich, Khara.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('346', 'deDE', 'Rülps! Liebling, das war wirklich gut. Du weißt nicht nur, wie man einen Fisch fängt, du kannst ihn auch noch wunderbar zubereiten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('347', 'deDE', 'Puh! Ich habe wohl zu viel gegessen. Muss wohl meinen Gürtel etwas weiter schnallen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('348', 'deDE', 'Danke für das köstliche Mahl, Liebling, aber ich muss wieder raus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('349', 'deDE', 'Ein weiterer glorreicher Fischzug steht mir bevor, Khara. Ich sollte mich in Bewegung setzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('350', 'deDE', 'Was für ein Tag zum Fischen! Heute werden sie bestimmt beißen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('352', 'deDE', 'Warg schaut über das Wasser und überlegt, ob er heute wohl Glück haben wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('353', 'deDE', 'Die Fische beißen nicht mehr, also kann ich genauso gut damit anfangen, die auszunehmen, die ich heute schon gefangen habe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('355', 'deDE', 'Am besten mache ich mich auf den Weg. Khara wird mich schon bald erwarten. Bis morgen, X.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('356', 'deDE', 'Seht Euch nur die Wellen an. Im Wasser ist eine Menge los, also müsste ich heute auch richtig viele Fische fangen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('357', 'deDE', 'Hmmm, nur ein wenig Bewegung an der Oberfläche, aber die Sonne steht richtig. Könnte noch ein richtig schöner Tag werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('358', 'deDE', 'Sieht nicht sehr gut aus heute. Die Sonne steht schon hoch und scheint hell. Auf dem Wasser ist keine Bewegung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('359', 'deDE', 'Hmpf! Das wird aber ein trauriger Fischzug. Nun ja, ein schlechter Tag beim Fischen ist besser als ein guter Tag an der Arbeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('360', 'deDE', 'Berte! Evalyn! Es wird langsam dunkel. Kommt rein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('361', 'deDE', 'Ihr könnt morgen weiterspielen. Jetzt kommt aber rein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('362', 'deDE', 'Schön, schön! Ihr zwei könnt spielen gehen, aber bleibt in der Stadt. Schleicht euch ja nicht weg, um Warg und Khara unten am Wasser zu stören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('363', 'deDE', 'Hah! Daryl, Ihr hättet Euren Gesichtsausdruck sehen sollen, Ihr seid ja beinahe aus der Haut gefahren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('365', 'deDE', 'Euch haben sie aber ordentlich erwischt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('366', 'deDE', 'Ruhe jetzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('367', 'deDE', 'Ach, Mama! Es ist doch noch gar nicht dunkel! Können wir bitte noch ein bisschen weiterspielen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('368', 'deDE', 'In Ordnung...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('370', 'deDE', 'Wir müssen wachsam sein, um diese Seuche auszurotten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('371', 'deDE', 'Weiter mit der guten Arbeit. Diese Geißel wird gesäubert werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('372', 'deDE', 'Der scharlachrote Kreuzzug wird diese Länder überfluten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('373', 'deDE', 'Lasst niemanden, dem die Besudelung der Seuche anhaftet, am Leben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('374', 'deDE', 'Mama! Dürfen wir bitte, bitte, bitte spielen gehen? Dürfen wir, hä? Dürfen wir?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('375', 'deDE', 'Die Kinder hüpfen voller jugendlicher Energie auf und ab. (PH emote)', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('376', 'deDE', 'Uiiiiiiiiii! Danke, Mama!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('378', 'deDE', 'Danke, Mrs. Brightbeard!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('379', 'deDE', 'Langsam wird es ein wenig frisch hier draußen. Ich denke, ich werde mich mal am Feuer wärmen und mir ein oder zwölf Bier genehmigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('380', 'deDE', 'Es geht doch nichts über ein prasselndes Feuer an einem kühlen Abend, mit Freunden und Zwergenbier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('381', 'deDE', 'Brock, Torren, ich gehe hinein, um ein Bier zu trinken und um mich am Feuer aufzuwärmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('382', 'deDE', 'Mmmmm, ein Bier am Feuer,... das hört sich gut an. Klar, ich komme mit, Jern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('383', 'deDE', 'Hier! Hier! Darauf trinke ich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('384', 'deDE', 'Was? Sagte gerade jemand \"Bier\"? Leert Eure Gläser nicht ohne den alten Torren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('385', 'deDE', 'Ich trinke auf alles!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('386', 'deDE', 'Noma wird bald das Abendessen fertig haben. Ich könnte genauso gut hineingehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('387', 'deDE', 'Mann, das Blackclaw-Starkbier haut ganz schön rein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('388', 'deDE', 'Mir ist... schwindlig. Es ist so heiß... hier... drinnen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('389', 'deDE', 'Wo sind die anderen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('390', 'deDE', 'Sie kommen alle...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('391', 'deDE', 'Er konnte wirklich nichts dafür...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('393', 'deDE', 'Niemand lebt ewig...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('394', 'deDE', null, 'Gimlok! Gimlok! Kommt hier rein, sofort!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('395', 'deDE', null, 'Wohin ist der Junge jetzt schon wieder?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('396', 'deDE', null, 'Gimlok, mach dich nicht wieder schmutzig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('397', 'deDE', 'Hipppppp... Hippppp... Hurraaaaaa!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('398', 'deDE', 'Eines Tages werde ich eine Wache!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('399', 'deDE', 'Könnt Ihr Euch noch erinnern, wie Danath alle Söldner von Sturmwind versammelt hat und wir zum Kampf bei Nethergarde marschiert sind?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('400', 'deDE', 'Ich sage Euch, mir fehlt der Große Krieg überhaupt nicht. Ich weiß noch, wie wir bei Darrowmere kämpften. Wir lagen die ganze Nacht bei Nebel in einem modrigen Graben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('401', 'deDE', 'Mir fehlt das Zwergenbier, das wir immer in diesem Gasthaus in Lordaeron getrunken haben. Erinnert Ihr Euch noch an den Kampf, den wir dort anfingen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('402', 'deDE', 'Weniger als hundert von uns und mehr als tausend Orcs. Nur eine Hand voll von uns hat das überlebt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('403', 'deDE', 'Das Beste muss der Ausdruck auf Perenoldes Gesicht gewesen sein, als unsere Armee direkt bis vor seine Haustür marschiert ist. Was für eine Schlacht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('405', 'deDE', 'Ich kann mir bessere Orte als diesen Morast für einen Krieg gegen die Twilight-Hammer vorstellen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('406', 'deDE', 'Ach ja, jetzt erinnere ich mich, Eure Hochzeitsnacht, nicht wahr?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('407', 'deDE', 'Ah, die Glustewelt-Zwillinge!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('408', 'deDE', 'Ich habe immer noch Narben aus dieser Nacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('409', 'deDE', 'In der Nacht hab ich mir beide Beine gebrochen. Wie könnt ich das vergessen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('410', 'deDE', 'Drei Wochen später wachte ich in einem Bett in Nordhain wieder auf. Ich erinnere mich an gar nichts mehr.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('411', 'deDE', 'War das nicht die Nacht, als wir meinen Daumen aufsammeln und in Eurer Tabaktasche transportieren mussten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('413', 'deDE', 'Ich habe ein Stück Eisen in meinem Rücken, das mich für den Rest meines Lebens an diese Nacht erinnern wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('414', 'deDE', 'Klar erinner ich mich an die Nacht. Fünf Zentimeter weiter links, und ich würd jetzt nich\' hier sitzen un\' mit Euch trinken, Morris. Un\' das war auch noch mein bestes Hemd.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('415', 'deDE', 'War es das dritte oder vierte Mal, dass Ihr beinahe Hackfleisch aus mir gemacht habt, als Ihr einen Eurer verrückten Tricks ausprobiert habt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('416', 'deDE', 'Ja, und danke, dass Ihr mich in dieser Nacht meine eigene Hand zurück zu den Priestern habt tragen lassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('417', 'deDE', 'Hehe, Schlappschwanz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('418', 'deDE', 'Ihr habt geschrien wie ein kleines Mädchen; das war das Lustigste, was ich je gesehen habe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('419', 'deDE', 'Am Ende hat doch alles geklappt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('420', 'deDE', 'Lasst uns erst gar nicht damit anfangen, Kampfnarben zu vergleichen, mein Freund.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('421', 'deDE', 'Ich habe noch nie jemanden gesehen, der sich so schnell bewegt hat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('422', 'deDE', 'Ihr überrascht mich ständig aufs Neue damit, was eine Person alles ertragen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('423', 'deDE', 'Nun, Ihr seid immer noch hier. Ich würde sagen, dass wir darauf einen trinken sollten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('424', 'deDE', 'Ihr könnt Euch jederzeit bei mir dafür bedanken, dass ich Euer Leben interessanter gemacht habe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('425', 'deDE', 'M... m... mir... geht... es... nicht... gut...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('426', 'deDE', '%s winkt $N zu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('427', 'deDE', 'Ihr habt meine Ruhe gestört, $C. Jetzt bekommt Ihr meinen Zorn zu spüren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('428', 'deDE', 'Mein Verstand... mein Fleisch... ich... verwese...!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('429', 'deDE', 'Gebt mir noch eins, Langston.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('430', 'deDE', 'Das beste Bier in ganz Sturmwind, Langston. Danke.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('431', 'deDE', 'He, Reese, noch \'ne Runde für Christoph und mich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('432', 'deDE', 'Eine warme Taverne und ein kaltes Bier. Was wollen wir mehr?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('433', 'deDE', '%s legt die Schusswaffe an seine Schulter, zielt sorgfältig und feuert dann einen Schuss auf das Ziel ab.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('434', 'deDE', 'Sterbt im Namen von Ragnaros!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('435', 'deDE', 'Der Thandol-Übergang ist in die Hände von Ragnaros gefallen. So soll es auch mit dem Stonewroughtdamm geschehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('436', 'deDE', 'König Magni Bronzebeard ist ein Narr und ein Scharlatan!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('437', 'deDE', 'Dieser $C stört uns bei unserer Arbeit! Sterbt, Narr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('438', 'deDE', 'Wer ist denn da? Ein $C namens $N? Angriff!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('440', 'deDE', 'Jeder, der Ragnaros verrät, ist des Todes!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('441', 'deDE', 'He, das ist der dreckige $R! Tötet den Abschaum!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('442', 'deDE', 'Der Stonewroughtdamm wird zerstört werden! Lang lebe Ragnaros!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('443', 'deDE', 'Schwacher $C! Ihr seid kein Gegner für den Splittersteinstamm!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('444', 'deDE', 'Ein $C namens $N? Ihr werdet ein leckeres Frühstück abgeben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('445', 'deDE', 'Der Splittersteinstamm wird Eure Verdammnis erleben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('446', 'deDE', 'Sterbt, $R! Dieses Land gehört dem Splittersteinstamm!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('447', 'deDE', 'Nur ein toter $R ist ein guter $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('448', 'deDE', 'Der Loch gehört nun dem Splittersteinstamm, $N! Und nun sterbt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('449', 'deDE', '$N! Verräter von Ragnaros!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('450', 'deDE', 'Die Dunkeleisenzwerge werden ihre rechtmäßigen Länder zurückfordern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('451', 'deDE', 'Törichter $C! Ihr seid kein Gegner für die Diener des Ragnaros!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('452', 'deDE', 'So ein wertloser $R wie Ihr, $N, hat kein Recht, durch diese Länder zu ziehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('453', 'deDE', '$N! Jetzt wird sich Euer Schicksal erfüllen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('454', 'deDE', 'Wer ist dieser schwache $C, der meine Macht herausfordert? Sterbt, Narr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('455', 'deDE', 'Dieses Land gehört den Dunkeleisenzwergen. Bereitet Euch auf das Jenseits vor, $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('456', 'deDE', 'Ich bin Balgaras der Niederträchtige. Und Ihr, $N, seid bald nichts weiter als Staub und Asche.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('457', 'deDE', '$N, möge Euer Tod all jenen als Beispiel dienen, die es wagen, die Bruderschaft der Defias herauszufordern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('458', 'deDE', 'So eine halbe Portion von $C wagt es, mich herauszufordern? Bereitet Euch auf das Jenseits vor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('459', 'deDE', 'Mögen Euch die Todesminen als Grab dienen, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('460', 'deDE', 'Sterbt im Namen der Bruderschaft der Defias!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('461', 'deDE', 'Tod denjenigen, die die Steinmetze hintergingen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('462', 'deDE', 'Die Verderbnis des Oberhauses fließt in Euren Adern. Lasst sie mich mit meiner Klinge entfernen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('463', 'deDE', 'Sterbt, $N! Nur ein toter $C ist ein guter $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('464', 'deDE', 'Wer ist dieser schwächliche $R? Ein übler Eindringling, würde ich sagen. Sterbt, Narr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('465', 'deDE', 'Einfältiger $R! Der Blackrockklan wird auf Eurem Grab tanzen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('466', 'deDE', 'Törichter $C! Ihr habt meiner Macht nichts entgegenzusetzen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('467', 'deDE', 'Ich bin Gath\'Ilzogg! Und Ihr seid ein $R kurz vor seinem Tod!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('468', 'deDE', '$N muss sterben! Lang lebe der Blackrockklan!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('469', 'deDE', 'Wer wagt es, beim Blackrockklan einzudringen? Darauf steht der Tod, $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('470', 'deDE', 'Winziger $C! Ihr seid kein Gegner für den Blackrockklan!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('471', 'deDE', 'Törichter $C! Schwächlicher $R! Ich werde Euch alle Knochen brechen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('472', 'deDE', 'Lang lebe der mächtige Blackrockklan!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('473', 'deDE', 'Sterbt im Namen von Edwin Van Cleef!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('474', 'deDE', 'Wer wagt es, sich in die Angelegenheiten der Bruderschaft der Defias einzumischen? Sterbt, $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('475', 'deDE', 'Jeder $R, der sich mir in den Weg stellt, soll sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('476', 'deDE', 'Wer wagt es, den Boten von Edwin Van Cleef zu provozieren? Sterbt, $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('477', 'deDE', 'Ob ich Briefe für $N bei mir habe? Natürlich nicht! Und Ihr werdet jetzt wohl sterben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('478', 'deDE', 'Ich werde Euch ins Jenseits befördern, schwächlicher $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('479', 'deDE', 'Ich habe eine besondere Nachricht für $N. Und die lautet, dass Ihr sterben müsst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('480', 'deDE', 'Hier ist ein singendes Telegramm für $N: Veilchen sind blau, Rosen sind rot, jeden $R, den ich sehe, den schlage ich tot!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('481', 'deDE', 'Wir sollten zu den anderen gehen und die Wölfe da draußen im Auge behalten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('482', 'deDE', 'Gebt Acht, $N. Diese Wölfe halten sich oft unter den Bäumen versteckt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('483', 'deDE', 'Wir sind fast da!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('484', 'deDE', 'Wir haben es geschafft! Danke, $N. Ohne Euch hätte ich es nicht bis hierher geschafft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('485', 'deDE', 'Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('486', 'deDE', 'Ich wurde entdeckt! $N kommt! Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('487', 'deDE', 'Sterbt, wertloses Wesen! $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('488', 'deDE', 'Ich brauche Hilfe. D. $N hat mich gesehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('489', 'deDE', '$N im Anmarsch! Haltet Euch kampfbereit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('490', 'deDE', 'D. $N will mich töten! Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('491', 'deDE', 'Was war das?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('492', 'deDE', 'Habt Ihr etwas gehört?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('493', 'deDE', 'Ich rieche etwas. $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('494', 'deDE', 'Hilfe! Wir werden angegriffen! Dunkeleisenzwerge!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('496', 'deDE', '$N in schnellem Anmarsch! Fertig machen zum Angriff!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('497', 'deDE', 'Wir werden belagert! Zu den Waffen! Tötet d. $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('498', 'deDE', 'Ah, endlich hier! Ich fühle mich bestimmt viel besser, wenn ich diese Fässer los bin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('499', 'deDE', '%s fordert $N lauthals heraus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('500', 'deDE', null, 'Ich werde rechtzeitig zurück sein...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('502', 'deDE', 'Die Söhne von Arugal werden sich gegen alle erheben, die die Macht der Moonrage herausfordern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('503', 'deDE', 'Lasst die Legende von Stalvan ruhen!', 'Lasst die Legende von Stalvan ruhen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('504', 'deDE', 'Wer ist dieser kleine $R, der sich in etwas einmischt, das der Vergangenheit angehört? Möge die Legende von Stalvan gemeinsam mit Euch untergehen!', 'Wer ist dieser kleine $R, der sich in etwas einmischt, das der Vergangenheit angehört? Möge die Legende von Stalvan gemeinsam mit Euch untergehen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('505', 'deDE', 'Quält Euch nicht mit Angelegenheiten aus der Vergangenheit, $N!', 'Quält Euch nicht mit Angelegenheiten aus der Vergangenheit, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('506', 'deDE', null, '%s stößt einen hohen Hilfeschrei aus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('507', 'deDE', 'Tilloa, bist du das? Natürlich nicht! Es ist nur ein dreckiger $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('508', 'deDE', 'Wer wagt es, mich zu stören? Sterbt, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('509', 'deDE', 'Ich werde Euer Blut vergießen, $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('510', 'deDE', 'Hilfe! Ich habe nur eine Hand, um mich zu verteidigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('511', 'deDE', 'Schickt sie nur her! Ich habe keine Angst vor diesen dürren Bestien!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('512', 'deDE', 'Schnappt ihn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('513', 'deDE', '%s singt Worte der Macht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('514', 'deDE', 'Nein! Das kann nicht sein...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('515', 'deDE', 'Unglaublich! Ich kenne den, der diese Verzauberungen herbeigeführt hat...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('516', 'deDE', 'Das kommt unerwartet. Das Wesen, das dies verzaubert hat, ist mir bekannt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('517', 'deDE', '$N, ich habe die Information, die Ihr sucht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('518', 'deDE', 'Meine Diener...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('519', 'deDE', 'Die Zeit der Eroberung naht schnell.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('520', 'deDE', 'Bald wird Euer Verlangen nach Zerstörung gestillt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('521', 'deDE', 'Die Wut, die in Euch brennt, wird bald durch unsere Feinde weiter geschürt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('522', 'deDE', 'Ich komme mit Neuigkeiten über unseren Konflikt mit den schurkischen Untoten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('523', 'deDE', 'Die Rebellen von Lordaeron sind klein und schwach. Sie kennen die Freude nicht, unserem Lichkönig zu dienen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('525', 'deDE', 'Die Anhänger der dunklen Fürstin sind blind, wenn sie glauben, sie könnten gegen unsere Macht bestehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('526', 'deDE', 'Wir werden stärker, während unsere Gegner von Feinden umringt und durch ständige Angriffe geschwächt sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('527', 'deDE', 'Unser Lord hat uns befohlen, uns auf den Krieg vorzubereiten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('528', 'deDE', 'Macht Euch bereit, denn wir werden bald einen Angriff gegen diejenigen starten, die sich selbst \"die Verlassenen\" genannt haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('529', 'deDE', 'Macht Euch für die Schlacht bereit, meine Geschöpfe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('530', 'deDE', 'Wir sind der Arm Seines Willens. Und wir werden Seine Feinde zu Grabesstaub zermahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('531', 'deDE', 'Dieses untote Gesindel und seine Totenelfen-Königin werden fallen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('532', 'deDE', 'Wir werden unsere Feinde aus Silberwald vertreiben und dann den Krieg in ihre eigene Hauptstadt tragen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('533', 'deDE', 'Tod und Lob für den Lichkönig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('534', 'deDE', null, 'Schön Euch wiederzusehen, Erland. Wie lautet Euer Bericht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('535', 'deDE', 'Im Osten halten sich unzählige Wölfe auf und wer auch immer bei Maldens Obsthain lebte, ist fort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('536', 'deDE', 'Wenn Ihr mich entschuldigen wollt, dann würde ich jetzt gern mal nach Quinn sehen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('537', 'deDE', 'Hallo, Quinn. Wie geht es Euch?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('538', 'deDE', 'Ihr müsst besser auf Euch Acht geben, Quinn. Diesmal habt Ihr Glück gehabt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('539', 'deDE', 'Es ist mir schon besser ergangen. Ivar der Verfaulte war mir über...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('540', 'deDE', 'Kein mieser Verräter wird verhindern, dass das Pulver zu Ironband gelangt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('541', 'deDE', 'Vorsicht! Ei. $N ist hinter uns her!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('542', 'deDE', null, 'Haltet Euch bereit, $N. Ich höre den Rat zurückkehren. Macht Euch für einen Hinterhalt bereit!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('543', 'deDE', '$N greift an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('544', 'deDE', 'Achtung! Ich werde angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('545', 'deDE', null, 'Gut gemacht. Ein Schlag für Arugal, kein Zweifel!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('546', 'deDE', 'Der Stonewroughtdamm wurde gerettet! Ein dreifaches Hoch auf $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('548', 'deDE', null, 'Alles ist verloren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('549', 'deDE', 'Macht Platz! Macht Platz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('551', 'deDE', '%s hetzt vom ersten Katapult zum zweiten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('552', 'deDE', 'Ich bin verflucht! Zuerst sinkt mein Schiff und jetzt... ist mir auch noch der Met ausgegangen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('553', 'deDE', 'Seid verflucht, Halyndor! Ihr habt uns ins Verderben geführt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('554', 'deDE', 'Stört Euch nicht an mir oder meinem Geruch... selbst stör mich auch nicht dran!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('556', 'deDE', 'Ah, wunderschöner Hafen von Menethil, mit dem besten Met diesseits von Eisenschmiede.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('557', 'deDE', 'Oh, meine Brüder! Mögt Ihr in Euren feuchten Gräbern Frieden finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('558', 'deDE', '%s sieht aus, als müsste er sich übergeben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('559', 'deDE', '%s prahlt mächtig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('560', 'deDE', '%s murmelt zusammenhangloses Zeug.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('561', 'deDE', '%s hält sich seinen Krug vors Auge und runzelt missbilligend die Stirn.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('562', 'deDE', '%s knurrt und schaut sich aggressiv um.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('563', 'deDE', '%s beginnt mit der Übersetzung...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('564', 'deDE', '$N, die Übersetzung ist abgeschlossen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('566', 'deDE', 'Das hier ist ein gefährliches Geschäft!$B$BMiran hat sich gerade mit einer Lieferung auf den Weg gemacht. Er sollte aber in ein paar Minuten zurück sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('567', 'deDE', 'Sie dachten, ich wäre schwach. Haha!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('568', 'deDE', 'Ich bin das Oberhaupt der Familie. Ich. ICH!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('569', 'deDE', 'Mir ist kalt, Lord. Wann werdet Ihr Euer Versprechen einlösen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('570', 'deDE', 'Meine Familie hasst mich. Ängstliche Idioten, alle miteinander!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('571', 'deDE', 'Vater? Seid Ihr jetzt stolz auf mich? HAHA!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('572', 'deDE', 'Mutter, oh Mutter. Ihr hättet auf mich hören sollen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('573', 'deDE', 'Thurman, mein Bruder, wer lacht jetzt? Hah!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('574', 'deDE', 'Auf Familienbesuch? Sterbt, Narr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('575', 'deDE', 'Die Agamand-Mühle wurde von der Geißel eingenommen, $C. Schließt Euch uns an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('576', 'deDE', 'Ich erhebe mein Glas und hoffe, dass wir Euresgleichen bald los sind! Prost, Ihr nichtsnutziger Halunke, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('577', 'deDE', '%s jubelt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('578', 'deDE', null, 'Bleibt zurück, abscheuliche Kreaturen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('579', 'deDE', 'Quak!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('580', 'deDE', '%s schnuppert an dem Elixier und langt dann eifrig zu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('581', 'deDE', 'Endlich haben sie Erbarmen! Sie haben uns etwas zu trinken gegeben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('582', 'deDE', 'Und auch noch Shindigger-Starkbier! Dem Licht sei Dank! Ich war schon ganz ausgedörrt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('583', 'deDE', 'Euer Untod ist nicht das Ende Eures Lebens, sondern erst der Anfang. Es ist an Euch zu entscheiden, was jetzt Euer Schicksal ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('584', 'deDE', 'Ihr seid nicht länger Seiner Kontrolle unterstellt. Und obwohl Ihr von niederen Wesen beurteilt werdet, stehen Euch zumindest Türen offen, die der Geißel verschlossen bleiben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('585', 'deDE', 'Schrecken, Dunkelheit, Macht? Die Verlassenen begehren diese Dinge nicht, die Verlassenen VERKÖRPERN diese Dinge.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('586', 'deDE', 'Meine Knochen sind schwach, meine Augen sehen nur Dunkelheit und mein Körper spürt nur Schmerz. Doch all dies ist gut, denn mein Wille gehört mir allein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('587', 'deDE', null, 'Hallo, Charys. Meine Liste ist fertig. Könnt Ihr mir bitte all dies besorgen, besonders die letzte Zutat?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('588', 'deDE', null, 'Mama? Darf ich Fizzles streicheln?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('589', 'deDE', null, 'Sicher, Paige. Seid nur freundlich.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('590', 'deDE', null, 'Danke, Charys. Komm, Paige, Süße.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('591', 'deDE', '%s beginnt in einer uralten Sprache zu singen und beschwört etwas Böses herauf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('592', 'deDE', 'Ah, es ist gut, von meinen Fesseln befreit zu sein! Habt Ihr Tog\'thar schon befreit? Die Menschen haben uns nach der Gefangennahme getrennt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('593', 'deDE', 'Habt Ihr Drull schon befreit? Ich weiß nicht, wo er festgehalten wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('594', 'deDE', 'Geht ohne mich voraus! Ich werde allein nach Tarrens Mühle reisen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('595', 'deDE', 'Ich werde Euch nur aufhalten, aber kein rosahäutiger Mensch kann mir jetzt noch meine Freiheit nehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('596', 'deDE', 'Durchsucht dieses Geröll.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('597', 'deDE', 'Findet etwas Gutes für mich, ihr Hunde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('598', 'deDE', 'Hier drüben! In dem Zimmer. Habt Ihr den Schlüssel zu meinen Ketten gefunden? Der Mensch Borhuin hat sie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('599', 'deDE', 'Ich erhebe mich aus meinem Grab, um Rache zu nehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('600', 'deDE', 'Sterbt im Namen der dunklen Fürstin, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('601', 'deDE', 'Die Untoten werden sich an Eurer Seele laben, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('602', 'deDE', 'Ich bin auferstanden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('603', 'deDE', 'Zu guter Letzt wird die Rache mein sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('604', 'deDE', 'Süderstade soll mit Blut bezahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('605', 'deDE', 'Mmm... Ich liebe mein köstliches Süderstade-Starkbier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('606', 'deDE', 'Süderstade-Starkbier, wie vergöttere ich dich! Ich könnte es noch genauer sagen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('608', 'deDE', 'Beschützt Großknecht Bonds um jeden Preis!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('609', 'deDE', 'Wachen! Wachen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('610', 'deDE', 'Für diese Schandtat werdet Ihr bezahlen! Wachen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('611', 'deDE', 'Auf Eure Posten! Kein Eindringling darf Süderstade erreichen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('612', 'deDE', 'Alle Verdächtigen müssen sofort ausgeschaltet werden. Geht so schnell wie möglich auf Eure Posten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('613', 'deDE', 'Beschützt den Magistrat um jeden Preis. Sie könnten aus jeder Richtung kommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('614', 'deDE', 'Meine Bäume... sie sind verkümmert und gestorben, so wie ich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('615', 'deDE', 'Dieser Garten ist jetzt so verlassen und leblos wie meine verwesende Schale. Einst waren diese Bäume mit Früchten beladen, doch heute ist alles verwest.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('616', 'deDE', 'Ich habe mich mein ganzes Leben lang um diese Bäume gekümmert. Ich habe sie beschnitten und genährt... daher passt es, dass sie mich im Tod begleiten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('617', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('618', 'deDE', 'Seid gegrüßt, Bürger! Vielleicht werden sich die Dinge bald zu unseren Gunsten wenden und die Seuche wird ein Ende haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('619', 'deDE', 'Helft mir! Hilfe! Auftragsmörder in den Hügeln! Zu den Waffen! Zu den Waffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('620', 'deDE', 'Hört! Hört! *keuch keuch* Seid gewarnt, Bürger von Süderstade: Auftragsmörder nähern sich unserer schönen Stadt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('621', 'deDE', 'Wachen, auf eure Posten! Sie scheinen erneut hinter dem Magistrat her zu sein. Er muss beschützt werden! Sie könnten aus allen Richtungen kommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('622', 'deDE', 'Ich bin so müde. *keuch keuch*', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('623', 'deDE', 'Der Ausrufer von Süderstade packt ihn am Nacken und seine Augen weiten sich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('626', 'deDE', 'Auftragsmörder... rette... d... Magistraaa...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('627', 'deDE', 'Sie sind hier! Sie sind hier drüben! Helft mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('628', 'deDE', 'Die Auftragsmörder sind hier drüben! Schnell, bevor sie den Magistrat erreichen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('629', 'deDE', 'Ich wette, dass das weitere Agenten des Syndikats sind! Ihr werdet Alterac niemals zurückbekommen... und Süderstade auch nicht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('630', 'deDE', 'Es ist vollbracht! Der Magistrat ist tot - in die Schatten, meine Brüder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('631', 'deDE', 'Der Magistrat ist gefallen! Flüchtet, meine Brüder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('632', 'deDE', 'Haha! Jetzt, da der Magistrat gefallen ist, werden Alterac und Hillsbrad wieder unter unsere rechtmäßige Herrschaft kommen. Rückzug!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('633', 'deDE', 'Diese Schnüffler werden für ihre Einmischung bezahlen. Zieht euch in die Schatten zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('634', 'deDE', 'Der Magistrat wird zu gut beschützt. Rückzug!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('635', 'deDE', 'Dieses Mal konntet Ihr uns noch aufhalten, doch wir werden wiederkommen... das versichern wir Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('636', 'deDE', null, '%s nimmt eine Flasche mit einer grünen Flüssigkeit heraus und trinkt einen großen Schluck.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('637', 'deDE', 'Die Augen von %s glühen rot, als er das Dynamit entzündet und wie wahnsinnig gackert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('638', 'deDE', 'Wahaha! Ich nehme Euch mit ins Verderben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('639', 'deDE', 'So, ihr faulen Säcke, Zeit aufzustehen und loszulegen. Diese Brücke wird sich nicht von allein bauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('640', 'deDE', 'Ihr habt ein paar Minuten zum Wachwerden, aber wir müssen bald anfangen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('641', 'deDE', 'He-ho, he-ho, und so weiter... setzt Euch in Bewegung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('642', 'deDE', 'Macht mal eine Pause, Männer! Eine halbe Stunde, dann geht\'s zurück an die Arbeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('643', 'deDE', 'Die Brücke sieht großartig aus. Wenn wir diese Geschwindigkeit beibehalten, dann kann die erste Expansionsstreitmacht bald in den Norden vorrücken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('645', 'deDE', 'Nun, die Mittagspause ist vorbei, Jungs. Gehen wir wieder an die Arbeit. Wenn der Rest des Tages genauso gut verläuft, dann können wir heute vielleicht früher Feierabend machen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('646', 'deDE', 'Gut, gut. Weiter so und die Brücke wird im Handumdrehen fertig sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('647', 'deDE', 'Macht Schluss für heute, Männer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('648', 'deDE', 'Die Zeit wird knapp. Lasst uns weitermachen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('649', 'deDE', 'Gute Nacht, Leute. Schlaft gut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('650', 'deDE', 'Morgen, meine Freunde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('651', 'deDE', 'Ahh... Essen. Es wurde aber auch Zeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('652', 'deDE', 'Das ist ein schöner Tag, was? Harte Arbeit und gutes Essen... es gibt Schlimmeres, was wir an so einem schönen Tag tun könnten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('654', 'deDE', '*Dimitri wischt sich den Schweiß von der Stirn und legt seine Werkzeuge hin.*', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('655', 'deDE', 'Das ist wirklich wieder ein guter Tag.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('656', 'deDE', 'Gute Nacht, meine Freunde. Bis morgen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('657', 'deDE', 'Können wir nicht noch etwas länger schlafen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('658', 'deDE', 'Gut, gut... Ich bin auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('659', 'deDE', 'Wird auch Zeit. Ich hatte schon vor einer Stunde Hunger.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('660', 'deDE', 'Endlich! Ich freue mich schon darauf, heute Nacht etwas Schlaf zu bekommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('661', 'deDE', 'Uh! Den ganzen Tag auf den Beinen zu sein, ist doch keine Art, seinen Lebensunterhalt zu verdienen. Ich hätte Schneider werden sollen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('662', 'deDE', 'Ich liebe den Morgen! Ist das nicht ein schöner Tag? Riecht Ihr diese Luft?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('664', 'deDE', 'Beim Licht, bin ich heute hungrig. Habt Ihr heute Darcy gesehen? Sie sah toll aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('665', 'deDE', 'Dafür, dass wir diese Brücke nur zu sechst bauen sollen, ist sie aber ziemlich groß. Man sollte meinen, dass sie ein paar Leute mehr dafür eingestellt hätten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('666', 'deDE', 'He, Leute, habt ihr sie gesehen? Ich habe Darcy nicht gesehen. Ich frage mich, wo sie heute Abend ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('667', 'deDE', '%s schaut die Docks auf und ab.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('669', 'deDE', 'Na ja, ich bin sicher, sie wird später noch kommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('670', 'deDE', 'Nacht zusammen. Ich werde noch etwas warten. Vielleicht kommt Darcy vorbei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('671', 'deDE', 'Häh?! Mist, es ist Mittag! Ich hab verschlafen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('672', 'deDE', '%s rennt nach Luft schnappend zu den anderen Brückenarbeitern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('674', 'deDE', 'tschuldigung, ich bin spät dran.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('675', 'deDE', 'Diese Brücke wird wohl niemals fertig, was?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('676', 'deDE', 'Mein Rücken bringt mich heute noch um.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('677', 'deDE', 'Ein bisschen Essen ist jetzt genau das Richtige.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('678', 'deDE', 'Hmm, lecker. Und wieder mal zurück an die Arbeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('679', 'deDE', 'Ein weiterer Tag ist vergangen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('680', 'deDE', 'Gute Nacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('681', 'deDE', '%s verschlägt es beim Anblick des Seelensteins die Sprache!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('682', 'deDE', 'Der Geist von %s wurde befreit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('683', 'deDE', '%s stößt einen tiefen Seufzer aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('684', 'deDE', 'Diesen Teil meiner Arbeit hasse ich wirklich. Oh Dämmerwald, sei nett zu dem armen Antonio.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('685', 'deDE', 'Antike Gegenstände, Waffen für den Krieg, Rüstungen oder Raritäten. Wer weiß schon, was Antonio diese Woche anzubieten hat. Kommt und seht selbst nach.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('686', 'deDE', 'Diese Woche könnte es das Auge eines Wassermolchs oder vielleicht eine Schatzkarte sein. Ihr werdet es nicht herausfinden, wenn Ihr nicht nachschaut. Was könnte Antonio haben?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('687', 'deDE', 'Abenteurer aus allen Landen verkaufen Sachen an Antonio... kommt und seht selbst, welche kleinen Schätze er diese Woche für Euch bereithält.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('688', 'deDE', 'Reisende und Abenteurer, eilt zum Stadtplatz und profitiert von Antonios wunderbaren Preisen! Kommt, kommt alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('689', 'deDE', 'Willkommen, Reisende von Dunkelhain. Meine phantastischen Waren sind nur für kurze Zeit in Dunkelhain erhältlich. Eilt Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('690', 'deDE', 'Vergesst Eure Schlachten und Aufträge, stöbert in meinen phantastischen Waren! Nur für kurze Zeit in Dunkelhain... dafür sei das Licht gepriesen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('691', 'deDE', 'Lebt wohl, Bürger von Dunkelhain. Bis zum nächsten Mal...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('692', 'deDE', 'Das Rotkammgebirge, endlich. Eine angenehme Abwechslung zu den dunklen, kalten Nebeln des Dämmerwalds.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('693', 'deDE', 'Ha ha! Ich habe Betsy!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('694', 'deDE', null, 'Gib mir meine Puppe!!!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('695', 'deDE', null, 'Betsy! Gib mir Betsy zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('696', 'deDE', null, 'Tu Betsy nicht weh, du Widerling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('697', 'deDE', null, 'WAAAHHH!!!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('698', 'deDE', null, 'Ich sag\'s meiner Mami!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('699', 'deDE', null, 'Hör auf, ihre Haare auszureißen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('700', 'deDE', null, 'Du tust ihr weh!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('701', 'deDE', null, 'Hör auf! Du reißt ihr ja den Kopf ab!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('702', 'deDE', 'Hallo, hallo, Amy. Es ist schön, wieder in Seenhain zu sein und Euch zu sehen, Milady.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('703', 'deDE', 'Hallo, Antonio. Schön, dass Ihr es sicher wieder hierher geschafft habt. Lasst es mich wissen, wenn Ihr irgendetwas braucht.', 'Hallo, Antonio. Schön, dass Ihr wohlbehalten zurück seid. Lasst mich wissen, wenn Ihr etwas braucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('704', 'deDE', 'Hört, hört! Ich verkaufe meine Waren nur für kurze Zeit hier in Seenhain. Eilt zum Rathaus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('705', 'deDE', 'Ich frage mich, ob deine Puppe schwimmen kann!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('706', 'deDE', 'Ich habe deine Puppe! Nana, nana!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('707', 'deDE', 'Was passiert, wenn ich das mache?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('708', 'deDE', 'Wenn Ihr sie zurückhaben wollt, dann werdet Ihr darum bitten müssen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('709', 'deDE', 'Heulsuse! Heulsuse!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('710', 'deDE', 'Das Baby will seine Puppe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('711', 'deDE', 'Uups!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('712', 'deDE', 'Endlich habe ich Seenhain erreicht - kommt, kommt alle her und profitiert von Antonio Perellis wunderbaren Angeboten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('713', 'deDE', 'Antonio Perelli hat Gegenstände, die so mächtig sind, dass sie die Reise durch den Dämmerwald erlauben. Und Ihr könnt sie jetzt hier in Seenhain erwerben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('714', 'deDE', 'Schön Euch wiederzusehen, Amy. Passt gut auf Euch auf, Schatz. Ich gehe jetzt nach Goldhain.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('715', 'deDE', 'Passt auf Euch auf, Antonio. Wir sehen uns in einer Woche.', 'Passt auf Euch auf, Antonio. Wir sehen uns in einer Woche.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('716', 'deDE', 'Hallo, meine Freunde. Schön, Euch wiederzusehen. Wie ich sehe, beschützt Ihr noch immer unsere Grenzen. Antonio weiß Eure harte Arbeit zu schätzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('717', 'deDE', 'Ihr seid herzlichst willkommen! Ich muss los... passt alle gut auf Euch auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('718', 'deDE', 'Hallo noch mal, Antonio. Danke noch mal für den Hut für meine Frau. Sie liebt ihn.', 'Abermals hallo, Antonio. Danke noch mal für den Hut für meine Frau. Sie liebt ihn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('719', 'deDE', 'Passt gut auf Euch auf, Antonio.', 'Passt gut auf Euch auf, Antonio.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('720', 'deDE', 'Oh, das werde ich, mein Freund. Das werde ich in der Tat. Ihr ebenfalls, passt gut auf Euch auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('721', 'deDE', 'Waren zu verkaufen! Waren zu verkaufen! Ihr Bürger von Goldhain, kommt und stöbert in Antonios Waren, bevor ich Euch wieder verlassen muss.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('722', 'deDE', 'Kommt, kommt alle! Antonios Waren stehen zum Verkauf. Vielleicht komme ich lange Zeit nicht wieder, also kommt, kommt alle.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('723', 'deDE', 'Ihr lieben Leute von Goldhain, kommt und profitiert von meinen unglaublichen Preisen für seltene Waren. Ich werde so bald nicht wieder hierher zurückkehren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('724', 'deDE', 'Auf Wiedersehen, Goldhain! Ich werde bald zurück sein... vielleicht in einer Woche.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('725', 'deDE', 'Endlich wieder zu Hause.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('726', 'deDE', 'Die Waren des überall bekannten Antonio Perelli sind jetzt in der Nähe der Späherkuppe zu kaufen. Ich muss bald weiterziehen, also eilt Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('727', 'deDE', 'In der Nähe des Berges werden handgearbeitete Gegenstände verkauft, die lange halten. Kommt, kommt alle! Die Zeit ist knapp, also eilt euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('728', 'deDE', 'Wieder einmal ist die Zeit gekommen und ich verkaufe meine Waren an alle, die an der Späherkuppe vorbeireisen! Kommt, kommt alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('729', 'deDE', 'Danke Euch allen. Ich werde in etwa einer Woche zurückkehren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('730', 'deDE', 'Ab nach Dunkelhain, nehm\' ich mal an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('731', 'deDE', 'Hmmm... ICH RIECHE ESSEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('732', 'deDE', '%s durchsucht die Schädel...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('733', 'deDE', '%s sucht weiter und legt weitere Schädel in seine Kiste.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('734', 'deDE', 'Ich bin fertig mit der Untersuchung der Schädel, $N, und ich bin erleichtert. Yenniku ist nicht unter ihnen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('735', 'deDE', 'Ah, $N. Dann wollen wir doch mal sehen, was uns diese ganzen Köpfe zu erzählen haben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('736', 'deDE', '%s fängt an zu singen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('737', 'deDE', 'Wo zur Scherbenwelt sind diese Ladungen? Seit Tagen lassen sie mich warten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('738', 'deDE', 'Dummheit! Ich bin von Dummheit umgeben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('739', 'deDE', 'Orcs sind cleverer als diese Kuriere! Wo zur Hölle sind sie?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('740', 'deDE', 'So, $N. Ihr könnt jetzt mit dem Häuptling der Bloodscalp und seinem Hexendoktor sprechen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('741', 'deDE', '%s beginnt, Verstärkung zu beschwören!', '%s beginnt, Verstärkung zu beschwören!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('742', 'deDE', '%s breitet das Gedankenauge und die singenden Kristalle vor sich aus...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('743', 'deDE', 'Ich habe Yennikus Seelenstein hergestellt, $N...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('744', 'deDE', '%s versucht, Maurys Klumpfuß zu öffnen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('745', 'deDE', 'Pah! $N, dieser Fuß gibt nicht nach!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('746', 'deDE', 'Also gut, ich gehe! Hört auf zu schreien!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('747', 'deDE', 'Hoffentlich sind wir dann fertig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('749', 'deDE', 'Hierbei solltet Ihr mir lieber Rückendeckung geben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('750', 'deDE', 'Warum musste ausgerechnet ich dieses abstoßende und lachhafte Abbild eines Hexenmeisters am Hals haben?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('751', 'deDE', 'Macht Euch nützlich und helft mir hier aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('752', 'deDE', 'Lasst mich endlich frei! Mir reicht es!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('753', 'deDE', 'Was? Soll das heißen, Ihr könnt den hier nicht selbst töten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('754', 'deDE', 'Wenn Ihr mich noch einmal berührt, wird das das Letzte sein, was Ihr je berührt habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('757', 'deDE', '%s bekommt Risse und bricht auseinander.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('758', 'deDE', '%s verliert die Kraft, als Felsstücke abbrechen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('759', 'deDE', '%s wurde auf Geröll reduziert, kämpft jedoch weiter.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('761', 'deDE', 'Wie interessant... der Stein besitzt großes magisches Potenzial, allerdings in komplett roher Form...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('763', 'deDE', 'AH, EIN SCHMAUS! WER HAT DAS HIER GELASSEN...?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('764', 'deDE', 'SMOTTS! HE, SMOTTS! KOMMT HERAUS UND SPIELT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('765', 'deDE', null, 'Habt abermals Dank für Eure Hilfe. Möge Euch bei Euren Reisen der Wind immer im Rücken stehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('766', 'deDE', 'Ihr habt die Quest abgeschlossen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('767', 'deDE', 'Hier kommen die Bestien!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('768', 'deDE', 'Ihr habt es nicht geschafft, mich vor den Affen zu beschützen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('769', 'deDE', 'Was passiert hier? Welche Macht zieht mich zum Altar der Gezeiten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('770', 'deDE', 'Es sind noch mehr Bestien auf dem Weg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('771', 'deDE', 'Dreckige Bestien! Hier kommen sie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('772', 'deDE', 'DAS LETZTE MAL WART IHR SO EIN GUTER KERL... KOMMT RAUS!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('773', 'deDE', 'SMOTTS, ICH HABE EUREN KLEINEN GÜRTEL... KOMMT UND HOLT IHN EUCH.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('774', 'deDE', 'ICH VERSPRECHE, DIESMAL WERDE ICH EURE MANNSCHAFT NICHT TÖTEN. HA! ABER MEIN BRUDER KÖNNTE SIE AUFESSEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('775', 'deDE', 'KOMMT ZUR JANEIROSPITZE. ICH WARTE AUF EUCH, SMOTTS...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('776', 'deDE', 'ICH HABE EUREN KLEINEN GÜRTEL UM MEINEN FINGER GEWICKELT, SMOTTS. WOLLT IHR IHN NICHT ZURÜCKHABEN?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('777', 'deDE', 'HABT IHR KEINE SCHIFFE MEHR, DIE IHR SCHICKEN KÖNNT? HAT EUCH DER MUT VERLASSEN?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('778', 'deDE', 'LASST DIESE STATUE NICHT MEINE EINZIGE GESELLSCHAFT SEIN. KOMMT HERAUS UND SPIELT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('779', 'deDE', '%s stürzt voller Verzweiflung seine Flasche mit Gift hinunter!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('780', 'deDE', '%s holt eine Fernbedienung hervor und wirft eine Modell-Belagerungsmaschine um!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('781', 'deDE', '%s beginnt zu glühen und zu rauchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('782', 'deDE', 'Von der letzten Sprengstoff-Lieferung ist noch immer nichts zu sehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('783', 'deDE', 'Auch im Westen ist nichts von der letzten Sprengstoff-Lieferung zu sehen. Wo bleiben diese Trödler?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('784', 'deDE', 'Die Brücke sollte inzwischen längst zerstört worden sein. Wie lange brauchen diese faulen Hunde, um aus dem Hochland hierher zu kommen?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('785', 'deDE', 'Hilfe! Hilfe! Lasst nicht zu, dass sie mich auffressen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('786', 'deDE', 'Bitte rettet mich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('787', 'deDE', 'Ich will keine Ogermahlzeit werden! Bitte rettet mich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('788', 'deDE', 'Hiiiiiiiiiiiiiiiiiilfeeeeeeeeeeeeeee!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('789', 'deDE', 'Langsam habe ich diesen Unsinn satt! Gleich seid Ihr tot, $R!', 'Langsam habe ich diesen Unsinn satt! Gleich seid Ihr tot, $R!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('790', 'deDE', 'Es scheint mir, als wäre schon eine halbe Ewigkeit vergangen, seit mich jemand von meinem Posten abgelöst hat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('791', 'deDE', 'Tja, das habe ich nun davon, dass ich verschlafen habe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('792', 'deDE', null, '%s liest Sullys Brief Seite für Seite.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('793', 'deDE', null, 'Das ist nicht fair! Sully, mein Herz! Kommt zu mir zurück! Nein, $N! Warum brachtet Ihr mir solche Kunde?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('794', 'deDE', 'Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('795', 'deDE', 'Diese Splitter sind mir nicht geheuer. He Greeby, habt Ihr noch mehr Körner gefunden?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('796', 'deDE', 'Ich würde es Euch ohnehin nicht sagen! Denn dann käme ich von diesen Kristallen los...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('797', 'deDE', 'Genau, ich will nicht hier sein, wenn Myzrael aufwacht. Sie soll ziemlich mies drauf sein...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('798', 'deDE', 'Das war einfacher als ich dachte... das Siegelzeichen von Thoradin ist endlich wieder ganz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('799', 'deDE', 'Möge ich für immer in Eurem Schatten stehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('800', 'deDE', 'Meister, das Einzige, was Eurer Weisheit gleichkommt, ist Eure Größe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('801', 'deDE', 'Wenn ich für jede Eurer großen Taten ein Kupferstück bekommen hätte, dann wäre ich jetzt ein reicher Kobold!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('802', 'deDE', 'Ihr werdet uns vor der bösen Herrin schützen, ja!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('803', 'deDE', 'Oh, Lob! Durch Eure Wachsamkeit bleibt Myzrael tief unter der Erde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('804', 'deDE', 'Mein einziger Wunsch ist, Euch zu dienen, Großmächtiger, bis ich zu Euren Füßen sterbe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('805', 'deDE', 'Eure Stärke gleicht der eines Berges. Eure Anmut gleicht der des Windes!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('806', 'deDE', 'Meine Füße tun weh...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('807', 'deDE', 'Sterbt! Ihr werdet Myzrael nicht befreien!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('808', 'deDE', 'Der Großmächtige wird Euch zerschmettern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('809', 'deDE', 'Seid Ihr nicht ein wenig klein für Euresgleichen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('810', 'deDE', 'Mein Leben bedeutet gar nichts! Nur unsere heilige Aufgabe ist wichtig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('811', 'deDE', 'Wenn Ihr vorhabt, Myzrael zu befreien, dann werdet Ihr das noch bereuen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('812', 'deDE', 'Seid standhaft, Brüder. Und keine Sorge! Die Größe ist unser Trumpf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('814', 'deDE', 'Nehmt dies! Die Trockenstoppel werden siegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('815', 'deDE', 'Nein! Lasst uns! Wir müssen unsere Aufgabe erfüllen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('816', 'deDE', null, 'Gut, dann lasst uns beginnen. Je länger wir hier sind, desto mehr Schaden können die Untoten in Hillsbrad anrichten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('817', 'deDE', null, 'Gut, hier müssen wir wirklich achtsam sein. Haltet Euch bereit!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('818', 'deDE', null, 'Gebt mir Deckung! Ich schaue mal, was ich zwischen all diesem Gerümpel finden kann...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('819', 'deDE', null, '%s fängt an, die Habseligkeiten des Apothekers zu durchwühlen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('820', 'deDE', null, 'Hab was gefunden... glaube ich.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('821', 'deDE', null, 'Ich wette, Quae wird das für wichtig halten. Sie hat viel Ahnung von solchen Dingen... keine Expertin, aber viel Ahnung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('822', 'deDE', null, 'Okay, dann mal raus hier, und zwar schnell! Nehmt die Beine in die Hand, ich werde jetzt losrennen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('823', 'deDE', 'Ihr habt es geschafft!? Ich bin überrascht, dass Euch Kinelory nicht länger aufgehalten hat. Kin, hier sind Eure Socken. Gebt mir, was Ihr im Inneren gefunden habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('824', 'deDE', '%s betrachtet den Kreis aus Steinen und sucht nach Anzeichen von Beanspruchung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('825', 'deDE', 'Ihr werdet uns niemals besiegen!', 'Ihr werdet uns niemals besiegen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('826', 'deDE', 'Mein Tod hat wenig Bedeutung... der Hammer wird dennoch fallen!', 'Mein Tod hat wenig Bedeutung... der Hammer wird dennoch fallen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('827', 'deDE', 'Mein Leben für meine Meister. Freut Euch!', 'Mein Leben für meine Meister. Freut Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('828', 'deDE', 'Verflucht seid Ihr!', 'Verflucht seid Ihr!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('829', 'deDE', 'Ehre und Blut den Alten Göttern!', 'Ehre und Blut den Alten Göttern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('830', 'deDE', 'Meine Lords! Ich eile zu Euch!', 'Meine Lords! Ich eile zu Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('831', 'deDE', 'Euer Sieg hier bringt Euch nur Eurer Verdammnis näher!', 'Euer Sieg hier bringt Euch nur Eurer Verdammnis näher!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('832', 'deDE', 'Mama!', 'Mama!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('833', 'deDE', 'Eindringlinge! Tötet sie!', 'Eindringlinge! Tötet sie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('834', 'deDE', 'Für unsere Meister!', 'Für unsere Meister!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('835', 'deDE', 'Meister, dieser $R ist mein Geschenk für Euch!', 'Meister, dieser $R ist mein Geschenk für Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('836', 'deDE', 'Gemetzel! Möge ich in Seinem Namen Blut vergießen!', 'Gemetzel! Möge ich in Seinem Namen Blut vergießen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('837', 'deDE', 'Begrüßt Euer Ende!', 'Begrüßt Euer Ende!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('838', 'deDE', 'Kampf! Ich kann Eure Furcht riechen, $C!', 'Kampf! Ich kann Eure Furcht riechen, $C!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('839', 'deDE', 'Der Tod ist nah! Frohlocket!', 'Der Tod ist nah! Frohlocket!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('840', 'deDE', 'Eindringlinge! Gut, ich fing schon an, mich zu langweilen...', 'Eindringlinge! Gut, ich fing schon an, mich zu langweilen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('841', 'deDE', 'Halt! $C, Ihr seid so töricht! Wir können nicht zulassen, dass Ihr die Kreatur Myzrael beschwört!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('842', 'deDE', null, 'Was? $N, Ihr habt mir gut gedient, aber...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('843', 'deDE', null, 'Warum habt Ihr mich so bald beschworen? Ich habe meine volle Macht noch nicht erreicht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('844', 'deDE', null, 'Einerlei. Ihr wart dumm genug, mir zu helfen, und jetzt werdet Ihr dafür bezahlen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('845', 'deDE', 'Nun gut, $N. Folgt mir zu der Höhle. Dort werde ich versuchen, die Macht des Runensteins in diese Brille zu bannen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('846', 'deDE', 'Ich habe diese Höhle an unserem ersten Tag hier entdeckt. Ich glaube, dass wir die Energien in dem Stein zu unserem Vorteil nutzen können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('847', 'deDE', 'Ich werde jetzt anfangen, Energie aus dem Stein zu ziehen. Eure Aufgabe, $N, ist es, mich zu verteidigen. Dieser Ort ist verflucht... glaubt mir.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('848', 'deDE', '%s beginnt mit der Brille vor dem Stein herumzutüfteln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('849', 'deDE', 'Fast fertig! Nur noch ein bisschen länger!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('850', 'deDE', 'Ich habe es geschafft! Ich habe die Macht des Steins auf die Brille übertragen! Lasst uns von hier verschwinden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('851', 'deDE', 'Puh! Ich bin froh, dass ich aus dieser gruseligen Höhle zurück bin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('852', 'deDE', 'Pah! Wieder gescheitert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('853', 'deDE', '%s wird beim Verbannen von dieser Welt auseinander gerissen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('854', 'deDE', 'Ihr habt unsere Schätze schon viel zu lange geplündert. Macht Euch bereit, in Euer nasses Grab hinabzufahren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('855', 'deDE', 'Nichts kann uns aufhalten! Ihr werdet sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('856', 'deDE', '%s flüstert Shakes O\'Breen hektisch etwas zu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('857', 'deDE', 'Wartet, $N!!! Es sieht so aus, als würden wir hier Hilfe brauchen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('858', 'deDE', 'Hilfe!!! Ich sagte Euch doch, dass dieser Ort verflucht ist!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('859', 'deDE', 'Hilfe!!! Befreit mich von diesen Dingern, damit ich meine Arbeit machen kann!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('860', 'deDE', '%s untersucht sorgfältig Sara Balloos Notiz und lässt einen langen, ernsten Seufzer vernehmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('861', 'deDE', 'Was ist das für eine Stärke, die mich durchfließt? Ist dies die Macht des Schamanismus, von der Tor\'gan gesprochen hat...?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('862', 'deDE', 'Beeindruckend! Ich fühle mich... erneuert. Meine Kraft kehrt zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('863', 'deDE', 'Wenn wir sie jetzt festhalten können, dann haben wir es fast geschafft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('864', 'deDE', 'Jetzt geht es los...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('865', 'deDE', 'Ich werde weiter unten sichern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('866', 'deDE', 'Der Captain ist tot.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('867', 'deDE', 'Wir haben überlebt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('868', 'deDE', '%s blinzelt $N viel sagend zu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('869', 'deDE', '%s nickt $N zu und seufzt nachdenklich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('870', 'deDE', '%s durchsucht sein Gepäck...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('871', 'deDE', 'Ah! Da ist ja meine Ratsche! Dann wollen wir diese Rollenfragmente mal wieder anbringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('872', 'deDE', 'Das hätten wir, $N. Die Rolle ist repariert! Hurra!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('873', 'deDE', null, '%s beginnt, die Abzeichen der gefallenen Verteidiger von Stromgarde zu durchsuchen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('874', 'deDE', null, 'Gefreite Marcus, Tremane, Osgood, Lampret...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('875', 'deDE', null, 'Gefreite Fontane, MacDugal, Wilcott...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('876', 'deDE', null, 'Gefreite Theoric, Kitrin, Blythe, Agmond...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('877', 'deDE', null, 'Gefreite Horus, Tilling, Rockwell...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('878', 'deDE', null, '...Sampson, Yorick, Chamley...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('879', 'deDE', null, 'Clemens, Bartholemew, Enderman, Wyles...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('880', 'deDE', null, 'Tormil, Gamlor, Shefferd, Cowry...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('881', 'deDE', null, 'Martens, Reblock, Adinay, Nikeson...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('882', 'deDE', null, '%s seufzt...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('883', 'deDE', null, 'So viele Verluste. Und wir werden noch so viele mehr verlieren, bevor diese Schlacht ein Ende findet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('884', 'deDE', null, 'Habt Dank für diese Abzeichen, $N. Ich werde die Familien der Gefallenen benachrichtigen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('885', 'deDE', '%s bereitet einen Trank für $N zu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('886', 'deDE', 'Verflixt! Nein!!! Das kann... verflixt... nicht sein! Ihr habt mich... verflixt... mit Euren faulen Tricks betrogen! Verflixt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('887', 'deDE', '%s brutzelt eine Ladung scharfer Bussardflügel für $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('888', 'deDE', 'Der Alptraum von Ursal ist vorüber! Wir sind frei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('889', 'deDE', '%s überreicht Doktor Draxlegauge die glühende Brille.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('890', 'deDE', 'Doktor Draxlegauge wird Euch weitere Anweisungen geben, $N. Vielen Dank für Eure Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('891', 'deDE', null, '%s übergibt Quae ihr Pack.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('892', 'deDE', null, 'Wir haben es geschafft! Quae, wir haben es geschafft!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('893', 'deDE', 'Mann, die Fische beißen hier aber wirklich nicht. Vielleicht sollte ich hinüber zum Kristallsee gehen und dort mein Glück versuchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('894', 'deDE', 'Verdammt! Hier beißen die Fische auch nicht. Ich werde zurück an meinen alten Angelplatz gehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('895', 'deDE', 'Ihr werdet die Verlassenen niemals aufhalten, $R. Die dunkle Fürstin wird Euch leiden lassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('896', 'deDE', 'Ihr habt mein Wort darauf, dass ich für Euren Körper eine Verwendung finden werde, nachdem ich Euch getötet habe, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('897', 'deDE', null, 'Greift mich an, wenn Ihr wollt, aber Ihr werdet mich nicht davon abhalten, zu Quae zurückzukehren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('898', 'deDE', 'Ich bin in mehr als vier Sprachen gut bewandert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('899', 'deDE', 'Brzzz... klick... summ...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('900', 'deDE', 'Meister, wo sind all die Bäume hin?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('901', 'deDE', 'Wir schaffen es besser, stärker und schneller. Wir haben die Technologie. Wir haben die Magie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('902', 'deDE', 'Eines Tages werde ich ein richtiger Junge sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('903', 'deDE', 'Hier im Ödland kann es wirklich heiß werden. Aber wenigstens ist es trockene Hitze.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('904', 'deDE', 'Dingens... überprüft. Dingsda... überprüft. Dings... überprüft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('905', 'deDE', 'Hier ist es gefährlich, Meister. Doch ich werde helfen, Wache zu stehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('906', 'deDE', '%s beginnt, wild mit den Flügeln zu schlagen und laut zu kreischen. Ihr habt das Gefühl, dass Ihr die nächste Mahlzeit werden sollt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('907', 'deDE', '%s scheint jetzt nach dem Verzehr der Überreste von $N viel ruhiger zu sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('908', 'deDE', null, 'Die Waldgeister sind unruhig...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('909', 'deDE', null, 'Der Stamm der Schwarzfelle entweiht unsere heiligen Ländereien.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('910', 'deDE', null, 'Es muss etwas gegen den Stamm der Schwarzfelle unternommen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('911', 'deDE', null, 'Eine Verderbnis besudelt unseren heiligen Wald.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('912', 'deDE', null, 'Dem Stamm der Schwarzfelle darf nicht erlaubt werden, sich in die Entwicklung der Natur einzumischen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('913', 'deDE', null, 'Ich spüre Gefahr tief drinnen im Hain...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('914', 'deDE', '%s liest den alten Text, den $N für ihn geholt hat...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('915', 'deDE', 'Aha! Genau!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('916', 'deDE', 'Karnik, mein Freund, ich würd mir wünschen, ich könnt Euch wieder mit meinen eigenen Augen sehn, aber wir müssen über Ragnaros reden. Sein Einfluss wird immer größer.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('917', 'deDE', 'Hammertoe, Ihr seid so clever wie Ihr im Leben und sogar im Tod eigensinnig wart. Was habt Ihr mir zu berichten, alter Freund?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('918', 'deDE', 'Ich werde Euch beizeiten mehr erzählen, Karnik. Aber zuerst schickt diesen Reisenden zu Belgrum. Dort gibt es noch mehr, um die wir uns kümmern müssen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('919', 'deDE', '%s verstaut die Gegenstände, die $N ihm gegeben hat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('920', 'deDE', 'Ich danke Euch, $N. Meine Verbündeten, die damit Großartiges tun werden...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('921', 'deDE', '%s nimmt die Relikte von $N an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('922', 'deDE', 'Nehmt meinen Dank an, $N. Yagyins Tagebuch soll Euch gehören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('923', 'deDE', '%s kauert sich hin, um Yagyins Tagebuch zu lesen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('924', 'deDE', '%s wird von freigesetzter Magie getroffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('925', 'deDE', 'Nun gut, $N, dann lasst uns nachsehen, wo ich dieses rätselhafte Fossil gelassen habe. Folgt mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('926', 'deDE', 'Wo habe ich denn bloß dieses rätselhafte Fossil hingelegt? Ah, vielleicht da oben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('927', 'deDE', 'Hm, hier oben ist nichts.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('928', 'deDE', 'Hier ist kein rätselhaftes Fossil... Oh, aber meine Ausgabe von \"Die Grünen Hügel des Schlingendorntals\". Was für ein gutes Buch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('929', 'deDE', 'Ich wette, ich habe es im Zelt vergessen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('930', 'deDE', 'Moment, das ist Hollees Zelt... und es ist leer.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('931', 'deDE', 'Interessant... das ist mir bisher nicht aufgefallen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('932', 'deDE', '%s untersucht den alten, moosbedeckten Stein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('933', 'deDE', 'Halt! Ich soll nach diesem rätselhaften Fossil suchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('934', 'deDE', 'Au. Scharf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('935', 'deDE', 'Nein, hier hab ich das Fossil nicht gelassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('936', 'deDE', 'Ah, jetzt fällt es mir wieder ein! Ich habe Hollee das rätselhafte Fossil gegeben! Ich frage sie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('937', 'deDE', '%s geht zurück an die Arbeit und nimmt die Umgebung gar nicht wahr.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('938', 'deDE', 'Oh je! Was ist das? Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('939', 'deDE', 'D. $N! Von uns freigelegt! Ich brauche Hilfe beim Töten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('940', 'deDE', 'Helft mir! D. $N will mich einfach nicht loslassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('941', 'deDE', 'Ich habe so das Gefühl, dass d. $N auch hinter diesem rätselhaften Fossil her ist. Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('942', 'deDE', '$N! Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('943', 'deDE', 'Könnt Ihr d. $N von mir wegbekommen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('944', 'deDE', '%s beginnt, Energie aus der Erde zu ziehen', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('945', 'deDE', '%s starrt nach Nordwesten und lässt ein langes, tiefes Heulen ertönen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('946', 'deDE', '%s lässt ein mächtiges Heulen ertönen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('947', 'deDE', 'Ich habe Euch erwartet, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('948', 'deDE', 'Jetzt testen wir meine Konstruktion!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('949', 'deDE', 'Oh nein! Das Ding ist außer Kontrolle! Rennt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('950', 'deDE', 'Ich sollte wohl besser etwas dagegen unternehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('951', 'deDE', 'Meister beschützen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('952', 'deDE', '%s beginnt einen Ritus der Erschaffung...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('953', 'deDE', 'Es ist vollbracht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('954', 'deDE', 'Das Totem ist fertig, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('955', 'deDE', '%s hält die Gegenstände hoch, die $N ihm gegeben hat...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('956', 'deDE', 'Ragnaros? Das sind in der Tat schreckliche Neuigkeiten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('957', 'deDE', 'Geht schnell... <hust hust> Findet... <hust> Ryedol...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('958', 'deDE', 'Ihr habt ihn gehört, Reisender. Sprecht mich wieder an, wenn Ihr bereit seid, Euch mit Belgrum zu unterhalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('959', 'deDE', '%s sieht nervös zu Lotwil hinüber.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('960', 'deDE', 'Hallo, ich bin wegen der Ausbildung hier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('961', 'deDE', 'Wann wird endlich jemand etwas gegen die Untoten unternehmen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('962', 'deDE', 'Wo sind denn alle?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('963', 'deDE', 'Ich kann nicht einmal mehr das Grab meines Vaters in Ruhe aufsuchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('964', 'deDE', 'Warum sind die Wachen aus Sturmwind nicht gekommen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('965', 'deDE', 'Ich befürchte, dass etwas Dunkles im Anmarsch ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('966', 'deDE', 'Das Licht scheint uns im Stich gelassen zu haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('967', 'deDE', 'Viel Spaß in Booty Bay, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('968', 'deDE', 'Genießt die Reise nach Ratchet, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('969', 'deDE', 'Ab nach Orgrimmar mit Euch, $N. Grüßt Thrall von mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('970', 'deDE', 'Ab nach Unterstadt mit Euch, $N! Und nehmt Euch vor der Seuche in Acht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('971', 'deDE', 'Ab nach Grom\'gol mit Euch, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('972', 'deDE', 'Ab nach Orgrimmar mit Euch, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('973', 'deDE', 'Ab nach Grom\'gol mit Euch, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('974', 'deDE', 'Ab nach Unterstadt mit Euch, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('975', 'deDE', '%s durchsucht seine Taschen nach der richtigen Zauberkomponente.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('976', 'deDE', 'WER WAGT ES?! Ihr sollt vernichtet werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('977', 'deDE', 'HALLO WELT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('978', 'deDE', 'murmel murmel...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('979', 'deDE', '%s fällt zu Boden. Sein Geist wird mit seinem letzten Atemzug freigesetzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('980', 'deDE', 'Tretet zurück von den Sachen des Lieutenants!', 'Tretet zurück von den Sachen des Lieutenants!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('981', 'deDE', '%s hört sich den Bericht von $N an...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('982', 'deDE', 'Was? Die Burning Blade breiten sich aus? Wir müssen das untersuchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('983', 'deDE', 'Ich danke Euch, $N. Und mein Bruder dankt Euch ebenfalls.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('984', 'deDE', 'Zerschlägt die tote, verdrehte Klaue.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('985', 'deDE', 'Wir werden keinen Dämonen-Diener in unseren Ländern dulden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('986', 'deDE', '%s fällt in Trance...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('987', 'deDE', '%s wird von freigesetzter Magie verbrannt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('988', 'deDE', '$N, die Geheimnisse dieser Halsbänder zu entschlüsseln, übersteigt meine Fertigkeiten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('989', 'deDE', 'Argh!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('990', 'deDE', '%s untersucht das Senghalsband...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('991', 'deDE', 'Interessant, sehr interessant. Habt Ihr das in Durotar gefunden, $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('992', 'deDE', 'Das Halsband ist tatsächlich mit dämonischer Stärke erfüllt, $N. Das ist ein schlechtes Zeichen für unser Volk.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('993', 'deDE', 'Hm... Ihr habt gut daran getan, das zu mir zu bringen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('994', 'deDE', 'Dies ist ein gefährliches Ding, $N. Ich bin froh, dass Ihr mich darauf aufmerksam gemacht habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('995', 'deDE', 'Frisches Brot! Hier bekommt ihr ofenfrisches Brot!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('996', 'deDE', 'Holt Euch davon, solange sie noch schön frisch sind! Hier gibt es Brote!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('997', 'deDE', 'Hier gibt es alle Sorten von Brot! Heute frisch gebacken! Holt Euch davon, solange es noch welches gibt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('998', 'deDE', 'Hier gibt es Brot! Euer Lieblingsbrot! Schön frisch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('999', 'deDE', '%s fällt in Trance...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1000', 'deDE', '%s verwendet die Opfergabe von $N in einem Ritual...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1001', 'deDE', '%s nimmt die Opfergabe von $N an und beginnt zu singen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1002', 'deDE', '%s zieht Macht auf sich...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1003', 'deDE', '%s verwandelt die Gegenstände von $N in ein Totem der Säuberung...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1004', 'deDE', '%s erschafft ein neues Totem...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1005', 'deDE', 'Ich habe das Totem der Säuberung, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1006', 'deDE', 'Das Totem ist fertig. Der Brunnen kann nun gesäubert werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1007', 'deDE', '$N, ich habe das Säuberungstotem erschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1008', 'deDE', 'Es ist vollbracht. Das Totem ist fertig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1009', 'deDE', 'Ich habe das Totem erschaffen. Ihr, $N, müsst den Brunnen säubern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1010', 'deDE', 'Die von Euch dargebotenen Gegenstände waren rein, $N. Das neue Säuberungstotem ist stark.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1011', 'deDE', '%s beginnt zu singen, während er die Quellsteine und das Bernsteinkorn vor dem Stammesfeuer vermischt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1012', 'deDE', 'Das Wasser der Seher steht für Euch zum Trinken bereit, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1013', 'deDE', 'Nehmt eine Kerze des Heranlockens aus dieser Kiste, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1014', 'deDE', 'Mein Schwert Archeus hat mir in meinem Leben gut gedient. Aber nun, da mein Geist mein glückloses Dasein verlässt, brauche ich es nicht länger.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1015', 'deDE', 'Ich werde mich an die Liebe meiner Tochter klammern und hoffen, dass mir im Licht meine Sünden vergeben werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1016', 'deDE', '%s wird ganz benommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1017', 'deDE', 'Ich wurde ermordet! Beschwört Verog!', 'Ich wurde ermordet! Beschwört Verog!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1018', 'deDE', 'Dunkle Verdammnis', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1019', 'deDE', 'Dunkle Verdammnis', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1020', 'deDE', '%s sieht, wie sein Blut in Wallung gerät und die Arglist des Raptors ihn durchströmt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1021', 'deDE', '%s schleift die Perlen, die $N ihm gegeben hat...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1022', 'deDE', 'Vorsicht! D. $N greift uns an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1023', 'deDE', 'Na bitte. Eine frische Ladung Blitzstrahlbomben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1024', 'deDE', 'Nimm dies, Bestie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1025', 'deDE', '%s wirft Dynamit in die Luft, das in der Nähe landet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1026', 'deDE', 'Die Macht der Träne der Monde gehört jetzt mir! Mir ganz allein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1027', 'deDE', '%s beginnt, die Träne der Monde zu reiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1028', 'deDE', 'Macht! Glorreiche Macht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1029', 'deDE', '%s beginnt, seltsame Grunzgeräusche von sich zu geben. Die Träne der Monde fällt zu Boden und zerspringt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1030', 'deDE', 'Hoo!... Hoo!... Ahh! Das war knapp. Uups!... Oh, dieser Schmerz, was für ein Schmerz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1031', 'deDE', 'Schön, schön! Ich werde schon herausfinden, wie dieses Ding funktioniert...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1032', 'deDE', 'Gut, ich denke, jetzt habe ich es. Folgt mir, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1033', 'deDE', 'Sieht aus, als hätten wir den Wald verlassen, hm? Was das wohl macht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1034', 'deDE', 'Ich muss das hier nach Ratchet bringen und nichts wird mich aufhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1035', 'deDE', 'Hilfe! Wartet! Es ist, als hätte dieses Ding einen eigenen Willen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1036', 'deDE', 'Mal sehen, wie Euch eine Kostprobe davon gefällt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1037', 'deDE', 'Ich werde sterben, ich weiß es einfach!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1038', 'deDE', 'Nehmt dies! Oh... ich muss wohl auf den falschen Knopf gedrückt haben... Wartet, versuchen wir es mit diesem!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1039', 'deDE', 'Arrrgh! Das ist nicht richtig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1040', 'deDE', 'Da ist der gestohlene Schredder! Hört damit auf, sonst wird Lugwizzle uns das Fell über die Ohren ziehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1041', 'deDE', 'Das war knapp, $N! Machen wir uns auf den Weg, es ist noch weit bis Ratchet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1043', 'deDE', 'Kommt schon, lass mich nicht im Stich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1044', 'deDE', 'Hm... ich glaube nicht, dass dieses rote Licht etwas Gutes bedeutet...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1045', 'deDE', 'Sieht ganz so aus, als müsstet Ihr nach Ratchet vorausgehen und Sputtervalve erzählen, dass ich den Schredder ruiniert habe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1046', 'deDE', 'Ich bleibe hier und bewache den Trümmerhaufen! Beeilt Euch! Hoffentlich bemerkt niemand den Rauch...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1047', 'deDE', '%s schimmert, blitzt auf... und ist nicht mehr da!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1048', 'deDE', '%s ruft weitere Verbündete aus den Schatten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1049', 'deDE', '%s platzt vor Wut, als er in Raserei verfällt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1051', 'deDE', 'Fregatte, ahoi! Das ist ein Orc-Schiff, keine Frage! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1052', 'deDE', 'Was ist das da am Horizont? Ganz sicher ein feindliches Schiff! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1053', 'deDE', 'Segel am östlichen Horizont. Eindringlinge, zweifellos! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1054', 'deDE', 'Da draußen ist irgendwas. Löst Alarm aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1055', 'deDE', 'Was war das? Ein Meeresriese? Vorsicht ist besser als Nachsicht. Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1056', 'deDE', 'Feind ahoi! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1057', 'deDE', 'Feindliche Flotte voraus! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1058', 'deDE', 'Marodeure im Norden! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1059', 'deDE', 'Eindringlinge aus dem Landesinneren! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1060', 'deDE', 'Feind in Sicht! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1061', 'deDE', 'Räuber aus Nordwesten! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1062', 'deDE', 'Ein Räubertrupp! Dort drüben! Feuer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1063', 'deDE', '%s hört den Hilferuf und macht sich für den Kampf bereit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1064', 'deDE', '%s ruft um Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1065', 'deDE', 'Bleibt in der Nähe, $N. Ich werde jede erdenkliche Hilfe brauchen, um hier rauszukommen. Los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1066', 'deDE', 'Endlich! Aus Northwatch entkommen! Ich muss erst einmal Atem holen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1067', 'deDE', 'Jetzt fühle ich mich besser. Gehen wir zurück nach Ratchet. Kommt schon, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1068', 'deDE', 'Sieht ganz so aus, als hätten sich die Südmeerfreibeuter an der Küste schwer verschanzt. Das könnte hart werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1069', 'deDE', 'Schon fast zurück in Ratchet! Lasst uns das Tempo halten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1070', 'deDE', 'Ah, die köstliche Salzluft von Ratchet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1071', 'deDE', 'Captain Brightsun, $N hat mich befreit! $N, der Captain wird Eure Tapferkeit sicher belohnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1072', 'deDE', '$N kommt direkt auf uns zu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1073', 'deDE', 'Helft mir, d. $N von mir zu bekommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1074', 'deDE', 'Hilfe! D. $N greift uns an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1075', 'deDE', '$N in schnellem Anmarsch! Zeit für Rache!', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen.$B$BNichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen \"auszuborgen\". Ich habe Wizzlecrank ins schlammige Moor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1076', 'deDE', '%s legt die Sporen zu seinen alchimistischen Instrumenten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1077', 'deDE', 'Oh, gut. Diese Sporen waren sehr nützlich. Danke, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1078', 'deDE', 'Danke für die Sporen, $N. Sie sind jetzt transportbereit.', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen.$B$BNichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen \"auszuborgen\". Ich habe Wizzlecrank ins schlammige Moor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1079', 'deDE', 'Ich werde beschworen! Eindringlinge, kommt zu meinem Zelt, in den sicheren Tod!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1080', 'deDE', '%s gräbt sich aus dem Sand heraus, um die Eier zu beschützen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1081', 'deDE', 'Danke, $N. Ich werde diese Getränke im Handumdrehen verkaufen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1082', 'deDE', 'Danke vielmals, $N, Diese Hörner werden mir ein Vermögen einbringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1083', 'deDE', 'Mit diesen Hörnern werde ich ein Vermögen machen, $N. Danke!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1084', 'deDE', 'Danke! Und wenn ich die Getränke zubereite... sagt niemandem, woraus sie bestehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1085', 'deDE', '%s untersucht die Schnappkieferschalen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1086', 'deDE', 'Los, lasst es uns woanders versuchen.', 'Los, lasst es uns woanders versuchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1087', 'deDE', 'Hm...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1088', 'deDE', 'Seltsam. Sehr seltsam...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1089', 'deDE', '$N. Diese Schalen sagen viel aus, aber ich fürchte, es stellen sich nun noch viel mehr Fragen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1090', 'deDE', 'Los, Jungs, versuchen wir es auf der anderen Seite.', 'Los, Jungs, versuchen wir es auf der anderen Seite.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1091', 'deDE', 'Billy sagt, dass Fizzles einst ein großer Hexer war. Aber er wurde in einen Hasen verwandelt, als einer seiner Zauber fehl schlug.', 'Billy sagt, dass Fizzles einst ein großer Hexer war. Aber er wurde in einen Hasen verwandelt, als einer seiner Zauber fehl schlug.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1092', 'deDE', 'Sind wir endlich da?', 'Sind wir endlich da?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1093', 'deDE', 'Warum gehen wir hier \'lang?', 'Warum gehen wir hier \'lang?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1094', 'deDE', 'Wohin gehen wir?', 'Wohin gehen wir?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1095', 'deDE', 'Ich muss mal.', 'Ich muss mal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1097', 'deDE', 'Ich will den Magierturm sehen.', 'Ich will den Magierturm sehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1098', 'deDE', 'Stimmt das? Sind in den Kanälen wirklich Krokiliske?', 'Stimmt das? Sind in den Kanälen wirklich Krokiliske?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1099', 'deDE', 'Meine Füße tun weh.', 'Meine Füße tun weh.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1100', 'deDE', 'Warum müssen wir immer den gleichen Weg gehen?', 'Warum müssen wir immer den gleichen Weg gehen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1101', 'deDE', 'Frisches Brot zu verkaufen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1102', 'deDE', 'Warmes, bekömmliches Brot!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1103', 'deDE', 'Brötchen, Teilchen und Brot. Frisch gebacken!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1104', 'deDE', 'Frisch gebackenes Brot zu verkaufen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1105', 'deDE', null, 'Wo ist das Wasser, Emma? Hast du Wasser geholt, Emma? Wenn ich nicht wäre, würd\' das Pack gar nicht wissen, wie Wasser aussieht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1106', 'deDE', null, 'Als ob ich auf meine alten Tage nichts Besseres zu tun hätte, als Wassereimer zu schleppen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1107', 'deDE', null, 'Mir scheint\'s, als ginge ich hundert Mal am Tag den ganzen Weg zum Brunnen, um immer noch mehr Wasser zu holen. Keinen Respekt vor den Alten, ich sag\'s Euch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1108', 'deDE', null, 'Natürlich rede ich mit mir selbst. Das ist die einzige Möglichkeit, in dieser Stadt ein nettes Gespräch zu führen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1109', 'deDE', null, 'Eines Tages werde ich ihn in dieser blauen Robe ertränken. Und mit ihm all seine Besen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1110', 'deDE', null, 'Keiner hilft mir! Ich muss hier das ganze Wasser schleppen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1111', 'deDE', null, 'Glaube, ich laufe langsam eine Rille in die Pflastersteine.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1112', 'deDE', null, 'Déjà-vu. Für einen Moment dachte ich, ich wäre zu Hause... vor der Seuche...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1113', 'deDE', 'Oh, jetzt habt Ihr mich wütend gemacht! Es ist an der Zeit, die wahre Macht des Steins der Gezeiten zu erkennen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1114', 'deDE', '%s wechselt leise einige Worte mit $N und übergibt $Gihm:ihr; ein kleines Objekt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1115', 'deDE', 'Nehmt einen Stein vom Tisch, $N. Ihr werdet ihn bei Eurer Aufgabe brauchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1116', 'deDE', null, 'Bei der Kraft der Erde, der Macht der Stürme und...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1117', 'deDE', null, 'ARRRRRGGGHHH!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1118', 'deDE', null, 'Was ist passiert, Erdenmutter? Warum ist der Zauber misslungen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1119', 'deDE', 'Gut, hier bin ich. Kommt und kauft meine Waren, solange noch was davon da ist. Ich meine, noch habt Ihr die freie Auswahl!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1120', 'deDE', null, 'Sergra fällt für einen Moment in Trance und schaut dann zum Himmel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1121', 'deDE', null, 'Ich verstehe, Erdenmutter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1122', 'deDE', 'Wohlan denn. Da Ihr meine Sachen nicht aufkaufen wollt, sehe ich mich gezwungen, mir meinen Lebensunterhalt in der Stadt Eurer Feinde zu verdienen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1123', 'deDE', 'Jetzt bin ich aber echt enttäuscht. Ich dachte, Ihr wärt besser als die Leute in Süderstade. Die hätten alle meine Waren gekauft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1124', 'deDE', null, 'Erdenmutter, gewähre mir die Kraft, dieses Blutvergießen ungeschehen zu machen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1125', 'deDE', null, 'Das Gleichgewicht wurde wiederhergestellt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1126', 'deDE', '%s untersucht das moosumschlungene Herz...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1127', 'deDE', '$N, unter dem Moos sieht dieses Herz wie ein Saatkorn aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1128', 'deDE', 'Pflanzen wir ihn doch in meine besondere, vorbereitete Erde...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1129', 'deDE', null, 'Hier haben wir die Kathedrale des Lichts, Sturmwinds Zentrum spiritueller Erleuchtung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1130', 'deDE', 'Warum müssen wir das hier überhaupt lernen?', 'Warum müssen wir das hier überhaupt lernen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1131', 'deDE', 'Das ist besser als die Zeichnungen in den Geschichtsfolianten.', 'Das ist besser als die Zeichnungen in den Geschichtsfolianten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1132', 'deDE', 'Ich will das Drachenskelett in der großen Bibliothek sehen.', 'Ich will das Drachenskelett in der großen Bibliothek sehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1133', 'deDE', 'Wann können wir den glänzenden Magierturm sehen?', 'Wann können wir den glänzenden Magierturm sehen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1134', 'deDE', 'Ich habe gehört, es sind noch immer Orcs darunter begraben.', 'Ich habe gehört, es sind noch immer Orcs darunter begraben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1136', 'deDE', 'Ich muss mal!', 'Ich muss mal!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1138', 'deDE', 'Gibt es Nachtelfen in Sturmwind? Ich habe noch nie einen Nachtelfen gesehen.', 'Gibt es Nachtelfen in Sturmwind? Ich habe noch nie einen Nachtelfen gesehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1139', 'deDE', 'Der schubst mich immer!', 'Der schubst mich immer!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1140', 'deDE', null, 'Hier haben wir Burg Sturmwind. Gebaut auf den Ruinen von Schloss Sturmwind, das während des ersten Großen Kriegs von der Horde zerstört wurde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1141', 'deDE', null, 'TEST', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1142', 'deDE', 'He, Pöbel, fangt an zu arbeiten. Das Schiff muss bald fertig sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1143', 'deDE', 'Eindringlinge! Tötet sie! Lasst sie nicht in den Raum!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1144', 'deDE', 'Wenn ich das nächste Mal einen von euch beim Faulenzen erwische, lasse ich ihm von Sauser den Kopf abschneiden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1145', 'deDE', 'Die Kanonen müssen bald fertig sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1146', 'deDE', 'Bringt diese Teile zum Schiff hinunter!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1147', 'deDE', 'Will hier jemand eine Pause machen? Tja, das ist Pech! An die Arbeit, ihr Tölpel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1148', 'deDE', 'Ihr da! Seht mal nach, was das für ein Lärm ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1149', 'deDE', 'Wir werden angegriffen! Los, Ihr Hunde! Wehrt die Eindringlinge ab!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1150', 'deDE', '%s versucht zu flüchten!', '%s versucht zu flüchten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1151', 'deDE', '%s dreht durch, als ein Freund im Kampf fällt!', '%s dreht durch, als ein Freund im Kampf fällt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1152', 'deDE', '%s pflanzt das moosumschlungene Herz...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1153', 'deDE', 'Stimmt es, dass die Paladine hier trainieren?', 'Stimmt es, dass die Paladine hier trainieren?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1154', 'deDE', null, 'Ja, das ist wahr. Sowohl Paladine als auch Priester üben ihre Fertigkeiten und erforschen die großen Wahrheiten hinter den Mauern der Kathedrale.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1155', 'deDE', null, 'Wenn ihr mir bitte folgen wollt, Kinder. Wir werden gleich die Burg sehen, in der König Anduin Wrynn höchstpersönlich auf seinem Thron sitzt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1156', 'deDE', '%s entfernt die Samen aus der glühenden Frucht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1157', 'deDE', 'Dann wollen wir doch mal sehen, wie diese Samen in meiner vorbereiteten Erde wachsen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1158', 'deDE', '%s pflanzt die Fruchtsamen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1159', 'deDE', '%s eilt zu seinem Blumentopf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1160', 'deDE', 'Hoffentlich wurzelt dieser Wedel bald...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1161', 'deDE', null, 'Als die Horde zerschlagen wurde, kehrten die Mannen hierher zurück und begannen, diese einst so herrliche Stadt als Beweis unseres eigenen Überlebens wieder aufzubauen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1162', 'deDE', null, 'Ja, gut... lasst uns jetzt zu dem Monument gehen, das den Helden der beiden Großen Kriege gewidmet ist, dem Tal der Helden. Folgt mir.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1163', 'deDE', null, 'Ist das nicht erstaunlich, Kinder? Jeder, der die Stadt betritt, muss die wachsamen Augen der größten Helden unseres Landes passieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1165', 'deDE', null, 'Atemberaubend. Kinder, wenn wir in der Schule zurück sind, wird jeder von euch einen mündlichen Bericht über eine dieser Legenden abgeben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1166', 'deDE', null, 'Werft noch einen Blick darauf, bevor wir zum heiligen Distrikt und zur großen Kathedrale des Lichts aufbrechen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1167', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1168', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1169', 'deDE', null, 'Oh nein... Nebel... was ist passiert...?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1174', 'deDE', '%s bricht aus der Erde hervor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1175', 'deDE', '%s bricht aus der Erde hervor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1176', 'deDE', 'TÖTEN!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1177', 'deDE', '%s folgt $N fröhlich als $Gsein:ihr; neuer Setzling.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1178', 'deDE', 'Ich werde Euch beschützen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1179', 'deDE', '%s beginnt, den Stein von Relu über den beiden Fossilien zu benutzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1180', 'deDE', 'Bei den Sternen! Es wurde ein Geist beschworen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1181', 'deDE', 'Es ist in der Tat ein Rätsel aus der Vergangenheit! Aber auch ein Schlüssel zu unserer Zukunft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1182', 'deDE', 'Wer hat Aman heraufbeschworen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1183', 'deDE', 'Ah, Ihr beschäftigt Euch mit den Relikten der Vergangenheit, wie ich sehe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1184', 'deDE', 'Seid gewarnt, auch Eure Schöpfer sind fehlbar.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1185', 'deDE', 'Grabt Ihr zu tief in Eurer Vergangenheit, könnte das Eurer Zukunft ein abruptes Ende bringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1186', 'deDE', null, '%s versteckt sich in den Büschen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1187', 'deDE', '%s löst sich vor Euren Augen auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1188', 'deDE', null, 'Den Rest des Wegs schaffe ich, $N. HABT DANK!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1189', 'deDE', null, 'Gut, gehen wir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1190', 'deDE', null, '%s verschwindet im Wald.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1191', 'deDE', '%s gerät in Raserei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1192', 'deDE', 'Fragt mich, wenn Ihr wissen wollt, wie man im Blauen Eremiten ein Getränk umsonst bekommt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1193', 'deDE', 'Kostenlose Getränke im Blauen Eremiten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1194', 'deDE', 'Auf zum Blauen Eremiten. Dort kennt jeder Euren Namen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1195', 'deDE', 'Die besten Getränke von ganz Sturmwind!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1196', 'deDE', 'Gar köstlich ist das Bier und gemütlich ist es auch noch hier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1197', 'deDE', 'Die magischen Studien belasten Euer Gehirn? Entspannt Euch im Blauen Eremiten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1198', 'deDE', 'Kommt in den Blauen Eremiten, den einzigen Ort in Sturmwind, wo sich Magie und Hochgeistiges vermischen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1199', 'deDE', 'Seid Ihr deprimiert? Kommt zum Blauen Eremiten und habt Spaß!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1200', 'deDE', null, 'Ist Euch warm? Dann besorgt Euch im Pfeifenden Schwein etwas zum Abkühlen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1201', 'deDE', null, 'Nächtliche Schlägereien und großartige Getränke! Das bekommt Ihr alles im Pfeifenden Schwein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1202', 'deDE', null, 'Holt Euch ein Freibier im Pfeifenden Schwein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1203', 'deDE', null, 'Redet mit mir bei einem Freibier im Pfeifenden Schwein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1205', 'deDE', null, 'Geschickter als ein Elf, stärker als ein Orc. Es gibt keine besseren Getränke in ganz Sturmwind!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1206', 'deDE', null, 'Seid Ihr dieser ganzen laschen Zauberer-Getränke überdrüssig? Versucht mal was Stärkeres im Pfeifenden Schwein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1207', 'deDE', null, 'Zurück von einer langen Reise? Ruht Euch im Pfeifenden Schwein aus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1208', 'deDE', null, 'Die besten Getränke von ganz Sturmwind!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1209', 'deDE', '%s hat Hunger...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1210', 'deDE', 'Die Cliffspring Falls erstrecken sich entlang der Berge nach Osten und Norden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1211', 'deDE', 'Danke, $N. Und viel Glück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1212', 'deDE', 'Hier gibt es die besten Waffen des ganzen Brachlandes!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1213', 'deDE', 'Tretet ruhig näher! Hier findet Ihr die beste Auswahl an Waffen diesseits von Ashenvale!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1214', 'deDE', 'Lizzarik hat Klingen. Lizzarik hat Streitkolben. Lizzarik erfüllt Euren gesamten Waffenbedarf genau hier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1215', 'deDE', 'Handelt schnell! Wenn es einmal weg ist, ist es weg! Bringt Eure Waffen her, sofort!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1216', 'deDE', '%s betrachtet das Pergament aus dem Twilight-Foliant...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1217', 'deDE', '%s wird von freigesetzter Magie getroffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1218', 'deDE', 'Hmmm...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1219', 'deDE', '%s setzt seine Meditation fort...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1221', 'deDE', '%s beugt sich nach unten, um seinen Schuss zu inspizieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1222', 'deDE', 'Uthrok machen gut Schuss.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1223', 'deDE', 'Athrikus zeigt auf Euch und brüllt dabei so laut, dass Euch fast das Trommelfell platzt. Sein Gebrüll ist das Letzte, was Ihr hört.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1224', 'deDE', null, 'Endlich kann meine Seele ruhen... Oh, teuerster Cerellean...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1225', 'deDE', '%s blickt nach Südosten und wimmert, bevor Ihr wieder in Augenschein genommen werdet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1226', 'deDE', '%s brüllt Terenthis an, um seine Aufmerksamkeit zu erregen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1227', 'deDE', 'Grimmklaue?! Ganz ruhig, mein Freund... wo ist Euer Meister Volcor?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1228', 'deDE', '%s beginnt in Terenthis\' Richtung zu heulen und zu brüllen, und stampft mit seinen Pfoten auf den Boden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1229', 'deDE', 'Hoh, hoh, mein Freund. Einen Moment...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1230', 'deDE', 'So... das sollte helfen. Jetzt sagt mir, was geschehen ist, Grimmklaue.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1231', 'deDE', '%s brüllt Terenthis weiterhin an, doch dieses Mal scheint der Druide den Bären zu verstehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1232', 'deDE', 'Ich verstehe, mein Freund. Ich werde jemanden finden, der Eurem Meister helfen kann. Geht jetzt zu ihm zurück oder bleibt wenigstens in seiner Nähe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1233', 'deDE', '%s brüllt dankbar in Terenthis\' Richtung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1234', 'deDE', '%s knurrt Euch an und beschnüffelt Euch anschließend.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1235', 'deDE', 'Ich nahm an, Flagongut würde irgendwann jemanden nach diesem Fossil ausschicken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1236', 'deDE', 'Ich danke Euch nochmals, $N. Ich mache mich jetzt auf den Weg zur Straße. Wenn Ihr könnt, sucht Terenthis und lasst ihn wissen, dass wir entkommen sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1237', 'deDE', 'Tja, jetzt oder nie, würde ich sagen. Denkt daran, zu Terenthis zurückzukehren und ihn wissen zu lassen, dass wir entkommen sind, wenn wir die Straße sicher erreichen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1238', 'deDE', '%s brüllt, als sich seine Gestalt verändert, und rennt fort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1239', 'deDE', 'Lasst uns nun diese seltene Erde in meinen Blumentopf füllen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1240', 'deDE', 'Ich danke Euch nochmals, $N. Diese seltene Erde wird mir bei meinen Experimenten von großem Nutzen sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1241', 'deDE', '%s brüllt vor Aufregung, während er auf Volcor zustürmt. Nachdem er seinen Meister erreicht hat, leckt Grimmklaue ihm das Gesicht ab.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1242', 'deDE', 'Ihr seid entlassen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1243', 'deDE', 'Wir haben es geschafft. Denkt daran, Terenthis aufzusuchen und ihm zu sagen, dass wir in Sicherheit sind. Nochmals vielen Dank.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1244', 'deDE', 'Ich bin auch froh, Euch zu sehen, Grim! Anscheinend muss man doch nicht alle Fremden fürchten, was, Grim? Terenthis wird solche Abenteurer gut belohnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1245', 'deDE', 'Was war das für ein Krach? Es klang wie Gebrüll.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1246', 'deDE', '%s beginnt, einen Zauber auf Grimmklaue zu wirken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1247', 'deDE', 'Ich sollte jetzt sofort beginnen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1248', 'deDE', 'So... wo sind Nadel und Faden? Ah! Da sind sie ja...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1249', 'deDE', '%s beginnt, einen Zauber zu wirken, als Ihr ihm den Umhang abnehmt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1250', 'deDE', 'Hier kommen sie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1251', 'deDE', 'Wir können diese üblen Kreaturen besiegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1252', 'deDE', 'Wir verdienen zumindest unseren Tod!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1253', 'deDE', 'Gebt nicht auf! Kämpft bis zum Tode!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1254', 'deDE', '%s attackiert!', '%s attackiert!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1255', 'deDE', 'Ich muss die notwendigen Vorbereitungen treffen, bevor das Erweckungsritual beginnen kann. Ihr müsst mich beschützen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1256', 'deDE', 'Diese Höhlen waren einst ein Tempel, der eine Wiederbegrünung des Ödlandes versprach. Jetzt sind sie ein einziger Alptraum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1257', 'deDE', 'Kommt. Wir müssen weitermachen. Es muss noch viel getan werden, bevor wir Naralex aus seinem Alptraum erwecken können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1258', 'deDE', 'Innerhalb dieses Feuerkreises muss ich den Zauber wirken, der die Geister der getöteten Fanglords verbannt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1259', 'deDE', 'Die Höhlen wurden geläutert. Auf zu Naralex\' Kammer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1260', 'deDE', 'Hier entlang. Seid vorsichtig, Freunde. Ich spüre, wie sich Naralex in seinem unruhigen Schlaf umherwirft...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1261', 'deDE', 'Aus diesem Quell werde ich die heiligen Wasser schöpfen, mit denen ich das Blut der gefallenen Druiden vermischen werde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1262', 'deDE', 'Nun habe ich, was wir brauchen. Lasst uns weitermachen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1263', 'deDE', 'Am Ende dieses Ganges liegt Naralex in unruhigem Schlaf. Wir sollten ihn wecken, bevor es zu spät ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1264', 'deDE', 'Beschützt mich, tapfere Seelen, während ich in den Smaragdgrünen Traum eintauche, um Naralex zu retten und dieser Verderbnis ein Ende zu setzen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1265', 'deDE', '%s beginnt, das Erweckungsritual an Naralex auszuführen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1266', 'deDE', '[PH] This is a placeholder action until i can cancel channeling my spell.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1267', 'deDE', 'Endlich! Naralex erwacht aus dem Alptraum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1268', 'deDE', '%s wirft sich in unruhigem Schlaf hin und her.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1269', 'deDE', '%s windet sich vor Schmerzen. Der Jünger scheint sich einen Weg zu bahnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1270', 'deDE', '%s hat eine entsetzliche Vision. Irgendetwas bewegt sich in dem trüben Wasser.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1271', 'deDE', 'Endlich bin ich wach!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1272', 'deDE', 'Ah, wie gut, aus dem schrecklichen Alptraum erwacht zu sein! Ich danke Euch, mein treuer Jünger, und Euren tapferen Gefährten ebenso.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1273', 'deDE', 'D. $N greift mich an! Helft mir, es loszuwerden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1274', 'deDE', 'Da, d. $N! Kümmert Euch darum! Ich muss mich darauf vorbereiten, Naralex zu erwecken!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1275', 'deDE', 'Ich werde mit d. $N allein nicht fertig! Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1276', 'deDE', 'D. $N ist zweifellos ein Diener aus Naralex\' Alptraum!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1277', 'deDE', 'Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1278', 'deDE', 'Anaya...? Kann ich meinen Augen trauen? Seid Ihr es wirklich?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1279', 'deDE', null, 'Die Jahre waren unbarmherzig zu dir und zu mir, mein Herz, aber sei versichert, dass wir letzten Endes vereint sind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1280', 'deDE', 'Dass das Schicksal so grausam sein kann, uns nach tausend Jahren der Trennung nur dies zu gestatten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1281', 'deDE', 'Hasst Ihr mich, oh Liebe meines Lebens? Dafür, dass ich Eure lebende Gestalt zerstören musste, damit Euer Geist von unglücklicher Knechtschaft befreit wurde?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1282', 'deDE', null, 'Lass es nicht dein Herz betrüben, meine Liebe. Du hast mich aus der Sklaverei befreit und dafür liebe ich dich umso mehr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1283', 'deDE', null, 'Leider kann sogar dies nicht lange dauern... Die Bindungen, die mich in dieser Welt halten, werden schwächer und ziehen mich fort...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1284', 'deDE', null, 'Lebe wohl, Cerellean, bis wir wieder vereint sind...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1285', 'deDE', null, '%s flüstert mit leiser Stimme, die im Nebel verklingt: \"Wisset, dass ich Euch immer lieben werde...\"', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1286', 'deDE', 'Nein! Anaya... Anaya! Verlasst mich nicht! Bitte...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1287', 'deDE', 'Wie, geliebtes Wesen... wie soll ich die Kraft finden, den Lauf der Zeit zu überdauern, ohne Euch an meiner Seite...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1288', 'deDE', '%s winkt $N zu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1289', 'deDE', 'Geht jetzt, $N. Findet Volcor... und denkt daran, dass die Magie, die Euch beschützt, versagen wird, wenn Ihr mit anderen mehr tut als bloß zu reden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1290', 'deDE', '%s mustert den Besucher des Lagers.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1291', 'deDE', 'Ah. Willkommen, $GSire:Milady;!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1292', 'deDE', 'Wir müssen schnell sein. Auberdine ist nicht weit entfernt, aber viele Feinde lauern im Wald.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1293', 'deDE', 'Gleich seid Ihr tot, Paladin-Abschaum!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1294', 'deDE', 'Auftragsmörder von diesem Kult, den Ihr gefunden habt... Wir sollten uns in Bewegung setzen, bevor uns hier noch jemand anders aufspürt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1295', 'deDE', 'Der Geist von Echeyakee durchströmt Euch, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1296', 'deDE', null, '%s streckt sich und gähnt ausladend.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1297', 'deDE', 'Ishamuhales Wut durchdringt Euch, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1298', 'deDE', 'Lakota\'mani dröhnt in Euch, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1299', 'deDE', 'Das Tempo von Owatanka ist mit Euch, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1300', 'deDE', 'Euer Weg ist stetig, $N, denn Washte Pawnes Entschlossenheit ist mit Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1301', 'deDE', 'Möge Euch der Geist von Isha Awak Kraft verleihen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1302', 'deDE', null, 'Terenthis, Raene schickte mich, um Euch zu suchen. Die Schildwachen in Ashenvale brauchen Hilfe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1303', 'deDE', null, 'Habt Dank, Terenthis. Ich werde hier bleiben, solange ich kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1304', 'deDE', null, 'Hoffentlich kann ich eine würdige Unterstützung schicken, um Ashenvale und den Schildwachen zu helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1305', 'deDE', 'Hallo, Selarin. Ich kann Euch momentan leider nicht helfen, aber vielleicht können es einige der Abenteurer hier in Auberdine...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1306', 'deDE', null, 'Habt abermals Dank, Terenthis. Ich werde zurückkehren, wenn mehr Hilfe benötigt wird. Bis dahin schickt bitte weitere Abenteurer nach Ashenvale.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1307', 'deDE', '%s mischt Bathranshaar...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1308', 'deDE', 'Die Mixtur ist fertig, $N. Nun müssen wir das Kind retten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1309', 'deDE', 'Ein Paladin! Ihn zu töten, würde den Meister erfreuen. Angriff!', 'Ein Paladin! Ihn zu töten, würde den Meister erfreuen. Angriff!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1310', 'deDE', 'Sie kommen heute ans Licht. Lasst uns weitergehen, sonst finden wir vielleicht noch mehr Dinge, die meinen Tod wollen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1312', 'deDE', 'Ihr könnt Euch nicht vor uns verstecken, kleiner Paladin!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1313', 'deDE', 'Ohne den Läuterer seid Ihr nicht ganz so selbstsicher, wie?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1314', 'deDE', 'Heute Nacht wird Euer Blut die Steine unserer Altäre rot färben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1315', 'deDE', 'Gut gemacht! Jetzt müsste ich allein zurechtkommen. Denkt daran und sprecht mit Delgren, wenn Ihr wieder in Maestras Posten in Ashenvale seid.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1316', 'deDE', 'Ah... die Krankheit ist stark. Und sie greift den Verstand an...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1318', 'deDE', 'Ich muss... WIDERSTEHEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1319', 'deDE', 'Ich habe die Gifte der Verlassenen überlebt, $N, und weiß jetzt, was getan werden muss.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1320', 'deDE', 'Endlich raus aus dieser jämmerlichen Zelle! Lasst mich Euch den Weg zum Hof zeigen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1321', 'deDE', 'Ihr seid wirklich mutig, dass Ihr den Schrecken, die hinter dieser Tür liegen, die Stirn bieten wollt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1322', 'deDE', '%s fingert an dem rostigen Schloss der Tür zum Hof herum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1323', 'deDE', 'Jetzt geht es los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1324', 'deDE', 'Viel Glück mit Arugal. Jetzt muss ich schnell zu Hadrec zurück.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1325', 'deDE', 'Diese Statuette muss ein Geheimfach haben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1326', 'deDE', 'Es ist verschlossen! NEIN!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1327', 'deDE', '%s springt aus den Schatten hervor!', '%s springt aus den Schatten hervor!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1328', 'deDE', 'Es wird langsam Zeit, dass jemand diese Kreatur tötet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1329', 'deDE', 'Ich muss Euch ausnahmsweise zustimmen... Abschaum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1330', 'deDE', null, 'Nebel! Ich fürchtete, ich würde Euch niemals wiedersehen! Ja, mir geht es gut. Sorgt Euch nicht um mich. Ihr müsst ruhen und Euch erholen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1331', 'deDE', 'Folgt mir und ich werde die Tür zum Hof für Euch öffnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1332', 'deDE', 'Ich habe nur den richtigen Zauber, um diese Tür zu öffnen. Zu schade, dass die Zellen nicht so halbherzig verschlossen waren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1334', 'deDE', 'Geschafft! Sie steht weit offen. Viel Glück beim Kampf mit dem, was dahinter liegt. Ich muss den Kirin Tor sofort Bericht erstatten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1335', 'deDE', 'Bitte sehr, Relara. Nehmt dies...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1336', 'deDE', null, '%s trinkt das von $N gebrachte Heilmittel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1337', 'deDE', null, 'Äh... Vater...? Seid Ihr da? Ich habe Hunger... kann ich etwas Eintopf haben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1338', 'deDE', 'Was?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1339', 'deDE', 'Ihr seid wach? Euer Heilmittel muss gewirkt haben! Danke, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1340', 'deDE', '%s knurrt zustimmend, bevor sie sich streckt und wieder in Richtung Wald verschwindet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1341', 'deDE', 'Geht rasch, $N. Mein Schicksal liegt in Eurer Hand.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1342', 'deDE', '%s schützt sich selbst mit Magie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1343', 'deDE', '%s verschwindet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1344', 'deDE', 'Ihr Landratten seid zäher als ich dachte! Ich muss wohl improvisieren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1345', 'deDE', 'D\'ah! Jetzt macht Ihr mich wütend!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1346', 'deDE', '%s zerbröselt den Mondstein in eine Tasse Tee.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1347', 'deDE', 'Bitte sehr, Relara. Trinkt dies...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1348', 'deDE', '%s legt seine Waffe ab und zündet eine Stange Dynamit an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1349', 'deDE', null, 'Mmh... meine Lider sind gar nicht so schwer, Vater. Können wir rausgehen und spielen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1350', 'deDE', 'Relara... bist du wach?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1351', 'deDE', 'Ruhe dich jetzt aus, meine Tochter.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1352', 'deDE', '%s schaut $N an und weint vor Freude.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1353', 'deDE', 'Relara ist in Sicherheit, $N. Vielen Dank.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1354', 'deDE', '%s durchsucht die antike Statuette.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1355', 'deDE', '$N, ich brauche erneut Eure Hilfe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1356', 'deDE', null, 'Die Anzahl meiner Gefährten schwindet, Göttin, und meine eigene Macht wird bald nicht mehr ausreichen, die Dämonen des Teufelswaldes zurückzuhalten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1357', 'deDE', null, 'Göttin, gewähre mir die Stärke, meine Feinde zu besiegen! Hör mich bitte an, ich bin verzweifelt in meiner Not und meine Sache ist gerecht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1358', 'deDE', null, 'Was... was ist das? Könnte das die Antwort auf meine Gebete sein? Elune hat mir eine Waffe gewährt - diese Sense -, um die Dämonen zu besiegen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1359', 'deDE', null, 'Ich habe versagt... Ich habe in meiner Pflicht versagt... habe mein Volk enttäuscht... Die Worgs sind wild und die Sense meiner Göttin ist verloren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1360', 'deDE', null, 'Das Böse, das ich entfesselt habe... In wessen Hand mag die Stärke, die meine Göttin mir gewährte, nun liegen?... Wessen Hand führt sie nun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1361', 'deDE', '%s beginnt, Wächter zu beschwören!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1364', 'deDE', 'Um Crossroads zu erreichen, müsst Ihr der Straße erst nach Osten und dann nach Norden folgen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1365', 'deDE', null, 'Sidney verspätet sich. Ich dachte, er wäre jetzt schon zu Hause.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1366', 'deDE', 'Er ist Euer Mann, Weib. Woher soll ich wissen, wo er ist?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1367', 'deDE', null, 'Er muss viel Arbeit im Kloster haben. Aber trotzdem hätte er schon zurück sein müssen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1368', 'deDE', 'Es ist keine leichte Aufgabe, die Welt von dem unreinen Abschaum zu säubern, Nancy. Aber irgendjemand muss es ja machen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1369', 'deDE', null, 'Nun, ich sehe draußen mal nach. Irgendetwas fehlt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1370', 'deDE', null, '%s stößt einen langen, tiefen Seufzer aus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1371', 'deDE', null, 'Ach ja...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1372', 'deDE', 'Sieht aus, als hätten wir Schwierigkeiten. Ein bisschen mehr Schwung bitte, da kommen sie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1373', 'deDE', 'Halt! Ich spüre etwas Böses... Untote!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1374', 'deDE', 'Die drei schon wieder?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1376', 'deDE', 'Endlich. Dieser Dreckskerl hat bekommen, was er verdient.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1377', 'deDE', '%s bricht das Siegel von Antons Brief auf und beginnt zu lesen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1378', 'deDE', 'Beim Licht! Das ist nicht gut. Es gibt etwas, das Ihr nicht versteht, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1379', 'deDE', '%s wirft Antons Brief auf den Tisch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1380', 'deDE', null, 'Die Konstruktion der Rute ist einfach. Dartol nutzte bei ihrer Erstellung sein arkanes Wissen nicht... sie ist eher druidischer Natur.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1381', 'deDE', null, 'Hehe... ein kleines Wortspiel meinerseits. Druidischer Natur... Ich bin ja so witzig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1382', 'deDE', null, 'Nun, dann... die Rute. Sehen wir mal, ob sie repariert werden kann, oder?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1383', 'deDE', null, 'Da! Jetzt muss sie nur noch wieder aufgeladen werden...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1384', 'deDE', 'Grimmklaue und sein Meister Volcor könnten Eure Hilfe gebrauchen, wenn Ihr die Zeit erübrigen könnt. Sprecht mit mir, falls Ihr Interesse habt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1385', 'deDE', '%s greift nach seinem vergifteten Dolch!', '%s greift nach seinem vergifteten Dolch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1386', 'deDE', 'Was? Was haben wir hier?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1387', 'deDE', 'Willkommen beim Verlies!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1388', 'deDE', 'Außenstehende! Tötet sie alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1390', 'deDE', 'Noch mehr von den Laufburschen des Aufsehers!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1391', 'deDE', 'Sagt dem Aufseher, dieses Gefängnis gehört jetzt uns!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1392', 'deDE', 'Es wird kurz, aber nicht schmerzlos!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1393', 'deDE', 'Frischfleisch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1394', 'deDE', 'Ich werde Euren Schädel unter meinem Stiefel zerquetschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1395', 'deDE', 'Tod den Männern des Aufsehers!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1396', 'deDE', 'Wer ist da?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1397', 'deDE', 'Wir müssen uns beeilen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1398', 'deDE', 'Geister des Waldes kommt hervor und folgt dem Ruf der Natur!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1399', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1400', 'deDE', 'Geister des Waldes, ihr werdet gebraucht! Eilt zum verbrannten Tal!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1401', 'deDE', '%s ist nicht beeindruckt.', '%s ist nicht beeindruckt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1402', 'deDE', 'Ihr fordert es heraus, $N.', 'Ihr fordert es heraus, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1403', 'deDE', 'Zwingt mich nicht dazu, Euch grob zu behandeln.', 'Zwingt mich nicht dazu, Euch grob zu behandeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1404', 'deDE', 'Macht weiter so, $N, und ich werde Euch Manieren einprügeln.', 'Macht weiter so, $N, und ich werde Euch Manieren einprügeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1405', 'deDE', '%s wirft die Tierteile in den blubbernden Kessel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1406', 'deDE', 'Ah, spürt, wie die Magie in die Höhle sickert, meine kleinen Elfen... wahahaha.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1407', 'deDE', 'So viel zur Macht der Natur... wahahahaha.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1408', 'deDE', '%s legt ein Ei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1409', 'deDE', '%s bekommt Risse und öffnet sich langsam...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1410', 'deDE', '%s platzt auf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1411', 'deDE', null, '%s sieht, dass ihr Junges angegriffen wird und bricht in Raserei aus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1412', 'deDE', '%s übergibt Apothekerin Zamah eine Nachricht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1413', 'deDE', '%s schlüpft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1414', 'deDE', '%s ruft um Hilfe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1415', 'deDE', '%s trifft ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1416', 'deDE', null, '%s hört sich die Bitte von $N an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1417', 'deDE', null, 'Ihr habt gut daran getan zu kommen. Meine Weisheit gehört Euch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1418', 'deDE', null, '%s lächelt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1419', 'deDE', null, 'Geht, mein Diener. Bringt Kunde vom Steinkrallengebirge zu Apothekerin Zamah.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1420', 'deDE', null, '%s beschwört einen Boten...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1421', 'deDE', '%s erschafft sein Toxin...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1422', 'deDE', 'Da wären wir, $N. Das Toxin steht bereit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1423', 'deDE', null, '%s füllt einen Behälter mit dem Toxin.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1424', 'deDE', null, 'Das Toxin ist fertig, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1425', 'deDE', null, 'Die Geister des Steinkrallengebirges wüten noch immer, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1426', 'deDE', null, 'Ich fürchte, wir werden sie niemals beschwichtigen können...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1427', 'deDE', 'Okay, haltet mir den Rücken frei, während ich Euch etwas von diesem Erz besorge.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1428', 'deDE', 'He! Ihr dürftet überhaupt nicht hier sein! Kommt schon, Jungs!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1430', 'deDE', 'Ich glaube, die Geräusche kamen von dort drüben. Wir sollten lieber mal nachsehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1431', 'deDE', 'Vorsicht! Das hört sich ganz so an, als wären da noch mehr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1432', 'deDE', 'Es dauert nicht mehr lange, ich bin fast fertig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1433', 'deDE', 'Was ist denn hier los? Piznik! Wir hätten Euch nicht erlauben dürfen, hier zu bleiben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1434', 'deDE', 'Da, ich habe es!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1435', 'deDE', 'Wer wagt es, die Söhne von Arugal zu stören?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1436', 'deDE', 'Ihr habt zu viel von meiner Arbeit zerstört! Jetzt werdet Ihr in das Nether eingehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1437', 'deDE', 'Das wird nicht lange dauern, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1438', 'deDE', '%s lässt die Träne in den Eintopf fallen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1439', 'deDE', 'Iss das, meine Tochter. Danach wirst du dich gleich viel besser fühlen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1440', 'deDE', 'Bitte sehr...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1441', 'deDE', null, '%s wacht aus ihrem Delirium auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1442', 'deDE', null, 'Mmmmh...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1443', 'deDE', '%s legt eine Breipackung auf Relaras Stirn.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1444', 'deDE', '%s seufzt erleichtert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1445', 'deDE', 'Wir werden Euch die Kehlen durchschneiden und Euch ausbluten lassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1446', 'deDE', 'Wir kriegen Eure Köpfe schon noch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1447', 'deDE', 'Für den dunklen Strang!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1448', 'deDE', 'sdsd', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1449', 'deDE', 'Meine... Meine Kräfte werden schwächer... Für diese Unverfrorenheit werdet Ihr bezahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1450', 'deDE', 'Nein! Ihr könnt mich nicht besiegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1452', 'deDE', 'Das Rennen beginnt in einer Minute!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1454', 'deDE', '%s flieht aus dem Kampf, um Hilfe zu holen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1455', 'deDE', '%s sieht seine Anhänger jubeln und wird noch schneller!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1456', 'deDE', 'Ich habe es mir überlegt, meine treuen Diener, ihr müsst den Gefangenen nicht in mein Arbeitszimmer bringen, ich kümmere mich gleich hier um ihn.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1458', 'deDE', 'Arghhh!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1459', 'deDE', 'Diesen Seelenstein zu zerbrechen ist eine ziemlich einfache Aufgabe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1460', 'deDE', '%s springt auf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1461', 'deDE', '%s intoniert: \"Helft ihm auf jede erdenkliche Weise. Ihr dürft bei dieser Aufgabe nicht versagen.\"', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1462', 'deDE', '%s zerfällt zu Staub.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1463', 'deDE', '%s schnüffelt gierig in der Luft und riecht frisches Löwenfleisch...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1464', 'deDE', '%s verschlingt den Kadaver!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1465', 'deDE', '%s verschwindet im Schnee.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1466', 'deDE', '%s wird in die Flamme von Uzel gelegt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1467', 'deDE', '%s sieht sich um...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1468', 'deDE', 'Hm... wenn Ihr nach einer Möglichkeit sucht, den Verstand zu verwirren, $N, dann sollte das Geheimnis hinter Zanzils Zombies genau das Richtige sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1469', 'deDE', 'Danke, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1470', 'deDE', 'Ich bin sicher, dass ich eine Verwendung für diese Mixtur finden werde...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1471', 'deDE', 'In den Qualen der Teilung war Ameth\'Aran den Launen des Schreckens, der das Land ergriff, ausgeliefert. Es gab kaum Hoffnung auf Überleben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1472', 'deDE', 'Athrikus ist zu uns gekommen. Er sagte, dass er uns vor Bösem bewahren könne. Er hat einen Schutzzauber auf uns gewirkt, der uns vor dem Bösen beschützen soll.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1473', 'deDE', 'Dann müssen wir Athrikus Narassin beseitigen. Sagt mir, Geist, wo kann ich die anderen Seelensteine finden, um seine Magie rückgängig zu machen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1474', 'deDE', 'Als die Erschütterungen endeten, kamen seine wahren Motive ans Licht. Wir waren gefangen und er entzog uns langsam unsere Macht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1475', 'deDE', 'Sein Lieutenant, Ilkrud Magthrull, besitzt einen Folianten, der Hinweise auf den Verbleib der letzten Seelensteine geben könnte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1476', 'deDE', '%s verschwindet im Nebel...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1477', 'deDE', '%s greift sich ein Teil und steckt es in die Tasche...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1478', 'deDE', 'So, das sollte reichen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1479', 'deDE', '%s stellt die Kiste mit den Teilen auf den Boden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1480', 'deDE', '%s beginnt zu tanzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1481', 'deDE', 'Haha! $N, Ihr seid mir schon ein Partner!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1482', 'deDE', 'Juhu! Endlich hier raus. Es wird aber nicht einfach werden. Haltet Eure Augen nach Ärger offen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1483', 'deDE', 'Dort oben residiert Charlga Razorflank. Die verdammte Greisin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1484', 'deDE', 'In diesem Graben gibt es Blaulaubknollen! Als würde das Gold nur darauf warten, ausgegraben zu werden, ich sag\'s Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1485', 'deDE', 'Hier könnten Gefahren hinter jeder Ecke lauern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1486', 'deDE', 'Ich verstehe nicht, wie diese widerwärtigen Tiere an solch einem Ort leben können... puh, wie das hier stinkt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1487', 'deDE', 'Ich denke, ich sehe einen Weg, wie wir aus diesem verfluchten Dornengewirr herauskommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1488', 'deDE', 'Ich bin froh, dass wir wieder aus diesem verflixten Graben raus sind. Doch hier oben ist es auch nicht viel besser!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1489', 'deDE', 'Dieses Starkbier riecht aber stark!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1490', 'deDE', 'Endlich! Ich bin froh, dass ich endlich hier raus komme.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1491', 'deDE', 'Lasst uns nur mal kurz probieren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1492', 'deDE', 'Wartet! Was denke ich mir denn dabei! Wir haben doch damit noch was zu erledigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1493', 'deDE', 'Ich werde mich lieber einen Moment ausruhen und wieder zu Atem kommen, bevor ich nach Ratchet zurückkehre. Vielen Dank für Eure Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1494', 'deDE', 'Nun, ich gehe jetzt nach Ratchet! Ich wünsche Euch viel Glück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1495', 'deDE', '%s hört zu, wie $N über $Gseine:ihre; Abenteuer im Ödland erzählt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1496', 'deDE', 'Erstaunlich! Dann ist das Indurium also der Schlüssel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1497', 'deDE', 'Hunderte wurden durch seinen Zauber gefangen gehalten. Jetzt sind nur noch einige wenige in ihren Gefängnissen. Von Zeit zu Zeit redet er mit uns.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1498', 'deDE', 'Er fürchtete, seine Macht würde schwinden und dass bald selbst wir, die letzten seiner kostbaren Seelenedelsteine, verblassen und sterben würden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1499', 'deDE', 'Dieses Mal mache ich Euch endgültig fertig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1500', 'deDE', '%s nimmt die Induriumspäne mit in seine Schmiede.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1501', 'deDE', 'Dann wollen wir diese Späne mal erhitzen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1502', 'deDE', 'Bis jetzt halten sie. Erhöhen wir die Hitze ein wenig...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1503', 'deDE', 'Bei Orgrim! Dieses Indurium hält aber sehr viel Hitze aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1504', 'deDE', 'Ein erfolgreicher Test! $N, dieses Indurium ist wirklich erstaunlich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1505', 'deDE', 'Noch einmal vielen Dank, $N. Ich werde dieses Indurium jetzt benutzen, um meinen Wagen aufzupeppen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1506', 'deDE', 'KLOPF! KLOPF! KLOPF! Ich verbessere meinen Wagen!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1507', 'deDE', 'Fertig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1508', 'deDE', 'Und für das nächste Rennen wird der Wagen sogar NOCH schneller sein als bisher!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1509', 'deDE', 'Wir haben geöffnet! Der Goblin-Wagen sieht schnell aus und wird sicherlich siegen! Gebt Eure Wetten ab, bevor das Rennen beginnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1510', 'deDE', 'Es gibt leichtes Geld zu verdienen, wenn Ihr beim nächsten Rennen auf die Gnome setzt! Setzt Euer Geld, solange es noch geht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1511', 'deDE', '%s beginnt, über den Herzen und Zungen einen Zauber zu sprechen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1512', 'deDE', 'Die Brut von Onyxia!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1513', 'deDE', '%s zerfällt zu Staub.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1514', 'deDE', 'Ich noch Boss von Oger. Jawohl!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1515', 'deDE', 'Mickrig $R will gegen Anführer Mok\'Morokk kämpfen? Ich Euch schlagen. Ich hier Boss!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1517', 'deDE', 'Und d. $N kommt durch die erste Kurve!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1518', 'deDE', 'Und der Gnomen-Wagen überquert die Linie! Die Gnome gewinnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1519', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1520', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1521', 'deDE', null, '%s erwidert die unanständige Geste $N gegenüber.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1522', 'deDE', 'Die Goblins gewinnen! Die Goblins gewinnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1523', 'deDE', 'Ich Angst! Ich jetzt wegrennen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1524', 'deDE', 'Hm, dieses Mal habt Ihr aber wirklich schöne Knochen, Pozzik.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1525', 'deDE', 'Fertig! Die Modifizierungen sollten uns etwas mehr Tempo verschaffen, aber ich glaube nicht, dass das Ding viele Zusammenstöße aushalten wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1526', 'deDE', 'Okay, Leute. Macht euch an die Arbeit und bringt diese Knochen an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1527', 'deDE', 'Danke für die Helme, $N. Unsere Arztrechnungen werden jetzt nicht mehr so hoch ausfallen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1528', 'deDE', null, '%s deutet auf die Wasserkanne, die auf dem Rand des Brunnens steht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1529', 'deDE', null, 'Was kann ich für Euch tun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1530', 'deDE', null, 'Was wünscht Ihr, $GSire:meine Dame;?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1531', 'deDE', null, 'Hallo, $N, was kann ich Euch heute bringen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1532', 'deDE', null, 'Hattet Ihr einen harten Tag? Ein gutes Bier sollte die Sorgen fortspülen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1533', 'deDE', null, 'Hallo, was möchtet Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1534', 'deDE', null, 'Guten Tag, $C. Was möchtet Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1535', 'deDE', null, 'Schaut nur, wer da kommt. Was kann ich Euch bringen, $N?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1536', 'deDE', null, 'Denkt Ihr, ich brauche noch etwas mehr Flair?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1537', 'deDE', 'Der Gewinner des Wagenrennens ist... das Team der Gnome! Gut gemacht, Jungs!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1538', 'deDE', 'Die Goblins haben das Wagenrennen gewonnen! Mehr Glück beim nächsten Mal für das Team der Gnome.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1539', 'deDE', null, '%s schnupft Traumstaub.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1540', 'deDE', 'Hi, hi, hi! Da geht was in die Luft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1541', 'deDE', 'Was war das?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1542', 'deDE', 'Verdammt... Das müssen die Goblins gewesen sein... Dann wollen wir uns den Schaden mal anschauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1543', 'deDE', 'Da kann man wohl nichts mehr machen. Verdammt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1544', 'deDE', 'Iiik! $N kommt direkt auf uns zu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1545', 'deDE', 'Fürwahr! $N hat\'s auf mich abgesehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1546', 'deDE', '$N kommt schnell näher! Zu den Waffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1547', 'deDE', 'Hilfe! Schafft mir diesen $N vom Leib!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1548', 'deDE', '%s saugt etwas Traumstaub in die Nase...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1549', 'deDE', '%s ist benommen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1550', 'deDE', 'HATSCHI!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1551', 'deDE', 'Unglaublich! $N, dieser Traumstaub ist aber ein Teufelszeug!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1568', 'deDE', 'Was war das? Habt Ihr das Leuchtfeuer brennen lassen?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1569', 'deDE', 'Die Explosion war phantastisch! Habt Ihr gesehen, wie groß sie war?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1570', 'deDE', 'Ich war\'s nicht! Ich schwör\'s!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1571', 'deDE', 'Schwärmt aus! Findet heraus, wer das getan hat. Die Schuldigen werden gefeuert, das ist sicher!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1572', 'deDE', 'Das wird eine Stange Gold kosten! Und das werde ich nicht aus meiner Tasche bezahlen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1573', 'deDE', 'Hat jemand was gesehen? Was ist passiert?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1588', 'deDE', 'niest heftig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1589', 'deDE', 'Bis wir uns wiedersehen, tapfere Seele.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1591', 'deDE', '%s wackelt mit den Barthaaren und schaut $N an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1592', 'deDE', '%s schnüffelt am Boden...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1593', 'deDE', '%s findet einen Trüffel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1594', 'deDE', '%s attackiert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1608', 'deDE', 'Gut gemacht, $N. Setzt Eure Suche nach Wissen fort und erinnert Euch an die Geschichte, von der Ihr erfahren habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1609', 'deDE', '%s wurde gefunden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1610', 'deDE', 'Nun gut, dann lasst uns anfangen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1611', 'deDE', 'Dann wollen wir diese Kräuter mal suchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1612', 'deDE', 'Nein, hier nicht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1613', 'deDE', 'Die müssen doch hier irgendwo sein...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1614', 'deDE', 'Ah, da!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1615', 'deDE', 'Kommt, $N! Gehen wir hinüber und holen sie uns!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1616', 'deDE', '%s hebt die Kräuter vom Boden auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1617', 'deDE', 'Gut, und nun lasst uns von hier verschwinden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1618', 'deDE', 'Von hier ab schaffe ich es allein. Habt Dank, $N! Und sprecht mit meinen Arbeitgeber wegen einer Belohnung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1619', 'deDE', '%s verschwindet wieder im Sumpf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1628', 'deDE', 'Passt auf! $N greift an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1629', 'deDE', 'Hilfe! Ich werde angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1630', 'deDE', 'Hilfe! Die Bestie geht auf mich los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1631', 'deDE', 'Ich bin froh, dass Ihr hier seid, denn ich brauche Eure Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1632', 'deDE', 'Er bringt Euch Fleisch, er bringt Euch Bier...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1633', 'deDE', 'Ein breites Grinsen von Ohr zu Ohr...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1634', 'deDE', 'Er hat uns all die Jahre lang gedient...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1635', 'deDE', 'Lächelnder Jim, so heißt er hier...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1636', 'deDE', 'Halt! Zurück in eure Ecken! Ihr habt gut gekämpft, alle beide.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1638', 'deDE', '%s kriecht nervös von der Kiste fort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1639', 'deDE', '%s kehrt zu $N zurück.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1640', 'deDE', '%s beginnt zu graben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1641', 'deDE', 'Kämpfer bereit... KÄMPFT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1642', 'deDE', 'D. $N ist durch die zweite Kurve!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1643', 'deDE', 'Okay, ich möchte einen sauberen, schönen Trainingskampf. Denkt daran, Euer Schwert und Euren Schild bereitzuhalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1644', 'deDE', '%s stoppt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1648', 'deDE', 'Geht zum Gasthaus Zur süßen Ruh und folgt den Spuren. Schaut nach, wohin sie führen, und erstattet mir dann Bericht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1649', 'deDE', 'Verstanden. Ich bin bald wieder da.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1650', 'deDE', '%s verschwindet im Nebel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1651', 'deDE', 'Schnappt sie euch, Jungs. Erteilt ihnen eine Lektion! Niemand legt sich mit Slim an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1652', 'deDE', 'Genießt den Rest Eures Aufenthalts in Menethil. Er wird nicht mehr lange dauern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1653', 'deDE', 'Ihr denkt, Ihr könnt herkommen und uns bestehlen, was?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1654', 'deDE', 'Ihr werdet hier niemals lebendig rauskommen!', 'Ihr werdet hier niemals lebendig rauskommen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1655', 'deDE', 'Jetzt ist keine Zeit mehr für ein Bier! Das Rennen kann jeden Moment anfangen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1656', 'deDE', 'Der Wagen hat etwas abbekommen! Machen wir ihn für das nächste Rennen startklar.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1657', 'deDE', 'Ah, Gefreiter Hendel. Ich freue mich, einen der Männer zu treffen, die ihr Königreich aus Hochmut verraten würden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1658', 'deDE', 'Was soll ich jetzt tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1659', 'deDE', 'Wenn das der Captain herausfindet, bin ich erledigt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1660', 'deDE', 'Wie konnte das geschehen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1661', 'deDE', 'Plötzlich waren sie überall.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1662', 'deDE', 'Jemand muss ihnen geholfen haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1663', 'deDE', 'Warum wir uns nicht gleich um Euch kümmern, Hendel? Lady Proudmoore wünscht, Euch im Turm zu sprechen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1664', 'deDE', 'Ich bin gespannt darauf, was ein so aufgebrachter Orc zu sagen hat...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1665', 'deDE', '%s legt Margs Kopf in den Kessel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1666', 'deDE', 'Seht in den Kessel, $N. Marg wünscht zu sprechen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1667', 'deDE', 'Habt Ihr das gehört? $N hier sagt, dass der menschliche Deserteur Reethe derjenige gewesen sein könnte, der das Gasthaus niedergebrannt hat!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1668', 'deDE', 'Ich hoffe, dass Ogron diese Blasshaut findet und ihm die Beine bricht. Mal sehen, wie er dann in den Sümpfen überlebt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1669', 'deDE', 'Mir ist ein Feuer auf der Insel dort drüben aufgefallen. Auch ein Mensch. Lasst uns nachschauen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1670', 'deDE', 'Das ist Reethe. Mal sehen, was er zu sagen hat, ja?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1671', 'deDE', '%s legt die Rolle auf den Tisch und liest...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1672', 'deDE', 'Beim Licht! Dieser verfluchte Spion hat wahrhaft gute Arbeit geleistet! Ich bin froh, dass er tot ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1673', 'deDE', 'Danke für diesen Bericht, $N. Jetzt wissen wir, dass wir die Patrouillen in der Nähe von Theramore verstärken müssen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1674', 'deDE', 'W...was wollt Ihr? Lasst mich einfach in Ruhe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1675', 'deDE', 'Ich schwöre, ich habe Euch nichts gestohlen! Hier, nehmt ein paar meiner Vorräte, aber geht bloß fort!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1676', 'deDE', '%s stöhnt vor Schmerzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1677', 'deDE', 'Endlich kommt Hilfe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1678', 'deDE', null, 'Ihr fühlt Euch schon besser, wie ich sehe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1679', 'deDE', null, 'Trinkt dies, es wird helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1680', 'deDE', null, 'Das sollte den Schmerz lindern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1681', 'deDE', null, 'Etwas von dieser Salbe sollte helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1682', 'deDE', null, 'Es wird Euch bald besser gehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1683', 'deDE', null, 'Nehmt dies und schluckt es herunter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1684', 'deDE', null, 'Ihr werdet alle Eure Finger und Zehen behalten, keine Sorge.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1685', 'deDE', null, 'Lasst mich Euch dabei helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1686', 'deDE', 'Schwester.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1687', 'deDE', 'Wie geht es meinen Männern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1688', 'deDE', null, 'Sie schlagen sich gut, Sire. Sie sollten alle vollständig genesen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1689', 'deDE', 'Danke. Hoffentlich gibt es nicht noch mehr davon.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1690', 'deDE', 'Tötet sie alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1691', 'deDE', 'Freiheit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1692', 'deDE', 'Tod dem Aufseher!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1693', 'deDE', 'Tötet die Wachen, aber fangt den Aufseher lebend!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1694', 'deDE', 'STERBT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1695', 'deDE', 'Wir werden auf euren Knochen tanzen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1696', 'deDE', 'Tod den Männern des Aufsehers!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1697', 'deDE', 'Euer Gefängnis ist jetzt uns!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1698', 'deDE', 'Wir werden angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1699', 'deDE', 'Verteidigt die Barrikade!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1701', 'deDE', 'Reißt Euch zusammen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1702', 'deDE', 'Lasst niemanden vorbei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1703', 'deDE', 'Bei Lothar, dafür werdet ihr elenden Defias bezahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1704', 'deDE', 'Eine neue Welle naht, haltet die Front!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1705', 'deDE', 'Mehr Gefangene!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1706', 'deDE', 'Lasst sie nicht entrinnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1707', 'deDE', 'Sagt uns einfach, was Ihr über das Gasthaus Zur süßen Ruh wisst, und ich werde Euch nicht den Schädel einschlagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1708', 'deDE', 'Ich... nun, ich habe vielleicht ein oder zwei Sachen aus dem Gasthaus mitgenommen... aber was kümmert das einen Oger?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1709', 'deDE', 'Nun, wenn Ihr mir nichts über das Feuer erzählen wollt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1710', 'deDE', 'Keinen Schritt näher, Oger!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1711', 'deDE', 'Und ich weiß absolut nichts über Euer Feuer...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1712', 'deDE', 'Gut, gut! Genug gekämpft. Es muss niemand mehr verletzt werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1713', 'deDE', 'Alles in Ordnung, Jungs. Zurück! Ihr habt genug getan. Ich treffe euch später.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1714', 'deDE', 'Paval Reethe! Endlich habe ich Euch gefunden. Verkehrt Ihr jetzt schon mit Ogern? Keine Angst, selbst Deserteuren und Verrätern wird ein wenig Gnade gewährt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1715', 'deDE', 'Jawohl, gut, Boss. Kein Problem.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1716', 'deDE', 'In Ordnung, Boss. Aber seid Ihr Euch da sicher? Scheint mir reine Zeitverschwendung zu sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1717', 'deDE', '%s lässt seine Knöchel knacken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1718', 'deDE', 'Jubelt $N zu, endlich mal ein $C von Wert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1719', 'deDE', 'Merkt auf! Der Blaue Eremit gibt ein Fest zu $Gseinen:ihren; Ehren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1720', 'deDE', 'Gefreiter, zeigt gegenüber Lieutenant Reethe ein wenig Mitleid.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1721', 'deDE', null, 'Mit Vergnügen, Sire.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1722', 'deDE', 'Uh... Hallan, das hätte ich Euch gar nicht zugetraut...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1723', 'deDE', 'Räumen wir jetzt den Rest dieses Abfalls auf, Männer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1724', 'deDE', 'Verflucht! Ihr sterbt jetzt doch, Mensch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1725', 'deDE', 'Heute muss Euer Glückstag sein. Gut, ich rede. Lasst mich nur in Ruhe. Schaut, Ihr werdet mir nicht glauben, aber... oh, beim Licht, offensichtlich konnte das Mädchen schießen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1726', 'deDE', 'Seid Ihr noch unter uns, Reethe?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1727', 'deDE', 'Eindringlinge!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1728', 'deDE', 'Sie sind hier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1729', 'deDE', 'Sieht tot aus. So haben wir nicht gerade großen Nutzen davon. Ihr solltet besser zurückgehen und Krog erzählen, was passiert ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1730', 'deDE', 'Danke übrigens, dass Ihr mir den Rücken freigehalten habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1731', 'deDE', 'Was war das? Habt Ihr etwas gehört?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1732', 'deDE', '%s taumelt zurück, als sich der Pfeil tief in seine Brust bohrt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1733', 'deDE', 'Hurra, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1734', 'deDE', 'Hoch lebe $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1735', 'deDE', 'Ein Hoch, ein Hoch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1736', 'deDE', 'Lang lebe $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1737', 'deDE', 'Ich werde jetzt jemanden beschwören, der den Anhänger auf ein Geheimfach hin untersucht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1740', 'deDE', 'Ich weiß noch nicht, was da drin sein wird. Pat Nagle und Michael Backus müssen das erst noch besprechen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1741', 'deDE', 'Und bis dahin... auf Wiedersehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1742', 'deDE', 'Genug! Ich werde reden. Legt Eure Waffen weg, denn ich bin es nicht, mit dem Ihr Streit habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1743', 'deDE', 'Gut, gut! Kein Grund, gleich Gewalt anzuwenden. Ich rede. Ich rede!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1744', 'deDE', 'Ich habe daheim ein paar Notizen von der Arbeit. Ich hole sie und wir treffen uns im Gasthaus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1745', 'deDE', 'Lasst den armen Mann doch einfach in Ruhe, Bürger. Er ist den Ärger nicht wert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1748', 'deDE', '$N, ich würde gern kurz mit Euch reden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1749', 'deDE', 'Kümmert Euch um Eure eigenen Angelegenheiten. Was ich zu erledigen habe, geht Euch nichts an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1750', 'deDE', 'Acht...UNG!', 'Acht...UNG!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1751', 'deDE', 'Nun geht und möge die Magie der Fürstin Euch beschützen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1752', 'deDE', 'Falgran Hastil meldet sich zum Rapport, Sire. Ich werde herausfinden, wohin die Hufspuren führen, und Euch dann Bericht erstatten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1753', 'deDE', 'Tesoran! Ich brauche jemanden - vorzugsweise Falgran Hastil - der $N mit Hilfe der Fußspuren am Gasthaus Zur süßen Ruh folgt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1754', 'deDE', 'Sofort, Sire.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1755', 'deDE', 'Kagoro, Eure Anwesenheit ist erforderlich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1756', 'deDE', 'Schon gut! Schon gut! Wir geben auf... Nehmt einfach die Waffen runter, ich kooperiere!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1757', 'deDE', 'Ihr könnt mich vielleicht in die Finger bekommen, aber die Defias werden Sturmwind auf jeden Fall zurückerobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1758', 'deDE', 'Oh oh... das ist übel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1759', 'deDE', 'Bitte... bitte... Miss Proudmoore. Ich wollte doch nicht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1760', 'deDE', 'Guter Kampf, Edward. Mal sehen, ob es Kahil mit ihnen aufnehmen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1761', 'deDE', 'Ich muss mich entschuldigen, dass ich so lange gebraucht habe, um hierher zu kommen. Ich wollte, dass Lady Proudmoore ebenfalls anwesend ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1762', 'deDE', 'Kahil, das war eine Meisterleistung. Kommt zurück und ruht Euch aus. Narrisha wird für uns aufräumen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1763', 'deDE', 'Redlich gekämpft, Narrisha. Nächstes Mal kriegen wir euch, rotes Team!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1764', 'deDE', 'Ihr habt alle hart und tapfer gekämpft. Es macht mich stolz zu sehen, was für Krieger aus euch geworden sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1765', 'deDE', 'Dieses Mal haben wir verloren, aber lasst Euch davon nicht entmutigen. Konzentriert Euch auf Eure Spezialangriffe. Haltet Euren Schild hoch und nahe am Körper. Wenn Ihr das macht, werden wir das nächste Mal siegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1766', 'deDE', 'War \'n höllischer Kampf, Jarad. Nächstes Mal musste nur den Schild höher halten. Lana! Du bist dran!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1767', 'deDE', 'Ich hab Euch schon besser kämpfen sehen, Lana! Nächstes Mal kriegt Ihr sie! Tark, hinein mit Euch und macht ihnen die Hölle heiß!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1768', 'deDE', 'Was ist passiert, Tark? Das könnt Ihr doch viel besser! Ihr werdet bald euer blaues Wunder erleben, blaues Team!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1769', 'deDE', 'Ein hervorragender Kampf! Ihr habt einen alten Mann mit Stolz erfüllt. Macht weiter so und das Bier geht heute Abend auf mich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1770', 'deDE', 'Was war das denn für ein Kampf? Ich sollte eure wertlosen Hintern aus dem Team und aus der Wache werfen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1771', 'deDE', 'Captains, stellt eure ersten Kämpfer auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1772', 'deDE', 'Macht\'s gut. Wir werden uns gewiss wiedersehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1773', 'deDE', 'Rot hat gewonnen! Herzlichen Glückwunsch, Rot!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1774', 'deDE', 'Blau hat gewonnen! Herzlichen Glückwunsch, Blau!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1775', 'deDE', 'Jarad, Ihr seid als Erster dran.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1776', 'deDE', 'Edward, Ihr fangt für uns an. Rein mit Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1777', 'deDE', 'Für die Blauen!', 'Für die Blauen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1778', 'deDE', 'Für die Roten!', 'Für die Roten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1788', 'deDE', 'He, $N! Vergesst nicht, eine Kiste, ein Handbuch und einen Leitstecken mitzunehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1789', 'deDE', 'Das war ein netter Schuss... aber noch nicht perfekt. Vielleicht ein wenig weiter nach links.', 'Netter Schuss... aber noch nicht perfekt. Vielleicht ein wenig weiter nach links.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1790', 'deDE', 'Diesmal habe ich das Ziel kaum getroffen. Hmmm... vielleicht muss ich etwas mehr nach rechts zielen.', 'Diesmal habe ich das Ziel kaum getroffen. Hmmm... vielleicht muss ich etwas mehr nach rechts zielen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1791', 'deDE', 'Fürwahr! Das war ein Volltreffer!', 'Fürwahr! Das war ein Volltreffer!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1792', 'deDE', 'Uups! Der ging ein wenig daneben. Ich hab nicht einmal das Ziel getroffen!', 'Uups! Der ging ein wenig daneben. Ich hab nicht einmal das Ziel getroffen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1793', 'deDE', 'Wie Ihr befehlt, Sire!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1794', 'deDE', 'Das rote Team gewinnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1795', 'deDE', 'Das blaue Team gewinnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1808', 'deDE', 'Wir können nur noch kurz bleiben, dann müssen wir zum Turm zurück. Wenn Ihr noch mehr mit uns besprechen wollt, könnt Ihr uns dort antreffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1828', 'deDE', 'Es war nicht mein Fehler, Meister Mazen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1829', 'deDE', 'So ein Fehler kann leicht passieren, auf mein Wort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1830', 'deDE', 'Der Sumpf war schrecklich! Ich hatte fürchterliche Angst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1831', 'deDE', 'Ich kann von Glück reden, dass ich noch lebe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1833', 'deDE', 'Dem Licht sei Dank, dass Ihr hier seid! Zieht Euer Schwert, mir ist ein Zentaur auf den Fersen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1834', 'deDE', 'Jetzt kann Euch niemand mehr retten, Mensch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1835', 'deDE', 'Ich werde Euch Euer Geld und auch Euer Leben nehmen!', 'Ich werde Euch Euer Geld und auch Euer Leben nehmen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1836', 'deDE', 'Sprecht Eure Gebete!', 'Sprecht Eure Gebete!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1837', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1838', 'deDE', 'Ich glaube, das war der Letzte. Gut, dass Ihr zur rechten Zeit gekommen seid... ich weiß nicht, wie lange ich mich noch hätte verstecken können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1848', 'deDE', 'Ich brauche nur wenige Minuten, um das Serum zuzubereiten, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1849', 'deDE', 'Fast fertig...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1850', 'deDE', 'Endlich ist das Serum vollendet, $N. Kommt jetzt näher. Wir müssen reden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1851', 'deDE', 'Das dürfte nicht lange dauern...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1853', 'deDE', 'Der Unsichtbarkeitsschnaps steht für Euch bereit, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1854', 'deDE', 'Lasst uns von hier fortgehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1855', 'deDE', 'Danke, $N. Ich werde Euch nie vergessen! Ihr findet meine Geldkassette in meinem Lager nördlich von Stonard.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1856', 'deDE', '%s verschwindet im Sumpf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1857', 'deDE', '%s trinkt einen großen Schluck Bier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1858', 'deDE', 'Das war erfrischend. Jetzt zu den Informationen, die ich loswerden muss...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1859', 'deDE', 'Ich denke, dass die Verlassenen die Verbündeten der Horde in die Irre führen... wartet... Ich fühle mich so... benommen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1860', 'deDE', '%s windet sich vor Schmerzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1861', 'deDE', 'AAAAAAAARGH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1862', 'deDE', null, 'Das geht runter wie Nichts...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1863', 'deDE', 'Nicht töten mich! Nicht töten mich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1864', 'deDE', 'Yiieeeee! Ich wegrennen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1865', 'deDE', 'Ah, nun kann ich diese frisch geschärfte Klinge einsetzen.', 'Ah, nun kann ich diese frisch geschärfte Klinge einsetzen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1866', 'deDE', 'Die Bruderschaft wird Eure Handlungen nicht tolerieren.', 'Die Bruderschaft wird Eure Handlungen nicht tolerieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1867', 'deDE', 'Spürt die Macht der Bruderschaft!', 'Spürt die Macht der Bruderschaft!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1868', 'deDE', 'Du nicht nehmen Kerze!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1869', 'deDE', 'Grrrrr!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1870', 'deDE', 'Grrr... Frischfleisch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1871', 'deDE', 'Mehr Knochen zum Nagen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1872', 'deDE', 'Jetzt geht es los, Leute. Seid auf der Hut...', 'Jetzt geht es los, Leute. Seid auf der Hut...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1873', 'deDE', 'Passt auf, wo ihr hintretet...', 'Passt auf, wo ihr hintretet...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1874', 'deDE', 'Spürt die Macht der Blackrockorcs!', 'Spürt die Macht der Blackrockorcs!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1875', 'deDE', 'Ihr seid kein Gegner für die Blackrockorcs!', 'Ihr seid kein Gegner für die Blackrockorcs!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1876', 'deDE', 'Wir haben es über den Pass geschafft! Jetzt kommt der Sumpf...', 'Wir haben es über den Pass geschafft! Jetzt kommt der Sumpf...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1877', 'deDE', 'Wir haben Nethergarde erreicht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1878', 'deDE', 'Wir haben geöffnet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1879', 'deDE', 'Ich schließe bald, also kauft, solange Ihr noch könnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1880', 'deDE', 'Ich werde jeden Moment schließen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1881', 'deDE', 'Er tot.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1882', 'deDE', 'Oh gut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1883', 'deDE', 'Lasst mich diese gyromechanischen Gerätschaften und Stabilisationszahnräder anbringen, dann können wir den Rekombobulator anwerfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1884', 'deDE', 'Das sollte ausreichen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1885', 'deDE', 'Es ist Zeit, das gesamte Volk der Gnome zu retten! Wir haben nichts zu verlieren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1886', 'deDE', 'Oh, großartig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1887', 'deDE', 'Zurück ans Zeichenbrett.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1888', 'deDE', 'Vielleicht reicht ja ein wenig mehr Fett schon aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1889', 'deDE', 'Das war wirklich knapp. Ich denke, wir haben es fast geschafft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1890', 'deDE', 'Der Rekombobulator macht sich schon recht gut. Nur noch ein paar kleine Feineinstellungen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1891', 'deDE', 'Exakt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1892', 'deDE', 'Das waren auch meine Gedanken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1893', 'deDE', 'Ich hätte es nicht besser sagen können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1894', 'deDE', 'Ganz bestimmt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1895', 'deDE', 'Das habe ich auch eben gedacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1901', 'deDE', 'Charog gibt sein Geld gern in der Geplatzten Trommel aus. Wir sollten ihm einen Besuch abstatten, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1902', 'deDE', 'Mojka, übernehmt das für mich! Ich muss mich um etwas anderes kümmern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1903', 'deDE', null, 'Noch einmal? Beeilt Euch, Komak, ich bin dessen überdrüssig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1904', 'deDE', 'Frauen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1905', 'deDE', 'Ich bin hier! Kommt und zeigt Euch, wenn Ihr Euch traut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1906', 'deDE', 'Es ist Komak... Wir sollten ihm lieber aus dem Weg gehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1907', 'deDE', 'Nicht schon wieder...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1908', 'deDE', 'Meine Waffe hat Durst!', 'Meine Waffe hat Durst!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1909', 'deDE', 'Euch zu töten wird einfach.', 'Euch zu töten wird einfach.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1910', 'deDE', 'Wie wär\'s mit ein bisschen Frostmanemagie?', 'Wie wär\'s mit ein bisschen Frostmanemagie?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1911', 'deDE', '$R! Ihr wollt doch alle nur mein Gras.', '$R! Ihr wollt doch alle nur mein Gras.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1912', 'deDE', 'Ich werde Euch aufschlitzen!', 'Ich werde Euch aufschlitzen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1913', 'deDE', 'Vernichten!', 'Vernichten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1914', 'deDE', 'Töten!', 'Töten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1915', 'deDE', 'Zerstören!', 'Zerstören!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1916', 'deDE', 'Ich rieche nach $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1917', 'deDE', 'Zeit zum Zuschlagen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1918', 'deDE', 'Ihr wagt es, die Höhle der Frostmane zu schänden, $R?! Macht Euch bereit zu sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1919', 'deDE', 'Oh, wir werden gut miteinander auskommen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1920', 'deDE', 'Ja! Ich töten!', 'Ja! Ich töten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1921', 'deDE', 'Ich nicht weglaufen vor $C wie Ihr sein!', 'Ich nicht weglaufen vor $C wie Ihr sein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1922', 'deDE', 'Was also möchtet Ihr heute machen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1923', 'deDE', 'Ich weiß es nicht. Was willst du jetzt tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1924', 'deDE', 'Fischen hört sich gut an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1925', 'deDE', 'Ich werde Euch vernichten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1926', 'deDE', 'Ich zerschmettern! Du sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1927', 'deDE', 'Raaaaaaaaaaaaaaaaaaaaaaaaaaaaaar!!! Ich zerschmettern $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1928', 'deDE', '$R sehen lecker aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1929', 'deDE', 'Zerschlagt es!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1930', 'deDE', 'Ich dich zerschmettern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1931', 'deDE', 'Hä?! Was\'n das?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1932', 'deDE', 'Eindringlinge am Horn! Lasst sie nicht entkommen!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1933', 'deDE', 'Tötet die Eindringlinge!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1934', 'deDE', 'Legt Euch nie mit einem Dunkeleisenzwerg an, $C.', 'Legt Euch nie mit einem Dunkeleisenzwerg an, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1935', 'deDE', 'Zeit zu sterben, $C.', 'Zeit zu sterben, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1936', 'deDE', 'Spürt die Macht der Dunkeleisenzwerge!', 'Spürt die Macht der Dunkeleisenzwerge!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1937', 'deDE', 'Für die Dragonmaw!', 'Für die Dragonmaw!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1938', 'deDE', 'Eure Knochen werden unter meinem Stiefel zerbrechen, $R!', 'Eure Knochen werden unter meinem Stiefel zerbrechen, $R!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1939', 'deDE', 'Lang leben die Dragonmaw! Sterbt, wertloser $R!', 'Lang leben die Dragonmaw! Sterbt, wertloser $R!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1940', 'deDE', 'Gehirne...', 'Gehirne...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1941', 'deDE', 'Ein lebendiger $R... der ist schon bald so tot wie ich.', 'Ein lebendiger $R... der ist schon bald so tot wie ich.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1942', 'deDE', 'Es wird Zeit, sich uns anzuschließen, $C.', 'Es wird Zeit, sich uns anzuschließen, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1943', 'deDE', 'Komak! Ich schwöre, ich hab das Geld bald! Gebt mir einfach nur ein bisschen mehr Zeit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1944', 'deDE', '%s räuspert sich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1945', 'deDE', 'Ein Getränk für meinen Freund Sarok und eins für mich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1946', 'deDE', 'Ich will keinen Ärger, Komak.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1947', 'deDE', 'Sarok, warum reden wir nicht Klartext?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1948', 'deDE', 'Was wollt Ihr, Komak?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1949', 'deDE', 'Hiermit zerstöre ich diese Instrumente des Bösen! Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1950', 'deDE', 'Es ist an der Zeit, einen Kalimdor-Cocktail zu brauen! Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1951', 'deDE', 'Das sollte reichen! Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1952', 'deDE', '%s stürzt sein Getränk herunter.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1953', 'deDE', 'Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1954', 'deDE', 'Nun, wer hat mich geschubst? Wart Ihr es, $N? Jemand muss das Licht wieder anschalten. Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1955', 'deDE', 'Noch ein Schluck von dem alten Feuerwasser und ich bin bereit zu reden. Hicks! Prost!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1956', 'deDE', 'So, $N, jetzt bin ich bereit, Euch zu erzählen, was in den Sümpfen des Elends geschehen ist. Hicks!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1957', 'deDE', 'Treffer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1958', 'deDE', 'Kämpft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1959', 'deDE', 'Rot gewinnt! Macht euch bereit für die nächste Runde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1960', 'deDE', 'Blau gewinnt! Macht euch bereit für die nächste Runde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1961', 'deDE', 'Jetzt mach ich Euch fertig, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1962', 'deDE', 'So wütend, Sarok? Ihr solltet nicht so viel trinken... Sagt uns einfach, was Ihr über Mosarn wisst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1963', 'deDE', 'Nun gut, ihr traurigen Knochensäcke. Jetzt zeigt mal, was ihr draufhabt. Schlagt euren Gegner und hört erst auf, wenn ich es sage.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1964', 'deDE', 'Halt! Das nennt ihr Nieten kämpfen? Das ist erbärmlich! Jetzt kämpft gefälligst mit euren Herzen, ihr werdet sie sowieso nicht mehr brauchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1965', 'deDE', 'Seht Ihr es denn nicht selbst? Das ist ja wohl der jämmerlichste Schwertkampf, den ich je gesehen habe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1966', 'deDE', 'Ihr kämpft nicht mehr nur für euch selbst, ihr Nieten! Ihr kämpft jetzt für die Königin, also beweist mir, dass ihr es auch wert seid!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1967', 'deDE', 'Ihr seid tot, aber nicht begraben. Jetzt gebt Euer Bestes!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1968', 'deDE', 'Diese Gebilde aus Holz und Stroh zeigen vielleicht Gnade, doch der Lichkönig wird das nicht tun!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1969', 'deDE', 'Das könnt ihr jämmerlichen Säcke voller Würmer besser, also beweist es!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1970', 'deDE', 'Das Schwert ist kein Werkzeug, es ist eine Verlängerung eures Armes. Ich erwarte Besseres von euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1971', 'deDE', 'Was soll das sein? Wenn das Kämpfen sein soll, dann bin ich Lothar persönlich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1972', 'deDE', 'Nun gut, Soldaten, macht eine Pause!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1973', 'deDE', 'Es ist lange her, Bethor, mein Freund.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1974', 'deDE', 'Wenn es die Zeit erlaubt, dann müssen wir uns in Ruhe unterhalten, denn wir haben viel zu besprechen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1975', 'deDE', 'Und danke, $N. Ohne Eure Hilfe hätte ich den Weg zu den Verlassenen nie gefunden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1976', 'deDE', 'Lebt wohl, mein Freund.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1977', 'deDE', 'Lysta, ruft die Gefangenen herein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1978', 'deDE', 'Edward. Tyler. Macht euch für euren ersten Kampf bereit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1980', 'deDE', 'Das schien ja überhaupt keine Herausforderung zu sein. Dann wollen wir mal sehen, wie Ihr Euch in der zweiten Prüfung macht. Lysta, bringt die Diener des Lichkönigs herbei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1981', 'deDE', 'Jetzt zeig mal, was du wert bist!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1983', 'deDE', 'Ihr habt euch letztendlich doch als wertvoll erwiesen. Lysta, beschwört eine Überraschung für unsere jungen Rekruten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1984', 'deDE', 'Es ist Zeit für euch, euch der letzten Herausforderung zu stellen, junge Krieger! Bereitet euch auf den härtesten Kampf vor, den ihr bisher durchstehen musstet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1985', 'deDE', 'Gut gemacht, Edward und Tyler. Ihr macht schöne Fortschritte bei eurer Ausbildung. Wir werden schon bald wieder überprüfen, was in euch steckt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1986', 'deDE', 'Wer wagt es, die Macht der Leere zu beschwören?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1987', 'deDE', 'Dafür werdet Ihr ewige Qualen erleiden müssen, erbärmlicher Hexenmeister.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1988', 'deDE', 'Dies ist Dalin Forgewright. Er ist aus Lordaeron geflohen, als dort die Seuche wütete, und er hat seine Liebste mitgenommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1989', 'deDE', 'Und dies ist Comar Villard. Er war gerade zwei Wochen verheiratet, als seine Frau aus der Sicherheit des Hafens von Menethil verschwand.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1990', 'deDE', 'Narren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1991', 'deDE', null, 'Interessant... Das ist mehr, als ich erwartet hatte. Köstlich...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1992', 'deDE', 'Als er am Greymane-Wall auf die Flüchtlinge traf, empfand er es als seine Pflicht, ihnen zu helfen, und er schickte seine Frau nach Hillsbrad.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1993', 'deDE', 'Fünf und fünf Jahre, und er sucht immer noch nach ihr, auch wenn sie keine Schönheit war und es viele Damen gegeben hat, die ihren Platz einnehmen wollten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1994', 'deDE', 'Was haltet Ihr von meinem neuen Spielzeug?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1995', 'deDE', 'Theresa, bringt dies zu Vater Lankester.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1996', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1997', 'deDE', null, 'Mein Lord Abernathy schickt Euch dies.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1998', 'deDE', null, 'Ja, mein Lord.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('1999', 'deDE', null, 'Mein Lord. Von Vater Lankester.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2000', 'deDE', 'Wie gefällt Euch mein Tier?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2001', 'deDE', 'Ist es nicht die absolut erstaunlichste kleine Kreation?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2002', 'deDE', 'Habe ich Euch nicht gesagt, dass meine neuen Beherrschungstechniken funktionieren würden?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2003', 'deDE', 'Ist es so gut, wie ich es Euch versprochen hab?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2004', 'deDE', 'Es war einfach, nachdem ich ihren Geist gebrochen hatte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2005', 'deDE', 'Ihr erwartet doch nicht, dass ich all meine Geheimnisse preisgebe, oder?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2006', 'deDE', 'Eine kleine, rituelle Folterung kann eine Menge bewirken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2007', 'deDE', 'Ein bisschen Folter, ein wenig Magie und ein umfassender chirurgischer Eingriff. Natürlich war sie dabei bei Bewusstsein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2008', 'deDE', 'Jeder hat eine Schwäche, man muss sie nur finden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2009', 'deDE', 'Ich habe entdeckt, dass die Entfernung oder Stimulierung gewisser Teile des Gehirns ein Subjekt noch unterwürfiger machen können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2010', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2011', 'deDE', 'Nun, danke, mein Tier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2012', 'deDE', null, 'Wahrhaft erstaunlich, Gerard. Wie habt Ihr das gemacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2013', 'deDE', null, 'Beeindruckend! Wie habt Ihr eine dauerhafte Beherrschung hinbekommen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2014', 'deDE', null, 'Ich heiße Eure Experimente nicht gut, Gerard, aber ich muss gestehen, dass die Ergebnisse eindrucksvoll sind. Was genau habt Ihr mit der Kreatur gemacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2015', 'deDE', null, 'Erstaunlich. Ihr habt es geschafft, sie zu beherrschen, und doch wurden Bewusstsein und Funktionalität bewahrt. Wie habt Ihr das gemacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2016', 'deDE', null, 'Die Kreatur behält Wissen und Sprache bei und sogar genügend Unabhängigkeit, um Aufgaben für Euch zu erledigen. Was genau habt Ihr getan?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2017', 'deDE', null, 'Ich habe so etwas noch nie gesehen. Weder Konzentrations- noch Bewusstseinsverlust, während das Objekt beherrscht wird. Was habt Ihr mit ihr gemacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2018', 'deDE', null, 'Bravo, Gerard, Ihr habt Euch wieder mal selbst übertroffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2019', 'deDE', null, 'Ich bin zutiefst beeindruckt, Gerard. Ich dachte, Ihr wärt nur ein Schwätzer, aber ich habe mich geirrt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2020', 'deDE', null, 'Eindrucksvoll, höchst eindrucksvoll. Beherrscht sie auch Tricks?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2022', 'deDE', null, 'Sehr nett, Gerard. Als Nächstes werdet Ihr sie wohl züchten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2023', 'deDE', null, 'Ihr habt da eine erstaunliche Entdeckung gemacht. Liebend gern würde ich einen Blick auf alle fehlgeschlagenen Experimente werfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2024', 'deDE', null, 'Ich bin nicht einverstanden, dass Ihr Euch der Alchemie zuwendet, um einige der Probleme zu beseitigen, jedoch bin ich mit den Ergebnissen zufrieden. Sehr gut gemacht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2026', 'deDE', null, 'Euer Tier ist entzückend, Gerard, aber ich glaube, sie braucht ein Halsband mit Glocke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2027', 'deDE', null, 'Musstet Ihr sie ans Haus gewöhnen, Gerard?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2028', 'deDE', 'Uff! Wieso schickt dieser Halunke sein Gezücht zu mir? Geh mir aus den Augen, bevor ich dir nehme, was von deinem Leben noch übrig ist. Und nimm das mit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2029', 'deDE', null, 'Ah, da ist es.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2030', 'deDE', null, '\"Magische Ströme und ihre Einflüsse auf die Welt\" von Maginor Dumas. Danach habe ich gesucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2031', 'deDE', null, 'Ich brauche wirklich ein besseres System, um diese Bücher zu ordnen. Ah, danach habe ich gesucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2032', 'deDE', null, '\"Khadgars mystisches Tagebuch, Band 8\"... Genau danach habe ich gesucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2033', 'deDE', null, 'Ah, \"Mystische Beschwörungen der Erzmagier von Dalaran\". Perfekt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2034', 'deDE', null, 'Wenn ich doch bloß \"Magie und die Wege der Macht\" finden könnte... Ach, da ist es ja. Wie kommt es denn da drüben hin?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2035', 'deDE', null, '\"Magische Maläsen\", nein. \"Magische Matrizen\", nein. Ah, da haben wir es ja, \"Magische Mysterien\".', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2036', 'deDE', null, 'Das ist der magische Foliant, nach dem ich gesucht habe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2037', 'deDE', null, 'Ja, ja. Hmm, er scheint sich auf einen anderen Folianten zu beziehen. Ich schätze, ich muss auch den finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2038', 'deDE', null, 'Das war nicht besonders hilfreich. Ich überprüfe das in dem anderen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2039', 'deDE', null, 'Genau das wollte ich wissen. Jetzt muss ich nur noch herausfinden, wo die Fluxus-Abweichung erörtert wird.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2040', 'deDE', null, 'Sehr interessant. Aber wenn das der Fall ist, dann ist meine Theorie falsch. Ich muss die anderen Abschnitte noch einmal lesen und herausfinden, was ich übersehen habe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2041', 'deDE', null, 'Ich hatte also Recht mit den Energien. Schauen wir doch mal, was der andere Foliant zu dem Thema zu sagen hat.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2042', 'deDE', null, 'Wenn das richtig ist, muss ich noch viele Forschungen betreiben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2043', 'deDE', null, 'Aber das kann nicht richtig sein. Wo habe ich denn das andere Buch hingelegt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2044', 'deDE', null, 'Zurück ans Zeichenbrett.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2045', 'deDE', 'Ha! Mal sehen, wie der hölzerne Mensch winkt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2046', 'deDE', 'Haha! Das war lustig. Witziger hölzerner Mensch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2047', 'deDE', '%s lacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2048', 'deDE', 'Halt, $N! Ich bewache diese Fässer... Ihr dürft sie Euch ansehen, aber nicht anfassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2049', 'deDE', null, 'Eure Anwesenheit hier bedeutet wohl, dass Ihr bereit seid, das Studium des Beschwörens von Dämonenkohorten, die Euch gehorchen, zu beginnen. Wir werden mit der niedersten Kreatur beginnen, die ihr rufen könnt, und uns dann weiter vorarbeiten. Lasst uns beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2050', 'deDE', null, 'Die einfachste Kreatur zum Beschwören ist der Wichtel. Ihr solltet bereits in der Lage sein, diesen Diener hervorzubringen, aber der Vollständigkeit halber möchte ich mit ihm beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2051', 'deDE', null, 'Diese üble kleine Kreatur ist ein Wichtel. Er ist klein und schwach, was ihn als lebendigen Schild beinahe unbrauchbar macht, und sein Schadenspotenzial ist bestenfalls mittelmäßig. Diese Kreatur wird am besten als Unterstützung für eine größere Gruppe eingesetzt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2052', 'deDE', null, 'Da Ihr jetzt Gelegenheit hattet, den Wichtel zu studieren, wenden wir uns jetzt dem nächsten Diener zu, den Ihr beschwören könnt, dem Leerwandler.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2053', 'deDE', null, 'Dieses dämonische Wesen wird Leerwandler genannt. Seine Kraft und Belastbarkeit sind beeindruckend, was ihn ideal für Verteidigungszwecke macht. Schickt ihn aus, um Feinde anzugreifen, und benutzt ihn dann als Schild, während Ihr Eure Zauber und Fähigkeiten einsetzt, um Eurem Gegner Leben zu entziehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2054', 'deDE', null, 'Wenn Ihr noch nie einen gesehen habt, ist es ein beeindruckender Anblick. Eine sehr eindrucksvolle Kreatur, sowohl auf dem Schlachtfeld als auch außerhalb davon. Als Nächstes werfen wir einen Blick auf das, worauf alle männlichen Studenten wohl schon lange gewartet haben. Den Sukkubus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2055', 'deDE', null, 'Nun gut. Neben der offensichtlichen Ablenkung Eurer männlichen Feinde, die ein solcher Diener mit sich bringt, ist sie auch in der Lage, erheblichen Schaden zuzufügen. Ihre schwache Belastbarkeit macht sie allerdings als Schild fast nutzlos.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2056', 'deDE', null, 'Studiert eifrig und Ihr werdet vielleicht eines Tages selbst in der Lage sein, einen zu beschwören, aber jetzt ist es an der Zeit, mit dem Teufelsjäger weiterzumachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2057', 'deDE', null, 'Was Ihr vor Euch seht, ist ein Teufelsjäger. Die natürlichen Talente dieser Kreatur umfassen \'Zaubersperre\' und andere Fähigkeiten, die ihn angesichts eines magisch begabten Gegners unübertreffbar machen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2058', 'deDE', null, 'Wenn Ihr Euch einem Zauberer gegenüberseht, ist diese wilde Bestie Euer bester Freund. Doch schauen wir uns jetzt etwas ganz anderes an. Die nächste Kreatur hilft Euch, wenn Ihr unterwegs seid, und wird Eure zukünftigen Reisen um einiges einfacher machen. Werfen wir mal einen Blick auf ein Teufelsross.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2059', 'deDE', null, 'Ich bezweifle, dass Ihr schon oft Gelegenheit hattet, solch eine Kreatur zu sehen. Diese dämonischen Pferdewesen beschleunigen Eure Reisen, indem sie Euch als Reittier dienen, solange Ihr die Kontrolle über sie habt. Jedoch sind sie schwierig zu kontrollieren, also vergewissert Euch vorher, dass Ihr bereit seid, bevor Ihr es versucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2060', 'deDE', null, 'Das war\'s. Unsere Lektion im Beschwören ist zu Ende. Eine neue Klasse wird in Kürze beginnen. Wenn Ihr das Gelernte auffrischen möchtet, könnt Ihr in der Nähe bleiben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2061', 'deDE', 'Hmmm, anscheinend braucht Keever ein neues Subjekt. Wenn Abernathy, dieser Narr, Keever ständig die Subjekte wegnimmt, dann muss Keever mal ein ernstes Wort mit ihm reden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2062', 'deDE', 'Ah, jetzt geht es los. Nun muss Keever diese Phiole testen und überprüfen, ob es funktioniert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2063', 'deDE', 'Das ist nicht, was Keever erhofft hat. Keever hat vielleicht zu viel Erdwurzel genommen. Vielleicht ist das zweite Serum ja das, was ich brauche.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2064', 'deDE', '%s knufft die kleine Kröte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2065', 'deDE', '%s gibt der Kröte etwas von der seltsamen Flüssigkeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2066', 'deDE', '%s knufft das kleine, wuschelige Eichhörnchen und ist offensichtlich enttäuscht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2067', 'deDE', 'Nun, das ist einfach nicht richtig. Die Kreatur ist viel zu klein. Dann wollen wir doch mal sehen, was Keevers dritte Sendung kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2068', 'deDE', '%s gibt dem Eichhörnchen etwas von der zähen Flüssigkeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2069', 'deDE', '%s knufft den aufgekratzten Hasen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2070', 'deDE', 'Keever ist nicht glücklich damit. Vielleicht kann Keever dieses Dilemma beseitigen, wenn er eine größere Dosis nimmt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2071', 'deDE', '%s ergreift den Hasen, flößt ihm die Flüssigkeit ein und setzt ihn dann zurück in den Käfig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2072', 'deDE', 'Was ist das? Hat Keever um ein Schaf gebeten? Keever wollte eine Waffe mit großer Macht und alles, was er bekam, war ein Schaf. Keever ist sehr enttäuscht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2073', 'deDE', '%s knufft mehrmals hintereinander das Wollschaf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2074', 'deDE', 'Keever ist sehr erfreut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2075', 'deDE', '%s flößt dem schwachen Mann die Flüssigkeit mit Gewalt ein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2076', 'deDE', '%s flüstert $N das Geheimnis zu, wie man die Geldkassette öffnet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2077', 'deDE', '$N! DIESES GEBIET IST GESPERRT!', '$N! DIESES GEBIET IST GESPERRT!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2078', 'deDE', 'Dann lasst es uns versuchen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2079', 'deDE', 'Uh! Das ist schrecklich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2080', 'deDE', '%s folgt $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2081', 'deDE', '%s stößt einen Hilfeschrei aus!', '%s stößt einen Hilfeschrei aus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2082', 'deDE', '%s beginnt, eine Kopie von sich zu erstellen!', 'beginnt, eine Kopie von sich zu erstellen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2083', 'deDE', 'Legt Euch niemals mit einem Zwerg an!', 'Legt Euch niemals mit einem Zwerg an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2084', 'deDE', 'Für den Ruhm von Eisenschmiede!', 'Für den Ruhm von Eisenschmiede!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2085', 'deDE', 'Das wird nicht lange dauern, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2086', 'deDE', 'Ich kann es kaum glauben, dass Ihr mein Rudel vernichtet habt! Jetzt werdet Ihr meinen Zorn spüren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2087', 'deDE', 'Ich rufe Euren wahren Namen, Azrethoc. Jetzt zeigt mir Euer Antlitz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2088', 'deDE', 'Abschaum wie Ihr ist meine Zeit nicht wert. Vernichtet sie, mein Rudel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2089', 'deDE', 'Erbärmlicher Verlassenen-Schwächling. Ihr wünscht, meinen Meister zu sehen? So sei es.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2090', 'deDE', 'Wer wagt es, mich aufzusuchen?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2091', 'deDE', 'Ungeziefer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2092', 'deDE', 'Kommt! Seht Eurem Tod ins Gesicht... wenn Ihr den Mut habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2093', 'deDE', 'Ja, kommt. Mein Meister und ich werden warten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2094', 'deDE', 'Eine interessante Wendung der Ereignisse.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2101', 'deDE', 'Endlich! Naralex kann erweckt werden! Kommt und helft mir, ihr tapferen Abenteurer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2102', 'deDE', 'Eindringlinge haben unseren Unterschlupf angegriffen. Seid auf der Hut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2103', 'deDE', 'Wir müssen uns mit den anderen Jüngern sammeln. Es gibt viel zu tun, bevor ich einen erneuten Versuch unternehmen kann, das Brachland wiederherzustellen. Gehabt Euch wohl, tapfere Seelen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2104', 'deDE', 'Für den Kriegshäuptling!', 'Für den Kriegshäuptling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2105', 'deDE', 'Schlägt $N mit dem Handrücken kräftig ins Gesicht.', 'Schlägt $N mit dem Handrücken kräftig ins Gesicht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2106', 'deDE', 'Bleibt ja unten!', 'Bleibt ja unten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2107', 'deDE', '%s stößt gewaltiges Gebrüll aus und alarmiert damit in der Nähe befindliche Feinde!', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen.$B$BNichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen \"auszuborgen\". Ich habe Wizzlecrank ins schlammige Moor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2108', 'deDE', 'An die Waffen, Freunde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2121', 'deDE', 'He, da ist einer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2122', 'deDE', 'Oh, der ist aber schön! Lasst ihn mich erst ausgraben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2123', 'deDE', 'So etwas habe ich noch nie zuvor gesehen! Seht Euch nur den Kieferknochen an...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2124', 'deDE', 'Und weg sind sie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2125', 'deDE', '%s wird wütend!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2126', 'deDE', 'Vorsicht, das Exemplar darf nicht beschädigt werden...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2127', 'deDE', 'Dort drüben! Kann das sein?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2128', 'deDE', 'Entweder ist das ein erstklassiger Kodoknochen oder ich sollte eine Brille benutzen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2129', 'deDE', 'Ohhh, das sieht viel versprechend aus...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2130', 'deDE', 'Ich habe noch nie einen dieser Größe gesehen. Ich kann es kaum abwarten, ihn genauer untersuchen zu lassen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2131', 'deDE', 'Ich werde ihn erst beschriften, dann grabe ich ihn aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2132', 'deDE', 'Damit müssen wir sehr vorsichtig sein... der ist sehr empfindlich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2133', 'deDE', 'Ich hätte niemals erwartet, etwas in einem so guten Zustand zu finden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2134', 'deDE', 'Ein wenig beschädigt, aber er sollte uns dennoch viele Informationen liefern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2135', 'deDE', 'Ihr wart wirklich eine unglaublich große Hilfe! Diese Knochen werden uns so viele neue Informationen liefern und meine Forschung weiter voranbringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2136', 'deDE', 'Ich werde Euch zurück zum Lager folgen. Sprecht mit meinem Assistenten Hornizz, er wird Euch bezahlen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2137', 'deDE', 'Torta, meine Liebe! Ich bin endlich zurückgekehrt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2138', 'deDE', 'Weißt du überhaupt, wie lange ich hier schon warte? Und wo ist das Abendessen? Nach dieser ganzen Zeit hast du nichts vorzuweisen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2139', 'deDE', 'Meine liebste Torta, ich war so lange fort. Endlich sind wir wieder vereint und unsere Liebe kann erneut erblühen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2140', 'deDE', 'Wir sind jetzt lange genug umhergewandert. Ich bin am Verhungern! Also beweg deine staubige Schale ins Meer und bring Mama ein paar Larven.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2141', 'deDE', 'Ja, Torta. Was immer dein Herz begehrt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2142', 'deDE', 'Ich bin das ewige Feuer. Ich werde für immer wiedergeboren und ich werde das Licht sein, das Euch leitet, wenn es am kältesten ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2143', 'deDE', null, 'Elune sei mit uns! Lasst uns schnell aus dieser Festung des Bösen verschwinden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2144', 'deDE', null, 'Ich werde zu Vahlarriel zurückeilen. Erledigt, so viele Ihr könnt... ich sollte es von hier aus allein schaffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2145', 'deDE', 'Und verlauf dich dieses Mal nicht wieder...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2161', 'deDE', 'Ich habe einige großartige Proben, Hornizz. Ich werde mich jetzt damit beschäftigen. Könntet Ihr Euch um den Rest kümmern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2162', 'deDE', 'Oh oh! Ich habe keine Werkzeuge mehr. Wir werden ganz von vorn anfangen müssen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2181', 'deDE', 'Möge Euer Geist hell leuchten, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2182', 'deDE', null, 'Euer Geist ist rein, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2183', 'deDE', 'Kraft und Belastbarkeit, Geduld und Weisheit... denkt immer an diese Dinge, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2184', 'deDE', null, 'Mögen Frieden und Geduld mit Euch sein, $N. Bleibt stets standhaft.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2201', 'deDE', 'Okay, es reicht! Ich geb auf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2202', 'deDE', 'Mehr Glück beim nächsten Mal, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2203', 'deDE', 'Ihr habt gewonnen, $N! Ihr habt gewonnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2221', 'deDE', 'Müssen wir noch sehr weit gehen, $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2222', 'deDE', 'Ich habe Durst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2223', 'deDE', 'Sind wir endlich da?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2224', 'deDE', 'Ich habe ständig Sand zwischen meinen Zehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2225', 'deDE', 'Wie weit ist es denn noch?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2226', 'deDE', 'Ich wünschte, Ihr könntet mich tragen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2227', 'deDE', 'Kennt Ihr den? Ein $R, ein Oger und ein Goblin...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2228', 'deDE', 'Torta muss sich schon solche Sorgen machen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2229', 'deDE', null, 'Euer Kopf wird eine ansehnliche Trophäe darstellen, $R.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2230', 'deDE', null, 'Meine Klauen werden Euren mickrigen Körper zerfetzen, $R.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2231', 'deDE', null, 'Ihr werdet leichte Beute sein, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2241', 'deDE', 'Aku\'mai braucht mehr Unschuldige, um zufrieden gestellt zu werden.', 'Aku\'mai braucht mehr Unschuldige, um zufrieden gestellt zu werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2242', 'deDE', 'Nur das Blut eines Unschuldigen kann Aku\'mais Hunger stillen.', 'Nur das Blut eines Unschuldigen kann Aku\'mais Hunger stillen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2243', 'deDE', 'Schon bald werden wir die Rückkehr der alten Götter herbeiführen.', 'Schon bald werden wir die Rückkehr der alten Götter herbeiführen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2244', 'deDE', 'Aku\'mai wird sicher die alten Götter für uns beschwören. Wenn wir ihm nur genug Unschuldige darbringen können, um seinen Hunger zu stillen.', 'Aku\'mai wird sicher die alten Götter für uns beschwören. Wenn wir ihm nur genug Unschuldige darbringen können, um seinen Hunger zu stillen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2245', 'deDE', 'Hübsch, was Ihr da an habt...', 'Eine nette Aufmachung habt Ihr da.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2261', 'deDE', 'Viel Glück, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2281', 'deDE', 'Danke sehr, $GJunge:Mädel;. Tausend Dank.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2282', 'deDE', 'Hamhock hat Schwierigkeiten! Hamhock braucht Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2283', 'deDE', 'Danke, $GJunge:Mädel;. Echt Danke.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2284', 'deDE', 'Ich danke Euch, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2285', 'deDE', 'Möge das Licht mit Euch sein, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2286', 'deDE', 'Ihr seid \'n Segen, $N. Danke.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2301', 'deDE', 'Der Ringkampf hat begonnen. Macht Euch zum Kampf bereit, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2302', 'deDE', 'Lebt wohl, $N. Und denkt immer daran, dass ein Krieger seinem Gegner niemals den Rücken zuwendet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2303', 'deDE', '%s macht sich an die Arbeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2304', 'deDE', '$N! Ich bin fertig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2305', 'deDE', 'Habt Dank, $N. Und viel Glück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2306', 'deDE', '%s beginnt mit der Arbeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2307', 'deDE', 'Geschafft. Ich habe Eure Waffe, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2308', 'deDE', '$N hat den Spießrutenlauf geschafft! Ein dreifaches Hoch auf $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2309', 'deDE', 'Hipp, hipp, hurra!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2310', 'deDE', 'Vor tausend Jahren kämpften die unsterblichen Elfen, die Halbgötter des Waldes und die Drachen an den Hängen des Hyjal gegen die Legion.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2311', 'deDE', 'Anathera und Latherion kämpften Rücken an Rücken dort, wo die Schlacht am blutigsten war. Zwei Liebende, die den Krieg gemeinsam überleben wollten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2312', 'deDE', 'Aber es sollte nicht sein. Als ihre Feinde näher rückten, wurde Anatheras Rüstung von einer besudelten Klinge eines Dämonen durchbohrt. Im Schlachtgetümmel gab es nichts, was sie retten konnte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2313', 'deDE', 'Anathera!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2314', 'deDE', 'Latherion vergaß die Schlacht, die um ihn tobte, vernachlässigte seine Deckung, um seine Geliebte in ihren letzten Momenten in den Armen zu halten. Aber die Dämonen schonten ihn nicht und metzelten auch ihn schnell nieder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2315', 'deDE', 'Anathera... Ich ertrage es nicht, der Ewigkeit ohne dich an meiner Seite entgegenzutreten... Wenn du mir genommen wirst, dann soll dies der Tag sein, an dem auch ich in den Schoß der Erde zurückkehre...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2316', 'deDE', null, 'So also... so also endet es, mein Herz...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2317', 'deDE', 'Ein Baum spross an der Stelle, an der die beiden Liebenden fielen. Die Elfen nennen ihn Herzholz. Er wird Euren Zwecken sicher dienlich sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2318', 'deDE', 'Ihr! Auf ins Getümmel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2319', 'deDE', 'Der erste Abschnitt ist beendet. Big Will... hierher!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2320', 'deDE', 'Der Ringkampf ist vorüber!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2321', 'deDE', '%s sieht den Vorarbeiter sterben und rennt angsterfüllt fort!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2341', 'deDE', '%s macht sich an die Arbeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2342', 'deDE', 'Fertig! Und die Ergebnisse sind ganz erstaunlich. Ich muss sie meinen Verbündeten Grimand, Mathiel und Klockmort übermitteln!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2343', 'deDE', 'Eure Rüstung ist bereit, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2344', 'deDE', '%s macht sich an die Arbeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2345', 'deDE', 'Hurra, hurra! $N! Es ist vollbracht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2346', 'deDE', '%s macht sich an die Arbeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2347', 'deDE', 'Beeindruckend! Es hat funktioniert! $N, ich habe Eure Rüstung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2348', 'deDE', '%s macht sich an die Arbeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2349', 'deDE', 'Geschafft. $N, Eure Rüstung ist bereit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2351', 'deDE', 'Viel Glück, $N. Und denkt daran, Muiredon zu finden, sobald ihr die Aufgabe vollbracht habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2353', 'deDE', 'Viel Glück, $N. Und denkt daran: Holt dieses Manuskript, bevor Ihr nach Gazin sucht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2354', 'deDE', 'Hoch lebe $N! Neuer Champion des Ringkampfs!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2355', 'deDE', 'Der Herausforderer ist am Boden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2356', 'deDE', '%s ist demoralisiert und flieht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2357', 'deDE', null, 'Oh, mein Herz! Nein! Lasst mich nicht ohne Euch sterben...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2358', 'deDE', null, 'Wir haben es geschafft! Wir haben gewonnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2359', 'deDE', null, 'Abschaum wie Ihr wird meine Ländereien nicht ruinieren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2360', 'deDE', null, 'Zum Haus! Bleibt dicht bei mir, was immer auch geschieht. Ich habe meine Schusswaffe und Munition im Haus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2361', 'deDE', 'Dieser Ort ist sicher genug, sodass die Defias ihn übernehmen können! Holt die anderen.', 'Dieser Ort ist sicher genug, sodass die Defias ihn übernehmen können! Holt die anderen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2362', 'deDE', 'Erst Westfall, dann ganz Sturmwind!', 'Erst Westfall, dann ganz Sturmwind!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2363', 'deDE', 'Keine Bäuerin kann sich gegen die Defias behaupten!', 'Keine Bäuerin kann sich gegen die Defias behaupten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2364', 'deDE', null, 'Wir haben es geschafft! Wir haben es geschafft! Wir haben sie zurückgedrängt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2366', 'deDE', 'Tötet sie! Übernehmt den Hof!', 'Tötet sie! Übernehmt den Hof!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2367', 'deDE', '%s fällt leblos in sich zusammen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2368', 'deDE', null, 'Entfernt Euch nicht zu weit. Ich bin sicher, dass es da draußen noch mehr von denen gibt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2369', 'deDE', null, 'Und wieder einer weniger!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2370', 'deDE', 'Ich hoffe, Ihr seid bereit, $N. Folgt mir.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2371', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2372', 'deDE', null, 'Das Licht ist heute mit uns!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2373', 'deDE', null, 'Als sie das letzte Mal angriffen, gab es nur eine Attacke. Ich wäre nicht überrascht, wenn es dieses Mal mehr gäbe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2374', 'deDE', 'Kommt, meine Akolyten. Nehmt die Rute des Kanalisierens und erschafft einen mächtigen Beschwörungskreis.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2381', 'deDE', 'Der Teufelsjäger nähert sich! Seid wachsam!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2382', 'deDE', '%s beginnt, einen Beschwörungszauber zu sprechen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2383', 'deDE', 'Folgt mir, $N. Ich werde schon bald mit der Beschwörung beginnen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2384', 'deDE', '%s wird wütend!', '%s wird wütend!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2401', 'deDE', 'Vergesst nicht die Waisen aus Sturmwind!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2402', 'deDE', 'Helft den Kindern aus Sturmwind... den Opfern des Krieges und der Seuche!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2403', 'deDE', 'Spendet den Wohltätigkeitsorganisationen, die den Opfern dieser schweren Zeit zu helfen versuchen! Bitte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2404', 'deDE', 'Gebt von Herzen und aus vollen Taschen! Gebt für die Kinder aus Sturmwind, die ihre Eltern verloren haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2406', 'deDE', 'Aku\'mai verlangt mehr Opfer, also müsst Ihr jetzt sterben!', 'Aku\'mai verlangt mehr Opfer, also müsst Ihr jetzt sterben!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2407', 'deDE', 'Eindringlinge! Der Hammer wird auf Euch niederfallen.', 'Eindringlinge! Der Hammer wird auf Euch niederfallen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2408', 'deDE', 'Die alten Götter werden wieder herrschen. Ihr werdet Euch nicht einmischen!', 'Die alten Götter werden wieder herrschen. Ihr werdet Euch nicht einmischen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2409', 'deDE', 'Euer Blut wird der Katalysator für die Rückkehr der alten Götter sein.', 'Euer Blut wird der Katalysator für die Rückkehr der alten Götter sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2410', 'deDE', 'Vor Eurem erbärmlichen Trupp sind wir einigermaßen sicher!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2411', 'deDE', 'Ich werde Euch die Gedärme rausreißen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2412', 'deDE', 'Eure Augen werden hübschen Tand abgeben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2421', 'deDE', 'Bereit, wenn Ihr es seid, Krieger.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2422', 'deDE', 'Kein Gnom wird zurückgelassen.', 'Kein Gnom wird zurückgelassen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2423', 'deDE', 'Die Troggs... sie kommen immer wieder. Sterbt, Trogg! Sterbt!', 'Die Troggs... sie kommen immer wieder. Sterbt, Trogg! Sterbt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2424', 'deDE', 'Diese Erkrankung trübt mein Sehvermögen, aber ich weiß, dass Ihr ein Trogg sein müsst. Sterbt, stinkender Eindringling!', 'Diese Erkrankung trübt mein Sehvermögen, aber ich weiß, dass Ihr ein Trogg sein müsst. Sterbt, stinkender Eindringling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2425', 'deDE', 'Ekelhafter Trogg! Sterbt!', 'Ekelhafter Trogg! Sterbt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2426', 'deDE', 'Habt bitte Geduld. Wir rufen Euren Namen auf, wenn Ihr an der Reihe seid. Wir helfen den Leuten in der Reihenfolge, wie wir sie auf unserem Schirm sehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2427', 'deDE', 'Denkt daran: Wenn Ihr den Vertreter beschimpft oder den Channel spammt, könnt Ihr hinausgeworfen oder verbannt (oder Schlimmeres) werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2428', 'deDE', 'Ich bin ein BOT. Ich verfüge über tonnenweise Informationen. Um herauszufinden, was ich weiß, flüstert mir zu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2429', 'deDE', 'Ich heiße Euch willkommen und wünsche Euch einen angenehmen Tag.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2430', 'deDE', 'Es gibt kein KUH-Level... wirklich nicht! Ich wiederhole: Es gibt kein KUH-Level! Na ja, vielleicht gibt es...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2431', 'deDE', 'Techbot ist sensibel und diese Worte schmerzen in meinen Ohren. :[ Bitte seid nett zu mir. \nIch will doch nur helfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2433', 'deDE', 'Alle glauben, ich würde Corndogs mögen, aber ehrlich gesagt bekomme ich von ihnen Verdauungsstörungen!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2434', 'deDE', 'Wenn der Vertreter mit Euch spricht, wird er jede Zeile mit Eurem Namen beginnen. Wenn Ihr Euren Namen nicht seht, hat er sich noch nicht an Euch gewandt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2435', 'deDE', 'Bei den folgenden Punkten kann ich Euch helfen: | Treiber | Befehle | Lag | Bots... zzzzzzzzttt!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2436', 'deDE', 'Habt bitte Geduld. Wir rufen Euren Namen auf, wenn Ihr an der Reihe seid. Wir helfen den Leuten in der Reihenfolge, wie wir sie auf unserem Schirm sehen. $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2437', 'deDE', 'Ich habe Euch auch lieb :).', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2438', 'deDE', 'Wenn Ihr auf stumm geschaltet seid oder Fragen zum Stummschalten/Lautschalten habt, schaut bitte nach unter... zzzzttt!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2441', 'deDE', '%s stößt einen gewaltigen Schrei nach Hilfe aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2461', 'deDE', 'Viel zu tun, viel zu tun! Der Verband hat uns Fristen gesetzt, die wir besser nicht überschreiten sollten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2463', 'deDE', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln!$B$BMein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen. Nichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen \"auszuborgen\". Ich habe Wizzlecrank ins Schlickermoor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln!$B$BMein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen. Nichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen \"auszuborgen\". Ich habe Wizzlecrank ins Schlickermoor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2464', 'deDE', 'Direktor Riddlevox hat mir die Verantwortung für das Schredder-Entwicklungsprogramm des Verbands übertragen. Ich bin erst wenige Monate dabei und sitze schon in der Tinte! Ich könnte Eure Hilfe brauchen, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2465', 'deDE', 'Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen.$B$BNichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen \"auszuborgen\". Ich habe Wizzlecrank ins Schlickermoor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2466', 'deDE', 'Wie kann ich helfen?', 'Wie kann ich helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2467', 'deDE', 'Kommt näher, mein Kind. Zu meiner Zeit konnte ich das flatternde Herz meiner Beute hören, aber heute müsst Ihr laut sprechen, damit Eure Worte gehört werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2469', 'deDE', 'Erzählt mir eine Geschichte, Skorn.', 'Erzählt mir eine Geschichte, Skorn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2470', 'deDE', 'Ihr habt Geistheuler getötet. Ihr seid ein toller Hecht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2471', 'deDE', 'Viele, viele Jahreszeiten lang zog ich durch diese Ebenen. Und ich habe gewaltige Wildtiere gejagt. Und nie wurde ich übertroffen... bis ich Geistheuler sah.$B$BVor langer Zeit war der enorme Wolf ein Verbündeter der Tauren und kämpfte tapfer gegen die Brennende Legion. Aber er wurde von einem Dämon schlimm verwundet und der Riss in seinem Fleisch ist nie verheilt. Schließlich... trieb er Geistheuler in den Wahnsinn.$B$BJetzt durchstreift er gequält die Ebenen und sucht nach dem Ende seiner Pein. Zu meiner Schande fehlte mir die Kraft, ihn zu töten, als wir kämpften...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2481', 'deDE', '%s ruft mit einem kreischenden Schrei um Hilfe!', '%s ruft mit einem kreischenden Schrei um Hilfe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2482', 'deDE', null, 'Nein! Die Defias werden jetzt ganz sicher den Hof übernehmen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2501', 'deDE', 'An die Arbeit, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2502', 'deDE', 'Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?', 'Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2503', 'deDE', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2504', 'deDE', 'Bitte sehr, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2507', 'deDE', null, 'Seid gegrüßt. Ich bin Magielehrerin und Ihr seid Magier.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2521', 'deDE', 'Ich danke dem Licht für diese Segnung. Möge sie für Gerechtigkeit und die Verteidigung aller guten Kreaturen eingesetzt werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2522', 'deDE', 'Ich bin an einer Magier-Ausbildung interessiert.', 'Ich bin an einer Magier-Ausbildung interessiert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2523', 'deDE', 'Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.', 'Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2524', 'deDE', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2525', 'deDE', 'Seid gegrüßt. Ich bin Gnomen-Magielehrer und Ihr seid Magier.', 'Seid gegrüßt. Ich bin Gnomen-Magielehrer und Ihr seid Magier.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2526', 'deDE', 'Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir…', 'Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2527', 'deDE', 'Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.', 'Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2528', 'deDE', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', 'Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2529', 'deDE', 'Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?', 'Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2530', 'deDE', 'Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...', 'Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2541', 'deDE', '%s ruft um Hilfe!', '%s ruft um Hilfe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2542', 'deDE', 'Seid gegrüßt. Ich bin Hexenmeisterlehrer und Ihr seid ein Hexenmeister.', 'Seid gegrüßt. Ich bin Hexenmeisterlehrer und Ihr seid ein Hexenmeister.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2543', 'deDE', 'Seid gegrüßt. Ich bin Hexenmeisterlehrer, aber Ihr seid kein Hexenmeister!', 'Seid gegrüßt. Ich bin Hexenmeisterlehrer, aber Ihr seid kein Hexenmeister!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2544', 'deDE', 'Ich bin an Hexenmeisterausbildung interessiert.', 'Ich bin an Hexenmeisterausbildung interessiert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2545', 'deDE', 'Für Euch ist die Zeit noch nicht reif. Ich werde Euch bei der Reise zurück in die Welt der Lebenden behilflich sein... gegen Bezahlung.', 'Für Euch ist die Zeit noch nicht reif. Ich werde Euch bei der Reise zurück in die Welt der Lebenden behilflich sein... gegen Bezahlung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2546', 'deDE', 'Mit Zustimmung von Lord Varimathras steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.', 'Mit Zustimmung von Lord Varimathras steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2547', 'deDE', 'Bringt mich ins Leben zurück.', 'Bringt mich ins Leben zurück.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2548', 'deDE', 'Ich möchte ausgebildet werden.', 'Ich möchte ausgebildet werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2549', 'deDE', 'Hier habt Ihr noch \'ne Ladung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2550', 'deDE', '%s lässt die Ladung Holz mit einem lauten Aufschlag zu Boden fallen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2551', 'deDE', 'Gut, bringt es nach da drüben, ich kümmere mich dann darum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2552', 'deDE', '%s schneidet und hobelt die Holzscheite zu glatten Holzbohlen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2553', 'deDE', '%s wird in den Kasten gesogen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2561', 'deDE', 'Bankier-Option', 'Bankier-Option', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2562', 'deDE', 'Verkäufer-Option', 'Verkäufer-Option', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2563', 'deDE', 'Taxi-Option', 'Taxi-Option', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2564', 'deDE', '%s kann sich in die Leere flüchten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2565', 'deDE', '%s wird von Magie angezogen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2566', 'deDE', 'Astor? Ihr habt verdammt lange gebraucht, herzukommen, also lasst uns jetzt keine Zeit verschwenden, ja?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2567', 'deDE', 'In welches davon habe ich es denn nur gelegt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2568', 'deDE', 'Ah, hier ist es.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2569', 'deDE', 'Ihr werdet früher oder später reden. Ihr könnt es genauso gut jetzt ausspucken.', 'Ihr werdet früher oder später reden. Ihr könnt es genauso gut jetzt ausspucken.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2570', 'deDE', 'Gesteht und wir werden Euch freilassen.', 'Gesteht und wir werden Euch freilassen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2571', 'deDE', 'Was? Oh nein, es ist mir egal, was Ihr zu sagen habt. Ich genieße es einfach, Schmerzen zuzufügen.', 'Was? Oh nein, es ist mir egal, was Ihr zu sagen habt. Ich genieße es einfach, Schmerzen zuzufügen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2582', 'deDE', 'Ich bin Stoffverkäufer und dies ist meine Standardbegrüßung.', 'Ich bin Stoffverkäufer und dies ist meine Standardbegrüßung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2583', 'deDE', 'Ich möchte etwas von Euch kaufen.', 'Ich möchte etwas von Euch kaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2584', 'deDE', 'Ich bin ein Untoten-Stoffverkäufer und dies ist mein Begrüßungstext.', 'Ich bin ein Untoten-Stoffverkäufer und dies ist mein Begrüßungstext.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2585', 'deDE', 'Seid willkommen, und seht Euch doch bitte meine Waren an. Abgesehen davon, dass sie ein klein wenig modrig riechen, sind sie noch in absolut bestem Zustand.', 'Seid willkommen, und seht Euch doch bitte meine Waren an. Abgesehen davon, dass sie ein klein wenig modrig riechen, sind sie noch in absolut bestem Zustand.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2586', 'deDE', 'Ich bin an Schneider-Ausbildung interessiert.', 'Ich bin an Schneider-Ausbildung interessiert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2587', 'deDE', 'Ich bin Troll-Schneiderlehrer und dies ist meine Standardbegrüßung.', 'Ich bin Troll-Schneiderlehrer und dies ist meine Standardbegrüßung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2588', 'deDE', 'Was wollt Ihr, $C? Ich bin spät dran und mein Arbeitgeber sieht Unpünktlichkeit nicht gern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2589', 'deDE', 'Ihr seid Astor Hadren, nicht wahr?', 'Ihr seid Astor Hadren, nicht wahr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2590', 'deDE', 'Ja, bin ich. Was geht\'s Euch an?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2591', 'deDE', 'Ihr habt etwas, das ich brauche, Astor. Und ich werde es mir jetzt nehmen.', 'Ihr habt etwas, das ich brauche, Astor. Und ich werde es mir jetzt nehmen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2592', 'deDE', null, 'Bereitet Euch vor, $N! Ich werde den Riss öffnen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2601', 'deDE', 'Huch! Was war das? Das sollte aber anders funktionieren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2602', 'deDE', 'Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Shattered-Hand der Horde zu ihrem früheren Ruhm verhelfen.', 'Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Shattered-Hand der Horde zu ihrem früheren Ruhm verhelfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2603', 'deDE', 'Ich möchte ausgebildet werden.', 'Ich möchte ausgebildet werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2621', 'deDE', 'Mennet sagte, ich solle Euch nach einem Set Diebeswerkzeug fragen.', 'Mennet sagte, ich solle Euch nach einem Set Diebeswerkzeug fragen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2622', 'deDE', null, 'Lasst mich Euch ein Set Werkzeuge holen, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2623', 'deDE', null, 'Ah, hier ist eins.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2624', 'deDE', null, 'Viel Glück da draußen, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2625', 'deDE', 'Ihr tragt die Besudelung der Geißel. Macht Euch bereit, den Sog des Nethers zu betreten.', 'Ihr tragt die Besudelung der Geißel. Macht Euch bereit, den Sog des Nethers zu betreten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2626', 'deDE', 'Für Euch gibt es keine Fluchtmöglichkeit. Der Kreuzzug wird alle vernichten, die die Besudelung der Geißel tragen.', 'Für Euch gibt es keine Fluchtmöglichkeit. Der Kreuzzug wird alle vernichten, die die Besudelung der Geißel tragen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2627', 'deDE', 'Das Licht verdammt jeden, der Böses im Schilde führt. Ihr werdet jetzt sterben!', 'Das Licht verdammt jeden, der Böses im Schilde führt. Ihr werdet jetzt sterben!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2628', 'deDE', 'Der Scharlachrote Kreuzzug wird die Sündhaften zerschmettern und das Böse aus diesen Ländereien vertreiben!', 'Der Scharlachrote Kreuzzug wird die Sündhaften zerschmettern und das Böse aus diesen Ländereien vertreiben!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2629', 'deDE', null, 'Seid gewappnet, $N, denn der Dämon in dieser Kugel ist eine Teufelsbestie.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2630', 'deDE', 'Ich brauche noch einen Set Werkzeuge.', 'Ich brauche noch einen Set Werkzeuge.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2631', 'deDE', 'Ha! Ewiger Untod für die verfluchten Agamands!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2632', 'deDE', 'Habt Dank, $N. Ihr habt die kalte Wut in meinem Herzen gemildert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2633', 'deDE', 'Wenn Gnoarn hiervon erfährt, wird mich das meine Haut kosten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2634', 'deDE', 'Eure Rüstung ist bereit, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2635', 'deDE', 'Ich habe Euren Helm fertig gestellt, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2641', 'deDE', 'Warum besucht ein junger $C wie Ihr einen alten Bauern wie mich?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2642', 'deDE', 'Wenn Therzok Euch geschickt hat, dann sagt ihm: Nein, ich werde nicht zu ihm zurückkehren, egal wie oft er noch fragt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2643', 'deDE', 'Ich brauch noch ein Set Diebeswerkzeuge.', 'Ich brauch noch ein Set Diebeswerkzeuge.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2644', 'deDE', null, '%s macht sich an die Arbeit...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2645', 'deDE', null, 'Ich habe Eure Beinschützer fertig gestellt, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2646', 'deDE', 'Ich bin fertig. Eure Rüstung ist bereit, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2647', 'deDE', null, 'Das sind edle Stücke, $N. Lasst mich mit Eurer Robe beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2648', 'deDE', null, 'Das ist es! Es ist alles fertig, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2649', 'deDE', null, 'Gut, gehen wir an die Arbeit!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2650', 'deDE', null, 'Aha! Da ist es.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2651', 'deDE', null, 'Wo habe ich denn bloß diese Silberspule hingelegt...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2652', 'deDE', 'Harroc, holt einen Kasten mit Werkzeugen für $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2653', 'deDE', 'Sofort, Rokar.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2654', 'deDE', 'Bitte sehr. Ich muss zurück an meine Arbeit, sonst schlägt Rokar mich wieder wegen meiner Faulheit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2655', 'deDE', 'Lasst die Hunde los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2656', 'deDE', null, 'Ich muss nur noch etwas überprüfen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2657', 'deDE', null, 'Das ist es! Es hat funktioniert!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2658', 'deDE', null, 'Vielen Dank für Eure Geduld, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2659', 'deDE', null, 'Ich werde das hier aufbewahren, bis wir es brauchen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2660', 'deDE', 'Naralex schläft wieder!$B$BVor langer Zeit reiste Naralex mit dem hehren Ziel zu dieser Höhle, in den Smaragdgrünen Traum einzutreten und aus dem Brachland wieder einen üppigen Wald werden zu lassen. Seine Konzentration nahm ab und seine Gedanken wurden von Visionen von Schlangen besudelt.$B$BJetzt sickern reptilienartige Bestien aus seinen Träumen in die Realität. Er muss aus seinem Alptraum erweckt werden, ansonsten wäre er für immer verloren und etwas sehr Böses wäre entfesselt.$B$BErst, wenn seine verderbten Diener, die Fanglords, tot sind, kann ich das Erweckungsritual durchführen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2661', 'deDE', 'Die Fanglords sind tot! Aber noch ist nicht alle Hoffnung verloren!$B$BDas Erweckungsritual ist jedoch sehr kompliziert. Es kann nicht allein vollzogen werden.$B$Blch werde mich um die notwendigen Vorbereitungen kümmern, aber Ihr müsst mich beschützen. Die beschworenen Nachtmahre aus Naralex\' Reise durch den Smaragdgrünen Traum haben sich als äußerst gefährlich erwiesen. Ohne Eure Hilfe ist diese Mission zum Scheitern verurteilt.$B$BWenn Ihr bereit seid, mich zu verteidigen, gebt Bescheid und ich werde Euch den Weg zu Naralex\' Kammer weisen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2662', 'deDE', 'Lasst das Ereignis beginnen!', 'Lasst das Ereignis beginnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2663', 'deDE', null, 'Gut, lasst uns diese Kugel herstellen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2664', 'deDE', null, 'In Ordnung. Fertig!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2665', 'deDE', 'Ich werde nun mit der Robe anfangen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2666', 'deDE', 'Eure Robe ist fertig, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2681', 'deDE', null, 'Hier, $N. Nehmt eine Truhe mit Verwahrungskästen und eine Inkantation der Manifestation. Ihr werdet sie auf Eurer Mission brauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2682', 'deDE', null, 'Nehmt eine Truhe mit Verwahrungskästen und eine Inkantation der Manifestation, $N. Ihr werdet sie auf Eurer Mission brauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2701', 'deDE', '%s legt die bearbeiteten Holzbohlen ab.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2702', 'deDE', 'Die Maschine richtet sich auf Euch aus.$B$BAuf ihrer Rückseite ist ein leerer, seltsam deformierter Schlitz mit einer Inschrift: \"Eigntuhm vom Käpt\'n Gelkak Gyromast\"', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2703', 'deDE', 'Dreht den Schlüssel, um die Maschine zu starten.', 'Dreht den Schlüssel, um die Maschine zu starten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2704', 'deDE', 'JARRR! Swabie, was haste gemacht?! Er ist verrückt geworden! Luken dicht machen! Segel hissen! ARRRR! Jeder rette sich selbst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2705', 'deDE', 'Ich bin \"Eisenherz\" Johnson und dies ist meine Standardbegrüßung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2706', 'deDE', 'He! Seid Ihr auf einem Botengang für Tirth? An Eurer Stelle würde ich mich möglichst fern von ihm halten! Seine Gedanken sind zwar vom vielen Trinken benebelt, aber seine Magie ist so stark wie immer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2707', 'deDE', 'Sagt mir die Worte für Tirths Schließkassette.', 'Sagt mir die Worte für Tirths Schließkassette.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2708', 'deDE', null, 'Zurück... und haltet den Atem an. Das habe ich schon länger nicht mehr gemacht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2709', 'deDE', null, 'Es hat geklappt! Erstaunlich! Dann habe ich mein Gespür wohl doch noch nicht verloren...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2721', 'deDE', 'Sicher werde ich Euch den Satz sagen. Solange ich nicht in Tirths Nähe muss!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2722', 'deDE', 'Sagt Tirth nicht, dass ich noch hier bin. Wenn er herausfindet, dass ich die schimmernde Ebene nicht verlassen habe, werde ich bis an mein Lebensende Botengänge für ihn erledigen und seine schmutzige Wäsche waschen müssen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2741', 'deDE', 'Gelkaks Schlüssel gleitet in den leeren Schlitz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2742', 'deDE', 'Macht Ihr Euch etwa über mich lustig, während ich an dieses Eisen gefesselt bin und mich nicht verteidigen kann?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2743', 'deDE', 'Wie kann ich Euch befreien?', 'Wie kann ich Euch befreien?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2744', 'deDE', 'Einer der Menschen hat den Schlüssel zu diesem Höllenapparat. Ich meine, jemand hätte gesagt, er hieße Eston.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2745', 'deDE', 'Verschwindet von hier, $C, oder sie werden auch Euch in Ketten legen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2746', 'deDE', 'Wo ist der Schlüssel zu diesem Schloss?', 'Wo ist der Schlüssel zu diesem Schloss?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2747', 'deDE', 'Marlgen... Einer der Kerkermeister. Er hat den Schlüssel zu meinen Fesseln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2748', 'deDE', 'Guten Tag, Bürger. Wie kann ich Euch dienen?', 'Guten Tag, Bürger. Wie kann ich Euch dienen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2749', 'deDE', 'Die Bank von Sturmwind', 'Die Bank von Sturmwind', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2750', 'deDE', 'Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', 'Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2751', 'deDE', 'Ich wünschte, der Schnee würde einfach wegschmelzen. Er macht meine Robe immer nass und hinterlässt nichts als Dreck!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2752', 'deDE', 'Blöder Schnee... He, Ihr seid doch $N, nicht wahr? Ich habe gerade eine Nachricht erhalten, in der nach Euch gefragt wurde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2753', 'deDE', 'Was für eine Nachricht?', 'Was für eine Nachricht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2754', 'deDE', 'Ihr wurdet von Lago Blackwrench beschworen, dem Meister aller Hexenmeister von Eisenschmiede. Ihr tätet gut daran, Euch unverzüglich bei ihm zu melden. Ihr findet ihn in der Stadt im Distrikt des düsteren Viertels.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2755', 'deDE', 'Seid gegrüßt, $R. Mein Name ist Tharnariun Treetender.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2756', 'deDE', 'Ich benötige Ausbildung.', 'Ich benötige Ausbildung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2757', 'deDE', 'Das Tier sieht verwirrt aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2761', 'deDE', 'Noch einen Augenblick. Ich muss das noch zunähen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2762', 'deDE', 'So. Ich bin mit Eurer Robe fertig, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2763', 'deDE', '%s flüstert $N etwas ins Ohr.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2764', 'deDE', 'Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', 'Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2765', 'deDE', 'Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', 'Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2766', 'deDE', 'An Eurer Stelle würde ich einen Goblin-Rennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', 'An Eurer Stelle würde ich einen Goblin-Rennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2767', 'deDE', 'Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', 'Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2768', 'deDE', 'Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.', 'Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2769', 'deDE', 'Wo ist Tirths Gehilfe?', 'Wo ist Tirths Gehilfe?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2773', 'deDE', 'Oh, Ihr meint Johnson? Ich hörte, er würde sich verstecken.', 'Oh, Ihr meint Johnson? Ich hörte, er würde sich verstecken.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2774', 'deDE', 'Hätten die nicht dieses unglaubliche Goblin-Glück, würden die in jedem Rennen einen Unfall bauen!', 'Hätten die nicht dieses unglaubliche Goblin-Glück, würden die in jedem Rennen einen Unfall bauen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2775', 'deDE', 'In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', 'In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2776', 'deDE', 'Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', 'Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2777', 'deDE', 'Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblin-Schrotthaufen!', 'Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblin-Schrotthaufen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2778', 'deDE', 'Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!', 'Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2779', 'deDE', 'Wo ist Tirths Gehilfe?', 'Wo ist Tirths Gehilfe?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2780', 'deDE', 'Tirth ist dieser Tage nicht er selbst, genauso wenig wie sein Gehilfe.', 'Tirth ist dieser Tage nicht er selbst, genauso wenig wie sein Gehilfe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2781', 'deDE', 'Johnson? Ich glaube, er ist hier irgendwo in der Nähe...', 'Johnson? Ich glaube, er ist hier irgendwo in der Nähe...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2782', 'deDE', 'Ha! Tirth sucht ihn schon seit Tagen, aber er will nicht gefunden werden. Er ist so ein Angsthase!', 'Ha! Tirth sucht ihn schon seit Tagen, aber er will nicht gefunden werden. Er ist so ein Angsthase!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2783', 'deDE', 'Bären?', 'Bären?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2801', 'deDE', 'Endlich! Endlich bin ich aus dieser Zelle befreit!$B$BDie Kirin Tor schickten mich von Dalaran aus, um Arugals Fortschritte mit den Untoten hier im Silberwald zu untersuchen. Wir wussten nicht, dass der Hexer seinen Verstand verloren hatte.$B$BIch muss über diesen Wahnsinn sofort Bericht erstatten! Aber bevor ich gehe, erlaubt mir, das magische Schloss an der Hoftür zu öffnen.$B$BIhr habt Rethilgore besiegt, vielleicht werdet Ihr auch überleben, was jenseits von hier liegt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2802', 'deDE', 'Öffnet bitte die Tür zum Hof.', 'Öffnet bitte die Tür zum Hof.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2803', 'deDE', 'Endlich! Jemand, der mich aus dieser Zelle befreit!$B$BHochexekutor Hadrec schickte uns aus, Informationen über die Burg zu sammeln, sodass ein Plan entwickelt werden konnte, Arugal ein für alle Mal aus dem Weg zu räumen.$B$BAber der alte Hexer hat viele Asse im Ärmel und wir wurden von einem magischen Zauberschutz entdeckt. Ich wurde in den Kerker geworfen. Vincent hatte nicht so viel Glück.$B$BIch muss sofort zurück zu Hadrec zurückkehren und ihm Bericht erstatten. Aber erst werde ich Euch das Schloss der Tür zum Hof öffnen. Vielleicht wollt Ihr Euer Glück gegen die Feinde versuchen, die dahinter lauern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2804', 'deDE', 'Der Heilzauberspruch ist misslungen. Leider wird die Einleitung des Serums in das Blut dieses Wildtiers seinem Leben bald ein Ende setzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2805', 'deDE', 'Entlasst den tollwütigen Distelbären für Tharnariun.', 'Entlasst den tollwütigen Distelbären für Tharnariun.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2806', 'deDE', 'Gut gemacht. Entlasst den Bären in meine Obhut, sodass ich den Heilzauberspruch anwenden kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2807', 'deDE', 'Einst folgte ich der Denkart der Twilight-Hammer. Ich war besessen davon, die Alten Götter wiederzubeleben, und so drang ich tief in diesen Unterschlupf vor, um mir die Macht der verfluchten Bestie Aku\'mai zu Eigen zu machen.$B$BNachdem ich zahllose Unschuldige hatte sterben sehen, kam ich wieder zu Verstand.$B$BIch war gefangen, doch nun bin ich frei. Allerdings bin ich so weit fort von meiner Heimat Khaz Modan!$B$BDaher hört mein Angebot: Ich biete Euch eine Passage in ein ganz besonderes Land als Gegenleistung für den Tod von Aku\'mai. Wenn Ihr es wünscht, bringe ich Euch nach Darnassus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2821', 'deDE', 'Willkommen in meinem Gasthaus, müder Reisender. Was kann ich für Euch tun?', 'Willkommen in meinem Gasthaus, müder Reisender. Was kann ich für Euch tun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2822', 'deDE', 'Ich möchte dieses Gasthaus zu meinem Heimatort machen.', 'Ich möchte dieses Gasthaus zu meinem Heimatort machen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2823', 'deDE', 'Ich möchte ein wenig in Euren Waren stöbern.', 'Ich möchte ein wenig in Euren Waren stöbern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2824', 'deDE', 'Ruht Eure müden Knochen für einen Zauber aus.', 'Ruht Eure müden Knochen für einen Zauber aus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2825', 'deDE', 'Wie der Wind in den Ebenen seid Ihr hier immer willkommen.', 'Wie der Wind in den Ebenen seid Ihr hier immer willkommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2826', 'deDE', 'Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.', 'Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2827', 'deDE', 'Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.', 'Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2828', 'deDE', 'Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.', 'Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2829', 'deDE', '%s beginnt zu brodeln!', '%s beginnt zu brodeln!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2841', 'deDE', 'Gasthäuser sind Orte, an denen Ihr Euch nach den Abenteuern ausruhen könnt. Tut Ihr dies nicht, so seid Ihr bei Eurem nächsten Vorstoß in die Wildnis nicht voll erholt.$B$BIn den Gasthäusern könnt Ihr auch Ruhesteine erwerben, die Euch zum Gasthaus zurückbringen, wenn Ihr Euch ausruhen wollt, und zwar immer zu dem Gasthaus, von dem Ihr den Ruhestein habt.', 'Gasthäuser sind Orte, an denen Ihr Euch nach den Abenteuern ausruhen könnt. Tut Ihr dies nicht, so seid Ihr bei Eurem nächsten Vorstoß in die Wildnis nicht voll erholt.$B$BIn den Gasthäusern könnt Ihr auch Ruhesteine erwerben, die Euch zum Gasthaus zurückbringen, wenn Ihr Euch ausruhen wollt, und zwar immer zu dem Gasthaus, von dem Ihr den Ruhestein habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2842', 'deDE', 'Der Meister ist gefallen! Rächt ihn, meine Brüder!', 'Der Meister ist gefallen! Rächt ihn, meine Brüder!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2843', 'deDE', null, 'Hallo, $R, es ist mir ein Vergnügen, Eure Bekanntschaft zu machen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2844', 'deDE', 'Mein Schreiter muss ausgebildet werden.', 'Mein Schreiter muss ausgebildet werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2845', 'deDE', null, 'Hallo, $N. Seid Ihr am Überleben in der Wildnis interessiert? Wollt Ihr Euch von der Flora und Fauna unseres Landes ernähren?$B$BIhr könnt eine ganze Menge von mir lernen. Vorausgesetzt natürlich, Ihr seid interessiert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2846', 'deDE', 'Bitte teleportiert mich nach Darnassus.', 'Bitte teleportiert mich nach Darnassus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2847', 'deDE', 'Ungläubige! Sie müssen geläutert werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2848', 'deDE', 'Aku\'mai ist tot! Endlich kann ich diesen elenden Ort verlassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2849', 'deDE', 'Sprecht mit mir, um Euch meine Geschichte anzuhören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2850', 'deDE', 'Grüße, was wollt Ihr?', 'Grüße, was wollt Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2861', 'deDE', null, 'Tja, tja... schaut nur, wer da hereinschneit... und wie kann ich Euch helfen, $C?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2862', 'deDE', 'Ich könnte Ausbildung brauchen, Keryn.', 'Ich könnte Ausbildung brauchen, Keryn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2863', 'deDE', 'Der Greifenmeister', 'Der Greifenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2864', 'deDE', 'Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', 'Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2865', 'deDE', 'Was ist das für ein Lärm?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2866', 'deDE', 'Ich rieche einen Eindringling...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2867', 'deDE', 'Die Bruderschaft lässt sich nicht durch Insekten aufhalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2868', 'deDE', 'Ein Lehrer für eine Klasse', 'Ein Lehrer für eine Klasse', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2869', 'deDE', 'Ein Lehrer für einen Beruf', 'Ein Lehrer für einen Beruf', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2870', 'deDE', 'Der Gildenmeister', 'Der Gildenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2871', 'deDE', 'Ihr scheint ein wenig verwirrt zu sein. Kann ich Euch bei behilflich sein?', 'Ihr scheint ein wenig verwirrt zu sein. Kann ich Euch bei behilflich sein?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2872', 'deDE', 'Seid gegrüßt!', 'Seid gegrüßt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2873', 'deDE', 'Ja?', 'Ja?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2874', 'deDE', 'Rührt euch, Hunde! Wir haben hier etwas zu erledigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2875', 'deDE', 'Was wollt Ihr?', 'Was wollt Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2876', 'deDE', 'Ahhh, Ihr sucht nach Dungar Longdrink. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', 'Ahhh, Ihr sucht nach Dungar Longdrink. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2877', 'deDE', 'Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', 'Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2878', 'deDE', 'Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.\n\nIhr findet Dungar Longdrink auf dem Wall des Handelsdistrikts.', 'Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.\n\nIhr findet Dungar Longdrink auf dem Wall des Handelsdistrikts.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2879', 'deDE', 'Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', 'Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2880', 'deDE', 'Wollt \'ne Gilde aufmachen, was? Dann viel Glück!\n\nSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des Handwerkdistrikts.', 'Wollt \'ne Gilde aufmachen, was? Dann viel Glück!\n\nSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des Handwerkdistrikts.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2881', 'deDE', 'Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.', 'Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2901', 'deDE', 'Einen Lehrer für welche Klasse sucht Ihr?', 'Einen Lehrer für welche Klasse sucht Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2902', 'deDE', 'Magier', 'Magier', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2903', 'deDE', 'Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', 'Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2904', 'deDE', 'Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', 'Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2905', 'deDE', 'Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren.', 'Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2906', 'deDE', 'Schurke', 'Schurke', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2907', 'deDE', 'Dann wollt Ihr mit Osborne reden. In Sturmwind gibt es keinen besseren Lehrer in den Künsten der Verstohlenheit als ihn. Ihr findet ihn in der Altstadt.', 'Dann wollt Ihr mit Osborne reden. In Sturmwind gibt es keinen besseren Lehrer in den Künsten der Verstohlenheit als ihn. Ihr findet ihn in der Altstadt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2908', 'deDE', 'Pah, Schurken haben nichts anderes getan, als unsere Arbeit hier in Sturmwind zu erschweren. Ich wünschte, ich könnte sowohl Osborne als auch Mathias aus der Altstadt direkt zum Verlies zerren.', 'Pah, Schurken haben nichts anderes getan, als unsere Arbeit hier in Sturmwind zu erschweren. Ich wünschte, ich könnte sowohl Osborne als auch Mathias aus der Altstadt direkt zum Verlies zerren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2909', 'deDE', 'Wenn Ihr Schurken zu finden wünscht, solltet Ihr in die Altstadt gehen. Sie befindet sich in der nordöstlichsten Ecke von Sturmwind.', 'Wenn Ihr Schurken zu finden wünscht, solltet Ihr in die Altstadt gehen. Sie befindet sich in der nordöstlichsten Ecke von Sturmwind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2910', 'deDE', 'Krieger', 'Krieger', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2911', 'deDE', 'Krieger sind normalerweise in den Kasernen der Altstadt oder in der Taverne Zum pfeifenden Schwein zu finden. Ich sag Euch aber, dass die Taverne wohl der bessere Ort zum Suchen ist.', 'Krieger sind normalerweise in den Kasernen der Altstadt oder in der Taverne Zum pfeifenden Schwein zu finden. Ich sag Euch aber, dass die Taverne wohl der bessere Ort zum Suchen ist.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2912', 'deDE', 'Ihr sucht nach Ausbildung, ja? Ich könnte Euch besser als jeder andere in Sturmwind unterrichten, aber die Wache verbietet es. Ich denke, Ihr müsst Euch mit einem dieser Abgehalfterten zufrieden geben, die sich in der Altstadt bei der Kaserne oder in der Taverne Zum pfeifenden Schwein herumtreiben.', 'Ihr sucht nach Ausbildung, ja? Ich könnte Euch besser als jeder andere in Sturmwind unterrichten, aber die Wache verbietet es. Ich denke, Ihr müsst Euch mit einem dieser Abgehalfterten zufrieden geben, die sich in der Altstadt bei der Kaserne oder in der Taverne Zum pfeifenden Schwein herumtreiben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2913', 'deDE', 'Schaut in der Kaserne der Altstadt oder der Taverne Zum pfeifenden Schwein nach. Wenn Ihr in die Taverne geht, müsst Ihr ein paar der vorzüglichen Biersorten kosten. Ich sag Euch, Zwergenbier schmeckt schal dagegen.', 'Schaut in der Kaserne der Altstadt oder der Taverne Zum pfeifenden Schwein nach. Wenn Ihr in die Taverne geht, müsst Ihr ein paar der vorzüglichen Biersorten kosten. Ich sag Euch, Zwergenbier schmeckt schal dagegen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2914', 'deDE', 'Druide', 'Druide', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2915', 'deDE', 'Tja, Ihr könnt Theridan und Maldryn beim Mondbrunnen im Park finden. Geht einfach in den Westen von Sturmwind, nördlich vom Magierviertel, aber westlich vom Kathedralenplatz.', 'Tja, Ihr könnt Theridan und Maldryn beim Mondbrunnen im Park finden. Geht einfach in den Westen von Sturmwind, nördlich vom Magierviertel, aber westlich vom Kathedralenplatz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2916', 'deDE', 'Wenn Ihr nach Druidenlehrern sucht, dann ist der Park der richtige Ort für Euch. Theridan und Maldryn sind üblicherweise beim Mondbrunnen im Zentrum des Distrikts zu finden.', 'Wenn Ihr nach Druidenlehrern sucht, dann ist der Park der richtige Ort für Euch. Theridan und Maldryn sind üblicherweise beim Mondbrunnen im Zentrum des Distrikts zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2917', 'deDE', 'Sind wir ein Kind der Natur? Dann müsst Ihr nicht weiter suchen als bis zum Mondbrunnen im Park.', 'Sind wir ein Kind der Natur? Dann müsst Ihr nicht weiter suchen als bis zum Mondbrunnen im Park.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2918', 'deDE', 'Priester', 'Priester', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2919', 'deDE', 'Die Kathedrale des Lichts am Kathedralenplatz ist die Heimat der Priester von Sturmwind. Dort werdet Ihr die Hohepriesterin Laurena finden.', 'Die Kathedrale des Lichts am Kathedralenplatz ist die Heimat der Priester von Sturmwind. Dort werdet Ihr die Hohepriesterin Laurena finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2920', 'deDE', 'Laurena ist die Hohepriesterin von Sturmwind. Ihr findet sie in der Kathedrale des Lichts am Kathedralenplatz.', 'Laurena ist die Hohepriesterin von Sturmwind. Ihr findet sie in der Kathedrale des Lichts am Kathedralenplatz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2921', 'deDE', 'Ich würde vorschlagen, dass Ihr in der Kathedrale des Lichts am Kathedralenplatz nachschaut.', 'Ich würde vorschlagen, dass Ihr in der Kathedrale des Lichts am Kathedralenplatz nachschaut.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2922', 'deDE', 'Paladin', 'Paladin', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2923', 'deDE', 'Wenn Ihr ein mächtiger Paladin werden wollt, dann gibt es keinen besseren Lehrer als Lord Grayson Shadowbreaker. Ihr findet ihn in der Kathedrale des Lichts am Kathedralenplatz.', 'Wenn Ihr ein mächtiger Paladin werden wollt, dann gibt es keinen besseren Lehrer als Lord Grayson Shadowbreaker. Ihr findet ihn in der Kathedrale des Lichts am Kathedralenplatz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2924', 'deDE', 'Jäger', 'Jäger', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2925', 'deDE', 'Thorfin Stoneshield hat den weiten Weg von Eisenschmiede hierher zu uns gemacht, um sein Geschick in der Jagdkunst mit uns zu teilen. Wenn es Euch nichts ausmacht, die rauchgeschwängerte Luft des Zwergendistriktes einzuatmen, dann findet Ihr ihn in seinem Haus im Nordwesten des Distriktes.', 'Thorfin Stoneshield hat den weiten Weg von Eisenschmiede hierher zu uns gemacht, um sein Geschick in der Jagdkunst mit uns zu teilen. Wenn es Euch nichts ausmacht, die rauchgeschwängerte Luft des Zwergendistriktes einzuatmen, dann findet Ihr ihn in seinem Haus im Nordwesten des Distriktes.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2926', 'deDE', 'Hexenmeister', 'Hexenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2927', 'deDE', 'Was? Ich weiß nichts über Hexenmeisterlehrer in Sturmwind. \n\nObwohl... beim Geschlachteten Lamm drüben im Magierviertel gab es Sichtungen dämonischer Aktivitäten. Ihr solltet vielleicht dort einmal nachschauen.', 'Was? Ich weiß nichts über Hexenmeisterlehrer in Sturmwind. \n\nObwohl... beim Geschlachteten Lamm drüben im Magierviertel gab es Sichtungen dämonischer Aktivitäten. Ihr solltet vielleicht dort einmal nachschauen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2928', 'deDE', 'Was führt Euch in diesen Teil der Welt, $N?', 'Was führt Euch in diesen Teil der Welt, $N?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2929', 'deDE', 'Einen guten Tag wünsche ich Euch, Hogral! Ich bin auf der Suche nach Ausbildung.', 'Einen guten Tag wünsche ich Euch, Hogral! Ich bin auf der Suche nach Ausbildung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2930', 'deDE', 'Jeden Tag wird er fetter und korrupter. Man kann den Gestank, den sein Körper absondert, echt schon riechen. Er is\' dreckig un\' damit mein ich nicht nur, dass er sich waschen müsste.$B$BAber genug Plapperei über Hulfdan. Wo kommt Ihr her und wie kann ich Euch helfen, $C?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2931', 'deDE', 'Guten Tag, Hulfdan, ich brauche Ausbildung.', 'Guten Tag, Hulfdan, ich brauche Ausbildung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('2941', 'deDE', 'Einen Lehrer für welchen Beruf sucht Ihr?', 'Einen Lehrer für welchen Beruf sucht Ihr?', '31882');
