INSERT INTO `broadcast_text_locale` VALUES ('3388', 'deDE', 'Möge das Licht Euch den Weg weisen und Euch auf Euren Reisen beschützen. Wenn Ihr Hilfe braucht, stehe ich Euch zur Verfügung.', 'Möge das Licht Euch den Weg weisen und Euch auf Euren Reisen beschützen. Wenn Ihr Hilfe braucht, stehe ich Euch zur Verfügung.', '31882');
