INSERT INTO `broadcast_text_locale` VALUES ('3394', 'deDE', 'He da, $Gwerter Herr:holde Dame;! An Eurer Stelle würde ich aufpassen, wo ich hintrete. Ich habe hier eine als Maus getarnte Bombe verloren und kann sie nicht mehr finden.', 'He da, $Gwerter Herr:holde Dame;! An Eurer Stelle würde ich aufpassen, wo ich hintrete. Ich habe hier eine als Maus getarnte Bombe verloren und kann sie nicht mehr finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3395', 'deDE', 'Ein Ingenieur! Hey, endlich kann ich mal die neuen Geräte zeigen, die ich gebaut habe!', 'Ein Ingenieur! Hey, endlich kann ich mal die neuen Geräte zeigen, die ich gebaut habe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3396', 'deDE', 'Wenn Ihr Lasche A in Schlitz B steckt, dann macht\'s natürlich BUMM! Lasst mich Euch mal zeigen, was ich habe. Danach könnt Ihr Lilliam fragen, wie man es zusammensetzt, ohne dass es Euch um die Ohren fliegt.', 'Wenn Ihr Lasche A in Schlitz B steckt, dann macht\'s natürlich BUMM! Lasst mich Euch mal zeigen, was ich habe. Danach könnt Ihr Lilliam fragen, wie man es zusammensetzt, ohne dass es Euch um die Ohren fliegt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3397', 'deDE', 'Hier wurde noch nie etwas gestohlen. Noch nie in der ganzen Geschichte von... der ganzen Geschichte!', 'Hier wurde noch nie etwas gestohlen. Noch nie in der ganzen Geschichte von... der ganzen Geschichte!', '31882');
