INSERT INTO `broadcast_text_locale` VALUES ('3402', 'deDE', 'Willkommen im Blauen Eremiten, wo die Magiebegabten Dampf ablassen. Was natürlich nicht wörtlich zu nehmen ist. Womit kann ich Euch dienen, $Gwerter Herr:holde Dame;?', 'Willkommen im Blauen Eremiten, wo die Magiebegabten Dampf ablassen. Was natürlich nicht wörtlich zu nehmen ist. Womit kann ich Euch dienen, $Gwerter Herr:holde Dame;?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3403', 'deDE', 'Hört auf meinen Rat und haltet Euch von den Schatten fern.', 'Hört auf meinen Rat und haltet Euch von den Schatten fern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3404', 'deDE', 'Willkommen im Pfeifenden Schwein! Holt Euch was zu trinken und setzt Euch. Je mehr, desto lustiger!', 'Willkommen im Pfeifenden Schwein! Holt Euch was zu trinken und setzt Euch. Je mehr, desto lustiger!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3405', 'deDE', 'Willkommen bei der Bank von Sturmwind. Wir bieten Bankkonten und Schließfächer für wertvolle Gegenstände. Habt Ihr bereits ein Konto bei uns, $Gwerter Herr:gnädige Frau;?', 'Willkommen bei der Bank von Sturmwind. Wir bieten Bankkonten und Schließfächer für wertvolle Gegenstände. Habt Ihr bereits ein Konto bei uns, $Gwerter Herr:gnädige Frau;?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3406', 'deDE', 'Wir sind die einzigen Lieferanten von gesunden Blumen in ganz Sturmwind. Wir haben wunderschöne Blumen für jeden Anlass.', 'Wir sind die einzigen Lieferanten von gesunden Blumen in ganz Sturmwind. Wir haben wunderschöne Blumen für jeden Anlass.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3407', 'deDE', 'Wohin des Weges, $GBursche:Mädel;? Wohin Ihr auch wollt, für ein paar Münzen bringen Euch meine Greife in Windeseile hin.', 'Wohin des Weges, $GBursche:Mädel;? Wohin Ihr auch wollt, für ein paar Münzen bringen Euch meine Greife in Windeseile hin.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3408', 'deDE', 'Diese großen Wildtiere kennen Wege, die man zu Fuß nie und nimmer finden würde. Sie bringen Euch blitzschnell überallhin und zeigen Euch dabei womöglich noch etwas Neues.', 'Diese großen Wildtiere kennen Wege, die man zu Fuß nie und nimmer finden würde. Sie bringen Euch blitzschnell überallhin und zeigen Euch dabei womöglich noch etwas Neues.', '31882');
