INSERT INTO `broadcast_text_locale` VALUES ('3416', 'deDE', 'Ich hoffe, Ihr habt nicht den ganzen Weg zur großen Schmiede auf Euch genommen, bloß weil\'s hier so schön warm ist. Sprecht, $GBursche:Mädel;, und sagt mir, was ich für Euch tun kann!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3417', 'deDE', 'Ihr seht mir wahrlich wie ein Schmied aus. Ruß unter den Nägeln, Stahl in den Knochen. Aye, in Euren Adern fließt sehr wohl das Blut eines Schmieds. Was bedeutet, Ihr wollt von mir ein oder zwei Dinge lernen. Nun, $GBursche:Mädel;, schnappt Euch einen Hammer. Und dann lasst uns mal sehen, was ich Euch noch so alles beibringen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3421', 'deDE', 'Ihr seht mir aus, als benötigtet Ihr Unterstützung. Wie kann ich Euch helfen?', 'Ihr seht mir aus, als benötigtet Ihr Unterstützung. Wie kann ich Euch helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3422', 'deDE', 'Wie darf ich Euch mit Weisheit erfüllen?', 'Wie darf ich Euch mit Weisheit erfüllen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3423', 'deDE', 'Die Geister leiten unsere Schritte auf dem Weg durchs Leben. Wie darf ich Eure leiten?', 'Die Geister leiten unsere Schritte auf dem Weg durchs Leben. Wie darf ich Eure leiten?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3424', 'deDE', 'Könnt Ihr mir mit einigen Anweisungen helfen?', 'Könnt Ihr mir mit einigen Anweisungen helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3425', 'deDE', 'Ihr sucht doch etwas. Was ist es?', 'Ihr sucht doch etwas. Was ist es?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3426', 'deDE', 'Die Bank', 'Die Bank', '31882');
