INSERT INTO `broadcast_text_locale` VALUES ('3441', 'deDE', '%s lässt die fehlerlose Draenethystsphäre frei vor sich schweben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3442', 'deDE', '%s singt in einer unbekannten Sprache.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3443', 'deDE', 'Der Stier, den Ihr sucht, heißt Krumn. Ihr findet ihn auf der unteren Anhöhe beim Informationszentrum der Stadtverwaltung Donnerfels.', 'Der Stier, den Ihr sucht, heißt Krumn. Ihr findet ihn auf der unteren Anhöhe beim Informationszentrum der Stadtverwaltung Donnerfels.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3444', 'deDE', 'Die Bank von Donnerfels findet Ihr auf der unteren Anhöhe.', 'Die Bank von Donnerfels findet Ihr auf der unteren Anhöhe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3445', 'deDE', 'Der Windreiterhorst befindet sich ganz oben auf der Totemtreppe in der Stadtmitte.', 'Der Windreiterhorst befindet sich ganz oben auf der Totemtreppe in der Stadtmitte.', '31882');
