INSERT INTO `broadcast_text_locale` VALUES ('3452', 'deDE', 'Sucht Turak Runetotem in der Halle der Ältesten auf der Anhöhe der Ältesten auf.', 'Sucht Turak Runetotem in der Halle der Ältesten auf der Anhöhe der Ältesten auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3453', 'deDE', 'Ah, dann müsst Ihr es in der Jägerhalle versuchen. Auf der Anhöhe der Jäger findet Ihr mehrere so große Jäger wie Holt Thunderhorn.', 'Ah, dann müsst Ihr es in der Jägerhalle versuchen. Auf der Anhöhe der Jäger findet Ihr mehrere so große Jäger wie Holt Thunderhorn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3454', 'deDE', 'Ein Anhänger des Arkanen seid Ihr? Hmpf! Nun gut - Ihr findet die Leute bei den Teichen der Visionen unterhalb der Anhöhe der Geister.', 'Ein Anhänger des Arkanen seid Ihr? Hmpf! Nun gut - Ihr findet die Leute bei den Teichen der Visionen unterhalb der Anhöhe der Geister.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3455', 'deDE', 'Vater Cobb ist Euer Mann. Ihr findet ihn unterhalb der Anhöhe der Geister in der Höhle bei den Teichen der Visionen.', 'Vater Cobb ist Euer Mann. Ihr findet ihn unterhalb der Anhöhe der Geister in der Höhle bei den Teichen der Visionen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3456', 'deDE', 'Die Halle der Geister ist das Zuhause unserer Schamanen. Ihr findet sie auf der Anhöhe der Geister. Mögen Eure Vorfahren über Euch wachen.', 'Die Halle der Geister ist das Zuhause unserer Schamanen. Ihr findet sie auf der Anhöhe der Geister. Mögen Eure Vorfahren über Euch wachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3457', 'deDE', 'Ihr wollt eines Tages zu den Unsrigen gehören? Dann haltet Euch an Sark Ragetotem. Er ist in der Jägerhalle.', 'Ihr wollt eines Tages zu den Unsrigen gehören? Dann haltet Euch an Sark Ragetotem. Er ist in der Jägerhalle.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3458', 'deDE', 'Welchen Ausbilder sucht Ihr?', 'Welchen Ausbilder sucht Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3459', 'deDE', 'Welcher Beruf?', 'Welcher Beruf?', '31882');
