INSERT INTO `broadcast_text_locale` VALUES ('3993', 'deDE', 'Einführen der gelben Lochkarte erfolgreich... Es erfolgt Ausgabe einer Einheit der Sicherheitsstufe Chi.\n\n01010100 01101000 01100101 00100000 01001100 01101001 01100011 01101000 00100000 01001011 01101001 01101110 01100111 00100000 01101100 01101001 01110110 01100101 01110011 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001 00100001', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3994', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-C, einem Sicherheitsterminal der Stufe Chi.$B$BFür den Zugriff auf die Terminalfunktionen wird eine blaue Lochkarte benötigt.\n\n01010010 01100101 01100011 01101001 01110000 01100101 00111010 00100000 01001101 01100101 01100011 01101000 01100001 01101110 01101001 01100011 01100001 01101100 00100000 01000011 01101000 01101001 01100011 01101011 01100101 01101110 00100000 01010011 01101111 01110101 01110000', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3996', 'deDE', 'Einführen der blauen Lochkarte erfolgreich... Es erfolgt Ausgabe einer Einheit der Sicherheitsstufe Delta.\n\n01010000 01101100 01100001 01111001 00100000 01001101 01101111 01110010 01100101 00100000 01010111 01101111 01010111', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3997', 'deDE', 'Hier geht nichts!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3998', 'deDE', 'Kein Wort...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('3999', 'deDE', '%s lacht hysterisch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4000', 'deDE', 'Ha! Geschieht dir recht, du dreckiger Trogg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4001', 'deDE', 'Ihr benötigt eine Zugangskarte für höhere Stufen.', 'Ihr benötigt eine Zugangskarte für höhere Stufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4002', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-D, einem Sicherheitsterminal der Stufe Delta.$B$BFür den Zugriff auf die Terminalfunktionen wird eine rote Lochkarte benötigt.\n\n01010011 01101000 01101111 01110010 01110100 00100000 01100001 01101110 01100100 00100000 01110011 01110111 01100101 01100101 01110100', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4003', 'deDE', 'Ihr benötigt eine Zugangskarte für hohe Stufen.', 'Ihr benötigt eine Zugangskarte für hohe Stufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4004', 'deDE', 'Einführen der roten Lochkarte erfolgreich... Es erfolgt Ausgabe einer Datenkarte der Sicherheitsstufe Delta.\n\n00111100 01101001 01101110 01110011 01100101 01110010 01110100 00100000 01100110 01110101 01101110 01101110 01111001 00100000 01101101 01100101 01110011 01110011 01100001 01100111 01100101 00100000 01101000 01100101 01110010 01100101 00111110', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4005', 'deDE', 'Eraka no Kimbul$BGott der Tiger, Lord der Bestien, König der Katzen, Verhängnis der Beutetiere$B$BUeetay no Mueh\'zala$BGott des Todes, Vater des Schlafes, Sohn der Zeit, Freund der Nacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4006', 'deDE', 'Weiterlesen...', 'Weiterlesen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4007', 'deDE', 'Elortha no Shadra$B$BGöttin der Spinnen, Mutter des Giftes, Seidentänzerin, Geliebte des Todes.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4008', 'deDE', 'Benutzt Ingenieurskunst, um auf versteckte Baupläne zuzugreifen!', 'Benutzt Ingenieurskunst, um auf versteckte Baupläne zuzugreifen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4009', 'deDE', 'Der Technologie-Wahrheitsdetektor des Matrix-Prägographen hat Euer Können in Sachen Ingenieurskunst bestätigt. Wählt nun unter einer der folgenden Optionen:\n\n01000111 01101111 00100000 01100111 01101111 00100000 01100111 01101111 00100001 00100001', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4010', 'deDE', 'Zugriff: Schwacher Rekombobulator', 'Zugriff: Schwacher Rekombobulator', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4011', 'deDE', 'Zugriff...\n\nUngültig - Ingenieur <#skill_level == n3wb>\n \n01000111 01100001 01101101 01100101 00100000 01001111 01110110 01100101 01110010\n\nZUGRIFF VERWEIGERT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4012', 'deDE', 'Zugriff auf Daten gewährt. Einen schönen Tag noch!\n\n01001110 01100001 01110100 01100001 01101100 01101001 01100101 00100000 01010000 01101111 01110010 01110100 01101101 01100001 01101110 00100000 01010010 01101111 01100011 01101011 01110011', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4013', 'deDE', null, 'Frisches Brot zu verkaufen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4014', 'deDE', null, 'Frisches Brot, heute Morgen erst gebacken!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4015', 'deDE', null, 'Frisches Brot! Hier gibt\'s ganz frisches Brot!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4018', 'deDE', 'Klaven zahlt mir nicht genug, als dass ich mich mit Zombies, Drohnen oder Untoten oder wie auch immer er die Dinger nennt, herumschlagen würde.', 'Klaven zahlt mir nicht genug, als dass ich mich mit Zombies, Drohnen oder Untoten oder wie auch immer er die Dinger nennt, herumschlagen würde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4019', 'deDE', null, 'Saftige Früchte zu verkaufen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4020', 'deDE', null, 'Beißt in einen frischen, saftigen Apfel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4021', 'deDE', null, 'Direkt aus den üppigen Wäldern von Elwynn, holt Euch Eure frischen Früchte hier.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4039', 'deDE', 'Feralas: Geschichte... Hmmmm... Da will ich mal einen Blick hineinwerfen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4040', 'deDE', '%s überfliegt die Seiten des Buchs.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4041', 'deDE', 'Erstaunlich! Ich glaube, ich habe es, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4042', 'deDE', null, 'Seht Euch um, $N. Vielleicht findet Ihr ja etwas, das ich ich übersehen habe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4043', 'deDE', null, '%s deutet auf einen Stapel Bücher.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4045', 'deDE', null, 'Gebt mir einen Moment, $N. Kindal ist unten im Grimmtotemlager und versucht die Feendrachen zu befreien. Wir hatten großes Glück, dass zufällig Hilfe kam. Wer weiß, was die Tauren ihnen sonst angetan hätten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4046', 'deDE', null, 'Oh weh! Die gefangenen Grimmlingflitzer haben es nicht geschafft. Was machen wir jetzt bloß?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4047', 'deDE', null, 'Jaaaaa! Sie haben es geschafft! Die gefangenen Grimmlingflitzer haben es geschafft!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4048', 'deDE', 'Primäre Interaktion abgeschlossen. Bildabbau in zehn Minuten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4049', 'deDE', 'Willkommen! Ich bin der Wächter des Eingangs. Bitte verwendet die richtigen Scheiben für den Zugang zu Uldum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4050', 'deDE', null, 'Mit Eurer Hilfe kann ich diese Tunnel berechnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4051', 'deDE', null, 'Mal sehen, ob ich herausfinden kann, woher diese Troggs kommen... und wie man die Invasion aufhalten kann!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4052', 'deDE', null, 'Welch eine Zerstörung! Was für ein fürchterliches Durcheinander!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4053', 'deDE', 'Willkommen. Ich bin der Wächter des Eingangs. Wenn sich in Eurem Scheibensatz nicht die richtigen Platten von Uldum befinden, werde ich Euch den Zugang zum Areal von Uldum nicht gewähren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4054', 'deDE', 'Welche Funktion habt Ihr?', 'Welche Funktion habt Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4055', 'deDE', 'Ich kontrolliere den Zugang zum Uldum-Komplex für die Schöpfer. Ich gewähre den Zugang zum Areal nur, wenn der Bittsteller die Scheiben in der richtigen Reihenfolge einlegt.$B$BEuer Satz Scheiben enthalt momentan nicht die Platten von Uldum, die Hauptvoraussetzung für einen Zugang sind. Zugang wird erst bei vorhandenen Platten von Uldum gewährt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4056', 'deDE', 'Was sind die Platten von Uldum?', 'Was sind die Platten von Uldum?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4057', 'deDE', 'Wo sind die Platten von Uldum?', 'Wo sind die Platten von Uldum?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4058', 'deDE', 'Wie bitte? Es ist ein \"erneuter Besuch\" geplant? Was soll denn das heißen?', 'Wie bitte? Es ist ein \"erneuter Besuch\" geplant? Was soll denn das heißen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4059', 'deDE', 'Was befindet sich denn in Uldum?', 'Was befindet sich denn in Uldum?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4060', 'deDE', 'Ich komme mit den Platten von Uldum wieder.', 'Ich komme mit den Platten von Uldum wieder.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4061', 'deDE', 'Die Platten von Uldum sind von den Schöpfern hergestellte Scheiben, die den Zugang zum Areal von Uldum ermöglichen. Auf diesen Scheiben sind spezielle Daten zu den Aktivitäten der Schöpfer in dieser Gegend gespeichert.$B$BVom Aussehen und vom Umfang her sind sie fast identisch mit dem Scheibensatz, den Ihr bereits besitzt. Die Zeichen darauf haben jedoch unmittelbar mit Informationen über Uldum zu tun und nicht mit dem Uldaman-Komplex.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4062', 'deDE', 'Die Schöpfer haben mehrere Sätze der Platten von Uldum zur ihrem speziellen Zweck geschaffen. Die Schöpfer kontrollieren die Verteilung der besagten Scheiben für alle Bereiche der Welt. So ist denn auch ein erneuter Besuch Azeroths geplant. In Zukunft könnten daher neue Scheiben in Umlauf kommen.$B$BDie vorhandenen Scheiben wurden nur an jene Gruppen ausgegeben, die gewisse Sicherheitsanforderungen erfüllten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4063', 'deDE', 'Die Schöpfer nutzen Besuche, wenn sie die Kontrolle über von ihnen erschaffene Welten zurückerlangen wollen. Dies geschieht, wenn externe oder interne Kräfte die Matrixdynamik der jeweiligen Welt durcheinander bringen. Man sollte diese Maßnahmen der Schöpfer nicht auf die leichte Schulter nehmen.$B$BWeitere Informationen zum geplanten Besuch dieser Welt entnehmt Ihr bitte dem entsprechenden Datenspeicher, der alle Pläne der Schöpfer hinsichtlich eines Besuchs enthält.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4064', 'deDE', 'Uldum ist ein Forschungsgebiet der Schöpfer. Dort versuchen sie, die Biosphäre von Azeroth zu verbessern. Genauere Angaben zum Wirken der Schöpfer in Uldum sind Personen vorbehalten, die in ihrem Satz Scheiben die Platten von Uldum haben.$B$BInnerhalb des Uldum-Komplexes gibt es Datenspeicher, die Informationen zu bestimmten Experimenten sowie Datenzusammenstellungen enthalten. Weitere Informationen enthaltet Ihr dort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4065', 'deDE', 'Ihr erhaltet Eure Scheiben zurück, sobald Ihr den Sockel betreten habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4079', 'deDE', null, 'Ich bin bereit, wenn Ihr es seid, $N. Geht voran!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4080', 'deDE', null, 'Wir haben es geschafft! Wir treffen uns bei Jer\'kai über dem Grim-Totem-Lager.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4081', 'deDE', null, 'Nein! Die Feendrachen schaffen es nicht, $N! Ich muss zurück zu Jer\'kai und nachsehen, ob sie gefunden wurde. Elune sei mit Euch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4083', 'deDE', null, 'Dem Licht sei Dank, dass Ihr hier seid! Im Augenblick würde ich auch Hilfe von einem zweiköpfigen Oger annehmen.\n\nDie Stadt ist von Troggs überrannt worden! Versuche, sie zu vernichten, endeten mit einer Massenverseuchung. Aber noch gibt es Hoffnung.\n\nUnsere nach Eisenschmiede evakuierten Wissenschaftler arbeiten an einem Heilmittel für die durch die Ablagerung ausgelöste Erkrankung. Wenn Ihr mir helft, die Invasion der Troggs aufzuhalten, können wir in der Zwischenzeit vielleicht die Stadt zurückerobern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4084', 'deDE', 'Ich bin bereit anzufangen.', 'Ich bin bereit anzufangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4102', 'deDE', 'Gebt mir ein paar Minuten, $N. Eben gab es hier einige Aufregung und ich will rasch schauen, ob alles wieder ruhig ist. Ich setze mich bald mit Euch in Verbindung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4122', 'deDE', null, 'Rasch! $N greift an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4123', 'deDE', null, 'Sterbt, $N. Elune wird nicht mehr zulassen, dass Ihr ihren Kreaturen etwas antut!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4124', 'deDE', null, 'Ihr sollt sterben, $N. Wie könnt Ihr es wagen, Euch mir auch nur auf Bogenreichweite zu nähern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4125', 'deDE', null, 'Ihr wagt es, mich zu berühren?!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4126', 'deDE', 'Macht während des Rituals bloß kein Geräusch! Ein einziger Fehler könnte unser Verhängnis bedeuten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4127', 'deDE', 'Seht jetzt her...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4128', 'deDE', 'Da. Es ist vollbracht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4129', 'deDE', null, 'Es ist still hier...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4130', 'deDE', null, '... zu still.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4131', 'deDE', null, 'Seht! Dort drüben an der Tunnelwand!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4132', 'deDE', null, 'Dort sind Troggs durchgebrochen! Verteidigt mich, während ich das Loch zum Einsturz bringe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4133', 'deDE', null, 'Ich glaube nicht, dass eine Sprengladung reicht. Drängt sie weiter zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4134', 'deDE', null, 'Die Sprengladungen sind scharf! Kommt zurück, ehe sie hochgehen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4135', 'deDE', null, 'Sprengung in 10 Sekunden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4136', 'deDE', null, 'Sprengung in 5 Sekunden. Raus aus dem Tunnel! Bleibt zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4137', 'deDE', null, 'ACHTUNG!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4138', 'deDE', null, '%s zwitschert $N ein Lebewohl zu.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4139', 'deDE', 'Willkommen bei \'Blaumond Dies-und-Das\', $C. Lasst es mich wissen, wenn Ihr Fragen habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4142', 'deDE', 'Warnung! Warnung! Eindringling! Alarm! Eindringling! Alarm!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4162', 'deDE', 'Sire?', 'Sire?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4163', 'deDE', 'Seid wachsam - die Eindringlinge sind nah.', 'Seid wachsam - die Eindringlinge sind nah.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4165', 'deDE', 'Jetzt reicht\'s! Ich habe es satt, Euch zu helfen! Es wird Zeit, dass wir die Dinge auf dem Schlachtfeld regeln.', 'Jetzt reicht\'s! Ich habe es satt, Euch zu helfen! Es wird Zeit, dass wir die Dinge auf dem Schlachtfeld regeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4166', 'deDE', 'Das Essen ist da, geliebte Kinder! Reißt sie in Stücke!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4167', 'deDE', 'Zugriff: Diskombobulatorstrahl', 'Zugriff: Diskombobulatorstrahl', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4169', 'deDE', 'Ich bin froh, dass sich die Unruhe hier gelegt hat. Das Letzte, was dieser Ort braucht, ist noch mehr Zwist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4170', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-A, einem Sicherheitsterminal der Stufe Alpha.$B$BWeiße Lochkarte erfasst. Zugriff gewährt.\n\n01010100 01100101 01101100 01101100 00100000 01111001 01101111 01110101 01110010 00100000 01100110 01110010 01101001 01100101 01101110 01100100 01110011 00100000 01110100 01101111 00100000 01110000 01101100 01100001 01111001 00100000 01010111 01101111 01010111', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4171', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-B, einem Sicherheitsterminal der Stufe Beta.$B$BGelbe Lochkarte erfasst. Zugriff gewährt.\n\n01001011 01101001 01110011 01110011 00100000 01101101 01100101 00101100 00100000 01001001 00100111 01101101 00100000 01100111 01101110 01101111 01101101 01101001 01110011 01101000 00100001', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4172', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-C, einem Sicherheitsterminal der Stufe Chi.$B$BBlaue Lochkarte erfasst. Zugriff gewährt.\n\n01000111 01101110 01101111 01101101 01100101 01110010 01100101 01100111 01100001 01101110 00100000 01100110 01101111 01110010 01100101 01110110 01100101 01110010 00100001', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4173', 'deDE', 'Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-D, einem Sicherheitsterminal der Stufe Delta.$B$BRote Lochkarte erfasst. Zugriff gewährt.\n\n01001000 01101111 01110111 00100000 01100001 01101101 00100000 01001001 00100000 01100100 01110010 01101001 01110110 01101001 01101110 01100111 00111111', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4175', 'deDE', '%s sammelt das Material zusammen und beginnt zu arbeiten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4176', 'deDE', 'Ich bin mit den Waffen fertig, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4177', 'deDE', 'Erhebt Euch und verteidigt Euren Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4178', 'deDE', 'Die Kinder von Sul werden ihren Meister beschützen. Auf ein Neues für Ruhm und Glorie, Sul\'lithuz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4179', 'deDE', 'Den Geistern, oder dem \"Muisek\", der Kreaturen, gegen die wir kämpfen, wohnt unglaubliche Macht inne.$B$BSeht in Euch selbst hinein und bündelt die Macht Eures eigenen Geistes, dann werdet Ihr mächtiger sein, als Ihr es Euch je erträumt habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4180', 'deDE', 'Die Muisekgefäße sind nun bereit, $N', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4181', 'deDE', '%s beginnt das Ritual.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4182', 'deDE', 'Sul\'thraze... als ich diesen Namen zum ersten Mal vernahm, war ich wohl in Eurem Alter. Es bedeutete mir damals nichts, so wie es Euch heute nichts bedeutet. Erst als eine Troll-Kriegsmeute aus Zul\'Farrak das Land verwüstete, verstand ich... und bekam Angst.$B$BViele werden Euch erzählen, dass die Spaltklinge des Leidens nicht existiert. Dass sie ein Mythos sei, ein Märchen, mit dem man unartige Kinder ängstigt. Aber glaubt mir, $R, es gibt sie.$B$B<Trenton fährt sich geistesabwesend mit der Hand über die gezackte Narbe in seinem Gesicht.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4183', 'deDE', 'Erzählt mir mehr, Trenton.', 'Erzählt mir mehr, Trenton.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4184', 'deDE', 'In zwei Teile gespalten, um eine Einheit zu bilden. Die Zwillingsklingen sind an Zul\'Farrak gebunden.$B$BSul\'thraze wird wiedergeboren, wenn die Klingen gefunden sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4185', 'deDE', 'Schädel werdet Ihr mir geben und Schädel werde ich nehmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4186', 'deDE', 'Seid geheilt, meine Tierchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4206', 'deDE', null, 'Gut gemacht! Ohne Eure Hilfe hätte ich diese Welle von Troggs nie zurückwerfen können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4207', 'deDE', null, 'Habt Ihr etwas gehört?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4208', 'deDE', null, 'Ich habe dort drüben etwas gehört.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4209', 'deDE', null, 'Noch mehr Troggs! Wehrt sie ab, während ich den Sprengstoff vorbereite!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4210', 'deDE', 'Niemals werdet Ihr die Zwillingsklingen von Sul finden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4211', 'deDE', 'Eine Flucht ist unmöglich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4212', 'deDE', 'Beugt Euch dem Blick, sterbliche Wesen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4213', 'deDE', 'Dafür werdet Ihr büßen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4214', 'deDE', 'Die Diener von Sul werden sich an Euren Überresten laben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4215', 'deDE', 'Der Tod ist nur der Übergang zur Wiedergeburt. Die Brut wird wieder auferstehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4216', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4225', 'deDE', 'Die Wachen von Theramore sind die besten Soldaten der Allianz in Kalimdor. Auch fern unserer Heimat ist es unsere Pflicht, die Gesetze der Allianz zu wahren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4226', 'deDE', 'Wir wissen nicht viel über den Brand im Gasthaus Zur süßen Ruh, nur dass James Hyals Frau und Kind den Flammen nicht entkommen konnten.$B$BJames konnten wir retten, aber seither ist er ein anderer Mensch.$B$BWir haben viele Hinweise, aber keine heiße Spur. Unsere Hoffnung ist, dass wir durch die Wächter und die Mithilfe der Bevölkerung von Theramore dieses schreckliche Verbrechen aufklären können.$B$BFalls Ihr bei den Ermittlungen helfen wollt, findet Ihr die Überreste des Gasthauses an der Grenze zum Brachland.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4227', 'deDE', 'Was wisst Ihr über das Gasthaus Zur süßen Ruh?', 'Was wisst Ihr über das Gasthaus Zur süßen Ruh?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4228', 'deDE', 'Ihr habt Kirith von seinen Leiden erlöst? Unvorstellbar - und nun hat Euch Trebor hierher geschickt, um alles über Dämonen und ihre Diener zu lernen?$B$B<Loramus lacht.>$B$BSeid froh, dass diese arkanen Schmerzen endlich vorüber sind, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4229', 'deDE', 'Könnt Ihr sie sehen, sterbliches Wesen? Nein... Hätte mich auch gewundert. Sehend, ohne sehen zu können - das ist Eure Gabe und Euer Fluch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4230', 'deDE', 'Seid gegrüßt, Loramus. Ich bringe Kunde aus den verwüsteten Landen.', 'Seid gegrüßt, Loramus. Ich bringe Kunde aus den verwüsteten Landen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4245', 'deDE', 'Werdet Ihr helfen?', 'Werdet Ihr helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4246', 'deDE', 'Im Krieg war Razelikh der Entweiher einer der gefürchtetsten Offiziere von Lord Kazzak. Als die Legion besiegt und Kazzak zum Rückzug in die faulende Narbe gezwungen worden war, bekam Razelikh von seinem Meister den Auftrag, die verwüsteten Lande zu sichern.$B$BWährend die Allianz in unbedeutende Scharmützel mit der Horde über Gebiete außerhalb der verwüsteten Lande verstrickt war, vereinigte Razelikh die verschiedenen Sekten des Schattenrats sowie die grobschlächtigen Schreckensfelsoger des Landes.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4247', 'deDE', 'Sprecht bitte weiter.', 'Sprecht bitte weiter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4248', 'deDE', 'Tragischerweise war es Thralls Weisheit, die letztlich den Bund zwischen dem Dämonenlord und seinen Dienern besiegelte. Trebors Regiment fungierte dabei als Katalysator für die Einheit zwischen Dämonenlord, Schattenanbeter-Sekten und Ogern. Die Aussicht auf nahezu unbegrenzte Macht und die Gewährung von unsterblichen Schocktruppen für jeden Anführer besiegelten den Handel mit Blut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4249', 'deDE', 'Das verstehe ich nicht.', 'Das verstehe ich nicht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4250', 'deDE', 'Was Sevine, Allistarj und Grol nicht erkannten, war, dass sie ebenso an das Land gebunden sein würden wie ihre Diener... Denn, seht Ihr, der Preis für ihre Unsterblichkeit war Gefangenschaft und ewige Dienerschaft unter dem Befehl von Razelikh. Seine grausame Schlauheit hatte Razelikh einmal mehr als Sieger hervorgehen lassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4251', 'deDE', 'So ist es.', 'So ist es.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4252', 'deDE', 'Ihre Gedanken sind nun besudelt, $R. Ja, sogar noch mehr als zuvor. Sie leben nur, um Razelikh zu dienen und ihn zu beschützen. Die Fragmente des Amuletts, die sie bei sich tragen, sind der Schlüssel zur Beschwörung des Dämonenlords. Was Ihr indes nicht zu verstehen scheint, ist, dass man sie nicht auf konventionelle Weise verletzen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4253', 'deDE', 'Ihre Hirne sind nun besudelt, $R. Ja, sogar noch mehr als zuvor. Sie leben nur, um Razelikh zu dienen und ihn zu beschützen. Die Fragmente des Amuletts, die sie bei sich tragen, sind der Schlüssel zur Beschwörung des Dämonenlords. Was Ihr indes nicht zu verstehen scheint, ist, dass man sie nicht auf konventionelle Weise verletzen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4254', 'deDE', 'Ich werde das durchziehen, mit Eurer Hilfe oder ohne, Loramus.', 'Ich werde das durchziehen, mit Eurer Hilfe oder ohne, Loramus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4255', 'deDE', 'Selbst falls Ihr es schafft, den Bann zu erzeugen, mit dem Ihr den dämonischen Zauberschutz durchbrechen könnt, unter dem die drei stehen... Was würdet Ihr dann tun? Dem Entweiher in seinem eigenen Unterschlupf gegenübertreten?$B$B<Loramus lacht.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4256', 'deDE', 'Ja.', 'Ja.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4257', 'deDE', 'Ich möchte Eure Geschichte hören.', 'Ich möchte Eure Geschichte hören.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4258', 'deDE', 'Die Menschen hatten einen großen Teil der Marschen von Dustwallow an sich gerissen, aber der Stonemaulklan konnte sie mit unserer Hilfe zurückdrängen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4259', 'deDE', 'Das brennende Gasthaus', 'Das brennende Gasthaus', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4260', 'deDE', 'Die Menschen bauten ein Gasthaus an der Grenze des Brachlandes. Gasthaus Zur süßen Ruh, so nannten sie es. Vor Wochen wurde es niedergebrannt.$B$BDer Kriegshäuptling sagte uns, wir sollten die Menschen in Ruhe lassen, solange sie unsere Grenzen nicht überqueren, aber offenbar handelte jemand seinem Wunsch zuwider.$B$BEr hat mir den Auftrag gegeben, der Sache auf den Grund zu gehen. Wenn Ihr mir helfen wollt, geht zu den Überresten des Gasthauses Zur süßen Ruh und bringt alle Hinweise, die Ihr finden könnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4261', 'deDE', 'Reethe finden.', 'Reethe finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4262', 'deDE', 'Wenn Reethe etwas mit dem Brand im Gasthaus Zur süßen Ruh zu tun hat, müsst Ihr ihn finden und Antworten von ihm verlangen.$B$BIch schicke Ogron los, um ihn aufzuspüren. Er wollte Richtung Südosten. Trefft Euch mit Ogron und geht dann Reethe suchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4265', 'deDE', null, 'Ich will das gar nicht hören, $N. Ihr werdet unter gar keinen Umständen in die sengende Schlucht gelassen. Unmöglich. Das wird nicht passieren. Wie oft soll ich Euch noch sagen, dass ich Euch nicht einlassen kann? Seid Ihr taub? Seid Ihr ein verkleideter Orc? N-E-I-N. NEIN!$B$BWenn ich Euch reinlasse, muss ich alle reinlassen: erst Eure besten Freunde, dann entfernte Verwandte, dann deren Freunde und so weiter. Als das zuletzt geschah... na, darüber sollten wir gnädig schweigen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4266', 'deDE', 'Was könnte denn schlimmstenfalls passieren?', 'Was könnte denn schlimmstenfalls passieren?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4267', 'deDE', null, 'Seid Ihr toll? Wisst Ihr, was für Bestien diesen Ort bevölkern? Schlimmstenfalls könnte passieren, dass ich Eure Überreste mit einer Schaufel aufsammeln muss. Wir haben schon manch guten Zwerg an diese Ungeheuer verloren.$B$BDie Antwort lautet nein. Wenn Ihr unbedingt in die sengende Schlucht wollt, müsst Ihr es anders anstellen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4268', 'deDE', 'Anders anstellen? Wie denn?', 'Anders anstellen? Wie denn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4269', 'deDE', null, 'Ich sollte das wahrscheinlich tunlichst für mich behalten, aber wenn Ihr es schafft, an all den Tieren und Bestien des Ödlandes vorbeizukommen, dann erreicht Ihr vielleicht auch die sengende Schlucht.$B$BJa, das Ödland. Es gibt eine Art Schleichweg, den die Orcs bei Kargath angelegt haben. Das soll Euch nur zeigen, mit was für Dummköpfen wir es hier zu tun haben! Eine Hintertür zur Hölle? Was haben die sich bloß dabei gedacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4270', 'deDE', 'Orcs? Ödland? Ich bin unverwundbar!', 'Orcs? Ödland? Ich bin unverwundbar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4271', 'deDE', null, 'Unverwundbar?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4272', 'deDE', 'Genau!', 'Genau!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4273', 'deDE', null, 'Ihr seid ein Dummkopf und solltet auf mich hören. Niemand ist unverwundbar. Ihr tätet gut daran, das zu beherzigen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4274', 'deDE', 'Entschuldigt bitte, Pebblebitty.', 'Entschuldigt bitte, Pebblebitty.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4275', 'deDE', null, 'Ich sag Euch was: Da Ihr Euch selbst als Helden zu betrachten scheint, bahnt Euch einen Weg durch das Ödland zur sengenden Schlucht und bringt mir Beweise Eurer Heldentaten. Dann sehe ich mal, ob ich Euch einen Schlüssel besorgen kann.$B$BGanz im Südosten der sengenden Schlucht lebt eine Frau, die fast so hart ist wie ich. Man nennt sie Margol die Wütende. Vielleicht solltet Ihr dort mal nachforschen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4276', 'deDE', 'Fertig, fertig und noch mal fertig.', 'Fertig, fertig und noch mal fertig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4285', 'deDE', '%s staubt den Sockel des Artefakts ab.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4286', 'deDE', '%s untersucht die riesige Vase minutiös.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4287', 'deDE', 'Armer Dorius. Wenn ich diese Dunkeleisenzwerge in die Finger bekomme, hat ihr letztes Stündlein geschlagen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4305', 'deDE', 'Ich habe genau den richtigen Platz für dieses Horn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4306', 'deDE', 'Diesen Platz habe ich für etwas Besonderes freigehalten. Und das da ist etwas ganz Besonderes, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4307', 'deDE', 'So, fertig. Das war\'s!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4308', 'deDE', 'Was kann ich in einem Gasthaus tun?', 'Was kann ich in einem Gasthaus tun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4309', 'deDE', 'Was Ihr in einem Gasthaus tun könnt?$B$BNun, in einem Gasthaus genießt Ihr einen sehr komfortablen Aufenthalt. Deshalb fühlt Ihr Euch auch schneller wieder \"gut erholt\" als draußen in der Wildnis. Und wenn Ihr gut erholt seid, lernt Ihr mehr aus Euren Erfahrungen.$B$BAußerdem könnt Ihr beim Gastwirt einen Ruhestein erwerben, mit dem Ihr dann später schneller wieder zum Gasthaus zurückgelangt.', 'Was Ihr in einem Gasthaus tun könnt?$B$BNun, in einem Gasthaus genießt Ihr einen sehr komfortablen Aufenthalt. Deshalb fühlt Ihr Euch auch schneller wieder \"gut erholt\" als draußen in der Wildnis. Und wenn Ihr gut erholt seid, lernt Ihr mehr aus Euren Erfahrungen.$B$BAußerdem könnt Ihr beim Gastwirt einen Ruhestein erwerben, mit dem Ihr dann später schneller wieder zum Gasthaus zurückgelangt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4325', 'deDE', null, 'Die letzte Ladung ist angebracht! Tretet zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4326', 'deDE', null, '10 Sekunden bis zur Explosion! Tretet zurück!!!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4327', 'deDE', null, '5 Sekunden bis zur Detonation!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4328', 'deDE', 'Wir kommen aus der Tiefe! Ihr könnt uns niemals aufhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4329', 'deDE', null, 'Saubere Arbeit! Ich löse die Sprengladungen aus, damit nicht noch mehr Troggs an die Oberfläche gelangen können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4345', 'deDE', 'Halt... muss Atem... holen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4346', 'deDE', 'Die Geheimnisse von Suntara werden dieses Land nicht verlassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4347', 'deDE', 'Ragnaros sieht alles, sterbliches Geschöpf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4348', 'deDE', 'Wohin führt mich jetzt mein Weg, Jorn?', 'Wohin führt mich jetzt mein Weg, Jorn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4349', 'deDE', 'Ihr macht Euch gut, $N. Aber Ihr habt immer noch so viel zu lernen. Eure nächste Lektion wartet im südlichen Brachland auf Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4350', 'deDE', 'Wir nennen dieses Land zwar Brachland, doch wer näher hinsieht, der bemerkt, dass der Ort voller Leben steckt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4351', 'deDE', 'Das wollte ich gerade feiern, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4352', 'deDE', 'Wollt Ihr einen verletzten Zwerg die ganze Arbeit machen lassen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4353', 'deDE', 'Los geht\'s. $N, das sind meine Fäuste. Fäuste, sagt hallo zu $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4354', 'deDE', 'Das ist der Ort, $R: Der Kessel! Markiert ihn auf der Karte oder sorgt irgendwie dafür, dass Ihr ihn nicht vergesst. Falls mir etwas zustößt, wären meine Leute auf ewig in dieser Mine gefangen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4355', 'deDE', 'Vorwärts! Und seht Euch diesmal vor! Ich will nicht in noch mehr Kämpfe geraten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4356', 'deDE', 'Ich muss mit dem Rauchen aufhören. Gebt mir eine Minute zum Atemholen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4357', 'deDE', 'Wir sind fast in Sicherheit. Das letzte Stück werden wir rennen. Seht zu, dass Ihr nicht zu weit zurückbleibt. LOS!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4358', 'deDE', 'Der gehört mir...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4359', 'deDE', 'ARRRRRGH! Er hat mich erwischt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4360', 'deDE', '%s schnüffelt die Luft...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4361', 'deDE', '%s frisst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4362', 'deDE', '%s verschwindet in einer Staubwolke.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4363', 'deDE', 'Das Tor ist in Sichtweite! Wir schaffen es! Aber zuerst muss ich verschnaufen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4364', 'deDE', 'Ihr wisst ja nicht, was Ihr tut! Wir müssen Euch zu Eurem eigenen Besten vernichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4365', 'deDE', 'Hört her, Bewohner von Tanaris! Ihr sollt wissen, dass $N ein erhabenes Mitglied des Mithrilordens ist. Ein Schmied von Ehre, Hingabe und unendlicher Geduld. Ein dreifaches Hoch auf $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4366', 'deDE', 'Im Loch! Im Loch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4367', 'deDE', 'Er schießt und er trifft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4368', 'deDE', 'Aus der Innenstadt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4370', 'deDE', 'Es läuft... und es läuft gut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4371', 'deDE', 'Er steckt den Felsen in das Loch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4372', 'deDE', 'Phantastisch! Heute brennt hier die Luft, Jungs.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4373', 'deDE', 'TOOOOOOOOOOOOOOOOOOOOR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4374', 'deDE', 'In die Röhre, 5 mal 5!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4375', 'deDE', 'Aye, \'s ist ganz schön kalt da draußen, aber nicht zu kalt, um meine Waren anzubieten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4376', 'deDE', 'Ich verkaufe die beste Stoff- und Lederkleidung diesseits des Tales!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4377', 'deDE', 'Es geht doch nichts über kochend heißes Morgengebräu an einem kalten Morgen in Dun Morogh! Danach kann der Tag kommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4378', 'deDE', 'Ooooh! Heiß! Heiß! Heiß! Wenn Euch das nicht auf Touren bringt, kann ich Euch auch nicht helfen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4379', 'deDE', 'Ich war lange genug hier, $N. Wir sehen uns bestimmt mal wieder. Viel Glück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4380', 'deDE', null, 'Ungläubige!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4381', 'deDE', null, 'Ihr wagt es, unser Gebet zu unterbrechen? Tötet sie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4382', 'deDE', null, 'Ihr werdet ein gutes Opfer für Ragnaros abgeben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4383', 'deDE', null, 'Hervorragend! Frisches Blut!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4384', 'deDE', 'Erzählt mir von dem leuchtenden Splitter.', 'Erzählt mir von dem leuchtenden Splitter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4385', 'deDE', null, 'Ihr seid weiter gekommen als viele andere, $N. Wenn Ihr die Elemente um Euch herum respektiert, dann wird es Euch weit besser ergehen als den meisten anderen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4390', 'deDE', 'Ihr seid zurückgekehrt, um Eure Aufgabe zu beenden? <Lathoric lacht.> Erhebe dich, Obsidion, und vernichte sie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4391', 'deDE', 'Eure Aufgabe ist vollbracht. Jetzt wird sich Euer Schicksal erfüllen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4392', 'deDE', 'Erhebe dich, Obsidion, und diene deinem Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4393', 'deDE', 'Bravo! Bravo! Ausgezeichnet! Was denn? Dachtet Ihr etwa, ich sei tot?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4394', 'deDE', 'Ihr Narren! Ich wusste, wenn ich auf die kümmerlichen Gefühle meines Bruders setze, würde er \'Retter\' schicken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4395', 'deDE', 'Es war ein Kinderspiel, Euch dazu zu bringen, diesen Schwachköpfen vom Twilight-Hammer den letzten Suntara-Stein abzuluchsen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4396', 'deDE', 'Als ich in der Grimesilt-Grabungsstätte auf die Suntara-Steine stieß, durchströmte mich die Macht von Ragnaros. Ragnaros gab meinem Leben einen Sinn.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4397', 'deDE', 'Die Schaffung Obsidions war Ragnaros\' Wille. Obsidion wird die Blackrockorcs von der Blackrockspitze vernichten und uns mit unseren Brüdern in den feurigen Tiefen vereinigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4398', 'deDE', 'Und schließlich war es auch Ragnaros, der mir meinen Namen gab, als ich als Akolyt des Feuers wiedergeboren wurde: Lathoric... Lathoric der Schwarze.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4399', 'deDE', 'Das Lachen von %s verklingt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4400', 'deDE', 'Der Altar scheint Euch anzuziehen, so, als sei er lebendig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4401', 'deDE', 'Berührt den Suntara-Stein und ruft Lathoric den Schwarzen sowie seinen Wächter Obsidion herbei.', 'Berührt den Suntara-Stein und ruft Lathoric den Schwarzen sowie seinen Wächter Obsidion herbei.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4402', 'deDE', null, 'Ich weiß von einer Druidin, die in den Hügeln über den Höhlen des Wehklagens lebt, $N. Sie kann Euch vielleicht mehr über diesen Splitter sagen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4403', 'deDE', 'Wache! Helft mir!', 'Wache! Helft mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4404', 'deDE', null, 'Seid gegrüßt, $N. Möge Elunes Weisheit Eure Hand und Euer Herz leiten. Womit kann ich Euch heute dienen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4405', 'deDE', 'Willkommen, Astarii. Ich benötige Ausbildung.', 'Willkommen, Astarii. Ich benötige Ausbildung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4406', 'deDE', 'Hab auf Euch gewartet, $N. Freut mich, dass Ihr es geschafft habt. Habt Ihr die Lieferung?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4407', 'deDE', 'Lobet Elune! Es ist schon viele Jahre her, dass ich Besucher hatte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4408', 'deDE', 'Ich möchte Eure Geschichte hören.', 'Ich möchte Eure Geschichte hören.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4409', 'deDE', 'Als junger Nachtelf in Dunkelküste kreuzten sich meine Wege einst mit einem widerlichen Pack, das sich Twilight-Hammer nannte. Mit einigen von ihnen freundete ich mich an und zu meiner Schande muss ich gestehen, dass ich meinen Glauben an Elune leugnete und die Parolen der Twilight-Hammer begeistert annahm.$B$BErst eine Vision von Elune ließ mich aus diesem bösen Traum erwachen, in dem ich als Anhänger der alten Götter gefangen war.$B$BVoller Reue begann ich mit der Arbeit an dem Gebet für Elune.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4410', 'deDE', 'Bitte fahrt fort, Zamael.', 'Bitte fahrt fort, Zamael.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4411', 'deDE', 'Ich ätzte das Zeugnis meiner Reue, meiner Scham, meines Bedauerns und meiner Trauer in eine Schrifttafel aus reinstem Gold. Leider fand der Klan der Twilight-Hammer die Tafel und brandmarkte mich als Ketzer. Ich wurde hingerichtet, ehe ich mein Gebet an Elune fertig stellen konnte. Nun bin ich als Geist an diese Welt gefesselt und kann nicht hinüber ins Nether; gefangen in einem Käfig als Warnung an all jene, die den Glauben der Twilight-Hammer verraten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4412', 'deDE', 'Lasst mich darüber nachdenken, Zamael.', 'Lasst mich darüber nachdenken, Zamael.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4413', 'deDE', 'Schleifer jetzt müssen heimgehen. Tschüss, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4414', 'deDE', null, 'Benutzt diesen Stab des Befehls um Schleifer Befehle zu erteilen, wenn Ihr Euch in der Nähe der Schattenseidewilderer befindet, und sie werden den Tag bereuen, da sie dieses Land betreten haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4415', 'deDE', 'Tschüss, du klein $R. Schleifer jetzt gehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4416', 'deDE', 'Der alte Altar von Hakkar steht stumm am Eingang des Raums.$B$BAls Ihr zu den Altaren darüber hinaufschaut, bemerkt Ihr große, schlangenähnliche Statuen, die darauf ruhen. Die Statuen sehen genau wie die auf dem Altar vor Euch aus. Es muss einen Zusammenhang zwischen ihnen geben...', 'Der alte Altar von Hakkar steht stumm am Eingang des Raums.$B$BAls Ihr zu den Altaren darüber hinaufschaut, bemerkt Ihr große, schlangenähnliche Statuen, die darauf ruhen. Die Statuen sehen genau wie die auf dem Altar vor Euch aus. Es muss einen Zusammenhang zwischen ihnen geben...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4417', 'deDE', 'In welchem Zusammenhang könnten der Altar und die Statuen stehen?', 'In welchem Zusammenhang könnten der Altar und die Statuen stehen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4418', 'deDE', 'Ich will mit dem Statuenrätsel beginnen.', 'Ich will mit dem Statuenrätsel beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4419', 'deDE', 'Erzählt mir mehr über das Muisek.', 'Erzählt mir mehr über das Muisek.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4420', 'deDE', 'In dieser Gegend leben viele Kreaturen, die als Verbündete der Nachtelfen gelten. Die Elfen hatten immer schon Zugang zu der Macht in diesen Kreaturen. Es ist an der Zeit, dass auch der Horde diese Genugtuung zuteil wird. Ich habe eine Möglichkeit gefunden, wie wir diese Macht fassen und nutzen können.$B$BZweifelt nicht an meinen Worten, $N. Helft mir und Ihr werdet sehen, dass ich die Wahrheit sage.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4421', 'deDE', 'Euer Leichnam wird ein neues Handwerk begründen, $N. Wir werden es \'Fleischverarbeitung\' nennen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4422', 'deDE', 'Die machen nichts weiter als ein bisschen Jagen und Sammeln. Was ist schon dabei?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4423', 'deDE', 'Wenn Schneidern Euer Metier ist, dann hat Vizzklick wahrscheinlich, was Ihr braucht. Seht Euch nur um, $N, und sagt mir, wenn Ihr etwas zu kaufen wünscht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4424', 'deDE', 'Ich möchte Eure Waren sehen.', 'Ich möchte Eure Waren sehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4425', 'deDE', 'Packt Eure Sachen, $N! Ich habe soeben von Nilith Lokrav in der sengenden Schlucht gehört, dass der Untermarkt mitten im Kessel aktiv werden will. Sucht Nilith Lokrav in der sengenden Schlucht auf und besorgt Euch die nötigen Informationen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4426', 'deDE', 'Ein helles Licht erscheint auf der anderen Seite des Raums und verschwindet wieder. Dann seht Ihr noch ein Licht aufleuchten... und noch eines.$B$BSie scheinen Euch ein Muster zu zeigen.', 'Ein helles Licht erscheint auf der anderen Seite des Raums und verschwindet wieder. Dann seht Ihr noch ein Licht aufleuchten... und noch eines.$B$BSie scheinen Euch ein Muster zu zeigen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4427', 'deDE', 'Die Statuen stehen an ihrem Platz. Ihr könnt mit dem Rätsel beginnen.', 'Die Statuen stehen an ihrem Platz. Ihr könnt mit dem Rätsel beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4428', 'deDE', '%s wird zum Berserker!', '%s wird zum Berserker!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4429', 'deDE', 'Die Rache wird mein sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4430', 'deDE', 'Sagt, warum wollt Ihr Rache?', 'Sagt, warum wollt Ihr Rache?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4431', 'deDE', 'Mein Vater wurde von dem Abschaum im Kessel niedergemetzelt. Mein Flehen um Beistand stieß beim Magistrat von Sturmwind auf taube Ohren. Nein, sie wollen nicht einmal zugeben, dass es diesen Teil der Welt überhaupt gibt, geschweige denn ihr tolles Militär hinschicken, um dort für Recht und Ordnung zu sorgen!$B$BIch will Rache, $R, ganz einfach. Gibt es einen besseren Beweggrund?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4432', 'deDE', 'Sprecht bitte weiter.', 'Sprecht bitte weiter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4433', 'deDE', 'Wie Ihr seht, bin ich nur ein einzelner Mann und mein Knappe ist ein einfältiger Blödian. Gegen die Macht der Zwerge von Dunkeleisen und der Bestien, die sich in Ragnaros\' eisernem Griff befinden, hätte ich sicherlich keine Chance. Aber mit einem so mutigen und ehrgeizigen Abenteurer, wie Ihr einer seid, könnten wir der Bewohner des Kessels Herr werden. Wenn Ihr mir helft, werde ich Euch reich machen - reicher, als Ihr Euch das je hättet träumen lassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4434', 'deDE', 'Ich möchte das mit meinen Kollegen besprechen.', 'Ich möchte das mit meinen Kollegen besprechen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4435', 'deDE', 'Wir versklaven die Stacheleber!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4436', 'deDE', 'Wir besiedeln dieses Brachland!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4437', 'deDE', 'Bald wird die Geißel die Welt beherrschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4438', 'deDE', 'Meine Augen! Das brennt! Das brennt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4439', 'deDE', 'Quält mich nicht länger! Lasst mich frei, sterbliches Wesen! Die Sümpfe des Elends... sterbliches Geschöpf... bitte...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4440', 'deDE', 'Wie töricht, sterbliches Wesen! Ich werde Eure Seele in Stücke reißen, wenn ich frei bin!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4441', 'deDE', 'Ihr seid ein Agent ihres tückischen Gottes, Dummkopf! Ich werde Euch vernichten lassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4442', 'deDE', 'Meine Seele ist kein Stück billigen Plunders! Sterbliches Wesen, Ihr müsst mich von diesen Ketten befreien!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4443', 'deDE', 'Es tut so weh... Itharius, alter Freund... bitte helft mir...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4444', 'deDE', 'Hat dieser Alptraum denn nie ein Ende? Ich kann mich nicht zwingen, wach zu werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4445', 'deDE', 'Ich habe meinen Auftrag zur Bewachung des Tempels nicht erfüllt und nun findet meine verderbte Seele keine Ruhe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4446', 'deDE', null, 'Ausgezeichnet! Dank Eurer Hilfe haben wir eine Chance, unsere geliebte Stadt zurückzuerobern. Ein dreifaches Hurra für Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4447', 'deDE', '%s singt Beschwörungsformeln in einer unbekannten Sprache.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4448', 'deDE', 'Habt Geduld, $N. Die Fackel ist fast fertig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4449', 'deDE', '%s beugt das Knie. Er sieht erschöpft aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4450', 'deDE', 'Es ist vollbracht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4451', 'deDE', 'Oh, $N, wartet eine Minute! Mir ist noch etwas eingefallen! Kommt zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4452', 'deDE', 'Zaubert die Fackel der Vergeltung herbei, Kalaran.', 'Zaubert die Fackel der Vergeltung herbei, Kalaran.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4453', 'deDE', '%s ist frei und nun an Euch gebunden, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4454', 'deDE', '%s verschlingt den Wachtpostenturm. Der Turm steht in Flammen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4455', 'deDE', 'Ich heiße Euch herzlich willkommen, $R. Ihr habt die normalen Pfade weit hinter Euch gelassen, nur um mit jemandem zu sprechen, der in einer Höhle haust. Ich vermute, Euer unerwarteter Besuch hier hat einen Grund. Ja?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4456', 'deDE', 'Glorreich, in der Tat, Maltrake. Es scheint, mein Plan ist aufgegangen. Würdet Ihr mir nicht zustimmen, sterbliches Wesen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4457', 'deDE', 'Ihr hättet der Legion von Blackrock sicher nicht geholfen, wenn wir Euch nur um Unterstützung gebeten hätten, oder?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4458', 'deDE', '%s lacht ein reptilisches Lachen... zumindest haltet Ihr es für ein Lachen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4459', 'deDE', 'Was sollen wir mit ihnen tun, Meister? Sie sind uns nicht länger von Nutzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4460', 'deDE', 'RUHE, SCHWACHKOPF!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4461', 'deDE', 'Sie sollen leben und sehen, was sie angerichtet haben: Sie sollen die Zerstörung und das Chaos sehen, das die Legion über die Schlucht bringen wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4462', 'deDE', 'Ich verlasse Euch jetzt, sterbliche Wesen. Lebend und mit diesem Plunder. Maltrake! Gebt ihnen den Plunder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4463', 'deDE', 'Oh ja, die abgestreifte Drachenhaut: verliert sie nicht. In der brennenden Steppe gibt es welche meiner Art, die Dinge fertigen, die nur die Abgestreifte Haut des schwarzen Drachenschwarms festigen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4464', 'deDE', '%s schlägt schneller mit seinen riesigen Schwingen. Gleich wird er abheben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4465', 'deDE', 'Die Legion von Blackrock naht, ihr Winzlinge! Die Erde wird brennen und der Himmel in Flammen stehen. Niemand wird überleben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4466', 'deDE', 'Alle Statuen im Raum wieder an ihren ursprünglichen Platz stellen.', 'Alle Statuen im Raum wieder an ihren ursprünglichen Platz stellen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4467', 'deDE', 'Alle Statuen werden wieder an ihren ursprünglichen Platz gestellt.$B$BDie Sequenz muss nun noch einmal von vorn beginnen.', 'Alle Statuen werden wieder an ihren ursprünglichen Platz gestellt.$B$BDie Sequenz muss nun noch einmal von vorn beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4468', 'deDE', 'Sofort, Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4469', 'deDE', 'Seid gegrüßet $Gwerter Herr:holde Dame;. Ich bin Meister Xylems Lehrling Nyrill. Sollte Euch etwas bedrücken, so habt keine Scheu, es meinem Meister zu sagen.$B$BIch stehe Euch zu Diensten, wenn Ihr bereit seid, wieder zum Fuß des Berges zurückzukehren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4470', 'deDE', 'Ich weiß, wer Ihr seid, $N. Das Schicksal und Kräfte, stärker noch als das Schicksal, haben Euch zu mir geführt. Dies hat einen Grund, wenn er mir auch nicht gefallen will. Ihr werdet in Zukunft viele Entscheidungen fällen und ich hoffe, Ihr seid weise genug, das Richtige zu tun.$B$BGlaubt mir, $R, wenn ich Euch versichere: Es ist möglich, die richtige Wahl zu treffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4471', 'deDE', 'Was macht ein Elf wie Ihr in solch einer Höhle?', 'Was macht ein Elf wie Ihr in solch einer Höhle?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4472', 'deDE', 'Ihr seid ziemlich neugierig, aber eine solche Neugier deutet auch immer auf einen Mangel an Verständnis hin. Ich sage dies, weil die von Euch gestellte Frage sinnlos wäre, hätte Euer Hiersein einen Sinn. $B$BDamit wünsche ich Euch einen guten Tag.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4473', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4474', 'deDE', 'Kennt ihr jemanden... oder vielmehr etwas mit Namen Eranikus?', 'Kennt ihr jemanden... oder vielmehr etwas mit Namen Eranikus?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4475', 'deDE', 'Ihr wisst, dass ich Eranikus kenne, sterbliches Wesen. Ich versuche nicht, Euch etwas vorzumachen, und rate Euch, es mir gleichzutun.$B$BIhr seid hier, weil Ihr wisst, dass Euch das, was Ihr im versunkenen Tempel von Atal\'al in Euren Besitz gebracht habt, zu einem bestimmten Zweck zu mir geführt hat. Dieser Zweck ist, meinen Gefährten... meinen Bruder zur Ruhe zu betten. Er ist in einem Alptraum innerhalb eines Traums gefangen. Und allein kann er sich nicht befreien.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4476', 'deDE', 'Was ist mit ihm geschehen? Als ich ihn... traf, war er ziemlich bösartig.', 'Was ist mit ihm geschehen? Als ich ihn... traf, war er ziemlich bösartig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4477', 'deDE', 'Ein Gott der Atal\'ai Trolle ist dafür verantwortlich. Wir haben die körperliche Manifestation des Gottes vernichtet und seinen Tempel versenkt, aber der Gott war viel stärker, als wir es erwartet hatten. Die Saat der Verderbnis wurzelt tief und befällt alles, was ihr in die Quere kommt.$B$BEranikus wurde damit betraut, den versunkenen Tempel zu bewachen und aufzupassen, dass weder die einfachen Atal\'ai noch ihr Gott wiederkehren. Er war ein unschuldiges Opfer der Verderbtheit und wir unterschätzten ihre Wirkung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4478', 'deDE', 'Ich besitze einen Teil von Eranikus\' Essenz. Was wollt Ihr damit... oder von mir?', 'Ich besitze einen Teil von Eranikus\' Essenz. Was wollt Ihr damit... oder von mir?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4479', 'deDE', 'Dass Ihr hier seid, ist ein erster Schritt... eine richtige Entscheidung, wenn Ihr so wollt. Ihr helft damit Yseras Drachenschwarm.$B$BMöglicherweise seid Ihr aus purer Gier hier oder weil Ihr Euch moralisch verpflichtet fühlt. Keiner der beiden Gründe ist für sich allein wichtig. Aber beide sind in einer Hinsicht bedeutsam: Eranikus\' Pein hat mich aus meinem Traum geschreckt und meine Aufgabe ist es nun, seine Qual zu beenden. Ich bitte Euch hiermit, mir dabei zu helfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4480', 'deDE', 'Ich werde nachdenken über das, was Ihr mir erzählt habt.', 'Ich werde nachdenken über das, was Ihr mir erzählt habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4481', 'deDE', 'Ich habe Euch einen Schwurstein von Yseras Drachenschwarm gegeben, $N. Wenn Ihr bereit seid, uns zu helfen, benutzt ihn. Dann können wir beginnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4482', 'deDE', 'Viel Glück. Es tut mir Leid, dass ich Euch nicht mitnehmen kann!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4483', 'deDE', '%s beginnt, Gespenster aus der eiskalten Luft zu beschwören!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4484', 'deDE', 'Eindringlinge! Greift die Eindringlinge an!', 'Eindringlinge! Greift die Eindringlinge an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4485', 'deDE', 'Meine Verbannung ist vorbei! Lasst Blut fließen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4486', 'deDE', 'Interessant, interessant... Ich weiß von einer Druidin, die in den Hügeln über den Höhlen des Wehklagens lebt, $N. Sie kann Euch vielleicht mehr über diesen Splitter sagen.$B$BUnd jetzt muss ich zurück an die Arbeit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4489', 'deDE', '%s taumelt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4490', 'deDE', 'Der Schild ist unten! Erhebt euch, Atal\'ai! Erhebt euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4491', 'deDE', 'Herrin! Sie wollen Euer Werk zerstören!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4492', 'deDE', 'Ha! Nun ist Euer Tod besiegelt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4493', 'deDE', 'Also gut, bleibt dicht zusammen. Wenn Ihr auch nur einen Moment unaufmerksam seid, wird Euch der Feind geradewegs aus dem Schatten anspringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4495', 'deDE', null, 'Ihr wagt es! Jetzt werdet Ihr meinen Zorn zu spüren bekommen, $R.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4496', 'deDE', 'Die Tage, die wir hier in Azshara verbracht haben, gehören sicher nicht zu unseren besten und glorreichsten, aber wir sind noch jung und noch fließt Blut in unseren Adern. Hier werden wir den Sieg erringen. Es ist nur eine Frage der Zeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4497', 'deDE', 'Belgrom hat uns aus Orgrimmar hergeschickt, um ihm in dieser wenig klugen Allianz beizustehen, aber wenn sich sein Wagnis auszahlt, ist es für die ganze Horde von großem Vorteil.$B$BZu viele Orcs mussten schon ihr Leben lassen, weil er den Weibern imponieren wollte, aber er ist trotzdem noch immer unser Befehlshaber. Die Ehre gebietet uns, das Beste aus der Situation zu machen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4498', 'deDE', null, 'Ich bin nicht der Ehre wegen hier, $N Ich bin wegen des Hochmuts eines einzigen Orcs hier.$B$BHätte ich Belgroms Angebote nicht ausgeschlagen, so würde ich mich jetzt sicherlich in Ogrimmar um die wirklich wichtigen Dinge kümmern.$B$BSein Verlangen nach Frauen ist auch den Kriegerinnen von Durotar nicht unbekannt, aber er spielt trotzdem weiter den Traumprinzen.$B$BEr kann allerdings von Glück sagen, dass er sein Verlangen nach mir nicht noch weiter verfolgt hat, sonst würden ihm heute nicht nur die Finger seiner Hände fehlen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4499', 'deDE', null, 'Seid gegrüßt, $N. Wie ich sehe, seid Ihr aus Ogrimmar hergekommen, um uns zu helfen. Das freut mich. Ich will diesen verfluchten Wald so schnell wie möglich hinter mir lassen. Da sitze ich doch viel lieber in Ogrimmar und vertreibe mir die Zeit bei einem guten Bier.$B$BObwohl ich es gar nicht erwarten kann zu hören, wie Euch Belgrom überredet hat, hierher zu kommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4500', 'deDE', 'Ihr habt es geschafft, $N! <Die merkwürdig vertraute Stimme scheint aus der Phiole zu kommen.> Beeilt Euch! Die Ablagerung bleibt nur 30 Minuten lang stabil.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4501', 'deDE', 'Na schön, los geht\'s. Es wird etwa fünf Minuten dauern, das Ding mit einem Ritual zu verschließen. Haltet mir das Gezücht vom Leib, wenn ich erst einmal angefangen habe, oder es ist unser aller Ende!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4502', 'deDE', 'Wer wagt es?! Ich werde Euch vernichten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4503', 'deDE', 'DUMMY SPELL TEST MESSAGE', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4504', 'deDE', 'Noch drei Minuten... ich spüre, wie sich die Energie aufbaut! Haltet die Abwehr aufrecht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4505', 'deDE', 'Noch zwei Minuten! Wir haben es schon zur Hälfte geschafft, aber Ihr dürft jetzt nicht nachlassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4506', 'deDE', 'Nur noch eine Minute! Haltet durch, das Ritual ist gleich vollbracht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4507', 'deDE', 'Das war\'s... wir haben es geschafft! Das Ritual ist in Gang gesetzt und die Götzenfeuer werden bald auf ewig verlöschen! Ihr seid wahrhaftig die Helden, für die ich Euch gehalten habe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4508', 'deDE', '%s schlägt nach Eurer Sammelphiole. Das grüne Leuchten schließt Euch ein. Du siehst gar nicht gut aus, Kumpel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4509', 'deDE', 'Was führt Euch zum Zirkel des Cenarius, $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4510', 'deDE', 'Ich bin wegen der Ausbildung hier.', 'Ich bin wegen der Ausbildung hier.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4511', 'deDE', 'Ich weiß, ich weiß, ich befinde mich hier am Ende der Welt!$B$BEigentlich stamme ich ja aus Ratchet, aber nachdem ich hier mit meinem Zeppelin abgestürzt war, dachte ich, ich könnte auch gleich mit den Forschungen beginnen, deretwegen ich hergekommen bin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4512', 'deDE', 'Dieser Geist schwebt über seinem Leichnam. Seine geisterhaften Augen starren durch Euch hindurch in weite Ferne...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4514', 'deDE', 'Feralas - Screecher Spirit - On Quest Default Greeting', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4515', 'deDE', '%s bekommt allmählich wirklich Hunger!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4516', 'deDE', '%s ist SEHR HUNGRIG!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4517', 'deDE', null, 'Das Land Feralas hat viel durchgemacht, $R. Erde, Bäume, Lebewesen - sie alle verändern sich und was Feralas ist und bleibt.$B$BDie Zwillingskolosse über uns sind die einzige Konstante, bleiben unverändert inmitten all der Geschehnisse...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4518', 'deDE', 'Ich biete den Bedürftigen meine Schwingen für einen sicheren Abstieg an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4519', 'deDE', 'Könnt Ihr mich nach unten bringen?', 'Könnt Ihr mich nach unten bringen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4520', 'deDE', 'Tja, jetzt, da Ihr es erwähnt... ja, gestern Nachmittag ist mir wirklich was Komisches aufgefallen.$B$BIch rannte gerade an ein paar Troll-Ruinen südöstlich von hier vorbei... Genau genommen hüpfte ich dran vorbei, wenn Ihr\'s wissen wollt... Der Sand ist so unglaublich heiß!$B$BJedenfalls hoppelte ich an den Ruinen vorbei, da seh ich doch, dass einer sein Lager aufgeschlagen hat. Ist ja schon ein komischer Platz für ein Lager, wenn Ihr mich fragt... Ich hätte mich ja noch ein bisschen genauer umgesehen, aber da hat mich so ein blöder Riesengeier weggejagt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4521', 'deDE', 'Schleifer folgen $N! Schleifer KAPUTTMACHEN Wilderer! KLATSCH, KLATSCH, KLATSCH! Ihr spielen mit Stecken und sagen Schleifer wenn er KAPUTTMACHEN soll! KLATSCH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4522', 'deDE', 'Hier! Arbeit ist vollbracht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4523', 'deDE', 'Keine Bange, ich finde schon ein nettes Zuhause für dich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4524', 'deDE', 'Kann irgendwer meinem süßen, überzähligen kleinen Kätzchen ein Zuhause geben?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4525', 'deDE', 'Ich versteh einfach nicht, dass mein Vater es mir nicht erlaubt, dein Schwesterchen zu behalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4526', 'deDE', 'Kätzchen zu verkaufen, in liebevolle Hände abzugeben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4527', 'deDE', 'Was bedeutet \'Allergie\' überhaupt? Und was hat das mit meinen Kätzchen zu tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4528', 'deDE', 'Würde irgendwer bitte meinem süßen Kätzchen ein Zuhause geben?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4529', 'deDE', null, 'Nein! Wir dürfen nicht zulassen, dass Ihr Hakkar beschwört!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4530', 'deDE', null, 'Ihr dürft unseren Gott nicht beschwören!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4531', 'deDE', null, 'Nein! Das dürft Ihr nicht tun!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4532', 'deDE', null, 'Stopp! Ungläubige!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4533', 'deDE', null, 'Wa... was geht hier vor?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4534', 'deDE', 'Danke, dass Ihr mich gerettet habt!$B$BIch bin ein Koch und Alchimist aus Sturmwind. Für Rezepte meines Onkel Angus war ich auf der Suche nach Kräutern, die es nur hier gibt, als mich diese Stacheleber gefangen nahmen. Wenn Ihr mich nicht gefunden hättet, wäre ich sicher im Kochtopf gelandet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4535', 'deDE', 'Ihr könnt kochen? Ich auch! Könnt Ihr mir vielleicht ein besonderes Rezept verraten?', 'Ihr könnt kochen? Ich auch! Könnt Ihr mir vielleicht ein besonderes Rezept verraten?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4536', 'deDE', 'Hm. Ja, ich habe da tatsächlich ein Rezept, das etwas für Euch sein könnte. Ich will es Euch lehren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4537', 'deDE', 'Ihr seid Alchimist? Ich auch. Vielleicht könnt Ihr mich lehren, was Ihr wisst...', 'Ihr seid Alchimist? Ich auch. Vielleicht könnt Ihr mich lehren, was Ihr wisst...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4538', 'deDE', 'Nun ja, ich kenne da tatsächlich ein seltenes alchimistisches Rezept. Ich will es Euch lehren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4539', 'deDE', '%s ist merklich geschwächt. Der Teufelsfluch wirkt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4540', 'deDE', null, '%s ist merklich anfälliger für Eure Attacken. Der Teufelsfluch wirkt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4541', 'deDE', '%s scheint nicht mehr in der Lage, Zauber zu wirken. Der Teufelsfluch wirkt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4542', 'deDE', 'Eure Teufelswaffe ist bereit, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4543', 'deDE', 'ICH NÄHERE MICH DIESER WELT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4544', 'deDE', 'NEEEIN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4545', 'deDE', 'HIER BIN ICH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4546', 'deDE', 'STERBT, STERBLICHE WESEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4547', 'deDE', null, '%s beginnt einen Gegenzauber zu wirken...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4548', 'deDE', 'Jetzt nur noch vorsichtig mischen... Hoppla! Tja, ich hab zwar ein bisschen verschüttet, aber es ist fertig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4549', 'deDE', 'Wer wagt es, mich zu beschwören?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4550', 'deDE', 'Seid gegrüßt! Falls es Euer Begehr ist, auf Goblin-Ingenieurskunst-Baupläne zuzugreifen, haltet jetzt bitte Eure Goblin-Ingenieur-Mitgliedskarte bereit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4551', 'deDE', 'Zugriff auf geheime Goblin-Ingenieurskunst-Baupläne!', 'Zugriff auf geheime Goblin-Ingenieurskunst-Baupläne!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4552', 'deDE', 'Seid gegrüßt! Falls es Euer Begehr ist, auf Gnomen-Ingenieurskunst-Baupläne zuzugreifen, haltet jetzt bitte Eure Gnomen-Ingenieur-Mitgliedskarte bereit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4553', 'deDE', 'Zugriff auf geheime Gnomen-Ingenieurskunst-Baupläne!', 'Zugriff auf geheime Gnomen-Ingenieurskunst-Baupläne!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4554', 'deDE', 'Guten Tag auch, $GBursche:Mädchen;, mein Name ist Oglethorpe und diese schier unglaublichen Gnomen-Geräte in Eurem Besitz gehen vielleicht auf mein Konto! Wenn sie in die Luft geflogen sind, waren es nicht meine. Wenn sie Euch reich gemacht haben, können wir hingegen gern über Tantiemen reden!$B$BOh, und falls Ihr aus Gnomeregan kommt, dann könnt Ihr Euch ebensogut zwei Neunziggradwinkel schnappen, sie aneinanderkleben und den Heimweg antreten. Ich habe Euch nichts zu sagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4555', 'deDE', 'Na, wenn das nicht $N ist! Ich freue mich, Euch zu sehen. Ich kann Euch gar nicht sagen, wie dankbar ich Euch für all die Hilfe bei meinen Erfindungen bin!$B$BWas kann ich heute für Euch tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4556', 'deDE', 'Ich bin Tüftlermeister Overspark, Chefarchitekt für moderne gnomische Ingenieursleitsätze in der heutigen Welt. Meine Aufgabe ist es, dafür zu sorgen, dass die Kraft der Erfindung auch weiterhin von unseren Bogenlichtschraubenschlüsseln ausgeht!$B$BUnd falls Ihr zufällig gerade ein gutes Mittel zur Strahlungseliminierung wisst... würden wir im Moment zufälligerweise sehr daran interessiert sein...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4557', 'deDE', 'Lasst mich Euch ein bisschen Nachhilfe in Mathematik geben: \"Goblin\" plus \"Ingenieurskunst\" gleich FACHWISSEN, Baby. Jeder, der etwas anderes behauptet, hat keine Ahnung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4558', 'deDE', 'Grunzer! Angriff!', 'Grunzer! Angriff!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4559', 'deDE', 'Wächter! Verteidigt Sen\'jin!', 'Wächter! Verteidigt Sen\'jin!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4560', 'deDE', 'Ihr werdet unser hochheiliges Land nicht entweihen!', 'Ihr werdet unser hochheiliges Land nicht entweihen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4561', 'deDE', 'Wachen!', 'Wachen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4562', 'deDE', 'Schildwachen, beschützt mich!', 'Schildwachen, beschützt mich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4563', 'deDE', 'Durch meine Studien ist mir jetzt klar, dass ihn die Niederlage des Avatars des Gottes Hakkar geschwächt hat. Doch der Großteil seiner Kraft ist noch da und wartet am anderen Ende des Nether.$B$BIhr habt seinen Avatar bereits einmal besiegt, $N. Wenn Euch das noch einmal gelänge, könntet Ihr den Gott vielleicht so weit schwächen, dass wir ihm eines Tages in seiner wahren Gestalt gegenübertreten können.$B$BIch habe einen Zauber vorbereitet, mit dem Ihr abermals einen Avatar Hakkars im Sanktum des gefallenen Gottes beschwören könnt. Wollt Ihr ihm nochmals gegenübertreten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4564', 'deDE', 'Schildwachen, beschützt mich!', 'Schildwachen, beschützt mich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4565', 'deDE', 'Seid gegrüßt, $C. Zwar haben Zeit und Trunksucht meine Sinne getrübt, doch sehe ich mit dem inneren Auge noch immer gut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4566', 'deDE', 'Wachen! Helft mir!', 'Wachen! Helft mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4567', 'deDE', 'Ja. Gebt mir den Zauber zur Beschwörung des Avatars.', 'Ja. Gebt mir den Zauber zur Beschwörung des Avatars.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4568', 'deDE', 'Wachen!', 'Wachen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4569', 'deDE', 'Eindringlinge! Greift die Eindringlinge an!', 'Eindringlinge! Greift die Eindringlinge an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4570', 'deDE', '%s sucht nach einer Quelle arkaner Energie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4571', 'deDE', '%s knurrt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4572', 'deDE', 'Hallo, $N. Ich bilde hier die jungen Hippogryphe aus und versorge sie. Seht Euch ruhig um.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4573', 'deDE', 'Erzählt mir mehr über die Hippogryphe.', 'Erzählt mir mehr über die Hippogryphe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4574', 'deDE', 'Druiden spüren die Not jeglicher Lebewesen dieser Welt und reagieren sehr sensibel darauf. Als klar wurde, dass diese Vögel in Gefahr sind, haben wir uns überlegt, wie wir sie vor der Verderbnis von Feralas retten könnten.$B$BEine Gnomin namens Curgle Cranklehop hat sich der Sache angenommen. Sie sammelt die Eier ein und bringt sie hierher, wo ich mich dann um sie kümmere.$B$BUnterstützung für unsere Sache können wir immer gebrauchen, $N. Vielleicht solltet Ihr Euch mal nach Gadgetzan begeben und mit Curgle reden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4575', 'deDE', 'Nochmals danke, $N. Ich werde jetzt einfach hier warten, bis ich ungefährdet gehen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4576', 'deDE', 'Ihr müsst das Teufelshundfährtenleserset öffnen und die Bedienungsanleitung lesen. Beschwört die Bestie, wenn Ihr Euch nahe der Halbinsel der Klippenriesen südlich von hier befindet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4577', 'deDE', 'Habt Dank, kleiner $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4578', 'deDE', 'Eure Kochkünste lassen doch noch zu sehr zu wünschen übrig. Wenn Ihr etwas mehr Übung habt, kann ich Euch vielleicht lehren, was ich weiß.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4579', 'deDE', 'Eure Fertigkeiten in Alchemie sind nicht gut genug, als dass Ihr von meinen Lehren profitieren könntet. Vielleicht später einmal...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4580', 'deDE', 'Captain, da kommen sie! Die Naga kommen!', 'Captain, da kommen sie! Die Naga kommen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4581', 'deDE', null, 'Das Arkane verdirbt nur die Schwachen. Selbst wenn Ihr dem Pfad des Hexenmeisters nicht folgt, solltet Ihr das niemals vergessen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4582', 'deDE', 'Freut mich, $N! Was kann ich für Euch tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4583', 'deDE', 'Wachen!', 'Wachen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4584', 'deDE', 'Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.', 'Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4590', 'deDE', 'Danke.', 'Danke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4591', 'deDE', 'Ich bin für immer an diese verfluchte Welt gebunden. Was wünscht Ihr, Sterblicher?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4592', 'deDE', 'Ich möchte dem Entweiher gegenüber treten.', 'Ich möchte dem Entweiher gegenüber treten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4593', 'deDE', 'Durch Euren Befehl!', 'Durch Euren Befehl!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4594', 'deDE', 'Verlasst diesen Ort. <Ein weiß glühender Flammenbogen geht von dem Relikt aus. Eine Erinnerung wurde erlangt: Ein einsamer Dunkeleisenzwerg kniet, von sieben Toten umgeben, vor einem Flammen-Monolithen.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4595', 'deDE', 'Ihr werdet hier sterben. <Visionen von Chaos und Zerstörung strömen in Eure Gedanken.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4596', 'deDE', 'Besudelt diese Ruinen nicht, sterbliches Wesen. <Ihr werdet von einem grellen Lichtblitz eingehüllt. Eine Kreatur, die ganz aus Feuer besteht, ist der einzige Anblick, an den Ihr Euch erinnern könnt.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4597', 'deDE', 'Helft uns, Fremdling. <Das Relikt zerfällt zu Staub. Eine Vision von acht Dunkeleisenzwergen, die eine Art Ritual durchführen, erstrahlt vor Eurem geistigen Auge.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4598', 'deDE', 'Er kann nicht besiegt werden. <Das Relikt brennt nieder, bis nichts mehr übrig ist. Die Erinnerungen, die es enthielt, sind jetzt Eure eigenen. Diese Stadt wurde von einem Wesen zerstört, das nicht von dieser Welt ist.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4599', 'deDE', 'Eure Existenz wird zur Kenntnis genommen. <Das Relikt zerfällt zu Staub. Neu erlangtes Wissen pulsiert in Eurem Kopf. Etwas Böses lauert im Herzen des Berges.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4600', 'deDE', 'Entweiher... für diesen Frevel werdet Ihr bestraft. <Das Symbol einer Flamme geht von dem Relikt aus, bevor es zerfällt.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4601', 'deDE', null, 'Seid gegrüßt, $N. Seid Ihr gekommen, um die Geschichte von Eisenschmiede kennen zu lernen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4602', 'deDE', null, 'Gebt mir Bescheid, wenn Ihr bereit seid, die Geschichte anzuhören.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4603', 'deDE', 'Ich bin bereit, Historikerin Archesonus.', 'Ich bin bereit, Historikerin Archesonus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4604', 'deDE', null, 'Vor Jahrhunderten ragte die große Stadt Thaurissan stolz über einem fruchtbaren Land empor, das heute die verwüstete brennende Steppe ist.$B$BThaurissan war die Heimat der Nation der Dunkeleisenzwerge und zu jener Zeit Zentrum eines Bürgerkriegs, der unter dem Zwergenvolk tobte.$B$BSeht, dieser Teil der Welt war einstmals ein Teil des Rotkammgebirges - bis zu dem Tag, als Thaurissan von einer gewaltigen Explosion vernichtet wurde. Die Stadt ward dem Erdboden gleich, das Umland eine Aschenwüste.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4605', 'deDE', 'Das ist tragisch. Wie konnte das geschehen?', 'Das ist tragisch. Wie konnte das geschehen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4606', 'deDE', null, 'Das wissen wir nicht.$B$BDie überlebenden Dunkeleisenzwerge zogen in die Berge und begannen mit dem Wiederaufbau ihrer Stadt.$B$BAus der Asche von Thaurissan entstanden dann die Blackrockspitze und die neue Dunkeleisennation. In dem Gebiet von der sengenden Schlucht, der Kessel genannt wird, wurden weitere Befestigungen errichtet.$B$BGerüchte über etwas uraltes Böses, das das Königreich der Dunkeleisenzwerge beherrschte, machten die Runde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4607', 'deDE', 'Interessant... fahrt bitte fort.', 'Interessant... fahrt bitte fort.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4608', 'deDE', null, 'In letzter Zeit sah man die Dunkeleisenzwerge wieder regelmäßiger an der Oberfläche. Im Ödland wurden Festungen erbaut. Auf der Suche nach der alten Lehre schickte man gewaltige Erforschungstrupps in die Tiefen von Uldaman. Sie sind sogar bis zu unserer Heimat vorgestoßen und dort gingen sie beim Loch gegen unseren Damm vor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4609', 'deDE', 'Unglaublich! Wie können sie es wagen!', 'Unglaublich! Wie können sie es wagen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4610', 'deDE', null, 'Wir brauchen Abenteurer zu unserer Unterstützung, $N. Werdet Ihr helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4611', 'deDE', 'Natürlich werde ich Euch helfen!', 'Natürlich werde ich Euch helfen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4612', 'deDE', 'Das war ausgezeichnete Arbeit, Spionagebot! Schön, dass wir wieder zusammengefunden haben!', 'Das war ausgezeichnete Arbeit, Spionagebot! Schön, dass wir wieder zusammengefunden haben!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4613', 'deDE', 'Das ist es! Darauf habt Ihr gewartet! TÖTET SIE!', 'Das ist es! Darauf habt Ihr gewartet! TÖTET SIE!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4614', 'deDE', 'Äh, hallo! Falls Ihr hier seid, um ein Honorar einzutreiben und/oder wegen einer Vorladung, dann müsst Ihr mich verwechseln. Tut mir schrecklich Leid!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4615', 'deDE', null, 'Ich glaube, meine Halskette ist in den See gefallen. Mein Papa hat sie mir geschenkt. Jetzt wird er bestimmt schrecklich wütend.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4616', 'deDE', null, 'Danke, $Gwerter Herr:werte Dame;, dass Ihr meine Halskette gefunden habt! Ihr seid \'ne Wucht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4617', 'deDE', 'Außer gegen die Grunzer da draußen kämpften meine Brüder und ich in der Schlacht von Hyjal. Alle, die die Bank bestehlen wollen, bekommen es zuerst mit uns zu tun.', 'Außer gegen die Grunzer da draußen kämpften meine Brüder und ich in der Schlacht von Hyjal. Alle, die die Bank bestehlen wollen, bekommen es zuerst mit uns zu tun.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4618', 'deDE', 'Hallo, $Gwerter Herr:werte Dame;, hättet Ihr wohl ein bisschen Zeit und könntet uns helfen? Bitte?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4619', 'deDE', 'Ich garantiere mit meinem Blut für die Sicherheit dieser Bank, genügt Euch das?', 'Ich garantiere mit meinem Blut für die Sicherheit dieser Bank, genügt Euch das?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4620', 'deDE', 'Nochmals vielen Dank, dass Ihr Hilary geholfen habt, $Gwerter Herr:werte Dame;! Wenn ich erwachsen bin, will ich auch so heldenhaft sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4621', 'deDE', 'Würdet Ihr Eure Waren lieber einem Goblin anvertrauen?', 'Würdet Ihr Eure Waren lieber einem Goblin anvertrauen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4622', 'deDE', 'Nach so vielen Jahren des Kampfs und der Sklaverei ist es schön, wieder ein Zuhause zu haben.', 'Nach so vielen Jahren des Kampfs und der Sklaverei ist es schön, wieder ein Zuhause zu haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4623', 'deDE', 'Sagt mir, was Ihr wollt, und fasst Euch kurz.', 'Sagt mir, was Ihr wollt, und fasst Euch kurz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4624', 'deDE', 'Ich lebe nur, um dem Meister zu dienen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4625', 'deDE', 'Präsentiert die Schmuckstücke, Maltrake.', 'Präsentiert die Schmuckstücke, Maltrake.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4626', 'deDE', 'Sagt mir, was Ihr wollt, und fasst Euch kurz.', 'Sagt mir, was Ihr wollt, und fasst Euch kurz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4627', 'deDE', 'Miau!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4628', 'deDE', null, 'Ich beherrsche die Katzensprache. Effsee sagte danke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4629', 'deDE', 'Selbst im Frieden herrscht noch Krieg. Viele Klans kämpfen nach wie vor unter dem Banner des Kriegshäuptlings. Seid Ihr hier, um Euren Klan bei jenen einzureihen, die für Orgrimmar kämpfen?', 'Selbst im Frieden herrscht noch Krieg. Viele Klans kämpfen nach wie vor unter dem Banner des Kriegshäuptlings. Seid Ihr hier, um Euren Klan bei jenen einzureihen, die für Orgrimmar kämpfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4630', 'deDE', 'Selbst im Frieden herrscht noch Krieg. Viele Klans kämpfen nach wie vor unter dem Banner des Kriegshäuptlings. Seid Ihr hier, um Euren Klan bei jenen einzureihen, die für Orgrimmar kämpfen?', 'Selbst im Frieden herrscht noch Krieg. Viele Klans kämpfen nach wie vor unter dem Banner des Kriegshäuptlings. Seid Ihr hier, um Euren Klan bei jenen einzureihen, die für Orgrimmar kämpfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4631', 'deDE', 'Ja, ich biete Ausbildung an. Aber sie ist nicht billig.', 'Ja, ich biete Ausbildung an. Aber sie ist nicht billig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4632', 'deDE', 'Hallo, $N! Ich bin Meisteringenieur Scooty.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4633', 'deDE', 'Ja, natürlich erteile ich anderen Verzauberern Unterricht, vorausgesetzt, Ihr seid dazu bereit.', 'Ja, natürlich erteile ich anderen Verzauberern Unterricht, vorausgesetzt, Ihr seid dazu bereit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4634', 'deDE', 'Ich habe meinen Transponder verloren und brauche einen neuen, Scooty.', 'Ich habe meinen Transponder verloren und brauche einen neuen, Scooty.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4635', 'deDE', 'Die Erde birgt viele Geheimnisse, $C; Eure Pflicht ist es, dafür zu sorgen, dass jene, die zum Wohle aller geheim bleiben müssen, es auch bleiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4636', 'deDE', 'Wenn Ihr hier seid, um meine Zeit zu verschwenden, dann verschwendet Ihr die Zeit des gesamten Zirkel des Cenarius. Lasst uns um Euretwillen hoffen, dass Ihr nicht töricht genug seid, das zu tun.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4637', 'deDE', 'Ich kann die Führung von Sturmwind nicht verstehen. Sie schickt den Großteil unserer Armee weg... wo unsere Heimat so sehr bedrängt wird!$B$BVon Westfall kommen Diebe, aus Ost und West nähern sich Gnolle - es ist ein Wunder, dass Sturmwind selbst nicht belagert wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4638', 'deDE', 'Hallo, $C. Ich hoffe, Ihr seid gekommen, um unsere Truppen zu verstärken. Bestien und Diebe dringen in unser geliebtes Elwynn ein und wir haben kaum ausreichend Männer, um die Straßen zu sichern!$B$BUnd falls Ihr es noch nicht getan habt, lest die Information über Hogger. Das ist eine Bestie in jenem Walde dort, derer man sich annehmen muss.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4639', 'deDE', 'Hallo, $N. Willkommen in Thelsamar. Versäumt während Eures Aufenthalts nicht, unsere örtliche Brauerei zu besuchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4640', 'deDE', 'Willkommen in Thelsamar, Eurer zweiten Heimat am Loch Modan.$B$BThelsamar ist ein hübsches Fleckchen, hat aber allen, die dem Ruf der Pflicht folgen, mehr zu bieten als nur Bier und Pökelfleisch! Wenn Ihr bereit seid, im Namen der Allianz Gefahren zu trotzen, dann lest den Aushang der Zweigstelle Thelsamar der Forscherliga.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4641', 'deDE', null, 'Ahoi, $N! Kein hübscher Anblick, was?$B$BDie HERRIN DES HORIZONTS auf dem Meeresgrund, meine Mannschaft arg mitgenommen und unsere Fracht sowie die Passagiere wie Treibgut verstreut... man könnte glatt meinen, nach einem Festgelage in einem der Kanäle Sturmwinds zu sein.$B$BUnd damit noch lange nicht genug...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4642', 'deDE', '%s stößt herausforderndes Gebrüll aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4643', 'deDE', 'Verschwörungen allerorten, sage ich. Vielen fehlt das visionäre Denken. Sie begreifen nicht, was ich mache, und würden mich am liebsten in Vergessenheit sehen!$B$BTretet nicht in den Schmutz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4644', 'deDE', 'Was macht Ihr hier draußen?', 'Was macht Ihr hier draußen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4645', 'deDE', 'Was ich mache? Seht Ihr das nicht? Ich bin ein Träumer, $Gwerter Herr:werte Dame;, und ich träume von einer besseren Zukunft für uns alle. Ich bin eindeutig ein Visionär.$B$BEs gibt da freilich auch jene, die uns in Banalität zu halten trachten. Ich sagte dem Erzdruiden Staghelm, dass ich ein Träumer sei, worauf er antwortete: \"Die Welt braucht auch Straßenfeger.\"$B$BBis zu der Zeit, da uns die Fesseln des Banalen abgestreift werden, helfe ich dem Zirkel des Cenarius diese Dreckballen herumzuschleppen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4646', 'deDE', 'Seid gegrüßt, $R. Ich bin Franclorn Forgewright. Seid Ihr hier, um etwas über die Spitze und die Dunkeleisenzwerge zu lernen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4647', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4648', 'deDE', 'Erstaunlich! Ich habe noch nie mit einer Geisterscheinung gesprochen. Ich möchte lernen!', 'Erstaunlich! Ich habe noch nie mit einer Geisterscheinung gesprochen. Ich möchte lernen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4649', 'deDE', 'Ich hatte keine Wahl, das dachte ich jedenfalls. Ich würde die Zerstörung von Thaurissan ertragen und an der Seite meines Königs und seines Volkes stehen.$B$BUnter der Leitung von Ragnaros ließ König Dagran Thaurissan das Herz des Berges herausreißen und errichtete die Blackrockspitze. Wir arbeiteten unermüdlich, um eine Zwergenstadt zu bauen, die mächtiger als jede Stadt oder Festung zuvor sein sollte... oder danach.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4650', 'deDE', 'Sprecht bitte weiter.', 'Sprecht bitte weiter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4651', 'deDE', 'Ich musste mit ansehen wie Ragnaros\' Verderbnis die Herzen und Gedanken der Dunkeleisennation vergiftete. Das einst ehrwürdige Volk, dem Wahnsinn verfallen... dem Bösen.$B$BAls der Bau der Spitze abgeschlossen war, schwand mein Wunsch, bei meinem Volk zu leben. Ich verschied als Held der neuen Dunkeleisennation. Dieses Grabmal wurde mir zu Ehren hier errichtet - am Eingang zu der Stadt, die ich geplant und erbaut hatte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4652', 'deDE', 'Faszinierend. Bitte erzählt mir mehr.', 'Faszinierend. Bitte erzählt mir mehr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4653', 'deDE', 'Ironfel, mein gewaltiger Hammer, wurde dem neuen Chefarchitekten weitergegeben, Fineous Darkvire: Ein Mann mit fragwürdigen Motiven und wenig Talent.$B$BUnd hier kommt Ihr ins Spiel, $N. Ich will, dass er aufgehalten wird. Er ist so unfähig, dass er die wahre Macht von Ironfel nicht begreift; sollte ihm jedoch jemand nachfolgen, der fähig ist, das ganze Potenzial von Ironfel zu nutzen, könnten die Verheerungen, die dessen Schöpfungen über die Welt bringen würden, katastrophal sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4654', 'deDE', 'Zeigt mir den Weg, Franclorn.', 'Zeigt mir den Weg, Franclorn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4655', 'deDE', 'Ihr findet Fineous Darkvire jenseits des Rings des Gesetzes in der Halle des Handwerks. Tötet den elenden Wicht und nehmt Ironfel an Euch.$B$BBringt Ironfel zum Schrein von Thaurissan und legt den Hammer an seinen rechtmäßigen Ort: in die Hände der Statue, die mir zu Ehren aufgestellt wurde.$B$BSobald dieses getan ist, öffnet sich das Fach, in dem ich den Hauptschlüssel verborgen habe. Wichtiger jedoch ist: Ironfel wird für alle Zeiten in meinen Händen bleiben. Sollte jemand versuchen, den Hammer zu entfernen, werden Statue und Hammer bersten und für immer verloren sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4656', 'deDE', 'Ja, aber Ihr werdet feststellen, dass es sehr viel lohnender ist, wenn Ihr auch einige Fertigkeiten in der Kräuterkunde habt! Ihr werdet bestimmte Samen hegen und kultivieren - und nicht blindwütig eine Axt schwingen oder hektisch nach vorüberhuschenden Schatten gestikulieren müssen. Mit dem Immergrünbeutel, den Ihr erhaltet, kann zwar selbst der dümmste Einfaltspinsel etwas wachsen lassen, aber alle, die über ein bisschen Wissen in der Kräuterkunde verfügen, werden eine reichlichere Ernte erzielen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4657', 'deDE', 'Ich bin kein Kräuterkundegeselle - kann ich Euch dennoch bei Eurer Arbeit behilflich sein?', 'Ich bin kein Kräuterkundegeselle - kann ich Euch dennoch bei Eurer Arbeit behilflich sein?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4658', 'deDE', null, 'Seid bereit! Wenn die Naga erst wissen, dass wir am Leben sind, werden sie nicht zögern, ihren Vorteil zu nutzen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4659', 'deDE', 'Wisset, $GPrinz:Prinzessin;... mir ist nicht nach Kommentaren, die Ihr witzig oder komisch findet. Ich bin hier, um darauf achten, dass die Forschungsarbeit des Erzdruiden wie geplant vorankommt. Ich werde alle aussondern, die nicht das Zeug haben, um meinem geliebten Zirkel des Cenarius zu dienen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4660', 'deDE', null, 'Seid gegrüßt, $C. Dieser demütige Diener des Zirkels des Cenarius wünscht Euch und den Euren Segen und Überfluss.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4661', 'deDE', 'Amakkar, was meint Ihr, wohin wird uns Kriegsherr Goretooth schicken? Ich bezweifle, dass ich noch eine Dienstzeit in der Steppe ertragen kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4662', 'deDE', 'Hrmmm...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4663', 'deDE', 'Stellt Euch nicht so an, Razal\'blade. Benehmt Euch endlich wie ein Mitglied der Elitetruppe des Kriegshäuptlings. Ihr wollt doch nicht wie Gargal enden, oder?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4664', 'deDE', '%s scheint keinerlei Kommentar zur Kenntnis zu nehmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4665', 'deDE', 'Proudtusk!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4666', 'deDE', 'Nein, Gargal. Schlicht und ergreifend nein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4667', 'deDE', 'Aber...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4668', 'deDE', 'NEIN! Psst! Klappe halten. Goretooth kommt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4669', 'deDE', '%s lacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4670', 'deDE', 'Seid lieber vorsichtig, $C, denn ich habe einen Verwandlungszauber, auf dem Euer Name steht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4672', 'deDE', 'Alle anwesend und durchgezählt, SIR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4673', 'deDE', 'IM GLEICHSCHRITT, IHR MADEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4674', 'deDE', 'Ich liebe Kuchen.', 'Ich liebe Kuchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4675', 'deDE', 'Nana sagte immer, es gäbe nichts Schöneres auf der Welt als ein warmes Stück frisch gebackenen Kirschkuchen.', 'Nana sagte immer, es gäbe nichts Schöneres auf der Welt als ein warmes Stück frisch gebackenen Kirschkuchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4676', 'deDE', null, 'Was hat es denn nun mit dieser neuen Seuche auf sich?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4677', 'deDE', 'Dieser Kuchen ist echt lecker, möchtet Ihr ein Stück?', 'Dieser Kuchen ist echt lecker, möchtet Ihr ein Stück?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4678', 'deDE', 'Kein guter Tag heute? Kostet etwas Kirschkuchen.', 'Kein guter Tag heute? Kostet etwas Kirschkuchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4679', 'deDE', null, 'Oh, das ist alles schrecklich, schrecklich schlimm! Ihr werdet STERBEN, wenn Ihr es seht, Vivian.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4680', 'deDE', 'Dieser Kirschkuchen schmeckt so lecker, er könnte einen ausgewachsenen Gnom zum Weinen bringen.', 'Dieser Kirschkuchen schmeckt so lecker, er könnte einen ausgewachsenen Gnom zum Weinen bringen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4681', 'deDE', 'Kostet von diesem Kirschkuchen, er wird ein zehn Meilen breites Lächeln auf Euer Gesicht zaubern.', 'Kostet von diesem Kirschkuchen, er wird ein zehn Meilen breites Lächeln auf Euer Gesicht zaubern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4682', 'deDE', 'Frischer Kirschkuchen zu verkaufen. Schon allein sein Anblick wird Euch Tränen der Begeisterung in die Augen treiben.', 'Frischer Kirschkuchen zu verkaufen. Schon allein sein Anblick wird Euch Tränen der Begeisterung in die Augen treiben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4683', 'deDE', null, 'Oh, erzählt schon! Diese Spannung ist unerträglich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4684', 'deDE', 'Kirschkuchen schmeckt von allen Sachen in Azeroth einfach am besten.', 'Kirschkuchen schmeckt von allen Sachen in Azeroth einfach am besten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4685', 'deDE', null, 'Tja, soweit ich gehört habe, hat die neue Seuche den VOLLEN Geschmack der alten, nicht aber ihre Kalorien oder Kohlehydrate!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4686', 'deDE', null, 'Oh, wie überaus entzückend!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4687', 'deDE', null, '%s klatscht aufgeregt in die Hände.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4688', 'deDE', 'Also gut, Proudtusk, Ihr könnt Euren Marsch beginnen. Los geht\'s!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4689', 'deDE', 'Ah, guten Tag, die Damen. Schon von der neuen Seuche gehört?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4690', 'deDE', 'SIR, JA, SIR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4691', 'deDE', 'Ihr habt den Kriegsherrn gehört, ihr Maden! MARSCH, MARSCH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4692', 'deDE', 'Gut gemacht, Soldaten! Rührt euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4693', 'deDE', null, '%s errötet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4694', 'deDE', null, 'Iiiiiiiih... nicht mal, wenn Ihr der letzte Orc auf ganz Kalimdor wärt, Lunk.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4695', 'deDE', '%s lacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4696', 'deDE', 'Oh, so spät schon! Ich muss zu meinem Posten zurückkehren... da drüben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4697', 'deDE', 'Wenn Ihr mich jetzt bitte entschuldigen wollt, ich muss gehen. Guten Tag, die Damen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4698', 'deDE', 'Hinter diesen Mauern warten große Gefahren, $N. Kehrt um, bis Ihr mehr Erfahrung im Kampf habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4699', 'deDE', 'Eure Schreckensherrschaft endet hier! Seht Eurem Ende ins Auge, sterbliche Wesen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4700', 'deDE', 'Ihr müsst hart im Nehmen sein, dass Ihr durch das Ödland zieht, $C. So hart wie ich.$B$BVielleicht seid Ihr aber auch hier, weil Ihr verrückt seid. Verrückt wie ich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4701', 'deDE', '%s läuft ums liebe Leben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4702', 'deDE', '%s streckt im Fallen die Hände nach Captain Beltis aus.', '%s streckt im Fallen die Hände nach Captain Beltis aus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4703', 'deDE', 'Das Böse darf niemals in diese Welt eindringen! Kommt, meine Kinder!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4704', 'deDE', 'Geht da weg!', 'Geht da weg!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4705', 'deDE', 'Ach, wenn ich doch nur einen Ast hätte, um ihn vor mich zu halten. Dann würden die Wachen mich nicht sehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4706', 'deDE', 'Ihr müsst Geduld mit mir haben, $N. Mein Spionagebot ist da drin und arbeitet an einer heiklen Sache. Ich bin sofort bei Euch.$B$BOh, und tut mir einen Gefallen, lasst Euch nicht von Lescovar sehen... wir wollen ihn in einen Hinterhalt locken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4707', 'deDE', 'GOR\'TESH TOT?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4708', 'deDE', 'LAUFT! LAUFT ZU HÜGELS!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4709', 'deDE', 'WER HABEN GOR\'TESH GETÖTEN? WER DAS WAREN?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4710', 'deDE', 'Harte Zeiten, eh, $N? Wo man geht und steht, braucht jemand Hilfe, und meine Frau und ich machen da keine Ausnahme.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4711', 'deDE', '$N TÖTEN! $GEr:Sie; Euch jetzt umbringen! LAUFT! LAUFT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4712', 'deDE', 'Zu den Hügels! Wartet! Wir schon in Hügels!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4713', 'deDE', 'Ich würde gern sehen, was Ihr zu verkaufen habt.', 'Ich würde gern sehen, was Ihr zu verkaufen habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4714', 'deDE', '%s sieht fragend zu Euch auf. Vielleicht solltet Ihr der Sache auf den Grund gehen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4715', 'deDE', 'Schießt mich hoch!', 'Schießt mich hoch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4716', 'deDE', 'Seid gegrüßt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4717', 'deDE', null, 'Ihr seid wahrhaft mächtig, $N. Kommt zu mir, wenn Ihr mehr Arbeit sucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4718', 'deDE', 'Meine Augen täuschen mich niemals, $R. Ich sehe große Macht in Euch - womöglich genug Macht, unsere Sache weiter voranzubringen. Sucht mich in der Turmspitze auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4719', 'deDE', 'Hallo, Ihr da! Man nennt mich Gregan. Was kann ich für Euch tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4720', 'deDE', 'Wollt Ihr was kaufen, ja?', 'Wollt Ihr was kaufen, ja?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4721', 'deDE', 'Könnt Ihr mir erzählen, wie ich ein Videre-Elixier bekommen kann?', 'Könnt Ihr mir erzählen, wie ich ein Videre-Elixier bekommen kann?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4722', 'deDE', 'Klar doch, $N. Ihr seht vertrauenswürdig aus... Vertrauenswürdig! Das sehe ich auf eine Meile Entfernung...$B$BWie auch immer, zurück zum Thema. Man braucht eine Evowurzelpflanze, um das Videre-Elixier herzustellen. Alle anderen Zutaten sind echt trivial, aber die Evowurzel ist nicht so leicht zu beschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4723', 'deDE', 'Schlüssel... holt den Schlüssel... Gerstahn hat... Schlüssel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4724', 'deDE', 'Los, macht Euch nützlich, $R. HOLT MICH HIER RAUS! Der Verhörmeister hat den Schlüssel.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4725', 'deDE', 'HE! HE, IHR DA! $R! Holt mich hier raus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4726', 'deDE', '%s stöhnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4727', 'deDE', 'Na gut, sehen wir uns das Ding da mal an...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4728', 'deDE', 'Oh, verdammt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4729', 'deDE', null, 'Na gut! Dann mal los!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4730', 'deDE', null, '... Uups.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4731', 'deDE', 'Na los doch, dreckiger $R, bringt es hinter Euch.$B$B<Kharan senkt den Kopf und bietet seinen kurzen Hals dar.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4732', 'deDE', 'Ich bin nicht hier, um Euch Leid zuzufügen, Kharan. Gor\'shak schickt mich. Er sagte, Ihr würdet mit mir über die Prinzessin sprechen.', 'Ich bin nicht hier, um Euch Leid zuzufügen, Kharan. Gor\'shak schickt mich. Er sagte, Ihr würdet mit mir über die Prinzessin sprechen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4733', 'deDE', 'Alles ist verloren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4734', 'deDE', 'Es ist nicht alles verloren, Kharan!', 'Es ist nicht alles verloren, Kharan!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4735', 'deDE', 'Sehe ich wie ein Dummkopf aus, $R? Ich könnte ihr Todesurteil auch persönlich unterschreiben! Warum sollte ich Euch trauen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4736', 'deDE', 'Weil Ihr immer noch am Leben seid und meine Hände nicht fest um Euren feisten Hals geklammert sind.', 'Weil Ihr immer noch am Leben seid und meine Hände nicht fest um Euren feisten Hals geklammert sind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4737', 'deDE', '<Kharan seufzt.>$B$BNa ja... Na ja, vielleicht habt Ihr ja Recht.$B$BGor\'shak sagte mir, dass Euer Kriegshäuptling große Pläne hat. Pläne, die die Beziehungen von Allianz und Horde verändern könnten. Was wisst Ihr davon?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4738', 'deDE', 'Nichts. Meine Befehle lauteten, erst mit Euch und dann mit Thrall zu sprechen. Ich weiß nur, dass Thrall Interesse daran hat, Eure Prinzessin zu retten.', 'Nichts. Meine Befehle lauteten, erst mit Euch und dann mit Thrall zu sprechen. Ich weiß nur, dass Thrall Interesse daran hat, Eure Prinzessin zu retten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4739', 'deDE', 'Dann hört gut zu, $R.$B$BVor mehreren Monaten schickte der Magistrat von Seenhain Botschafter nach Eisenschmiede und bat den König um Unterstützung gegen die Blackrockinvasoren, die aus dem Rotkammgebirge und der brennenden Steppe kamen.$B$BGegen meinen Rat bot Prinzessin Moira ihre Hilfe an. Mir wurden zwanzig Elitegardisten zugeteilt, um die Prinzessin auf der gefährlichen Reise vom Loch nach Seenhain zu beschützen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4740', 'deDE', 'Was erklärt, weshalb Ihr in einer Gefängniszelle unter einem Berg sitzt, Zwerg. Richtig?', 'Was erklärt, weshalb Ihr in einer Gefängniszelle unter einem Berg sitzt, Zwerg. Richtig?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4741', 'deDE', '<Kharan räuspert sich.>$B$BWir durchquerten den Tunnel der sengenden Schlucht und gelangten in das verheerte Wüstenland, das die Schlucht bildet. Zurückblickend muss ich sagen, es war verdächtig ruhig. Die einzigen Gefahren unterwegs stellte die Tierwelt der Region dar: Kreaturen, mit denen meine Männer mühelos fertig wurden.$B$BWir gelangten fast ungehindert zum Blackrock und bahnten uns ohne jegliches Problem einen Weg durch das steinerne Bauwerk.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4742', 'deDE', 'Und was ist passiert?', 'Und was ist passiert?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4743', 'deDE', 'Auf dem Marsch durch die brennende Steppe gerieten wir in einen Hinterhalt. Es müssen Hunderte gewesen sein... Dunkeleisenzwerge! Sie kamen aus den Felswänden, aus den Steinen der Steppe; aus jeder erdenklichen Spalte, Nische und Öffnung. Sie überrannten uns und überwältigten die Wachen und die wenigen tapferen Helden, die uns freiwillig begleitet hatten.$B$BEines stand fest: Es war ein minutiös geplanter Hinterhalt. Es war, als hätten sie jede unserer Bewegungen vorausgeahnt, bevor wir sie machten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4744', 'deDE', 'Ihr vermutet also, dass jemand auf Eurer Seite darin verwickelt war? Dass sie einen Tipp bekamen?', 'Ihr vermutet also, dass jemand auf Eurer Seite darin verwickelt war? Dass sie einen Tipp bekamen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4745', 'deDE', 'Wie wäre das sonst möglich gewesen? Jemand muss die Dunkeleisenzwerge informiert haben.$B$B<Kharan schüttelt die Faust unter Eurer Nase.>$B$BHelft mir. Wenn ich jemals hier rauskomme, werde ich den Verräter den Rest meines Lebens jagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4746', 'deDE', 'Fahrt bitte mit Eurer Geschichte fort.', 'Fahrt bitte mit Eurer Geschichte fort.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4747', 'deDE', 'Wo war ich doch gleich? Ach ja, wir wurden also gefangen genommen. Meine Elitegardisten wurden auf der Stelle hingerichtet. Mich verschonten sie... und die Prinzessin brachten sie mit Hilfe eines Widders fort. Ich wurde erst in die Zellen der Schlackengrube und von dort in dieses Gefängnis gebracht.$B$BIch nehme an, ich bin nur noch am Leben, weil sie ein Lösegeld für mein jämmerliches Leben erpressen möchten. Oder aus Grausamkeit, damit ich bis in alle Ewigkeit über die Geschehnisse nachdenken kann. Bedauern ist der grausamste aller Kerkermeister, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4748', 'deDE', 'So ist es.', 'So ist es.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4749', 'deDE', 'Jetzt weilt sie an der Seite von Imperator Thaurissan. Er hat sie mit einem mächtigen, die Gedanken verändernden Zauber belegt... und ich sitze vollkommen hilflos hier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4750', 'deDE', 'Ich werde die Zellentür offen lassen, falls das ein Trost ist. Wie Ihr hinausgelangt, ist Euer Problem. Lebt wohl, Kharan.', 'Ich werde die Zellentür offen lassen, falls das ein Trost ist. Wie Ihr hinausgelangt, ist Euer Problem. Lebt wohl, Kharan.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4751', 'deDE', null, '%s hält die Rune des Entkommens vor sich.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4752', 'deDE', 'Ich bin hier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4753', 'deDE', 'Nur ein Narr würde wagen, mich in meinem Unterschlupf zu stören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4754', 'deDE', 'Ich präsentiere Euch Beweise meiner Taten, Cyrus.', 'Ich präsentiere Euch Beweise meiner Taten, Cyrus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4755', 'deDE', 'Ich habe keine Beweise, Cyrus.', 'Ich habe keine Beweise, Cyrus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4756', 'deDE', '<Cyrus\' Augen leuchten.>$B$BVerschlinge ich Euch mit Haut und Haar oder lasse ich Euch leben?$B$BVielleicht könnt Ihr ja noch nützlich sein...$B$BFordert meinen Diener heraus, $R. Überlebt und bringt mir einen Beweis Eures Sieges.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4757', 'deDE', 'Ein Verteidiger ist gefallen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4758', 'deDE', 'Seid auf der Hut, ihr faulen Esel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4759', 'deDE', 'Ich bin bereit!', 'Ich bin bereit!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4760', 'deDE', 'Grummel, grummel...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4761', 'deDE', 'Sucht nach Yelmaks \'Alchemie und Tränke\'. Ihr findet es mitten in der Gasse, westlich des gewaltigen Baumes.', 'Sucht nach Yelmaks \'Alchemie und Tränke\'. Ihr findet es mitten in der Gasse, westlich des gewaltigen Baumes.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4762', 'deDE', 'Tränke und Öle, darüber lehrt Yelmak Euch etwas, wenn Ihr würdig seid. Sucht sein Geschäft auf, es befindet sich in der Mitte der Gasse.', 'Tränke und Öle, darüber lehrt Yelmak Euch etwas, wenn Ihr würdig seid. Sucht sein Geschäft auf, es befindet sich in der Mitte der Gasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4763', 'deDE', 'Sprecht mit Saru Steelfury beim Brennenden Amboss. Geht zum Tal der Ehre. Es befindet sich am Ende des Pfades rechts vom \'Roter Canyon-Bergbau\'.', 'Sprecht mit Saru Steelfury beim Brennenden Amboss. Geht zum Tal der Ehre. Es befindet sich am Ende des Pfades rechts vom \'Roter Canyon-Bergbau\'.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4764', 'deDE', 'Vielleicht wird Saru Steelfury Euch lehren, wie Ihr eines Tages eine Axt herstellen könnt, die so gut wie meine ist. Ihr findet ihn beim Brennenden Amboss im Tal der Ehre, den Pfad hinab rechts hinter \'Roter Canyon-Bergbau\'.', 'Vielleicht wird Saru Steelfury Euch lehren, wie Ihr eines Tages eine Axt herstellen könnt, die so gut wie meine ist. Ihr findet ihn beim Brennenden Amboss im Tal der Ehre, den Pfad hinab rechts hinter \'Roter Canyon-Bergbau\'.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4765', 'deDE', 'Ihr sucht Zamja in \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Geht die linke Treppe hinauf, wenn Ihr die Gasse vom Tal der Stärke her betretet.', 'Ihr sucht Zamja in \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Geht die linke Treppe hinauf, wenn Ihr die Gasse vom Tal der Stärke her betretet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4766', 'deDE', 'Grummel...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4767', 'deDE', 'Ihr müsst bei Zamja lernen. Geht zu \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Nehmt die Treppe links, wenn Ihr vom Tal der Stärke her hereinkommt.', 'Ihr müsst bei Zamja lernen. Geht zu \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Nehmt die Treppe links, wenn Ihr vom Tal der Stärke her hereinkommt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4768', 'deDE', '%s beschnüffelt den Köder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4769', 'deDE', 'Ihr müsst zu \'Godans Runenarbeiten\' gehen. Sucht am westlichen Ende der Gasse danach, gleich neben dem Tal der Weisheit.', 'Ihr müsst zu \'Godans Runenarbeiten\' gehen. Sucht am westlichen Ende der Gasse danach, gleich neben dem Tal der Weisheit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4770', 'deDE', 'Könnt Ihr meine Axt verzaubern? Ich will einen dauerhaften Zauber. Na gut, dann vielleicht später. Sucht nach \'Godans Runenarbeiten\' am westlichen Ende der Gasse nahe dem Tal der Weisheit.', 'Könnt Ihr meine Axt verzaubern? Ich will einen dauerhaften Zauber. Na gut, dann vielleicht später. Sucht nach \'Godans Runenarbeiten\' am westlichen Ende der Gasse nahe dem Tal der Weisheit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4771', 'deDE', '%s nagt ausgehungert an dem Köder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4772', 'deDE', 'Arnok macht gute Verbände. Ihr findet ihn in der Hütte \'Der Stärkste überlebt\' am Ufer des Sees neben der Geisterlodge im Tal der Geister. Das ist westlich über dem Tal der Stärke.', 'Arnok macht gute Verbände. Ihr findet ihn in der Hütte \'Der Stärkste überlebt\' am Ufer des Sees neben der Geisterlodge im Tal der Geister. Das ist westlich über dem Tal der Stärke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4773', 'deDE', 'Ich schätze, jemand muss uns Krieger nach den Kämpfen verbinden. Sucht Arnok in der \'Der Stärkste überlebt\'-Hütte neben der Geisterlodge im Tal der Geister auf. Haltet westlich über dem Tal der Stärke nach einem See Ausschau. Da müsste er zu finden sein.', 'Ich schätze, jemand muss uns Krieger nach den Kämpfen verbinden. Sucht Arnok in der \'Der Stärkste überlebt\'-Hütte neben der Geisterlodge im Tal der Geister auf. Haltet westlich über dem Tal der Stärke nach einem See Ausschau. Da müsste er zu finden sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4774', 'deDE', 'Dieser Lumak... ständig angelt er, statt zu kämpfen. Ihr findet ihn auf seinem Dock am See im Tal der Ehre. Und grüßt Shankys von mir, wenn Ihr schon mal da seid.', 'Dieser Lumak... ständig angelt er, statt zu kämpfen. Ihr findet ihn auf seinem Dock am See im Tal der Ehre. Und grüßt Shankys von mir, wenn Ihr schon mal da seid.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4775', 'deDE', 'Angeln ist ein Sport für die Alten, die nicht im Kampf gefallen sind! Sucht Lumak am See im Tal der Ehre auf.', 'Angeln ist ein Sport für die Alten, die nicht im Kampf gefallen sind! Sucht Lumak am See im Tal der Ehre auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4776', 'deDE', 'Kräuter... Ihr müsst mit Jandi in ihrem Arboretum in der Gasse sprechen. Hinter \'Asorans Markt\' im Tal der Weisheit gibt es eine Holztreppe. Geht zur oberen Etage der Gasse hinauf, da findet Ihr sie.', 'Kräuter... Ihr müsst mit Jandi in ihrem Arboretum in der Gasse sprechen. Hinter \'Asorans Markt\' im Tal der Weisheit gibt es eine Holztreppe. Geht zur oberen Etage der Gasse hinauf, da findet Ihr sie.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4777', 'deDE', 'Ja, Mann, da müsst Ihr zu Jandis Arboretum. Hab ich das gerade gesagt? Glok-Tar, ich muss müde sein! Folgt der Holztreppe hinter \'Asorans Markt\' im Tal der Weisheit in die Gasse hinauf. Ihr findet Jandi auf der oberen Etage.', 'Ja, Mann, da müsst Ihr zu Jandis Arboretum. Hab ich das gerade gesagt? Glok-Tar, ich muss müde sein! Folgt der Holztreppe hinter \'Asorans Markt\' im Tal der Weisheit in die Gasse hinauf. Ihr findet Jandi auf der oberen Etage.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4778', 'deDE', 'Sucht die \'Kodobalg-Lederer\' in der Gasse auf. Fragt nach Kamari.', 'Sucht die \'Kodobalg-Lederer\' in der Gasse auf. Fragt nach Kamari.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4779', 'deDE', 'Da müsst Ihr wohl zu \'Kodobalg-Lederer\'. Die findet Ihr in der Mitte der Gasse.', 'Da müsst Ihr wohl zu \'Kodobalg-Lederer\'. Die findet Ihr in der Mitte der Gasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4780', 'deDE', 'Sucht Zando\'zan in der Kluft der Schatten auf. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer in der Gasse.', 'Sucht Zando\'zan in der Kluft der Schatten auf. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer in der Gasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4781', 'deDE', 'Ihr habt eine Schatztruhe? Lasst mich sehen! Na gut, sucht Zando\'zan in der Kluft der Schatten auf. Sucht den Tunnel, der in die Gasse hinabführt, dann findet Ihr ihn.', 'Ihr habt eine Schatztruhe? Lasst mich sehen! Na gut, sucht Zando\'zan in der Kluft der Schatten auf. Sucht den Tunnel, der in die Gasse hinabführt, dann findet Ihr ihn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4782', 'deDE', 'Sucht den \'Roter Canyon-Bergbau\' nordöstlich des Sees im Tal der Ehre auf. Sprecht dort mit Makaru.', 'Sucht den \'Roter Canyon-Bergbau\' nordöstlich des Sees im Tal der Ehre auf. Sprecht dort mit Makaru.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4783', 'deDE', 'Makaru ist Tag und Nacht beim \'Roter Canyon-Bergbau\' im Tal der Ehre zu finden, direkt am See.', 'Makaru ist Tag und Nacht beim \'Roter Canyon-Bergbau\' im Tal der Ehre zu finden, direkt am See.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4784', 'deDE', 'Thuwd zeigt Euch, wie Ihr Eure getöteten Tiere häutet. Ihr findet ihn bei \'Kodobalg-Lederer\' in der Gasse.', 'Thuwd zeigt Euch, wie Ihr Eure getöteten Tiere häutet. Ihr findet ihn bei \'Kodobalg-Lederer\' in der Gasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4785', 'deDE', 'Es ist gut, dass Ihr die getöteten Tiere ehrt, indem Ihr ihre Kadaver sinnvoll verwendet. Sucht Thuwd bei \'Kodobalg-Lederer\' in der Mitte der Gasse auf.', 'Es ist gut, dass Ihr die getöteten Tiere ehrt, indem Ihr ihre Kadaver sinnvoll verwendet. Sucht Thuwd bei \'Kodobalg-Lederer\' in der Mitte der Gasse auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4786', 'deDE', 'Ich bevorzuge mein Essen roh, aber wenn Ihr Eures gekocht haben wollt, kann Denni\'ka Euch zeigen, wie man ein Feuer entfacht. Ihr findet sie in \'Der Stärkste überlebt\', einer Hütte am Ufer des Sees neben der Geisterlodge. Sucht westlich über dem Tal der Stärke danach.', 'Ich bevorzuge mein Essen roh, aber wenn Ihr Eures gekocht haben wollt, kann Denni\'ka Euch zeigen, wie man ein Feuer entfacht. Ihr findet sie in \'Der Stärkste überlebt\', einer Hütte am Ufer des Sees neben der Geisterlodge. Sucht westlich über dem Tal der Stärke danach.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4787', 'deDE', 'Denni\'ka kann Euch zeigen, wie man sich warm hält. Ihr findet sie in \'Der Stärkste überlebt\', einer Hütte am Ufer des Sees neben der Geisterlodge. Das ist oben, westlich vom Tal der Stärke.', 'Denni\'ka kann Euch zeigen, wie man sich warm hält. Ihr findet sie in \'Der Stärkste überlebt\', einer Hütte am Ufer des Sees neben der Geisterlodge. Das ist oben, westlich vom Tal der Stärke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4788', 'deDE', 'Magars Tuchwaren\' liegt in der Gasse. Ihr findet das südlich des gewaltigen Baums neben \'Kodobalg-Lederer\'.', 'Magars Tuchwaren\' liegt in der Gasse. Ihr findet das südlich des gewaltigen Baums neben \'Kodobalg-Lederer\'.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4789', 'deDE', 'Kleidung!? Die nützt Euch nichts gegen guten Stahl! Pah, wenn Ihr wirklich das Schneiderhandwerk erlernen möchtet, sucht nach \'Magars Tuchwaren\' in der Gasse.', 'Kleidung!? Die nützt Euch nichts gegen guten Stahl! Pah, wenn Ihr wirklich das Schneiderhandwerk erlernen möchtet, sucht nach \'Magars Tuchwaren\' in der Gasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4790', 'deDE', 'Spürt Shim\'la beim südlichen Eingang der Jägerhalle im Tal der Ehre auf.', 'Spürt Shim\'la beim südlichen Eingang der Jägerhalle im Tal der Ehre auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4791', 'deDE', 'Was ein Glück, dass hier jemand Bescheid weiß! Sucht nach Shim\'la in der Jägerhalle im Tal der Ehre.', 'Was ein Glück, dass hier jemand Bescheid weiß! Sucht nach Shim\'la in der Jägerhalle im Tal der Ehre.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4792', 'deDE', 'Wir haben unsere Verpflichtungen, $C. Verpflichtungen gegenüber der Horde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4793', 'deDE', 'Wo ist Kriegsherr Krom\'zar?', 'Wo ist Kriegsherr Krom\'zar?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4794', 'deDE', 'Ich glaube, ich höre ihn kommen! Schaut nach Westen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4795', 'deDE', 'Die Bank von Orgrimmar liegt im Tal der Stärke. Sucht nördlich des Haupteingangs zur Stadt danach.', 'Die Bank von Orgrimmar liegt im Tal der Stärke. Sucht nördlich des Haupteingangs zur Stadt danach.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4796', 'deDE', 'Geht zum Tal der Stärke und sucht die Bank von Orgrimmar südlich des Himmelsturms auf.', 'Geht zum Tal der Stärke und sucht die Bank von Orgrimmar südlich des Himmelsturms auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4797', 'deDE', 'Sucht nach dem Windreitermeister auf der Spitze des Himmelsturms im Tal der Stärke. Doras wird dafür sorgen, dass Ihr den Weg zu dem Kampf findet, den Ihr sucht.', 'Sucht nach dem Windreitermeister auf der Spitze des Himmelsturms im Tal der Stärke. Doras wird dafür sorgen, dass Ihr den Weg zu dem Kampf findet, den Ihr sucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4798', 'deDE', 'Was dachtet Ihr denn, wo der Windreitermeister sein würde? Geht zur Spitze des Himmelsturms im Tal der Stärke!', 'Was dachtet Ihr denn, wo der Windreitermeister sein würde? Geht zur Spitze des Himmelsturms im Tal der Stärke!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4799', 'deDE', 'Ihr sucht Urtrun Klanbringer in der Botschaft der Horde. Die findet Ihr an der Westseite des Tals der Kraft.', 'Ihr sucht Urtrun Clanbringer in der Botschaft der Horde. Die findet Ihr an der Westseite des Tals der Stärke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4800', 'deDE', 'Die Botschaft der Horde liegt im Tal der Stärke auf der Westseite.', 'Die Botschaft der Horde liegt im Tal der Stärke auf der Westseite.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4801', 'deDE', 'Das ist das zweite Gebäude rechts im Tal der Stärke, nördlich des Haupteingangs der Stadt. Sprecht mit Gastwirtin Gryshka.', 'Das ist das zweite Gebäude rechts im Tal der Stärke, nördlich des Haupteingangs der Stadt. Sprecht mit Gastwirtin Gryshka.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4802', 'deDE', 'Sucht nach der Gastwirtin Gryshka im Tal der Stärke. Ihr Gasthaus liegt auf der Ostseite, nördlich des Hauptstadttores.', 'Sucht nach der Gastwirtin Gryshka im Tal der Stärke. Ihr Gasthaus liegt auf der Ostseite, nördlich des Hauptstadttores.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4803', 'deDE', 'So etwas findet Ihr vor der Bank von Orgrimmar, inmitten des Tals der Stärke.', 'So etwas findet Ihr vor der Bank von Orgrimmar, inmitten des Tals der Stärke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4804', 'deDE', 'Ihr könnt Eure Liebesbriefe vor der Bank in der Mitte des Tals der Kraft abholen.', 'Ihr könnt Eure Liebesbriefe vor der Bank in der Mitte des Tals der Kraft abholen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4805', 'deDE', 'Geht zur Jägerhalle im Tal der Ehre, westlich des Kolosseums. Sprecht dort mit Ormak Grimshot.', 'Geht zur Jägerhalle im Tal der Ehre, westlich des Kolosseums. Sprecht dort mit Ormak Grimshot.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4806', 'deDE', 'Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.', 'Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4807', 'deDE', 'Sprecht mit Pephredo in der Dunkeldornen-Lodge im Tal der Geister, westlich über dem Tal der Stärke.', 'Sprecht mit Pephredo in der Dunkeldornen-Lodge im Tal der Geister, westlich über dem Tal der Stärke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4808', 'deDE', 'Was, Ihr könnt nicht dorthin teleportieren? Geht zur Dunkeldornen-Lodge im Tal der Geister am See, westlich und über dem Tal der Stärke.', 'Was, Ihr könnt nicht dorthin teleportieren? Geht zur Dunkeldornen-Lodge im Tal der Geister am See, westlich und über dem Tal der Stärke.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4809', 'deDE', 'Zayus und seine Priester sind in der Geisterlodge, und die befindet sich im Tal der Geister. Sucht am See westlich und über dem Tal der Stärke danach.', 'Zayus und seine Priester sind in der Geisterlodge, und die befindet sich im Tal der Geister. Sucht am See westlich und über dem Tal der Stärke danach.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4810', 'deDE', 'Heilung ist für jene, die keinen Schmerz ertragen können. Aber wenn es denn unbedingt sein muss, dann geht zur Geisterlodge am See im Tal der Geister, über dem Tal der Stärke im Westen.', 'Heilung ist für jene, die keinen Schmerz ertragen können. Aber wenn es denn unbedingt sein muss, dann geht zur Geisterlodge am See im Tal der Geister, über dem Tal der Stärke im Westen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4811', 'deDE', 'Ihr sucht Kardris Dreamseeker. Sie und die anderen Schamanen sind im Eingangsraum von Thralls Festung im Tal der Weisheit zu finden.', 'Ihr sucht Kardris Dreamseeker. Sie und die anderen Schamanen sind im Eingangsraum von Thralls Festung im Tal der Weisheit zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4812', 'deDE', 'Eure Suche ist fast abgeschlossen. Sucht Eure Wahrheit im Eingangsraum von Thralls Festung im Tal der Weisheit.', 'Eure Suche ist fast abgeschlossen. Sucht Eure Wahrheit im Eingangsraum von Thralls Festung im Tal der Weisheit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4813', 'deDE', 'Ihr sucht nach Shenthul von der Shadowswift-Bruderschaft, hm? Sucht in der Gasse gegenüber von \'Droffers und Sohn Bergungen\' nach einem Tunnel, der in die Kluft der Schatten hinabführt.', 'Ihr sucht nach Shenthul von der Shadowswift-Bruderschaft, hm? Sucht in der Gasse gegenüber von \'Droffers und Sohn Bergungen\' nach einem Tunnel, der in die Kluft der Schatten hinabführt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4814', 'deDE', 'Lasst Euch nicht dabei erwischen, wie Ihr etwas stehlt! Sucht nach Shentul und seiner Shadowswift-Bruderschaft in der Kluft der Schatten. Ihr findet den Tunnel, der dort hinabführt, im Süden des Tals der Weisheit.', 'Lasst Euch nicht dabei erwischen, wie Ihr etwas stehlt! Sucht nach Shentul und seiner Shadowswift-Bruderschaft in der Kluft der Schatten. Ihr findet den Tunnel, der dort hinabführt, im Süden des Tals der Weisheit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4815', 'deDE', 'Ihr sucht Grol\'dar und die Darkfire-Enklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.', 'Ihr sucht Grol\'dar und die Darkfire-Enklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4816', 'deDE', 'Haben wir uns nicht gerade erst von der Brennenden Legion befreit, und schon lasst Ihr Euch wieder mit Dämonen ein? Die Darkfire-Enklave ist in der unterirdischen Höhle namens Kluft der Schatten zu finden. Von der Gasse aus führt ein Tunnel hinunter.', 'Haben wir uns nicht gerade erst von der Brennenden Legion befreit, und schon lasst Ihr Euch wieder mit Dämonen ein? Die Darkfire-Enklave ist in der unterirdischen Höhle namens Kluft der Schatten zu finden. Von der Gasse aus führt ein Tunnel hinunter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4817', 'deDE', 'Sprecht mit Grezz Ragefist in der Halle der Kriegerhelden. Sie befindet sich im Tal der Ehre. Legt ein gutes Wort für mich ein!', 'Sprecht mit Grezz Ragefist in der Halle der Kriegerhelden. Sie befindet sich im Tal der Ehre. Legt ein gutes Wort für mich ein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4818', 'deDE', 'Sucht die Halle der Kriegerhelden im Tal der Ehre auf und sprecht mit Grezz Ragefist. Lasst die Leute wissen, dass ich mich nach meiner Schicht hier bei ihnen melde.', 'Sucht die Halle der Kriegerhelden im Tal der Ehre auf und sprecht mit Grezz Ragefist. Lasst die Leute wissen, dass ich mich nach meiner Schicht hier bei ihnen melde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4819', 'deDE', 'Habt Ihr Euch verlaufen?', 'Habt Ihr Euch verlaufen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4820', 'deDE', 'Was wollt Ihr?', 'Was wollt Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4821', 'deDE', 'Was!?', 'Was!?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4822', 'deDE', 'Lok-Tar Ogar!', 'Lok-Tar Ogar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4823', 'deDE', 'Könnt Ihr mir wohl die Richtung weisen?', 'Könnt Ihr mir wohl die Richtung weisen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4824', 'deDE', 'Bitte lasst einen alten Geist in Frieden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4825', 'deDE', 'Nur sehr wenige suchen nach mir... Was kann ich für Euch tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4826', 'deDE', 'Ich habe Euch mein ganzes Wissen gegeben, $N. Möge das Glück mit Euch sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4827', 'deDE', 'Wie kann ich Evowurzel bekommen?', 'Wie kann ich Evowurzel bekommen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4828', 'deDE', 'Hi, hi... Ihr seid recht neugierig, was, $N? Also, das müsst Ihr tun:$B$BNördlich von hier, in den alten Ruinen, lebt ein höchst übellauniger Gnoll namens Miblon Fletschmaul. Er bewacht die einzige Evowurzelpflanze, die ich kenne, und das tut er Tag und Nacht! Er scheint niemals zu schlafen!$B$BAllerdings könnt Ihr eines tun: ihn bestechen. Ich kann Euch Köder verkaufen, die er lecker finden wird - legt sie einfach in seiner Nähe hin, das lenkt ihn lange genug ab, dass Ihr Euch die Evowurzel schnappen könnt. Viel Glück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4829', 'deDE', 'Trotzt mir, wenn Ihr es wagt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4830', 'deDE', 'Vorfalk', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4831', 'deDE', 'Bethek', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4832', 'deDE', 'Uggel', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4833', 'deDE', 'Zimrel', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4834', 'deDE', 'Ofgut', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4835', 'deDE', 'Pelver', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4836', 'deDE', '%s lässt von dem Block ab, den er in der Tür hochgehalten hat, und schaut zu dem Köder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4837', 'deDE', '%s klopft sich auf die Brust und ruft um Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4838', 'deDE', '%s explodiert zu Klumpen ätzenden Schleims!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4839', 'deDE', null, 'Ich weiß, diesmal klappt es...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4840', 'deDE', null, 'Das wird mein bislang bestes Experiment!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4841', 'deDE', null, 'So, wenn ich jetzt die Gelben hinzufüge...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4842', 'deDE', null, 'Ich weiß, diesmal klappt es... Meine letzten 13 Versuche dienten nur der Übung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4843', 'deDE', null, 'Ahhh... meine Experimente scheinen nie richtig zu klappen. Vielleicht sollte ich wieder abwärts gehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4844', 'deDE', 'Ich möchte gern noch mehr Tharlendrissamenkörner kaufen.', 'Ich möchte gern noch mehr Tharlendrissamenkörner kaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4845', 'deDE', 'Ich möchte gern noch mehr Tharlendrissamenkörner kaufen.', 'Ich möchte gern noch mehr Tharlendrissamenkörner kaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4846', 'deDE', null, '%s kombiniert verschiedenfarbige Kristalle miteinander.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4847', 'deDE', null, 'Ich glaube... Ich glaube, es funktioniert!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4848', 'deDE', 'Ich bin kein Kräuterkundegeselle - kann ich Euch dennoch bei Eurer Arbeit behilflich sein?', 'Ich bin kein Kräuterkundegeselle - kann ich Euch dennoch bei Eurer Arbeit behilflich sein?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4849', 'deDE', 'Ja, aber Ihr werdet feststellen, dass es sehr viel lohnender ist, wenn Ihr auch einige Fertigkeiten in der Kräuterkunde habt! Damit könnt Ihr besondere Samenkörner anbauen und aufziehen. Mit dem Immergrünbeutel, den Ihr erhaltet, kann zwar selbst ein absoluter Neuling etwas wachsen lassen, aber alle, die über ein bisschen Wissen in der Kräuterkunde verfügen, werden eine reichlichere Ernte erzielen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4850', 'deDE', 'Seid gegrüßt, $C. Was kann dieser bescheidene Diener der Festung Feathermoon heute für Euch tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4851', 'deDE', 'Ich möchte gern noch mehr Tharlendrissamenkörner kaufen.', 'Ich möchte gern noch mehr Tharlendrissamenkörner kaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4852', 'deDE', null, 'AU! Da stimmt was nicht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4853', 'deDE', null, 'Na ja... Vielleicht beim nächsten Mal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4854', 'deDE', 'Steht gerade! Eure Schwäche macht mich krank!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4855', 'deDE', 'Seid auf der Hut! Die Orcs und ihre Verbündeten sind ganz in der Nähe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4856', 'deDE', 'Ihr müsst gefährlich wirken! Ihr seid ein Kolkar, also benehmt Euch auch so!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4857', 'deDE', 'Ihr sucht doch etwas. Was ist es?', 'Ihr sucht doch etwas. Was ist es?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4858', 'deDE', 'Welcher Beruf?', 'Welcher Beruf?', '31882');
