INSERT INTO `broadcast_text_locale` VALUES ('4872', 'deDE', 'Fährtenlesen', 'Fährtenlesen', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4873', 'deDE', null, 'Der Tod unseres Fleisches bedeutete die Geburt unseres Geistes und unserer heiligen Aufgabe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4874', 'deDE', 'Ich bin einer der Sieben und wir sind dazu verdammt, Wache zu stehen. Auf ewig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4875', 'deDE', 'Unser Anführer Un\'rel hat die Herausforderung gehört.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4876', 'deDE', 'Die Berührung des Steins ist mir für immer verwehrt. Nie wieder werde ich die Pracht geschmiedeten Eisens spüren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4877', 'deDE', null, 'Ihr dürft nicht vorbei. Ihr seid weder von unserer Art noch habt Ihr eine Herausforderung ausgesprochen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4878', 'deDE', 'Welchen Ausbilder sucht Ihr?', 'Welchen Ausbilder sucht Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4879', 'deDE', 'Unser Schicksal ist das Verhängnis aller, die dem großen Feuer trotzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4880', 'deDE', 'Jäger', 'Jäger', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4881', 'deDE', 'Unsere Strafe ist auch unser Segen.', null, '31882');
