INSERT INTO `broadcast_text_locale` VALUES ('4888', 'deDE', 'Die Bank', 'Die Bank', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4889', 'deDE', 'Der Windreitermeister', 'Der Windreitermeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4890', 'deDE', 'Der Gildenmeister', 'Der Gildenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4891', 'deDE', 'Ein Lehrer für eine Klasse', 'Ein Lehrer für eine Klasse', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4892', 'deDE', 'Eure Knechtschaft hat ein Ende, Un\'rel. Ich fordere Euch heraus!', 'Eure Knechtschaft hat ein Ende, Un\'rel. Ich fordere Euch heraus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4893', 'deDE', 'Ein Gasthaus', 'Ein Gasthaus', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4894', 'deDE', 'Ihr habt die Sieben herausgefordert; jetzt werdet Ihr sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4895', 'deDE', 'Ein Briefkasten', 'Ein Briefkasten', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4896', 'deDE', 'Ein Lehrer für einen Beruf', 'Ein Lehrer für einen Beruf', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4897', 'deDE', 'Dunk\'rel, gebt Eure Geheimnisse preis!', 'Dunk\'rel, gebt Eure Geheimnisse preis!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4898', 'deDE', 'Ihr möchtet das alte Handwerk erlernen? Ihr wollt dunkles Eisen verhütten?$B$BStimmt mich milde, $R. Zeigt mir ein Opfer und ich denke darüber nach!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4899', 'deDE', 'Euer Wille ist stark, Eure Absicht eindeutig.$B$BVielleicht seid Ihr würdig...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4900', 'deDE', 'Ich habe Euren Preis bezahlt, Dunk\'rel. Jetzt lehrt mich Eure Geheimnisse!', 'Ich habe Euren Preis bezahlt, Dunk\'rel. Jetzt lehrt mich Eure Geheimnisse!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4901', 'deDE', 'Ihr habt mir Euer Begehren gezeigt und mit kostbarem Stein bezahlt. Ich werde Euch unterrichten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4902', 'deDE', 'Eindringlinge! Greift die Eindringlinge an!', 'Eindringlinge! Greift die Eindringlinge an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4903', 'deDE', 'Ich kenne den Weg, Insekt. Mich anzutreiben wie ein Stück Vieh, das ist wahrlich nicht nötig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4904', 'deDE', 'Ihr glaubt doch nicht wirklich, dass Ihr damit ungestraft davonkommt. Sie werden mich befreien und Ihr werdet für Eure Dreistigkeit bezahlen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4905', 'deDE', 'MACHT SIE NIEDER, BRÜDER!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4906', 'deDE', 'Wenn Ihr das nächste Mal auf meine Brüder trefft, werdet Ihr nicht solches Glück haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4907', 'deDE', 'Sie werden Euch erwischen, Dummkopf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4908', 'deDE', 'Was wollt Ihr damit eigentlich erreichen, Dummkopf? In diesem Moment bereiten sich die Armeen des Blackrock bereits darauf vor, Eure Welt zu vernichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4909', 'deDE', 'Auf dunkelsten Schwingen fliegen sie. Bereitet Euch auf Euer Ende vor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4910', 'deDE', 'Ihr werdet unser hochheiliges Land nicht entweihen!', 'Ihr werdet unser hochheiliges Land nicht entweihen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4911', 'deDE', 'Das Schlimmste kommt erst noch, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4912', 'deDE', '%s lacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4913', 'deDE', 'Zeit für Euer letztes Gefecht, Insekt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4914', 'deDE', 'Die Kolkar werden herrschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4915', 'deDE', 'Eure Horde bedeutet den Kolkar nichts!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4916', 'deDE', 'Schlottert vor Furcht, Hunde, die ihr seid! Ich bin hier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4917', 'deDE', 'Die Kolkar sind die Stärksten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4918', 'deDE', '%s unterwirft sich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4919', 'deDE', 'Ihr Zweibeiner seid nichts! Meine Kinder könnten Euch besiegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4920', 'deDE', 'Ich bin hier! Eure Herrschaft über dieses Land ist vorbei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4921', 'deDE', 'Für die Horde!', 'Für die Horde!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4922', 'deDE', 'Verteidigt die Bunker!', 'Verteidigt die Bunker!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4923', 'deDE', 'Die Eindringlinge sind hier!', 'Die Eindringlinge sind hier!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4924', 'deDE', 'Vorsicht! Die Kolkar greifen an!', 'Vorsicht! Die Kolkar greifen an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4925', 'deDE', 'Unsere Gegner werden fallen!', 'Unsere Gegner werden fallen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4926', 'deDE', 'Vorsicht, $N! Schaut nach Westen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4927', 'deDE', 'Ihr seid zum Spielen gekommen? Dann lasst uns spielen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4928', 'deDE', 'Auf die Knie, Grark.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4929', 'deDE', 'Grark Lorkrub, Ihr seid des Hochverrats an der Horde angeklagt und für schuldig befunden worden. Wie Ihr plädiert ist unwichtig. Oberhenker Nuzrak, tretet vor.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4930', 'deDE', '%s hebt seine riesige Axt über Grark.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4931', 'deDE', 'Macht ihm ein Ende...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4932', 'deDE', '%s hebt die Hand und lässt sie wieder sinken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4933', 'deDE', 'Ihr da, Soldat, macht Kargath unverzüglich Meldung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4934', 'deDE', 'Weg von den Fässern da!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4935', 'deDE', 'Ich... Ich weiß nicht so genau, wie ich hierher gekommen bin...$B$BIch erinnere mich schwach an eine Reise... ein Floß...$B$B', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4936', 'deDE', 'Dafür werdet Ihr bezahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4937', 'deDE', 'Bezahlt mich und ich werde reden...$B$BOh, Ihr seid es, $N! Entschuldigt bitte...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4938', 'deDE', 'Kommt Euch mein Name bekannt vor? Vielleicht kannten wir einander vor langer Zeit...$B$BIch glaube, ich wurde nach jemand Wichtigem benannt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4939', 'deDE', 'Hallo, Ihr da! Schön, dass Ihr zurückgekommen seid, $N. Sagt, habt Ihr die erste Aufgabe abgeschlossen, die ich Euch gestellt habe? Wenn ja, habe ich möglicherweise eine zweite für Euch...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4940', 'deDE', 'Als ich nach meiner Bruchlandung hier durch den Krater lief, fiel mir etwas ein, das man mir einst erzählt hatte... \"Geh hinauf, den Berg hinauf\", sagte die Stimme in meinem Kopf.$B$BIch hörte auf sie und so kam ich hierher, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4941', 'deDE', 'Vor langer Zeit traf ich auf meinen Reisen einen Mann, der zu mir sagte: \"Meistere seine Benutzung, dann kannst du dies haben.\"$B$BWas, meint Ihr, soll das heißen? Ich wusste es damals nicht und weiß es auch heute nicht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4942', 'deDE', 'Es ist für alle ein Rätsel... mich eingeschlossen!$B$BSchön, dass Ihr mich gefunden habt - zum Glück verstecke ich mich nicht im Gebüsch oder unter einem Stein, was?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4943', 'deDE', 'Danke, $N! Ihr seid der Held des Kraters von Un\'Goro!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4944', 'deDE', 'Ragnaros wird unangefochten herrschen!', 'Ragnaros wird unangefochten herrschen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4945', 'deDE', 'Trinkt einen Schluck, $C. Er könnte Eure letzter sein!', 'Trinkt einen Schluck, $C. Er könnte Eure letzter sein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4946', 'deDE', 'Ohne den \'Grimmigen Säufer\' würde ich in meiner Freizeit Großdrachenwelpen die Flügel ausreißen.', 'Ohne den \'Grimmigen Säufer\' würde ich in meiner Freizeit Großdrachenwelpen die Flügel ausreißen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4947', 'deDE', 'Ohne den Grimmigen Säufer würde ich in meiner Freizeit vermutlich Großdrachenwelpen die Flügel ausreißen.', 'Ohne den Grimmigen Säufer würde ich in meiner Freizeit vermutlich Großdrachenwelpen die Flügel ausreißen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4948', 'deDE', 'Die Wasserelementare führen Krieg gegen die Feuerelementare, und damit also auch in gewisser Weise gegen uns. Hydraxis, einer ihrer Anführer, rekrutiert Agenten für seine Sache...', 'Die Wasserelementare führen Krieg gegen die Feuerelementare, und damit also auch in gewisser Weise gegen uns. Hydraxis, einer ihrer Anführer, rekrutiert Agenten für seine Sache...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4949', 'deDE', 'Oh Mann, ich dachte wirklich, dass das mein sicheres Ende gewesen sei. Ugh... bin noch ganz benommen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4950', 'deDE', 'Ich hörte, dass Hydraxis, ein Adliger unter den Wasserelementaren, Krieg gegen die Diener des Feuers führt. Hoffen wir, dass er ihn verliert!', 'Ich hörte, dass Hydraxis, ein Adliger unter den Wasserelementaren, Krieg gegen die Diener des Feuers führt. Hoffen wir, dass er ihn verliert!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4951', 'deDE', 'Der Fürst der Wasserlords, Hydraxis, versteckt sich in Azshara, auf einer Insel in der Bucht der Stürme. Was für eine Bedrohung kann er von dort aus wohl zusammenziehen?', 'Der Fürst der Wasserlords, Hydraxis, versteckt sich in Azshara, auf einer Insel in der Bucht der Stürme. Was für eine Bedrohung kann er von dort aus wohl zusammenziehen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4952', 'deDE', 'Mir gefällt Eure Denkweise! Lasst uns von hier verschwinden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4953', 'deDE', 'Falls Hydraxis in diesem Krieg noch weiter rekrutiert, könnte sich das Machtverhältnis ändern...', 'Falls Hydraxis in diesem Krieg noch weiter rekrutiert, könnte sich das Machtverhältnis ändern...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4954', 'deDE', 'Es geht nur um Waffen und Flammen, bis jemand verliert und stirbt.', 'Es geht nur um Waffen und Flammen, bis jemand verliert und stirbt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4955', 'deDE', 'Na gut, von hier aus schaffe ich es allein. Ich weiß zwar nicht, wer Ihr seid... aber ich schulde Euch was. Danke!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4956', 'deDE', 'Mein Vater sagte mir, dass solche Tage kommen würden, da ich fremden Leuten begegnen würde und mich hüten müsste, sie zu töten.', 'Mein Vater sagte mir, dass solche Tage kommen würden, da ich fremden Leuten begegnen würde und mich hüten müsste, sie zu töten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4957', 'deDE', 'Willkommen im Camp Mojache, oh $C. Wie kann ich Euch heute dienen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4958', 'deDE', 'Seid gegrüßt, $C... Wenn Ihr nach Lederwaren und Lederverarbeitungsbedarf sucht, seid Ihr zum richtigen halb pensionierten Paladin gekommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4959', 'deDE', 'Ihr seht aus, als würdet Ihr eine Überfahrt über das große Meer brauchen. Tja, da seid Ihr hier genau richtig, dies ist die Anlegestelle der LAUNISCHEN MINNA, des besten Passagierschiffs in diesen Gewässern. Wenn es einläuft, dann geht einfach an Bord, und im Handumdrehen befindet Ihr Euch an den Sandstränden des Schlingendorntals.', 'Ihr seht aus, als würdet Ihr eine Überfahrt über das große Meer brauchen. Tja, da seid Ihr hier genau richtig, dies ist die Anlegestelle der LAUNISCHEN MINNA, des besten Passagierschiffs in diesen Gewässern. Wenn es einläuft, dann geht einfach an Bord, und im Handumdrehen befindet Ihr Euch an den Sandstränden des Schlingendorntals.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4960', 'deDE', 'Ich muss sagen, in Booty Bay gibt es die beste Muschelsuppe der ganzen südlichen Meere. Oh, Ihr interessiert Euch nicht für Cuisine, eh? Dann lasst mich Euch als Ersten an der Anlegestelle der LAUNISCHEN MINNA begrüßen, des besten Passagierschiffs, das zwischen den östlichen Königreichen und Kalimdor verkehrt. Es sollte bald für eine weitere Reise nach Ratchet hier einlaufen, also macht es Euch bis zu seiner Ankunft bequem.', 'Ich muss sagen, in Booty Bay gibt es die beste Muschelsuppe der ganzen südlichen Meere. Oh, Ihr interessiert Euch nicht für Cuisine, eh? Dann lasst mich Euch als Ersten an der Anlegestelle der LAUNISCHEN MINNA begrüßen, des besten Passagierschiffs, das zwischen den östlichen Königreichen und Kalimdor verkehrt. Es sollte bald für eine weitere Reise nach Ratchet hier einlaufen, also macht es Euch bis zu seiner Ankunft bequem.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4961', 'deDE', 'Eindringlinge!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4962', 'deDE', 'Was macht Ihr da bloß? Eindringlinge!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4963', 'deDE', 'Natürlich besteht keine Gefahr, dass alles Feuer fängt und explodiert wie eine riesige Heliumbombe. Dieses Schmuckstück reist schneller und sicherer als jedes Boot nach Unterstadt und die Aussicht ist wahrhaft atemberaubend. Und da wir gerade vom Atmen sprechen: Rauchen ist an Bord des Zeppelins nicht gestattet, und während der Reise dürfen auch keine Feuerzauber gewirkt werden.', 'Natürlich besteht keine Gefahr, dass alles Feuer fängt und explodiert wie eine riesige Heliumbombe. Dieses Schmuckstück reist schneller und sicherer als jedes Boot nach Unterstadt und die Aussicht ist wahrhaft atemberaubend. Und da wir gerade vom Atmen sprechen: Rauchen ist an Bord des Zeppelins nicht gestattet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4964', 'deDE', 'Hallo! Ihr seid genau die Person, nach der ich gesucht habe. Setzt Euch und hört zu, denn ich habe einen Plan, der uns beide reich machen wird!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4965', 'deDE', 'Mein Bruder sagte, wir könnten viel Geld mit dieser Handelsroute machen, aber ich wusste nicht, dass ich auf dieser Seite leben würde. Wenn Ihr mit dem Zeppelin wieder hier vorbeikommt, könntet Ihr mir da wohl etwas zu essen mitbringen? In Orgrimmar verkaufen sie das beste Fleisch, das man sich nur vorstellen kann, und ich könnte wirklich etwas vertragen, das nicht schon Wochen, bevor es sich zu bewegen aufhörte, gestorben ist.', 'Mein Bruder sagte, wir könnten viel Geld mit dieser Handelsroute machen, aber ich wusste nicht, dass ich auf dieser Seite leben würde. Wenn Ihr mit dem Zeppelin wieder hier vorbeikommt, könntet Ihr mir da wohl etwas zu essen mitbringen? In Orgrimmar verkaufen sie das beste Fleisch, das man sich nur vorstellen kann, und ich könnte wirklich etwas vertragen, das nicht schon Wochen, bevor es sich zu bewegen aufhörte, gestorben ist.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4966', 'deDE', 'Ich hoffe, es funktioniert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4967', 'deDE', '%s fügt den Dunst zu den Blutblütensprösslingen hinzu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4968', 'deDE', 'Seht nur, wie die abgehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4969', 'deDE', '%s lacht, als er das Ungeziefer fliehen sieht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4970', 'deDE', 'Es ist GEH-ZEIT! Holt euch was! <replace this>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4971', 'deDE', 'Eure Familie grüßt Euch, Ribbly. Und sie will Euren Kopf!', 'Eure Familie grüßt Euch, Ribbly. Und sie will Euren Kopf!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4972', 'deDE', 'Ihr seid ein Taugenichts, Ribbly. Es ist an der Zeit, dass Ihr für Eure Gemeinheit bezahlt!', 'Ihr seid ein Taugenichts, Ribbly. Es ist an der Zeit, dass Ihr für Eure Gemeinheit bezahlt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4973', 'deDE', 'Nein! Bleibt mir vom Leib! Hilfe!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4974', 'deDE', 'Sucht nach \'Noggs Maschinenladen\' im Tal der Ehre nordöstlich von \'Roter Canyon-Bergbau\'.', 'Sucht nach \'Noggs Maschinenladen\' im Tal der Ehre nordöstlich von \'Roter Canyon-Bergbau\'.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4975', 'deDE', 'Ihr findet diesen Goblin und seinen Spielzeugladen im Tal der Ehre.', 'Ihr findet diesen Goblin und seinen Spielzeugladen im Tal der Ehre.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4976', 'deDE', 'Ingenieurskunst', 'Ingenieurskunst', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4977', 'deDE', 'Helft mir, weise zu wählen.', 'Helft mir, weise zu wählen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4978', 'deDE', 'Ich bin ein sehr mächtiger Magier, ich habe den Verstand und das Gedächtnis studiert, was das Lernen und Behalten erlernter Fähigkeiten angeht. Wären nicht diese lauten Stimmen in der Nacht, die mir sagten, dass meine Dienste gebraucht würden, würde ich jetzt meine Studien weiter vervollkommnen. Ich werde nur kurze Zeit hier sein und meine Fähigkeit, Eure Talente auszulöschen, kostenlos anbieten. Bald gehe ich wieder, und dann müsst Ihr mit den Entscheidungen leben, die Ihr trefft.', 'Ich bin ein sehr mächtiger Magier, ich habe den Verstand und das Gedächtnis studiert, was das Lernen und Behalten erlernter Fähigkeiten angeht. Wären nicht diese lauten Stimmen in der Nacht, die mir sagten, dass meine Dienste gebraucht würden, würde ich jetzt meine Studien weiter vervollkommnen. Ich werde nur kurze Zeit hier sein und meine Fähigkeit, Eure Talente auszulöschen, kostenlos anbieten. Bald gehe ich wieder, und dann müsst Ihr mit den Entscheidungen leben, die Ihr trefft.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4979', 'deDE', null, 'Alles klar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4980', 'deDE', null, 'Kommt sofort!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4981', 'deDE', null, 'Ich bringe es sofort, Baby...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4983', 'deDE', null, 'Ja, $Gwerter Herr:holde Dame;.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4984', 'deDE', 'He, Nagmara! Wie wäre es mit einem Becher Dunkeleisenbier?', 'He, Nagmara! Wie wäre es mit einem Becher Dunkeleisenbier?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4985', 'deDE', 'Nagmara! Ist noch Hammelfleisch übrig?', 'Nagmara! Ist noch Hammelfleisch übrig?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4986', 'deDE', 'Herrin, sagt Stöpsel, dass ich noch mehr von seinem Bier möchte. Ich bekomme nicht genug davon!', 'Herrin, sagt Stöpsel, dass ich noch mehr von seinem Bier möchte. Ich bekomme nicht genug davon!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4987', 'deDE', 'He, Nag, würdet Ihr mir davon wohl eine Scheibe abschneiden...', 'He, Nag, würdet Ihr mir davon wohl eine Scheibe abschneiden...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4988', 'deDE', 'Ich würde gern Kochunterricht bekommen.', 'Ich würde gern Kochunterricht bekommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4989', 'deDE', 'Hallo, $N. Wenn Ihr das eine oder andere über das Kochen lernen möchtet, schaut einfach bei mir rein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4990', 'deDE', null, '<Lady Prestor schaut Euch böse an.>', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4991', 'deDE', 'Bitte entschuldigt die Störung, Lady Prestor, aber Hochlord Bolvar schlug vor, dass ich Euren Rat suche.', 'Bitte entschuldigt die Störung, Lady Prestor, aber Hochlord Bolvar schlug vor, dass ich Euren Rat suche.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4992', 'deDE', null, 'Ich stand direkt neben Euch, Schwachkopf. Ich weiß, was Ihr sucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4993', 'deDE', 'Entschuldigt bitte, Lady Prestor.', 'Entschuldigt bitte, Lady Prestor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4994', 'deDE', null, 'Ihr wollt unsere globale Verteidigung schwächen, um einem paranoiden Magistrat und Gesindel zu gefallen, die höchstwahrscheinlich an Hitzschlag leiden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4995', 'deDE', 'Pardon, Lady Prestor. Das war nicht meine Absicht.', 'Pardon, Lady Prestor. Das war nicht meine Absicht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4996', 'deDE', null, 'Natürlich nicht, $Glieber Junge:liebes Mädchen;. Ihr Abenteurertypen seid häufig Opfer der Umstände und den politischen Dramen ausgeliefert, die sich zwischen den Bürgern und dem Adel abspielen.$B$BMein Rat für Euch ist daher, sucht etwas anderes, genauer: einen neuen Beruf.$B$BDiese Welt hat bereits genügend Helden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4997', 'deDE', 'Ich danke Euch für die Zeit, die Ihr aufgewendet habt, Lady Prestor.', 'Ich danke Euch für die Zeit, die Ihr aufgewendet habt, Lady Prestor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4998', 'deDE', '%s wird echt schwindelig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('4999', 'deDE', '%s vergeigt betrunken einen \'Schattenblitz\'!', '%s vergeigt betrunken einen \'Schattenblitz\'!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5000', 'deDE', null, 'Heda, Rocknot!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5001', 'deDE', null, 'Auf geht\'s, Liebling.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5002', 'deDE', null, '%s küsst ihren Liebsten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5003', 'deDE', '%s küsst Herrin Nagmara.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5004', 'deDE', 'Bitte helft mir, diesen verfluchten Wald zu durchqueren, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5005', 'deDE', 'Die teuflische Verderbnis hat keine der Kreaturen im Teufelswald unberührt gelassen, $N. Bitte seht Euch vor.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5006', 'deDE', 'Was wollt Ihr? *Hick*', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5007', 'deDE', 'Offizielle Angelegenheit, John. Ich brauche Informationen über Marshal Windsor. Erzählt mir vom letzten Mal, als Ihr ihn gesehen habt.', 'Offizielle Angelegenheit, John. Ich brauche Informationen über Marshal Windsor. Erzählt mir vom letzten Mal, als Ihr ihn gesehen habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5008', 'deDE', 'Dass ich gegen meine eigene Art kämpfen muss, betrübt mich zutiefst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5009', 'deDE', 'Windsor war an dem Tag besonders unausstehlich - und glaubt mir, bei Windsor ist das eine reife Leistung. Er sagte immer wieder, dass \'etwas nicht stimmt\'. Und das war kein Scherz!$B$BWir waren in der Mitte des Blackrock, als die elenden Bestien angriffen. Ich spreche natürlich von den Orcs. Passt gefälligst auf, ja? Man konnte nur Grunzen und das Klirren von Stahl hören, als sie auf uns zustürmten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5010', 'deDE', 'Und was habt Ihr gemacht?', 'Und was habt Ihr gemacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5012', 'deDE', 'Ich gegen fünfzig Orcs? Ich bin doch kein Narr, $N. Mein Papa hat immer gesagt: \'Diskreditieren ist die bessere Hälfte eines Kräckers\', oder so ähnlich, und ich wusste, was das bedeutet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5013', 'deDE', 'Redet kein dummes Zeug, Zwerg. Ich will nichts mit Eurem Kräcker, Eurem Papa oder irgendeiner Form von \'Diskreditieren\' zu tun haben.', 'Redet kein dummes Zeug, Zwerg. Ich will nichts mit Eurem Kräcker, Eurem Papa oder irgendeiner Form von \'Diskreditieren\' zu tun haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5014', 'deDE', 'Schon gut, schon gut. Wie auch immer, ich verkrümelte mich in die Schatten.$B$BDas kam nicht besonders gut an bei Windsor, der sowieso schon total zappelig war. Der fängt an, den ollen Ironfoe zu schwingen und die Orcs wie ein Wahnsinniger anzubrüllen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5015', 'deDE', 'Ironfoe?', 'Ironfoe?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5016', 'deDE', 'Yep. Noch nie von Ironfoe gehört? Dem legendären Orc-Schlächterhammer? Jupp, jupp, das war der Hammer des alten Windsor. Er sagte mir, dass Franclorn Forgewright diesen Hammer höchstpersönlich für seinen Ururgroßpapa gemacht hat. DER Franclorn Forgewright: Der Dunkeleisenzwerg, der verantwortlich ist für Stonewrought-Archimetek... Bauzeugs halt. Er sagte auch, der Hammer hätte einen Zwillingsbruder, den Franclorn für sich behielt. Ich glaube, er nannte ihn Ironfel oder so.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5017', 'deDE', 'Interessant... weiter im Text, John.', 'Interessant... weiter im Text, John.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5018', 'deDE', 'Wo war ich doch gleich? Ach ja, die Orcs stürmten also auf Windsor los, aber Windsor, der bewegte sich keinen Millimeter. Er stand reglos da, während sie in Zehnergruppen auf ihn los stürmten. Ich konnte nur Ironfoe funkeln und jede Menge Blut sehen. Das ging Stunden so, möglicherweise Tage. Ich erinnere mich nicht. Jedenfalls war es dann ENDLICH vorbei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5019', 'deDE', 'So ist Windsor also gestorben...', 'So ist Windsor also gestorben...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5020', 'deDE', 'Gestorben? Seid Ihr nicht gescheit, $N? Pardon, $Gwerter Herr:holde Dame; $N! Als hätten fünfzig Orcs Windsor töten können. So sicher, wie Blutwurst aus Thelsamar die größte Köstlichkeit der Welt ist, stand er da: Von Kopf bis Fuß von Orc-Fetzen bedeckt, in rund achtzehn Schichten ihres Blutes gehüllt, aber eindeutig am Leben... und so was von wütend.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5021', 'deDE', 'Und wie ist er gestorben?', 'Und wie ist er gestorben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5022', 'deDE', 'Warum sagt Ihr dauernd, dass er gestorben sei? Wer hat Euch gesagt, dass er starb? Ich jedenfalls nicht.$B$BEr wird vermisst, das ist alles.$B$BSeht Ihr, er geriet offenbar mitten in einen großen Kampf von Orcs gegen Dunkeleisenzwerge. Die Orcs, diese schändlichen, dreckigen kleinen Feiglinge, kamen früher raus und richteten ein paar Fallen und andere diabolische Sachen ein, die Ihr nicht verstehen würdet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5023', 'deDE', 'Jetzt sehe ich es, $N. Ashenvale liegt direkt voraus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5024', 'deDE', 'Gut, also wo, zum Teufel, ist er? Moment mal! Seid Ihr betrunken?', 'Gut, also wo, zum Teufel, ist er? Moment mal! Seid Ihr betrunken?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5025', 'deDE', 'Zwerge werden nicht betrunken, $GJungchen:grünes Gör;. Ich bin nur ein bisschen bedüdelt.$B$BWie auch immer, zurück zu Windsor. Ich glaube, der ist irgendwo in den Blackrocktiefen. Für Euch uninformatierte Dummköpfe, dies ist die Stadt der Dunkeleisenzwerge.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5026', 'deDE', 'Ich fühle... etwas Seltsames...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5027', 'deDE', 'WARUM ist er in den Blackrocktiefen?', 'WARUM ist er in den Blackrocktiefen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5028', 'deDE', 'Langsam! Dazu kam ich doch gerade!$B$BEr steht also da und trieft von Blut und Gekröse, und wer erscheint da auf der Bildfläche?$B$BDie Dunkeleisenzwerge! Habt Ihr mir denn überhaupt nicht zugehört???$B$BNa ja, die Dunkeleisenzwerge sind etwas geschickter als diese Orcs des Blackrockklans. Sie waren vorbereitet. Mit vorbereitet meine ich, sie waren rund 300 an der Zahl... *Hick* \'tschuljung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5029', 'deDE', '$N, meine Gestalt hat sich verändert! Die wahre Kraft meines Geistes kehrt zu mir zurück… die faulige Macht des Waldes schwindet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5030', 'deDE', '300? Also haben die Dunkeleisenzwerge ihn getötet und in die Tiefen geschleppt?', '300? Also haben die Dunkeleisenzwerge ihn getötet und in die Tiefen geschleppt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5031', 'deDE', '$GJungchen:Mademeusele;, wenn ich es nicht besser wüsste, würde ich denken, Ihr würdet zu dieser... \'speziellen\' Sorte von Leuten gehören. Wir nennen sie Troggs.$B$BWindsor hatte kein Hühnchen mit den Dunkeleisenzwergen zu rupfen, immerhin war der beste Freund seines Ururgroßpapas ein Dunkeleisenzwerg. Aus diesem Grund hat die Dunkeleisenarmee ihn vermutlich auch nicht sofort getötet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5032', 'deDE', 'Euer Wissen ist unzureichend. Kehrt wieder, wenn Eure Fertigkeiten bei der Verhüttung größer sind!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5033', 'deDE', 'Ihr könnt mich hier lassen - in meinen letzten Augenblicken möchte ich allein sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5034', 'deDE', 'Ahhh... Ironfoe.', 'Ahhh... Ironfoe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5035', 'deDE', 'Endlich! Steckt Euch die Finger in die Ohren; Euer Hirn scheint um das Fünffache gewachsen zu sein und ich will nicht, dass es rausquillt.$B$BAlso, die Dunkeleisenzwerge schonten sein Leben und nahmen ihn gefangen. Ihr Anführer, ein selbstgefälliger, aufgeblasener Bursche namens Thauris-Sowieso nahm Ironfoe an sich. Und da hab ich den ollen Windsor zum letzten Mal gesehen... *Hick* \'tschuljung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5036', 'deDE', 'Danke, $N. Jetzt wird mein Geist endlich Frieden finden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5037', 'deDE', 'Die Verderbnis des Teufelswaldes hat meinen Geist hier gefangen... Ich finde wohl niemals Ruhe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5038', 'deDE', 'Danke, Struppiger John. Die Geschichte war sehr erhebend und informativ.', 'Danke, Struppiger John. Die Geschichte war sehr erhebend und informativ.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5039', 'deDE', null, 'Willkommen im Grimmigen Säufer!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5040', 'deDE', 'Warum sucht Ihr und Rocknot Euch nicht ein ruhiges Plätzchen...', 'Warum sucht Ihr und Rocknot Euch nicht ein ruhiges Plätzchen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5041', 'deDE', null, 'Gute Idee!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5042', 'deDE', null, 'Nochmals danke, $N. Rocknot und ich laufen echt auf der derselben Wellenlänge!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5043', 'deDE', 'Ein Ingenieur! Hey, endlich kann ich mal die neuen Geräte zeigen, die ich gebaut habe!', 'Ein Ingenieur! Hey, endlich kann ich mal die neuen Geräte zeigen, die ich gebaut habe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5044', 'deDE', null, 'Wenn Ihr in die Blackrocktiefen gehen wollt, kommt und sprecht mit mir. Ich habe vielleicht ein bisschen Arbeit für Euch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5045', 'deDE', '%s sieht müde aus. Dann gähnt er und legt sich neben A-Me 01 nieder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5046', 'deDE', '%s schnarcht laut und kuschelt sich neben A-Me 01.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5047', 'deDE', null, 'Habt Ihr die wunderbaren Dinge hier im Krater von Un\'Goro gesehen, $N? Einfach erstaunlich. Nicht so erstaunlich wie viele der Dinge, die ich mit meiner Ingenieurskunst-Fertigkeit herstellen kann, aber da hier alles Natur pur ist... nicht übel.$B$BMein Onkel Remtravel liebt altes Zeug wie diesen Ort. Ich trete irgendwie in seine Fußstapfen - auch ich möchte neue Sachen von alten Kulturen entdecken!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5048', 'deDE', null, 'Hmm, ich habe keine Mithrilgehäuse. Nichts mehr da. Mehr braucht Ihr nicht, um A-Me zu reparieren? Oh, das wäre phantastisch!$B$BMithrilgehäuse sind daheim in der Zivilisation nicht schwer zu finden. Bittet irgendeinen geschickten Ingenieur, Euch eins zu machen. Ich glaube, dazu sind ein paar Barren Mithril erforderlich, aber wenn Ihr mir aushelft, werdet Ihr gewiss gut belohnt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5049', 'deDE', 'Habt Ihr Mithrilgehäuse?', 'Habt Ihr Mithrilgehäuse?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5050', 'deDE', null, 'Trinkt unbedingt unser Dunkeleisenbier. Ich habe gehört, die Zwerge hier können gar nicht genug davon bekommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5051', 'deDE', null, 'Mein Meister, Stöpsel Zapfring, ist ein guter Boss. Und er verdient eine Menge Geld. Seine Taschen platzen förmlich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5052', 'deDE', 'Das hier ist das beste und modernste Fortbewegungsmittel, das man für Geld überhaupt bauen kann. Was? Die Tiefenbahn? Gnome? Hört mal, $GKumpel:Mädel;, wenn Ihr diesen Stuss glaubt, dann hab\' ich eine Statue unten im Schlingendorntal, die ich Euch unbedingt verkaufen muss. Also, wollt Ihr jetzt nach Grom\'gol laufen oder wollt Ihr stilvoll in diesem wunderbaren Flugapparat reisen? Es liegt bei Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5053', 'deDE', 'He! Geht da weg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5054', 'deDE', 'Was tut Ihr da drüben?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5055', 'deDE', 'Lasst Eure Finger von meiner Ware!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5056', 'deDE', 'He! Lasst das!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5057', 'deDE', 'Phalanx, ich glaube, Ihr müsst jener Person eine Lektion erteilen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5058', 'deDE', 'Ihr wurdet als Bedrohung eingestuft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5059', 'deDE', 'Ich werde wiedergeboren, sterbliche Wesen! Incendius wird mich aus der Asche auferstehen lassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5060', 'deDE', 'Jetzt reicht\'s! Das wird Euer Untergang!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5061', 'deDE', 'Ich kann nicht zerstört werden! Durch Ragnaros Willen werde ich wiedergeboren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5062', 'deDE', 'A-Me gut. Gut, A-Me. Folgen... A-Me folgen. Heim. A-Me geht heim.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5063', 'deDE', 'Gut... gut, A-Me. A-Me gut. Heim. Heim suchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5064', 'deDE', 'Helft mir! $N kommt näher!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5065', 'deDE', 'Helft mir gegen $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5066', 'deDE', 'Habt Ihr schon das Donnerbräu Lagerbier versucht? Das ist echt guter Stoff!', 'Habt Ihr schon das Donnerbräu Lagerbier versucht? Das ist echt guter Stoff!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5067', 'deDE', 'In einer Stunde habe ich Dienst, aber bis dahin... hoch die Tassen!', 'In einer Stunde habe ich Dienst, aber bis dahin... hoch die Tassen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5068', 'deDE', 'Seht Ihr mich an, $GJüngelchen:Mädchen;?', 'Seht Ihr mich an, $GJüngelchen:Mädchen;?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5069', 'deDE', 'Ich nehme einen Becher Dunkeleisenbier. Oh, seid Ihr nicht $Gder Kellner:die Kellnerin;?', 'Ich nehme einen Becher Dunkeleisenbier. Oh, seid Ihr nicht $Gder Kellner:die Kellnerin;?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5070', 'deDE', 'Ich glaube, Stöpsel hat eines seiner Fässchen zu voll gemacht. Sieht aus, als würde es gleich explodieren!', 'Ich glaube, Stöpsel hat eines seiner Fässchen zu voll gemacht. Sieht aus, als würde es gleich explodieren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5071', 'deDE', 'Stört mich nicht beim Abfeiern...', 'Stört mich nicht beim Abfeiern...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5072', 'deDE', 'Wollt Ihr den Weg wissen, $GBursche:Mädchen;?', 'Wollt Ihr den Weg wissen, $GBursche:Mädchen;?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5073', 'deDE', 'Ich liebe den Geruch von Eisenschmiede am Morgen! Eh? Und was möchtet Ihr haben, $C?', 'Ich liebe den Geruch von Eisenschmiede am Morgen! Eh? Und was möchtet Ihr haben, $C?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5074', 'deDE', 'Was braucht Ihr?', 'Was braucht Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5075', 'deDE', 'WENN, sagen sie... WENN! Es gibt kein Wenn in Eisenschmiede! Eh? Was jetzt?', 'WENN, sagen sie... WENN! Es gibt kein Wenn in Eisenschmiede! Eh? Was jetzt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5076', 'deDE', 'Könnt Ihr mir wohl die Richtung weisen?', 'Könnt Ihr mir wohl die Richtung weisen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5077', 'deDE', 'Was ist denn Euer Ziel?', 'Was ist denn Euer Ziel?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5078', 'deDE', 'Die Bank von Eisenschmiede', 'Die Bank von Eisenschmiede', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5079', 'deDE', 'Habt Ihr neue Schätze, die Ihr sicher aufbewahren wollt? Dann solltet Ihr sie bei den Stonemantles im \'Gewölbe\' einlagern. \n\nIhr findet \'Das Gewölbe\' knapp einen Hammerwurf nordöstlich vom Eingang nach Eisenschmiede.', 'Habt Ihr neue Schätze, die Ihr sicher aufbewahren wollt? Dann solltet Ihr sie bei den Stonemantles im \'Gewölbe\' einlagern. Ihr findet \'Das Gewölbe\' knapp einen Hammerwurf nordöstlich vom Eingang nach Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5080', 'deDE', 'Die Bank? Oh, Ihr meint \'Das Gewölbe\'! Es gibt keinen sichereren Ort für Wertsachen als \'Das Gewölbe\'. Ihr findet es gleich nordöstlich des Eingangs von Eisenschmiede.', 'Die Bank? Oh, Ihr meint \'Das Gewölbe\'! Es gibt keinen sichereren Ort für Wertsachen als \'Das Gewölbe\'. Ihr findet es gleich nordöstlich des Eingangs von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5081', 'deDE', 'Der Greifenmeister', 'Der Greifenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5083', 'deDE', 'Ihr habt Lust auf einen Greifenritt, ja? Mit diesem Anliegen solltet Ihr zu Gryth Thurden gehen. \n\nIhr findet ihn auf der Ostseite der großen Schmiede in der Mitte von Eisenschmiede.', 'Ihr habt Lust auf einen Greifenritt, ja? Mit diesem Anliegen solltet Ihr zu Gryth Thurden gehen. \n\nIhr findet ihn auf der Ostseite der großen Schmiede in der Mitte von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5085', 'deDE', 'Ah, nichts ist mit einem Greifenritt zu vergleichen. Die Nachtelfen können ihre Hippos getrost behalten, mir genügt ein Greif und der Wind in den Haaren! \n\nAber ich will Euch nicht länger aufhalten, geht einfach zu Gryth Thurden auf der Ostseite der großen Schmiede, der bringt Euch in die Lüfte.', 'Ah, nichts ist mit einem Greifenritt zu vergleichen. Die Nachtelfen können ihre Hippos getrost behalten, mir genügt ein Greif und der Wind in den Haaren! \n\nAber ich will Euch nicht länger aufhalten, geht einfach zu Gryth Thurden auf der Ostseite der großen Schmiede, der bringt Euch in die Lüfte.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5086', 'deDE', 'Der Gildenmeister', 'Der Gildenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5087', 'deDE', 'Wenn Ihr eine Gilde gründen möchtet, dann müsst Ihr zu Jondor Steelbrow drüben im Besucherzentrum von Eisenschmiede gehen. Ihr findet ihn südöstlich des Eingangs von Eisenschmiede, wenn Ihr ihn sprechen wollt.', 'Wenn Ihr eine Gilde gründen möchtet, dann müsst Ihr zu Jondor Steelbrow drüben im Besucherzentrum von Eisenschmiede gehen. Ihr findet ihn südöstlich des Eingangs von Eisenschmiede, wenn Ihr ihn sprechen wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5088', 'deDE', 'Ein Lehrer für eine Klasse', 'Ein Lehrer für eine Klasse', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5089', 'deDE', 'Für welche Klasse sucht Ihr denn einen Lehrer?', 'Für welche Klasse sucht Ihr denn einen Lehrer?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5090', 'deDE', 'Ein Gasthaus', 'Ein Gasthaus', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5091', 'deDE', 'Das Gasthaus? Ah, Ihr wollt ein paar der besten Bierchen wegkippen, die Eisenschmiede zu bieten hat, was? Dann müsst Ihr Euch auf den Weg zur Stonefire-Taverne machen. $B$BIhr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst.', 'Das Gasthaus? Ah, Ihr wollt ein paar der besten Bierchen wegkippen, die Eisenschmiede zu bieten hat, was? Dann müsst Ihr Euch auf den Weg zur Stonefire-Taverne machen. $B$BIhr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5092', 'deDE', 'Wenn Ihr ein Gasthaus sucht, dann ist die Stonefire-Taverne die erste Adresse. Ihr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst. $B$BOh, und wenn Ihr dorthin geht, dann sagt ihnen, sie sollen schon mal einen Humpen für mich zapfen.', 'Wenn Ihr ein Gasthaus sucht, dann ist die Stonefire-Taverne die erste Adresse. Ihr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst. $B$BOh, und wenn Ihr dorthin geht, dann sagt ihnen, sie sollen schon mal einen Humpen für mich zapfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5093', 'deDE', 'Ein Briefkasten', 'Ein Briefkasten', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5094', 'deDE', 'Ihr findet einen Briefkasten gleich drüben bei der Stonefire-Taverne. Die liegt gleich nördlich des Stadttors von Eisenschmiede.', 'Ihr findet einen Briefkasten gleich drüben bei der Stonefire-Taverne. Die liegt gleich nördlich des Stadttors von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5095', 'deDE', 'Jäger', 'Jäger', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5096', 'deDE', 'Gestern hörte ich Regnus Thundergranite in der Halle der Waffen über eine neue Trophäe für seinen Bau prahlen. Wenn Ihr Ausbildung bei einem versierten Jäger sucht, dann ist Regnus Euer Mann.', 'Gestern hörte ich Regnus Thundergranite in der Halle der Waffen über eine neue Trophäe für seinen Bau prahlen. Wenn Ihr Ausbildung bei einem versierten Jäger sucht, dann ist Regnus Euer Mann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5097', 'deDE', 'Magier', 'Magier', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5098', 'deDE', 'Bink, die kleine Hexenbraut drüben in der Halle der Mysterien, kann Euch wahrscheinlich in allen magischen Belangen weiterhelfen. \n\nDie Halle der Mysterien liegt gleich nördlich des Stadttors von Eisenschmiede.', 'Bink, die kleine Hexenbraut drüben in der Halle der Mysterien, kann Euch wahrscheinlich in allen magischen Belangen weiterhelfen.\n\nDie Halle der Mysterien liegt gleich nördlich des Stadttors von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5099', 'deDE', 'Ausbildung in den magischen Künsten könnt Ihr ganz sicher drüben in der Halle der Mysterien nördlich des Stadttors von Eisenschmiede bekommen. Juli Stormkettle ist dort und hat ständig die Nase in einem Buch.', 'Ausbildung in den magischen Künsten könnt Ihr ganz sicher drüben in der Halle der Mysterien nördlich des Stadttors von Eisenschmiede bekommen. Juli Stormkettle ist dort und hat ständig die Nase in einem Buch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5100', 'deDE', 'Priester', 'Priester', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5101', 'deDE', 'Ah, Braenna Flintcrag. Sie hat magische Hände. Braenna muss man konsultieren, wenn es um Fragen der Priesterschaft geht. \n\nIhr müsst vom Stadttor von Eisenschmiede aus nach Norden ziehen, zur Halle der Mysterien. Da müsste sie zu finden sein.', 'Ah, Braenna Flintcrag. Sie hat magische Hände. Braenna muss man konsultieren, wenn es um Fragen der Priesterschaft geht. \n\nIhr müsst vom Stadttor von Eisenschmiede aus nach Norden ziehen, zur Halle der Mysterien. Da müsste sie zu finden sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5102', 'deDE', 'Paladin', 'Paladin', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5103', 'deDE', 'Brandur Ironhammer ist Euer Mann. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', 'Brandur Ironhammer ist Euer Mann. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5104', 'deDE', 'Ah, der gute alte Brandur Ironhammer. Er wird Euch zweifellos das Wissen geben können, das Ihr sucht, $GJungchen:Mädel;. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', 'Ah, der gute alte Brandur Ironhammer. Er wird Euch zweifellos das Wissen geben können, das Ihr sucht, $GJungchen:Mädel;. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5105', 'deDE', 'Schurke', 'Schurke', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5106', 'deDE', 'Na, warum bedürft Ihr wohl der Dienste von einem dieser Kerle? Hulfdan Blackbeard, dieser Schurke drüben im düsteren Viertel, macht uns schon genug Ärger, muss ich da auch noch Euch im Auge behalten?', 'Na, warum bedürft Ihr wohl der Dienste von einem dieser Kerle? Hulfdan Blackbeard, dieser Schurke drüben im düsteren Viertel, macht uns schon genug Ärger, muss ich da auch noch Euch im Auge behalten?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5107', 'deDE', 'Diese ganzen Schurkenkerle hängen im düsteren Viertel nördlich des Stadttors von Eisenschmiede herum. Ich wette, Ihr findet Fenthwick dort, und zweifellos wird er die Münzen in einer weiteren gestohlenen Börse zählen. Eines Tages werde ich ihn erwischen! \n\nBis dahin behalte ich Euch ebenfalls im Auge, $GJungchen:Mädchen;.', 'Diese ganzen Schurkenkerle hängen im düsteren Viertel nördlich des Stadttors von Eisenschmiede herum. Ich wette, Ihr findet Fenthwick dort, und zweifellos wird er die Münzen in einer weiteren gestohlenen Börse zählen. Eines Tages werde ich ihn erwischen! \n\nBis dahin behalte ich Euch ebenfalls im Auge, $GJungchen:Mädchen;.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5108', 'deDE', 'Hexenmeister', 'Hexenmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5109', 'deDE', 'Wo sollte man wohl nach einer finsteren und grimmigen Bande suchen, wenn nicht im verrufenen Teil von Eisenschmiede, der so zutreffend düsteres Viertel genannt wird. Thistleheart ist ganz sicher dort zu finden und wahrscheinlich beschwört er gerade eine garstige Kreatur, ihm zu dienen.', 'Wo sollte man wohl nach einer finsteren und grimmigen Bande suchen, wenn nicht im verrufenen Teil von Eisenschmiede, der so zutreffend düsteres Viertel genannt wird. Thistleheart ist ganz sicher dort zu finden und wahrscheinlich beschwört er gerade eine garstige Kreatur, ihm zu dienen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5110', 'deDE', 'Krieger', 'Krieger', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5111', 'deDE', 'Soso! Ihr möchtet also \'ne Ausbildung zum Krieger, wie? Kelstrum Stonebreaker zeigt Euch, wo\'s langgeht! Marschiert einfach rüber zur Halle der Waffen, östlich vom Stadttor von Eisenschmiede.', 'Soso! Ihr möchtet also \'ne Ausbildung zum Krieger, wie? Kelstrum Stonebreaker zeigt Euch, wo\'s langgeht! Marschiert einfach rüber zur Halle der Waffen, östlich vom Stadttor von Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5112', 'deDE', 'Ein Lehrer für einen Beruf', 'Ein Lehrer für einen Beruf', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5113', 'deDE', 'Für welchen Beruf sucht Ihr denn einen Lehrer?', 'Für welchen Beruf sucht Ihr denn einen Lehrer?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5114', 'deDE', 'Alchemie', 'Alchemie', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5115', 'deDE', 'Einen Lehrer für Alchemie braucht Ihr also, ah ja. Tja, da war ich auch gerade. Ihr findet Ms. Berryfizz in der Tüftlerstadt, wo sich die Gnome treffen.', 'Einen Lehrer für Alchemie braucht Ihr also, ah ja. Tja, da war ich auch gerade. Ihr findet Ms. Berryfizz in der Tüftlerstadt, wo sich die Gnome treffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5116', 'deDE', 'Ach, Ihr sucht also einen Lehrer für Alchemie? Von so einem komm ich gerade her. Ihr findet Ms. Berryfizz in der Tüftlerstadt, wo sich die Gnome treffen. \n\n::seufzen::... warum, oh warum nur habe ich den blauen Trank nicht genommen.', 'Ach, Ihr sucht also einen Lehrer für Alchemie? Von so einem komm ich gerade her. Ihr findet Ms. Berryfizz in der Tüftlerstadt, wo sich die Gnome treffen. \n\n::seufzen::... warum, oh warum nur habe ich den blauen Trank nicht genommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5117', 'deDE', 'Schmiedekunst', 'Schmiedekunst', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5118', 'deDE', 'Ihr seid hier genau richtig, $GJungchen:Mädel;! Die Leute kommen aus der ganzen Welt hierher, um sich zum Schmied ausbilden zu lassen. Bengus Deepforge wird zweifellos mitten in unserer schönen Stadt, bei der großen Schmiede selbst, auf seinem Amboss herumhämmern.', 'Ihr seid hier genau richtig, $GJungchen:Mädel;! Die Leute kommen aus der ganzen Welt hierher, um sich zum Schmied ausbilden zu lassen. Bengus Deepforge wird zweifellos mitten in unserer schönen Stadt, bei der großen Schmiede selbst, auf seinem Amboss herumhämmern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5119', 'deDE', 'Kochkunst', 'Kochkunst', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5120', 'deDE', 'Ein Kochkunstlehrer soll es sein? Wohlan, begebt Euch \'Zum Bronzekessel\' auf der nordöstlichen Seite der großen Schmiede. Daryl Riknussun bringt Euch bei, wie man ein sagenhaft leckeres Gericht zubereitet, das Eure Lebensgeister ganz bestimmt wieder weckt!', 'Ein Kochkunstlehrer soll es sein? Wohlan, begebt Euch \'Zum Bronzekessel\' auf der nordöstlichen Seite der großen Schmiede. Daryl Riknussun bringt Euch bei, wie man ein sagenhaft leckeres Gericht zubereitet, das Eure Lebensgeister ganz bestimmt wieder weckt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5121', 'deDE', 'Verzauberkunst', 'Verzauberkunst', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5122', 'deDE', 'Ah, ich glaube, Gimble Thistlefuzz ist Euer Mann, wenn Ihr den Beruf des Verzauberers erlernen wollt. Ihr findet Gimble in seinem Laden, \'Arkanarien Thistlefuzz\', direkt beim Greifenmeister an der großen Schmiede.', 'Ah, ich glaube, Gimble Thistlefuzz ist Euer Mann, wenn Ihr den Beruf des Verzauberers erlernen wollt. Ihr findet Gimble in seinem Laden, \'Arkanarien Thistlefuzz\', direkt beim Greifenmeister an der großen Schmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5123', 'deDE', 'Ingenieurskunst', 'Ingenieurskunst', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5124', 'deDE', 'Das Tüftler-Handwerk soll es also für Euch sein? Dann solltet Ihr zweifellos Springspindle Fizzlegear in der Tüftlerstadt aufsuchen. \n\nGeht einfach vom Stadttor von Eisenschmiede aus nach Osten oder folgt dem Knallen der Explosionen.', 'Das Tüftler-Handwerk soll es also für Euch sein? Dann solltet Ihr zweifellos Springspindle Fizzlegear in der Tüftlerstadt aufsuchen. \n\nGeht einfach vom Stadttor von Eisenschmiede aus nach Osten oder folgt dem Knallen der Explosionen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5125', 'deDE', 'Erste Hilfe', 'Erste Hilfe', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5126', 'deDE', 'Erste Hilfe ist eine wertvolle Fertigkeit. Nissa Firestone, unsere hiesige Lehrerin, kann Euch zeigen, wie es gemacht wird. Ihr findet sie auf der Südseite der großen Schmiede beim \'Heiler von Eisenschmiede\'.', 'Erste Hilfe ist eine wertvolle Fertigkeit. Nissa Firestone, unsere hiesige Lehrerin, kann Euch zeigen, wie es gemacht wird. Ihr findet sie auf der Südseite der großen Schmiede beim \'Heiler von Eisenschmiede\'.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5127', 'deDE', 'Angeln', 'Angeln', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5128', 'deDE', 'Grimnur Stonebrand wird Euch Geschichten über Fische, die ihm entkommen sind, auftischen, dass sich die Balken biegen, aber er ist trotzdem einer der besten Angler in der Gegend. Er hat im düsteren Viertel einen Laden, der \'Reisender Angler\' heißt.', 'Grimnur Stonebrand wird Euch Geschichten über Fische, die ihm entkommen sind, auftischen, dass sich die Balken biegen, aber er ist trotzdem einer der besten Angler in der Gegend. Er hat im düsteren Viertel einen Laden, der \'Reisender Angler\' heißt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5129', 'deDE', 'Kräuterkunde', 'Kräuterkunde', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5130', 'deDE', 'Kräuterkunde', 'Kräuterkunde', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5131', 'deDE', 'Blumen und Samenkörner und dergleichen? Das wäre Reyna Stonebranchs Fachgebiet. Sie hegt und pflegt ihre Pflanzen beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', 'Blumen und Samenkörner und dergleichen? Das wäre Reyna Stonebranchs Fachgebiet. Sie hegt und pflegt ihre Pflanzen beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5132', 'deDE', 'Oh, Ihr wollt lernen, wie man Gänseblümchen pflückt, ja? Ha! Ich nehm Euch doch nur auf den Arm, $GJüngelchen:Mädel;. \n\nEs ist ein guter Beruf, und Reyna Stonebranch kann Euch alles darüber beibringen. Sie arbeitet beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', 'Oh, Ihr wollt lernen, wie man Gänseblümchen pflückt, ja? Ha! Ich nehm Euch doch nur auf den Arm, $GJüngelchen:Mädel;. \n\nEs ist ein guter Beruf, und Reyna Stonebranch kann Euch alles darüber beibringen. Sie arbeitet beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5133', 'deDE', 'Lederverarbeitung', 'Lederverarbeitung', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5135', 'deDE', 'Fimble Finespindle hat einen Laden auf der Nordwestseite der großen Schmiede. Ich wette, der könnte Euch zeigen, wie man eine erlesene Rüstung macht.', 'Fimble Finespindle hat einen Laden auf der Nordwestseite der großen Schmiede. Ich wette, der könnte Euch zeigen, wie man eine erlesene Rüstung macht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5136', 'deDE', 'Schlossknacken', 'Schlossknacken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5137', 'deDE', 'Durtham Greldon drüben im düsteren Viertel kann Euch die Kniffe und Tricks des Schlossknackens zeigen. Ihr solltet diese Kniffe und Tricks aber nicht hier in Eisenschmiede ausprobieren, wenn Ihr versteht, was ich meine.', 'Durtham Greldon drüben im düsteren Viertel kann Euch die Kniffe und Tricks des Schlossknackens zeigen. Ihr solltet diese Kniffe und Tricks aber nicht hier in Eisenschmiede ausprobieren, wenn Ihr versteht, was ich meine.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5138', 'deDE', 'Bergbau', 'Bergbau', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5139', 'deDE', 'Ob wir hier Bergbaulehrer haben? Was denkt Ihr denn, wo Ihr seid!? \n\nIhr findet einen der besten Minenarbeiter in ganz Dun Morogh in der Deepmountain-Bergbaugilde auf der Nordseite der großen Schmiede. Dort wird Euch Geofram Bouldertoe in die wahre Kunst des Bergbaus einweisen.', 'Ob wir hier Bergbaulehrer haben? Was denkt Ihr denn, wo Ihr seid!? \n\nIhr findet einen der besten Minenarbeiter in ganz Dun Morogh in der Deepmountain-Bergbaugilde auf der Nordseite der großen Schmiede. Dort wird Euch Geofram Bouldertoe in die wahre Kunst des Bergbaus einweisen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5140', 'deDE', 'Kürschnerei', 'Kürschnerei', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5141', 'deDE', 'Balthus Stoneflayer muss man konsultieren, wenn es um die Kürschnerei geht. Wenn man etwas häuten kann, dann zeigt er Euch, wie es geht. Wandert einfach zu \'Lederwaren Finespindle\' auf der Nordseite der großen Schmiede.', 'Balthus Stoneflayer muss man konsultieren, wenn es um die Kürschnerei geht. Wenn man etwas häuten kann, dann zeigt er Euch, wie es geht. Wandert einfach zu \'Lederwaren Finespindle\' auf der Nordseite der großen Schmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5142', 'deDE', 'Überlebenskunst', 'Überlebenskunst', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5143', 'deDE', 'Tresa Farmountain hat jede Menge Unwetter erlebt, aber dank ihrer Kenntnisse der Überlebenskunst-Fertigkeiten hat sie alle unbeschadet überstanden. \n\nSie ist im Militärviertel, südlich des Stadttors von Eisenschmiede, zu finden, wenn Ihr mit ihr sprechen wollt.', 'Tresa Farmountain hat jede Menge Unwetter erlebt, aber dank ihrer Kenntnisse der Überlebenskunst-Fertigkeiten hat sie alle unbeschadet überstanden. \n\nSie ist im Militärviertel, südlich des Stadttors von Eisenschmiede, zu finden, wenn Ihr mit ihr sprechen wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5144', 'deDE', 'Schneiderei', 'Schneiderei', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5145', 'deDE', 'Dieser Jormund Stonebrow hat den Bogen mit der Nadel raus. Wenn Ihr eine Ausbildung braucht, dann findet Ihr ihn in der \'Tuchmacherei Stonebrow\' auf der Nordseite der großen Schmiede.', 'Dieser Jormund Stonebrow hat den Bogen mit der Nadel raus. Wenn Ihr eine Ausbildung braucht, dann findet Ihr ihn in der \'Tuchmacherei Stonebrow\' auf der Nordseite der großen Schmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5146', 'deDE', 'Fährtenlesen', 'Fährtenlesen', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5147', 'deDE', 'Fährtenlesen, ja? Dann solltet Ihr Denmul Farmountain einen Besuch abstatten. Er kann Euch Feinheiten dieses Handwerks beibringen wie kein anderer. \n\nGeht vom Stadttor von Eisenschmiede nach Süden zum Militärviertel, wenn Ihr ihn sucht.', 'Fährtenlesen, ja? Dann solltet Ihr Denmul Farmountain einen Besuch abstatten. Er kann Euch Feinheiten dieses Handwerks beibringen wie kein anderer. \n\nGeht vom Stadttor von Eisenschmiede nach Süden zum Militärviertel, wenn Ihr ihn sucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5148', 'deDE', 'Ich muss meine Energien auf dieses Messer konzentrieren. Dadurch sollte ich einige der stärkeren übersinnlichen Eindrücke wahrnehmen können, die es besitzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5149', 'deDE', 'Ich... Ich habe genug gesehen, glaube ich... das ist überaus anstrengend...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5150', 'deDE', 'Ich habe gelitten, aber jetzt spüre ich, wie es langsam nachlässt. Ich fühle mich... frei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5151', 'deDE', 'Die Kristallpylonen von Un\'Goro sind allen ein Rätsel. Die wenigen Einwohner hier sehen sie als Legenden und spekulieren viel über ihren Sinn und Zweck.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5152', 'deDE', 'Ich möchte diesen Pylon untersuchen.', 'Ich möchte diesen Pylon untersuchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5153', 'deDE', 'Nach eingehenderer Untersuchung des Pylons findet Ihr eine kleine Schrifttafel mit blauen, gelben und grünen Kristallen, die in einer bestimmten Folge angeordnet zu sein scheinen.$B$BEin großer blauer Kreis von Kristallen wird auf beiden Seiten von einem kleineren Kreis aus gelben und einem weiteren aus grünen flankiert.$B$BIhr prägt Euch die Farben der Kristalle sorgfältig ein, da Ihr wisst, J. D. wird größtes Interesse für Euren Fund zeigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5154', 'deDE', 'Nach eingehenderer Untersuchung des Pylons findet Ihr eine kleine Schrifttafel mit gelben, roten und grünen Kristallen, die in einer bestimmten Folge angeordnet zu sein scheinen.$B$BEin großer gelber Kreis von Kristallen wird auf beiden Seiten von einem kleineren Kreis aus roten und einem weiteren aus grünen flankiert.$B$BIhr prägt Euch die Farben der Kristalle sorgfältig ein, da Ihr wisst, J. D. wird größtes Interesse für Euren Fund zeigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5155', 'deDE', 'Nach eingehenderer Untersuchung des Pylons findet Ihr eine kleine Schrifttafel mit roten, blauen und grünen Kristallen, die in einer bestimmten Folge angeordnet zu sein scheinen.$B$BEin großer roter Kreis von Kristallen wird auf beiden Seiten von einem kleineren Kreis aus blauen und einem weiteren aus grünen flankiert.$B$BIhr prägt Euch die Farben der Kristalle sorgfältig ein, da Ihr wisst, J. D. wird größtes Interesse für Euren Fund zeigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5156', 'deDE', 'A-Me daheim! A-Me, gut! Gute A-Me. Daheim. Daheim. Daheim.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5157', 'deDE', 'Böser $N. $N böse!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5158', 'deDE', '$N A-Me nicht wehtun. A-Me gut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5159', 'deDE', 'Gefahr. Gefahr! $N will A-Me wehtun.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5160', 'deDE', 'A-Me daheim. A-Me geht heim! $N A-Me nicht aufhalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5161', 'deDE', null, 'Schafft mir $N vom Hals!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5162', 'deDE', null, 'Hilfe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5163', 'deDE', null, 'Ich brauche Hilfe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5164', 'deDE', null, '$N greift an! Hilfe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5166', 'deDE', 'Ich will mehr Bier! Gebt mir mehr Bier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5167', 'deDE', 'Das Ding ist leer!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5168', 'deDE', 'Ah, schon wieder leer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5169', 'deDE', 'BIER!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5170', 'deDE', '%s sieht Euch erwartungsvoll an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5171', 'deDE', null, 'Ihr habt wahrscheinlich nicht erwartet, mich in dieser Höhle zu finden, was?$B$BMein Name ist J. D. und ich kann Euch wirklich nicht sagen, wie ich hierher gekommen bin, aber eines weiß ich, diese Höhle ist jetzt mein Zuhause!$B$BIch habe mein Leben lang die seltsamen Kristalle studiert, die man überall in dem Krater finden kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5172', 'deDE', 'Ah, perfekt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5173', 'deDE', 'Barmherzige Elune, danke, dass Ihr mich aus diesem... Ding geholt habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5175', 'deDE', 'Kommt mit mir, wenn Ihr leben wollt.', 'Kommt mit mir, wenn Ihr leben wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5176', 'deDE', 'Brennende Steppe - Schrifttafel der Sieben', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5177', 'deDE', 'Schreibt die Schrifttafel ab.', 'Schreibt die Schrifttafel ab.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5178', 'deDE', 'Die Symbole auf dem Täfelchen werden sorgfältig abgeschrieben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5179', 'deDE', 'Seid gegrüßt, $C. Für einige ist Jäger eine Lebensweise, ein erwählter Beruf. Für andere ein Zeitvertreib, eine Mutprobe, ein Initiationsritual, je nach Stamm.$B$BFür mich sind Volk und Beruf bedeutungslos - die Prüfungen von Geist und Verstand sind es, die ich respektiere. Wenn sich jemand gegen die Naturgewalten stellt und all seine Fertigkeiten der Prüfung unterzieht, ist das für mich ein Zeichen von Ehre.$B$BWenn Ihr Euch prüfen und meinen Respekt erlangen wollt, sprecht weiter mit mir.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5181', 'deDE', 'Ihr habt den Weg der Ehre eingeschlagen, $N. Meine Vorfahren respektieren Eure Bereitwilligkeit, aber mein Wohlwollen erlangt Ihr erst, wenn Ihr meine Aufgaben erledigt habt.$B$BIhr wisst, was Ihr tun müsst, wenn Ihr Euch als Jäger im Geiste beweisen wollt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5182', 'deDE', 'Ich grüße Euch aufs Herzlichste, $C, genau wie die Shattered-Hand.$B$BSchaut nicht so überrascht drein, $N. Schatten gibt es selbst auf den höchsten Zinnen von Orgrimmar - dort muss sich etwas verbergen. Die Shattered-Hand übernimmt diese Rolle bereitwillig. Thrall stimmt trotz seines gütigen Herzens darin zu, dass die Hand notwendig ist, um die Sicherheit von Orcs und Trollen zu gewährleisten.$B$BAber Ihr seid nicht hier, um über mein Geschäft zu sprechen, sondern wegen etwas anderem.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5183', 'deDE', 'Der Krater von Un\'Goro liegt weit im Süden von Kalimdor. Jenseits von Tausend Nadeln in Tanaris. Sucht nach den großen schwebenden Steinen... sie kennzeichnen den Eingang zum Krater. Aber seid vorsichtig... es ist kein leichter Weg. Die Raptoren sind nicht weit vom Eingang entfernt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5184', 'deDE', 'Wo ist der Krater von Un\'Goro?', 'Wo ist der Krater von Un\'Goro?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5185', 'deDE', null, 'Dank Euch können wir jetzt die Geheimnisse der Kristalle und der geheimnisvollen Pylons entschlüsseln, $N. Wenn Ihr irgendwann mehr Informationen benötigt, kommt zurück und besucht mich wieder!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5186', 'deDE', 'Erzählt mir mehr über den östlichen Kristallpylon.', 'Erzählt mir mehr über den östlichen Kristallpylon.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5187', 'deDE', 'Wer ist dieser Lar\'korwi, den Ihr erwähnt habt?', 'Wer ist dieser Lar\'korwi, den Ihr erwähnt habt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5188', 'deDE', 'Erzählt mir mehr über den nördlichen Kristallpylon.', 'Erzählt mir mehr über den nördlichen Kristallpylon.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5189', 'deDE', 'Die Ravasaurier hier in Un\'Goro scheinen mit den Raptoren des Brachlands verwandt zu sein - hochintelligent, gefräßig und mit einem gewissen Verhaltenskodex. Der größte von allen, denen ich begegnet bin, ist Lar\'korwi. Er ist eine der gefährlichsten Bestien, die ich je gesehen habe. Auf Taurisch heißt Lar\'korwi so viel wie \"Scharfe Klaue\", aber das wird ihm nicht gerecht.$B$BUnterschätzt Lar\'korwi nicht, wenn Ihr je vorhaben solltet, ihn zu jagen. Aber wenn Ihr das beabsichtigt, unterstütze ich Euch natürlich in jeder erdenklichen Weise.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5190', 'deDE', 'Erzählt mir mehr über den westlichen Kristallpylon.', 'Erzählt mir mehr über den westlichen Kristallpylon.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5191', 'deDE', 'Ich habe mein Handbuch für Kristallpylonen verloren und brauche ein neues.', 'Ich habe mein Handbuch für Kristallpylonen verloren und brauche ein neues.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5192', 'deDE', null, 'Der östliche Pylon akzeptiert blaue, gelbe und grüne Kristalle.$B$BEr fokussiert auf die Energie, die mit den blauen Kristallen als Hauptenergiequelle erzeugt werden kann. Es gibt zwei mögliche Effekte: Einer verleiht der Willenskraft einen Bonus, einer erzeugt einen Schaden zufügenden Schild.$B$B\'Kristallkraft\' erfordert blaue und grüne Kristalle. Das verleiht Euch eine Erhöhung der Willenskraft.$B$B\'Kristallspitze\' erfordert blaue und gelbe Kristalle. Damit wird ein Schild erzeugt, der Angreifern Schaden zufügt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5193', 'deDE', null, 'Der nördliche Pylon akzeptiert gelbe, rote und grüne Kristalle.$B$BDer Fokus ist die Energie, die mit den gelben Kristallen als Hauptenergiequelle erzeugt werden kann. Es gibt zwei mögliche Effekte: Einer erzeugt Schaden und einer negiert Schaden. $B$B\'Kristallsprengladung\' erfordert rote und gelbe Kristalle. Damit wird eine gewaltige Explosion erzeugt, die Eure Feinde lähmt.$B$B\'Kristallflicker\' erfordert gelbe und grüne Kristalle. Das verleiht Euch eine heilende Aura.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5194', 'deDE', null, 'Der westliche Pylon akzeptiert rote, grüne und blaue Kristalle.$B$BDer Pylon fokussiert auf die Energie, die mit den roten Kristallen als Hauptenergiequelle erzeugt werden kann. Es gibt zwei mögliche Effekte: Einer erhöht Euren Schutz vor Schaden, einer schwächt die Verteidigung des Gegners.$B$B\'Kristallbarriere\' erfordert rote und grüne Kristalle. Das verleiht Euch eine Erhöhung der Rüstung.$B$B\'Kristallschwächer\' erfordert blaue und rote Kristalle. Damit kann die Rüstung eines bestimmten Ziels verringert werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5195', 'deDE', null, 'Klar doch, $N. Hier ist noch so \'n Teil für Euch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5196', 'deDE', 'Ihr seht den vagen Umriss einer humanoiden Gestalt in der Kapsel; jetzt wisst Ihr, dass dies tatsächlich der Ort ist, den Ihr gesucht habt. Ihr glaubt zwar, dass es Euch irgendwann gelingen wird, die Kapsel zu öffnen, dennoch bleibt sie trotz Anwendung äußerster Gewalt geschlossen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5197', 'deDE', '%s greift ein neues Ziel an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5198', 'deDE', 'Ihr seid perfekter kleiner Hut, $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5199', 'deDE', 'Ograbisi braucht neuen Hut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5200', 'deDE', 'Welche Art von Gesindel wagt es, meine Kammern zu betreten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5201', 'deDE', 'Ah, genau zum richtigen Zeitpunkt für Eure Säuberung, $R. Ich werde die Unreinheiten aus Eurer Seele herausspülen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5202', 'deDE', 'Seid Ihr das, mein werter Tobias???', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5203', 'deDE', 'Vielleicht wird Ograbisi Euren Kopf als kleinen Hut benutzen, wenn ich mit Euch fertig bin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5204', 'deDE', null, 'Dieser einst herrliche Wald leidet unter einem schrecklichen Fluch! Kräfte der Verderbnis lassen das Leben hier zu einem Schatten seiner einstigen Pracht verkommen. Wenn Ihr kein Agent dieser Verderbnis seid, könnt Ihr mir vielleicht helfen, den Teufelswald wieder für das Leben zurückzuerobern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5205', 'deDE', 'Dann legen wir mal los. Meine Ausrüstung sollte im Lagerbereich da oben sein...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5206', 'deDE', null, '\"Taurahe?\"$B$BDie Taurendruidin sieht Euch erwartungsvoll an, aber Euch ist klar, dass Ihr verbal nicht mit ihr kommunizieren könnt. Unbeeindruckt zeigt sie auf sich selbst und lächelt. Sie zeigt auf Euch und lächelt abermals. Schließlich weist sie mehrmals den Weg nach Süden entlang.$B$BVielleicht will sie damit sagen, dass weiter unten an der Straße ein Druide ist, mit dem Ihr sprechen könnt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5207', 'deDE', 'Untersucht diese Zelle, $N. Wenn jemand da drin am Leben ist, müssen wir ihn rausholen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5208', 'deDE', null, 'Dieser einst herrliche Wald leidet unter einem schrecklichen Fluch! Kräfte der Verderbnis lassen das Leben hier zu einem Schatten seiner einstigen Pracht verkommen. Wenn Ihr kein Agent dieser Verderbnis seid, könnt Ihr mir vielleicht helfen, den Teufelswald wieder für das Leben zurückzuerobern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5209', 'deDE', null, '\"Darnassae?\"$B$BDie Nachtelf-Druidin sieht Euch erwartungsvoll an, aber Euch ist klar, dass Ihr verbal nicht mit ihr kommunizieren könnt. Unbeeindruckt zeigt sie auf sich selbst und lächelt. Sie zeigt auf Euch und lächelt abermals. Schließlich weist sie mehrmals den Weg nach Norden entlang.$B$BVielleicht will sie damit sagen, dass weiter oben an der Straße ein Druide ist, mit dem Ihr sprechen könnt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5210', 'deDE', 'Danke, $N! Ich bin frei!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5211', 'deDE', 'HILFE!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5212', 'deDE', 'Ihr seid frei, Dughal! Verschwindet!', 'Ihr seid frei, Dughal! Verschwindet!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5213', 'deDE', 'Gute Arbeit! Wir haben es fast geschafft, $N. Hier geht\'s lang.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5214', 'deDE', 'Das ist es, $N. Mein Zeug müsste in dem Raum da sein. Gebt mir Deckung, ich geh rein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5215', 'deDE', 'Ah, da haben wir\'s!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5216', 'deDE', 'Spürt Ihr die Energie, $N??? Zeit für ROCK \'N\' ROLL!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5217', 'deDE', 'Jetzt müssen wir nur noch Tobias befreien, dann können wir verschwinden. Hier geht\'s lang!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5218', 'deDE', 'Endlich! Ich kann dieses elende Loch verlassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5219', 'deDE', 'Hat Crest Euch geschickt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5220', 'deDE', 'Raus hier, Tobias, Ihr seid frei!', 'Raus hier, Tobias, Ihr seid frei!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5221', 'deDE', 'Gut gemacht, $N. Suchen wir den Ausgang. Ich glaube, ich kenne den Weg. Folgt mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5222', 'deDE', 'Macht das Ding auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5223', 'deDE', 'Ich konnte die beiden noch nie leiden. Gehen wir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5224', 'deDE', 'Macht das Ding auf, aber seid diesmal vorsichtig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5225', 'deDE', 'Dieser unerträgliche Schleimbeutel hat endlich bekommen, was er verdiente. Auf Nimmerwiedersehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5226', 'deDE', '%s spuckt auf den Boden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5227', 'deDE', 'Also dann mal los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5228', 'deDE', 'Macht das Ding auf. Wir müssen uns beeilen. Ich kann diese Dunkeleisenzwerge auf eine Meile Entfernung riechen und ich sage Euch eines, sie KOMMEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5229', 'deDE', 'Er muss in der letzten Zelle sein. Falls sie ihn nicht... getötet haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5230', 'deDE', 'Holt ihn da raus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5231', 'deDE', 'Wir haben es geschafft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5232', 'deDE', 'Wir treffen uns in Maxwells Lager. Dort besprechen wir die nächsten Stufen des Plans und suchen nach einer Möglichkeit, meine Schrifttafeln ohne den Dechiffrier-Ring zu dekodieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5233', 'deDE', 'Grurk bald aufmachen. Dann wir teilen Schatz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5234', 'deDE', 'Dummer Kasten! Dummer, dummer Kasten! GEH AUF! GRURK SAGEN, GEH AUF!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5236', 'deDE', 'Lasst mich versuchen! Ihr zu dumm!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5237', 'deDE', 'Grurk dumm? Ihr versuchen, Kasten zu öffnen! Vielleicht Ihr klug wie Gnom???', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5238', 'deDE', 'Lumurk öffnen diesen Kasten leicht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5239', 'deDE', 'Kasten dumm. Lumurk müde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5240', 'deDE', null, 'Es gibt Pflanzen im Teufelswald, die vor der Verderbnis gerettet werden können, wenn man sie mit Cenarischer Pflanzensalbe einreibt. Ich weiß, wie man die Salbe aus verschiedenen Arten von Dingen herstellt; je nach Euren Fertigkeitswerten habt Ihr verschiedene Möglichkeiten, um mir zu helfen.$B$BAußerdem braucht Ihr ein cenarisches Leuchtfeuer, um hier zu arbeiten; das ist ein Mechanismus, mit dem Ihr die Besudelung und Verderbnis in der Natur erkennen könnt, die man mit bloßem Auge nicht sehen kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5241', 'deDE', null, 'Es gibt Pflanzen im Teufelswald, die vor der Verderbnis gerettet werden können, wenn man sie mit Cenarischer Pflanzensalbe einreibt. Ich weiß, wie man die Salbe aus verschiedenen Arten von Dingen herstellt; je nach Euren Fertigkeitswerten habt Ihr verschiedene Möglichkeiten, um mir zu helfen.$B$BAußerdem braucht Ihr ein cenarisches Leuchtfeuer, um hier zu arbeiten; das ist ein Mechanismus, mit dem Ihr die Besudelung und Verderbnis in der Natur erkennen könnt, die man mit bloßem Auge nicht sehen kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5243', 'deDE', 'Ich brauche ein cenarisches Leuchtfeuer.', 'Ich brauche ein cenarisches Leuchtfeuer.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5244', 'deDE', null, 'Hier ist ein Leuchtfeuer für Euch, $N. Ihr müsst es in Eurem Besitz behalten, wenn Ihr die verderbten Gegenstände finden wollt, die wir brauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5245', 'deDE', null, 'Hier ist ein Leuchtfeuer für Euch, $N. Ihr müsst es in Eurem Besitz behalten, wenn Ihr die verderbten Gegenstände finden wollt, die wir brauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5246', 'deDE', 'Die Überreste sind mit Fliegen und Maden übersät.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5247', 'deDE', 'Ich beschwöre Euch, Sha\'ni Proudtusk!', 'Ich beschwöre Euch, Sha\'ni Proudtusk!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5248', 'deDE', '%s sieht aus, als wäre er gelähmt worden. Er braucht einige Augenblicke, um sich von seinen Verletzungen zu erholen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5249', 'deDE', 'Furorfäuste\' auf $N anwenden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5250', 'deDE', 'Ihr haltet mich besser zurück, sonst wird $N schwere Prügel beziehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5251', 'deDE', 'Das wird Spuren hinterlassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5252', 'deDE', 'Ich wette, jetzt tut es Euch Leid, was, $N!?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5253', 'deDE', 'Ihr habt Euch den falschen Marshal ausgesucht, $N. Bereitet Euch auf Euer Ende vor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5254', 'deDE', 'Vor vielen Jahren errichteten die Nachtelfen, die dieses Land bewohnten, diesen Schrein.$B$BWelche Geheimnisse mag er bergen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5255', 'deDE', 'Ich brauche noch einen funkelnden Stein.', 'Ich brauche noch einen funkelnden Stein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5256', 'deDE', 'Weiter...', 'Weiter...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5257', 'deDE', 'Die Tür ist offen, Kharan. Ihr seid ein freier Mann.', 'Die Tür ist offen, Kharan. Ihr seid ein freier Mann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5258', 'deDE', 'Wo ich herkomme, kriegt man Haue, wenn man die Zellentür eines Mitgefangenen öffnet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5259', 'deDE', 'Klopft denn heutzutage GAR NIEMAND mehr an?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5260', 'deDE', 'Ich habe meine Azsharit-Waffen zerstört! Ich brauche Eure Hilfe, um das Dreigestirn der Macht zu besiegen.', 'Ich habe meine Azsharit-Waffen zerstört! Ich brauche Eure Hilfe, um das Dreigestirn der Macht zu besiegen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5261', 'deDE', 'Ich bedaure, dass ich gegen $N kämpfen muss.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5262', 'deDE', 'He! Was gibt\'s?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5263', 'deDE', 'Dafür werdet Ihr bezahlen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5264', 'deDE', 'Oh, jetzt bin ich aber sauer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5265', 'deDE', 'Nein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5266', 'deDE', 'He, meine Taschen wurden geplündert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5267', 'deDE', 'Jetzt reicht\'s! Kein Bier mehr, bis dieser Schlamassel behoben ist!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5268', 'deDE', 'Ha! Ha! Ha! Danke, dass Ihr mich befreit habt, ihr Dummköpfe. Ich werde es Euch vergelten, indem ich Euch das Fleisch von den Knochen brenne.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5269', 'deDE', null, 'Dem haben wir\'s gezeigt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5270', 'deDE', 'Die in den Pylonen von Un\'Goro verborgenen Geheimnisse sind jetzt Euer. Ihr könnt die Pylonen jederzeit besuchen, ihre Macht nutzen und nützliche Rohstoffe bekommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5271', 'deDE', 'Vor uns könnt Ihr Euch nicht verstecken. Gleich sollt Ihr im Feuer schmoren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5274', 'deDE', 'Willkommen in Tanaris, $N. Wie kann ich Euch heute dienlich sein? Ihr seht mir nicht wie ein Schmied aus, aber das Aussehen kann täuschen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5275', 'deDE', null, 'Oh, oh! Ich hau ab!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5281', 'deDE', 'Ich verschwinde von hier!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5283', 'deDE', '%s beginnt, eine Lavabrut zu beschwören!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5284', 'deDE', 'Konversation liegt mir nicht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5285', 'deDE', null, 'Oh nein! Unsere Zeit ist abgelaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5286', 'deDE', '%s stößt einen Warnruf aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5287', 'deDE', null, 'Mal sehen... Wenn ich Recht habe, dann... Hmmm...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5288', 'deDE', null, 'Blau plus rot... Ich würde mal sagen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5289', 'deDE', null, '$N, das ist es! Endlich weiß ich Bescheid!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5290', 'deDE', 'Sie sind verloren, $R. Von den Elfenmeistern geschriebene Buchbände. Sie wurden in aller Welt verstreut, als die Geißel über Quel\'Thalas kam.$B$BJetzt suchen wir nach diesen Buchbänden. Suchen sie, um ihre Geheimnisse zu bergen - und verlorene Macht zurückzugewinnen.$B$BIch suche die zehn, die Meister Kariel Winthalus schrieb. Solltet Ihr einen Buchband finden, so lasst mich seine magischen Eigenschaften absorbieren, und für Euch werde ich ein Gemisch der Macht anfertigen... natürlich hat alles seinen Preis. Ihr werdet meinen Preis recht hoch finden, aber bezahlen werdet Ihr ihn.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5291', 'deDE', 'Ich präsentiere Euch den \'Buchband des Nachsinnens\'.', 'Ich präsentiere Euch den \'Buchband des Nachsinnens\'.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5292', 'deDE', 'Ah... Ihr wurdet also fündig.$B$B<Mathredis zeigt auf das Buch in Eurem Besitz.>$B$BHabt Ihr die erforderlichen Komponenten gesammelt?$B$BDie im Buchband aufgelisteten Manifestationen, Schwachkopf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5293', 'deDE', '<Mathredis schaut den Buchband durchdringend an.>$B$BSeid Ihr wegen eines Gemischs gekommen? Habt Ihr Euch an die Anweisungen in dem Buchband gehalten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5294', 'deDE', 'Gebt mir die Arkanarien und ich stelle das Gemisch her, sterbliches Wesen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5295', 'deDE', 'Wirklich ein seltener Fund.$B$B<Mathredis nickt.>$B$B Wenn Ihr weitermachen möchtet, brauche ich die Komponenten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5296', 'deDE', 'Die Gier führt Euch zu mir zurück.$B$B<Mathredis lacht.>$B$BEin weiser Draenei sagte mir einst, dass sie - die Gier - allen Völkern dieser Welt gemeinsam sei. Ich muss ihm Recht geben.$B$BHabt Ihr die in dem Buchband aufgelisteten Komponenten gesammelt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5297', 'deDE', 'Eindringlinge in der Manufaktur? Meine Konstrukte werden Euch vernichten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5298', 'deDE', 'Möge das Licht Euch heute beschützen.', 'Möge das Licht Euch heute beschützen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5299', 'deDE', 'Ich möchte weiter in den Wegen des Lichts unterwiesen werden.', 'Ich möchte weiter in den Wegen des Lichts unterwiesen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5300', 'deDE', 'Gewalt! Beschädigung von Eigentum! Niemand darf durch!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5301', 'deDE', 'Ihr müsst die Bar verlassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5302', 'deDE', 'Ihr seid nicht mehr willkommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5303', 'deDE', 'IHR WERDET NICHT BEDIENT!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5304', 'deDE', 'Was Ihr wollen von Schleifer?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5305', 'deDE', null, 'Hallo, $N. Solltet Ihr Unterstützung benötigen, um Eure Mission vollends anzuschließen, müsst Ihr nur fragen. Mein Wächter Schleifer kann Euch helfen, die Schattenseidewilderer zu besiegen. Benutzt diesen Stab des Befehls, um Schleifer im Kampf zu befehligen. Er ist darauf trainiert, NUR Schattenseidewilderer anzugreifen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5306', 'deDE', 'Ich brauche Hilfe, um die Schattenseidewilderer zu besiegen.', 'Ich brauche Hilfe, um die Schattenseidewilderer zu besiegen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5307', 'deDE', 'Trinkt aus! Es ist noch genügend Nachschub da!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5308', 'deDE', 'Viel Spaß damit! Ihr werdet nirgendwo besseres Bier finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5309', 'deDE', 'Habt Ihr das Dunkeleisenbier gekostet? Es ist das Beste!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5310', 'deDE', 'Versucht den Eber! Ein neues Rezept von mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5311', 'deDE', 'Danke, dass Ihr die törichten Senatoren aus dem Weg geräumt habt. Doch jetzt soll Euch durch Ragnaros\' mächtigsten Diener Euer Schicksal ereilen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5312', 'deDE', 'Eure Anstrengungen sind vergebens, Dummköpfe! Ihr werdet mich nie besiegen können!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5313', 'deDE', 'Die waren sowieso nur im Weg.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5314', 'deDE', 'Ha! Ihr begreift nicht einmal im Ansatz, wie vergeblich Eure Anstrengungen sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5315', 'deDE', 'Etwas Besseres habt Ihr nicht drauf? Habt Ihr wirklich geglaubt, Ihr könntet jemand Ehrfurchtgebietenden wie mich besiegen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5316', 'deDE', 'Das Auktionshaus', 'Das Auktionshaus', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5317', 'deDE', 'Wo das Auktionshaus ist? Das liegt direkt beim Stadttor von Eisenschmiede, wenn Ihr unsere schöne Stadt betretet. Ihr findet es gleich gegenüber dem Gewölbe.', 'Wo das Auktionshaus ist? Das liegt direkt beim Stadttor von Eisenschmiede, wenn Ihr unsere schöne Stadt betretet. Ihr findet es gleich gegenüber dem Gewölbe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5318', 'deDE', 'Anu therador mali, $C. Wohin kann ich Euch geleiten?', 'Anu therador mali, $C. Wohin kann ich Euch geleiten?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5319', 'deDE', 'Wie kann ich Euch bei Eurer Reise helfen?', 'Wie kann ich Euch bei Eurer Reise helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5320', 'deDE', 'Braucht Ihr Führung zu Eurem Ziel?', 'Braucht Ihr Führung zu Eurem Ziel?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5321', 'deDE', 'In welche Richtung kann ich Euch weisen?', 'In welche Richtung kann ich Euch weisen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5324', 'deDE', 'Könnt Ihr mir wohl die Richtung weisen?', 'Könnt Ihr mir wohl die Richtung weisen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5325', 'deDE', 'Zu welchem der vielen wundersamen Orte in Darnassus darf ich Euch geleiten?', 'Zu welchem der vielen wundersamen Orte in Darnassus darf ich Euch geleiten?', '31882');
