INSERT INTO `broadcast_text_locale` VALUES ('5869', 'deDE', 'Ihr gehen zu inneres Ring von Apothekarium. Lavinia Crowe da sein.', 'Ihr gehen zu inneres Ring von Apothekarium. Lavinia Crowe da sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5870', 'deDE', 'Lady Euch lehren magisch Gegenstände zu machen. Sie in inneres Ring von Apothekarium.', 'Lady Euch lehren magisch Gegenstände zu machen. Sie in inneres Ring von Apothekarium.', '31882');
