INSERT INTO `broadcast_text_locale` VALUES ('5881', 'deDE', 'Ihr gehen zu inneres Ring von Kriegsviertel. Dort Brom Killian sein.', 'Ihr gehen zu inneres Ring von Kriegsviertel. Dort Brom Killian sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5882', 'deDE', 'Brom Killian Euch lehren, glänziges Zeugs für mir ausgraben. Er in inneres Ring von Kriegsviertel.', 'Brom Killian Euch lehren, glänziges Zeugs für mir ausgraben. Er in inneres Ring von Kriegsviertel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5883', 'deDE', 'Killian Hagey lehren Kürschnerei in Schurkenviertel an inneres Ring.', 'Killian Hagey lehren Kürschnerei in Schurkenviertel an inneres Ring.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5884', 'deDE', 'Wir glänzend Haken haben. Wir das von Killian Hagey kriegen. Der uns lehren, wie tote Tierens häuten. Ihr haben schön Haut. Ihr ihn finden in inneres Ring von Schurkenviertel.', 'Wir glänzend Haken haben. Wir das von Killian Hagey kriegen. Der uns lehren, wie tote Tierens häuten. Ihr haben schön Haut. Ihr ihn finden in inneres Ring von Schurkenviertel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5885', 'deDE', 'Ihr suchen nach Josef Gregorian. Er in Magieviertel auf inneres Ringseit von grün Kanal.', 'Ihr suchen nach Josef Gregorian. Er in Magieviertel auf inneres Ringseit von grün Kanal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('5886', 'deDE', 'Hmmm, das gut Idee. Vielleicht wir hingehen sollten und paar Stichens ersetzen lassen? Er gut nähen. Ihr finden Josef Gregorian in inneres Ring von Magieviertel.', 'Hmmm, das gut Idee. Vielleicht wir hingehen sollten und paar Stichens ersetzen lassen? Er gut nähen. Ihr finden Josef Gregorian in inneres Ring von Magieviertel.', '31882');
