INSERT INTO `broadcast_text_locale` VALUES ('6374', 'deDE', 'Ihr wagt es, diesen heiligen Boden zu entweihen?', 'Ihr wagt es, diesen heiligen Boden zu entweihen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6375', 'deDE', 'Werde durch die Klinge gesäubert, Abschaum!', 'Werde durch die Klinge gesäubert, Abschaum!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6376', 'deDE', 'Der Feind steht vor der Tür! Vorrücken! Wir müssen die Stellung halten!', 'Der Feind steht vor der Tür! Vorrücken! Wir müssen die Stellung halten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6377', 'deDE', 'Zieht euch zurück und haltet die Stellung! Wir dürfen nicht versagen, sonst ist alles verloren!', 'Zieht euch zurück und haltet die Stellung! Wir dürfen nicht versagen, sonst ist alles verloren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6378', 'deDE', 'Zieht euch zur Treppe zurück und verteidigt sie!', 'Zieht euch zur Treppe zurück und verteidigt sie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6379', 'deDE', 'Sie sind in die Halle der Lichter eingedrungen! Wir müssen die Eindringlinge aufhalten!', 'Sie sind in die Halle der Lichter eingedrungen! Wir müssen die Eindringlinge aufhalten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6380', 'deDE', 'Der Scharlachrote Kreuzzug wird hier nicht sein Ende finden! Ihr werdet unsere Reihen nicht durchbrechen!', 'Der Scharlachrote Kreuzzug wird hier nicht sein Ende finden! Ihr werdet unsere Reihen nicht durchbrechen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6381', 'deDE', 'Hallo, $GReisender:Reisende;. Es ist selten, dass ich Besuch hier draußen bekomme.$B$BIch heiße Rivern und ich bilde die Frostsäbler von Winterspring zu Reittieren aus.$B$BDiese Katzen sind die wildesten aller Säbler und am schwierigsten zu zähmen. Nur jemand, der aufs Höchste geachtet wird und dieses Vertrauen auch verdient, darf den Versuch unternehmen, auf einer von ihnen zu reiten.$B$BEin solches Reittier verkaufe ich nicht einfach so jedem dahergelaufenen Abenteurer; oh nein, Ihr müsst hart dafür arbeiten und Euch nicht nur meine Anerkennung, sondern auch die der Säbler verdienen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6382', 'deDE', 'Informationen über den chromatischen Knochenpanzer.', 'Informationen über den chromatischen Knochenpanzer.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('6383', 'deDE', 'Informationen über glänzende chromatische Schuppen.', 'Informationen über glänzende chromatische Schuppen.', '31882');
