INSERT INTO `broadcast_text_locale` VALUES ('6636', 'deDE', 'Lok-Tar! Ihr seht aus, also könntet Ihr Hilfe gebrauchen.', 'Lok-Tar! Ihr seht aus, also könntet Ihr Hilfe gebrauchen.', '31882');
