INSERT INTO `broadcast_text_locale` VALUES ('6725', 'deDE', 'Ihr seht ein wenig verloren aus. Ich kann Euch helfen.', 'Ihr seht ein wenig verloren aus. Ich kann Euch helfen.', '31882');
