INSERT INTO `broadcast_text_locale` VALUES ('6797', 'deDE', 'Seh ich etwa wie ein Fremdenführer aus? Na gut. Was braucht Ihr?', 'Seh ich etwa wie ein Fremdenführer aus? Na gut. Was braucht Ihr?', '31882');
