INSERT INTO `broadcast_text_locale` VALUES ('6923', 'deDE', 'Druiden? Hier in der Gegend habe ich keine Druiden gesehen. Aber in Sturmwind gibt es einen Distrikt, wo hauptsächlich Nachtelfen leben.', 'Druiden? Hier in der Gegend habe ich keine Druiden gesehen. Aber in Sturmwind gibt es einen Distrikt, wo hauptsächlich Nachtelfen leben.', '31882');
