INSERT INTO `broadcast_text_locale` VALUES ('6925', 'deDE', 'Ich habe gehört, dass es in Sturmwind einen Zwerg gibt, der recht berühmt für seine Jagdkünste ist. Vielleicht solltet Ihr ihn einmal fragen, ob er Euch helfen kann.', 'Ich habe gehört, dass es in Sturmwind einen Zwerg gibt, der recht berühmt für seine Jagdkünste ist. Vielleicht solltet Ihr ihn einmal fragen, ob er Euch helfen kann.', '31882');
