INSERT INTO `broadcast_text_locale` VALUES ('8270', 'deDE', 'Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.', 'Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8271', 'deDE', 'Ich möchte meine Talente verlernen.', 'Ich möchte meine Talente verlernen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8272', 'deDE', 'Ja. Das möchte ich.', 'Ja. Das möchte ich.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8273', 'deDE', 'Ich möchte meine Talente verlernen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8274', 'deDE', null, 'Ich möchte meine Talente verlernen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8275', 'deDE', 'Was kann ich für Euch tun?', 'Was kann ich für Euch tun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8276', 'deDE', 'Mehr hab ich nicht, was ich jemandem beibringen könnte! Das ist zu wenig für jemanden, der so viel weiß wie Ihr! Ihr Typen von der Horde müsst mit Nogg in Orgrimmar sprechen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8277', 'deDE', 'Wüteriche, fasst!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8278', 'deDE', 'In Ordnung, brechen wir auf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8279', 'deDE', null, 'Wer wagt es, den Außenposten der Silverwing anzugreifen?!?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8280', 'deDE', 'Silverwing gehört uns!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8281', 'deDE', 'Geht und meldet, dass der Außenposten eingenommen wurde. Wir werden hierbleiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8282', 'deDE', 'Macht Euch bereit. Silverwing liegt gleich hinter dieser Kehre.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8283', 'deDE', 'Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8284', 'deDE', 'Alle bereit?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8285', 'deDE', null, 'Ich will Euch nichts Böses. Ich spähe die Stacheleber aus und mache mir Notizen über ihre Aktivitäten. Meine Leute haben etwas Merkwürdiges bemerkt - eine Ausstrahlung oder Dunkelheit -, das aus diesem Bereich kommt. Ich bin gekommen, um das zu untersuchen, und was ich gefunden habe, ist recht verstörend.$B$BEs scheint einen untoten Diplomaten zu geben, der die Kunst des Arkanen äußerst gut beherrscht. Ich glaube nicht, dass er einer der Verlassenen ist... Ich glaube, es ist schlimmer.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8286', 'deDE', null, 'Was für ein Zufall. Normalerweise muss ich meinen Unterschlupf verlassen, um etwas zu essen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8287', 'deDE', null, 'Ihr müsst lernen, wo Euer Platz ist, sterbliches Wesen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8288', 'deDE', null, 'Diese sinnlose Anstrengung langweilt mich. Ich werde Euch alle von oben verbrennen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8289', 'deDE', 'Ihr wisst genug, um gefährlich zu sein, aber für mich sieht es immer noch so aus, als würdet Ihr Ausbildung benötigen, $N.', 'Ihr wisst genug, um gefährlich zu sein, aber für mich sieht es immer noch so aus, als würdet Ihr Ausbildung benötigen, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8290', 'deDE', null, 'Mir scheint, dass Ihr noch eine Lektion braucht, sterbliche Wesen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8291', 'deDE', 'Euer Wille ist nicht stark genug, um die Kräfte zu manipulieren, die wir kontrollieren, $C.', 'Euer Wille ist nicht stark genug, um die Kräfte zu manipulieren, die wir kontrollieren, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8292', 'deDE', 'Ich suche nach größerem Wissen über die Wege des Hexenmeisters.', 'Ich suche nach größerem Wissen über die Wege des Hexenmeisters.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8293', 'deDE', '$N, wie kann ich Eure Ausbildung in den Augen der Erdenmutter voranbringen?', '$N, wie kann ich Eure Ausbildung in den Augen der Erdenmutter voranbringen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8294', 'deDE', 'Vielfältig sind die Wege der Erdenmutter. Mögen Eure Vorfahren über Euch wachen, $C.', 'Vielfältig sind die Wege der Erdenmutter. Mögen Eure Vorfahren über Euch wachen, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8295', 'deDE', 'Ich suche eine weiterführende druidische Ausbildung, um ein besseres Verständnis des Willens der Erdenmutter zu erlangen.', 'Ich suche eine weiterführende druidische Ausbildung, um ein besseres Verständnis des Willens der Erdenmutter zu erlangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8296', 'deDE', 'Es ist weise, dass Ihr Euch in Eurem Wunsch nach mehr Hexenmeisterwissen vor mir erniedrigt, $N.', 'Es ist weise, dass Ihr Euch in Eurem Wunsch nach mehr Hexenmeisterwissen vor mir erniedrigt, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8297', 'deDE', 'Ich kann es mir nicht leisten, mit Euresgleichen meine Zeit zu verschwenden, $C.', 'Ich kann es mir nicht leisten, mit Euresgleichen meine Zeit zu verschwenden, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8298', 'deDE', 'Ich werfe mich zur weiteren Ausbildung Euch zu Füßen, oh Meister.', 'Ich werfe mich zur weiteren Ausbildung Euch zu Füßen, oh Meister.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8299', 'deDE', 'Ich kann schon allein aus der Art, wie Ihr dort steht, erkennen, dass Ihr $Gkein Krieger:keine Kriegerin; seid. $B$BWarum macht Ihr nicht etwas, das sicher ist, wie zum Beispiel ein Buch lesen.', 'Ich kann schon allein aus der Art, wie Ihr dort steht, erkennen, dass Ihr $Gkein Krieger:keine Kriegerin; seid. $B$BWarum macht Ihr nicht etwas, das sicher ist, wie zum Beispiel ein Buch lesen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8300', 'deDE', 'Die Dunkelheit umgibt Euch nicht, $C. Hört mit dem Geschwätz auf und geht mir aus den Augen! Hinfort mit Euch!', 'Die Dunkelheit umgibt Euch nicht, $C. Hört mit dem Geschwätz auf und geht mir aus den Augen! Hinfort mit Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8301', 'deDE', 'Ich fürchte, Euer Dunkelheit-Quotient ist nicht ausreichend, als dass sich noch länger mit Euch zu reden lohnt. Geht weiter bitte.', 'Ich fürchte, Euer Dunkelheit-Quotient ist nicht ausreichend, als dass sich noch länger mit Euch zu reden lohnt. Geht weiter bitte.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8302', 'deDE', 'Ihr seid kein Krieger... Wie wollt Ihr mehr als einen Tag unter meiner Ausbildung bestehen?$B$BEin $C, der sich für einen Krieger hält. Ha!', 'Ihr seid kein Krieger... Wie wollt Ihr mehr als einen Tag unter meiner Ausbildung bestehen? $B$BEin $C, der sich für einen Krieger hält. Ha!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8303', 'deDE', 'Ich sehe in Euch nicht den Geist des Kriegers. Ich kann Euch nichts beibringen, was Euch helfen würde, $C.', 'Ich sehe in Euch nicht den Geist des Kriegers. Ich kann Euch nichts beibringen, was Euch helfen würde, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8304', 'deDE', '%s erscheint, um den Fathom-Stein zu beschützen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8305', 'deDE', 'Jämmerlich. Ihr beide verdient die Ausbildung nicht, die ihr hier bekommt, wenn das euer Bestes war.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8306', 'deDE', 'Lysta, beschwöre untote Gefangene.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8307', 'deDE', 'Lysta, beschwört sie hervor... die Monstrosität!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8308', 'deDE', 'Bitte teilt Euer Wissen mit mir, Kriegshäuptling.', 'Bitte teilt Euer Wissen mit mir, Kriegshäuptling.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8309', 'deDE', 'Die Shattered-Hand konnte Spione überall in die obere Zitadelle von Blackrock einschleusen. Obwohl die Informationen nur langsam kamen, haben wir Entdeckungen gemacht, die nicht ignoriert werden können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8310', 'deDE', 'Was für Entdeckungen?', 'Was für Entdeckungen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8311', 'deDE', 'Es scheint, als wäre der Einfluss des schwarzen Drachenschwarms gewachsen und über die Grenzen der brennenden Steppe hinaus bis in das Königreich Sturmwind gelangt! Der Thronräuber wurde inzwischen demaskiert und vertrieben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8312', 'deDE', 'Thronräuber?', 'Thronräuber?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8313', 'deDE', 'So ist es. Die Brutmutter des schwarzen Drachenschwarms hat Illusion und Betrügerei angewendet, um sich als Mitglied des königlichen Hofes zu verkleiden. Und was noch schlimmer ist, sie übernahm die Kontrolle über den derzeitigen Hochlord von Sturmwind, Bolvar Fordragon.$B$BKein Wunder, dass solche närrischen militärischen Entscheidungen gefällt wurden. Die in letzter Zeit erfolgten Bewegungen der Streitkräfte der Allianz hatten uns sehr verwundert. Wir wissen jetzt, dass der schwarze Drachenschwarm versucht hatte, das Reich zu teilen - es von Innen zu zerstören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8314', 'deDE', 'Mit allem gebotenen Respekt, Kriegshäuptling - warum lassen wir nicht zu, dass es zerstört wird? Stärkt das nicht unsere Position?', 'Mit allem gebotenen Respekt, Kriegshäuptling - warum lassen wir nicht zu, dass es zerstört wird? Stärkt das nicht unsere Position?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8315', 'deDE', '<Thrall lacht.>$B$BIhr müsst noch so viel lernen, $R.$B$BIhr seid zu jung, um Euch an die Kriege und das Blutvergießen zu erinnern, das weit in der Vergangenheit liegt. Zu jung, um Euch an die Bürde des Fluchs zu erinnern, die von unseren Leuten getragen wird.$B$BUnsere Position gegenüber der Allianz ist im Moment recht angreifbar. Scharmützel und kleinere Kämpfe finden statt, aber es herrscht Frieden - etwas, das unsere Leute seit Jahrtausenden nicht mehr kannten. Würdet Ihr das aufgeben und uns wieder in die Klauen des Chaos stürzen, nur diesmal gegen einen neuen Feind? Einer, der erst ruhen wird, wenn wir alle vernichtet sind?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8316', 'deDE', 'Ich... So habe ich das nicht gesehen, Kriegshäuptling.', 'Ich... So habe ich das nicht gesehen, Kriegshäuptling.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8317', 'deDE', 'Nein, $N, das habt Ihr nicht. Ich gebe Euch aber keine Schuld, denn Eure Gedanken gleichen den meinen, als ich noch jung war. Ungestüm, rebellisch und häufig ohne einen zusammenhängenden Gedanken! Eigenschaften, die ich mir nicht mehr leisten kann.$B$BAber genug gejammert. $R, ich habe eine Aufgabe für Euch, eine, die Ihr vielleicht nicht überleben werdet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8318', 'deDE', 'Ich lebe nur, um Euch zu dienen, Kriegshäuptling. Mein Leben ist leer und bedeutungslos ohne Eure Führung.', 'Ich lebe nur, um Euch zu dienen, Kriegshäuptling. Mein Leben ist leer und bedeutungslos ohne Eure Führung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8319', 'deDE', '<Thrall lacht.>$B$BDas merke ich mir... Und jetzt passt auf, $N. Die Brutmutter des schwarzen Drachenschwarms, Onyxia, wurde von ihrer Machtposition in Sturmwind verjagt. Sie ist entkommen und zu ihrem Unterschlupf in den Marschen von Dustwallow geflüchtet.$B$BWir dürfen nicht zulassen, dass sie so nahe an unseren großen Städten lebt. Wir müssen zuschlagen bevor wir geschlagen werden!$B$BIch will, dass sie vernichtet wird, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8320', 'deDE', 'Natürlich, Kriegshäuptling!', 'Natürlich, Kriegshäuptling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8321', 'deDE', '%s bricht zusammen, als er seinen Meister sterben sieht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8322', 'deDE', 'Willkommen, $Gmein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BWir sind die Bewahrer des Gleichgewichts, jetzt und immerdar, während Malfurion in Träumen liegt. Vergesst das niemals.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8323', 'deDE', 'Willkommen, $Gmein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BWir sind die Bewahrer des Gleichgewichts, jetzt und immerdar, während Malfurion in Träumen liegt. Vergesst das niemals.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8324', 'deDE', 'Myranda, habt Ihr ausreichend Augäpfel, um ein weiteres Amulett anzufertigen? Ich habe das alte verloren.', 'Myranda, habt Ihr ausreichend Augäpfel, um ein weiteres Amulett anzufertigen? Ich habe das alte verloren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8325', 'deDE', 'Ich sehe hier am Hafen einige interessante Leute kommen und gehen. Passt auf Euch auf, $N. Man weiß nie genau, wem man vertrauen kann, bei den ganzen zwielichtigen Aktivitäten, die hier ablaufen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8326', 'deDE', 'Ihr seid also wegen der Warsongäxte gekommen. Das wurde auch Zeit, $N. Diese Kiste steht jetzt schon seit Tagen hier herum.$B$BSchafft sie weg, wann immer Ihr bereit seid.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8327', 'deDE', null, 'Wir sollten abhauen, bevor sie feststellen, dass ich frei bin!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8328', 'deDE', null, 'Passt auf! Wir werden angegriffen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8329', 'deDE', null, 'Vielen Dank für Eure Hilfe. Von hier aus kenne ich den Weg zurück.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8330', 'deDE', 'In dieser Region des Steinkrallengebirges gibt es viele gefährliche Höhlen. Die Erforschung dieser Höhlen hält viele Abenteuer für die bereit, die ihr Leben für Ruhm und Ehre riskieren wollen.$B$BVielleicht sucht Ihr nach Reichtümern, die Ihr nur erreichen könnt, wenn Ihr Eurem Herz und Eurer Seele folgt, $C.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8331', 'deDE', '%s dreht durch, als Misha im Kampf fällt!', '%s dreht durch, als Misha im Kampf fällt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8332', 'deDE', null, 'Wartet bitte, während ich versuche, diese Splitter wieder zusammenzufügen, sodass sie wieder ein Ganzes bilden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8333', 'deDE', null, 'Ishnu... Oshalan thara dormil... Anu\'dorin...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8334', 'deDE', 'Fisch sehr lecker. Ihr kommen lernen angeln? Fisch essen? Mmmmm...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8335', 'deDE', 'Ich benötige Ausbildung, Lumak.', 'Ich benötige Ausbildung, Lumak.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8336', 'deDE', 'Ja, ich kann Euch beibringen zu angeln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8337', 'deDE', 'Ich benötige Ausbildung, Grimnur.', 'Ich benötige Ausbildung, Grimnur.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8338', 'deDE', null, 'Ja, ich konnte das Relikt erneut versiegeln, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8339', 'deDE', null, 'Bitte, verlasst diesen Ort, $R. Ihr wisst nichts von dem, was uns Sorgen bereitet...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8340', 'deDE', 'Wo bekomme ich verzaubertes Thorium?', 'Wo bekomme ich verzaubertes Thorium?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8341', 'deDE', 'Wo bekomme ich Kristallwiederherstellung?', 'Wo bekomme ich Kristallwiederherstellung?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8342', 'deDE', null, 'Verzaubertes Thorium ist ein sehr gefragtes Metall. Am besten sprecht Ihr einen Schmiedekunstfachmann oder einen Minenarbeiter darauf an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8343', 'deDE', null, 'Kristallflicker ist ein schwer zu beschaffender Gegenstand.$B$BIch glaube, dass er vom Krater von Un\'Goro kommt, also begebt Ihr Euch am besten dorthin.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8344', 'deDE', 'Wir leiden vielleicht unter einer Dürre, aber an Fleisch herrscht kein Mangel! Mögt Ihr ein wenig in meinen Waren stöbern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8345', 'deDE', 'Ich möchte mich mal umsehen, Dirge.', 'Ich möchte mich mal umsehen, Dirge.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8346', 'deDE', null, 'Kann Zamja Euch helfen, $R?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8347', 'deDE', 'Ich benötige Ausbildung, Zamja.', 'Ich benötige Ausbildung, Zamja.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8348', 'deDE', 'Ich sorge für sie. Jawoll, Daryl Riknussun, zu Euren Diensten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8349', 'deDE', 'Ich benötige Ausbildung, Daryl.', 'Ich benötige Ausbildung, Daryl.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8350', 'deDE', 'Ihr seid eine kleine Mücke für die Fäulnisklauen! Sterbt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8351', 'deDE', 'Nein! Ihr könnt doch nicht stärker sein als die Fäulnisklauen! Nein!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8352', 'deDE', 'Heeellft miiir... Ri... Rippennn... ge... gebrochen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8353', 'deDE', 'Sterbe... Sehe das Licht... herrrrlich....', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8354', 'deDE', 'Ich... Ich glaube, ich sehe das Ende nahen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8355', 'deDE', 'Ich bin gerettet! Danke, Doktor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8356', 'deDE', 'Meine Eingeweide quellen mir aus dem Leib! HILFE!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8357', 'deDE', 'Ich werde es nicht schaffen... Tod kommt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8358', 'deDE', 'Auf Wiedersehen, grausame Welt... Ich verlasse dich heute... auf Wiedersehen... auf Wiedersehen... auf Wiedersehen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8359', 'deDE', 'HURRA! ICH BIN GERETTET!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8360', 'deDE', 'Gehirn zerstört... Mörser... kap... putt... läuft aus... ster...be...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8361', 'deDE', 'Süße, süße Umarmung... nimm mich auf...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8362', 'deDE', 'Der Doktor hat alles zum Guten gewendet! HURRA!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8363', 'deDE', 'Ich muss Euch leider mitteilen, dass Ihr Eure Prüfung nicht bestanden habt, Doktor.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8364', 'deDE', 'Ich bin ein Orc der Medizin, $R. Wenn Ihr hergekommen seid, um zu lernen, werde ich mein Bestes geben, um Euch zu unterrichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8365', 'deDE', 'Ich benötige Ausbildung, Arnok.', 'Ich benötige Ausbildung, Arnok.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8366', 'deDE', null, 'Möge diese böse Aura von diesem Artefakt verbannt werden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8367', 'deDE', null, 'Ich kann fühlen, dass die Anwesenheit des Bösen schwindet...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8368', 'deDE', null, 'Seid Ihr ein Schüler der Medizin?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8369', 'deDE', 'Ich benötige Ausbildung, Nissa.', 'Ich benötige Ausbildung, Nissa.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8370', 'deDE', null, 'Warum seid Ihr hier, $C?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8371', 'deDE', null, 'Meine Leute sind so kaputt wie das Relikt, dass Ihr reparieren wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8372', 'deDE', null, 'Wenn auch nichts die Vergangenheit ändern kann, so heilt doch die Zeit alle Wunden. Danke, dass Ihr mir gezeigt habt, dass das möglich ist, $N. Alles Gute.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8373', 'deDE', 'Kel\'Thuzad? Wenn Ihr diesen Namen kennt, habt Ihr wohl das Buch gelesen, das ich Euch zu holen schickte.$B$BLeider ist Eure Antwort nicht richtig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8374', 'deDE', 'Gul\'dan. Ein Name, den nicht viele in Azeroth gehört haben.$B$BLeider ist es nicht die richtige Antwort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8375', 'deDE', 'Eine interessante Antwort auf meine Frage... und falsch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8376', 'deDE', 'Ah, Ner\'zhul. Ein Name, den man nicht leichtfertig aussprechen sollte, aber ich hatte Euch ja um eine Antwort gebeten.$B$BUnd Ner\'zhul ist die richtige.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8377', 'deDE', 'Meine Antwort ist Kel\'Thuzad.', 'Meine Antwort ist Kel\'Thuzad.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8378', 'deDE', 'Meine Antwort ist Gul\'dan.', 'Meine Antwort ist Gul\'dan.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8379', 'deDE', 'Meine Antwort ist Kil\'jaeden.', 'Meine Antwort ist Kil\'jaeden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8380', 'deDE', 'Meine Antwort ist Ner\'zhul.', 'Meine Antwort ist Ner\'zhul.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8381', 'deDE', 'Meine Antwort ist Ysera.', 'Meine Antwort ist Ysera.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8382', 'deDE', 'Ah, Ysera die Träumerin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8383', 'deDE', 'Meine Antwort ist Neltharion.', 'Meine Antwort ist Neltharion.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8384', 'deDE', 'Meine Antwort ist Alexstrasza.', 'Meine Antwort ist Alexstrasza.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8385', 'deDE', 'Meine Antwort ist Malygos.', 'Meine Antwort ist Malygos.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8386', 'deDE', 'Neltharion, hmm? So gut wie jede andere Antwort, nicht wahr?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8387', 'deDE', 'Alexstrasza die Lebensbinderin. Eine prachtvolle Kreatur.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8388', 'deDE', 'Malygos also?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8389', 'deDE', 'Seid gegrüßt, $C. Kann ich Euch irgendwie helfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8390', 'deDE', 'Meine Frage an Euch, $C, ist diese:$BAus welchem der Aspekte, die von den Titanen geschaffen wurden, wurde die schändliche Kreatur erschaffen, die als Deathwing bekannt ist?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8391', 'deDE', 'Es ist immer nett, wenn jemand mit mir sprechen möchte. Wie laufen die Dinge bei Euch, $C?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8392', 'deDE', 'Beantwortet mir folgende Frage, $N:$BWas für eine Kreatur war der Lichkönig, bevor er die Seuche über Lordaeron brachte?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8393', 'deDE', 'Das ist die falsche Antwort, $N. Und nun fort mit Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8394', 'deDE', 'Leider nicht, $N. Und nun fühlt die Schande, die Ihr über Euch selbst gebracht habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8395', 'deDE', null, 'In Position!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8396', 'deDE', null, 'Für die große Mutter!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8397', 'deDE', null, 'Krieger der Nacht... kommt herbei!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8398', 'deDE', '%s greift Karangs Banner an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8399', 'deDE', 'Ja, Ihr wisst ein paar Dinge! Wenn Ihr mehr lernen wollt, müsst Ihr mit Georgio Bolero in Sturmwind sprechen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8400', 'deDE', 'Die Thoriumbruderschaft besitzt das Wissen, das erforderlich ist, um die hochheiligen Waffen und Rüstungsteile des Feuerlords herzustellen, aber uns fehlen die Leute, um die Materialien zu beschaffen, die für die Herstellung notwendig sind.$B$BIch, Lokhtos Darkbargainer, wurde von der Bruderschaft ermächtigt, die erhabenen Anleitungen für Materialien einzutauschen, die vom geschmolzenen Kern gesammelt wurden.$B$BPräsentiert Euer Angebot, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8401', 'deDE', 'Zeigt mir, worauf ich Zugriff habe, Lokhtos.', 'Zeigt mir, worauf ich Zugriff habe, Lokhtos.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8402', 'deDE', 'Ihr möchtet Eure Dämonen mächtiger machen? Das wird Euch einiges kosten, aber Ihr seid hier richtig.', 'Ihr möchtet Eure Dämonen mächtiger machen? Das wird Euch einiges kosten, aber Ihr seid hier richtig.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8403', 'deDE', 'Ihr seid kein Hexenmeister und ich habe keine Zeit für Euch. Hebt Euch hinweg!', 'Ihr seid kein Hexenmeister und ich habe keine Zeit für Euch. Hebt Euch hinweg!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8404', 'deDE', 'Zeigt mir Eure Auswahl an Zauberfolianten.', 'Zeigt mir Eure Auswahl an Zauberfolianten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8405', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8406', 'deDE', 'Ihr seid zur Ausbildung hergekommen, um sie an Eure Tiere weiterzugeben?', 'Ihr seid zur Ausbildung hergekommen, um sie an Eure Tiere weiterzugeben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8407', 'deDE', 'Ah, ich helfe nur Jägern und ihren Tieren.', 'Ah, ich helfe nur Jägern und ihren Tieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8408', 'deDE', 'Lehrt mich den Weg der Wildtiere.', 'Lehrt mich den Weg der Wildtiere.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8409', 'deDE', 'Furcht liegt in Eurem Herzen, $N. Wenn Ihr wirklich bereit seid, Vorsha der Peitscherin gegenüberzutreten, dann kommt und sprecht mit mir.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8410', 'deDE', 'Jetzt müssen wir warten, $N. Es wird nicht lange dauern, bis die Naga erkennen, was wir getan haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8411', 'deDE', 'Die Königliche Apothekervereinigung hat nur wenig Geduld für Narren, ebenso wie ich. Wenn wir das nächste Mal über meine Rösser sprechen, begegnen Euch die Einwohner von Unterstadt vielleicht mit Ehrfurcht. Bis dahin...', 'Die Königliche Apothekervereinigung hat nur wenig Geduld für Narren, ebenso wie ich. Wenn wir das nächste Mal über meine Rösser sprechen, begegnen Euch die Einwohner von Unterstadt vielleicht mit Ehrfurcht. Bis dahin...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8412', 'deDE', 'Seid auf der Hut, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8413', 'deDE', 'Vielleicht können wir uns einen Moment lang ausruhen. Aber ich werde nicht aufgeben, bis wir Vorsha gegenübergetreten sind!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8414', 'deDE', 'Meine Reittiere stelle ich nur denjenigen zur Verfügung, die von den Einwohnern Orgrimmars mit Ehrfurcht behandelt werden. Wenn Ihr Euch uns beweist, dann lasse ich Euch meine mächtigen Wölfe begutachten.', 'Meine Reittiere stelle ich nur denjenigen zur Verfügung, die von den Einwohnern Orgrimmars mit Ehrfurcht behandelt werden. Wenn Ihr Euch uns beweist, dann lasse ich Euch meine mächtigen Wölfe begutachten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8415', 'deDE', 'Tut mir leid, $GMann:Mädel;, aber meine Raptoren verkauf ich keinem, der sich nich\' bewiesen hat. Erst wenn Ihr klar Stellung bezieht und der Darkspearstamm Euch mit Ehrfurcht begegnet, dürft Ihr einen von meinen Raptoren ha\'m.', 'Tut mir leid, $GMann:Mädel;, aber meine Raptoren verkauf ich keinem, der sich nich\' bewiesen hat. Erst wenn Ihr klar Stellung bezieht und der Darkspearstamm Euch mit Ehrfurcht begegnet, dürft Ihr einen von meinen Raptoren ha\'m.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8416', 'deDE', 'Ihr seid meinem Volk nicht bekannt. Kodos sind mächtige Reittiere, die von meinem Volk mit viel Liebe und Pflege groß gezogen werden, und solche Tiere überlässt man nicht einfach so jedem beliebigen Fremden. Solange die Ältesten von Donnerfels Euch nicht mit Ehrfurcht begegnen, kann ich Euch leider keinen Kodo verkaufen.', 'Ihr seid meinem Volk nicht bekannt. Kodos sind mächtige Reittiere, die von meinem Volk mit viel Liebe und Pflege groß gezogen werden, und solche Tiere überlässt man nicht einfach so jedem beliebigen Fremden. Solange die Ältesten von Donnerfels Euch nicht mit Ehrfurcht begegnen, kann ich Euch leider keinen Kodo verkaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8417', 'deDE', 'Leider begegnen Euch die Nachtelfen von Darnassus und Teldrassil nicht mit Ehrfurcht, $C. Erst wenn Ihr Euch uns gegenüber durch Taten bewiesen habt - und nicht nur durch Worte -, werde ich Euch erlauben, einen Blick auf meine Auswahl an Reittieren zu werfen.', 'Leider begegnen Euch die Nachtelfen von Darnassus und Teldrassil nicht mit Ehrfurcht, $C. Erst wenn Ihr Euch uns gegenüber durch Taten bewiesen habt - und nicht nur durch Worte -, werde ich Euch erlauben, einen Blick auf meine Auswahl an Reittieren zu werfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8418', 'deDE', 'Bleibt zurück! Kinder sollten nicht hier sein! Hinfort, Kleines!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8419', 'deDE', 'Eindringlinge! Greift die Eindringlinge an!', 'Eindringlinge! Greift die Eindringlinge an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8420', 'deDE', 'Ruhig, Leute. Wir werden schon bald zuschlagen', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8421', 'deDE', 'Mit etwas Glück gewinnen wir heute ein paar schöne Narben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8422', 'deDE', 'Diese Wälder lassen meine Hauer jucken...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8423', 'deDE', 'Es heißt, Duriel sei ein gar wilder Nachtelf. Das wird einen guten Kampf geben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8424', 'deDE', 'Nach diesem Kampf kriegen unsere Axtstiele etliche Kerben mehr.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8425', 'deDE', 'Bleibt ruhig, Leute. Wir wollen unsere Position nicht verraten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8426', 'deDE', 'Eisenherz? Ich hörte, dass Rennmeister Kronkrider auf dem Turm weiß, wo er ist.', 'Eisenherz? Ich hörte, dass Rennmeister Kronkrider auf dem Turm weiß, wo er ist.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8427', 'deDE', 'Ihr sucht nach Eisenherz? Tja, das habt Ihr nicht von mir, aber... wenn Ihr Kronkrider auf dem Turm fragt, sagt er es Euch vielleicht...', 'Ihr sucht nach Eisenherz? Tja, das habt Ihr nicht von mir, aber... wenn Ihr Kronkrider auf dem Turm fragt, sagt er es Euch vielleicht...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8428', 'deDE', 'Es tut mir Leid, aber ich bin nicht bereit, Pferde an jemanden zu verkaufen, der vom Königreich Sturmwind nicht wenigstens mit Ehrfurcht bedacht wird. Diese Rösser sind die besten, die sich überhaupt irgendwo finden lassen, und wir wollen doch nicht, dass sie in die Hand unserer Feinde geraten, oder?$B$BDas soll natürlich nicht heißen, dass Ihr ein Feind seid... Verdient Euch einfach das Vertrauen von Sturmwind, dann können wir miteinander ins Geschäft kommen.', 'Es tut mir Leid, aber ich bin nicht bereit, Pferde an jemanden zu verkaufen, der vom Königreich Sturmwind nicht wenigstens mit Ehrfurcht bedacht wird. Diese Rösser sind die besten, die sich überhaupt irgendwo finden lassen, und wir wollen doch nicht, dass sie in die Hand unserer Feinde geraten, oder?$B$BDas soll natürlich nicht heißen, dass Ihr ein Feind seid... Verdient Euch einfach das Vertrauen von Sturmwind, dann können wir miteinander ins Geschäft kommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8429', 'deDE', 'Nicht so schnell, $GJunge:Mädel;... Ich gebe die Zügel eines mächtigen Widders doch nicht jemandem in die Hand, der sich nicht als Freund der Zwerge von Eisenschmiede erwiesen hat. Sobald Euch die Einwohner von Eisenschmiede mit Ehrfurcht begegnen, zeige ich Euch gern alle Widder, die bei mir zum Verkauf stehen.', 'Nicht so schnell, $GJunge:Mädel;... Ich gebe die Zügel eines mächtigen Widders doch nicht jemandem in die Hand, der sich nicht als Freund der Zwerge von Eisenschmiede erwiesen hat. Sobald Euch die Einwohner von Eisenschmiede mit Ehrfurcht begegnen, zeige ich Euch gern alle Widder, die bei mir zum Verkauf stehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8430', 'deDE', 'Ähm... So gut kenne ich Euch leider nicht, tut mir Leid. Will sagen, ich bin offizieller Vertragshändler für Roboschreiter, und da kann ich meine Reittiere wirklich nur an Leute verkaufen, die von den Gnomen mit Ehrfurcht bedacht werden. Wenn die Gnome von Gnomeregan Euch mit Ehrfurcht begegnen, zeige ich Euch gern, was ich auf Lager habe.', 'Ähm... So gut kenne ich Euch leider nicht, tut mir Leid. Will sagen, ich bin offizieller Vertragshändler für Roboschreiter, und da kann ich meine Reittiere wirklich nur an Leute verkaufen, die von den Gnomen mit Ehrfurcht bedacht werden. Wenn die Gnome von Gnomeregan Euch mit Ehrfurcht begegnen, zeige ich Euch gern, was ich auf Lager habe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8431', 'deDE', 'Die Zwerge von Eisenschmiede müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Zwerge von Eisenschmiede müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8432', 'deDE', 'Die Verlassenen von Unterstadt müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Verlassenen von Unterstadt müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8433', 'deDE', 'Die Verbannten von Gnomeregan müssen Euch mit Ehrfurcht begegnen, bevor ich Euch lehre, wie ein Roboschreiter gesteuert wird, $C.', 'Die Verbannten von Gnomeregan müssen Euch mit Ehrfurcht begegnen, bevor ich Euch lehre, wie ein Roboschreiter gesteuert wird, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8434', 'deDE', 'Die Menschen von Sturmwind müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Menschen von Sturmwind müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8435', 'deDE', 'Die Nachtelfen von Darnassus müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Nachtelfen von Darnassus müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8436', 'deDE', 'Die Orcs von Orgrimmar müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Orcs von Orgrimmar müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8437', 'deDE', 'Die Tauren von Donnerfels müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Tauren von Donnerfels müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8438', 'deDE', 'Die Trolle des Darkspearstamms müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', 'Die Trolle des Darkspearstamms müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8439', 'deDE', 'Der halbe Weg ist geschafft...', null, '31882');
