INSERT INTO `broadcast_text_locale` VALUES ('8458', 'deDE', 'Von Ratten überrannt! Helft einem armen Piraten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8459', 'deDE', 'Leistet Euren Beitrag, um die Tiefenbahn sauber zu halten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8460', 'deDE', 'Bedrängt mich nicht, Leute!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8461', 'deDE', 'Eure Stärke nährt mich, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8462', 'deDE', 'Der Kleine wird sich wohl in einer der Kisten verstecken. Viel Glück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8463', 'deDE', 'Monty is\' mein Name und Ratten sin\' mein Beruf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8464', 'deDE', 'Ich bin bereit, das Eichhörnchen zu suchen, Monty.', 'Ich bin bereit, das Eichhörnchen zu suchen, Monty.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8465', 'deDE', 'In den Kasten, meine Süßen! Genau so. Einer nach dem anderen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8466', 'deDE', 'Gewinner! Wir haben hier einen Gewinner!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8467', 'deDE', 'Die stärksten Bälge kommen von Kodos, die sich zum Sterben in diese Einöde begeben haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8468', 'deDE', 'Eure Fertigkeiten übersteigen die meinen. Geht und sucht Una, die einen Laden auf dem Zentralplateau von Donnerfels hat. Sie kann Euch die Anleitung bieten, die Ihr sucht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8470', 'deDE', 'Es tut mir Leid, aber ich kann keine weiteren Lehrlinge ausbilden. Sucht Mak in Donnerfels auf. Er kann Euch die Grundlagen lehren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8471', 'deDE', 'Ihr müsst Euch mir erst beweisen, dann werde ich Euch ausbilden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8472', 'deDE', 'Ihr seid jetzt bereit, die Feinheiten des Gerbens von Kodobälgen zu lernen. Lasst uns beginnen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8473', 'deDE', null, 'Zwischen dem Licht und dem Dunkel liegen Schatten... und diese Schatten verfügen über Kräfte, wie Ihr sie noch nicht gesehen habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8474', 'deDE', null, 'Zwischen dem Licht und dem Dunkel liegen Schatten... und diese Schatten verfügen über Kräfte, wie Ihr sie noch nicht gesehen habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8475', 'deDE', 'Ihr findet keinen besseren Rattenkebab diesseits von Khaz Modan!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8476', 'deDE', 'Ich möchte etwas kaufen, Nipsy.', 'Ich möchte etwas kaufen, Nipsy.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8477', 'deDE', 'Ihr seht aus, als ob Ihr Arbeit gebrauchen könntet, $G Kumpel:Mädel;. Ihr solltet die Bahn besteigen und meinen Bruder Monty beim Tiefenbahndepot in Eisenschmiede besuchen. Ich bin sicher, dass er etwas für Euch zu tun hat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8478', 'deDE', 'Seid bloß wachsam, Fremdling.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8479', 'deDE', 'Auf dem Ravenholdt-Anwesen kennt man Euch nicht, $C.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8480', 'deDE', 'Dreist. Meister Ravenholdt mag dreist nicht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8481', 'deDE', 'Schön, Euch wiederzusehen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8482', 'deDE', 'Seid gegrüßt, $C. Habt Ihr schon mit Fahrad gesprochen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8483', 'deDE', 'Das Syndikat besteht aus Dummköpfen - unorganisiert, ungelernt und faul...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8484', 'deDE', 'Ihr seid wahrlich schlauer geworden, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8485', 'deDE', 'Es ist eine Ehre, Euch wiederzusehen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8486', 'deDE', 'Ich glaube, Simone hat Euch vorhin gesucht, $N. Habt Ihr schon mit ihr gesprochen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8487', 'deDE', '$G Meister:Herrin; $N! Meister Kang hat nach Euch gefragt! Vielleicht ist Eure Zeit gekommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8488', 'deDE', 'Eure Anwesenheit macht uns stark, $GMeister:Herrin; $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8489', 'deDE', 'Die Geschichten Eurer Heldentaten hört man im ganzen Ravenholdt-Anwesen, $GMeister:Herrin; $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8490', 'deDE', 'Ich bin dieser Unterhaltung nicht würdig, $GMeister:Herrin;.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8491', 'deDE', 'Ich träume davon, eines Tage unter Eurem Befehl zu dienen, $GMeister:Herrin; $N. Falls Ihr mich jemals solcher Dinge für würdig befinden solltet...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8492', 'deDE', 'Auf dem Anwesen sprach man überall von Eurer Ankunft, $GMeister:Herrin; $N. Wir alle sind von Eurer Anwesenheit geehrt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8493', 'deDE', 'Ein Wurm wie Ihr wagt es, seinen Fuß auf das Ravenholdt-Anwesen zu setzen?', 'Ein Wurm wie Ihr wagt es, seinen Fuß auf das Ravenholdt-Anwesen zu setzen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8494', 'deDE', 'Es wäre eine Leistung, ohne eine Klinge im Rücken oder Gift in den Adern hier herauszukommen.', 'Es wäre eine Leistung, ohne eine Klinge im Rücken oder Gift in den Adern hier herauszukommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8495', 'deDE', 'Ihr macht Euch gut, $N. Ich habe gute Dinge gehört...', 'Ihr macht Euch gut, $N. Ich habe gute Dinge gehört...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8496', 'deDE', 'Ihr solltet mit Winstone sprechen. Ich habe gehört, dass er nach Dieben mit Eurer Fertigkeit sucht.', 'Ihr solltet mit Winstone sprechen. Ich habe gehört, dass er nach Dieben mit Eurer Fertigkeit sucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8497', 'deDE', 'Wunderbar. Einfach wunderbar. Ihr seid eine Inspiration für uns alle, $N.', 'Wunderbar. Einfach wunderbar. Ihr seid eine Inspiration für uns alle, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8498', 'deDE', 'Ich beneide Euch wirklich, $N. Ihr leistet erstaunliche Arbeit.', 'Ich beneide Euch wirklich, $N. Ihr leistet erstaunliche Arbeit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8499', 'deDE', 'Eure Zeit ist gekommen, $N. Meister Kang wartet...', 'Eure Zeit ist gekommen, $N. Meister Kang wartet...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8500', 'deDE', 'Vielleicht werdet Ihr eines Tages die Person sein, von der die Legenden sprechen...', 'Vielleicht werdet Ihr eines Tages die Person sein, von der die Legenden sprechen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8501', 'deDE', 'Ich habe wie ein Baby geweint, als ich von Euren Heldentaten hörte. Meister Ravenholdt täte gut daran, einen Lehrling wie Euch zu haben.', 'Ich habe wie ein Baby geweint, als ich von Euren Heldentaten hörte. Meister Ravenholdt täte gut daran, einen Lehrling wie Euch zu haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8502', 'deDE', 'Wenn ich nicht durch die Pflicht an Meister Ravenholdt gebunden wäre, würde ich Euch bis ans Ende der Welt folgen, $GMeister:Herrin; $N. Ihr seid Anmut und Schlauheit in Person.', 'Wenn ich nicht durch die Pflicht an Meister Ravenholdt gebunden wäre, würde ich Euch bis ans Ende der Welt folgen, $GMeister:Herrin; $N. Ihr seid Anmut und Schlauheit in Person.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8503', 'deDE', null, 'Möge Elune mit Euch sein, $C. Der Tempel des Mondes ist den verängstigten Bürgern von Auberdine zu Hilfe gekommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8504', 'deDE', 'Ich bin ein Testgeistheiler.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8505', 'deDE', 'Warum habt Ihr mich getötet, $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8506', 'deDE', 'Shoja\'my steht drüben vor dem Gasthaus. Und jetzt geht weiter, ich bin beschäftigt.', 'Shoja\'my steht drüben vor dem Gasthaus. Und jetzt geht weiter, ich bin beschäftigt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8507', 'deDE', 'Um Tiere kümmern? Hauptsache Ihr räumt hinter ihnen auf! Sucht nach Shoja\'my vor dem Gasthaus.', 'Um Tiere kümmern? Hauptsache Ihr räumt hinter ihnen auf! Sucht nach Shoja\'my vor dem Gasthaus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8508', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8509', 'deDE', 'Geht zum Tal der Ehre und sucht Xon\'cha in der Nähe der Ställe auf.', 'Geht zum Tal der Ehre und sucht Xon\'cha in der Nähe der Ställe auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8510', 'deDE', 'Xon\'cha wird Eure zusätzlichen Tiere unterbringen. Ihr findet ihn im Tal der Ehre bei den Ställen zwischen der Arena und der Jägerhalle. Nun hinfort!', 'Xon\'cha wird Eure zusätzlichen Tiere unterbringen. Ihr findet ihn im Tal der Ehre bei den Ställen zwischen der Arena und der Jägerhalle. Nun hinfort!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8511', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8512', 'deDE', 'Nur ein Narr läuft nichts ahnend in eine Diebeshöhle. Und dieser Narr kommt selten wieder heraus...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8513', 'deDE', 'Ah ja, da müsst Ihr Seikwa aufsuchen. Ich glaube, ich habe sie zuletzt vor dem Gasthaus gesehen.', 'Ah ja, da müsst Ihr Seikwa aufsuchen. Ich glaube, ich habe sie zuletzt vor dem Gasthaus gesehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8514', 'deDE', 'Seikwa kann sich um Eure tierischen Freunde kümmern, wenn Ihr das wollt. Ihr findet sie vor dem Gasthaus.', 'Seikwa kann sich um Eure tierischen Freunde kümmern, wenn Ihr das wollt. Ihr findet sie vor dem Gasthaus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8515', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8516', 'deDE', 'Ihr benötigt die Hilfe von Bulrug. Den findet Ihr draußen auf der unteren Anhöhe, zwischen dem Gasthaus und der Donnerfels-Bank.', 'Ihr benötigt die Hilfe von Bulrug. Den findet Ihr draußen auf der unteren Anhöhe, zwischen dem Gasthaus und der Donnerfels-Bank.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8517', 'deDE', 'Die Besorgnis, die Ihr für das Wohlergehen Eurer tierischen Begleiter an den Tag legt, ist lobenswert. Bulrug auf der unteren Anhöhe kann Euch dabei helfen. Ihr findet ihn zwischen der Bank und dem Gasthaus.', 'Die Besorgnis, die Ihr für das Wohlergehen Eurer tierischen Begleiter an den Tag legt, ist lobenswert. Bulrug auf der unteren Anhöhe kann Euch dabei helfen. Ihr findet ihn zwischen der Bank und dem Gasthaus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8518', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8519', 'deDE', 'Ist kaum zu glauben, ich weiß, aber Ihr findet Morganus, den Stallmeister, tatsächlich vor den Ställen.', 'Ist kaum zu glauben, ich weiß, aber Ihr findet Morganus, den Stallmeister, tatsächlich vor den Ställen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8520', 'deDE', 'Ihr seid nicht sehr schlau, was? Hmm, also wenn ich ein Stallmeister wäre, dann würde ich mich in der Nähe, sagen wir mal, der Ställe aufhalten... Sucht dort nach Morganus.', 'Ihr seid nicht sehr schlau, was? Hmm, also wenn ich ein Stallmeister wäre, dann würde ich mich in der Nähe, sagen wir mal, der Ställe aufhalten... Sucht dort nach Morganus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8521', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8522', 'deDE', 'Anya Maulray bei Gasthaus auf oberer Ring von des Handwerksviertel.', 'Anya Maulray bei Gasthaus auf oberer Ring von des Handwerksviertel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8523', 'deDE', 'Wir viele Tiere wollen. Anya Maulray Euch helfen sich um sie kümmern. Ihr sie finden bei Gasthaus auf oberer Ring von Handwerksviertel.', 'Wir viele Tiere wollen. Anya Maulray Euch helfen sich um sie kümmern. Ihr sie finden bei Gasthaus auf oberer Ring von Handwerksviertel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8524', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8525', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8526', 'deDE', 'Ihr könnt Euch darauf verlassen, dass sich Alassin gut um all Eure Tiere kümmern wird. Er befindet sich in der Enklave des Cenarius, wo sich die Jäger versammeln.', 'Ihr könnt Euch darauf verlassen, dass sich Alassin gut um all Eure Tiere kümmern wird. Er befindet sich in der Enklave des Cenarius, wo sich die Jäger versammeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8527', 'deDE', 'Seit Hunderten von Jahren kümmert sich Alassin um die Tiere, die unsere Jäger zähmen. Seine Pflege der Tiere wird von den Jägern hier sehr geschätzt. Wenn auch Ihr Euch seiner Dienste versichern möchtet, dann sucht ihn in der Enklave des Cenarius auf.', 'Seit Hunderten von Jahren kümmert sich Alassin um die Tiere, die unsere Jäger zähmen. Seine Pflege der Tiere wird von den Jägern hier sehr geschätzt. Wenn auch Ihr Euch seiner Dienste versichern möchtet, dann sucht ihn in der Enklave des Cenarius auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8528', 'deDE', 'Wir sind alle Teile eines viel größeren Puzzles, $N. So wie Ihr ein Teil eines Puzzles zu seinem Ziel führt, so werden auch wir durch einen anderen geführt.$B$BUnd vielleicht werden die, die uns leiten, selbst von einer noch größeren Macht geleitet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8529', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8530', 'deDE', 'Ah, die sanfte Seriadne. Alle Tiere sind in ihrer Obhut sicher. Ihr findet sie vor dem Gasthaus in Dolanaar.', 'Ah, die sanfte Seriadne. Alle Tiere sind in ihrer Obhut sicher. Ihr findet sie vor dem Gasthaus in Dolanaar.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8531', 'deDE', 'Ihr sucht jemanden, der sich um Tiere kümmert? Dann solltet Ihr Seriadne aufsuchen, gleich neben dem Gasthaus in Dolanaar. Sie ist perfekt dafür.', 'Ihr sucht jemanden, der sich um Tiere kümmert? Dann solltet Ihr Seriadne aufsuchen, gleich neben dem Gasthaus in Dolanaar. Sie ist perfekt dafür.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8532', 'deDE', 'Die gute alte Erma, sie kümmert sich jetzt schon seit Jahren um die hiesigen Tiere. Ich bin sicher, dass sie sich auch gern um alle kümmern wird, die Ihr ihr bringt. Ihr findet sie beim Gasthaus Zur Höhle des Löwen.', 'Die gute alte Erma, sie kümmert sich jetzt schon seit Jahren um die hiesigen Tiere. Ich bin sicher, dass sie sich auch gern um alle kümmern wird, die Ihr ihr bringt. Ihr findet sie beim Gasthaus Zur Höhle des Löwen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8533', 'deDE', 'Erma kümmert sich um alle Tiere hier. Sie befindet sich beim Gasthaus Zur Höhle des Löwen, wenn Ihr mit ihr sprechen wollt.', 'Erma kümmert sich um alle Tiere hier. Sie befindet sich beim Gasthaus Zur Höhle des Löwen, wenn Ihr mit ihr sprechen wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8534', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8535', 'deDE', 'Warum habt Ihr mich getötet, $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8536', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8537', 'deDE', 'Jenova Stoneshield drüben im Zwergendistrikt dürfte Euren Vorstellungen entsprechen. Sie kümmert sich um die Tiere der Jäger, während die bei ihrem Vater ausgebildet werden.', 'Jenova Stoneshield drüben im Zwergendistrikt dürfte Euren Vorstellungen entsprechen. Sie kümmert sich um die Tiere der Jäger, während die bei ihrem Vater ausgebildet werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8538', 'deDE', 'Wenn Ihr Euer Tier unterbringen müsst, während Ihr ein anderes ausbildet, dann solltet Ihr mit Jenova Stoneshield drüben im Zwergendistrikt sprechen.', 'Wenn Ihr Euer Tier unterbringen müsst, während Ihr ein anderes ausbildet, dann solltet Ihr mit Jenova Stoneshield drüben im Zwergendistrikt sprechen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8539', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8540', 'deDE', 'Tja, $GJungchen:Mädel;, Ihr könnt Shelby Stoneflint direkt vor der Brauerei Donnerbräu in Kharanos finden. Sie wird sich um alle Tiere kümmern, die Ihr habt.', 'Tja, $GJungchen:Mädel;, Ihr könnt Shelby Stoneflint direkt vor der Brauerei Donnerbräu in Kharanos finden. Sie wird sich um alle Tiere kümmern, die Ihr habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8541', 'deDE', 'Shelby Stoneflint betreut sehr viele Tiere für die hiesigen Jäger. Ihr findet sie drüben bei der Brauerei Donnerbräu, falls Ihr wollt, dass sie sich um ein Tier kümmern soll.', 'Shelby Stoneflint betreut sehr viele Tiere für die hiesigen Jäger. Ihr findet sie drüben bei der Brauerei Donnerbräu, falls Ihr wollt, dass sie sich um ein Tier kümmern soll.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8542', 'deDE', 'Der Stallmeister', 'Der Stallmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8543', 'deDE', 'Die Pflege von Tieren ist etwas, das Ulbrek Firehand ziemlich ernst nimmt. Er treibt sich normalerweise bei den Jägern in der Halle der Waffen herum, falls Ihr wollt, dass er sich für Euch um ein oder zwei Tiere kümmern soll.', 'Die Pflege von Tieren ist etwas, das Ulbrek Firehand ziemlich ernst nimmt. Er treibt sich normalerweise bei den Jägern in der Halle der Waffen herum, falls Ihr wollt, dass er sich für Euch um ein oder zwei Tiere kümmern soll.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8544', 'deDE', 'Drüben in der Halle der Waffen könnt Ihr unseren hiesigen Stallmeister, Ulbrek Firehand, finden. Er wird sicherstellen, dass alle Tiere, die Ihr habt, gut gefüttert und betreut werden.', 'Drüben in der Halle der Waffen könnt Ihr unseren hiesigen Stallmeister, Ulbrek Firehand, finden. Er wird sicherstellen, dass alle Tiere, die Ihr habt, gut gefüttert und betreut werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8545', 'deDE', 'Ihr glaubt also, Ihr hättet schon gewonnen? Mir scheint, dass Ihr noch eine Lektion in Sachen Schmerz braucht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8546', 'deDE', 'Hilfe! Wachen! Das Ding wird auf mich treten!', 'Hilfe! Wachen! Das Ding wird auf mich treten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8547', 'deDE', 'Ich habe etwas für Euch, $N. Könnte wichtig sein.', 'Ich habe etwas für Euch, $N. Könnte wichtig sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8548', 'deDE', 'Darf ich das bitte haben?', 'Darf ich das bitte haben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8549', 'deDE', 'Willkommen im Hauptquartier von SI:7, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8550', 'deDE', '$N, das hier kam heute Morgen für Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8551', 'deDE', 'Boten von Ravenholdt haben mir heute Morgen diesen Brief ausgehändigt. Er ist an Euch adressiert, $N.', 'Boten von Ravenholdt haben mir heute Morgen diesen Brief ausgehändigt. Er ist an Euch adressiert, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8552', 'deDE', 'Nur Mut! Der Schmerz vergeht und $GFrauen mögen Männer mit Narben:Männer mögen Frauen mit Narben;!', 'Nur Mut! Der Schmerz vergeht und $GFrauen mögen Männer mit Narben:Männer mögen Frauen mit Narben;!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8553', 'deDE', 'Ich brauche Hilfe.', 'Ich brauche Hilfe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8554', 'deDE', 'Zeigt Eurem Feind gegenüber Stärke und wir werden die Oberhand erlangen!', 'Zeigt Eurem Feind gegenüber Stärke und wir werden die Oberhand erlangen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8555', 'deDE', 'Das wird ein harter Kampf, $N. Bleibt nahe bei mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8556', 'deDE', 'Das ist die Kohlenpfanne, $N. Löscht sie. Vorsha ist eine Bestie, die von niemandem Lob verdient!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8557', 'deDE', 'Mist, nicht da! Ich frage mich, wo ich das Ding nur gelassen habe...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8558', 'deDE', 'Ihr habt meine tiefe Anerkennung, $N. Ich danke Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8559', 'deDE', 'Wo habe ich denn bloß dieses Begleitdokument hingelegt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8560', 'deDE', 'Eine ziemlich fesselnde Geschichte. Jetzt weiß ich, warum so viel Staub auf diesem Buch war.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8561', 'deDE', 'So viele Bücherregale, so wenig Zeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8562', 'deDE', 'Ich sollte mit Dewey über ein Beschriftungssystem sprechen, damit diese Bücher besser geordnet sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8563', 'deDE', 'Ein Buch von einem Goblin über militärische Siege der Gnome... und alle Seiten sind leer. Typisch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8564', 'deDE', 'Ich werde noch eine Weile in diesem Bereich patrouillieren, damit wirklich alles sicher ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8565', 'deDE', 'Kehrt bitte nach Zoram\'gar zurück und meldet dem Warsongläufer unseren Erfolg.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8566', 'deDE', 'Seid Ihr sicher, dass Ihr bereit seid? Falls uns keine Gruppe Eurer Verbündeten hilft, werden wir mit Sicherheit verlieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8567', 'deDE', '$N, ich spüre, dass die große Bestie näher kommt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8568', 'deDE', 'Seht, direkt an der Wasseroberfläche!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8569', 'deDE', 'Wir haben es geschafft, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8570', 'deDE', null, 'Ihr wollt mich von meinem Gelege weglocken? Für diese Frechheit sollt Ihr büßen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8571', 'deDE', 'Ja, $C? Erstattet Ihr Bericht?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8572', 'deDE', 'KOMMT HERBEI, MEINE DIENER! VERTEIDIGT EUREN HERRN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8573', 'deDE', 'NIEMAND BESIEGT DIE LEBENDE FLAMME! KOMMT IHR DIENER DES FEUERS! VORWÄRTS, IHR KREATUREN DES HASSES! EUER MEISTER RUFT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8574', 'deDE', 'Zermalmt sie!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8575', 'deDE', 'MACHT SIE NIEDER, SOLDATEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8576', 'deDE', 'Drängt sie zu ihrem Stützpunkt zurück! Lasst keinen einzigen Soldaten der Allianz auf diesem Schlachtfeld am Leben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8577', 'deDE', 'Sammelt Euch um Eure Lieutenants und Kommandanten! Schützt unsere Stützpunkte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8578', 'deDE', 'Majordomus aktiviert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8579', 'deDE', 'Wer kontrolliert die Coldtooth-Mine?', 'Wer kontrolliert die Coldtooth-Mine?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8580', 'deDE', 'Die Coldtooth-Mine gehört den Kobolden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8581', 'deDE', 'Die Coldtooth-Mine gehört der Allianz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8582', 'deDE', 'Die Coldtooth-Mine gehört der Horde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8583', 'deDE', 'Die Coldtooth-Mine liegt im Gebiet der Horde, in den Bergen nördlich vom Friedhof der Frostwolf.$B$BSie wird von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8584', 'deDE', 'Die Coldtooth-Mine liegt im Gebiet der Horde, in den Bergen nördlich vom Friedhof der Frostwolf.$B$BSie wird von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8585', 'deDE', 'Die Coldtooth-Mine befindet sich im Gebiet der Horde, in den Bergen nördlich vom Friedhof der Frostwolf.$B$BSie wird von den Kobolden kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8586', 'deDE', 'Wer kontrolliert die Irondeep-Mine?', 'Wer kontrolliert die Irondeep-Mine?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8587', 'deDE', 'Die Irondeep-Mine befindet sich im Gebiet der Allianz, nördlich vom Stormpike-Friedhof.$B$BSie wird von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8588', 'deDE', 'Die Irondeep-Mine befindet sich im Gebiet der Allianz, nördlich vom Stormpike-Friedhof.$B$BSie wird von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8589', 'deDE', 'Die Irondeep-Mine befindet sich im Gebiet der Allianz, nördlich vom Stormpike-Friedhof.$B$BSie wird von den Troggs kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8590', 'deDE', 'Dieses Monument ist all denen gewidmet, die beim Schutz von Sturmwind gefallen sind. Unsere Leute haben Unglaubliches durchgemacht, um ihre Freiheit zu bewahren und ihr Schicksal in die eigenen Hände zu nehmen. Hier erinnern wir uns an jedes einzelne Opfer, das unsere Bürger während des ersten Krieges gebracht haben, der unser Volk erschütterte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8591', 'deDE', 'Die Irondeep-Mine gehört der Allianz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8592', 'deDE', 'Die Irondeep-Mine gehört der Horde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8593', 'deDE', 'Die Irondeep-Mine gehört den Troggs.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8594', 'deDE', 'Die Allianz hat die Coldtooth-Mine eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8595', 'deDE', 'Die Allianz hat die Irondeep-Mine eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8596', 'deDE', 'Die Horde hat die Coldtooth-Mine eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8597', 'deDE', 'Die Horde hat die Irondeep-Mine eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8599', 'deDE', 'Die Troggs nehmen die Irondeep-Mine wieder ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8600', 'deDE', 'Die Kobolde nehmen die Coldtooth-Mine wieder ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8601', 'deDE', 'Im Alteractal toben Kämpfe und es gibt wenig Nachschub.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8602', 'deDE', 'Mein Bedarf an Nachschub ist vorerst gedeckt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8603', 'deDE', 'Nehmt mich an!', 'Nehmt mich an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8604', 'deDE', null, 'Gebt mir Deckung, Soldat! Ich muss zu unserem Stützpunkt zurückkehren und meine Flotte für einen Luftangriff vorbereiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8605', 'deDE', null, 'WUSCH! Geschafft!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8606', 'deDE', null, 'Wir dürfen keine Zeit verschwenden. Ich muss sofort zurück zum Stützpunkt. Gebt mir Deckung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8607', 'deDE', null, 'Lasst uns zum Geschäft kommen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8608', 'deDE', 'Guse? Jeztor? Sind sie... flache Drehung?? Meere von Süderstade??', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8609', 'deDE', 'Ich muss zu meinem Ross zurückkehren! Sofort suchen und zerstören! Gebt mir Deckung! Auf sechs Uhr! LOS, LOS, LOS!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8610', 'deDE', 'Ich mache dich fertig, du kümmerliche Allianz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8611', 'deDE', 'Gute Arbeit, Soldat! Jetzt zünden wir die Feuer an und entfachen ihren Zorn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8612', 'deDE', '%s lässt einen bedrohlichen Schrei los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8613', 'deDE', null, 'Soldaten von Frostwolf, kommt mir zu Hilfe! Die Zeit ist reif, um den Furor der Elemente zu entfesseln!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8614', 'deDE', 'Mein Bedarf an Vorräten ist vorerst gedeckt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8615', 'deDE', 'Diese Expedition ist eine schreckliche Verschwendung militärischer Ressourcen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8616', 'deDE', 'WER WAGT ES, LOKHOLAR ZU BESCHWÖREN?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8617', 'deDE', 'Ich werde das Blut Tausender Soldaten der Allianz vergießen... Niemand wird sich gegen die Macht des Eislords stellen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8618', 'deDE', 'Ich labe mich an deinem Leid, sterbliches Geschöpf. Vermische deine Essenz mit Lokholar!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8619', 'deDE', '%s hat durch einen Todesschrei die Silithiden in ihrem Schwarmbau aufgescheucht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8620', 'deDE', null, 'Der Todesschrei von %s hat den Silithidenschwarmbau in der Nähe aufgescheucht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8621', 'deDE', null, 'Das Blut unserer Feinde soll das Schlachtfeld tränken. Die großen Elementare werden unserem Ruf folgen und alles zerschmettern, was sich uns in den Weg stellt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8622', 'deDE', null, '%s kichert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8623', 'deDE', null, 'Der Eislord wird die gesamte Armee der Stormpike verschlingen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8624', 'deDE', 'Antwort kann durch den wirbelnden Nether nicht gesehen werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8625', 'deDE', null, 'Seht, wie das Blut in den Schnee sickert, Kinder. Die Elementare werden sich mit uns verbünden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8626', 'deDE', null, 'Es ist vollbracht! Der Eislord ist da! Beugt Euch der Macht der Horde, Ihr Narren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8627', 'deDE', 'Vergesst es einfach!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8628', 'deDE', 'Das ist einer Antwort nicht wert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8629', 'deDE', 'Soll das ein Witz sein?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8630', 'deDE', 'Beschwört mich erst wieder, wenn Ihr eine richtige Frage habt, klar?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8631', 'deDE', '%s ist wütend!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8632', 'deDE', null, 'Kanalisiert Eure Energien auf den Altar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8633', 'deDE', 'Es ist einfach nur schrecklich. Unsere drei besten Schützen wurden während eines Luftangriffs über dem Gebiet der Allianz abgeschossen. Niemand weiß, ob sie tot sind oder leben!$B$BDas schränkt unsere Fähigkeiten für Luftschläge erheblich ein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8634', 'deDE', 'Ihr müsst mir helfen, hier wegzukommen, Martin.', 'Ihr müsst mir helfen, hier wegzukommen, Martin.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8635', 'deDE', null, 'Gute Arbeit, Soldat! Ich muss zum Stützpunkt zurück! Gebt Ihr mir Deckung?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8636', 'deDE', 'Ich behalte Euch im Auge, Guse.', 'Ich behalte Euch im Auge, Guse.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8637', 'deDE', null, 'Wurde auch Zeit, dass mal jemand kommt. Was hat Euch so lange aufgehalten? Ach, egal. Gebt mir Deckung, Soldat. Ich muss zum Stützpunkt zurück!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8638', 'deDE', 'Verlasst Euch auf mich, Jeztor.', 'Verlasst Euch auf mich, Jeztor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8639', 'deDE', null, 'Ihr gehört nicht in diese Gärten. Euer Körper wird unsere hübschen Kreationen nähren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8640', 'deDE', null, 'Nichts darf die Gärten beschmutzen! Ihr müsst vernichtet werden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8641', 'deDE', 'Wie viel Blut brauchen wir denn noch, Kommandant?', 'Wie viel Blut brauchen wir denn noch, Kommandant?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8642', 'deDE', null, 'Es wartet noch viel Arbeit auf Euch, $N. Kämpft weiter gegen unsere Feinde!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8643', 'deDE', 'Wir müssen nah dran sein, Kommandant. Ich kann die Elemente spüren.', 'Wir müssen nah dran sein, Kommandant. Ich kann die Elemente spüren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8644', 'deDE', null, 'Ja, $N. Die Elemente rühren sich. Schon bald werden wir ihre Macht über unseren Feinden entfesseln. Bei unseren Schlägen wird ihnen Hören und Sehen vergehen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8645', 'deDE', 'Ich spüre eine große Kraft in unserer Mitte, Kommandant. Ist der Zauber bereit?', 'Ich spüre eine große Kraft in unserer Mitte, Kommandant. Ist der Zauber bereit?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8646', 'deDE', null, 'Ich habe Kontakt hergestellt, $N. Es ist Lokholar der Eislord. Er erachtet unsere Sache als würdig - unsere Opfer an die Erde als ausreichend. Er wird bald kommen... macht Euch bereit.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8647', 'deDE', 'Ich brauche ein paar gute Flügelmänner, die mich sicher hier weg und zurück zum Stützpunkt bringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8648', 'deDE', 'Ich werde Euer Flügelmann sein!', 'Ich werde Euer Flügelmann sein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8649', 'deDE', 'Verlasst diesen heiligen Ort, wenn Euch Euer Leben lieb ist!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8650', 'deDE', 'Für die Legion! Für Kil\'jaeden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8651', 'deDE', 'Kazzak ist der Größte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8652', 'deDE', 'Hält einen Mistelzweig über $Gseinen:ihren; Kopf.', 'Hält einen Mistelzweig über $Gseinen:ihren; Kopf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8653', 'deDE', 'Die Seiten des Tagebuchs scheinen mit Informationen über Geschäfte, Käufer und andere Geschäftsdinge gefüllt zu sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8654', 'deDE', null, 'Seht dem Kommandant niemals direkt in die Augen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8655', 'deDE', null, 'Wir haben direkte Befehle von Cairne, $R. Ihr dürft Euch in die Aufgabe des Kommandanten nicht einmischen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8656', 'deDE', null, 'Man darf den Schamanen-Kommandanten nicht unterschätzen, $R.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8657', 'deDE', null, 'Bald schon wird sich Lokholar erheben... Bald... Sehr, sehr bald...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8658', 'deDE', 'Ihr blättert weiter durch das Tagebuch, um Informationen zu finden, die Euch bei der Suche nach der gestohlenen Truhe helfen könnten.$B$BAuf den letzten Seiten des Buches findet Ihr am Rand in roter Schrift eine Anmerkung von James. Sie besagt: \"Altes Haus beim Stonecairnsee... Gnolle eignen sich perfekt als Leibwächter.\"$B$BIhr habt keinen Grund zur Annahme, dass sich die Truhe dort befinden könnte, aber es scheint keine weiteren Hinweise zu geben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8659', 'deDE', null, 'Die Flotte ist bereit, $N. Ihr müsst dieses Signal nehmen und im östlichen Krater auf dem zentralen Schlachtfeld platzieren. Verteidigt das Signal gegen Angreifer, bis die Flotte die Koordinaten des Signals erfasst hat!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8660', 'deDE', '** BESTÄTIGT ZIELFIXIERUNG. KRIEGSREITEREINHEIT IM ANMARSCH. LUFTANGRIFF STEHT UNMITTELBAR BEVOR. **', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8661', 'deDE', '** PLATZIERT. KRIEGSREITERFIXIERUNG BESTÄTIGT. BEREIT HALTEN. T MINUS 60 SEKUNDEN. **', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8662', 'deDE', '** SERVOS ZERSTÖRT. EINHEITEN-ZIELERFASSUNG GESTÖRT. ABSCHALTUNG STEHT UNMITTELBAR BEVOR. **', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8663', 'deDE', '%s stößt einen schrillen Schrei aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8664', 'deDE', 'Ich bin Hydraxis, Fürst der Wasserlords. Ihr seid kein Elementar und könnt daher unsere Probleme nicht verstehen, aber vielleicht können wir ja eine Einigung erzielen, die uns beiden zugute kommt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8665', 'deDE', 'Es ist schön, Euch zu sehen, $N. Unser Krieg gegen die Lords der Elementare wütet noch immer, aber Dank Eurer Bemühungen werden wir uns den Sieg sichern können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8666', 'deDE', 'Ich brauche eine Phiole mit wässriger Quintessenz, Hydraxis, denn ich gehe zum geschmolzenen Kern, um eine Rune der Feuerlords zu löschen.', 'Ich brauche eine Phiole mit wässriger Quintessenz, Hydraxis, denn ich gehe zum geschmolzenen Kern, um eine Rune der Feuerlords zu löschen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8667', 'deDE', 'Ich bringe dieses Signal sicher zum Krater, Guse. Verlasst Euch auf mich!', 'Ich bringe dieses Signal sicher zum Krater, Guse. Verlasst Euch auf mich!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8668', 'deDE', null, 'Die Flotte ist bereit, $N. Ihr müsst dieses Signal nehmen und im westlichen Krater auf dem zentralen Schlachtfeld platzieren. Verteidigt das Signal gegen Angreifer, bis die Flotte die Koordinaten des Signals erfasst hat!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8669', 'deDE', 'So gut wie erledigt, Jeztor. Gebt mir ein Signal.', 'So gut wie erledigt, Jeztor. Gebt mir ein Signal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8670', 'deDE', 'Es ist an der Zeit, dass Ihr Euer Können als Flügelmann unter Beweis stellt, $N. Nehmt dieses Signal und platziert es genau im Zentrum des Snowfall-Friedhofs. Verteidigt es, bis meine Flotte eintrifft.$B$B<Mulverick salutiert.>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8671', 'deDE', 'Ich bin bereit, das Signal zu platzieren, Mulv. Gebt mir ein Signal.', 'Ich bin bereit, das Signal zu platzieren, Mulv. Gebt mir ein Signal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8672', 'deDE', 'Jippieee! Mulvericks Flotte ist bereit zuzuschlagen. Alle einsatzfähigen Soldaten der Horde müssen ein Signal anfordern und ihr Bestes geben, um es zu platzieren, damit meine Kriegsreiter es für den Luftangriff anpeilen können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8673', 'deDE', null, 'Die Kriegsreiter sind jetzt bereit! Soldaten der Horde, ich brauche Eure Hilfe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8674', 'deDE', null, 'Jeztors Reiter sind bereit! Soldaten der Horde, meldet Euch zum Dienst!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8675', 'deDE', '%s stürzt vorwärts, um den Schwarmbau zu verteidigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8676', 'deDE', '%s stürzt vorwärts, um den Schwarmbau zu verteidigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8677', 'deDE', 'Die Erdenmutter wacht über uns, im Schutze der Nacht und auch bei Tage. Möge dieses Feuer sowohl unseren Körper als auch unseren Geist erleuchten und uns an unsere verehrten Vorfahren erinnern, die immer um uns sind und uns den Weg weisen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8678', 'deDE', 'Ich möchte ein Spezialgebiet der Lederverarbeitung erlernen.', 'Ich möchte ein Spezialgebiet der Lederverarbeitung erlernen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8679', 'deDE', 'Zeigt diesen Wilden gegenüber keine Gnade, Soldaten! Die tun das auch nicht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8680', 'deDE', 'Zieht Eure Truppen zurück, Bruder, dann verspreche ich Euch einen schnellen zweiten Tod!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8681', 'deDE', 'Sammelt Euch um Eure Lieutenants und Kommandanten, Soldaten! Treibt die Wilden zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8682', 'deDE', 'Zeigt Euren Feinden, wie entschlossen Ihr seid, Soldaten! Zermalmt sie unter Euren Füßen! Unter Stahl!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8683', 'deDE', 'FÜR DEN KÖNIG! Treibt sie zurück, Soldaten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8684', 'deDE', 'Gebt auf, dann lassen wir Euch leben, Heiden! Lasst einfach die Waffen fallen und zieht ab!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8685', 'deDE', 'Für Bodenangriffe werden große Mengen an Vorräten benötigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8686', 'deDE', '$N, worauf wartet Ihr noch? Findet Feldmarschall Teravaine im Feld des Kampfes und überbringt ihm die Angriffsbefehle. Sobald er die Befehle erhält, werden er und seine Truppen die Stellungen der Horde stürmen. Und sie werden keine Gefangenen machen!', '$N, worauf wartet Ihr noch? Findet Feldmarschall Teravaine im Feld des Kampfes und überbringt ihm die Angriffsbefehle. Sobald er die Befehle erhält, werden er und seine Truppen die Stellungen der Horde stürmen. Und sie werden keine Gefangenen machen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8687', 'deDE', 'Gebt mir die Befehle! Ich werde Sie Teravaine im Feld des Kampfes überbringen!', 'Gebt mir die Befehle!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8688', 'deDE', 'Das Auge sieht alles, was sich in seinem Einflussbereich befindet, $C. Was wünscht Ihr zu sehen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8689', 'deDE', 'Die Befehle wurden erteilt! Rückt aus und zeigt denen, aus welchem Holz wir geschnitzt sind!', 'Die Befehle wurden erteilt! Rückt aus und zeigt denen, aus welchem Holz wir geschnitzt sind!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8691', 'deDE', 'Danke, $N. Ich werde meine Leute sogleich mobil machen!', 'Danke, $N. Ich werde meine Leute sogleich mobil machen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8692', 'deDE', 'Ich möchte meine Feinde sehen, Ryson. Zeigt sie mir.', 'Ich möchte meine Feinde sehen, Ryson. Zeigt sie mir.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8693', 'deDE', 'Nehmt es! Bringt mir das Auge wieder zurück, sobald seine Kräfte schwinden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8694', 'deDE', 'Wenn Ihr nach Informationen sucht, die der Zirkel des Cenarius irgendwo archiviert haben könnte, bin ich genau der richtige Ansprechpartner. Ihr müsst allerdings schon wissen, welche Informationen Ihr genau benötigt, damit ich Euch wirklich helfen kann.$B$BVersteht Ihr, wie das funktioniert? Das ist genial!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8695', 'deDE', 'Hört nicht auf die Herzen der Erde - die Antwort liegt im Inneren. Ihr kennt die Wahrheit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8696', 'deDE', 'In diesen Wüsten lebt die Vergangenheit, $C. Wir müssen vorsichtig sein, damit wir sie nicht wecken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8697', 'deDE', 'Die Macht des Auges ist unbestreitbar, Ryson, aber was kann ich sonst noch tun?', 'Die Macht des Auges ist unbestreitbar, Ryson, aber was kann ich sonst noch tun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8698', 'deDE', 'Die Macht des Auges ist unbestreitbar, Ryson, aber was kann ich sonst noch tun?', 'Die Macht des Auges ist unbestreitbar, Ryson, aber was kann ich sonst noch tun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8699', 'deDE', 'Ihr habt gerade erst begonnen, die Macht des Auges zu sehen, $C. Wenn Ihr die Fähigkeiten des Auges nutzen wollt, müsst Ihr zuerst eine Aufgabe erledigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8700', 'deDE', 'Was ist das für eine Aufgabe?', 'Was ist das für eine Aufgabe?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8701', 'deDE', 'Nehmt dieses Signal und geht damit zum Stützpunkt des Feindes. Arbeitet Euch bis vor seine Hauptfestung vor und platziert das Signal in der Mitte des Hofs. Verteidigt das Signal gegen Angreifer, bis das Auge die Koordinaten des Signals erfasst hat.$B$BSobald dies geschehen ist, kann man von hier aus alle Aktivitäten außerhalb des feindlichen Stützpunkts beobachten.$B$BWollt Ihr diese Aufgabe übernehmen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8702', 'deDE', 'Absolut. Gebt mir ein Signal, Ryson.', 'Absolut. Gebt mir ein Signal, Ryson.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8703', 'deDE', 'Ihr seid bereit für die allerletzte Aufgabe, $N. Die feurigen Runen, die in den Boden des geschmolzenen Kerns eingraviert wurden, leiten ins Reich des Feuers und sie sind für uns nicht nur ein Affront, sondern auch gleichzeitig eine Machtquelle für die Feuerlords. Sie müssen ausgelöscht werden!$B$BUm eine Rune zu löschen, braucht Ihr eine Phiole mit wässriger Quintessenz. Gießt das auf die Rune, um ihre Flamme zu löschen und somit nutzlos zu machen und den Feind zu schwächen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8704', 'deDE', 'Willkommen bei der Gildenregistratur von Unterstadt. Was wollt Ihr?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8705', 'deDE', 'Willkommen beim Zirkel des Cenarius, $C. Wie kann ich Euch dienen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8706', 'deDE', 'Ich brauche ein Wahrsage-Zielfernrohr, um die Kugel des Wahrsagens zu sehen, Ryson.', 'Ich brauche ein Wahrsage-Zielfernrohr, um die Kugel des Wahrsagens zu sehen, Ryson.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8707', 'deDE', 'Sprecht mit Hydraxis.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8708', 'deDE', 'Ich habe mit Euch nichts zu schaffen, Winzling.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8709', 'deDE', 'Sprecht nicht in Eurer vulgären Sprache mit mir! Nur Hydraxis kann sie ertragen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8710', 'deDE', 'Seid gegrüßt. Was wollt Ihr von den Wasserlords?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8711', 'deDE', 'Die Kunde von Euren Heldentaten spricht sich herum. Euer Name ist uns nicht unbekannt, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8712', 'deDE', 'Ihr habt unsere Feinde zu den Euren gemacht. Ich teile mit Freuden das Wasser mit Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8713', 'deDE', 'Vielleicht erzählt Ihr mir ein bisschen von Euren Taten, $N. Es wäre mir eine Ehre, sie zu hören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8714', 'deDE', 'Unser Fürst Hydraxis scheint viel von Euch zu halten. Mein Wasser ist auch Euer Wasser.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8715', 'deDE', 'Wenn Ihr das nächste Mal zum geschmolzenen Kern kommt, tötet dort einen Feuerlord für mich, ja?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8716', 'deDE', 'Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. Ein $C kann Euch da wohl besser als Lehrer dienen.', 'Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. Ein $C kann Euch da wohl besser als Lehrer dienen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8717', 'deDE', 'Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?', 'Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8718', 'deDE', 'Ausbau zu erfahrenen Einheiten!', 'Ausbau zu erfahrenen Einheiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8719', 'deDE', 'Ausbau zu Veteraneneinheiten!', 'Ausbau zu Veteraneneinheiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8720', 'deDE', 'Wie ich sehe, habt Ihr das Auge gefunden.$B$B<Meister Ryson grinst.>$B$BAlso gebt schon her!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8721', 'deDE', 'Nehmt es! Dieses Ding macht wahrscheinlich mehr Ärger als es wert ist.', 'Nehmt es! Dieses Ding macht wahrscheinlich mehr Ärger als es wert ist.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8722', 'deDE', 'Das allsehende Auge muss wiederbeschafft werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8723', 'deDE', 'Ausbau zu Championeinheiten!', 'Ausbau zu Championeinheiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8724', 'deDE', 'Gehen wir zu unserem Stützpunkt zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8725', 'deDE', 'Ich stehe tief in Eurer Schuld, $C. Vielleicht kann ich Euch ja helfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8726', 'deDE', 'Ich war auf der Suche nach Material für meine neueste und größte Erfindung, der tragbaren ferngesteuerten Schreddereinheit, als diese Trolle mir eins über den Schädel zogen und in die Tiefen ihrer Höhle verschleppten.$B$BBringt mich in Sicherheit, dann werde ich Euch helfen, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8727', 'deDE', 'Auf geht\'s, Goblin!', 'Auf geht\'s, Goblin!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8728', 'deDE', 'Ausbau zu erfahrenen Einheiten!', 'Ausbau zu erfahrenen Einheiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8729', 'deDE', 'Ausbau zu Veteraneneinheiten!', 'Ausbau zu Veteraneneinheiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8730', 'deDE', 'Ausbau zu Championeinheiten!', 'Ausbau zu Championeinheiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8731', 'deDE', null, 'Das Land ist besudelt! Ivus muss gerufen werden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8732', 'deDE', null, 'Soldaten der Stormpike, helft und beschützt uns! Der Waldlord gewährt uns seinen Schutz! Das Tor muss jetzt geöffnet werden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8733', 'deDE', null, 'Kommt, Brüder!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8734', 'deDE', null, 'Vorwärts!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8735', 'deDE', null, 'Wir müssen unsere Gedanken auf den Kreis des Rufens konzentrieren, wenn der Waldlord kommen soll! Wir müssen uns sputen! Konzentriert Eure Energien!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8736', 'deDE', 'Böse, böse sterbliche Wesen! Der Wald weint. Die Elemente schaudern zurück ob der Zerstörung. Ivus muss Euch von dieser Welt entfernen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8737', 'deDE', 'Ich bin gekommen, um Eure Stützpunkte dem Erdboden gleichzumachen, Frostwolfklan. Ivus wird Euch für diesen Verrat bestrafen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8738', 'deDE', 'Ihr Uralten, erhebet Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8739', 'deDE', 'Euer Stützpunkt ist Geschichte, sterbliche Wesen! Aus seiner Asche wird neues Leben entstehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8740', 'deDE', 'Euer Stützpunkt ist Geschichte, jämmerliche sterbliche Wesen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8741', 'deDE', 'Unsere erfahrenen Einheiten verlassen das Feld. Wir brauchen mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8742', 'deDE', 'Unsere Veteraneneinheiten verlassen das Feld. Wir brauchen mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8743', 'deDE', 'Unsere Championeinheiten verlassen das Feld. Wir brauchen mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8744', 'deDE', 'Erfahrene Einheiten treten in den Kampf ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8745', 'deDE', 'Veteraneneinheiten treten in den Kampf ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8746', 'deDE', 'Championeinheiten treten in den Kampf ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8747', 'deDE', 'Man hat uns von Darnassus hergeschickt, um den Stormpike in Zeiten der Not beizustehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8748', 'deDE', 'Die Erzdruide glaubt, dass Ivus der Waldlord kommen wird, sobald er von der Zerstörung dieses Tales erfährt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8749', 'deDE', 'Es ist nicht an uns, diese Gräueltaten zu beurteilen. Ivus wird darüber richten, sobald er hier eingetroffen ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8750', 'deDE', null, 'Mein Glaube leitet meine Aktionen, $R. Ich weiß, dass Ivus kommen wird.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8751', 'deDE', 'Unsere erfahrenen Einheiten verlassen das Feld. Wir brauchen mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8752', 'deDE', 'Unsere Veteranen verlassen das Feld. Wir brauchen mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8753', 'deDE', 'Unsere Championeinheiten verlassen das Feld. Wir brauchen mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8754', 'deDE', 'Erfahrene Einheiten ziehen ins Feld!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8755', 'deDE', 'Unsere Veteranen betreten das Feld!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8756', 'deDE', 'Unsere Championeinheiten sind ausgerüstet! Nehmt Euch in Acht, Allianzler!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8757', 'deDE', 'Habt Ihr schon Kontakt zu Ivus aufgenommen, Erzdruide?', 'Habt Ihr schon Kontakt zu Ivus aufgenommen, Erzdruide?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8758', 'deDE', null, 'Ich habe mich in dem Traum verloren. Ich spüre seine Lebenskraft, aber meine Kräfte sind noch nicht stark genug, um Kontakt zu ihm aufzunehmen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8759', 'deDE', 'Die Luft vibriert vor Energie, Erzdruide. Wie steht es mit dem Waldlord?', 'Die Luft vibriert vor Energie, Erzdruide. Wie steht es mit dem Waldlord?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8760', 'deDE', null, 'Ja. $N, ich habe den Waldlord gefunden. Wir müssen unsere Anstrengungen verdoppeln! Es werden weitere Kristalle benötigt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8761', 'deDE', 'Der Wald schüttelt sich und erzittert! Die Winde heulen! Ob wohl der Waldlord kommt?', 'Der Wald schüttelt sich und erzittert! Die Winde heulen! Ob wohl der Waldlord kommt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8762', 'deDE', null, 'Der Waldlord weiß um die Zerstörung in diesem Tal, $N. Bald müssen wir zum Kreis des Rufens. Macht Euch bereit!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8763', 'deDE', 'Dieser tollkühne Narr Mulverick hat mich abgeschossen. Ich fürchte, wenn ich es nicht zurück zum Stützpunkt schaffe, ist alles verloren! Mulverick und seine Truppe aus Kriegsreitern müssen aufgehalten werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8764', 'deDE', 'Ich decke Euch den Rücken, Ichman. LOS, LOS, LOS!', 'Ich decke Euch den Rücken, Ichman. LOS, LOS, LOS!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8765', 'deDE', 'Ich kann es nicht glauben... Ich war auf einer routinemäßigen Aufklärungsmission in der zentralen DMZ, als plötzlich die Hölle losbrach. Dort wurde ich Zeuge, wie der Schwadronskommandant der Horde Mulverick sowohl Ichman als auch Slidore ausschaltete! Wut überkam mich... ich trennte mich von meinem Flügelmann und streckte diesen Bastard Mulverick nieder. Leider verletzte er meinen Greifen, bevor er zu Boden ging, und zwang mich ebenfalls hinunter. So fingen mich schließlich diese Wilden.$B$BIch muss zum Stützpunkt zurück! Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8766', 'deDE', 'Ich gebe Euch Deckung, Vipore. Zieht los!', 'Ich gebe Euch Deckung, Vipore. Zieht los!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8767', 'deDE', 'Ich hörte noch ihr Hohngelächter, als mein Greif zu Boden stürzte und verbrannte, $C.$B$B\"Slidore, du stinkst!\", riefen sie.$B$BNiemand spricht so mit Slidore! Helft mir hier raus, damit ich denen zeigen kann, wer hier der Stinker ist!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8768', 'deDE', 'Ich gebe Euch Deckung, Slidore, aber um ehrlich zu sein, Ihr stinkt wirklich. Nehmt doch mal \'ne Dusche, Mann.', 'Ich gebe Euch Deckung, Slidore, aber um ehrlich zu sein, Ihr stinkt wirklich. Nehmt doch mal \'ne Dusche, Mann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8769', 'deDE', 'Gebt mir Deckung, ich will es wagen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8770', 'deDE', 'Wir dürfen keine Zeit verschwenden. Das ist eine ganz schöne Strecke! Gebt mir Deckung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8771', 'deDE', 'Bringt mich einfach nah genug an unseren Stützpunkt heran, dann kann ich einen Teleporter kanalisieren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8772', 'deDE', 'Das ist weit genug! Wir sehen uns im Stützpunkt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8773', 'deDE', 'Und abwärts, Mulv!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8774', 'deDE', null, 'Ich habe hier die Befehle für einen Angriff gegen die Streitkräfte der Allianz auf den mittleren Feldern. Ich brauche jemanden, der sie zum Angriffsleiter bringt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8775', 'deDE', null, 'Es ist ein eiskalter, blutiger Kampf, den wir hier im Alteractal führen. Es ist perfekt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8776', 'deDE', 'Ja, ich werde die Befehle an Kriegsmeister Garrick überbringen!', 'Ja, ich werde die Befehle an Kriegsmeister Garrick überbringen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8777', 'deDE', null, 'Dieser Wald kann einen ganz schön verwirren! Seid vorsichtig da draußen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8778', 'deDE', 'Schöne Grüße an Euch und die Euren!', 'Schöne Grüße an Euch und die Euren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8779', 'deDE', 'Frisch von unserem Hof auf Euren Tisch - beste Vollwertkost von Kokelwälder!', 'Frisch von unserem Hof auf Euren Tisch - beste Vollwertkost von Kokelwälder!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8780', 'deDE', 'Schnäppchenzeit ist angebrochen! Tra la la la laaaa, la la, la laaa!', 'Schnäppchenzeit ist angebrochen! Tra la la la laaaa, la la, la laaa!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8781', 'deDE', 'Ich wünsche ein wunderbares Winterhauchfest! Läutet es ein mit Leckereien von Kokelwälder!', 'Ich wünsche ein wunderbares Winterhauchfest! Läutet es ein mit Leckereien von Kokelwälder!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8782', 'deDE', 'Vergesst nicht, Altvater Winter zu sagen, welche Überraschung Ihr Euch dieses Winterhauchfest wünscht!', 'Vergesst nicht, Altvater Winter zu sagen, welche Überraschung Ihr Euch dieses Winterhauchfest wünscht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8783', 'deDE', 'Möge das Klingeln der Glöckchen Euch nicht vom Klingeln in Eurem Geldbeutel ablenken! Kauft Leckereien von Kokelwälder für Eure Winterhauchfeiern!', 'Möge das Klingeln der Glöckchen Euch nicht vom Klingeln in Eurem Geldbeutel ablenken! Kauft Leckereien von Kokelwälder für Eure Winterhauchfeiern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8784', 'deDE', 'Wenn ich ein paar Kastanien hätte, würde ich sie sofort über dem Feuer rösten. Mmmm!', 'Wenn ich ein paar Kastanien hätte, würde ich sie sofort über dem Feuer rösten. Mmmm!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8785', 'deDE', 'Vergesst nicht, Euch für die diesjährigen Festtage mit Geschenkpapier einzudecken! Nichts sagt \'Fröhliches Winterhauchfest\' schöner als hübsch verpackte Rüstungen und Waffen für Eure Freunde und Familie.', 'Vergesst nicht, Euch für die diesjährigen Festtage mit Geschenkpapier einzudecken! Nichts sagt \'Fröhliches Winterhauchfest\' schöner als hübsch verpackte Rüstungen und Waffen für Eure Freunde und Familie.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8786', 'deDE', 'Ich möchte ein wenig in Eurem Angebot stöbern.', 'Ich möchte ein wenig in Eurem Angebot stöbern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8787', 'deDE', 'Tempoverdopplung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8788', 'deDE', '%s scharf gemacht. T minus 60 Sekunden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8789', 'deDE', '%s scharf gemacht. Aerie-Greif ist unterwegs.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8790', 'deDE', '%s wurde vernichtet. Luftangriff konnte Zielpunkt nicht bestätigen. Mission wurde abgebrochen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8791', 'deDE', 'Soldaten von Stormpike, kommt mir zu Hilfe! Das Signal muss platziert werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8792', 'deDE', 'Aufpassen, Soldat! Ihr müsst dieses Signal zum Snowfall-Friedhof bringen und dort platzieren! Schützt es lange genug, damit mein Greif es erfassen kann!$B$BWenn mein Greif einen festen Zielpunkt hat, können wir Mulv und seiner Mannschaft einen bösen Schlag versetzen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8793', 'deDE', 'Ich bin Euer Mann, Ich. Gebt mir ein Signal!', 'Ich bin Euer Mann, Ich. Gebt mir ein Signal!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8794', 'deDE', 'Mein Klan ist bereit! Soldaten, kommt mir zu Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8795', 'deDE', '$N! Endlich!$B$BIhr müsst dieses Signal im östlichen Krater auf dem zentralen Schlachtfeld platzieren. Mein Klan wird angreifen, sobald das Signal die Koordinaten sendet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8796', 'deDE', 'Gebt mir das Signal, Slidore! Übrigens stinkt Ihr immer noch!', 'Gebt mir das Signal, Slidore! Übrigens stinkt Ihr immer noch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8797', 'deDE', 'Ihr Kampfeswille ist wieder voll da! Die Greifen sind bereit für den Schlag! Kommt, Soldaten! Eilt an meine Seite!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8798', 'deDE', 'Die Greifen sind bereit für den Schlag, $N! Ihr müsst dieses Signal im westlichen Krater auf dem zentralen Schlachtfeld platzieren.$B$BGelingt Euch das, ist die Schlacht gewonnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8799', 'deDE', 'Ich bin kampfbereit, Vipore! Gebt mir ein Signal.', 'Ich bin kampfbereit, Vipore! Gebt mir ein Signal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8800', 'deDE', 'Jetzt haben wir die Weisheit der Winterax, $N. Ihr könnt für kurze Zeit über diese Macht gebieten, so Ihr dies wünscht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8801', 'deDE', 'Gewährt mir die Weisheit der Winterax!', 'Gewährt mir die Weisheit der Winterax!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8802', 'deDE', 'Unsere Einheiten sind nicht ausgebaut und ich habe nicht genug Vorräte, um sie auszubauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8803', 'deDE', 'Unsere Einheiten sind zu \'Erfahren\' ausgebaut, aber ich habe nicht genug Vorräte, um sie zu \'Veteranen\' auszubauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8804', 'deDE', 'Unsere Einheiten sind zu \'Veteranen\' ausgebaut, aber ich habe nicht genug Vorräte, um sie zu \'Champions\' auszubauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8805', 'deDE', 'Ich habe nicht genügend Vorräte, um unsere Truppen zu \'Erfahren\' auszubauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8806', 'deDE', 'Ich habe genügend Vorräte, um unsere Truppen zu \'Erfahren\' auszubauen', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8807', 'deDE', 'Ich habe genügend Vorräte, um unsere Truppen zu \'Champions\' auszubauen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8808', 'deDE', 'Unsere Einheiten sind zu \'Champions\' ausgebaut, aber ich kann noch weitere Vorräte auftreiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8809', 'deDE', 'Ich kann keine weiteren Vorräte unterbringen. Das, was ich habe, reicht aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8810', 'deDE', 'Seid Ihr hier, um den Umgang mit einer neuen Waffe zu lernen, oder nur, um meine Zeit zu vergeuden?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8811', 'deDE', 'Ich möchte eine Waffenausbildung.', 'Ich möchte eine Waffenausbildung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8812', 'deDE', 'Was können mir andere Waffenmeister beibringen?', 'Was können mir andere Waffenmeister beibringen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8813', 'deDE', 'Ansekhwa, der Waffenmeister der Tauren, lebt auf der unteren Anhöhe in Donnerfels. Er kann Euch im Umgang mit Ein- und Zweihandstreitkolben, Stäben und Schusswaffen unterrichten.\n\nHanashi hier neben mir kann Euch in der Benutzung von Bogen, Ein- und Zweihandäxten, Stäben und allen möglichen Arten von Wurfwaffen unterweisen.\n\nArchibald, der Waffenmeister aus den Reihen der Untoten im Kriegsviertel von Unterstadt, kann Euch in Armbrüsten, Dolchen, Ein- und Zweihandschwertern sowie Stangenwaffen unterrichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8814', 'deDE', 'Wer kontrolliert den Stormpike-Friedhof?', 'Wer kontrolliert den Stormpike-Friedhof?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8817', 'deDE', 'Der Stormpike-Friedhof befindet sich in den Bergen, gleich über der Brücke östlich von Dun Baldar.$B$BEr wird momentan von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8818', 'deDE', 'Der Stormpike-Friedhof befindet sich in den Bergen, gleich über der Brücke östlich von Dun Baldar.$B$BSie wird momentan von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8820', 'deDE', 'Wer kontrolliert den Snowfall-Friedhof?', 'Wer kontrolliert den Snowfall-Friedhof?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8821', 'deDE', 'Der Snowfall-Friedhof liegt hoch auf einem Hügel im Feld des Kampfes, östlich der Feste der Winterax.$B$BEr wird momentan von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8823', 'deDE', 'Der Snowfall-Friedhof liegt hoch auf einem Hügel im Feld des Kampfes, östlich der Feste der Winterax.$B$BEr wird momentan von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8824', 'deDE', 'Wer kontrolliert den Friedhof der Frostwolf?', 'Wer kontrolliert den Friedhof der Frostwolf?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8825', 'deDE', 'Der Friedhof der Frostwolf liegt nahe der Straße, nördlich von Frostwolf.$B$BEr wird momentan von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8826', 'deDE', 'Der Friedhof der Frostwolf liegt nahe der Straße, nördlich von Frostwolf.$B$BEr wird momentan von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8830', 'deDE', 'Der Friedhof der Frostwolf wird von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8831', 'deDE', 'Der Friedhof der Frostwolf wird von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8832', 'deDE', 'Der Snowfall-Friedhof gehört der Allianz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8833', 'deDE', 'Der Snowfall-Friedhof gehört der Horde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8834', 'deDE', 'Ein fröhliches Winterfest wünsch ich,... äh... $GJungchen:Mädchen;. Brave Kinder bekommen besondere, äh, Sonderkonditionen bei allen Verkäufern von Kokelwälder. Lecker.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8835', 'deDE', 'Ich kann mir gut vorstellen, was der alte Altvater Winter tut, wenn seine Schicht um ist. Ich hab hier \'n spezielles Winterhauch-Tröpfchen in der Tasche, das ich mir mit Genuss einverleiben werde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8836', 'deDE', 'Auf meinem Schoß sitzen? Kommt nicht in Frage.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8837', 'deDE', 'Ja, sicher, man sollte Altvater Winter erzählen, was man sich zum Winterhauchfest wünscht.$B$BHoho, Tempo!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8838', 'deDE', 'Der Stormpikeklan beugt sich niemandem, und schon gar nicht der Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8839', 'deDE', 'Eure Angriffe lassen zu wünschen übrig! Geht und übt das an ein paar Hasen und kommt wieder her, wenn Ihr stärker seid.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8840', 'deDE', 'Etwas Besseres habt Ihr nicht drauf?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8841', 'deDE', 'Es braucht mehr als einen Wicht wie Euch, um mich in die Knie zu zwingen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8842', 'deDE', 'Wir lassen uns nicht von unserer Mission abbringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8843', 'deDE', 'Wir, die Allianz, werden siegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8844', 'deDE', 'Ihr könnt den Frostwolfklan nicht besiegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8845', 'deDE', 'Ihre Geister sind schwach und Eure Schläge sind noch schwächer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8846', 'deDE', 'Eure Angriffe sind wohl durch die Kälte gebremst, was?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8847', 'deDE', 'Wenn Ihr das Alteractal nicht aus freien Stücken verlasst, werden Euch die Frostwölfe mit Gewalt vertreiben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8848', 'deDE', 'Ihr seid kein Gegner für die Kraft der Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8849', 'deDE', 'Heute werdet Ihr Euren Ahnen begegnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8850', 'deDE', 'Die Flügelschläge von %s sind in der Ferne zu hören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8851', 'deDE', 'Erzählt mir von dem leuchtenden Splitter.', 'Erzählt mir von dem leuchtenden Splitter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8852', 'deDE', 'Meins! Meins! Meins! Gizlock ist der Herrscher über diese Domäne! Ihr werdet mich niemals finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8853', 'deDE', 'Dun Baldar musste einen schweren Schlag während der letzten Attacke der Horde hinnehmen. Wir brauchen Vorräte, um unseren Stützpunkt wieder aufzubauen und Reparaturen vorzunehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8854', 'deDE', 'Ein leerer Magen marschiert nicht gern, und das ist die Wahrheit! Wenn Eure Offensive erfolgreich sein soll, sorgt dafür, dass Eure Truppen wohlgenährt und gut ausgerüstet sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8855', 'deDE', 'Einige sagen, dass Stärke und Tapferkeit das Wichtigste für den Erfolg einer Armee sind. Und das ist wahr! Doch braucht eine Armee auch Vorräte, um in guter Verfassung zu bleiben, und es ist meine Aufgabe, dafür zu sorgen, dass sie das bekommen, was sie benötigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8857', 'deDE', 'Burg Frostwolf wurde ausgelöscht! Wir brauchen Vorräte, um unseren Stützpunkt wieder aufzubauen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8858', 'deDE', '%s winselt gehorsam.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8859', 'deDE', null, 'Seid gegrüßt, Soldat. Seid Ihr hier, um der Sache zu dienen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8860', 'deDE', null, 'Gut gemacht, Soldat. Noch ein paar mehr und unsere Ställe werden vor Reittieren überquellen, auf denen unsere Kavallerie in die Schlacht ziehen kann!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8861', 'deDE', 'Danke, Stallmeister. Bitte nehmt Euch des Tieres an.', 'Danke, Stallmeister. Bitte nehmt Euch des Tieres an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8862', 'deDE', 'He, Mann. Bei mir seid Ihr genau richtig, wenn Ihr lernen wollt, wie man eine neue Waffe führt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8863', 'deDE', 'Ah, $N! Ihr seid genau die Person, auf die ich gewartet habe! Wir haben genügend Vorräte, um einen Bodenangriff gegen die Horde zu starten! Feldmarschall Teravaine wartet im Feld des Kampfes auf Befehle...$B$BIhr seid ein tapferer Veteran, der sich immer wieder behauptet hat. Wollt Ihr Teravaine die Befehle überbringen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8864', 'deDE', 'Ansekhwa, der Waffenmeister der Tauren, lebt auf der unteren Anhöhe in Donnerfels. Er kann Euch im Umgang mit Ein- und Zweihandstreitkolben, Stäben und Schusswaffen unterrichten.\n\nArchibald, der Waffenmeister aus den Reihen der Untoten im Kriegsviertel von Unterstadt, kann Euch in Armbrüsten, Dolchen, Ein- und Zweihandschwertern sowie Stangenwaffen unterrichten.\n\nSayoc, der hässliche Orc hier, kann Euch zeigen, wie man Bogen, Dolche, Faustwaffen, Ein- und Zweihandäxte und Wurfwaffen benutzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8865', 'deDE', '$N! Was redet Ihr mit mir? Ihr solltet draußen sein und gegen die Horde kämpfen!$B$BIch habe Einsatzbefehle für einen unserer Feldmarschalle im Feld des Kampfes, aber ich benötige jemanden, auf den ich mich verlassen kann.$B$BWenn Eurer Ansehen bei den Truppen der Stormpike höher wäre, könnte ich vielleicht auf Euch zählen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8866', 'deDE', 'Es wäre mir eine Ehre, Euch im Umgang mit den Waffen zu unterweisen, die ich beherrsche, so Ihr die Fähgkeit, sie zu erlernen, in euch tragt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8867', 'deDE', '$N! Unsere Vorräte schwinden und bis wir Nachschub erhalten, können wir keine größeren Bodenattacken im Feld des Kampfes starten!$B$BSprecht mit dem Stormpike-Rüstmeister. Er wird Euch sagen, was Ihr tun könnt, um unsere Angriffsstreitkräfte neu auszurüsten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8868', 'deDE', 'Sayoc ist der orcische Waffenmeister. Ihr findet ihn im Tal der Ehre in Orgrimmar. Er kann Euch in der Benutzung von Bogen, Dolchen, Faustwaffen, Ein- und Zweihandäxten sowie Wurfwaffen unterrichten.\n\nHanashi, der Waffenmeister der Trolle im Tal der Ehre in Orgrimmar kann Euch in der Benutzung von Bogen, Ein- und Zweihandäxten, Stäben und allen möglichen Arten von Wurfwaffen unterweisen.\n\nArchibald, der Waffenmeister aus den Reihen der Untoten im Kriegsviertel von Unterstadt, kann Euch in Armbrüsten, Dolchen, Ein- und Zweihandschwertern sowie Stangenwaffen unterrichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8869', 'deDE', 'Tod den Stormpike!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8870', 'deDE', 'Schickt die Kavallerie!', 'Schickt die Kavallerie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8871', 'deDE', 'Ihr habt außergewöhnlich gute Arbeit geleistet, Soldat! Die Kavallerie ist bereit, auf Euren Befehl hin loszureiten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8872', 'deDE', 'Ich erwarte Befehle von meinen Vorgesetzten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8873', 'deDE', 'Ich nehme an, Ihr seid hier, um Euch im Umgang mit einer neuen Waffe ausbilden zu lassen, ja? Beeilt Euch, ich bin sehr beschäftigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8874', 'deDE', 'Sayoc ist der orcische Waffenmeister. Er befindet sich im Tal der Ehre in Orgrimmar. Er kann Euch in der Benutzung von Bogen, Dolchen, Faustwaffen, Ein- und Zweihandäxten sowie Wurfwaffen unterweisen.\n\nHanashi, der Waffenmeister der Trolle im Tal der Ehre in Orgrimmar kann Euch in der Benutzung von Bogen, Ein- und Zweihandäxten, Stäben und allen Arten von Wurfwaffen unterweisen.\n\nAnsekhwa, der Waffenmeister der Tauren, lebt auf der unteren Anhöhe in Donnerfels. Er kann Euch im Umgang mit Ein- und Zweihandstreitkolben, Stäben und Schusswaffen unterrichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8875', 'deDE', 'Ich möchte mein Tier bei Euch unterbringen, Stallmeister.', 'Ich möchte mein Tier bei Euch unterbringen, Stallmeister.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8876', 'deDE', '$N, es tut gut, Veteranen wie Euch in der Schlacht zu sehen! Ich muss Euch leider mitteilen, dass unsere Vorräte im Moment zu gering sind, um einen Bodenangriff gegen die Horde starten zu können.$B$BSprecht mit unserem Rüstmeister; er hat Missionen zur Vorratsbeschaffung für Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8877', 'deDE', 'Seid Ihr gekommen, um Euch im Kampf mit Waffen ausbilden zu lassen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8878', 'deDE', 'Buliwyf, der zwergische Waffenmeister in Eisenschmiede bei Schlachtholzwaffen, kann Euch im Umgang mit Faustwaffen, Schusswaffen, Ein- und Zweihandäxten sowie Ein- und Zweihandstreitkolben unterrichten.\n\nBixi Wobblebonk, die gnomische Waffenmeisterin bei Schlachtholzwaffen in Eisenschmiede, kann Euch in der Benutzung von Armbrüsten, Dolchen und Wurfwaffen unterweisen.\n\nIlyenia, die Waffenmeisterin der Nachtelfen auf der Terrasse der Krieger in Darnassus, kann Euch im Umgang mit Bogen, Dolchen, Faustwaffen, Stäben und Wurfwaffen unterrichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8879', 'deDE', null, 'Die Anmut, mit der ich kämpfe, kann ich Euch leider nicht beibringen, aber ich kann Euch in der Benutzung diverser Waffen unterweisen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8880', 'deDE', null, 'Buliwyf, der zwergische Waffenmeister in Eisenschmiede bei Schlachtholzwaffen, kann Euch im Umgang mit Faustwaffen, Schusswaffen, Ein- und Zweihandäxten sowie Ein- und Zweihandstreitkolben unterrichten.\n\nBixi Wobblebonk, die gnomische Waffenmeisterin bei Schlachtholzwaffen in Eisenschmiede, kann Euch in der Benutzung von Armbrüsten, Dolchen und Wurfwaffen unterweisen.\n\nWoo Ping, der Waffenmeister aus den Reihen der Menschen bei Wellers Arsenal in Sturmwind, kann Euch in Armbrüsten, Dolchen, Ein- und Zweihandschwertern, Stangenwaffen sowie Stäben unterrichten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8881', 'deDE', 'Ihr wollt Euch im Umgang mit einer neuen Waffe ausbilden lassen, richtig? Dann steht da nicht rum und haltet Maulaffen feil, $GBursche:Mädel;!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8882', 'deDE', 'Bixi Wobblebonk, die gnomische Waffenmeisterin, wandert irgendwo hier im Laden herum. Sie kann Euch die Benutzung von Armbrüsten, Dolchen und Wurfwaffen beibringen.\n\nWoo Ping, der Waffenmeister der Menschen, lebt bei Wellers Arsenal in Sturmwind. Er kann Euch an Armbrüsten, Dolchen, Ein- und Zweihandschwertern, Stangenwaffen und Stäben ausbilden.\n\nIlyenia Moonfire, die Waffenmeisterin der Nachtelfen, lebt auf der Terrasse der Krieger in Darnassus. Sie kann Euch die Benutzung von Bögen, Dolchen, Faustwaffen, Stäben und Wurfwaffen beibringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8883', 'deDE', null, 'Hallo auch! Seid Ihr hier, um die Feinheiten im Umgang mit einer neuen Waffe zu erlernen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8884', 'deDE', null, 'Buliwyf, der zwergische Waffenmeister hier bei Schlachtholzwaffen, kann Euch im Umgang mit Faustwaffen, Schusswaffen, Ein- und Zweihandäxten sowie Ein- und Zweihandstreitkolben unterrichten.\n\nIlyenia, die Waffenmeisterin der Nachtelfen auf der Terrasse der Krieger in Darnassus, kann Euch im Umgang mit Bogen, Dolchen, Faustwaffen, Stäben und Wurfwaffen unterrichten.\n\nWoo Ping, der Waffenmeister aus den Reihen der Menschen bei Wellers Arsenal in Sturmwind, kann Euch in Armbrüsten, Dolchen, Ein- und Zweihandschwertern, Stangenwaffen sowie Stäben unterrichten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8885', 'deDE', null, 'Wir brauchen mehr Wölfe! Kommt zu den Ställen und helft mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8886', 'deDE', null, 'Gut gemacht, Horde! Wir haben genug Frostwölfe in unseren Ställen, um all unsere Wolfreiter mit Reittieren auszustatten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8887', 'deDE', 'Wolfreiter, auf zum Frostdagger-Pass!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8888', 'deDE', 'RARRRRRR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8889', 'deDE', 'Lasst mich wissen, wann es am günstigsten ist loszureiten. Ihr müsst unsere Augen und Ohren an der Front sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8890', 'deDE', 'Die Zeit ist gekommen, Brüder. Wir dürfen den Feind nicht über den Frostdagger-Pass vordringen lassen. Ein paar von uns werden sicherlich fallen, aber will auch nur einer von Euch sich vor dieser Pflicht drücken? Vor dieser Ehre? Vorwärts! Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8891', 'deDE', 'Für den Kriegshäuptling!', 'Für den Kriegshäuptling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8892', 'deDE', 'Myolor, ich war einst Rüstungsschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Rüstungsschmiedes.', 'Myolor, ich war einst Rüstungsschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Rüstungsschmiedes.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8893', 'deDE', 'Myolor, ich war einst Waffenschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Waffenschmiedes!', 'Myolor, ich war einst Waffenschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Waffenschmiedes!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8894', 'deDE', 'Krathok, ich war einst Rüstungsschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Rüstungsschmiedes.', 'Krathok, ich war einst Rüstungsschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Rüstungsschmiedes.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8895', 'deDE', 'Krathok, ich war einst Waffenschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Waffenschmiedes!', 'Krathok, ich war einst Waffenschmied und möchte jetzt wieder den Hammer schwingen! Lehrt mich den Weg des Waffenschmiedes!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8896', 'deDE', 'Patrouilliert den Frostdagger-Pass. Lasst niemanden durch! Sterbt mit Ehre...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8897', 'deDE', 'Die Eindringlinge der Kolkar ziehen sich zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8898', 'deDE', 'Wir haben genügend Wolfsbälge gesammelt. Bald ist die Kavallerie bereit loszureiten! Kommt wieder zu mir, wenn wir genügend Widder eingefangen haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8899', 'deDE', null, 'Die Ställe sind wieder voll! Unsere Kavallerie wird bald losziehen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8900', 'deDE', null, 'Der Stall ist leer! Wir müssen unsere Kavallerie mit angemessenen Reittieren versorgen. Die Stormpike-Ställe brauchen Eure Hilfe!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8901', 'deDE', 'Tod den Frostwolf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8902', 'deDE', 'Wir reiten auf Euren Befehl!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8903', 'deDE', 'JAAA! Äh, an die Front mit Euch!', 'JAAA! Äh, an die Front mit Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8904', 'deDE', 'Ich erwarte Befehle von meinen Vorgesetzten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8905', 'deDE', 'Widderreiter, SORGT FÜR ORDNUNG!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8906', 'deDE', 'Leute. Der heutige Tag könnte für uns der großartigste werden, denn heute werden wir für den Ruhm der Stormpike kämpfen! Reitet in die Schlacht und gewährt unseren Feinden keine Gnade! Mäht diese Wilden nieder und lasst ihren Kriegshäuptling den Rest erledigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8907', 'deDE', 'Patrouilliert die Front, Soldaten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8908', 'deDE', 'Für den König!', 'Für den König!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8909', 'deDE', null, 'Die in Alterac beheimateten Widder sind lebenswichtig für die Einsatzfähigkeit unserer Kavallerie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8910', 'deDE', null, 'Noch ein paar mehr, dann quellen unsere Ställe von Reittieren über. Gebt das Tier in meine Obhut, Soldat. Ich kümmere mich dann darum.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8911', 'deDE', 'Mit Vergnügen. Diese Dinger stinken ja vielleicht!', 'Mit Vergnügen. Diese Dinger stinken ja vielleicht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8912', 'deDE', 'Ich möchte mein Tier unterbringen.', 'Ich möchte mein Tier unterbringen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8913', 'deDE', 'Also dann, Häscher! Die Befehle sind da! In den Kampf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8914', 'deDE', 'Das werde ich mit Vergnügen tun, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8915', 'deDE', '%s kommt aus der Pflanze hervor und ermöglicht so, dass sie wieder gesund wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8916', 'deDE', '%s greift an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8917', 'deDE', 'Grüße, $N. Fasst Euch kurz, denn es gibt viel zu tun.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8918', 'deDE', 'Möge Cenarius Euch leiten, $C.$B$BIch bin mit meinen Schwestern hier in diesem verderbten Land, um eine äußerst wichtige Mission für den Zirkel des Cenarius zu erfüllen. Allerdings bitte ich darum, dass sich nur jene, die stark genug sind, an mich wenden - jemand Geringeren zu schicken, wäre schiere Dummheit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8919', 'deDE', null, 'Meine Schwestern und ich kamen aus dem Steinkrallengebirge herunter, um eine Tragödie aus der fernen Vergangenheit zu beenden. Leider sind wir nicht stark genug, um unsere Ziele allein zu erreichen, und suchen jetzt nach Sterblichen wie Euch, die uns helfen könnten.$B$BFalls Ihr stark genug seid, haben wir eine Menge zu besprechen, $C. Falls Ihr es nicht seid, dann werde ich zu Eurer eigenen Sicherheit nicht über die Details meiner Mission reden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8920', 'deDE', null, 'Bitte werft einen Blick auf diese Schlangenstrunkranke, die ich in der Höhle gepflückt habe, damit Ihr wisst, wonach Ihr suchen müsst, $N...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8921', 'deDE', 'Ich grüße Euch, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8922', 'deDE', 'Frei! Endlich frei von meinen Banden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8923', 'deDE', 'Bitte werft einen Blick auf diese Schlangenstrunkranke, die ich in der Höhle gepflückt habe, damit Ihr wisst, wonach Ihr suchen müsst, $N...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8924', 'deDE', 'Ich brauche kein Heim, um zu wissen, dass ich in dieser Welt immer noch einen Platz habe, $C. Die Stämme werden das erkennen... alle werden es. Sie kämpfen gegeneinander, und wofür? Krumen, die die Geier übrig gelassen haben... Land, das von der Legion heimgesucht wurde... pah!$B$BDie Zentauren sollten stolz auf ihr Erbe sein... es ist ebenso edel wie das der Nachtelfen oder Orcs. Es wird langsam Zeit, dass wir uns auch dementsprechend verhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8925', 'deDE', 'Endlich bin ich von der Verderbnis befreit, die mich einst an diesen Ort band...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8926', 'deDE', 'Vielen Dank für Eure Mühen, $N.$B$BPasst gut auf das Szepter auf. Möge es Euch bei Euren Kämpfen gegen das Böse, das in diesen Ort eingedrungen ist, helfen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8927', 'deDE', 'Wie kann das sein, $N? Haltet Ihr einen Gegenstand wie das Szepter von Celebras etwa für bedeutungslos?$B$BDieses Mal kann ich Euch helfen, aber geht nicht noch einmal so achtlos mit diesem wertvollen Gegenstand um...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8928', 'deDE', 'Celebras, ich habe mein Szepter verloren...', 'Celebras, ich habe mein Szepter verloren...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8929', 'deDE', 'Ich habe beide Teile des Szepters bei mir.', 'Ich habe beide Teile des Szepters bei mir.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8930', 'deDE', 'Mein Szepter... Nach so vielen Jahren... Ihr habt es gefunden! Ihr seid wahrlich der Retter dieses Ortes...$B$BBitte hört mir gut zu, $N. Der Kopf und der Griff des Szepters können nur beim Stein von Maraudon zu einem einzigen Gegenstand wieder zusammengefügt werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8931', 'deDE', 'Bitte zeigt mir den Stein von Maraudon.', 'Bitte zeigt mir den Stein von Maraudon.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8932', 'deDE', 'Bitte erklärt mir, wie das Szepter funktioniert.', 'Bitte erklärt mir, wie das Szepter funktioniert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8933', 'deDE', 'Zwischen den Farben... ein Weg.$B$BBlinzelt hinüber ins Licht.$B$BAus dem Wasser steigt Ihr empor.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8934', 'deDE', 'Ich verstehe nicht so ganz...', 'Ich verstehe nicht so ganz...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8935', 'deDE', 'Zwischen dem orangefarbenen und dem lila Durchgang in der Nähe der Türen nach Maraudon findet Ihr einen dritten Weg.$B$BDas Szepter in Eurer Hand ist der Schlüssel dazu.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8936', 'deDE', 'Nein, wirklich, ich verstehe es immer noch nicht...', 'Nein, wirklich, ich verstehe es immer noch nicht...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8937', 'deDE', 'Ich bin es langsam leid...$B$BIhr könnt von hier aus die Wasserfälle sehen, $N... Das Szepter verleiht Euch die Macht, zu eben jenem Ort zu reisen, falls Ihr jemals den Wunsch verspürt, noch einmal durch die vorderen Tore Maraudons zu gehen.$B$BIn der Nähe des Eingangs nach Maraudon findet Ihr einen Durchgang, der unter die Höhlen führt. Benutzt das Szepter an dem alten Ritualplatz, um ein Portal zu erzeugen, dann findet Ihr Euch hier wieder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8938', 'deDE', 'Bitte erzählt mir mehr über Maraudon.', 'Bitte erzählt mir mehr über Maraudon.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8939', 'deDE', 'Vor langer Zeit begaben sich meine Dryaden-Schwestern und ich in diese Höhlen, um den gequälten Geist meines Onkels Zaetar zu befreien. Die Verderbnis, die sich in diesen Höhlen bereits ausgebreitet hatte, überwältigte uns jedoch schon bald und auch wir fielen ihr zum Opfer.$B$BWir waren dazu verdammt, in diesen Gängen umherzuirren. Die Toxine vergifteten unseren Verstand...$B$BUnd obwohl mein Geist jetzt zur Ruhe gekommen sein mag, weiß ich doch, dass ich bei meiner Mission versagt habe. Solch große Trauer umfängt mich jetzt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8940', 'deDE', 'Bitte erzählt mir mehr über Zaetar.', 'Bitte erzählt mir mehr über Zaetar.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8941', 'deDE', 'Ah, Zaetar... <Celebras wendet den Blick ab.>$B$BMein Vater Remulos versuchte, ihm Einhalt zu gebieten...$B$BZaetar war sehr eigensinnig, und als er einer Prinzessin der Erdelementare verfiel, wusste mein Vater, dass ihre Verbindung unter einem Fluch stand, aber Zaetar wollte einfach nicht auf ihn hören.$B$BDie Zentauren sind das Ergebnis seines Fehlers. Sie kamen missgebildet und hasserfüllt zur Welt. Mein Onkel Zaetar wurde von seinen eigenen Leuten getötet...$B$BDie böse Prinzessin Theradras indes wacht immer noch hier in den Kristallhöhlen über seine Gebeine.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8942', 'deDE', 'Diese Goblins sollten sich schämen. Pah!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8943', 'deDE', 'Geld aus dem Fenster zu werfen, ist NICHT der wahre Zweck des Winterhauchfests!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8944', 'deDE', 'Ich kann es nicht fassen, dass man diesen gierigen Goblins erlaubt, mit ihren Waren hausieren zu gehen und so den Geist dieses Fests in den Schmutz zu treten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8945', 'deDE', 'Elende Goblins... tun alles für eine Goldmünze.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8946', 'deDE', 'Ich hätte gern eine Ausgabe von \'Das Winterhauchfest\' zum Lesen.', 'Ich hätte gern eine Ausgabe von \'Das Winterhauchfest\' zum Lesen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8947', 'deDE', 'Vielleicht können wir das später probieren. Kommt wieder zu mir, wenn Ihr das wünscht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8948', 'deDE', 'Zusammengefügt werden die Teile bald wieder ein Ganzes sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8949', 'deDE', 'Shal myrinan ishnu daldorah...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8950', 'deDE', 'Lest diesen Folianten, den ich Euch hingelegt habe, und sprecht die Worte laut vor Euch hin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8951', 'deDE', '%s beginnt, seine Energie zu kanalisieren und auf den Stein zu konzentrieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8952', 'deDE', 'Ihr möchtet etwas über den Stein erfahren? Folgt mir.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8953', 'deDE', 'Viel zu lange musste ich in verfluchter Form umherwandeln. Ihr habt mich befreit... Eure harte Arbeit soll Euch vergolten werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8954', 'deDE', 'Bitte richtet Euch nach meinen Anweisungen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8955', 'deDE', 'Mein Szepter wird wieder heil sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8956', 'deDE', 'Willkommen bei der Forscherliga. Wenn Ihr Informationen braucht und diese in Büchern zu finden sind, kann ich Euch bestimmt helfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8957', 'deDE', 'Wie finde ich die beiden Teile Eures Szepters?', 'Wie finde ich die beiden Teile Eures Szepters?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8958', 'deDE', 'Als die Toxine, die in Maraudon lauerten, in meinen Verstand eindrangen, begann ich meinen Realitätssinn zu verlieren. Weil meine Dryadenschwestern besorgt waren, dass man mich für das Böse missbrauchen könnte, nahmen sie mir heimlich das Szepter weg, brachen es in zwei Teile und versteckten diese in den Höhlen.$B$BBald schon fielen auch sie dem Wahnsinn anheim und die beiden Teile des Szepters, die jetzt unbewacht waren, gelangten in den Besitz von Schlangenzunge. Der Satyr vertraute eines der Teile Noxxion an, einem bösen Elementar, und das andere besitzt er heute noch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8959', 'deDE', 'Warum hat Lord Schlangenzunge die beiden Teile des Szepters nicht wieder zusammengefügt?', 'Warum hat Lord Schlangenzunge die beiden Teile des Szepters nicht wieder zusammengefügt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8960', 'deDE', 'Ohne die Zauberformeln zu kennen, die für die Zusammenfügung der beiden Teile vonnöten sind, konnte Lord Schlangenzunge das Szepter nicht nutzen. Da ich jetzt aber von dem Fluch befreit bin, der mich Schlangenzunges Verderbnis unterjochte, kann ich Euch helfen, wenn Ihr mir die beiden Teile bringt - den Kopf und den Griff des Szepters.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8961', 'deDE', 'Erzählt mir mehr über die Geschichte von Remulos und Zaetar.', 'Erzählt mir mehr über die Geschichte von Remulos und Zaetar.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8962', 'deDE', 'Ihr fragt nach Cenarius\' Söhnen Remulos und Zaetar.$B$BMein Vater Remulos war stark und gut aussehend und mein Onkel Zaetar äußerst schlau und von schlanker Gestalt. Obwohl man zwar beide gleichermaßen respektierte, nagte in Zaetar stets das Gefühl, Remulos würde mehr Ruhm und Aufmerksamkeit zuteil werden.$B$BAls meine Schwestern und ich geboren wurden, machte sich Zaetar, blind vor Eifersucht, auf, seinen Bruder auszustechen... Er führte damit nur sein eigenes Ende herbei. Die Trauer, die ich darüber empfinde, hat nie nachgelassen, auch nach all dieser Zeit nicht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8963', 'deDE', 'Lange waren mein Geist und meine sterblichen Überreste hier in Maraudon gefangen, $C. Die Zeit hat mir nichts gegeben, außer reichlich Gelegenheit, über meine Fehler nachzudenken und zu erkennen, wie dumm meine Eifersucht auf Remulos war. Er versuchte mich zu warnen und mir klarzumachen, dass aus meiner Beziehung zu Theradras nichts Gutes entspringen würde - dass aus Chaos nur Zerstörung erwachsen würde. Mein Bruder hatte Recht.$B$BDoch ich muss Buße tun, auch wenn Ihr mich jetzt befreit habt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8964', 'deDE', 'Bitte, fahrt fort.', 'Bitte, fahrt fort.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8965', 'deDE', 'Obwohl ich als Ketzer verbannt wurde, bleibe ich meinem Glauben treu - das allein treibt mich voran. Aber damit mein Schicksal sich erfüllt, benötige ich die Hilfe eines Außenstehenden... jemanden, der keinem Zentaurenstamm angehört.$B$BFür diese Aufgabe muss jemand die heiligen Hallen von Maraudon betreten - das ist ein Tempel, den mein Volk schützt, und nur die Gläubigsten dürfen ihn betreten.$B$BWenn Ihr einwilligt, mir zu helfen, dann müsst Ihr nur nicken. Ich werde Euch ein Pergament mit meinen Anweisungen geben. Lest es durch und denkt über meine Worte nach.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8966', 'deDE', 'Ich helfe Euch. Gebt mir die Anweisungen.', 'Ich helfe Euch. Gebt mir die Anweisungen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8967', 'deDE', '%s verbeugt sich tief vor Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8968', 'deDE', 'Danke, $C.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8969', 'deDE', 'Seid gegrüßt, $N. Ich bin Doktor Gregory Victor, Cheftraumachirurg der Traumatologie der Horde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8970', 'deDE', 'Schön, Euch wiederzusehen, Doktor $N. Ich habe Eure Fortschritte verfolgt und würde sagen, dass es für Euch an der Zeit ist, ein paar der eher komplizierteren Verbandstechniken zu erlernen.$B$BBesucht mich doch gelegentlich, sobald sich Eure Fertigkeiten verbessern, dann werde ich Euch alles lehren, was ich weiß.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8971', 'deDE', 'Lehrt mich, wie man einen schweren Magiestoffverband herstellt und anlegt, Doktor.', 'Lehrt mich, wie man einen schweren Magiestoffverband herstellt und anlegt, Doktor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8972', 'deDE', 'Lehrt mich, wie man einen Runenstoffverband herstellt und anlegt, Doktor.', 'Lehrt mich, wie man einen Runenstoffverband herstellt und anlegt, Doktor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8973', 'deDE', 'Lehrt mich, wie man einen schweren Runenstoffverband herstellt und anlegt, Doktor.', 'Lehrt mich, wie man einen schweren Runenstoffverband herstellt und anlegt, Doktor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8974', 'deDE', 'Seid gegrüßt, $N. Ich bin Doktor Gustaf Van Howzen, Cheftraumachirurg der Traumatologie der Allianz.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8975', 'deDE', 'Großartig! Lasst uns für Nachschub sorgen, Leute!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8976', 'deDE', 'Danke für die Vorräte, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8977', 'deDE', 'Danke vielmals, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8978', 'deDE', 'Wir machen Fortschritte! Schafft mehr Vorräte für die Reparaturen von Dun Baldar herbei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8979', 'deDE', 'Wir haben genügend Vorräte! Unser Captain hat seine Position im Feld des Kampfes wieder eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8980', 'deDE', 'Jetzt machen wir Fortschritte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8981', 'deDE', 'Danke für die Rüstungsteile, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8982', 'deDE', 'Unser Ausrüstungslager füllt sich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8983', 'deDE', 'Danke für die Vorräte, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8984', 'deDE', 'Sehr gut. Unser Vorratslager füllt sich...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8985', 'deDE', 'Mehr Nachschub! Wir brauchen mehr Vorräte zum Wiederausrüsten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8986', 'deDE', 'Wir werden alles wieder aufbauen!  Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8987', 'deDE', null, 'Seid gegrüßt, $N! Gut das Ihr hier vorbeikommt! Wir sind bereit einen Bodenangriff über das Feld des Kampfes zu führen. Sobald Kriegsmeister Garrick seine Anweisungen erhält, werden er und seine Männer eine Bresche in die Reihen der Allianz schlagen!$B$BWollt Ihr im diese Befehle überbringen, $N? Garrick erwartet Euch auf dem Feld des Kampfes...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8988', 'deDE', null, 'Warum seid Ihr noch hier, $N? Habt Ihr ein Loch in Euren Stiefeln?$B$BIhr habt eure Angriffsbefehl - Kriegsmeister Garrick braucht ihn auf dem Feld des Kampfes bevor er seinen Angriff beginnen kann!  Geht jetzt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8989', 'deDE', null, 'Es ist gut, Veteranan wie Euch im Alteractal zu sehen, $N. Wir haben zu wenig Vorräte, um einen Bodenangriff durch das Tal zu starten. Redet mit unserem Rüstmeister - er wird Euch erklären, wie wir unsere Vorräte auffrischen können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8990', 'deDE', null, 'Seid gegrüßt, $C! Wir verfügen über genügend Vorräte, und wir können einen Bodenangriff im Feld des Kampfes starten. Wenn Euer Ruf etwas besser wäre, würde ich Euch das Überbringen der Angriffsbefehle anvertrauen.$B$BGeht also ins Feld und bekämpft die Allianz! Zeigt mir, dass Ihr ein fähiges und vertrauenswürdiges Mitglied der Horde seid!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8991', 'deDE', null, 'Ihr!  $N! Wieso wandert Ihr hier einfach so im Dorf herum? Allianztruppen sind in Alterac eingedrungen! Wir müssen sie vertreiben!$B$BUnsere Vorräte sind zu gering, um einen massiven Angriff zu starten, also sprecht mit unserem Rüstmeister. Er wird Euch erklären, wie wir unsere Vorräte aufstocken können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8993', 'deDE', null, 'Selbst der mutigste Krieger muss gut ausgerüstet sein, wenn er seinem Feind mit Stärke begegnen will!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8994', 'deDE', 'Ich habe die Belohnung fürs töten gerade $N gegeben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8995', 'deDE', 'Klickt hier um Wiederbelebungsnebenwirkungen aufzuheben und alle Eure Ausrüstung zu reparieren.', 'Klickt hier um Wiederbelebungsnebenwirkungen aufzuheben und alle Eure Ausrüstung zu reparieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8996', 'deDE', 'Hi! Hier könnt Ihr all Eure Wiederbelebungsnachwirkungen behandeln!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8997', 'deDE', 'Hier klicken um alle Eure Ausrüstung zu reparieren', 'Hier klicken um Eure Ausrüstung zu reparieren', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8998', 'deDE', 'Wir haben genügend Vorräte, um einen Bodenangriff zu starten. Wenn Ihr den Angriffsbefehl geben wollt und glaubt, dass Ihr qualifiziert genug seid, sprecht mit sergeant Yazra Bloodsnarl.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('8999', 'deDE', 'Wir haben genügend Vorräte, ume einen Bodenangriff zu starten. Wenn Ihr den Angriffsbefehl geben wollt und Euer Ruf gut genug ist, sprecht mit Korporal Noreg Stormpike.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9000', 'deDE', 'Ich bin verantwortlich für die Anwerbung beim Frostwolfklan. Als ein Soldat der Frostwolf ist es Eure Pflicht, Euch bei mir zu melden, sobald Ihr innerhalb des Klans im Rang aufsteigt.$B$BEhre und Respekt werden hier auf diesem eisigen Schlachtfeld gewonnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9001', 'deDE', 'Kriegsmeister, ich habe meine Abzeichen verloren. Könnt Ihr mir mit einem Ersatz aushelfen?', 'Kriegsmeister, ich habe meine Abzeichen verloren. Könnt Ihr mir mit einem Ersatz aushelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9002', 'deDE', 'Seid gegrüßt, $R! Ich bin Lieutenant Haggerdin, verantwortlicher Offizier in Sachen Nachschub für die Stormpikegarde.$B$BWenn Euer Ruf bei den Stormpike sich verbessert hat, kommt zurück zu mir, und ich werde Euch befördern. Solltet Ihr Euer Abzeichen verlieren, kann ich für Euch einen Ersatz besorgen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9003', 'deDE', 'Lieutenant, ich habe meine Abzeichen verloren. Könnt Ihr mir mit einem Ersatz aushelfen?', 'Lieutenant, ich habe meine Abzeichen verloren. Könnt Ihr mir mit einem Ersatz aushelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9004', 'deDE', 'Ich habe beide Teile des Szepters bei mir.', 'Ich habe beide Teile des Szepters bei mir.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9005', 'deDE', 'Argh, ich brauche hier Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9006', 'deDE', 'Sucht nach $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9007', 'deDE', 'Achtung da kommt was, $N - Haltet die Augen offen, Freunde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9008', 'deDE', 'Ihr werdet den Tag bereuen, an dem Ihr mir begegnet seid, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9009', 'deDE', 'Unsere Vorfahren lehren uns, dass der Einzelne stark sein muss, wir aber nur mit Vielen Sicherheit finden. Wollt Ihr die Ehre von Donnerfels verteidigen, indem Ihr eine Gilde gründet?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9010', 'deDE', 'Ihr seht wahrlich wie ein Anführer aus. Seid Ihr gekommen um eine Gilde zu organisieren, um für die Allianz zu Kämpfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9011', 'deDE', 'Ich bin nicht so leicht weg zu locken!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9012', 'deDE', 'Shenthul, Ich habe meine Signalpfeife verloren und keine Fackeln mehr. Bitte helft mir!', 'Shenthul, Ich habe meine Pfeife verloren und keine Fackeln mehr. Bitte helft mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9013', 'deDE', 'Seid gegrüßt, $C. Ich bin Rexxar, Champion der Horde, Diener von Thrall, Meister der Tiere der Wildnis.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9014', 'deDE', 'Rexxar, ich habe das Drachenfeueramulett verloren. Kann ein weiteres hergestellt werden?', 'Rexxar, ich habe das Drachenfeueramulett verloren. Kann ein weiteres hergestellt werden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9015', 'deDE', null, 'Was wollt Ihr vom blauen Drachenschwarm?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9016', 'deDE', 'Haleh, ich habe das Drachenfeueramulett verloren. Könnt Ihr ein weiteres herstellen?', 'Haleh, ich habe das Drachenfeueramulett verloren. Könnt Ihr ein weiteres herstellen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9017', 'deDE', 'Meister Nesingwary geht auf eine anstrengende Jagd. Ich finde es wesentlich entspannender, ihm bei den Vorbereitungen für einen Tag im Dschungel zu helfen, als ihn für einen Kampf auszurüsten. Allerdings muss ich zugeben, dass mir die glorreichen Momente auf dem Schlachtfeld doch fehlen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9018', 'deDE', 'Barnil, Ich glaube ich habe Kapitel I. verlegt.', 'Barnil, Ich glaube ich habe Kapitel I. verlegt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9019', 'deDE', 'Barnil, ich fürchte, ich habe Kapitel 2 falsch angebracht.', 'Barnil, ich fürchte, ich habe Kapitel 2 falsch angebracht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9020', 'deDE', 'Barnil, Ich glaube ich habe Kapitel III. verlegt.', 'Barnil, Ich glaube ich habe Kapitel III. verlegt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9021', 'deDE', 'Barnil, Ich glaube ich habe Kapitel IV. verlegt.', 'Barnil, Ich glaube ich habe Kapitel IV. verlegt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9022', 'deDE', 'Ich sehe, Ihr habt das Kapitel an dem Ihr solange gearbeitet habt verlegt, $N. Nun, Ihr habt es genau hier liegen lassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9023', 'deDE', 'Kalaran, bitte verzaubert die Fackel.', 'Kalaran, bitte verzaubert die Fackel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9024', 'deDE', null, 'Ich kenne die Wälder so gut, als währen sie ein Teil von mir... Doch wisset das man noch immer viel von ihnen lernen kann und sie bergen auch viel Gefahr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9025', 'deDE', 'Raene, ich habe Dartols Rute der Transformation verloren.', 'Raene, ich habe Dartols Rute der Transformation verloren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9026', 'deDE', null, 'Ja, ich dachte mir das Ihr zurückkehrt. Ich habe Korra nach Euch ausgesendet und sie fand Darols Stab verlassn im Wald.$B$BWir haben so hart dafür gearbeitet, ihn zurückzubekommen, $N. Geht bitte in Zukunft nicht so achtlos mit ihm um.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9027', 'deDE', null, 'Ihr habt Teronis Mission erfüllt, und dafür bin ich Euch sehr dankbar, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9028', 'deDE', null, 'Hi da! Diese Werkstatt gehört mir gar nicht, nein! Aber eines Tages, wenn ich meine Ausbildung beendet habe, werde ich ein Erfinder, genau wie mein Held Marvon Rivetseeker.$B$BIhr habt noch nie von Ihm gehört, stimmt\'s? Nun es passierte, dass er vor einiger Zeit verschwand. Man sagt er wollte einige uralte Trollruinen studieren, über die er ständig sprach. Er flog mit einem Zeppelin... ich bin mir allerdings nicht sicher wohin...$B$BTanarasin? Tenkeris? Taneranisani?$B$B<Liv schüttelt ihren Kopf.>$B$BNein, das war\'s auch nicht...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9029', 'deDE', null, 'Hallo! Nein, Marvon ist nicht hier, und er wird wohl für eine Weile nicht zurückkommen. In Wahrheit beginne ich bereits, mir Sorgen um ihn zu machen...$B$BIch hoffe, er kommt bald zurück. Es ist nämlich eine Kiste für ihn geliefert worden, und die ist für mich einfach zu schwer, um sie hier hineinzutragen. Ich musste sie erstmal draußen stehen lassen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9030', 'deDE', 'Ihr habt bereits alles gelernt, was ich Euch bis jetzt beibringen kann, Doktor $N. Kommt zu mir zurück, wenn Ihr geübter seid, und ich werde Euch in der fortgeschrittenen medizinischen Lehre unterweisen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9031', 'deDE', 'Lauft! Sie kommen!', 'Lauft! Sie kommen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9032', 'deDE', null, 'Dieser Ort... ist verflucht.$B$BIch habe es kaum geschafft zu entkommen. Daher bringe ich es kein zweites mal fertig, in diese Tunnel hinabzusteigen$B$BMein Bruder und meine Schwester hatten weniger Glück... Sie sind noch drinnen gefangen, und leiden unter dem verderbten Nebel welcher von dem dort wachsenden bösen Garten ausgeht.$B$BBitte, Ihr müsst helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9033', 'deDE', null, 'Oh, ich bin so froh, dass Ihr zurückgekommen seid, $N.$B$BDer Geist meines Bruders ist nun endlich befreit von der Verderbtheit, doch habe ich mich entschieden, weiter zu trauern.$B$BIch danke Euch sehr für Eure Hilfe, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9034', 'deDE', 'Eindringlinge! Invasoren! Das allsehende Auge wurde gestohlen! $G Dieser miese Hund:Diese miese Hündin;, $N, hat es in $G seinem:ihren; Besitz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9036', 'deDE', null, 'Braucht Ihr Vorräte?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9037', 'deDE', 'Was verkauft Ihr?', 'Was verkauft Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9038', 'deDE', 'Korrak ist gekommen, Schwächlinge! Snowfall gehört zu Winterax!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9039', 'deDE', 'Wieviele Vorräte benötigen wir noch?', 'Wieviele Vorräte benötigen wir noch?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9040', 'deDE', 'Alterac Valley - Alliance Quatmaster Greeting Option Default', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9041', 'deDE', 'Der Kommandant wurde getötet! Stürmt ihre Reihen! Sterbt in Ehre!', 'Der Kommandant wurde getötet! Stürmt ihre Reihen! Sterbt in Ehre!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9042', 'deDE', 'Unser Kommandant ist gefallen! Die Zeit der Rache ist gekommen! Widerreiter zum Angriff!', 'Unser Kommandant ist gefallen! Die Zeit der Rache ist gekommen! Widerreiter zum Angriff!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9044', 'deDE', 'Kommandant, die Zeit ist reif, dass die Wolfsreiter die gegnerischen Linien stürmen. Gebt den Befehl.', 'Kommandant, die Zeit ist reif, dass die Wolfsreiter die gegnerischen Linien stürmen. Gebt den Befehl.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9045', 'deDE', 'Sir, ich erwarte Ihre Anweisungen. Auf Ihr Kommando, werde ich die Hölle auf sie loslassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9046', 'deDE', 'Der Befehl ist gegeben worden! Tod den Stormpike! Greift den Gegner an!', 'Der Befehl ist gegeben worden! Tod den Stormpike! Greift den Gegner an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9047', 'deDE', 'Kommandant, die Zeit den Frostwolfschmutz anzugreifen ist nun gekommen! Überrennt sie!', 'Kommandant, die Zeit den Frostwolfschmutz anzugreifen ist nun gekommen! Überrennt sie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9048', 'deDE', 'Truffles!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9049', 'deDE', 'Sir, auf Euren Befehl hin werde ich die Kavallerie auf den Feind loslassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9050', 'deDE', 'Zum Angriff! Überennt sie!', 'Zum Angriff! Überennt sie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9051', 'deDE', 'Nun seid Ihr dran Eure Füße zu pöckeln!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9052', 'deDE', 'Zeit zum Metzeln...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9053', 'deDE', 'Eindringling!', 'Eindringling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9054', 'deDE', 'Tötet es!', 'Tötet es!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9055', 'deDE', 'Raaaaaaaaaaaaarrrr!', 'Raaaaaaaaaaaaarrrr!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9056', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9057', 'deDE', 'Der Stormpike-Friedhof wurde von der Allianz erobert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9058', 'deDE', 'Der Stormpike-Friedhof wird angegriffen! Wenn er unbewacht bleibt, wird die Allianz ihn erobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9059', 'deDE', 'Der Stormpike-Friedhof wurde von der Horde erobert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9060', 'deDE', 'Der Stormpike-Friedhof wird angegriffen! Wenn er unbewacht bleibt, wird die Horde ihn erobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9061', 'deDE', 'Der Snowfall-Friedhof wurde von der Allianz erobert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9062', 'deDE', 'Der Snowfall-Friedhof wird angegriffen! Wenn er unbewacht bleibt, wird die Allianz ihn erobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9063', 'deDE', 'Der Snowfall-Friedhof wurde von der Horde erobert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9064', 'deDE', 'Der Snowfall-Friedhof wird angegriffen! Wenn er unbewacht bleibt, wird die Horde ihn erobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9065', 'deDE', 'Der Friedhof der Frostwolf wurde von der Allianz erobert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9066', 'deDE', 'Der Friedhof der Frostwolf wird angegriffen! Wenn er unbewacht bleibt, wird die Allianz ihn erobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9067', 'deDE', 'Der Friedhof der Frostwolf wurde von der Horde erobert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9068', 'deDE', 'Der Friedhof der Frostwolf wird angegriffen! Wenn er unbewacht bleibt, wird die Horde ihn erobern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9069', 'deDE', 'Ihr sucht Anleitung in der Kriegskunst, $R? Dann seid Ihr hier falsch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9070', 'deDE', 'Ihr seid hier, um etwas über gewonnene und verlorene Kämpfe auf diesem Schlachtfeld zu erfahren? Hier gibt es keine Gewinner...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9071', 'deDE', 'Kommt, ihr Wichte! Tretet mir gegenüber!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9072', 'deDE', 'Dieser Ort steht unter meinem Schutz. Die Mysterien des Arkanen werden unberührt bleiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9073', 'deDE', 'Das ist der Preis der Neugier.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9074', 'deDE', 'Seid gegrüßt, Soldat. Der Geheimdienst hat mir berichtet, dass die Winteraxtrolle im Westen einen Meister der Goblin-Ingenieure gefangen genommen haben. Vielleicht solltet Ihr etwas mehr darüber herausfinden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9075', 'deDE', 'Meine Späher berichteten mir, dass das Syndikat in einer versteckten Höhle auf dem östlichen Plateau ein Lager aufgeschlagen hat. Seid vorsichtig, wenn Ihr in diese Gegend reisen wollt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9076', 'deDE', 'Seid vorsichtig, Soldat! Die Stormpike haben Landminen um ihre Lager herum ausgelegt. Sprecht mit unserem Sprengexperten im Wachturm im Osten, wenn Ihr wissen wollt, wie ihr die Minen entschärfen könnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9077', 'deDE', 'Beeilt euch, Soldat. Der Geheimdienst hat mir berichtet, dass die Winteraxtrolle einen Meister der Gobliningenieure gefangen genommen haben. Die Bastarde der Frostwolf versuchen bereits, ihn zu retten, damit sie seine technischen Geräte gegen uns verwenden können! Ihr dürft das nicht zulassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9078', 'deDE', 'Es wird berichtet das, das Syndikat wohl in einer Höhle auf dem östlichen Plateau herumschnüffelt. Findet heraus was sie dort treiben, Soldat! Und achtet auf Aktivitäten der Frostwolf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9079', 'deDE', 'Seid vorsichtig, wenn Ihr das Gebiet der Frostwolf betretet, Soldat! Die ganze verdammte Gegend ist vermint! Massenweise Landminen. Sprecht mit unserem Sprengmeister im ersten Turm in der Frontlinie. Ich bin mir sicher, dass er Euch verraten kann, wie Ihr die Minen entschärfen könnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9080', 'deDE', 'Ja, Kommandant.', 'Ja, Kommandant.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9081', 'deDE', 'Dann hört genau zu, $C.$B$BKönnt ihr die von mir ausgehende Kraft spüren? Steigert es nicht Eure Moral? Das gleiche gilt auch für die Gegenseite. Die feindliche Verteidigung zu brechen und die Reihen ihrer Truppen auszudünnen wird keine einfache Sache. Aber es muss getan werden! Der Sieg gehört uns!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9082', 'deDE', 'Wen soll ich töten? Ihr müsst nur in ihre Richtung zeigen, Kommandant.', 'Wen soll ich töten? Ihr müsst nur in ihre Richtung zeigen, Kommandant.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9083', 'deDE', 'Sechs Lieutenants kümmern sich um die Verteidigung der Frontlinie. Sie müssen zuerst ausgeschaltet werden, und zwar schnell! Wir können nicht riskieren, dass einer ihrer Priester gefallene Lieutenants wiederbelebt.$B$BWenn alle Lieutenants in schneller Folge getötet werden, wird die Moral der Frontlinie wanken! Sie wird zerschlagen werden! Ihre Truppen werden ängstlich davonlaufen und die übrigbleibenden Soldaten werden keinen Ersatz haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9084', 'deDE', 'Das war schon alles? Ich dachte, Ihr wolltet mich herausfordern.', 'Das war schon alles? Ich dachte, Ihr wolltet mich herausfordern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9085', 'deDE', 'Nein, $C, das ist nicht alles. Die Elitestreitkräfte der Gegenseite werden von den vier Kommandanten des Lagers befehligt. Wenn es Euch gelingt, ihre Kommandanten mit einem schnellen, tödlichen Schlag zu beseitigen, werden ihre Elitestreitkräfte zu wanken beginnen. Ihre Reihen werden sich dann schnell lichten.$B$BAber seid gewarnt, Soldat. Kommandanten und Lieutenants sind keine leichte Beute. Sie haben sich ihre Ränge verdient...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9086', 'deDE', 'Und alle in die Hände klatschen, und alle in die Hände klatschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9087', 'deDE', 'Dankeschön, vielen Dank! Ich bin hier noch die ganze Woche!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9088', 'deDE', 'Habt Ihr ein wenig Kleingeld?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9089', 'deDE', 'Ich hatte alles! Ich hatte alles und verlor es wieder. Verloren... komplett weg... wie... mein Gedächtnis. Mein Gedächtnis ist wie... Käse. Ich mag Käse.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9090', 'deDE', 'Zeigt Eure Verehrung für die Kunst, indem Ihr mir ein paar Münzen in meinen Hut werft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9091', 'deDE', 'Suche nach Lebensformen... kein Ziel gefunden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9092', 'deDE', 'Unbekannte Lebensform entdeckt....berechne...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9093', 'deDE', 'Ziel verloren.... setze Suchmuster Delta fort', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9094', 'deDE', 'Forscher meldet sich!....Hervorragend!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9095', 'deDE', '%s starrt hungrig auf die Untergrundratte', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9096', 'deDE', 'Mmmm... du siehst zum Fressen aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9097', 'deDE', 'Einen Moment bitte... Ich denke ich lehne ab.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9098', 'deDE', '42... 42... 42 was? Das kann ja alles mögliche sein. 42... hmmm...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9099', 'deDE', 'Wie Ihr befehlt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9100', 'deDE', 'Meine Kräfte schwinden, Sterbliche. Ich habe gemacht, was ich versprochen habe, der Weg ist frei. Ich werde nun dahin zurückkehren, woher ich gekommen bin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9101', 'deDE', 'Hilflos... keine Energie mehr... wieder herstellen... muss wieder herstellen... Zevrim hat Kontrolle... beendet diese Qual...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9102', 'deDE', 'Danke, Sterblicher. Es gibt noch einen Dienst, den ich Euch erweisen kann, bevor ich wieder in den Traum zurückkehre.$B$B.Die Tür zu Zevrims Meister, Alzzin, sie blockiert Euren Weg. Ich kann Euch als Mittel der Zerstörung dienlich sein. Die Tür wird geöffnet werden, ich erwarte Euren Befehl.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9103', 'deDE', 'Danke, Eisenborke. Wir öffnen Euch die Tür.', 'Danke, Eisenborke. Wir öffnen Euch die Tür.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9104', 'deDE', 'Endlich... Befreit von seiner Umklammerung der Verfluchung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9105', 'deDE', 'Wer kontrolliert den Stonehearth-Friedhof?', 'Wer kontrolliert den Stonehearth-Friedhof?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9106', 'deDE', 'Der Stonehearth-Friedhof liegt östlich vom Stonehearth-Außenposten im Feld des Kampfes.$B$BEr wird momentan von der Allianz kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9107', 'deDE', 'Der Stonehearth-Friedhof liegt östlich vom Stonehearth-Außenposten im Feld des Kampfes.$B$BEr wird momentan von der Horde kontrolliert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9110', 'deDE', 'Wieviele Vorräte werden noch benötigt, um Bodenangriffe starten zu können?', 'Wieviele Vorräte werden noch benötigt, um Bodenangriffe starten zu können?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9111', 'deDE', 'Wieviele Vorräte benötigen wir, um unseren Stützpunkt wieder aufzubauen?', 'Wieviele Vorräte benötigen wir, um unseren Stützpunkt wieder aufzubauen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9112', 'deDE', 'Wir haben viele Vorräte, aber wir benötigen immer noch mehr, um einen Bodenangriff im Feld des Kampfes starten zu können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9113', 'deDE', 'Wir haben zwar ein paar Vorräte, aber nicht genügend. Wir benötigen sehr viel mehr, um einen Bodenangriff im Feld des Kampfes starten zu können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9114', 'deDE', 'Wir haben kaum noch Vorräte. Wenn wir einen Bodenangriff im Feld des Kampfes starten wollen, werden wir noch sehr viel mehr Vorräte brauchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9115', 'deDE', 'Wir haben eine große Menge an Vorräten, um Burg Frostwolf wieder aufzubauen. Nur noch ein wenig mehr, und wir können sie wieder in Stand setzen und in die Schlacht zurückkehren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9116', 'deDE', 'Wir verfügen über genügend Vorräte für den Wiederaufbau. Wir brauchen noch sehr viel mehr, um Burg Frostwolf vollständig in Stand zu setzen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9117', 'deDE', 'Wir haben nur sehr wenig Vorräte für den Wiederaufbau von Burg Frostwolf. Für die Instandsetzung und die Wiederaufnahme des Kampfes brauchen wir noch sehr viel mehr.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9118', 'deDE', 'Wir haben kaum Vorräte, um Burg Frostwolf wieder aufzubauen. Wir brauchen sehr viel mehr Vorräte, um unser Lager in Stand zu setzen und wieder Bodenangriffe starten zu können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9119', 'deDE', '$N, wir haben genügend Vorräte, um Bodenangriffe im Feld des Kampfes zu starten. Wenn Ihr den Angriffsbefehl geben wollt und Euer Rang hoch genug ist, sprecht mit Sergeant Yazra Bloodsnarl in Frostwolf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9120', 'deDE', 'Wir haben sehr viele Vorräte. Noch ein wenig mehr, und wir sind in der Lage, Dun Baldar in Stand zu setzen und wieder in den Kampf zu ziehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9121', 'deDE', 'Wir haben sehr viele Vorräte für die Instandsetzung gesammelt, aber wir benötigen noch weitere für die Reparaturen von Dun Baldar. Bringt mehr Vorräte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9122', 'deDE', 'Wir haben nicht sehr viele Vorräte. Wir benötigen noch sehr viel mehr für die Instandsetzung von Dun Baldar.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9123', 'deDE', 'Dun Baldar ist in sehr schlechtem Zustand, und wir sind knapp an Vorräten für den Wiederaufbau. Die Allianz braucht Eure Hilfe bei der Vorratsbeschaffung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9124', 'deDE', '$N, wir haben genügend Vorräte, um einen Bodenangriff zu starten. Wenn Euer Ruf bei der Stormpikegarde gut genug ist, sprecht mit Korporal Noreg Stormpike.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9125', 'deDE', 'Wir haben eine Menge überschüssiger Vorräte, aber wir brauchen noch mehr, bevor wir einen Bodenangriff im Feld des Kampfes starten können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9126', 'deDE', 'Wir haben überschüssige Vorräte, jedoch nicht besonders viel. Wir brauchen noch sehr viel mehr, bevor wir einen Bodenangriff im Feld des Kampfes starten können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('9127', 'deDE', 'Wir haben kaum überschüssige Vorräte. Wir müssen noch hart arbeiten, bis wir einen Bodenangriff im Feld des Kampfes starten können.', null, '31882');
