INSERT INTO `broadcast_text_locale` VALUES ('9129', 'deDE', 'Wieviele Vorräte werden noch benötigt, um unser Lager wieder aufbauen zu können?', 'Wieviele Vorräte werden noch benötigt, um unser Lager wieder aufbauen zu können?', '31882');
