INSERT INTO `broadcast_text_locale` VALUES ('10220', 'deDE', 'Die Horde hat den Hof eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10221', 'deDE', '$N hat den südlichen Hof verteidigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10222', 'deDE', '$N hat den Hof verteidigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10223', 'deDE', 'Das nenne ich einen guten Fang! Ihr ehrt die Horde, Junge, wir danken dir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10224', 'deDE', '$N hat das Sägewerk angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10225', 'deDE', '$N hat das Sägewerk angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10226', 'deDE', 'Die Allianz hat das Sägewerk eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10227', 'deDE', 'Die Horde hat das Sägewerk eingenommen!', null, '31882');
