INSERT INTO `broadcast_text_locale` VALUES ('10230', 'deDE', '$N hat die Mine angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10231', 'deDE', '$N hat die Mine angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10232', 'deDE', 'Die Allianz hat die Mine eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10233', 'deDE', 'Die Horde hat die Mine eingenommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10234', 'deDE', '$N hat die Mine verteidigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10235', 'deDE', '$N hat die Mine verteidigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10236', 'deDE', 'Ich verspreche Euch eines: Das Tal wird unser sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10237', 'deDE', 'Wie viele unserer Kommandanten sind am Leben, Vanndar?', 'Wie viele unserer Kommandanten sind am Leben, Vanndar?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10238', 'deDE', '$962w, Soldat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10239', 'deDE', 'Wie viele unserer Lieutenants sind am Leben, Vanndar?', 'Wie viele unserer Lieutenants sind am Leben, Vanndar?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10240', 'deDE', '$950w, Soldat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10241', 'deDE', 'Wie viele der Offiziere der Frostwolf sind am Leben, Vanndar?', 'Wie viele der Offiziere der Frostwolf sind am Leben, Vanndar?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10242', 'deDE', '$966w Kommandant(en) der Frostwolf und $964w Lieutenant(s) der Frostwolf, Soldat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10243', 'deDE', 'Soldaten der Stormpike, euer General wird angegriffen! Ich fordere Unterstützung! Kommt! Kommt! Löscht diese räudigen Hunde der Frostwolf aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10245', 'deDE', 'Stormpike-Abschaum! In meiner Burg?! Tötet sie alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10249', 'deDE', 'Hmmm... wo ist mein Angelhaken? Oh, da ist er ja.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10250', 'deDE', 'Mal sehen, ob die Fische anbeißen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10251', 'deDE', null, 'Hast du Hunger, Trok? Ich mache gerade etwas zu essen für uns!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10252', 'deDE', null, 'Hier ist etwas zu essen für dich, Trok! Trok? Warum rennst du wie ängstliche Menschen herum?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10253', 'deDE', null, 'Ist mir recht, scheinbar bist du nicht so hungrig. Das nächste Mal kannst du dir dein Essen selber machen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10255', 'deDE', null, 'Ich frage mich, ob die Schweine hungrig sind...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10256', 'deDE', null, 'Seid ihr Schweinchen hungrig? Ich habe etwas zu fressen für euch...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10257', 'deDE', null, 'Ich schätze ihr wart hungrig! Trok hätte euch schon viel früher füttern sollen... Was hat er wohl all die Zeit über getan?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10259', 'deDE', 'Hilfe! Ich werde von einem Bienenschwarm verfolgt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10260', 'deDE', 'Ich kann nicht mehr rennen, aber diese Bienen lassen mich nicht in Ruhe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10261', 'deDE', 'Ich wusste nicht, dass Bienen kein Feuer mögen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10262', 'deDE', 'Kannst du mir bitte helfen diese Bienen loszuwerden, Karu? Sie stechen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10265', 'deDE', 'Aaahh... sehr gut. Jetzt aber los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10266', 'deDE', 'Wie Ihr wünscht, Sire.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10267', 'deDE', 'Sofort, Sire.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10268', 'deDE', 'Wenn Ihr gekommen seid, um die Welt zu retten, dann seid Ihr hier richtig. Der Gesichtslose, der Blutgott Hakkar, wurde zurück in unsere Welt gebracht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10269', 'deDE', 'Die Stärke Eurer Entschlossenheit strahlt, $N. Ihr beweist auch weiterhin, dass Ihr ein Gewinn für unsereins seid. Eines Tages wird die Welt das Ausmaß Eurer großen Taten erkennen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10270', 'deDE', 'Der erblühende Held ist zurück! Es ist gut Euch wieder zu sehen, $N. Ihr seid in unserem Dorf stets willkommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10271', 'deDE', 'Ich weiß von einem anderen, der einst wie Ihr war... Sein Name war Vol\'jin. Ihr seid eine Seltenheit, $G mein Freund:meine Freundin;.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10272', 'deDE', 'Der Weg, der hinter Euch liegt, war lang und mühsam. Nur wenige erreichen diesen Stand innerhalb unserer Ränge. Ihr seid ein guter Freund des zandalarianischen Imperiums, $N.$B$BVielleicht sogar schon eine Legende... ja... vielleicht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10273', 'deDE', 'Sofort, Sire.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10274', 'deDE', null, 'Kann ich noch mehr Steine für meine Halskette sammeln gehen, Papa?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10275', 'deDE', 'Ja, Ihr könnt, Okla. Treibt Euch aber nicht zu weit von zu Hause umher.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10276', 'deDE', null, 'Dabu! Ich werde vorsichtig sein, Papa!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10277', 'deDE', null, 'Ooohh... dieser Stein ist so schön und bunt. Vielleicht kann ich ja noch mehr davon finden...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10278', 'deDE', null, 'Hmm... dieser Stein ist schön, aber zu klein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10279', 'deDE', null, 'Das hier ist ein glänzender Stein, ich werde ihn in meinen Eimer legen. Ok, ich brauche nur noch ein paar mehr...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10280', 'deDE', null, 'Ich bin mir sicher, dass mein Freund Gruk diesen Stein gerne für den Speer hätte, an dem er gerade bastelt! Er ist sehr scharfkantig... ich hoffe er gefällt ihm.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10281', 'deDE', null, 'Na also... diese Steine dürften für eine Halskette genügen. Ich sollte jetzt wohl sowieso nach Hause gehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10282', 'deDE', '$N hat die Mine besetzt! Wenn niemand eingreift, hat sie die Allianz in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10283', 'deDE', '$N hat die Mine besetzt! Wenn niemand eingreift, hat sie die Horde in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10284', 'deDE', '$N hat das Sägewerk besetzt! Wenn niemand eingreift, hat es die Allianz in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10285', 'deDE', '$N hat Sägewerk besetzt! Wenn niemand eingreift, hat es die Horde in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10286', 'deDE', '$N hat die Ställe besetzt! Wenn niemand eingreift, hat sie die Allianz in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10287', 'deDE', '$N hat die Ställe besetzt! Wenn niemand eingreift, hat sie die Horde in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10288', 'deDE', '$N hat den Hof besetzt! Wenn niemand eingreift, hat ihn die Allianz in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10289', 'deDE', '$N hat den Hof besetzt! Wenn niemand eingreift, erlangt die Horde in 1 Minute die Kontrolle über ihn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10290', 'deDE', '$N hat die Schmiede besetzt! Wenn niemand eingreift, hat sie die Allianz in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10291', 'deDE', '$N hat die Schmiede besetzt! Wenn niemand eingreift, hat sie die Horde in 1 Minute unter Kontrolle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10292', 'deDE', null, 'Lecker. Ich liebe gerösteten Ebenenschreiter.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10293', 'deDE', 'Ich werde trainieren und ein Krieger werden, genau wie mein Vater!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10294', 'deDE', 'Zeit, sich eine ahnungslose Harpyie vorzunehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10295', 'deDE', 'Hier ist einer! Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10296', 'deDE', 'Nimm das, räudige Harpyie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10297', 'deDE', 'Einem Krieger der Horde könnt Ihr nicht entkommen! Nehmt das! Und das!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10298', 'deDE', 'Verschwindet in Eure Heimat, wo Ihr hingehört!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10299', 'deDE', 'Aaahhh... Ein weiterer glorreicher Sieg für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10300', 'deDE', null, 'Mogg, sieh zu, dass du reinkommst. Hier draußen ist es nicht sicher, solange dein Vater weg ist.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10301', 'deDE', 'Sofort, Mama!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10303', 'deDE', 'Wann kommt Papa denn endlich aus der Kriegshymnenschlucht zurück? Dauern die Kämpfe noch lange? Er ist schon so lange weg…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10304', 'deDE', null, 'Bald Kind… schon sehr bald.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10305', 'deDE', null, 'Was ist mit unseren Schweinen passiert?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10306', 'deDE', 'Wache, bringt den Gefangenen! Platziert ihn im Kreis der Bindung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10307', 'deDE', 'Diener, beeilt Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10308', 'deDE', 'Bewegung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10309', 'deDE', 'Auf die Knie, Ungläubiger!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10310', 'deDE', 'Seltsam. Ein Diener Rastakhans, der nicht weiß, dass die Hakkari nicht in der alten Sprache sprechen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10311', 'deDE', 'Ich sagte, KNIET NIEDER!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10312', 'deDE', 'Wisst Ihr überhaupt, wen Ihr vor Euch habt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10313', 'deDE', 'Ihr seid ein Narr, der bald sein Leben verliert. Oder nicht?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10314', 'deDE', 'Ich könnte Euch mit einer einzigen Bewegung vernichten! Fordert nicht meinen Zorn heraus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10315', 'deDE', '%s lacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10316', 'deDE', 'Wie armselig. Die Kraft des Seelenschinders verleiht mir Kraft und Schutz. Außer der Macht des allmächtigen Hakkar brauche ich nichts und niemanden zu fürchten. Einzig mein Meister Jin, vermag mir Befehle zu erteilen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10317', 'deDE', 'Hakkar ist also zurückgekehrt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10318', 'deDE', 'Habt Ihr wirklich noch Zweifel? Spürt Ihr nicht seine Gegenwart? Ist die rechte Hand des allmächtigen Königs Rastakhan in diplomatischer Mission hierher entsandt worden?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10319', 'deDE', 'Ihr hört richtig, Hakkar ist zurückgekehrt! Nicht mehr lange, und die Welt wird unter seiner Macht erbeben. Er wird sie verschlingen und nichts außer Ruinen zurücklassen…', 'Ihr hört richtig, Hakkar ist zurückgekehrt! Nicht mehr lange, und die Welt wird unter seiner Macht erbeben. Er wird sie verschlingen und nichts außer Ruinen zurücklassen…', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10320', 'deDE', 'Das werden wir nicht zulassen… Wir haben ihn einst aus dieser Welt verbannt und wir werden es wieder tun.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10321', 'deDE', '%s lacht irre.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10322', 'deDE', 'NARR! Behauptet Ihr allen Ernstes auch nur ein Zehntel der Macht zu besitzen, über die Ihr während des Aufstandes verfügt habt? Alle Nationen waren vereint und wären dennoch beinahe von Hakkars Schatten verschlungen worden. Ihr habt nicht den Hauch einer Chance.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10323', 'deDE', 'Und jetzt haben sich selbst diejenigen von Euch abgewandt, die Ihr einst als Freunde betrachtet habt. Wie es aussieht, haben sich Eure ehemaligen Verbündeten unserer Sache angeschlossen… Schaut nicht so überrascht drein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10324', 'deDE', 'Bloodscalp, Sandfury, Skullsplitter, Vilebranch und Witherbark. Sie alle schwören Jin die Treue; doch ihre Seelen gehören Hakkar. Ihr habt verloren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10325', 'deDE', 'Die Zeiten haben sich geändert. Die jungen Völker werden uns unterstützen. Diese Auseinandersetzung werdet Ihr nicht gewinnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10326', 'deDE', 'Die jungen Völker? Diese Kinder? Menschen? Orcs? Zwerge? Ihr glaubt wirklich, die Hakkari besiegen und einen GOTT vernichten zu können?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10327', 'deDE', 'Das wird NIEMALS passieren. Wir sind Legionen. Wir stehen zusammen. Wo sind all Eure Helden? Vol\'jin thront völlig ahnungslos an der Seite des jungen Kriegshäuptlings. Zul\'jin wird vermisst, ist vielleicht sogar schon tot.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10328', 'deDE', 'Und… glaubt Ihr wirklich, die wilden Götter besiegen zu können?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10329', 'deDE', '%s grinst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10330', 'deDE', 'Die wilden Götter? Unmöglich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10331', 'deDE', 'Tatsächlich? Ruft sie herbei, Diener.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10332', 'deDE', '%s deutet mit seinem Zeigefinger eine Schneidebewegung an seinem Hals an.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10333', 'deDE', 'Wo sind Eure Götter jetzt? WO NUR?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10334', 'deDE', 'Streckt mich nieder und ich werde wiedergeboren werden… stärker als je zuvor…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10335', 'deDE', '%s lässt seine Schultern sinken und entblößt seinen Nacken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10336', 'deDE', '%s spuckt auf den Leichnam des gefallenen Zeloten', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10337', 'deDE', 'I would like to enter my e-coupon code to win the virtual item.', 'I would like to enter my e-coupon code to win the virtual item.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10338', 'deDE', 'Welcome to the iCoke-WOW summer promotion.$B$BDear $N, the halo of fortune goddess is shining on you.  Obey to the order of god; you\'ll get a special and mystery present.  Now, the gate of Azeroth is open for you.  A great adventure will begin soon.  Are you ready?$B$BMay the honor be with you!', 'Welcome to the iCoke-WOW summer promotion.$B$BDear $N, the halo of fortune goddess is shining on you.  Obey to the order of god; you\'ll get a special and mystery present.  Now, the gate of Azeroth is open for you.  A great adventure will begin soon.  Are you ready?$B$BMay the honor be with you!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10339', 'deDE', null, 'Guse tritt in die Schlacht ein! Zeigen wir den Stormpike, wo es lang geht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10340', 'deDE', null, 'Erwarte Anweisungen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10341', 'deDE', 'Es ist soweit, Guse. Ihr könnt wieder fliegen!', 'Es ist soweit, Guse. Ihr könnt wieder fliegen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10342', 'deDE', null, 'Jeztor kommt euch holen, elende Stormpike!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10343', 'deDE', 'Jeztor lebt! Es ist Zeit, den Nachthimmel mit unserem Feuer zu erhellen, Kommandant.', 'Jeztor lebt! Es ist Zeit, den Nachthimmel mit unserem Feuer zu erhellen, Kommandant.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10344', 'deDE', null, 'Lasst mich auch, $N! Ein Wort von Euch genügt und Ich greife die Brücke an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10345', 'deDE', 'Alles ist bereit, Sir! Auf Euren Befehl lassen wir unser Höllenfeuer auf die feindliche Basis regnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10346', 'deDE', 'Fühlt Ihr das, Mulv? Dieses Verlangen? Erst dann, wenn Ihr es wirklich wollt, kann ich Euch den Befehl erteilen. Was rede ich da? Raus mit Euch!', 'Fühlt Ihr das, Mulv? Dieses Verlangen? Erst dann, wenn Ihr es wirklich wollt, kann ich Euch den Befehl erteilen. Was rede ich da? Raus mit Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10347', 'deDE', 'Die Luftunterstützung für Dun Baldar naht! Geht besser in Deckung, Stormpike!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10348', 'deDE', 'Bereit zum Flugeinsatz, Kommandant! Geben Sie den Befehl und Ich werde wie ein Geier über die feindliche Basis herfallen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10349', 'deDE', 'Greift die Basis an. Wir werden sie gleichzeitig vom Boden aus belagern.', 'Greift die Basis an. Wir werden sie gleichzeitig vom Boden aus belagern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10350', 'deDE', 'Ich werde den Pass zur Burg Frostwolf entlang patroullieren. Ich warte nur noch auf Euren Einsatzbefehl, Kommandant.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10351', 'deDE', 'Der Angriff muss so präzise wie möglich erfolgen, Slidore. Legt die Straße in Schutt und Asche!', 'Der Angriff muss so präzise wie möglich erfolgen, Slidore. Legt die Straße in Schutt und Asche!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10352', 'deDE', 'Geben Sie den Einsatzbefehl, Kommandant. Ich werde den unteren Teil Ihrer Basis in Schutt und Asche legen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10353', 'deDE', 'Taktisches Deckungsfeuer, Vipore. Gebt alles, was Ihr habt!', 'Taktisches Deckungsfeuer, Vipore. Gebt alles, was Ihr habt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10354', 'deDE', null, 'Seid gegrüßt, $C. Wollt ihr den Schildwachen der Silverwing in ihrem Kampf in der Kriegshymnenschlucht helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10355', 'deDE', 'Ich möchte das Schlachtfeld betreten.', 'Ich möchte das Schlachtfeld betreten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10356', 'deDE', 'Ich komme Euch holen, Frostwölfe! Und diesmal werdet Ihr den Schmerz spüren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10357', 'deDE', 'Schwadronskommandant Vipore hebt jetzt ab. Betet für einen schnellen Tod, Frostwolf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10358', 'deDE', 'Ich komme dich holen, Drek\'Thar!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10359', 'deDE', 'Der Kampfmeister', 'Der Kampfmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10360', 'deDE', null, 'Gelobt sei Elune! Habt Ihr schon den Kampfmeister der Kriegshymnenschlucht aufgesucht? Ihr Name ist Aethalas, und sie erwartet Euch bereits in der Nähe der Kriegerlehrer.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10361', 'deDE', null, 'Die Aufgabe, auf die Ihr Euch einlasst, wird nicht leicht, $N. Wenn Ihr in die Kriegshymnenschlucht reisen wollt, solltet Ihr Aethalas, die Kampfmeisterin von den Schildwachen der Silverwing aufsuchen. Sie wird Euch den Weg weisen. Möge die Göttin Euch segnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10362', 'deDE', 'Der Kampfmeister', 'Kampfmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10363', 'deDE', 'Na $G Mädel : Junge;, Ihr glaubt also das Zeug dazu zu haben, das Alteractal gegen die Horde zu verteidigen? Nun dann sprecht mit Glordrum Steelbeard, hier in Eisenschmiede. Er ist wahrscheinlich immer noch in der Halle der Waffen und brütet über den Schlachtplänen.', 'Na $G Mädel : Junge;, Ihr glaubt also das Zeug dazu zu haben, das Alteractal gegen die Horde zu verteidigen? Nun dann sprecht mit Glordrum Steelbeard, hier in Eisenschmiede. Er ist wahrscheinlich immer noch in der Halle der Waffen und brütet über den Schlachtplänen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10364', 'deDE', 'Ah, das Alteractal. Das waren noch Zeiten...<seufzt> Leider kann ich Euch nicht begleiten, aber ich bin sicher, Ihr werdet mir dort alle Ehre machen! Falls Ihr also in die Schlacht ziehen wollt, sprecht mit Glordrum Steelbeard. Er ist hier in Eisenschmiede in der Halle der Waffen.', 'Ah, das Alteractal. Das waren noch Zeiten...<seufzt> Leider kann ich Euch nicht begleiten, aber ich bin sicher, Ihr werdet mir dort alle Ehre machen! Falls Ihr also in die Schlacht ziehen wollt, sprecht mit Glordrum Steelbeard. Er ist hier in Eisenschmiede in der Halle der Waffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10365', 'deDE', 'Der Kampfmeister', 'Der Kampfmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10366', 'deDE', 'Falls Ihr unsere Streitkräfte im Alteractal unterstützen wollt, sprecht mit Kartra Bloodsnarl. Sie hält sich momentan in der Halle der Kriegerhelden im Tal der Ehre auf.', 'Falls Ihr unsere Streitkräfte im Alteractal unterstützen wollt, sprecht mit Kartra Bloodsnarl. Sie hält sich momentan in der Halle der Kriegerhelden im Tal der Ehre auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10367', 'deDE', 'Wie gerne würde ich mich Seite an Seite mit Euch in die Schlacht stürzen. Stattdessen muss ich hier meinen Dienst verrichten. Doch lasst Euch nicht aufhalten. Sucht Kartra Bloodsnarl in der Halle der Kriegerhelden in Orgrimmars Tal der Ehre auf. Sie wird Euch helfen zum Alteractal zu gelangen. Macht uns stolz, $N!', 'Wie gerne würde ich mich Seite an Seite mit Euch in die Schlacht stürzen. Stattdessen muss ich hier meinen Dienst verrichten. Doch lasst Euch nicht aufhalten. Sucht Kartra Bloodsnarl in der Halle der Kriegerhelden in Orgrimmars Tal der Ehre auf. Sie wird Euch helfen zum Alteractal zu gelangen. Macht uns stolz, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10368', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10369', 'deDE', null, 'Ich befehle Euch Feuer über diese Eindringlinge regnen zu lassen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10370', 'deDE', null, '%s stößt einen betäubenden Schrei aus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10371', 'deDE', 'Tell me about the iCoke promotion.', 'Tell me about the iCoke promotion.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10372', 'deDE', 'World of Warcraft and Coca-Cola (R) have teamed up to bring you the iCoke-World of Warcraft Summer Promotion.  Look for codes under the caps of specially marked bottles of Coca-Cola.  Then return here and enter the code to win a virtual item.', 'World of Warcraft and Coca-Cola (R) have teamed up to bring you the iCoke-World of Warcraft Summer Promotion.  Look for codes under the caps of specially marked bottles of Coca-Cola.  Then return here and enter the code to win a virtual item.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10373', 'deDE', 'Ihr werdet mich niemals aus meinem Bunker vertreiben, Ihr Ungläubigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10374', 'deDE', 'Warum versucht Ihr es nicht nochmal ohne eure billigen Taktiken, Ihr Waschlappen! Hat da vielleicht jemand Schiss???', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10375', 'deDE', null, 'Dreckige feige Frostwolf! Wenn ihr einen Kampf wollt, müsst ihr schon zu mir kommen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10376', 'deDE', 'Stormpike-Schwächlinge, stellt Euch mir in meiner Burg... wenn Ihr Euch traut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10377', 'deDE', 'Ihr versucht, den General der Frostwolf Armee aus seiner Burg zu locken? WIE LÄCHERLICH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10378', 'deDE', 'Darauf falle ich nicht rein, Dummkopf! Wenn Ihr einen Kampf wünscht, wird er nach meinen Bedingungen und in meinem Unterschlupf erfolgen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10379', 'deDE', 'Ich bin Jin\'rokh der Zerstörer, Gesandter von Mugamba, der Faust von Rastakhan!$B$BIch schlage mich mit Kriegern und Heiden herum.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10380', 'deDE', 'Ich habe meinen Talisman verloren und brauche einen neuen, Jin\'rokh.', 'Ich habe meinen Talisman verloren und brauche einen neuen, Jin\'rokh.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10381', 'deDE', 'Ich bin Al\'tabim, der Allsehende, ein Gesandter König Rastakhans.$B$BIch bin für Priester, Magier und Hexenmeister zuständig.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10382', 'deDE', 'Der Bund von Arathor schwor Arathi für die Menschen von Stromgarde und der Allianz wiederzuerobern. Es wird eine schwere Schlacht, aber sie ist es wert gekämpft zu werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10383', 'deDE', null, 'Um diesen Kampf zu gewinnen, können wir nicht tatenlos herumsitzen und einfach warten, dass der Sturm uns erfasst. Wir müssen zuschlagen, hart und schnell oder Arathi wird nie wieder unser sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10384', 'deDE', 'Ich habe meinen Talisman verloren und brauche einen neuen, Al\'tabim.', 'Ich habe meinen Talisman verloren und brauche einen neuen, Al\'tabim.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10385', 'deDE', null, 'Ich weiß, von wem Ihr sprecht, $C. Brogun Stoneshield findet Ihr auf der Terrasse der Krieger.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10386', 'deDE', null, 'Die Stormpikegarde braucht Eure Unterstützung im Kampf gegen den Frostwolfklan, $N. Sprecht mir Brogun Stoneshield auf der Terrasse der Krieger, er wird Euch den Weg ins Alteractal weisen. Gelobt sei Elune.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10387', 'deDE', null, 'Es gibt mehrere Schlachtfelder aus denen Ihr auswählen könnt. Zu welchem Kampfmeister soll ich Euch schicken?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10388', 'deDE', 'Alteractal', 'Alteractal', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10389', 'deDE', 'Kriegshymnenschlucht', 'Kriegshymnenschlucht', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10390', 'deDE', 'Wir müssen die Elfen und ihre feige Allianz aus der Kriegshymnenschlucht vertreiben. Begebt Euch zur Halle der Kriegerhelden im Tal der Ehre und sprecht mit Brakgul Deathbringer, wenn Ihr glaubt, dass Ihr das Zeug dazu habt, die Horde zu unterstützen.', 'Wir müssen die Elfen und ihre feige Allianz aus der Kriegshymnenschlucht vertreiben. Begebt Euch zur Halle der Kriegerhelden im Tal der Ehre und sprecht mit Brakgul Deathbringer, wenn Ihr glaubt, dass Ihr das Zeug dazu habt, die Horde zu unterstützen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10391', 'deDE', 'Die Schildwachen der Silverwing sind nicht gerade das, was ich als ehrenwerte Feinde betrachte. Aber wenn Ihr sie dennoch bekämpfen wollt $C, dann sprecht mir Brakgul Deathbringer in der Halle der Kriegerhelden.', 'Die Schildwachen der Silverwing sind nicht gerade das, was ich als ehrenwerte Feinde betrachte. Aber wenn Ihr sie dennoch bekämpfen wollt $C, dann sprecht mir Brakgul Deathbringer in der Halle der Kriegerhelden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10392', 'deDE', 'In welchem Schlachtfeld wollt Ihr Euren Ruhm erlangen?', 'In welchem Schlachtfeld wollt Ihr Euren Ruhm erlangen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10393', 'deDE', 'Alteractal', 'Alteractal', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10394', 'deDE', 'Kriegshymnenschlucht', 'Kriegshymnenschlucht', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10395', 'deDE', 'Dann solltet Ihr mit Taim Ragetotem sprechen. Ihr findet Ihn auf der Anhöhe der Jäger.', 'Dann solltet Ihr mit Taim Ragetotem sprechen. Ihr findet Ihn auf der Anhöhe der Jäger.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10396', 'deDE', 'Es gibt hier jemanden in Donnerfels, der Euch mit Eurer Reise ins Alteractal helfen kann. Sucht auf der Anhöhe der Jäger nach Taim Ragetotem. Möget Ihr finden, wonach Ihr sucht, $N.', 'Es gibt hier jemanden in Donnerfels, der Euch mit Eurer Reise ins Alteractal helfen kann. Sucht auf der Anhöhe der Jäger nach Taim Ragetotem. Möget Ihr finden, wonach Ihr sucht, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10398', 'deDE', 'Kergul Bloodaxe auf der Anhöhe der Jäger ist bereit Euch zu helfen zur Kriegshymnenschlucht zu gelangen.', 'Kergul Bloodaxe auf der Anhöhe der Jäger ist bereit Euch zu helfen zur Kriegshymnenschlucht zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10399', 'deDE', 'Welch unnötige Auseinandersetzung, aber wenn die Elfen der Feste Silverwing nicht in Frieden mit uns sprechen wollen, welche Alternative bleibt uns dann? Lasst Euch von Kergul Bloodaxe auf der Anhöhe der Jäger helfen, um zur Kriegshymnenschlucht zu gelangen.', 'Welch unnötige Auseinandersetzung, aber wenn die Elfen der Feste Silverwing nicht in Frieden mit uns sprechen wollen, welche Alternative bleibt uns dann? Lasst Euch von Kergul Bloodaxe auf der Anhöhe der Jäger helfen, um zur Kriegshymnenschlucht zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10400', 'deDE', 'Und zu welchem Schlachtfeld wollt Ihr reisen?', 'Und zu welchem Schlachtfeld wollt Ihr reisen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10401', 'deDE', 'Alteractal', 'Alteractal', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10402', 'deDE', 'Kriegshymnenschlucht', 'Kriegshymnenschlucht', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10403', 'deDE', 'Der Kampfmeister', 'Der Kampfmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10404', 'deDE', 'Es ist so kalt dort! Geht ins Königsviertel und sprecht mit Grizzle Halfmane.', 'Es ist so kalt dort! Geht ins Königsviertel und sprecht mit Grizzle Halfmane.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10405', 'deDE', 'Sein Name ist Grizzle Halfmane. Er ist im Königsviertel nahe der Bansheekönigin und dem Schreckenslord zu finden.', 'Sein Name ist Grizzle Halfmane. Er ist im Königsviertel nahe der Bansheekönigin und dem Schreckenslord zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10406', 'deDE', 'Ich denke, er ist im Königsviertel zu finden. Kurden Bloodclaw ist ein kleiner Orc.', 'Ich denke, er ist im Königsviertel zu finden. Kurden Bloodclaw ist ein kleiner Orc.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10407', 'deDE', 'Die Elfen in der Kriegshymnenschlucht machen lustige Geräusche, wenn man sie zermalmt. Wenn Ihr neugierig seid, sprecht mit Kurden Bloodclaw im Königsviertel.', 'Die Elfen in der Kriegshymnenschlucht machen lustige Geräusche, wenn man sie zermalmt. Wenn Ihr neugierig seid, sprecht mit Kurden Bloodclaw im Königsviertel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10408', 'deDE', 'Welches Schlachtfeld wünscht Ihr?', 'Welches Schlachtfeld wünscht Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10409', 'deDE', 'Alteractal', 'Alteractal', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10410', 'deDE', 'Kriegshymnenschlucht', 'Kriegshymnenschlucht', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10411', 'deDE', 'Der Kampfmeister', 'Der Kampfmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10412', 'deDE', 'Die Kriegshymnenschlucht sagt Ihr? Na dann wollt Ihr wahrscheinlich mit der hübschen Lylandris drüben in der Halle der Waffen sprechen.', 'Die Kriegshymnenschlucht sagt Ihr? Na dann wollt Ihr wahrscheinlich mit der hübschen Lylandris drüben in der Halle der Waffen sprechen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10413', 'deDE', 'Jede Gelegenheit, die Horde zu bekämpfen, ist eine gute Gelegenheit wenn Ihr mich fragt. Und an Gelegenheiten wird es Euch in der Kriegshymnenschlucht nicht mangeln, sicherlich nicht. Geht weiter zu der Halle der Waffen und sprecht mit Lylandris. Sie wird Euch den Weg weisen.', 'Jede Gelegenheit, die Horde zu bekämpfen, ist eine gute Gelegenheit wenn Ihr mich fragt. Und an Gelegenheiten wird es Euch in der Kriegshymnenschlucht nicht mangeln, sicherlich nicht. Geht weiter zu der Halle der Waffen und sprecht mit Lylandris. Sie wird Euch den Weg weisen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10414', 'deDE', 'Jup, und nach welchem Schlachtfeld sucht Ihr?', 'Jup, und nach welchem Schlachtfeld sucht Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10415', 'deDE', 'Alteractal', 'Alteractal', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10416', 'deDE', 'Kriegshymnenschlucht', 'Kriegshymnenschlucht', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10417', 'deDE', 'Dann ist es Thelman Slatefist, mit dem Ihr sprechen wollt. Ihr findet ihn in der Gegenwart des Königs in der Burg Sturmwind.', 'Dann ist es Thelman Slatefist, mit dem Ihr sprechen wollt. Ihr findet ihn in der Gegenwart des Königs in der Burg Sturmwind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10418', 'deDE', 'Und ich wünschte, ich könnte Euch begleiten, $N. Begebt Euch zur Burg Sturmwind, dort werdet Ihr Thelman Slatefist im königlichen Audienzsaal finden. Er wird Euch helfen, den Weg ins Alteractal zu finden.', 'Und ich wünschte, ich könnte Euch begleiten, $N. Begebt Euch zur Burg Sturmwind, dort werdet Ihr Thelman Slatefist im königlichen Audienzsaal finden. Er wird Euch helfen, den Weg ins Alteractal zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10419', 'deDE', 'In der Tat benötigt die Feste Silverwing Eure Unterstützung. Sprecht mit Elfarran im Audienzsaal der Burg Sturmwind.', 'In der Tat benötigt die Feste Silverwing Eure Unterstützung. Sprecht mit Elfarran im Audienzsaal der Burg Sturmwind.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10420', 'deDE', 'Die Schildwachen der Silverwing können im Kampf gegen die Warsongvorhut jede noch so geringe Hilfe gebrauchen. $N, geht zur Burg Sturmwind und sprecht mit Elfarran, wenn Ihr ihnen im Kampf beistehen wollt.', 'Die Schildwachen der Silverwing können im Kampf gegen die Warsongvorhut jede noch so geringe Hilfe gebrauchen. $N, geht zur Burg Sturmwind und sprecht mit Elfarran, wenn Ihr ihnen im Kampf beistehen wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10421', 'deDE', 'An welchem Schlachtfeld seid Ihr interessiert?', 'An welchem Schlachtfeld seid Ihr interessiert?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10422', 'deDE', 'Alteractal', 'Alteractal', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10423', 'deDE', 'Kriegshymnenschlucht', 'Kriegshymnenschlucht', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10424', 'deDE', 'Der Kampfmeister', 'Der Kampfmeister', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10425', 'deDE', '%s wird vollständig von Flammen umhüllt und in den Augen liegt plötzlich ein verrückter Ausdruck!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10426', 'deDE', null, 'Was erwartest du denn?$B$BMaywiki kümmert sich nur um Druiden und Schamanen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10427', 'deDE', 'Maywiki, Ich habe meinen Talisman verloren und brauche einen Ersatz.', 'Maywiki, Ich habe meinen Talisman verloren und brauche einen Ersatz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10428', 'deDE', 'Seid gegrüßt, $C. Es ist die Zeit des Erntedankfests, die Zeit großer Gaben und die Zeit der Erinnerung. Wir nehmen an Festen teil, um all das zu feiern, wofür wir dankbar sind, und gedenken unserer Vorfahren und Helden und dem Vermächtnis, das sie uns hinterlassen haben.$B$BBitte erfreut Euch mit uns an diesem Fest. Ich stelle noch zusätzlich etwas Feuerwerk zur Verfügung, um diese frohe Zeit des Jahres gebührend zu feiern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10429', 'deDE', 'Darf Ich Eure Auswahl an Feuerwerken durchstöbern?', 'Darf Ich Eure Auswahl an Feuerwerken durchstöbern?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10430', 'deDE', 'Seid gegrüßt, $C. Es ist die Zeit des Erntedankfests, die Zeit großer Gaben und die Zeit der Erinnerung. Wir nehmen an Festen teil, um all das zu feiern, wofür wir dankbar sind, und gedenken unserer Vorfahren und Helden und dem Vermächtnis, das sie uns hinterlassen haben.$B$BBitte erfreut Euch mit uns an diesem Fest. Ich stelle noch zusätzlich etwas Feuerwerk zur Verfügung, um diese frohe Zeit des Jahres gebührend zu feiern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10431', 'deDE', null, 'Wir dürfen es der Allianz nicht erlauben, das Zentrum ihrer Macht in Arathi auszubauen! Sogar ihre winzige Zuflucht kann uns vielleicht eines Tages gefährlich werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10432', 'deDE', 'Der Kampf um das Arathibecken ist ein Kampf um Ressourcen. Holz, Erz, Nahrung... Alles was benötigt wird, um einen guten Krieg zu führen.$B$BUnseren Feinden den Zugang zu Vorräten zu verwehren ist genauso gut, wie sie auf dem Schlachtfeld zu töten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10433', 'deDE', 'Ich mag zwar blind sein, doch habe ich Visionen. $B$BIch kümmere mich hauptsächlich um Jäger und Schurken, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10434', 'deDE', 'Ihr werdet schon bald tot sein!', 'Ihr werdet schon bald tot sein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10435', 'deDE', 'Ich mache Euch zu Mojo!', 'Ich mache Euch zu Mojo!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10436', 'deDE', 'Euer Schädel wird unsere Ritualaltare hübsch verzieren!', 'Euer Schädel wird unsere Ritualaltare hübsch verzieren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10437', 'deDE', 'Troll-Mojo ist das stärkste Mojo!', 'Troll-Mojo ist das stärkste Mojo!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10438', 'deDE', 'Spürt die Macht des Voodoo!', 'Spürt die Macht des Voodoo!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10439', 'deDE', 'Ich habe den Talisman verloren, Falthir. Ich benötige einen Ersatz.', 'Ich habe den Talisman verloren, Falthir. Ich benötige einen Ersatz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10440', 'deDE', 'Hallo Abenteurer. Ich bin Rin\'wosho, der Händler von Zuldazar. Ich habe aus meiner Heimat, den südlichen Meeren, viele exotische Waren und Gegenstände mitgebracht.$B$BFalls Ihr Euch für meinen Stamm als nützlich erweisen solltet, können wir den einen oder anderen Tauschhandel abschließen. Solltet Ihr auch noch eine Ehrenmünze meines Stammes besitzen, dann könnten wir sogar noch etwas mehr Handel betreiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10441', 'deDE', 'Lasst uns Handeln, Rin\'wosho. Zeigt mir Euer Angebot!', 'Lasst uns Handeln, Rin\'wosho. Zeigt mir Euer Angebot!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10442', 'deDE', 'Rin\'wosho, ich benötige Ausbesserungen.', 'Rin\'wosho, ich benötige Ausbesserungen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10443', 'deDE', null, 'Shadra, macht mich zu Eurem Abbild!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10444', 'deDE', null, 'Die Brut darf nicht versagen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10445', 'deDE', '%s ist voll ausgewachsen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10446', 'deDE', 'Ich werde Hakkar eure Seelen zum Fraß vorwerfen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10447', 'deDE', 'EURE ÜBERHEBLICHKEIT KÜNDET BEREITS VOM ENDE DIESER WELT! KOMMT STERBLICHE - STELLT EUCH DEM ZORN DES SEELENSCHINDERS!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10448', 'deDE', null, 'Helft mir, meine Brut!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10449', 'deDE', 'Willkommen zu Eurem Begräbnis! Tretet vor und sterbt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10450', 'deDE', null, 'Die Macht des Seelenschinders fällt von mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10451', 'deDE', 'Hakkar kontrolliert mich nicht länger! Endlich Frieden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10452', 'deDE', null, 'Ah...der Tod! Ich verfluche Euch, Hakkar! Ich verfluche Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10453', 'deDE', '%s stirbt.', '%s stirbt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10454', 'deDE', '%s wurde von einem nahen Verbündeten wiedererweckt!', '%s wurde von einem nahen Verbündeten wiedererweckt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10455', 'deDE', 'Shirvallah, erfülle mich mit deinem Zorn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10456', 'deDE', 'Es ist wahr, $N. Das Rad der Verdammnis wendet sich unaufhaltbar der Rückkehr von Hakkar zu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10457', 'deDE', 'Blut und Seelen für Hakkar! HAHAHAH!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10458', 'deDE', 'Betina, bitte gebt mir erneut ein Siegel der Dämmerung.', 'Betina, bitte gebt mir erneut ein Siegel der Dämmerung.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10459', 'deDE', null, 'Seid gesegnet für meine Befreiung, Sterbliche… Hakkar beherrscht mich nicht länger!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10460', 'deDE', 'Ssseelenfrieden...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10461', 'deDE', null, 'Bethekk, Eure Priesterin erfleht eure Macht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10462', 'deDE', 'Betina, bitte gebt mir erneut eine Rune der Dämmerung!', 'Betina, bitte gebt mir erneut eine Rune der Dämmerung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10463', 'deDE', 'Ich bin Zanza, ein Gott der Zandalarianer. Ich kam in dieser ätherischen Form von Zandalar, um meinem Volk zu helfen. Hakkar besitzt keine Macht über meine Seele.$B$BIch helfe all denen, die den Zandalarianern halfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10464', 'deDE', 'Ein Freund der Zandalarianer! Ihr seid tief in den Unterschlupf des Blutgottes vorgedrungen, $C. Ihr solltet besser auf das hören, was ich zu sagen habe.$B$BVor vielen tausend Jahren, zur Zeit als die eingeborenen Trolle des Imperiums von Zul\'Aman die eindringenden frevlerischen Hochelfen bekämpften, wurde mächtige Magie von Zanza gestohlen. Mit Hilfe dieser Magie verstärkten die Hochelfen ihre Waffen und Rüstungen, um ihren Eroberungsfeldzug gegen die Trolle voranzutreiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10465', 'deDE', 'Ich denke, ich habe von dieser Magie bereits gehört.', 'Ich denke, ich habe von dieser Magie bereits gehört.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10466', 'deDE', 'Habt Ihr? Dann wisst Ihr, wie schwach und lächerlich die Hochelfenmagie gegenüber der Trollmagie war? Die Hochelfen konnten nicht verstehen, warum sie nichts gegen die Macht des Troll-Voodoo ausrichten konnten. Das konnten sie einfach nicht begreifen. Sie fanden sich damit ab und benutzten verfälschte Versionen von Trollverzauberungen. Ich glaube sie nannten sie \'Arkanum\'', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10467', 'deDE', 'Arkanum? Das kommt mir sehr bekannt vor.', 'Arkanum? Das kommt mir sehr bekannt vor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10468', 'deDE', 'Zanza kann die antiken Trollverzauberungen mit den richtigen Komponenten herstellen. Diese Magie wird Euch helfen Hakkar und seine Dienerschaft zu besiegen.$B$BIch bitte Euch nur mir zwei Gegenstände zu bringen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10469', 'deDE', 'Welche Komponenten sind es?', 'Welche Komponenten sind es?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10470', 'deDE', 'Zuerst wird ein uralter Gegenstand benötigt, der von den mächtigsten Hakkari erlangt werden muss, von Jin\'do und dem Blutfürsten.$B$BDas Zweite ist eine durchstochene Voodoopuppe. Ihr findet sie in den Abfallhaufen, die diesen verdorbenen Ort bedecken. Schaut in den verwunschenen Hoodoohaufen nach.$B$BBringt mir diese drei Zutaten und ich erschaffe einen mächtigen Zauber für Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10471', 'deDE', 'Es tut mir leid, $C, aber Ihr seid noch nicht erfahren genug, um die Kriegshymnenschlucht betreten zu können.', 'Es tut mir leid, $C, aber Ihr seid noch nicht erfahren genug, um die Kriegshymnenschlucht betreten zu können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10472', 'deDE', null, 'Labt euch an $N, meine Süßen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10473', 'deDE', 'Nun ist nur noch ein letzter Schritt nötig, um uns von der Bedrohung des Seelenschinders zu befreien...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10474', 'deDE', 'Beginnt mit dem Ritual, meine Diener. Wir müssen das Herz von Hakkar wieder in das Nichts verbannen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10475', 'deDE', 'Willkommen zum Booty Bay Angelwettbewerb! Jede Woche küren wir unter den besten Anglern den Anglermeister!\n\nZwischen 14 Uhr und 16 Uhr wird der Schwarm Leckerfisch an der Küste des Schlingendorntals hinauf- und hinabziehen. Wenn einer Euer Schwimmer in dem Schwarm landet, werdet Ihr sicherlich auch etwas fangen, das nicht zu den gewöhnlichen Fischsorten hier an der Küste des Schlingendorntals gehört.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10476', 'deDE', 'Seid gegrüßt, $C. Ich bin Exzhal, Diener Rastakhans.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10477', 'deDE', 'Die Schlacht um das Arathibecken wird in 1 Minute beginnen.', 'Die Schlacht um das Arathibecken wird in 1 Minute beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10478', 'deDE', 'Die Schlacht um das Arathibecken wird in 30 Sekunden beginnen.', 'Die Schlacht um das Arathibecken wird in 30 Sekunden beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10479', 'deDE', 'Die Schlacht um das Arathibecken hat begonnen!', 'Die Schlacht um das Arathibecken hat begonnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10480', 'deDE', 'Lasst Euch nicht in die Falle der Sterblichen locken. Ergötzt Euch an meiner Macht! Fühlt Euch gestärkt und zerstört die Ungläubigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10481', 'deDE', 'Es tut mir leid, $C, aber Ihr seid noch nicht erfahren genug, um im Alteractal bestehen zu können.', 'Es tut mir leid, $C, aber Ihr seid noch nicht erfahren genug, um im Alteractal bestehen zu können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10482', 'deDE', 'Was sind die Symbole der Macht?', 'Was sind die Symbole der Macht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10483', 'deDE', 'Die Symbole der Macht sind Überbleibsel des uralten Imperiums der Gurubashi. Wir Zandalarianer suchen nach ihnen; sie sind nicht nur von hohem Sammlerwert für uns, sondern wir glauben auch, dass sie verborgene Zeichen der Magie enthalten, die von dem alten Imperium verwendet wurden.$B$BDreierlei Symbole werdet Ihr in Zul\'Gurub finden: Münzen, Schmuckstücke und urzeitliche Gegenstände. Bringt uns diese Dinge in der Anzahl, die wir benötigen, und wir werden Euch dafür mit ein paar der feinsten Gegenstände belohnen, die wir Zandalarianer jemals angefertigt haben.', 'Die Symbole der Macht sind Überbleibsel des uralten Imperiums der Gurubashi. Wir Zandalarianer suchen nach ihnen; sie sind nicht nur von hohem Sammlerwert für uns, sondern wir glauben auch, dass sie verborgene Zeichen der Magie enthalten, die von dem alten Imperium verwendet wurden.$B$BDreierlei Symbole werdet Ihr in Zul\'Gurub finden: Münzen, Schmuckstücke und urzeitliche Gegenstände. Bringt uns diese Dinge in der Anzahl, die wir benötigen, und wir werden Euch dafür mit ein paar der feinsten Gegenstände belohnen, die wir Zandalarianer jemals angefertigt haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10484', 'deDE', 'Die Markierungen auf dieser Schrifttafel beschreiben uralte Diagramme und gräßliche Worte der Macht, aber deren Bedeutung ist für Euch unergründlich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10485', 'deDE', 'Die Markierungen auf dieser Schrifttafel beschreiben uralte Diagramme und gräßliche Worte der Macht, die dazu verwandt wurden den Mojowahnsinn der Gurubashi zu erschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10486', 'deDE', 'Erlernt das Rezept.', 'Erlernt das Rezept.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10487', 'deDE', 'Die Markierungen auf dieser Schrifttafel beschreiben uralte Diagramme und gräßliche Worte der Macht.$B$BIhr glaubt vielleicht, es sei ein alchimistisches Rezept, doch ist es jenseits Eurer Macht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10488', 'deDE', 'Wenn Ihr mich fragt, das Beste am Angeln ist die Ruhe. Es gibt einem Zeit nachzudenken, alte Taten zu reflektieren und neues für die Zukunft zu planen.$B$BOh, und es ist natürlich eine tolle Ausrede, um zu trinken. Viel zu trinken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10489', 'deDE', 'Vielen Dank nochmal $N, dass Ihr mein Glücksmaßband zurückgebracht habt. Ich schwöre, es hilft mir größere Fische zu fangen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10490', 'deDE', 'Ich möchte Gahz\'ranka fangen! Bitte sagt mir wie!', 'Ich möchte Gahz\'ranka fangen! Bitte sagt mir wie!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10491', 'deDE', 'Um Gahz\'ranka zu fangen, braucht Ihr einen speziellen Matschstinkerköder. Ich mache sie selbst!$B$BAber abgesehen von dem Köder braucht Ihr auch noch ein paar Matschstinker... Ihr müsst sie in den Köder legen, um ihm einen kräftigen, ekligen Geruch zu verleihen!$B$BSobald Ihr den Köder bereit habt, werft Ihr ihn einfach von Pagles Spitze in Zul\'Gurub herunter... und dann holt Euch etwas zu trinken und wartet... aber auch nicht zu lange, denn Gahz\'ranka kommt sicherlich schnell herangestürmt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10492', 'deDE', 'Nat, ich möchte Eure Matschstinkerköder kaufen!', 'Nat, ich möchte Eure Matschstinkerköder kaufen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10493', 'deDE', '*Surrr* *PLIIING*\n\nIch wurde programmiert, um hochwertigen Fisch anzunehmen und dafür wertvolle Nat Pagle-Angelutensilien herauszugeben. Fisch Erforderlich.\n\n*KLING* *Bzzzz*', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10494', 'deDE', null, '%s beginnt Großes Heilen zu wirken!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10495', 'deDE', 'Es tut mir leid, $C, aber Ihr müsst mächtiger und stärker sein, bevor ich Euch Zutritt zum Arathibecken gewähren kann.', 'Es tut mir leid, $C, aber Ihr müsst mächtiger und stärker sein, bevor Ich Euch Zutritt zum Arathibecken gewähren kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10496', 'deDE', 'Gri\'lek, der des Eisernen Blutes.$B$BDer Wanderer. Möge seine Stärke und seine Leidenschaft für den Kampf kommende Jahrzehnte durchdringen.$B$BGri\'lek weilt nun weit entfernt von hier. Eines Tages wird er vielleicht zurückkehren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10497', 'deDE', 'Arathibecken', 'Arathibecken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10498', 'deDE', 'Geht zur Halle der Kriegerhelden. Dort werdet Ihr den Entweihten, Deze Snowbane finden, die Euch helfen wird zum Arathibecken zu gelangen.', 'Geht zur Halle der Kriegerhelden. Dort werdet Ihr den Entweihten, Deze Snowbane finden, die Euch helfen wird zum Arathibecken zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10499', 'deDE', 'Ihr wollt den Verlassenen in ihrem Kampf gegen die Menschen im Arathibecken helfen? Dann sucht die Halle der Kriegerhelden auf und sprecht mir Deze Snowbane.', 'Ihr wollt den Verlassenen in ihrem Kampf gegen die Menschen im Arathibecken helfen? Dann sucht die Halle der Kriegerhelden auf und sprecht mir Deze Snowbane.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10500', 'deDE', 'Wie fange ich die Matschstinker für deinen Köder?', 'Wie fange ich die Matschstinker für deinen Köder?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10501', 'deDE', 'Arathibecken', 'Arathibecken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10502', 'deDE', 'Die Matschstinker sind eine besonders übel riechende Fischsorte, die tropische Gewässer lieben. Der zulianische Matschstinker, der muffigste unter ihnen allen, kann nur in den dampfenden Gewässern von Zul\'Gurub gefunden werden.$B$BIch glaube, es waren die zulianischen Matschstinker, die Gahz\'ranka an diesen Ort lockte, doch werden Euch die Trolle bestimmt erzählen er sei einer ihrer Götter...$B$BGlaubt was Ihr wollt, aber nehmt diesen Rat mit auf den Weg: Bestückt einen meiner Köder mit zulianischen Matschstinkern und das Viech wird kommen. Garantiert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10503', 'deDE', 'In Königsviertel gehen. Suchen nach Sir Malory Wheeler. Er schaurig...', 'In Königsviertel gehen. Suchen nach Sir Malory Wheeler. Er schaurig...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10504', 'deDE', 'Das Arathibecken müssen wir von den schwachen Menschen wiedererobern. Sir Malory Wheeler wird Euch dorthin schicken.', 'Das Arathibecken müssen wir von den schwachen Menschen wiedererobern. Sir Malory Wheeler wird Euch dorthin schicken.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10505', 'deDE', 'DING!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10507', 'deDE', 'Euer Tod nährt meine Stärke!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10508', 'deDE', 'Arathibecken', 'Arathibecken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10509', 'deDE', 'Sucht nach Martin Lindsey in den Teichen der Visionen unterhalb der Anhöhe der Geister. Er wird Euch helfen den Weg zum Arathibecken zu finden.', 'Sucht nach Martin Lindsey in den Teichen der Visionen unterhalb der Anhöhe der Geister. Er wird Euch helfen den Weg zum Arathibecken zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10510', 'deDE', 'Ihr wollt den Entweihten in ihrer erbitterten Schlacht gegen den Bund von Arathor im Arathibecken helfen? Sehr gut, dann solltet Ihr Martin Lindsey, um seine Unterstützung bitten. Ihr findet ihn in den Teichen der Visionen.', 'Ihr wollt den Entweihten in ihrer erbitterten Schlacht gegen den Bund von Arathor im Arathibecken helfen? Sehr gut, dann solltet Ihr Martin Lindsey, um seine Unterstützung bitten. Ihr findet ihn in den Teichen der Visionen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10511', 'deDE', 'Arathibecken', 'Arathibecken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10512', 'deDE', 'Die Menschen brauchen Eure Unterstützung im Kampf gegen die Verlassenen im Arathibecken. Geht zur Halle der Waffen und sprecht mit ihrem Beauftragten, Donal Osgood.', 'Die Menschen brauchen Eure Unterstützung im Kampf gegen die Verlassenen im Arathibecken. Geht zur Halle der Waffen und sprecht mit ihrem Beauftragten, Donal Osgood.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10513', 'deDE', 'Der Bund von Arathor versucht seine alten Besitztümer im Arathibecken von den Verlassenen wieder zu beanspruchen. Wenn Ihr ihnen helfen wollt, $N, sprecht mit Donal Osgood in der Halle der Waffen.', 'Der Bund von Arathor versucht seine alten Besitztümer im Arathibecken von den Verlassenen wieder zu beanspruchen. Wenn Ihr ihnen helfen wollt, $N, sprecht mit Donal Osgood in der Halle der Waffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10514', 'deDE', 'Arathibecken', 'Arathibecken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10515', 'deDE', 'Im Audienzsaal des Königs werdet Ihr Lady Hoteshem antreffen. Sie wird Euch helfen den Weg zum Arathibecken zu finden. Der Bund von Arathor benötigt dringend Eure Unterstützung gegen die Entweihten.', 'Im Audienzsaal des Königs werdet Ihr Lady Hoteshem antreffen. Sie wird Euch helfen den Weg zum Arathibecken zu finden. Der Bund von Arathor benötigt dringend Eure Unterstützung gegen die Entweihten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10516', 'deDE', 'Es ist beeindruckend, was der Bund von Arathor im Arathibecken versucht. Zu Schade, dass diese untoten Monstrositäten und ihre Hordenfreunde ihnen im Weg stehen. Lauft hinüber zur Burg Sturmwind. Lady Hoteshem erwartet Euch im Audienzsaal des Königs. Sie kann Euch helfen, zum Arathibecken zu gelangen.', 'Es ist beeindruckend, was der Bund von Arathor im Arathibecken versucht. Zu Schade, dass diese untoten Monstrositäten und ihre Hordenfreunde ihnen im Weg stehen. Lauft hinüber zur Burg Sturmwind. Lady Hoteshem erwartet Euch im Audienzsaal des Königs. Sie kann Euch helfen, zum Arathibecken zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10517', 'deDE', 'Arathibecken', 'Arathibecken', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10518', 'deDE', null, 'Die Menschen brauchen definitiv all unsere Hilfe im Arathibecken. Der mit dem Ihr zu sprechen wünscht, nennt sich Keras Wolfheart. Er ist bereit Euch zu helfen, sucht ihn auf der Terasse der Krieger auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10519', 'deDE', null, 'Wenn dies Euer Wille ist, $C, müsst Ihr mit Keras Wolfheart auf der Terrasse der Krieger sprechen. Er ist einer der Beauftragten des Bunds von Arathor. Er kann Euch helfen zum Arathibecken zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10520', 'deDE', 'Throm\'ka $C! Seid Ihr bereit, für die Horde in der Kriegshymnenschlucht zu kämpfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10521', 'deDE', null, 'Seid gegrüßt, $N. Wollt Ihr, dass ich Euch auf Eurem Weg zur Kriegshymnenschlucht helfe?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10522', 'deDE', '$N, die Schildwachen der Silverwing müssen zerstört werden! Seid Ihr bereit für die Kriegshymnenschlucht?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10523', 'deDE', 'Seid Ihr hier um für uns in der Kriegshymnenschlucht zu kämpfen? Lok\'tar Ogar $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10524', 'deDE', null, '$C, wir brauchen Eure Unterstützung in der Kriegshymnenschlucht. Werdet Ihr uns helfen gegen die Warsongvorhut zu kämpfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10525', 'deDE', 'Genau, $G Junge : Mädchen;, seid Ihr bereit, ins Alteractal zu reisen und der Horde das zu geben, worum sie gebettelt hat?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10526', 'deDE', '$N, habt Ihr auch nur die geringste Ahnung, gegen wen wir im Alteractal bestehen müssen? Seid Ihr bereit den Frostwolfklan zu bekämpfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10527', 'deDE', 'Die Stormpikegarde muss zerschlagen werden! Bereit für das Alteractal, $C?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10528', 'deDE', null, 'Lok\'tar $C! Wir brauchen Eure Unterstützung im Alteractal. Soll ich Euch der Liste hinzufügen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10529', 'deDE', 'Unsere Streitkräfte im Alteractal benötigen Weisheit, um den Sieg zu erlangen,$C. Ihr seht aus wie jemand, der sie führen könnte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10530', 'deDE', 'Ich hoffe Ihr seid hier um in das Alteractal zu gehen, $N. Wir können alle Hilfe gebrauchen, die wir kriegen können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10531', 'deDE', null, 'Die Menschen und ihr sogenannter Bund von Arathor gedenken, uns aus dem Arathibecken zu vertreiben. Doch das soll ihnen nicht gelingen, nicht wahr, $C? Seid Ihr bereit, dem Ruf zu folgen und den Menschen zu zeigen, dass sie es sind, die bald abziehen müssen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10532', 'deDE', 'Das Arathibecken war einst unser Zuhause und wird es mit Eurer Hilfe wieder sein! Werdet Ihr uns helfen $C?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10533', 'deDE', 'Ich hoffe Ihr seid hier, um uns zu helfen, das Arathibecken von den Entweihten zurückzuerobern. Was sagt Ihr $N?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10534', 'deDE', null, 'Ich habe Euch erwartet, $N. Seid Ihr bereit uns im Arathibecken zu unterstützen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10535', 'deDE', 'Ihr glaubt also Ihr habt das Zeug um den Bund von Arathor zu zerstören, $C? Ich hoffe es. Was wollt Ihr genau von mir oder seid Ihr hier, um meine Zeit zu verschwenden?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10536', 'deDE', 'Seht Euch um, $C. Ihr befindet Euch zwar in außergewöhnlicher Gesellschaft, aber dennoch finde ich, dass Ihr Euch beweisen solltet. Hmm, vielleicht sollte ich es Euch möglich machen für unsere Sache im Arathibecken einzutreten. Was haltet Ihr davon?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10537', 'deDE', 'Begrüßt $N, Besieger von Hakkar und Held von Azeroth!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10538', 'deDE', 'Gri\'lek, der des Eisernen Blutes.$B$BDer Wanderer. Möge seine Stärke und seine Leidenschaft für den Kampf kommende Jahrzehnte durchdringen.$B$BGri\'lek hält sich derzeit am Rande des Wahnsinns auf...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10539', 'deDE', 'Hazza\'rah, der Traumweber.$B$BSein ist die Macht über Alpträume, mögen seine Feinde für immer schlafen.$B$BHazza\'rah weilt nun weit entfernt von hier. Eines Tages wird er vielleicht zurückkehren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10540', 'deDE', 'Hazza\'rah, der Traumweber.$B$BSein ist die Macht über Albträume, mögen seine Feinde für immer schlafen.$B$BHazza\'rah träumt nun nahe des Randes des Wahnsinns.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10541', 'deDE', 'Renataki, der Tausend Klingen.$B$BSchmerz ist sein Lebenselixir. Angst sein Verbündeter. Möge er eines Tages wiederkehren und frohes Blutvergießen mit sich bringen.$B$BRenataki weilt nun weit entfernt von hier. Eines Tages wird er vielleicht zurückkehren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10542', 'deDE', 'Renataki, der Tausend Klingen.$B$BSchmerz ist sein Lebenselixir. Angst sein Verbündeter. Möge er eines Tages wiederkehren und frohes Blutvergießen mit sich bringen.$B$BRenataki haust nun am Rande des Wahnsinns.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10543', 'deDE', 'Wushoolay, die Sturmhexe.$B$BIhre Macht ist die Macht des Himmels, des Regens und der zersprungenen Erde. Möge sie eines Tages wieder als die Mutter der Gurubashi regieren.$B$BWushoolay weilt nun weit entfernt von hier. Eines Tages wird sie vielleicht zurückkehren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10544', 'deDE', 'Wushoolay, die Sturmhexe.$B$BIhre Macht ist die Macht des Himmels, des Regens und der zersprungenen Erde. Möge sie eines Tages wieder als die Mutter der Gurubashi regieren.$B$BWushoolay wütet nun am Rande des Wahnsinns.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10545', 'deDE', '%s gerät in Rage, als er sieht, dass sein Raptor im Kampf stirbt!', '%s gerät in Rage, als er sieht, dass sein Raptor im Kampf stirbt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10546', 'deDE', 'Diener von Hakkar, hört Euren Gott. Das Heiligtum dieses Tempels wurde entweiht. Eindringlinge bewegen sich auf seinem heiligen Boden! Der Altar des Blutes muss beschützt werden. Tötet sie alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10547', 'deDE', 'Hey hey hey! Ich bin hier um einen Angelwettbewerb im Schlingendorntal anzukündigen. Um teilzunehmen braucht Ihr genug Erfahrung, um dort zu überleben und ausreichend Geschick, um den Fisch aus dem Wasser zu locken.$B$BKommt später zurück, wenn Ihr qualifizierter seid!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10548', 'deDE', 'Heya heya hey! Wenn Ihr Euer Angelgeschick testen möchtet, dann habe ich genau das Richtige für Euch! Diesen Sonntag gibt es einen Angelwettbewerb im Schlingendorntal!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10549', 'deDE', '%s flüchtet in die sichere Nähe eines anderen Tigers.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10550', 'deDE', 'Eure offene Geringschätzung gegenüber der souveränen Macht des Imperiums der Gurubashi wurde zur Kenntnis genommen. Die Einwohner von Zul\'Gurub wurden in Alarmbereitschaft versetzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10551', 'deDE', 'Ihr seid noch nicht erfahren genug, $C. Geht Eurer Wege.', 'Ihr seid noch nicht erfahren genug, $C. Geht Eurer Wege.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10552', 'deDE', 'Unser Bedarf an Unterstützung im Arathibecken ist größer als man annehmen könnte, $N. So verhält es sich auch mit der Ehre und der Anerkennung, die man dort in diesen Zeiten der Not erlangen kann. Ich bitte Euch inständig, den Kampfmeister des Arathibeckens in dieser Stadt aufzusuchen.', 'Unser Bedarf an Unterstützung im Arathibecken ist größer als man annehmen könnte, $N. So verhält es sich auch mit der Ehre und der Anerkennung, die man dort in diesen Zeiten der Not erlangen kann. Ich bitte Euch inständig, den Kampfmeister des Arathibeckens in dieser Stadt aufzusuchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10553', 'deDE', 'Ich möchte mich dem Kampf anschließen!', 'Ich möchte mich dem Kampf anschließen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10554', 'deDE', 'Ich bin so froh, dass Ihr gefragt habt. Ihr findet Sir Malory Wheeler im Königsviertel in der Gesellschaft unserer dunklen Fürstin.', 'Ich bin so froh, dass Ihr gefragt habt. Ihr findet Sir Malory Wheeler im Königsviertel in der Gesellschaft unserer dunklen Fürstin.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10555', 'deDE', 'Natürlich, natürlich. Bitte sprecht mit Deze Snowbane in der Halle der Kriegerhelden in dem Tal der Ehre, $C.', 'Natürlich, natürlich. Bitte sprecht mit Deze Snowbane in der Halle der Kriegerhelden in dem Tal der Ehre, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10556', 'deDE', 'Und große Ehre wird Euch zuteil werden, da bin ich mir sicher. Sprecht mit Martin Lindsay in den Teichen der Visionen. Die Teiche befinden sich in einer Höhle unterhalb der Anhöhe der Geister.', 'Und große Ehre wird Euch wiederfahren, da bin ich mir sicher. Sprecht mit Martin Lindsay in den Teichen der Visionen. Die Teiche befinden sich in einer Höhle nahe der Anhöhe der Geister.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10557', 'deDE', 'Ihr müsst erfahrener sein, $C, bevor Ihr uns im Alteractal helfen könnt.', 'Ihr müsst erfahrener sein, $C, bevor Ihr uns im Alteractal helfen könnt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10558', 'deDE', 'Hallo $C. Das Alteractal benötigt dringend Eure Hilfe und wird Euch mit größerer Ehre und Anerkennung denn je belohnen. Schließt Ihr Euch uns im Kampf gegen die Stormpikegarde an?', 'Hallo $C. Das Alteractal benötigt dringend Eure Hilfe und wird Euch mit größerer Ehre und Anerkennung denn je belohnen. Schließt Ihr Euch uns im Kampf gegen die Stormpikegarde an?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10559', 'deDE', 'Ehre sei Eurem Haus und Euren Vorfahren, $C! Ihr findet Kartra Bloodsnarl in der Halle der Kriegerhelden im Tal der Ehre.', 'Ehre sei Eurem Haus und Euren Vorfahren, $C! Ihr findet Kartra Bloodsnarl in der Halle der Kriegerhelden im Tal der Ehre.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10560', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10561', 'deDE', 'Euer Name wird durch die Jahrhunderte in Erinnerung bleiben, $N. Sprecht mit Taim Ragetotem auf der Anhöhe der Jäger und er wird Euch helfen den Weg ins Alteractal zu finden.', 'Euer Name wird durch die Jahrhunderte in Erinnerung bleiben, $N. Sprecht mit Taim Ragetotem auf der Anhöhe der Jäger und er wird Euch helfen den Weg ins Alteractal zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10562', 'deDE', 'Sehr gut $N. Ihr werdet Grizzle Halfmane im Königsviertel in Unterstadt finden. Möget Ihr große Ehre im Alteractal erlangen.', 'Sehr gut $N. Ihr werdet Grizzle Halfmane im Königsviertel in Unterstadt finden. Möget Ihr große Ehre im Alteractal erlangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10563', 'deDE', 'Leider seid Ihr noch nicht stark genug, um in der Kriegshymnenschlucht zu überleben, $C.', 'Leider seid Ihr noch nicht stark genug, um in der Kriegshymnenschlucht zu überleben, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10564', 'deDE', '$C, wir benötigen dringend Eure Hilfe in der Kriegshymnenschlucht, im Kampf gegen die Schildwachen der Silverwing. Ihr könnt auch noch mehr Ehre und Anerkennung bei der Warsongvorhut erlangen! Seid Ihr mutig genug, um Euch in die Schlacht zu begeben?', '$C, wir benötigen dringend Eure Hilfe in der Kriegshymnenschlucht, im Kampf gegen die Schildwachen der Silverwing. Ihr könnt auch noch mehr Ehre und Anerkennung bei der Warsongvorhut erlangen! Seid Ihr mutig genug, um Euch in die Schlacht zu begeben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10565', 'deDE', 'Dann erfüllt es mich mit großem Stolz, Euch sagen zu können, dass Brakgul Deathbringer in der Halle der Waffen bereitsteht, um Euch zu helfen, den Weg in die Kriegshymnenschlucht zu finden. Ihr findet ihn im Tal der Ehre.', 'Dann erfüllt es mich mit großem Stolz, Euch sagen zu können, dass Brakgul Deathbringer in der Halle der Waffen bereitsteht, um Euch zu helfen, den Weg in die Kriegshymnenschlucht zu finden. Ihr findet ihn im Tal der Ehre.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10566', 'deDE', 'Ihr habt weise gewählt, $N. Sucht nach Kergul Bloodaxe auf der Anhöhe der Jäger. Er wird Euch auf Eurem Weg in die Kriegshymnenschlucht helfen.', 'Ihr habt weise gewählt, $N. Sucht nach Kergul Bloodaxe auf der Anhöhe der Jäger. Er wird Euch auf Eurem Weg in die Kriegshymnenschlucht helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10567', 'deDE', 'Ehre und Ruhm wird Euer sein, $C! Sucht nach Kurden Bloodclaw im Königsviertel dieser Stadt.', 'Ehre und Ruhm wird Euer sein, $C! Sucht nach Kurden Bloodclaw in dem Königsviertel dieser Stadt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10568', 'deDE', 'Ich glaube nicht, dass Ihr lange im Alteractal überleben würdet, $C. Kommt und sprecht mit uns, sobald Ihr ein paar Winter mehr unter Eurem Gürtel habt.', 'Ich glaube nicht, dass Ihr lange im Alteractal überleben würdet, $C. Kommt und sprecht mit uns, sobald Ihr ein paar Winter mehr unter Eurem Gürtel habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10569', 'deDE', '$N, die Stormpikegarde benötigt mehr denn je Eure Unterstützung im Kampf gegen den Frostwolfklan im Alteractal. Die Möglichkeiten, dort große Ehre und Anerkennung zu erlangen, sind ebenfalls größer denn je! Schließt Ihr Euch uns im Kampf an?', '$N, die Stormpikegarde benötigt mehr denn je Eure Unterstützung im Kampf gegen den Frostwolfklan im Alteractal. Die Möglichkeiten, dort große Ehre und Anerkennung zu erlangen, sind ebenfalls größer denn je! Schließt Ihr Euch uns im Kampf an?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10570', 'deDE', 'Exzellent, $N! Ihr werdet Brogun Stoneshield auf der Terrasse der Krieger finden. Er kann Euch helfen ins Alteractal zu gelangen.', 'Exzellent, $N! Ihr werdet Brogun Stoneshield auf der Terrasse der Krieger finden. Er kann Euch helfen ins Alteractal zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10571', 'deDE', null, 'Fischereimeister Bassbait hat hier in den Gewässern Leckerfisch freigelassen, damit sein Wettbewerb stattfinden kann.\n\nSobald der Gewinner feststeht, werde ich Euch von da an für jeweils fünf Leckerfische, die Ihr mir bringt, Silber geben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10572', 'deDE', 'Ihr habt die richtige Entscheidung getroffen $C. Ihr findet unseren Kampfmeister Glordrum Steelbeard in der Halle der Waffen. Viel Glück, $N!', 'Ihr habt die richtige Entscheidung getroffen $C. Ihr findet unseren Kampfmeister Glordrum Steelbeard in der Halle der Waffen. Viel Glück, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10573', 'deDE', 'Schön für Euch $C! Unser Kampfmeister Thelman Slatefist befindet sich im königlichen Audienzsaal in der Burg Sturmwind. Er wird Euch helfen ins Alteractal zu gelangen.', 'Schön für Euch $C! Unser Kampfmeister Thelman Slatefist befindet sich im königlichen Audienzsaal in der Burg Sturmwind. Er wird Euch helfen ins Alteractal zu gelangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10574', 'deDE', 'Vielen Dank für das Angebot, $C. Allerdings seid Ihr noch nicht erfahren genug, um in den Gefahren des Arathibeckens bestehen zu können.', 'Vielen Dank für das Angebot, $C. Allerdings seid Ihr noch nicht erfahren genug, um in den Gefahren des Arathibeckens bestehen zu können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10575', 'deDE', 'Werdet Ihr uns in Zeiten der Not im Arathibecken helfen, $C? Die Aussichten Ehre und Anerkennung zu erlangen sind größer denn je und Eure Unterstützung wird sicherlich Früchte tragen und die Entweihten in ihre Schranken weisen. Können wir auf Euch zählen?', 'Werdet Ihr uns in Zeiten der Not im Arathibecken helfen, $C? Die Aussichten Ehre und Anerkennung zu erlangen sind größer denn je und Eure Unterstützung wird sicherlich Früchte tragen und die Entweihten in ihre Schranken weisen. Können wir auf Euch zählen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10576', 'deDE', 'Vielen Dank, $N! Ihr findet Keras Wolfheart auf der Terrasse der Krieger nahe der Lehrer. Er wird Euch den Weg zum Arathibecken weisen. Sei Euch das Glück hold und großer Ruhm Euer Lohn.', 'Vielen Dank, $N! Ihr findet Keras Wolfheart auf der Terrasse der Krieger nahe der Lehrer. Er wird Euch den Weg zum Arathibecken weisen. Sei Euch das Glück hold und großer Ruhm Euer Lohn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10577', 'deDE', 'Wir stehen in Eurer Schuld, $C. Sucht nach Donal Osgood in der Halle der Waffen. Er wird mehr als erfreut sein, Euch auf Eurer Reise zum Arathibecken behilflich sein zu können.', 'Wir stehen in Eurer Schuld, $C. Sucht nach Donal Osgood in der Halle der Waffen. Er wird mehr als erfreut sein, Euch auf Eurer Reise zum Arathibecken behilflich sein zu können.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10578', 'deDE', 'Exzellent! Ihr erweist dem Bund von Arathor einen großen Dienst, indem Ihr das Arathibecken von den Entweihten und ihrer diebischen Horde befreit. Sucht Lady Hoteshem im königlichen Audienzsaal in der Burg Sturmwind auf. Sie erwartet Euch, $N.', 'Exzellent! Ihr erweist dem Bund von Arathor einen großen Dienst, indem Ihr das Arathibecken von den Entweihten und Ihrer diebischen Horde befreit. Sucht Lady Hoteshem im königlichen Audienzsaal in der Burg Sturmwind auf. Sie erwartet Euch, $N.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10579', 'deDE', 'Ich wurde durch Euch befreit! Rächt mich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10580', 'deDE', 'Die Zeiten ändern sich für uns alle über die Jahre, aber Ihr befindet Euch noch im Frühling Eures Lebens. Bitte kehrt zu einem späteren Zeitpunkt zu uns zurück $C, wenn Ihr erfahrener seid.', 'Die Zeiten ändern sich für uns alle über die Jahre, aber Ihr befindet Euch noch im Frühling Eures Lebens. Bitte kehrt zu einem späteren Zeitpunkt zu uns zurück $C, wenn Ihr erfahrener seid.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10581', 'deDE', '$C, wir schämen uns nicht zuzugeben, dass wir jede Unterstützung gebrauchen können, die wir für unserem Kampf in der Kriegshymnenschlucht bekommen können. Ihr könnt dort mehr Ruhm denn je erlangen, falls Ihr Euch entscheiden solltet, uns mit Euren Fähigkeiten beizustehen. Auch die Schildwachen der Silverwing werden Eure Bemühungen in unserem Namen mit Sicherheit mit mehr Gefälligkeit betrachten. Schließt Ihr Euch uns im Kampf an?', '$C, wir schämen uns nicht zuzugeben, dass wir jede Unterstützung gebrauchen können, die wir für unserem Kampf in der Kriegshymnenschlucht bekommen können. Ihr könnt dort mehr Ruhm denn je erlangen, falls Ihr Euch entscheiden solltet, uns mit Euren Fähigkeiten beizustehen. Auch die Schildwachen der Silverwing werden Eure Bemühungen in unserem Namen mit Sicherheit mit mehr Gefälligkeit betrachten. Schließt Ihr Euch uns im Kampf an?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10582', 'deDE', 'Das Licht von Elune erleuchte Euch, $N. Ihr findet Aethalas, unsere Kampfmeisterin, auf der Terrasse der Krieger. Sie wird Euch auf Eurem Weg in die Kriegshymnenschlucht helfen.', 'Das Licht von Elune erleuchte Euch, $N. Ihr findet Aethalas, unsere Kampfmeisterin, auf der Terrasse der Krieger. Sie wird Euch auf Eurem Weg in die Kriegshymnenschlucht helfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10583', 'deDE', 'Ihr beweist wahre Weisheit, $C. Ich danke Euch. Ihr findet unsere Kampfmeisterin Lylandris in der Halle der Waffen, höchstwahrscheinlich brütet sie über den Schlachtplänen. Sie wird Euch helfen den Weg in die Kriegshymnenschlucht zu finden.', 'Ihr beweist wahre Weisheit, $C. Ich danke Euch. Ihr findet unsere Kampfmeisterin Lylandris in der Halle der Waffen, höchstwahrscheinlich brütet sie über den Schlachtplänen. Sie wird Euch helfen den Weg in die Kriegshymnenschlucht zu finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10584', 'deDE', null, 'Die Symbole der Macht sind Überbleibsel des uralten Imperiums der Gurubashi. Wir Zandalarianer suchen nach ihnen... sie sind nicht nur von hohem Wert für uns, sondern wir glauben auch, dass sie verborgene Zeichen der Magie enthalten, die von dem alten Reich verwandt wurden.$B$BDreierlei Symbole werdet Ihr in Zul\'Gurub finden: Münzen, Schmuckstücke und urzeitliche Gegenstände. Bringt uns diese Dinge in der Anzahl, die wir benötigen und wir werden Euch dafür mit ein paar der feinsten Gegenstände belohnen, die wir Zandalarianer jemals gefertigt haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10585', 'deDE', 'Die Schildwachen der Silverwing stehen in Eurer Schuld, $C. Ihr findet Elfarran, unseren Kampfmeister in der Burg Sturmwind, wo er der Audienz des jungen Königs beiwohnt. Die Göttin wache über Euch!', 'Die Schildwachen der Silverwing stehen in Eurer Schuld, $C. Ihr findet Elfarran, unseren Kampfmeister in der Burg Sturmwind, wo er der Audienz des jungen Königs beiwohnt. Die Göttin wache über Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10586', 'deDE', 'Ihr werdet bis in alle Ewigkeit leiden, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10587', 'deDE', 'Hakkar wird die Welt beherrschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10588', 'deDE', 'Ihr macht ein höchst passendes Opfer.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10589', 'deDE', 'Liefert Eure Seele aus, Sterblicher.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10590', 'deDE', 'Die Welt wird unsagbare Grausamkeiten unter meiner Herrschaft erleiden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10591', 'deDE', 'Deine Götter können dich jetzt nicht mehr retten, $C.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10592', 'deDE', 'Die Wilden Götter dienen Hakkar. Kehrt um, Ihr habt keine Chance.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10593', 'deDE', 'Schwört Hakkar die Treue und der Schmerz, den Ihr erleiden müsst, wird nur gering sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10594', 'deDE', 'Ihr wagt es, den geheiligten Boden der Hakkari zu betreten? Diener Hakkars, zerstört diese Ungläubigen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10595', 'deDE', 'Seid gegrüßt, Meister der Angelrute und Spule! Wenn Ihr gewillt seid, Euer Geschick zu testen und gegen die besten Angler des Landes anzutreten, dann hört mir zu! An diesem Sonntag wird ein Wettbewerb im Schlingendorntal stattfinden, der den wahren Anglermeister ermitteln wird!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10596', 'deDE', 'Hallo! Ich bin hier um einen Angelwettbewerb im Schlingendorntal anzukündigen. Um teilzunehmen braucht Ihr genug Erfahrung, um dort zu bestehen, und ausreichend Geschick, um den Fisch aus dem Wasser zu locken.$B$BKommt später zurück, wenn Ihr qualifizierter seid!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10597', 'deDE', 'Die Symbole der Macht bilden mächtiges Mojo, Mojo des Imperiums der Gurubashi. Der Stamm wird sich das Mojo nehmen, der Blutgott wird uns nicht zerstören.$B$BEs gibt drei Arten von Symbolen in Zul\'Gurub. Findet Münzen, Schmuckstücke und urzeitliche Gegenstände, dies sind die Symbole der Macht.$B$BGeht nach Zul\'Gurub. Tötet. Nehmt die Symbole an Euch. Bringt sie dem Stamm. Verdient Euch unseren Respekt. Verdient Euch unsere Kampfausrüstung dafür. Verdient Euch wahre Macht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10598', 'deDE', 'Die Allianz hat $1776W Vorräte angesammelt und ist dem Sieg nahe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10599', 'deDE', 'Die Horde hat $1777W Ressourcen angesammelt und ist dem Sieg nahe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10600', 'deDE', 'Es gibt bereits einen Sieger bei unserem Wettbewerb, Jang kauft Euch jedoch mit Freude jeden Leckerfisch ab, den Ihr gefangen habt!\n\nBis zum nächsten Sonntag ist es nicht mehr lange hin, dann könnt Ihr Eure Angelfertigkeit wieder unter Beweis stellen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10601', 'deDE', 'Glückwunsch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10602', 'deDE', 'Nicht übel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10603', 'deDE', 'Yipiee!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10604', 'deDE', '$N! Ich behalte Euch im Auge!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10605', 'deDE', 'Ihr habt mich zum letzten Mal verärgert, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10606', 'deDE', 'Gebt acht, $C. Hört Ihr den Ruf des Wassers?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10607', 'deDE', 'Ich wünsche mit den Geistern zu sprechen, Tiev.', 'Ich wünsche mit den Geistern zu sprechen, Tiev.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10608', 'deDE', 'Lasst den Angelwettbewerb BEGINNEN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10609', 'deDE', 'Das war\'s für diese Woche mit Leckerfischen! Ich bleibe noche eine weitere Stunde hier, damit Ihr Eure Fische abliefern könnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10610', 'deDE', 'Wir haben einen Gewinner! $N ist der Anglermeister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10611', 'deDE', 'Hinter mir ist der Altar von Zanza, ein heiliger Ort für uns Zandalarianer. Wir beten hier, um unsere Entschlossenheit hinsichtlich der Kriegsvorbereitungen gegen den Blutgott Hakkar zu stärken.$B$BBeweist Euch dem Stamm als würdig, indem Ihr den Altar nutzt, um alle Schmuckstücke der Hakkari zu zerstören, die Ihr in Zul\'Gurub finden könnt. Ich werde Euch auch Ehrenmünzen im Tausch gegen Münzsätze geben, die Ihr in Zul\'Gurub findet und die von unserem Stamm verwendet werden. Bringt mir die Münzen, nach denen ich verlange, und Ihr sollt belohnt werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10612', 'deDE', 'Der Blutgott, der Seelenschinder, wurde besiegt! Wir werden nicht länger unterdrückt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10613', 'deDE', 'Die zandalarianischen Trolle sind weit gereist und mussten viel ertragen, um diesen Ort zu erreichen. Nichts zu tun... wäre allerdings eine Einladung für unser Verhängnis...', 'Die zandalarianischen Trolle sind weit gereist und mussten viel ertragen, um diesen Ort zu erreichen. Nichts zu tun... wäre allerdings eine Einladung für unser Verhängnis...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10614', 'deDE', 'Fasst Euch ein Herz!$B$BDer Seelenschinder wurde besiegt und sein elendes Herz ins Jenseits befördert!', 'Fasst Euch ein Herz!$B$BDer Seelenschinder wurde besiegt und sein elendes Herz ins Jenseits befördert!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10615', 'deDE', 'Der Seelenschinder wurde besiegt! Unser Anführer, Molthor, ruft nun nach noch mehr seiner Diener...', 'Der Seelenschinder wurde besiegt! Unser Anführer, Molthor, ruft nun nach noch mehr seiner Diener...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10616', 'deDE', 'Wir, die zandalarianischen Trolle, sind hierher von weit entfernten Inseln gereist, denn wir können uns der Bedrohung, die sich unserer Welt stellt, nicht entziehen.$B$BDer Anführer unserer Expedition, Molthor, brachte uns zu der Insel Yojamba, die entlang der nördlichen Küste des Schlingendorntals liegt. Selbst jetzt stellt er noch mutige Helden ein, die sich in die uralte Stadt der Trolle wagen... Zul\'Gurub.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10618', 'deDE', 'Wir, die zandalarianischen Trolle, sind hierher von weit entfernten Inseln gereist, denn wir können uns der Bedrohung, die sich unserer Welt stellt, nicht entziehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10619', 'deDE', 'Nicht jetzt. Habt Ihr irgendwelche Aufgaben für mich?', 'Nicht jetzt. Habt Ihr irgendwelche Aufgaben für mich?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10620', 'deDE', 'Ich denke, ich komme alleine zur Festung zurück. Jetzt, da Ihr mir Zeit verschafft habt, dürfte ich mich herausschleichen können. Wer immer Ihr auch sein mögt...danke. Möge Elune Euch stets segnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10621', 'deDE', 'Ich möchte, dass mein Begleiter alles verlernt.', 'Ich möchte, dass mein Begleiter alles verlernt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10622', 'deDE', 'Was der Welpe nicht lernt, lernt ein treuer Gefährte nimmermehr. Zumindest habe ich mir das sagen lassen. Zu Eurem Glück habe ich gelernt, dass das nicht stimmt.$B$BAllerdings gibt es einen Haken, die Tiere tendieren dazu störrischer zu werden, mehr Widerstand zu leisten und es kostet mich auch immer mehr Futter, um sie bei Laune zu halten. Das bedeutet, dass Ihr mir jedes Mal, wenn ich dem gleichen Tier etwas verlerne, mehr Geld schuldet.$B$BSo, möchtet Ihr also, dass ich mit dem Verlernen beginne?', 'Was der Welpe nicht lernt, lernt ein treuer Gefährte nimmermehr. Zumindest habe ich mir das sagen lassen. Zu Eurem Glück habe ich gelernt, dass das nicht stimmt.$B$BAllerdings gibt es einen Haken, die Tiere tendieren dazu störrischer zu werden, mehr Widerstand zu leisten und es kostet mich auch immer mehr Futter, um sie bei Laune zu halten. Das bedeutet, dass Ihr mir jedes Mal, wenn ich dem gleichen Tier etwas verlerne, mehr Geld schuldet.$B$BSo, möchtet Ihr also, dass ich mit dem Verlernen beginne?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10623', 'deDE', 'Für welche Waren von den Entweihten habe ich mir das Kaufrecht verdient?', 'Für welche Waren von den Entweihten habe ich mir das Kaufrecht verdient?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10624', 'deDE', 'Ich bin Rutherford Twing, der Hauptlieferant für die Entweihten hier im Arathibecken. Wenn sich Euer Ansehen bei uns verbessert, werde ich Euch eine ausgezeichnete Auswahl an Waren anbieten, die für Euch von Nutzen sein könnten. Solltet Ihr eine Reparatur Eurer Gegenstände benötigen, so werde ich Euch auch diesen Dienst gewähren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10625', 'deDE', 'Für welche Waren vom Bund von Arathor habe ich mir das Kaufrecht verdient?', 'Für welche Waren vom Bund von Arathor habe ich mir das Kaufrecht verdient?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10626', 'deDE', 'Ich bin Samuel Hawke, der Hauptlieferant für den Bund von Arathor hier im Arathibecken. Wenn sich Euer Ansehen bei uns verbessert, werde ich Euch eine ausgezeichnete Auswahl an Waren anbieten, die für Euch von Nutzen sein könnten. Solltet Ihr eine Reparatur Eurer Gegenstände benötigen, so werde ich Euch auch diesen Dienst gewähren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10627', 'deDE', 'Ja, bitte doch.', 'Ja, bitte doch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10628', 'deDE', 'Verärgert mich nicht! Ihr würdet mich nicht mögen, wenn ich verärgert bin!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10629', 'deDE', null, 'Die Schlacht um die Kriegshymnenschlucht ist eine Schlacht, die wir nicht verlieren dürfen. Wir kämpfen um unsere Ehre und um unser Land, kein Orc oder fehlgeleiteter Verbündeter wird uns dies streitig machen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10630', 'deDE', 'Es ist keine einfache Aufgabe Kräfte zu sammeln, um das Holz der Horde in Ashenvale zu beschützen, aber wir haben uns noch nie vor einem Kampf gescheut! Die Nachtelfen und ihre Verbündeten werden fallen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10631', 'deDE', 'Zeituhr für den Spielstart deaktiviert.', 'Zeituhr für den Spielstart deaktiviert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10632', 'deDE', 'Die Angreifer sind geflohen! Kraft und Ehre der Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10633', 'deDE', 'Die Allianz siegt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10634', 'deDE', 'Die Horde siegt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10635', 'deDE', 'Es ist sinnlos zu flüchten, Sterbliche!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10636', 'deDE', '$N hat den Snowfall-Friedhof besetzt! Wenn niemand eingreift, erlangt die Allianz die Kontrolle über ihn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10637', 'deDE', '$N hat den Snowfall-Friedhof besetzt! Wenn niemand eingreift, erlangt die Horde die Kontrolle über ihn!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10638', 'deDE', 'Der Kampf um das Alteractal beginnt in 1 Minute.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10639', 'deDE', 'Der Kampf um das Alteractal beginnt in 30 Sekunden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10640', 'deDE', 'Der Kampf um das Alteractal hat begonnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10641', 'deDE', 'Hi! Ich bin Luis! Wie findet Ihr mich bis jetzt...?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10644', 'deDE', 'Was anderes!!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10645', 'deDE', '%s gerät in Raserei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10646', 'deDE', null, 'Seid gegrüßt, $G Reisender:Reisende;. Solange Ihr Euch innerhalb der Mauern von Burg Cenarius aufhaltet, seid Ihr in Sicherheit.$B$BGebt Acht, wenn Ihr den Schutz der Burg verlasst, die Silithiden sind in jüngster Zeit am Ausschwärmen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10647', 'deDE', 'Ich würde gerne Eure Waren durchstöbern, Calandrath.', 'Ich würde gerne Eure Waren durchstöbern, Calandrath.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10648', 'deDE', 'Ich versorge die Burg mit zahlreichen Relikten, die zum Zaubern benötigt werden, sowie mit anderen alchimistischen Reagenzien.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10649', 'deDE', 'Böses ist am Kochen, $R. Könnt Ihr es fühlen?$B$BBah! Natürlich könnt Ihr es nicht. Gebt mir Eure Rüstung und Eure Waffen und ich werde sie erneut mit Energie erfüllen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10650', 'deDE', null, 'Oh jeh, ich habe kaum noch Vorräte! Warum braucht der verfluchte Goblin nur so lange?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10651', 'deDE', 'Seht hinter Euch, $R. Die Schwärme sind in Bewegung und das Land hier zittert und bebt.$B$BEs ist an der Zeit, dass ich mein Versprechen wahrmache, das ich vor tausend Jahren gegeben habe. Mit den Kriegsplänen der Qiraji ziehen auch wieder Sandstürme auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10652', 'deDE', 'Ich möchte mein Tier unterbringen.', 'Ich möchte mein Tier unterbringen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10653', 'deDE', 'Warum jetzt, $R? Ich verstehe es einfach nicht. Es ist so viel zu tun...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10654', 'deDE', 'Der Kommandant war in letzter Zeit nicht er selbst. Seine geliebte Frau verschwand während einer archeologischen Ausgrabungstour in der südlichen Wüste - spurlos verschwunden! Höchst sonderbar, in der Tat.$B$BIch vermute Ihr sucht nach Arbeit? Muss ich Euch erst einen Nagel in den Kopf schlagen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10655', 'deDE', null, 'Könnt Ihr es hören, Fremder? Das Brummen wird mit jedem Tag lauter. Es müssen diese Kristalle sein, die die Käfer so verrückt machen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10656', 'deDE', 'Seht auf in den Himmel, $C. Die Drachenschwärme wurden in dieser Gegend seit tausend Jahren nicht mehr gesehen! Diese Qiraji haben etwas vor, ich weiß es einfach!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10657', 'deDE', 'Bronzebeard ist schon wieder verschwunden! Der Zwerg hat mit Sicherheit nichts von dem Gespür seines Bruders geerbt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10658', 'deDE', 'Ihr habt einen furchtbaren Fehler begangen, Sterbliche. Es ist nicht an der Zeit für mich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10659', 'deDE', 'Die Asche des brennenden Weidenmanns strahlt magische Energien aus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10660', 'deDE', 'Schmiert die Asche wie Kriegsbemalung auf mein Gesicht!', 'Schmiert die Asche wie Kriegsbemalung auf mein Gesicht!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10661', 'deDE', 'Die Asche des brennenden Weidenmanns strahlt magische Energien aus. Ihr habt die Asche bereits auf Euer Gesicht aufgetragen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10662', 'deDE', null, 'Kinder der Nacht, hört auf den Ruf Eurer Königin!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10663', 'deDE', null, 'Ich werde Euch beim Feiern dieser Nacht der Nächte begleiten - die Nacht, in der wir Verlassenen die Willkür der Geißel brachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10664', 'deDE', null, 'Es ist diese Nacht, während der uns unsere Feinde am meisten fürchten. Es ist DIESE Nacht, in der wir unseren Feinden zeigen, was es heißt, sich gegen die Verlassenen zu stellen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10665', 'deDE', null, 'Jetzt ist es an der Zeit, die Welt in ihren Grundfesten zu erschüttern! JETZT ist der Augenblick gekommen, um denen, die versuchen uns zu versklaven, zu zeigen, dass wir uns niemals beugen werden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10666', 'deDE', null, 'Wir verbrennen den Weidenmann als Symbol für unseren Kampf gegen jene, die sich uns entgegenstellen. Wir tragen dessen Asche als ein Symbol unseres endlosen Kampfes gegen alle, die uns unterjochen wollen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10667', 'deDE', null, 'JETZT ist die Zeit der Verlassenen! Alle Macht den Verlassenen, JETZT UND FÜR IMMER!!!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10668', 'deDE', 'Gruselige Schlotternächte, $C!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10669', 'deDE', 'Mögt Ihr Süßes? Ich liebe Süßes.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10670', 'deDE', 'Jau! Ein Hoch auf die Verlassenen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10671', 'deDE', null, 'Willkommen zum Fest des Weidenmanns, $C!$B$BWährend der Feierlichkeiten der Schlotternächte verbrennen die Verlassenen einen Weidenmann zu Ehren dieses höchst angesehenen Anlasses. Es war an diesem Tag, da die Bansheekönigin uns persönlich aus den Klauen des Lichkönigs und der Geißel befreite. Seitdem blieben wir frei. Diejenigen, die unseren Untergang vor Augen hatten, werden ihre Strafe erhalten... unsere Vergeltung! Die Verbrennung des Weidenmanns beginnt um 20 Uhr!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10672', 'deDE', 'Wa... wo bin ich? So... schwach...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10673', 'deDE', 'Ihr da! Ihr seht tauglich aus... wir brauchen Eure Hilfe bei der Verteidigung von Süderstade!$B$BDie verdammten Verlassenen sind wegen der Schlotternächte total aufgebracht. Ihre Art zu \"feiern\" wird sie zweifellos auch an unsere Türen führen. Solltet Ihr die Zeit haben, so könnten wir Eure Unterstützung wirklich gebrauchen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10674', 'deDE', 'So... kalt...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10675', 'deDE', 'Ja! Ich bin geheilt! Ich bin ge... whoa, bin wohl zu schnell aufgestanden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10676', 'deDE', 'Danke für das Aufspüren meiner Reagenzien! *Gähn* Das Gift hat mich \'n bisschen... müde gemacht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10677', 'deDE', '%s gerät in Raserei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10678', 'deDE', '%s brüllt, hoch oben auf der Blackrockspitze...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10679', 'deDE', 'Baristolth, ich habe mein Abzeichen verloren und bräuchte Ersatz.', 'Baristolth, ich habe mein Abzeichen verloren und bräuchte Ersatz.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10680', 'deDE', 'Wir sind es, die als Erste angreifen müssen. Wir können es uns nicht erlauben, die Fehler der Vergangenheit zu wiederholen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10681', 'deDE', 'Ich bin bereit, um mir Eure Geschichte anzuhören, Baristolth.', 'Ich bin bereit, um mir Eure Geschichte anzuhören, Baristolth.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10682', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10683', 'deDE', 'Verschwindet, $G Ungläubiger:Ungläubige;! Möge Euch die Macht des Abgrunds zerschmettern!', 'Verschwindet, $G Ungläubiger:Ungläubige;! Möge Euch die Macht des Abgrunds zerschmettern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10684', 'deDE', 'Ich bin kein Kultist, Ihr Monster! Kommt zu mir und stellt Euch Eurer Zerstörung!', 'Ich bin kein Kultist, Ihr Monster! Kommt zu mir und stellt Euch Eurer Zerstörung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10685', 'deDE', 'Purpurroter Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', 'Purpurroter Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10686', 'deDE', 'Der abyssische Rat duldet keinen Verrat! Fühlt unseren Zorn, schwächlicher $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10687', 'deDE', null, 'Seid gegrüßt, $C. Willkommen auf Burg Cenarius. Von hier aus werden wir die stärksten Positionen der Silithiden angreifen. Ich hoffe, dass Ihr uns dabei helfen werdet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10688', 'deDE', null, 'Es ist eine Freude Euch wiederzusehen, $N. Eure Hilfe hat für einen wahren Aufschwung in Sachen Sicherheit unserer Versorgungszufuhr gesorgt. Ich hoffe, dass Ihr versteht, was für eine wichtige Rolle die Logistik bei großen Schlachten wie dieser spielt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10689', 'deDE', null, 'Kein Glück mit dem Deserteur der Twilight-Hammer? Sucht eingehend in den Bergen im Südosten. Es kursieren zahlreiche Gerüchte über einen geheimnisvollen Einsiedler dort.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10690', 'deDE', 'Weißgrauer Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', 'Weißgrauer Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10691', 'deDE', 'Irdener Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', 'Irdener Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10692', 'deDE', 'Azurblauer Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', 'Azurblauer Templer! Ich halte Euer Wappen der Anrufung. Erhört meine Beschwörung!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10693', 'deDE', 'Süßes oder Saures!', 'Süßes oder Saures!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10694', 'deDE', 'Mein Lord wird empört sein, wenn er von diesem Hinterhalt erfährt. Lasst uns hoffen, dass Euer Tod ihn besänftigen wird.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10695', 'deDE', 'Es ist meine Pflicht und meine Ehre, für den abyssischen Rat zu sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10696', 'deDE', 'Euer Leben ist ein angemessenes Opfer für meinen Meister, $C.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10697', 'deDE', 'Was bildet Ihr Euch ein mit einem Fürsten des Rats zu sprechen? Wurm!', 'Was bildet Ihr Euch ein mit einem Fürsten des Rats zu sprechen? Wurm!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10698', 'deDE', 'Hört mir genau zu, elendiger Fürst! Ich bin nicht Euer Schoßhund der Twilight-Hammer! Ich bin hier, um Euch herauszufordern! Kommt her! Kommt und findet Euren Tod...', 'Hört mir genau zu, elendiger Fürst! Ich bin nicht Euer Schoßhund der Twilight-Hammer! Ich bin hier, um Euch herauszufordern! Kommt her! Kommt und findet Euren Tod...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10699', 'deDE', 'Fürst der Asche! Ich halte Euer Siegel! Erhört meinen Ruf!', 'Fürst der Asche! Ich halte Euer Siegel! Erhört meinen Ruf!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10700', 'deDE', 'Fürst der Stürme! Ich halte Euer Siegel! Erhört meinen Ruf!', 'Fürst der Stürme! Ich halte Euer Siegel! Erhört meinen Ruf!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10701', 'deDE', 'Fürst der Splitter! Ich halte Euer Siegel! Erhört meinen Ruf!', 'Fürst der Splitter! Ich halte Euer Siegel! Erhört meinen Ruf!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10702', 'deDE', 'Fürst der Tiefen! Ich halte Euer Siegel! Erhört meinen Ruf!', 'Fürst der Tiefen! Ich halte Euer Siegel! Erhört meinen Ruf!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10703', 'deDE', 'Ihr jagt Totenstachel, $N? Ich bin beeindruckt. Allein diese Woche habe ich zwei Männer an ihn verloren. Hoffen wir, dass Ihr mehr Glück habt als sie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10704', 'deDE', 'Hey! Danke, dass Ihr meine Haut gerettet habt! Ich glaube ich muss erstmal eine Weile liegen bleiben, damit ich wieder zu Kräften komme.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10705', 'deDE', 'Wer seid Ihr? Wo bin ich? Ich fühle mich... so schwach...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10706', 'deDE', 'Eine donnernde Stimme ertönt aus dem Stein...$B$BSeid gegrüßt, Kommandant. Welche Neuigkeiten aus Silithus bringt Ihr den Fürsten des Rats?', 'Eine donnernde Stimme ertönt aus dem Stein...$B$BSeid gegrüßt, Kommandant. Welche Neuigkeiten aus Silithus bringt Ihr den Fürsten des Rats?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10707', 'deDE', 'Der Tag des Gerichts ist gekommen, Unmensch! Ich fordere Euch zum Kampf heraus!', 'Der Tag des Gerichts ist gekommen, Unmensch! Ich fordere Euch zum Kampf heraus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10708', 'deDE', 'Skaldrenox! Ich halte Euer Szepter und rufe Euch an, damit Ihr diese Welt betretet!', 'Skaldrenox! Ich halte Euer Szepter und rufe Euch an, damit Ihr diese Welt betretet!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10709', 'deDE', 'Whirlaxis! Ich halte Euer Szepter! Ich befehle Euch, diese Welt zu betreten!', 'Whirlaxis! Ich halte Euer Szepter! Ich befehle Euch, diese Welt zu betreten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10710', 'deDE', 'Kazum! Ich halte Euer Szepter! Ich befehle Euch, diese Welt zu betreten!', 'Kazum! Ich halte Euer Szepter! Ich befehle Euch, diese Welt zu betreten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10711', 'deDE', 'Skwol! Ich halte Euer Szepter! Ich befehle Euch, diese Welt zu betreten!', 'Skwol! Ich halte Euer Szepter! Ich befehle Euch, diese Welt zu betreten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10712', 'deDE', 'Hallo, Rutgar. Der Kommandant hat mich hierher geschickt, um ein paar Informationen bezüglich seiner vermissten Frau in Erfahrung zu bringen.', 'Hallo, Rutgar. Der Kommandant hat mich hierher geschickt, um ein paar Informationen bezüglich seiner vermissten Frau in Erfahrung zu bringen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10713', 'deDE', 'Ihr setzt Euch besser hin, das wird eine lange Geschichte.$B$BAls wir zu Beginn in Silithus ankamen, war die Herrin Mar\'alith die erste Nachtelfin von der Burg Cenarius, die sich unserer Expedition anschloss. Unser Programm stellte nichts Ungewöhnliches dar: es galt eine Gruppe zu bilden, die zu den zahlreichen Schwarmbauten ziehen und nach verlorenen Artefakten suchen würde.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10714', 'deDE', 'Das hört sich gefährlich an.', 'Das hört sich gefährlich an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10715', 'deDE', 'Was denkt Ihr? Diese Bauten quollen über mit diversen Silithiden, die alles andere als froh waren uns zu sehen.$B$BAlles verlief problemlos bis wir auf einen glyphenverzierten Kristall im Bau des Regalschwarms stießen. Sie wurde von ihm angezogen wie eine Motte vom Licht! Sie presste ihre kleine, hübsche Wange gegen den Kristall und verfiel sofort in eine Art Trance. Meine Crew war deshalb natürlich beunruhigt. Wir trugen sie daraufhin so schnell wie möglich aus dem Bau!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10716', 'deDE', 'Was ist danach mit ihr geschehen?', 'Was ist danach mit ihr geschehen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10717', 'deDE', 'Wir haben sie zum Kommandanten gebracht und gehofft, dass die Druiden des Zirkels sie wieder zu sich bringen würden. Natürlich konnten sie sie wieder aufwecken. Wir betrachteten unsere Abmachung mit dem Zirkel als erledigt und machten uns wieder an unsere Arbeit.$B$BEs vergingen einige Wochen und alles verlief bestens. Brann war noch immer da und so hatten wir etwas Ordnung in unserer Arbeitsstruktur. Dann, eines Tages, wie aus dem Nichts, kam sie zu unserem Lager zurück.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10718', 'deDE', 'Natalia?', 'Natalia?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10719', 'deDE', 'Seid Ihr bescheuert? Von wem würde ich wohl sonst reden? Ja, das Mädl\' - Herrin Mar\'alith. Egal, sie war zurück und in nicht allzu guter Stimmung. Das war der Zeitpunkt, als es mit den Forderungen anfing.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10720', 'deDE', 'Welche Forderungen?', 'Welche Forderungen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10721', 'deDE', 'Sie verlangte, dass wir sofort zum Bau des Regalschwarms zurückkehren! Brann hätte sich sowas nie erlaubt. Er sagte ihr, dass es überhaupt nicht in Frage käme, ohne entsprechende Verstärkung, wieder dorthin zurückzukehren. Es war mittlerweile viel zu gefährlich geworden. Die Zahl der verdammten Insekten schien sich seit unserer letzten Begegnung schier verdreifacht zu haben.$B$BDas war der Punkt, an dem Natalia durchdrehte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10722', 'deDE', 'Durchgedreht? Was meint Ihr damit?', 'Durchgedreht? Was meint Ihr damit?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10723', 'deDE', 'Was meint Ihr? Sie ist durchgeknallt! Das Mädel ging sofort auf uns los. Normalerweise würde ich bei einer Frau niemals handgreiflich werden, aber als ich mich von dem Blackout erholt hatte, war ich verdammt wütend. Brann, Rutgar, ich und der Affe wussten uns kaum zu helfen! Es ist ein Wunder, dass wir sie abwehren konnten. Sie schien wie besessen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10724', 'deDE', 'Besessen? Von was?', 'Besessen? Von was?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10725', 'deDE', 'Wer weiß? Zumindest war das das letzte Mal, dass ich sie gesehen habe. Vielleicht weiß Frankal mehr.$B$BIch möchte ja nicht vom Thema ablenken, Eure Arbeit für den Kommandanten ist mit Sicherheit sehr wichtig, aber wir sind hier an einer ganz großen Sache dran. RIESIG! Unser Gesuch nach einer Armee stieß in Eisenschmiede bis jetzt auf taube Ohren. Scheinbar sind wir zu wenige in der Gegend, um für Sicherheit zu sorgen, oder so was Blödsinniges. Sobald Ihr Euren Bericht beim Kommandanten abgeliefert habt, kommt mit ein paar Freunden zurück zu uns. Wir brauchen Eure Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10726', 'deDE', 'Ich werde zurück sein, sobald ich Ordnung in dieses Chaos gebracht habe.', 'Ich werde zurück sein, sobald ich Ordnung in dieses Chaos gebracht habe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10727', 'deDE', 'Hallo, Frankal. Ich habe gehört, dass Ihr vielleicht ein paar Informationen hinsichtlich des Verbleibs von Herrin Natalia Mar\'alith habt.', 'Hallo, Frankal. Ich habe gehört, dass Ihr vielleicht ein paar Informationen hinsichtlich des Verbleibs von Herrin Natalia Mar\'alith habt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10728', 'deDE', 'Oh nein, nicht das schon wieder... Nehmt Platz $G Kleiner:Kleine;.$B$BIch bin mir sicher, dass Rutgar Euch schon einen Teil dieser Geschichte erzählt hat und verschone Euch daher mit langweiligen Einzelheiten, um direkt zur Sache zu kommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10729', 'deDE', 'Das ist genau das, was ich hören will.', 'Das ist genau das, was ich hören will.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10730', 'deDE', 'Na dann! Die Herrin kam also wegen einer Rückkehr zu den Schwarmbauten zurück, aber Brann hielt nichts von diesem Unfug! Bevor es zum Gemenge kam, begann sie mit jemandem oder etwas zu sprechen. Noch verrückter war, dass sie in einer Sprache, die zuvor noch nie jemand von uns gehört hatte, zu singen anfing.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10731', 'deDE', 'Das ist irre.', 'Das ist irre.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10732', 'deDE', 'Wem erzählt Ihr das! Es ist noch komischer, wenn man bedenkt, dass Brann, Rutgar, ich und der Affe zusammen 38 unterschiedliche Sprachen verstehen, einschließlich etwas Titanisch - sowohl Vanir als auch Aesir.$B$BAußerdem, mit wem auch immer sie gequatscht haben mag, es war definitiv keiner der von uns \"Anwesenden\". Sie nickte dann und wurde aggressiv! Hat uns direkt angegriffen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10733', 'deDE', 'Ihr wurdet also nicht mit einer einzigen Nachtelfenpriesterin fertig?', 'Ihr wurdet also nicht mit einer einzigen Nachtelfenpriesterin fertig?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10734', 'deDE', 'Hört mal $G Kleiner:Kleine;, damit Ihr\'s wisst, was auch immer uns angegriffen hat, es war keine Priesterin von gar nichts! In ihren Augen glühte etwas Böses und aus ihrem Mund trat Schaum hervor! Sie kam herein, stieß eine Art verrückten Schrei aus, der uns alle in unterschiedliche Richtungen rennen ließ, und begann dann unseren Geist, einen nach dem anderen, zu foltern. Ich hörte schattenhafte Wörter in meinem Kopf und sah hier und dort die anderen in Ohnmacht fallen. Es war Wahnsinn! Es hätte das Ende für uns alle bedeutet, wäre da nicht der verdammte Affe gewesen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10735', 'deDE', 'Ich wollte Euch wegen dieses Affens fragen.', 'Ich wollte Euch wegen dieses Affens fragen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10736', 'deDE', 'Was gibt\'s da zu fragen? Er ist ein Affe, Spinner. Egal. Nun, der Affe ist durchgedreht als er den alten Brann mit dem Gesicht voran in den Sand fallen sah. Nichts, was die Nachtelfin tat, konnte Glibb davon abhalten auf sie einzuhämmern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10737', 'deDE', 'Und dann?', 'Und dann?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10738', 'deDE', 'Ich sag Euch was \'dann\' war. Sie rannte geradewegs zum Schwarmbau. Wisst Ihr, was noch toller war? Kein einziges Krabbelviech stellte sich ihr in den Weg oder versuchte sie aufzuhalten. Sie rannte zwischen den Insekten hindurch, als ob sie mit ihnen schon seit ewigen Zeiten befreundet sei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10739', 'deDE', 'Was für eine Geschichte! Sie lief also in den Bau des Regalschwarms und das war das Letzte, was Ihr von ihr gesehen habt?', 'Was für eine Geschichte! Sie lief also in den Bau des Regalschwarms und das war das Letzte, was Ihr von ihr gesehen habt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10740', 'deDE', 'Jau.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10741', 'deDE', 'Danke für diese Informationen, Frankal.', 'Danke für diese Informationen, Frankal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10742', 'deDE', null, 'Ihr werdet ein passendes Opfer abgeben!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10743', 'deDE', null, 'C\'Thun wird diese Welt wieder zurückerobern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10744', 'deDE', 'Der Kristall ist mit komplizierten Glyphen und Runen bedeckt. Nichts von all dem ergibt einen Sinn.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10745', 'deDE', 'Aus dem Stein klingt ein schwaches Flüstern...$B$BSeid gegrüßt, $G Kleiner:Kleine;. Habt Ihr Neuigkeiten für die Templer?', 'Aus dem Stein klingt ein schwaches Flüstern...$B$BSeid gegrüßt, $G Kleiner:Kleine;. Habt Ihr Neuigkeiten für die Templer?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10746', 'deDE', 'Eine klare Stimme ertönt aus dem Stein...$B$BWo ist Euer Standesmedaillon, $G Kleiner:Kleine;? Ohne einen Standesbeweis dürft Ihr nicht mit dem Fürsten sprechen...', 'Wo ist Euer Standesmedaillon, $G Kleiner:Kleine;? Ohne einen Standesbeweis dürft Ihr nicht mit dem Fürsten sprechen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10747', 'deDE', 'Eine klare Stimme ertönt aus dem Stein...$B$BIhr kommt mit Zeichen Eures Standes zu uns und daher werden wir Euch anhören, aber stellt unsere Geduld nicht auf die Probe, $C. Der Rat hat keine Zeit für unnutzes Geschwätz von jemanden wie Euch.', 'Ihr kommt mit Zeichen Eures Standes zu uns und daher werden wir Euch anhören, aber stellt unsere Geduld nicht auf die Probe, $C. Der Rat hat keine Zeit für unnutzes Geschwätz von jemanden wie Euch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10748', 'deDE', 'Was für ein Insekt mag wohl vor den Rat treten? Eines, das mit Sicherheit zerquetscht werden möchte!', 'Was für ein Insekt mag wohl vor den Rat treten? Eines, das mit Sicherheit zerquetscht werden möchte!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10749', 'deDE', 'Eine donnernde Stimme ertönt aus dem Stein...$B$BIch kann Euer Standesmedaillon spüren, Kultist, aber habt Ihr keinen Herrschaftsring? Seid Ihr wirklich ein Kommandant der Twilight-Hammer oder seid Ihr lediglich der Laufbursche für andere?', 'Eine donnernde Stimme ertönt aus dem Stein...$B$BIch kann Euer Standesmedaillon spüren, Kultist, aber habt Ihr keinen Herrschaftsring? Seid Ihr wirklich ein Kommandant der Twilight-Hammer oder seid Ihr lediglich der Laufbursche für andere?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10750', 'deDE', 'Eine donnernde Stimme ertönt aus dem Stein...$B$BWas ist das? Ich spüre einen kleinen Kultisten umhergeistern! Lauft fort Wurm, oder stellt Euch den Fürsten des Rats und ihrem Zorn!', 'Eine donnernde Stimme ertönt aus dem Stein...$B$BWas ist das? Ich spüre einen kleinen Kultisten umhergeistern! Lauft fort Wurm, oder stellt Euch den Fürsten des Rats und ihrem Zorn!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10751', 'deDE', null, 'Du warst schon immer der Unruhestifter der Famile, Noggle. Immer!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10752', 'deDE', '<Verwendet das Abschriftenset, um ein Abpausbild zu erhalten.>', '<Verwendet das Abschriftenset, um ein Abpausbild zu erhalten.>', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10753', 'deDE', 'Wohin würdet Ihr gerne fliegen?', 'Wohin würdet Ihr gerne fliegen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10754', 'deDE', 'Die Geister von Silithus rufen nach uns, $N. Das Land stöhnt, es hat eine traurige Geschichte zu erzählen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10755', 'deDE', '%s gibt ein merkwürdiges Geräusch von sich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10756', 'deDE', 'Grüße, $C. Habe ich Euch nicht neulich auf einer exklusiven Veranstaltung gesehen? Ihr kommt mir bekannt vor, aber nun ja, in dieser Stadt tummeln sich ja etliche Reisende.$B$BSolltet Ihr auf dieser Veranstaltung gewesen sein, dann hat man Euch sicherlich einen geheimen Code gegeben. Verratet mir Euren Code und ich werde Euch dafür einen besonderen Preis geben.', 'Grüße, $C. Habe ich Euch nicht neulich auf einer exklusiven Veranstaltung gesehen? Ihr kommt mir bekannt vor, aber nun ja, in dieser Stadt tummeln sich ja etliche Reisende.$B$BSolltet Ihr auf dieser Veranstaltung gewesen sein, dann hat man Euch sicherlich einen geheimen Code gegeben. Verratet mir Euren Code und ich werde Euch dafür einen besonderen Preis geben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10757', 'deDE', 'Ich würde gerne den geheimen Code eingeben, um meinen Murlocbegleiter zu erhalten.', 'Ich würde gerne den geheimen Code eingeben, um meinen Murlocbegleiter zu erhalten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10759', 'deDE', null, 'Der Zirkel des Cenarius ist weise und hat sich mit zahlreichen Geheimnissen der Erde befasst. Handwerker würden gut daran tun, wenn sie den Worten dieser Druiden Beachtung schenken würden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10760', 'deDE', null, 'Die geisterhafte Gestalt sieht Euch mit Misstrauen in den Augen an und schweigt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10761', 'deDE', 'Zeigt mir, was ich erlernen kann.', 'Zeigt mir, was ich erlernen kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10762', 'deDE', null, 'Ich werde mein Bestes tun, um zu übersetzen und zu beschreiben, was ich sehe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10768', 'deDE', null, 'Willkommen! Tretet hervor, scheut Euch nicht. Hier ist mein Stolz und meine Freude... die Blastenheimer 5000 Ultrakanone! Ist sie nicht schön? Ihr seht um den Bauch herum etwas kräftig aus, aber ich denke Ihr werdet passen. Fliegen macht so viel Spaß, möchtet Ihr es lernen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10769', 'deDE', 'Erklärt mir, wie man die Blastenheimer 5000 Ultrakanone verwendet.', 'Erklärt mir, wie man die Blastenheimer 5000 Ultrakanone verwendet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10770', 'deDE', null, 'Oh, das ist einfach! Ihr verwendet sie wie alles andere, das man aktivieren muss. Keine Sorge, ich kümmere mich um den Zünder. Sobald Ihr in der Luft seid, werdet Ihr feststellen, dass Ihr zu \"schweben\" scheint, aber das hält nicht ewig an. Ihr müsst ein Ziel weit hinten auf dem See anvisieren. Vergesst nicht Euren Landeanflug zur richtigen Zeit zu beginnen, indem Ihr Eure \"Flügel\" ausbreitet, um auch im Wasser zu landen... mh, ja, ich verkaufe keine Lebensversicherungen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10771', 'deDE', null, 'Ich kam zu meinem alten Heim zurück, damit mein Wissen weitergegeben werden kann. Im Gegensatz zu den geistlosen Untoten um mich herum, habe ich mich zu diesem Unleben aus meinen eigenen Gründen heraus entschlossen.$B$BDie Silithiden sind fabelhafte Gegner, $N. Ich habe gelernt sie zu respektieren, genauso wie ich sie verachte.$B$BIhre Tödlichkeit beruht vor allem auf der Stärke und Leichtigkeit ihres Chitins, dessen Verarbeitung in Rüstungen ich erlernt habe. Da wir scheinbar einen gemeinsamen Feind haben, werde ich Euch mein Gewerbe lehren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10772', 'deDE', null, 'Hallo $C, seid willkommen auf dem Dunkelmond-Jahrmarkt. Es gibt hier so viel zu tun, dass der erste Besuch auf dem Jahrmarkt etwas überwältigend sein kann. Deshalb bin ich hier, um zu helfen. Habt Ihr Fragen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10773', 'deDE', 'Was kann ich kaufen?', 'Was kann ich kaufen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10774', 'deDE', null, 'Eine ausgezeichnete Frage! Auf dem Jahrmarkt gibt es zahlreiche Dinge zu kaufen. Interessiert Ihr Euch für Essen und Getränke, dann geht am besten rüber zum Stand von Stamp und Sylannia. Ich bevorzuge die Dunkelmond-Spezialmischung.\n\nWenn Ihr lieber etwas Exotischeres kaufen möchtet, dann seid Ihr bei Lhara wahrscheinlich an der richtigen Adresse. Sie hat sich auf Seltenes aus der ganzen Welt spezialisiert.\n\nAußerdem rennt Flik hier irgendwo herum. Solltet Ihr ihm über den Weg laufen, er verkauft Wurfbälle und Frösche, die man sich als Haustier halten kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10775', 'deDE', 'Was sind Gewinnlose des Dunkelmond-Jahrmarkts und wie bekomme ich sie?', 'Was sind Gewinnlose des Dunkelmond-Jahrmarkts und wie bekomme ich sie?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10776', 'deDE', null, 'Gut, dass Ihr gefragt habt. Mit Hilfe der Gewinnlose des Dunkelmond-Jahrmarkts lassen wir hier auf dem Jahrmarkt aushelfen und im Gegenzug können die Lose für besondere Preise eingelöst werden!\n\nFür den Anfang solltet Ihr mit Chronos, Kerri Hicks, Ringling und Yebb Nebblegear sprechen, um zu sehen, wobei Ihr am besten helfen könnt. Sie wissen immer etwas, was zur Verbesserung oder Erweiterung unserer Attraktionen gebraucht wird.\n\nSolltet Ihr genug Lose haben, so sprecht mit Gelvas Grimegate, er wird Euch eine Liste zeigen, aus der Ihr unterschiedliche Preise wählen könnt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10777', 'deDE', 'Was sind Dunkelmond-Karten?', 'Was sind Dunkelmond-Karten?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10778', 'deDE', null, 'Niemand weiß so wirklich, wer die Dunkelmond-Karten angefertigt hat oder welchem Zweck sie genau dienen, allerdings kann ich Euch sagen, dass sie in ganz Azeroth gefunden wurden, normalerweise in den Händen eines Bösewichts, der ihre Geheimnisse enthüllen wollte.\n\nEs gibt vier Arten von Dunkelmond-Karten; Bestien, Elementare, Portale und Kriegsfürsten. Jedes Set besteht aus acht Karten, von Ass bis 8. Sammelt Ihr alle acht Karten einer Farbe, könnt Ihr diese als zusammengefasstes Set bei unserem Professor Thaddeus Paleo gegen ein mächtiges Schmuckstück eintauschen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10779', 'deDE', 'Was kann ich noch auf dem Jahrmarkt machen?', 'Was kann ich noch auf dem Jahrmarkt machen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10780', 'deDE', null, 'Ich rate Euch besonders zu einem Besuch bei Sayge, damit er Euch Euer Schicksal voraussagen kann. Ich weiß, ich weiß... er ist ein Gnoll. Er ist aber ein ganz besonderer Gnoll, mit der Macht Eure Zukunft erahnen zu können!$B$BWir erweitern den Jahrmarkt auch regelmäßig mit neuen Attraktionen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10781', 'deDE', 'Wir haben mehr als nur einen Feind in Silithus, $N.$B$BWährend sich jedermanns Aufmerksamkeit auf die Silithiden richtet, haben mein großer Bruder und ich ein Auge auf die Verrückten der Twilight-Hammer geworfen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10782', 'deDE', null, 'Ich rate Euch besonders zu einem Besuch bei Sayge, damit er Euch Euer Schicksal voraussagen kann. Ich weiß, ich weiß... er ist ein Gnoll. Er ist aber ein ganz besonderer Gnoll, mit der Macht Eure Zukunft erahnen zu können!$B$BWir erweitern den Jahrmarkt auch regelmäßig mit neuen Attraktionen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10783', 'deDE', 'Seid gegrüßt, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10784', 'deDE', 'Grüße, Uralter. Ich habe alles getan, was von mir verlangt wurde. Jetzt bitte ich Euch, dass Ihr mir Rhok\'delar gewährt.', 'Grüße, Uralter. Ich habe alles getan, was von mir verlangt wurde. Jetzt bitte ich Euch, dass Ihr mir Rhok\'delar gewährt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10785', 'deDE', 'Grüße, Uralter. Ich habe alles getan, was von mir verlangt wurde. Jetzt bitte ich Euch, dass Ihr mir Lok\'delar gewährt.', 'Grüße, Uralter. Ich habe alles getan, was von mir verlangt wurde. Jetzt bitte ich Euch, dass Ihr mir Lok\'delar gewährt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10786', 'deDE', 'Bawk, bawk, bawk! Gruselige Schlotternächte, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10787', 'deDE', null, 'Ich mag vielleicht tausend Probleme haben, aber Tanzen gehört nicht dazu! Gruselige Schlotternächte, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10788', 'deDE', null, 'Tschugga-tschugga, huu-huu! Gruselige Schlotternächte, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10789', 'deDE', null, 'Oooh, so stark! Gruselige Schlotternächte, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10790', 'deDE', null, 'Seid gegrüßt, Reisender. Ja, es war hier, vor fast 1000 Jahren, als die Qiraji ihren Marsch der Zerstörung, der sich über ganz Kalimdor erstrecken sollte, begannen.$B$BTausende verloren ihr Leben in dem Krieg, aber hier in dieser verfluchten Wüste, während der ersten Invasion, starben die meisten unserer tapferen Krieger.$B$BIch bin einer der wenigen Überlebenden, der sein Dasein hier angekettet an die Vergangenheit und die Gegenwart gleichzeitig fristen muss. Ich weiß nicht warum ich hier noch stationiert bin, aber ich halte mich an meine Anweisungen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10791', 'deDE', 'Woher wisst Ihr das?', 'Woher wisst Ihr das?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10792', 'deDE', null, 'Ich wurde schon hunderte Male getötet, doch wache ich immer wieder hier auf, lebendig. Einige behaupten, ich sei verflucht, andere jedoch...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10793', 'deDE', 'Was sagen sie?', 'Was sagen sie?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10794', 'deDE', null, 'Sie sagen ich sei gesegnet: Ein Herold, um eine neue Ära anzukündigen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10795', 'deDE', 'Und was sagt Ihr dazu?', 'Und was sagt Ihr dazu?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10796', 'deDE', null, 'Uralte Mächte erwachen - Mächte die seit Tausenden von Jahren geschlafen haben. Die Welt wurde in Angst und Schrecken versetzt, $R. Eine neue Trennung ist zu befürchten, eine Seuche, die aus der Tiefe geboren werden wird...$B$BDas Verderben hat sich bereits in unseren Ländern verbreitet. Des Nachts werden aus Träumen Alpträume, Alptäume, die uns am Tage vergiften werden...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10797', 'deDE', null, 'Oooh, so stark! Gruselige Schlotternächte, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10798', 'deDE', 'Bawk, bawk, bawk! Gruselige Schlotternächte, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10799', 'deDE', 'Tschugga-tschugga, huu-huu! Gruselige Schlotternächte, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10800', 'deDE', null, 'Ich mag vielleicht tausend Probleme haben, aber Tanzen gehört nicht dazu! Gruselige Schlotternächte, $N!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10801', 'deDE', 'Ihr werdet für diese Unverfrorenheit bezahlen, Wurm. Eure Seele gehört mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10802', 'deDE', 'Diese Tat der Missachtung wird nicht unbestraft bleiben. Ihr und Eure Welt werdet sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10803', 'deDE', 'Was? So ein kleines, gebrechliches Ding wagt es, mich anzurufen? Das wird nicht unbestraft bleiben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10804', 'deDE', 'Betrüger! Es ist eine unvergleichliche Schande von einem Wurm, wie Euch, angerufen zu werden! STERBT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10805', 'deDE', 'Euer Verrat bedeutet Euren Untergang. Diese Welt wird von der Dunkelheit verschlungen werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10806', 'deDE', 'Ihr wagt es! Frevelhaft! Ich verfluche Euch, $C. Ich verfluche Euch... zu Tode!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10807', 'deDE', 'Was? So ein kleines, gebrechliches Ding wagt es, mich anzurufen? Ich werde mit Euren Knochen meinen Thron verzieren, $R!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10808', 'deDE', null, 'Gruselige Schlotternächte! Mögt Ihr meine Maske? Ganz schön gruselig!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10809', 'deDE', null, 'Gruselige Schlotternächte! Wie findet Ihr meine Maske? Ganz schön gruselig!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10810', 'deDE', 'Euch und Eure Schar zu töten, $C, wird mich köstlich amüsieren. Ich werde es kurz machen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10811', 'deDE', null, 'Ich bin traurig! Jetzt sind Schlotternächte und ich bin krank. Ich werde all die Süßigkeiten und das Streichespielen verpassen, weil ich nicht nach draußen kann!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10812', 'deDE', 'Ich bin traurig! Jetzt sind Schlotternächte und ich bin krank. Ich werde all die Süßigkeiten und das Streichespielen verpassen, weil ich nicht nach draußen kann!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10813', 'deDE', null, 'Ihr seid einfach genial, $N - danke, dass Ihr für mich das Süßigkeitensammeln und Streichespielen übernommen habt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10814', 'deDE', 'Ihr seid einfach genial, $N - danke, dass Ihr für mich das Süßigkeitensammeln und Streichespielen übernommen habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10815', 'deDE', 'Was für Schlotternachtssüßigkeiten bietet Ihr zum Kauf an?', 'Was für Schlotternachtssüßigkeiten bietet Ihr zum Kauf an?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10816', 'deDE', 'Was kann man während der Schlotternächte machen?', 'Was kann man während der Schlotternächte machen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10817', 'deDE', null, 'Was kann man NICHT machen, meint Ihr wohl!$B$BIn der ganzen Welt verteilen alle Gastwirte Schlotternachtssüßigkeiten und viele Ortschaften sind festlich geschmückt! Abends gibt es zum Beispiel die Feier des Weidenmanns vor den Toren Unterstadts.$B$BUnd noch etwas - ich habe gehört, dass ein kleines krankes Mädchen aus dem Waisenhaus hier in Orgrimmar nach jemandem sucht, der ihr hilft, Süßigkeiten für das Fest zu sammeln... vielleicht könnt Ihr ihr ja helfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10818', 'deDE', null, 'Was kann man NICHT machen, meint Ihr wohl!$B$BIn der ganzen Welt verteilen alle Gastwirte Schlotternachtssüßigkeiten und viele Ortschaften sind festlich geschmückt! Sergeant Hartman in Süderstade benötigt außerdem Hilfe, um gegen die Untoten und ihre Feier des Weidenmanns vorzugehen.$B$BUnd noch etwas - ich habe gehört, dass ein kleiner kranker Junge aus dem Waisenhaus in Sturmwind nach jemandem sucht, der ihm hilft, Süßigkeiten für das Fest zu sammeln.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10819', 'deDE', '$N hat mich freigegeben.', '$N hat mich freigegeben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10820', 'deDE', 'Sergeant Hartman! Ich benötige noch einen weiteren Stinkbombenreiniger.', 'Sergeant Hartman! Ich benötige noch einen weiteren Stinkbombenreiniger.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10821', 'deDE', 'Ich möchte eine weitere Stinkbombe kaufen!', 'Ich möchte eine weitere Stinkbombe kaufen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10822', 'deDE', '%s lacht irre.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10823', 'deDE', 'Nein! Das kann nicht sein…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10824', 'deDE', 'Erbärmlicher Narr! Ich werde Euch zeigen, was Schmerz bedeutet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10826', 'deDE', '%s bittet Impsy um Verzeihung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10827', 'deDE', 'Bereitet Euch auf Euren Untergang vor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10828', 'deDE', 'Oh oh…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10829', 'deDE', null, 'Es ist eine Geschichte… eine Geschichte über Silithus… über Ahn\'Qiraj… über Titanen und die alten Götter… Ich lese nun aus der Prophezeiung von C\'Thun, so wie sie einst vom Qirajipropheten Skerram verfasst wurde. Die Prophezeiung spricht von einer Zeit der Umwälzung...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10830', 'deDE', null, 'In einer Zeit vor der Zeit, als die Welt noch jung war, tobte auf dieser Welt eine Schlacht zwischen einem Titanen und einem Wesen von unvollstellbarem Bösen. Die Prophezeiung ist undeutlich, ob der Titan in diesem Kampf letztlich vernichtet wurde, sicher ist nur, dass einer der Titanen gefallen ist. Man nahm an, dass einer der alten Götter ebenfalls der Schlacht zum Opfer fiel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10831', 'deDE', null, 'Seid Jahrtausenden schlief dieses Wesen unter dem schützenden Mantel der Welt, wartend, bis seine Zeit kommen würde. Von seinem irdenen Gefängnis aus, lauerte es auf den richtigen Moment, den Tag, an dem es gegen jene zurückschlagen würde, die es vernichten wollten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10832', 'deDE', null, 'Das Land des ewigen Sternenlichts, Kalimdor, war wie eine fürsorgliche Mutter für seine Bewohner. Der Brunnen der Ewigkeit erfüllte das Land mit seiner Magie. Seine Kraft brachte die artenreiche Flora und Fauna hervor, welche einst diese Welt bewohnen sollte. Dieser magische Äther gebar letztlich auch die Silithiden. Sie sollten das Werkzeug sein, mit dem der alte Gott versuchen würde, die Welt, die er einst mit eiserner Hand beherrschte, zu teilen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10833', 'deDE', null, 'Der uralte Gott formte aus den Silithiden Avatare, nach dem Vorbild seiner selbst. Diese Avatare würden später als Qiraji bekannt werden. Empfindungsfähig und mit einer Bestimmung ausgestattet, würden sie schon bald ihrem Schöpfer einen Namen geben: C\'Thun war geboren... Jahrtausende lang arbeiteten die Qiraji daran eine Streitmacht aufzustellen, welche diese Welt, die ihren Schöpfer so sehr verraten hatte, in Schutt und Asche verwandeln sollte. Man erbaute die große Festungsstadt Ahn\'Qiraj, um die wachsenden Armeen zu beherbergen und um sich auf die Ankunft von C\'Thun vorzubereiten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10834', 'deDE', '%s untersucht den Geißelstein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10835', 'deDE', 'Das Böse war stark in diesen Steinen, doch nun ist es gebannt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10836', 'deDE', null, 'Das kann nicht stimmen. Einem alten Gott könnte es niemals gestattet werden diese Welt zu betreten. Die Folgen wären… oh mein Gott!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10837', 'deDE', null, '%s erlangt Ihre Fassung wieder.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10838', 'deDE', null, 'Da ist noch mehr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10839', 'deDE', null, 'Der Augenblick, auf den C\'Thun solange gewartet hatte, war endlich gekommen. Seine Kinder verbrachten tausende von Jahren damit, eine Armee aufzustellen, welche sich an ganz Kalimdor rächen würde und nun war der Sieg zum Greifen nahe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10840', 'deDE', null, 'Es war lange her, seit die Titanen diese Welt aufgegeben hatten. Nur die Nachtelfen, welche einst dieses Gebiet bewohnten, sammelt sich hier zur Verteidigung. Oh! Die nächste Passage ist faszinierend!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10841', 'deDE', null, 'Ein mächtiger Qiraji, bekannt als General Rajaxx, würde den ersten Angriff auf Silithus anführen. Nur eine Person würde es wagen, sich seinen Plänen entgegenzustellen, ein Nachtelf namens Staghelm. Man spricht in der Muttersprache der Qiraji auch als Khar\'sis oder \'Hand der Erde\' von ihm.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10842', 'deDE', null, 'Als Rajaxx Truppen zu tausenden in Silithus einfielen, waren ihnen Staghelms Armeen klar unterlegen. Jedoch, sollte sich schnell herausstellen, dass Staghelm für die Qiraji weit mehr war, als ein kleines Ärgernis, das es aus dem Weg zu schaffen galt. Unter Staghelm versammelte sich alle Armeen der Nachtelfen, um Kalimdor zu verteidigen. Es sollte nicht lange dauern, bis der Vormarsch der Qirajistreitkräfte merklich verlangsamt wurde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10843', 'deDE', null, 'Unglücklicherweise war es absehbar, dass die Verteidigung der Nachtelfen nicht dauerhaft standhalten würde, dies wusste auch Rajaxx. Doch die Hartnäckigkeit der Verteidiger trieb Rajaxx fast in den Wahnsinn, während sein König vor Wut schäumte. Moment, Könige..? Unglaublich...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10844', 'deDE', null, 'Vek\'nilash und Vek\'lor, die Zwillingsimperatoren von Ahn\'Qiraj, beobachteten den Fortgang des Krieges aus dem Heiligtum ihres Tempels heraus. Die klugen Brüder entwarfen einen Plan, mit dem sie die Streitkräfte Staghelms letztlich aufsplitten und demoralisieren wollten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10845', 'deDE', null, 'Valstann Staghelm diente Fandral, als dessen rechte Hand. Er war ein stolzer und edler Krieger, dessen Hingabe und Verehrung für seinen Vater keine Grenzen kannte… darin erkannten die Zwillingsimperatoren seine Schwäche. Eine Schwäche, die es auszunutzen galt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10846', 'deDE', null, 'Den Armeen der Qiraji befahl man sich zurückzuziehen und sich unter dem Sandmeer der Wüste zu verstecken. So sollten sie ihre wahre Stärke verbergen. Während Staghelms Streitkräfte durch ständige Angriffe an die Frontlinie gebunden wurden, begann der Angriff auf den Südwindposten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10847', 'deDE', null, 'Valstann, geblendet davon seinen Vater zufrieden zu stellten, überzeugte Fandral, ihn mit einem kleinen Batallion zur Verteidigung des Südwindpostens zurückzulassen. Fandral war sicher, dass jetzt, wo das Schlachtenglück sich zu ihren Gunsten verschob, sein geliebter Sohn dort sicher wäre. Er sollte sich irren, als wenig später Vallstann von den im Verborgenen lauernden Qiraji gefangengenommen und der Südwindposten ausradiert wurde. Es war Rajaxx selbst, der den gefangenen Valstann an die Frontlinie der tobenden Schlacht führte, wo er den jungen Nachtelfen vor den Augen Staghelms und dem Nachtelfenheer auf grausamste Weise hinrichten ließ.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10848', 'deDE', null, 'Der Krieg ging weiter, doch der Wille des großen Anführers war gebrochen. Es dauerte nicht lange, und ganz Silithus wurde von den Silithiden und ihren Herren, den Qiraji, verschlungen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10849', 'deDE', null, 'Armer Mann, das erklärt so manches.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10850', 'deDE', null, 'Die Nachtelfenarmee wurde durch Un\'Goro bis hin zu den Grenzen der Tanariswüste zurückgeworfen. Interessanterweise schien irgendetwas in Un\'Goro die Qiraji daran zu hindern, sich das dortige Gebiet einzuverleiben. Ich verstehe die Bedeutung des nächsten Wortes nicht ganz, aber ich vermute, es bedeutet so etwas wie das \'Land der Götter\'. Es heißt, dass sie das \'Land der Götter\' nicht einnehmen konnten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10851', 'deDE', 'Es ist faszinierend zu sehen, wie sehr sich das alles mit den Theorien deckt, die besagen, dass Un\'Gro einst die Heimat der Titanen war, als diese noch auf Kalimdor wandelten. Vielleicht wachte sogar Aman\'thul selbst über das \"Land der Götter\".', 'Es ist faszinierend zu sehen, wie sehr sich das alles mit den Theorien deckt, die besagen, dass Un\'Gro einst die Heimat der Titanen war, als diese noch auf Kalimdor wandelten. Vielleicht wachte sogar Aman\'thul selbst über das \"Land der Götter\".', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10852', 'deDE', null, 'Hmm, merkwürdig… Dieser Teil ist unvollständig. Es heißt, in Tanaris würden die Qiraji auf einen Bronzedrachen treffen. Allerdings fehlt der Rest des Textes… wir müssen unbedingt die fehlenden Informationen finden! Ich bin sicher, dass sie eine erneute Invasion von Kalimdor planen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10853', 'deDE', '%s rutscht ungemütlich hin und her.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10854', 'deDE', 'Wenn die Zeit reif ist, Euer Gnaden. Wenn es soweit ist, wird alles ganz klar vor uns liegen. Selbst jetzt regt sich der Meister. Seht zum Himmel, denn die Brut Nozdormus ist zurückgekehrt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10855', 'deDE', 'Einige nennen mich… Impsy?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10856', 'deDE', null, 'Die Verbrennung des Weidenmanns beginnt in dreißig Minuten! Besucht das Fest, westlich von Unterstadt, und feiert mit uns die Schlotternächte und unsere Befreiung von der Geißel!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10857', 'deDE', 'Verlasst diesen Ort, Fremder! Zwischen uns gibt es nichts zu bereden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10858', 'deDE', 'Ihr seid schon zurück? Was kann ich dieses Mal für Euch tun?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10859', 'deDE', null, 'Die Verbrennung des Weidenmanns beginnt in fünfzehn Minuten! Besucht das Fest, westlich von Unterstadt, und feiert mit uns die Schlotternächte und unsere Befreiung von der Geißel!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10860', 'deDE', null, 'Die Verbrennung des Weidenmanns beginnt in fünf Minuten! Besucht das Fest, westlich von Unterstadt, und feiert mit uns die Schlotternächte und unsere Befreiung von der Geißel!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10861', 'deDE', null, 'Das Fest des Weidenmanns beginnt jetzt! Alle Macht den Verlassenen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10862', 'deDE', 'Der Krieg gegen die Horde hat enorme Ausmaße angenommen, $N. Dies ist eine Zeit der Prüfungen für die Allianz und ein jeder von uns sollte seinen Beitrag leisten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10863', 'deDE', 'Es herrscht Krieg, $N! Es ist an der Zeit, dass ein jedes Mitglied der Horde dem Ruf der Waffen folgt, um den Kampf mit der feigen Allianz aufzunehmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10864', 'deDE', '%s begutachtet den Boden und nickt zustimmend.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10865', 'deDE', 'Ich denke es ist nicht stark genug.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10866', 'deDE', 'Kommt, $N. Der See befindet sich direkt hinter der nächsten Biegung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10867', 'deDE', 'Bleibt in meiner Nähe, $N. Ich werde Euch beschützen, falls etwas schief geht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10868', 'deDE', 'Malfurion!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10869', 'deDE', 'Remulos, mein alter Freund. Es ist schön Euch wiederzusehen. Ich wusste, dass meine Botschaft Euch erreichen würde, auf dem einen oder auf dem anderen Weg.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10870', 'deDE', 'Alles ist von schrecklichen Alpträumen durchzogen, Malfurion. Was geht im Smaragdgrünen Traum nur vor? Was könnte ihn derartig grausam verzerren?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10871', 'deDE', 'Ich fürchte das Schlimmste, alter Freund. Innerhalb des Smaragdgrünen Traums bekämpfen wir einen neuen Feind, geboren aus uraltem Bösen. Yseras edle Brut ist den uralten Einflüsterungen erlegen. Es scheint, als sei es dem Alptraum gelungen die Grenzen der Traumwelt zu durchbrechen, um sich in Azeroth eine neuen Wirt zu suchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10872', 'deDE', 'Ich habe es auch gespürt, Malfurion. Dunkle Zeiten werfen ihre Schatten voraus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10873', 'deDE', 'Ja, Remulos. Die sterblichen Völker müssen auf das Kommende vorbereitet werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10874', 'deDE', 'Ihr seid schon zu lange fort, Malfurion. Der Frieden zwischen den Kindern Azeroths ist bestenfalls brüchig. Was ist mit meinem Vater? Eurem Bruder? Habt Ihr Neuigkeiten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10875', 'deDE', null, 'Ihr seid ein Lederer, nicht wahr? Es ist dieser leichte Duft nach gegerbten Fellen an Euch, den ich überall erkennen würde.$B$BMein Vater erzählte mir oft von einem sehr begabten Handwerksmeister, der vor Jahrhunderten beim Südwindposten gelebt haben soll. Es ist eine Schande, dass es dort heutzutage nur noch Ruinen gibt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10876', 'deDE', 'Cenarius kämpft an meiner Seite. Illidan verweilt, vor sich hinbrütend, auf seinem Thron in der Scherbenwelt. Ich befürchte, dass die Niederlage gegen Arthas ihn innerlich gebrochen hat. Er scheint dem Wahnsinn anheim gefallen zu sein, Remulos. In seinen Gedanken wiederholt er immer und immer wieder die vergangenen Ereignisse, doch in Gedanken macht er alles richtig. In seinem Verstand besiegt er Arthas ein auf das andere Mal, es ist ein endloser Kreislauf. Er ist schon zu weit von uns gegangen, mein alter Freund. Ich fürchte, es naht die Zeit, in der unser Freundschaft auf die Probe gestellt wird, ganz anders als damals am Brunnen in Zin-Azshari.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10877', 'deDE', 'Lebt wohl, alter Freund… Lebt wohl…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10878', 'deDE', 'Remulos, ich werde zurückgezogen… Tyrande… sende ihr meine Liebe… sagt ihr, dass es mir gut geht. Sagt ihr, dass ich zurückkehren werde… Lebt wohl...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10879', 'deDE', 'Lasst uns zum Hain zurückkehren, Sterblicher.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10880', 'deDE', 'Die Fäden des LEBENS wurden durchtrennt! Die Träumer müssen gerächt werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10881', 'deDE', 'Tretet vor, ihr Träumer, und nehmt eure Rache!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10882', 'deDE', 'Eure verdorbenen Seelen werden meine Kräfte stärken!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10883', 'deDE', 'Ich fühle die SCHATTEN in Euren Herzen. Niemals darf das Böse Ruhe finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10884', 'deDE', 'Kostet die Verderbnis Eurer Welt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10885', 'deDE', 'Die Hoffnung ist eine KRANKHEIT der Seele. Dieses Land wird verdorren und sterben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10886', 'deDE', 'Frieden ist nur ein flüchtiger Traum! Von nun an herrscht der ALPTRAUM!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10887', 'deDE', 'Kinder des Wahnsinns, ich schenke euch diese Welt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10888', 'deDE', 'Kommt, mickrige Sterbliche. Stellt Euch Eurem bitteren Ende! Die Geheimnisse von Ahn\'Qiraj werden mit Roman\'khan untergehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10890', 'deDE', 'Wir haben alle benötigten Ressourcen angesammelt! Juhuu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10891', 'deDE', 'Wir haben nun alle Kupferbarren, die wir benötigen. Ich muss Euch für Eure Hilfe danken, $C. Vielleicht könntet Ihr den anderen beim Sammeln Ihrer Vorräte helfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10892', 'deDE', '$C, Ich brauche dringend mehr Kupferbarren! Vor den Toren von Ahn\'Qiraj braut sich ein Krieg zusammen und wir benötigen Vorräte! Die Allianz hat bis jetzt $1997w Barren eingelagert. Allerdings brauchen wir eine Gesamtmenge von $1998w Stück. Wenn ich die restlichen Kupferbarren nicht bald zusammenkriege, servieren sie demnächst meinen Kopf auf einem Silbertablett.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10893', 'deDE', 'Meine Wut ist frei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10894', 'deDE', '%s wird in 3 Sekunden wieder kampfbereit sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10895', 'deDE', 'Myolor, ich war einst ein Rüstungsschmied und möchte stattdessen die Kunst des Waffenschmiedens erlernen. Werdet Ihr mich ausbilden?', 'Myolor, ich war einst ein Rüstungsschmied und möchte stattdessen die Kunst des Waffenschmiedens erlernen. Werdet Ihr mich ausbilden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10896', 'deDE', 'Myolor, ich war einst ein Waffenschmied und möchte stattdessen die Kunst des Rüstungsschmiedens erlernen. Werdet Ihr mich ausbilden?', 'Myolor, ich war einst ein Waffenschmied und möchte stattdessen die Kunst des Rüstungsschmiedens erlernen. Werdet Ihr mich ausbilden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10897', 'deDE', 'Krathock, ich war einst ein Rüstungsschmied und möchte stattdessen die Kunst des Waffenschmiedens erlernen. Werdet Ihr mich ausbilden?', 'Krathock, ich war einst ein Rüstungsschmied und möchte stattdessen die Kunst des Waffenschmiedens erlernen. Werdet Ihr mich ausbilden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10898', 'deDE', 'Krathok, ich war einst ein Waffenschmied und möchte stattdessen die Kunst des Rüstungsschmiedens erlernen. Werdet Ihr mich ausbilden?', 'Krathok, ich war einst ein Waffenschmied und möchte stattdessen die Kunst des Rüstungsschmiedens erlernen. Werdet Ihr mich ausbilden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10899', 'deDE', 'Im Inneren des Kristalls scheinen sich höhlenartige Tunnelsysteme und Korridore wiederzuspiegeln. Ihr erkennt die feurigen Tiefen des geschmolzenen Kerns.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10900', 'deDE', '<Berührt den instabilen Risskristall>', '<Berührt den instabilen Risskristall>', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10901', 'deDE', 'Lasst sie den Zorn des blauen Drachenschwarms spüren! Möge Malygos mir zur Seite stehen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10902', 'deDE', 'Alexstrasza, gewähre mir die Kraft, unsere Feinde zurückzutreiben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10903', 'deDE', null, 'Gebt euch dem endlosen Traum hin, Winzlinge. Auf dass er euch verzehren möge!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10904', 'deDE', 'Anachronos, diese Ableckung sollte Euch und dem jungen Druiden genug Zeit geben, das Portal wieder zu versiegeln. Zögert nicht! Jetzt lasst uns sehen, wie sich unser Feind gegen die Magie des Chaos schlägt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10907', 'deDE', 'Niemals dürft Ihr die am heutigen Tage dargebrachten Opfer vergessen, Nachtelf. Wir alle haben durch diese Untiere endloses Leid erfahren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10908', 'deDE', null, 'Wir werden sie zurückdrängen, Anachronos. Das schwöre ich. Haltet Ihr Euren Teil der Vereinbarung ein. Lasset Eure Hände nicht zögern, wenn Ihr unser Schicksal hinter der Barriere entscheidet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10909', 'deDE', 'Wir müssen schnell handeln, oder alles ist verloren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10910', 'deDE', 'Meine Streitkräfte können die Verteidigung der Qiraji nicht überwinden. Nun, Drache, wie es aussieht, werden wir nicht nahe genug an sie herankommen, um Eure kostbare Barriere zu platzieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10911', 'deDE', null, 'Es gibt einen Weg...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10912', 'deDE', null, '%s wirft einen flüchtigen Blick auf ihre Verbündeten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10913', 'deDE', '%s nickt wissend.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10914', 'deDE', 'Fandral, denkt immer an diese Worte: Lasst Eurer Schicksal nicht von Eurem Kummer leiten. Diese Gedanken, die Ihr in Euch tragt… sie werden Euch an dunkle Orte führen, Nachtelf. Erlösung erlangt man nicht durch fehlgeleitete Rache.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10915', 'deDE', 'JETZT, STAGHELM! WIR MÜSSEN LOS! Bereitet Eure Magie vor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10916', 'deDE', 'Es ist getan, Drache. Zeigt mir den Weg.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10917', 'deDE', 'Bleibt zusammen…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10918', 'deDE', 'Seid gegrüßt, $R. Ein wundervoller Tag, um am Leben zu sein, nicht wahr?', 'Seid gegrüßt, $R. Ein wundervoller Tag, um am Leben zu sein, nicht wahr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10919', 'deDE', 'Nicht alle Tauren verachten Euresgleichen, $R. Dennoch ist es mir nicht erlaubt, Euch meine Dienste anzubieten.', 'Nicht alle Tauren verachten Euresgleichen, $R. Dennoch ist es mir nicht erlaubt, Euch meine Dienste anzubieten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10920', 'deDE', 'WIRKT DEN ZAUBER, STAGHELM! ICH KANN DIE GLYPHEN DES ZAUBERSCHUTZES NICHT LÄNGER HALTEN! BESCHWÖRT DIE WURZELN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10921', 'deDE', 'Ihr Uralten, leitet meine Hand… Erwacht aus Eurem tiefen Schlaf! ERWACHT UND VERSIEGELT DIESEN VERFLUCHTEN ORT!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10922', 'deDE', '%s fällt völlig erschöpft auf die Knie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10923', 'deDE', 'Es... es ist vorbei, Lord Staghelm. Wir sind siegreich. Doch die Kosten für diesen Sieg waren hoch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10924', 'deDE', 'Es gilt noch eine letzte Pflicht zu erfüllen…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10925', 'deDE', 'Bevor ich diesen Ort verlassen werde, gibt es nur noch eines zu klären, Lord Staghelm. Sollte der Tag kommen, an dem Ihr diese verfluchte Festung erneut betreten müsst, benutzt das Szepter der Sandstürme mit dem heiligen Gong. Die Magie, welche diese Barriere aufrecht erhält, wird sich verflüchtigen und die Schrecken Ahn\'Qirajs werden erneut auf die Welt losgelassen werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10926', 'deDE', '%s übergibt Fandral Staghelm das Szepter der Sandstürme.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10927', 'deDE', 'Nach all\' dem Leid, das mein Volk mit ansehen und erdulden musste, erwartet Ihr von mir, dass ich eine weitere Bürde auf mich nehme, Drache? Ihr müsst wahrlich verrückt sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10928', 'deDE', 'Ich will nichts mit Silithus, den Qiraji oder irgendwelchen verdammten Drachen zu tun haben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10929', 'deDE', '%s schleudert das Szepter der Sandstürme in die Barriere, welche sofort zusammenbricht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10930', 'deDE', 'Der Sand der Zeit verharrt für einen kurzen Moment! Ich werde jetzt die Barriere beschwören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10931', 'deDE', 'Lord Staghelm, wohin wollt Ihr gehen? Ihr würdet unser Bündnis Eurem Stolz opfern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10932', 'deDE', 'Durch diesen bitteren Sieg wird die Seele meines Sohnes keinen Frieden finden, Drache. Ich werde ihn zurückbekommen. Und wenn es tausend Jahre dauert, ich WERDE meinen Sohn zurückbekommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10933', 'deDE', '%s schüttelt voller Enttäuschung seinen Kopf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10934', 'deDE', '%s kniet sich hin, um die Fragmente des zerschlagenen Szepters aufzusammeln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10935', 'deDE', 'Und nun wisst Ihr alles, was es zu wissen gibt, $G Sterblicher:Sterbliche;…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10936', 'deDE', 'Passt auf, $G Freundchen:Mädel;, bei dem Kampf gegen die Käfer aus Ahn\'Qiraj wird es hart auf hart zugehen und wir müssen uns vorbereiten. Die Allianz verfügt momentan nur über einen Vorrat von $2002w Eisenbarren. Wir gehen allerdings davon aus, dass wir für diesem Krieg einen Gesamtbedarf an $2003w Eisenbarren haben werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10937', 'deDE', 'Dank der Hilfe zahlreicher Freiwilliger, verfügen wir jetzt über eine ausreichende Menge an Eisenbarren. Fragt doch mal die anderen, ob sie noch weitere Vorräte für unsere Kriegsanstrengungen benötigen. Was mich betrifft, ich werd\' jetzt erstmal \'ne Runde Karten spielen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10938', 'deDE', null, '$C, bitte hört mir gut zu. Die Horde und die Allianz bereiten sich auf den Krieg mit den Silithiden und ihren Meistern in Ahn\'Qiraj vor. Jegliche Unterstützung, die Ihr leisten könnt, würden wir sehr zu schätzen wissen. Wie Ihr sehen könnt, sammele ich hier Thoriumbarren für die Kriegsanstrengungen. Insgesamt brauchen wir $2012w und haben bisher $2011w.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10939', 'deDE', null, 'Danke der Nachfrage, $C. Wir haben jetzt alle benötigten Thoriumbarren beisammen. Ihr solltet allerdings mal bei den anderen Sammlern nachfragen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10940', 'deDE', 'Es stimmt tatsächlich, selbst Würgetang ist eine wertvolle Ressource für den kommenden Krieg. Es gibt so viele Anwendungsmöglichkeiten für diese Pflanze, zum Beispiel lässt sich daraus ein äußerst schmackhaftes Eis herstellen, genau richtig bei dieser sengenden Hitze in Silithus, oder eine Salbe gegen die Wirkung bestimmter gifter Pflanzen, Tränke zur Unterwasseratmung, zur Härtung Eurer Haut, um Eure Geschwindigkeit zu erhöhen, magische Kräfte zu erneuern,... ach man kann soviel damit anstellen, dass ich mir gar nicht alles merken kann! Hatte ich schon Eiscreme erwähnt?$B$BNaja, egal! Ich habe hier $2047w Pflanzen und brauche insgesamt $2048w!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10941', 'deDE', 'Ich glaube zwar nicht, dass man jemals genug Würgetang sein Eigen nennen kann, geschweige denn Eiscreme, doch ich denke, dass wir langsam über einen ausreichenden Vorrat verfügen. Zumindest wurde mir das so mitgeteilt. Es sieht allerdings so aus, als könnten einige der anderen noch etwas Hilfe beim Sammeln von was auch immer brauchen. Verständlich, wenn man weiß, dass nichts so viel Spaß macht, wie das Einsammeln von Würgetang.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10942', 'deDE', 'Seid gegrüßt, junger $C. Die Allianz bat mich um Hilfe, eine ausreichende Menge lila Lotusse für den kommenden Krieg gegen die Silithiden in Ahn\'Qiraj, zu sammeln. Ich kenne viele Nutzungsmöglichkeiten dieser Pflanze und habe daher Ihrem Gesuch zugestimmt, einen Vorrat von $2055w dieser Pflanzen anzulegen. Bisher ist es uns gelungen $2053w lila Lotusse einzusammeln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10943', 'deDE', 'Es erfüllt mich wahrlich mit Zufriedenheit, Euch mitteilen zu können, dass wir nun über einen ausreichenden Vorrat an lila Lotussen verfügen. Ich bin Euch für Eure Spenden und Eure Mithilfe zu Dank verpflichtet, $C. Solltet Ihr auch weiterhin helfen wollen, viele der anderen Sammler wären für Eure Dienste sicherlich sehr dankbar.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10944', 'deDE', null, 'Wir bereiten uns auf den Krieg vor, $C. Wir werden uns das nicht mehr länger von den Silithiden und ihren Meistern bieten lassen. Um uns vorzubereiten, sammeln wir verschiedene Materialien. Ich wurde damit betraut, Arthas Tränen zu sammeln. Bin mir allerdings nicht sicher, wofür wir die brauchen. Wenn Ihr mich fragt, ich will nichts mit diesem Wahnsinnigen zu tun haben, selbst wenn es hier nur um eine Blume geht, die nach ihm benannt wurde.$B$BAber ich bin dennoch die Tochter meines Vaters und eine Soldatin. Ich tue, was mir befohlen wird. Wir haben momentan $2057w und brauchen insgesamt $2058w.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10945', 'deDE', null, 'Dann wär\'s dann wohl. Wir haben jetzt genug Arthas Tränen beisammen. Aber fragt mal was herum, es könnte sicherlich noch einer der anderen Sammler Eure Hilfe gebrauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10946', 'deDE', 'Ich erhalte regelmäßige Berichte über Eure Aktionen gegen den abyssischen Rat, $N. Ihr leistet dort draußen hervorragende Arbeit!$B$BMacht weiter so und wir werden Euch dafür entlohnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10947', 'deDE', null, 'Der Krieg von Ahn\'Qiraj steht kurz bevor, $C. Um uns darauf vorzubereiten, hat die Allianz meine Familie gebeten, eine Menge leichtes Leder zu beschaffen. Von den benötigten $2061w Stücken, haben wir bisher $2060w. Ihr seht aus, als würdet Ihr helfen wollen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10948', 'deDE', 'Ihr wagt es, Blut auf neutralem Grund zu vergießen? RAUS! RAUS MIT EUCH!', 'Ihr wagt es, Blut auf neutralem Grund zu vergießen? RAUS! RAUS MIT EUCH!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10949', 'deDE', 'Leute wie Euch, behandeln wir nicht sehr gut, $R!', 'Leute wie Euch, behandeln wir nicht sehr gut, $R!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10950', 'deDE', 'Spürt meine Klinge, Bastard!', 'Spürt meine Klinge, Bastard!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10951', 'deDE', 'Bitte sagt mir, dass Ihr nicht das getan habt, von dem ich glaube, dass Ihr es gerade getan habt. Bitte sagt mir, dass ich Euch nicht wehtun muss…', 'Bitte sagt mir, dass Ihr nicht das getan habt, von dem ich glaube, dass Ihr es gerade getan habt. Bitte sagt mir, dass ich Euch nicht wehtun muss…', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10952', 'deDE', 'Ein Seil muss her!', 'Ein Seil muss her!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10953', 'deDE', 'Als wenn wir nicht schon genug Probleme hätten, und Ihr veschafft uns direkt neue!', 'Als wenn wir nicht schon genug Probleme hätten, und Ihr veschafft uns direkt neue!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10954', 'deDE', 'Glaubt mir, wenn ich Euch sage: Ihr werdet Euch wünschen niemals geboren worden zu sein!', 'Glaubt mir, wenn ich Euch sage: Ihr werdet Euch wünschen niemals geboren worden zu sein!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10955', 'deDE', 'Eure Taten beschämen uns alle, $C. Es schmerzt mich zutiefst, Euch bewusstlos prügeln zu müssen.', 'Eure Taten beschämen uns alle, $C. Es schmerzt mich zutiefst, Euch bewusstlos prügeln zu müssen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10956', 'deDE', null, 'Es ist nett von Euch zu fragen, $C, aber wir haben jetzt genug leichte Lederstücke beisammen. Aber vielleicht könnt Ihr ja noch einem der anderen Sammler hier weiterhelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10957', 'deDE', 'Ja, $G mein Herr : meine Dame;, Gefreiter Porter meldet sich zum Dienst und ist bereit, Euch bei allen Fragen rund um das Sammeln von mittlerem Leder zu unterstützen. Der Krieg mit Ahn\'Qiraj steht vor der Tür und wir müssen gut vorbereitet sein. Wie Ihr sicherlich sehen könnt, haben wir bis zum jetzigen Zeitpunkt exakt $2063w Einheiten mittleres Leder eingesammelt, wobei ein Gesamtziel von $2064w Einheiten erreicht werden soll.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10958', 'deDE', 'Natürlich, $G mein Herr : meine Dame;. Es sieht so aus, als hätten wir genügend mittleres Leder für die Allianz gesammelt. Allerdings wurde mir mitgeteilt, dass einige der anderen Sammler ebenfalls Eure Hilfe benötigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10959', 'deDE', null, 'Heda, $C! Jetzt wo der Krieg von Ahn\'Qiraj immer näher rückt, sind unsere Vorbereitungen in vollem Gange. Ich wurde mit der Beschaffung von dickem Leder beauftragt. Ein schöner Tag zum Sammeln von dickem Leder, meint Ihr nicht? Ich habe hier schon einen Stapel, insgesamt haben wir bisher $2069w Stücke. Die magische Zahl ist aber $2071w. Wollt Ihr aushelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10960', 'deDE', null, 'Wow! Sieht so aus, als hätten wir bereits all das dicke Leder, das wir brauchen. Das ging ja schnell. Es gibt hier aber vermutlich noch andere Sammler, die Eure Hilfe gebrauchen könnten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10961', 'deDE', 'Feuer frei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10962', 'deDE', null, 'Hallo, $C. Ich sammele hier Leinenverbände für die Kriegsanstrengungen. Die Kriegsanstrengungen für den Krieg von Ahn\'Qiraj stellen uns alle auf die Probe, werden uns aber auch für den Krieg stärken. Ich kann Euch versichern, dass die Verbände, die Ihr uns spendet, einem guten Zweck dienen werden. Nämlich dem, die Leben von Soldaten zu retten, wenn es soweit ist. Ich brauche insgesamt $2077w und habe bisher $2076w. Werdet Ihr uns weiterhelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10963', 'deDE', null, 'Danke der Nachfrage, $C, aber wie Ihr sehen könnt, habe ich bereits alle Leinenverbände beisammen, die die Allianz für den bevorstehenden Krieg in Ahn\'Qiraj benötigt. Lasst das Euch aber nicht davon abhalten, mit den anderen Sammlungen hier auszuhelfen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10964', 'deDE', 'Hoffentlich habt Ihr einen guten Grund für Eure Störung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10967', 'deDE', 'Bald wird hier die Hölle los sein, Freunde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10968', 'deDE', 'Sieht so aus, als würde dies einer dieser besonderen Tage werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10969', 'deDE', 'Ich hätte mir ein größeres Schwert nehmen sollen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10970', 'deDE', 'Soldaten, bildet eine Schlachtreihe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10971', 'deDE', 'Ein Gefühl von großer Trauer scheint von diesem Kristall auszugehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10972', 'deDE', '<Blick in die Kristallträne>', '<Blick in die Kristallträne>', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10973', 'deDE', 'Irgendetwas Großes nähert sich aus dem Schwarmbau, Mylady. Es bewegt sich direkt auf uns zu!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10974', 'deDE', null, 'Soldat, ruft sofort Captain Blackanvil!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10975', 'deDE', 'Jawohl, Mylady!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10976', 'deDE', 'Captain! Lieutnant Stouthammer erbittet Eure Anwesenheit! Der Feind nähert sich unseren Stellungen, Sir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10977', 'deDE', null, 'Hallo, wie geht es Euch heute, $C? Gut, hoffe ich. Ihr fragt Euch vielleicht, was hier vorgeht.Es wird bald zu einem Krieg in Ahn\'Qiraj kommen und wir müssen planen und uns vorbereiten. Wir und die Horde tun alles, um die Vorbereitungen schnellstmöglich abzuschließen.$B$BDeshalb hat die Allianz mich darum gebeten, hier die Sammlung von Seidenverbänden zu überwachen. Bisher haben wir $2082w der benötigten $2083w beisammen. Könntet Ihr uns vielleicht weiterhelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10978', 'deDE', null, 'Es ist sehr nett von Euch nachzufragen, aber wie es scheint, haben wir bereits sämtliche Seidenverbände beisammen, die wir benötigen. Ihr könntet allerdings mal etwas herumfragen und sehen, ob noch irgendjemand sonst Hilfe benötigt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10979', 'deDE', '$C, früher oder später in diesem Krieg, werden auch die stärksten unserer Krieger und Späher die Hilfe von Heilkunst benötigen. Und wenn keine magische Heilung verfügbar ist, müssen sie sich auf unsere Bemühungen verlassen. Bis zum heutigen Tage haben wir von den angestrebten $2090w Runenstoffverbänden bereits $2088w eingesammelt.$B$BWir sollten uns darüber unterhalten, wie Ihr uns weiterhelfen könnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10980', 'deDE', '$C, dank der Hilfe zahlreicher Freiwilliger, konnten wir einen ausreichenden Vorrat an Runenstoffverbänden anlegen. Damit sollte die Allianz ihren Bedarf während des kommenden Krieges decken können. Fragt doch einen der umstehenden Beauftragten, ob sie nicht noch Eure Hilfe bei einem Sammelauftrag gebrauchen könnten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10982', 'deDE', 'Haltet Euch bereit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10983', 'deDE', 'Hmmm, mmm, mmm. Riecht Ihr das!? Das ist so richtig gutes Essen, $C. Regenbogenthunfische schmecken einfach am besten. Ich bin Slicky Gastronome, der beste Kenner alles Eßbaren und vor allem Fisch. Ein richtiger Gastrognom eben, hehe. Jetzt wo der Krieg von Ahn\'Qiraj kurz bevor steht, hielt die Allianz es für schlau, einen Experten in Sachen Fisch anzuheuern, und hier bin ich und habe alles voll im Griff. Wir brauchen insgesamt $2093w von den Fischen, haben bisher aber nur $2092w.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10984', 'deDE', 'Nun, es sieht ganz so aus, als ob wir genügend gekochte Thunfische zum Pökeln und Lagern für den Krieg von Ahn\'Qiraj eingesammelt haben. Trotzdem danke der Nachfrage, $C. Wenn Ihr noch etwas mithelfen wollt, fragt bitte die anderen. Ich wette, einer von den anderen Sammlern wäre sicherlich dankbar für Eure Hilfe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10988', 'deDE', null, 'Oh, hi, $C. Ich schätze, die Allianz hat mich hierhergebeten, um mich um all das geröstete Fleisch zu kümmern, dass die Allianz für das Grauen von Ahn\'Qiraj zusammensammelt. Klingt das nicht spaßig?$B$BWie auch immer, Ihr wollt vermutlich wissen, wie viel wir schon haben. Als ich das letzte Mal nachgezählt habe, hatten wir $2098w von benötigten $2099w gerösteten Raptoren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10989', 'deDE', null, 'Es scheint, dass Ihr zu spät kommt, $C. Wir haben schon genug gerösteten Raptor beisammen. Getötet, geröstet und gepökelt. Sieht aber so aus, als könnten noch einige der anderen hier Hilfe brauchen. Fragt mal bei denen nach. Ich werd mal sehen, ob ich jetzt nach Hause gehen kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10990', 'deDE', null, 'Gute Ernährung kann einen Soldaten selbst dann noch am Leben erhalten, wenn er normalerweise schon seinen Wunden anheim gefallen sein sollte, $C. Wenn wir im bevorstehenden Krieg von Ahn\'Qiraj bestehen wollen, brauchen wir so viele Provisionen, wie wir tragen können. Ich sammele hier Tüpfelgelbschwänze, bevor sie dann weitertransportiert werden.$B$BBis jetzt haben wir insgesamt $2101w, aber wir müssen noch $2103w weitere zusammenkriegen, wenn wir jeden durchfüttern wollen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10991', 'deDE', null, 'Danke der Nachfrage, $C, aber wir haben jetzt genug Tüpfelgelbschwänze. Es könnte aber noch andere Sammler geben, die immer noch weitere Vorräte oder Materialien brauchen. Fragt mal bei ihnen nach und möge die Göttin auf Euch herabscheinen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10995', 'deDE', 'Hallo, $C. Ich denke, ich werde Euch nicht erst über den Ernst der Situation aufklären müssen. Wir müssen eine große Menge der verschiedensten Waren beschaffen, sie zu brauchbarem Kriegsmaterial verarbeiten und anschließend nach Burg Cenarius schaffen. Diese Aufgabe wird alles andere als leicht.$B$BErst kürzlich haben wir im Inneren des geschmolzenen Kerns sogenannte sengende Kernsteine entdeckt. Unsere Schmiede und Ingenieure sind der Meinung, dass sie in unserer Kriegsgüterproduktion sinnvoll eingesetzt werden könnten, also hat man mich mit ihrer Einlagerung beauftragt. Von $2110w benötigten sengenden Kernen konnten wir bisher $2108w zusammentragen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10996', 'deDE', 'Dank der vereinten Kräfte aller Abenteurergilden der Allianz ist es uns gelungen, eine mehr als ausreichende Menge an sengenden Kernsteinen zu sammeln. Dennoch wäre es wunderbar, wenn Ihr den anderen Sammlern beim Anlegen ihrer Vorräte helfen könntet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10997', 'deDE', 'Wir benötigen eine große Anzahl Kupferbarren, $C. Für den kommenden Krieg in Ahn\'Qiraj werden wir eine Menge Metall für Waffen, Rüstungen und was weiß ich noch brauchen. Man hat mich davon in Kenntnis gesetzt, dass alleine die Horde einen Kupferbarrenbedarf von nicht weniger als $1998w Stück hat. Davon konnten wir bereits $2018w einsammeln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('10998', 'deDE', 'Sofern die letzten Zählungen stimmen, verfügen wir jetzt über die nötige Anzahl an Kupferbarren für diesen Krieg. An Eurer Stelle, würde ich die anderen Sammler bei Ihren Aufgaben unterstützen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11001', 'deDE', 'Throm\'ka $C. Seid Ihr gekommen, um mir mit den Zinnbarren weiterzuhelfen? Man sagte mir, dass wir $2006w Barren benötigen, um daraus Waffen und Rüstungen für den Krieg in Ahn\'Qiraj zu fertigen. Leider konnte ich bisher erst $2005w Zinnbarren einsammeln. Ihr werdet losziehen und mir weitere Barren bringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11002', 'deDE', 'Bitte keine weiteren Zinnbarren mehr. Selbst meine Arme schmerzen schon von dem ganzen Barrenstapeln. Wir haben soweit alles was wir brauchen. Fragt doch die anderen, ob sie noch Hilfe beim Sammeln benötigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11005', 'deDE', null, 'Grüße, $C. Die Eisenschmiedebrigade wurde hier stationiert, um den Bau des Zoraschwarms im Auge zu behalten. Die Druiden des Zirkels des Cenarius haben diese Gegend überwacht und erwarten hier etwas Bedeutendes.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11006', 'deDE', 'Ich bin hier, um den Captain zu unterstützen.', 'Ich bin hier, um den Captain zu unterstützen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11007', 'deDE', null, 'Der Captain ist momentan beschäftigt. Er studiert gerade Karten der Silithidenbäue, die wir vor Kurzem erhalten haben. Bleibt einen Moment hier, wenn Ihr helfen wollt. Er wird sicherlich bald herauskommen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11008', 'deDE', '%s wurde durch Elunes Kerze betäubt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11009', 'deDE', 'Töte $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11010', 'deDE', null, 'Hey, Mann, Ihr seid bestimmt hier, um mir Mithrilbarren zu bringen. Es braut sich \'ne Menge Zoff in Ahn\'Qiraj in der Silithuswüste zusammen. Wir und die Allianz tun uns diesmal zusammen, um diese Käferplage aufzuhalten, deshalb seht Ihr hier so viele verschiedene Leute.$B$BDie Horde hat mich gefragt, $2009w Mithrilbarren zu sammeln, aber ich hab nur $2008w. Also helft Ihr mir jetzt, oder was?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11011', 'deDE', null, 'Wir ham zwar schon die ganzen Mithrilbarren zusammen, die wir für den Krieg brauchen, aber es gibt immer noch genug Leute, die Eure Hilfe brauchen könnten. Danke, Mann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11012', 'deDE', 'Hier kommen sie. Bleibt am Leben, Welpen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11013', 'deDE', null, 'Hallo, junger $C. Seid Ihr hier, um mir Friedensblumen zu bringen? Wir sammeln die, um daraus Tränke und Elixiere für die Kriegsanstrengungen zu brauen. Es ist zwar tragisch, dass wir zu diesem Krieg gezwungen werden, aber die Resultate werden umso tragischer sein, wenn wir nicht gut vorbereitet sind.$B$BDie Horde hat mich damit beauftragt, $2020w Friedensblumen zusammenzusammeln. Bisher haben wir $2021w. Wenn Ihr dazu befähigt seid, bringt mir bitte so viele wie möglich.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11014', 'deDE', null, 'Es ist gut, dass Ihr aushelfen wollt, $C. Leider habe ich schon all die Friedensblumen beisammen, die für den Krieg benötigt werden.Fragt doch mal etwas herum. Ich bin mir sicher, dass die anderen Sammler hier noch Hilfe gebrauchen könnten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11015', 'deDE', 'Ihr wollt mir schaden, $R?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11016', 'deDE', 'Ich bin ein Schatzsucher und auf der Suche nach mächtigen Artefakten. Gebt sie mir und Euch wird nichts geschehen.', 'Ich bin ein Schatzsucher und auf der Suche nach mächtigen Artefakten. Gebt sie mir und Euch wird nichts geschehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11017', 'deDE', 'Ihr wagt es, den blauen Drachenschwarm herauszufordern? STERBT, Ungeziefer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11018', 'deDE', '%s schlägt zurück.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11022', 'deDE', 'Meine Wache ist unendlich, $G Sterblicher:Sterbliche;.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11025', 'deDE', '<Andorov schaut Euch grimmig an.>$B$BWenn es blutet, kann man es auch töten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11026', 'deDE', 'Finden wir\'s raus.', 'Finden wir\'s raus!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11027', 'deDE', 'Eure Welt soll ein unbarmherziges Ende erleiden. Der Alptraum erwartet Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11028', 'deDE', 'Erinnerst du dich daran, Rajaxx, wann ich dir das letzte Mal sagte, ich würde dich töten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11029', 'deDE', 'Das Ende rückt zusehends näher! Ergebt Euch Eranikus gegenüber und undendliches Leiden wird Euch erspart bleiben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11030', 'deDE', 'Erbärmliche, berechenbare Sterbliche... Ihr wisst nicht, was Ihr getan habt! Der Wunsch des Meisters hat sich erfüllt. Moonglade wird zerstört werden und Malfurion gleich mit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11031', 'deDE', 'Flieht, mickrige Wesen. Ergreift die Flucht und sagt Euren Meistern, dass Eranikus gekommen ist, um Eure Welt zu zerstören!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11032', 'deDE', 'Zuerst töten, dann Fragen stellen... Hier kommen sie!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11033', 'deDE', 'Ihr sucht nach dem Szeptersplitter, welcher der Obhut des blauen Drachenschwarms anvertraut wurde?$B$B<Ein Lächeln zeichnet sich in Azuregos Gesichtszügen ab>.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11034', 'deDE', 'Woher wusstet Ihr das? Ich meine, ja… ja, ich bin auf der Suche nach dem Splitter. Habt Ihr ihn?', 'Woher wusstet Ihr das? Ich meine, ja… ja, ich bin auf der Suche nach dem Splitter. Habt Ihr ihn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11035', 'deDE', 'Natürlich trage ich den Splitter nicht bei mir, ich bin doch nicht verrückt. Ich wandle hier zwischen Wilden, in diesem verfluchten Land! In dieser Gegend kann ich keinen Schritt gehen, ohne das nicht irgendein durchgedrehter Mensch oder Orc versuchen würde, mir einen scharfen Gegenstand ins Fleisch zu bohren. Ehe Ihr Euch verseht, würdet Ihr nur noch mit meinem Geist sprechen.$B$BWie auch immer... ich habe den Splitter versteckt.$B$BFahrt fort... fragt mich wo.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11036', 'deDE', 'Aha. Wo?', 'Aha. Wo?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11037', 'deDE', 'Oh, diese Geschichte werdet Ihr lieben, $R. Also, das war so: Als mich Malygos mit dem Schutz des Szeptersplitters beauftragte, wusste ich, dass diese Aufgabe mich eine Ewigkeit beschäftigen und mir nächtelang Kopfschmerzen bereiten würde.$B$BDabei wollte ich viel lieber diese magiereichen Küstengebiete erforschen und all die Artefakte einsammeln, die die Teilung der Welt überlebt haben. Wusstet Ihr, dass es in dieser Gegend einst eine florierende Zivilisation der Hochgeborenen gab? Wir beide wissen, dass auf diesem Landstrich mehr als ein wertvolles magisches Artefakt zu finden ist, $R. Ich kann sie riechen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11038', 'deDE', 'Bei Bronzebeards... hm, Bart! Was erzählt Ihr da?', 'Bei Bronzebeards... hm, Bart! Was erzählt Ihr da?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11039', 'deDE', 'Wie ich es Euch schon sagte, habe ich gut und gerne fünfhundert Jahre lang über den Szeptersplitter gewacht. In der ganzen Zeit, hat er mir nichts als Ärger bereitet. Dieser Splitter zieht die Aufmerksamkeit aller auf sich, die falsche Art von Aufmerksamkeit. Dabei will ich mich nur meinen Studien widmen, ohne diese lästige Aufpasserei auf diesen Möchtegern-Heldenfänger, bei dem alle glauben, es wäre der schnelle Weg zum Ruhm. Naja... dann hab\' ich ihn halt dem Fisch gegeben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11040', 'deDE', 'Ein Fisch? Zur Hölle, Ihr habt den Schlüssel zur Errettung allen Lebens in Kalimdor einem Fisch überlassen?', 'Ein Fisch? Zur Hölle, Ihr habt den Schlüssel zur Errettung allen Lebens in Kalimdor einem Fisch überlassen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11041', 'deDE', 'Nicht bloß ein Fisch, Sterblicher. Dieser Fisch war eine Sardine, und nicht irgendeine Sardine, sondern eine ganz besondere!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11042', 'deDE', 'Eine Sardine? Das Meer ist voll von Sardinen. Da draußen gibt es bestimmt etliche Millionen von diesen Viechern!', 'Eine Sardine? Das Meer ist voll von Sardinen. Da draußen gibt es bestimmt etliche Millionen von diesen Viechern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11043', 'deDE', 'Genau!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11044', 'deDE', '...', '...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11045', 'deDE', 'Genial, ich weiß... Niemand wird den Splitter je finden können. Aber falls doch, habe ich die Sardine mit einigen speziellen Kräften ausgestattet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11046', 'deDE', 'Ihr habt den Splitter einer Sardine überlassen und diese im Meer zwischen hier und dem östlichen Königreich ausgesetzt? Und Sie verfügt über besondere Kräfte?', 'Ihr habt den Splitter einer Sardine überlassen und diese im Meer zwischen hier und dem östlichen Königreich ausgesetzt? Und die Sardine verfügt über besondere Kräfte?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11047', 'deDE', 'In der Tat. Wie mir scheint, habt Ihr gut zugehört! Mit dieser Sardine habe ich eine wahrhaft schreckliche Kreatur erschaffen. Manchmal, wenn ich döse, denke ich an all die unglücklichen Fischer, die sie fangen wollten, nur um dann von ihr verschlungen zu werden. Ich vermute, dass sie darauf wohl kaum vorbereitet waren!$B$B<Azuregos lacht>', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11048', 'deDE', 'Ihr seid wahnsinnig!', 'Ihr seid wahnsinnig!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11049', 'deDE', 'Wahre Genialität wird oft missverstanden, $R! Nun, zurück zum Szeptersplitter… wenn Ihr diesen Splitter tatsächlich wiederfinden wollt, ohne dabei die nächsten zehntausend Jahre mit Suchen zu verbringen, würde ich an Eurer Stelle genau zuhören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11050', 'deDE', 'Ich bin ganz Ohr.', 'Ich bin ganz Ohr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11051', 'deDE', 'Nur ein Wort: Arkanitboje!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11052', 'deDE', 'Wie bitte?', 'Wie bitte?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11053', 'deDE', 'Nun gut… Ihr müsst eine Arkanitboje konstruieren und sie im Meer aussetzen. Die von der Boje ausgestrahlten magischen Energien sollten meine Sardine anziehen. Und wenn sie sich der Boje nähert, KAWUMM! Die Boje wird in einem riesigen Ausbruch arkaner Energien explodieren und die wahre Form der Sardine offenbaren. Im schlimmsten Fall zieht Ihr dabei den Zorn Neptulons auf Euch. Eine 50:50-Chance, würde ich sagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11054', 'deDE', 'Ok, machen wir\'s kurz. Ihr habt tatsächlich den Szeptersplitter, welcher Euch von Anachronos persönlich anvertraut wurde, an eine Sardine verfüttert, und nun erwartet Ihr allen Ernstes von mir, dass ich eine Arkanitboje oder so konstruiere, um Eure kleine Sardine aus ihrem Versteck zu locken? UND mit viel Glück, ziehe ich dabei noch den Zorn eines Elementarfürsten auf mich? Hab\' ich irgendetwas verpasst? Ich meine, vielleicht muss ich diese ganze Sache ja nackt durchziehen, während einer Sonnenfinsternis, natürlich nur in einem Schaltjahr?', 'Ok, machen wir\'s kurz. Ihr habt tatsächlich den Szeptersplitter, welcher Euch von Anachronos persönlich anvertraut wurde, an eine Sardine verfüttert, und nun erwartet Ihr allen Ernstes von mir, dass ich eine Arkanitboje oder so konstruiere, um Eure kleine Sardine aus ihrem Versteck zu locken? UND mit viel Glück, ziehe ich dabei noch den Zorn eines Elementarfürsten auf mich? Hab\' ich irgendetwas verpasst? Ich meine, vielleicht muss ich diese ganze Sache ja nackt durchziehen, während einer Sonnenfinsternis, natürlich nur in einem Schaltjahr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11055', 'deDE', 'General Rajaxx wurde vernichtend geschlagen. Ein weiterer Sieg für Andorov!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11056', 'deDE', 'Wenn ich es nicht besser wüsste, Sterblicher, würde ich annehmen, dass Ihr mich verspottet. Im Großen und Ganzen habt Ihr verstanden, wie es scheint. Wenn es Euch Recht ist, könntet Ihr Eure Kleidung ruhig anbehalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11057', 'deDE', 'VON MIR AUS! Und wo, wenn ich fragen darf, soll ich eine Arkanitboje auftreiben?', 'VON MIR AUS! Und wo, wenn ich fragen darf, soll ich eine Arkanitboje auftreiben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11058', 'deDE', 'Nehmt diese Aufzeichnungen und bringt sie zu meinem alten Bekannten in Tanaris. Sein Name ist Narain Soothfancy, er ist zwar extrem verrückt, aber dafür auch einer der genialsten Ingenieure seiner Zeit. Er sollte sich einen Reim auf all das machen können.$B$BNichts zu danken, $N. Das ist das Mindeste, was ich für Euch tun kann.$B$BGuten Tag!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11059', 'deDE', 'Aber...', 'Aber...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11060', 'deDE', 'ICH SAGTE, GUTEN TAG!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11061', 'deDE', null, 'Achtet auf Eure Schritte, $G Sterblicher : Sterbliche;. Ihr schreitet über Boden, auf dem das Schicksal weit mächtigerer Wesen besiegelt wurde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11062', 'deDE', 'Vor tausend Jahren, zur Zeit, als der Krieg der Sandstürme sein Ende fand, opferten sich drei unserer Art, um die Qiraji in ihren Hallen festzuhalten, während Anachronos und Staghelm Ahn\'Qiraj versiegelten.$B$BEin jeder von uns schwor, nach Merithra, Arygos und Caelestrasz zu suchen, sollte der Skarabäuswall jemals fallen.$B$BNun ist es soweit... und so kamen wir hierher.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11063', 'deDE', 'Seid Ihr nicht damit beschäftigt, die Welt zu retten?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11064', 'deDE', '<Narain hält einen ungeöffneten Umschlag an seine Stirn.>$B$BWas ist… Mmmm… Samt!$B$BHabe ich Recht?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11065', 'deDE', 'Sagt nichts! Schhh! Ihr seid wegen dem Aschenbringer hier?!? Ja... Ja... Nein! Zu Schade.$B$BWomit kann ich Euch behilflich sein, $R?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11066', 'deDE', 'Narain Soothfancy, zu Euren Diensten! Ehemaliger Ingenieur und Meisterwahrsager! Ich kann Euch Eure Zukunft schon voraussagen, bevor sie überhaupt passiert!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11067', 'deDE', 'Ihr langweilt mich, $N!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11074', 'deDE', '%s behält $N im Blickfeld!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11075', 'deDE', 'He he he, wie geht\'s Euch? Ich habe so den Eindruck, als wolltet Ihr Pele\'keiki einen großen Gefallen tun. Wir benötigen eine riesige Menge an Feuerblüten für den Krieg von Ahn\'Qiraj. Die Horde hat zu mir gesagt: \"Pele\'keiki, beschaffe uns $2051w Feuerblüten und das möglichst schnell.\" Leider konnte Pele\'keiki erst $2050w Stück auftreiben. Sicher werdet Ihr mir weiterhelfen, oder?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11076', 'deDE', 'Pele\'keiki kann es selbst kaum glauben, aber wir haben alle Feuerblüten, die die Horde für diesen Krieg brauchen wird, eingesammelt. Vielleicht könnt Ihr ja die anderen Sammler unterstützen. Es gibt sicher noch vieles, das Ihr für sie sammeln könntet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11077', 'deDE', null, 'Ah, da seid Ihr ja. Mir wurde schon mitgeteilt, dass ich Hilfe bekommen würde, also hört gut zu. Die Situation ist folgende: Ich wurde damit beauftragt, $2055w lila Lotusse für den bevorstehenden Krieg von Ahn\'Qiraj zu sammeln. Bisher haben wir $2054w.$B$BDie Aufgabe, in die Wildnis zu ziehen und diese Planzen zu beschaffen, fällt Euch und anderen wie Euch zu. Ich werde hier gebraucht, um dafür zu sorgen, dass unsere Bestände gut geplegt werden und um mir neue und kreative Anwendungsmöglichkeiten für sie gegen die Silithiden, oder was auch immer uns sonst da draußen erwartet, auszudenken.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11078', 'deDE', null, 'Wir haben jetzt alle benötigten lila Lotusse beisammen, $C. Frage einen der anderen, ob er Eure Hilfe benötigt, und lasst mich meinen Studien nachgehen. Es gibt keine Zeit zu verlieren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11079', 'deDE', null, 'Ihr seid also hier, um mit der Beschaffung von schwerem Leder für den Krieg von Ahn\'Qiraj zu helfen, $C? Sehr gut. Wie Ihr sehen könnt, haben wir bisher $2066w schwere Lederstücke beisammen. Aber wir brauchen noch weitere $2067w. Das bedeutet eine Menge Kürschnerei, mein Freund. Ich hoffe, Euer Kürschnermesser ist scharf. Das Leder ist für alle möglichen Kriegsmaterialien nötig, mit deren Hilfe wir sicherlich siegen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11080', 'deDE', null, 'Das is\' echt nett von Euch, Mann, aber wir hab\'n schon genug schweres Leder beisammen. Euer Messer is\' aber bestimmt immer noch scharf, oder? Vielleicht könnt Ihr es ja benutzen, um den anderen Sammlern hier weiterzuhelfen, oder nicht? Macht\'s gut!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11081', 'deDE', null, 'Etwas unglaublich Böses hat sich hier niedergelassen, $G Sterblicher:Sterbliche;. C\'thun und die, die ihn verehren, sind nicht nur ein Affront gegen die Natur, nein, ihre bloße Existenz kündet von der Zerstörung Azeroths wie wir es kennen.$B$BMerithra, Caelestrasz und Arygos opferten sich vor tausend Jahren, weil sie die Wahrheit kannten. Unser Schwur, nach ihnen zu suchen, hat uns hierher geführt. Wir sind auch gekommen, um C\'thun aufzuhalten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11082', 'deDE', null, 'Lok\'tar, $C, ich habe wichtige Neuigkeiten! Es wird bald zu einem Krieg in Ahn\'Qiraj kommen und wir müssen vorbereitet sein. Ich sammele all das dicke Leder ein, das ich kriegen kann. Bis jetzt haben wir $2070w Stücke. Der Kriegshäuptling hat mich allerdings beauftragt, $2071w Stücke einzusammeln. Wie Ihr sehen könnt, brauche ich noch einige mehr.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11083', 'deDE', null, 'Wenn Ihr noch dickes Leder übrig habt, kommt Ihr leider zu spät. Wir haben schon genug beisammen. Fragt aber noch mal bei den anderen Sammlern nach, bevor Ihr geht. Es gibt bestimmt noch jemanden, der Eure Hilfe gebrauchen könnte.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11084', 'deDE', 'Hallo $C. Ich hoffe doch, Ihr habt gute Neuigkeiten was die Kriegsvorbereitungen betrifft. Ich selbst bin mit dem Sammeln von unverwüstlichem Leder für die Horde beauftragt worden, eine Aufgabe, für die ich auf einzigartige Art und Weise qualifiziert bin. Unterschätzt niemals den Nutzen einer scharfen Klinge.$B$BWas das unverwüstliche Leder betrifft, von den benötigten $2074w Einheiten konnten wir bisher $2073w Einheiten auftreiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11085', 'deDE', 'Ich wette, Ihr habt bereits Eure Klinge geschärft, um mir mehr Tierhäute zu bringen. Was für eine Schande! Leider, haben wir bereits die notwendige Menge an unverwüstlichem Leder beisammen, $C. Doch zögert nicht, und fragt die anderen hier, ob sie nicht einen weiteren Sammelauftrag für Euch haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11086', 'deDE', null, 'Für mich war der Grund des Ozeans immer ein sehr friedlicher Ort.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11087', 'deDE', null, 'Es ist wunderbar, Euch wiederzusehen, $N! Ich kann Euch gar nicht genug dafür danken, dass ihr meinem lieben, kleinen Gnom diese Nachricht überbracht habt.$B$BWürdet Ihr gerne ein Lied hören? Es wird Euch die Reise durch die wogenden Wellen erleichtern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11088', 'deDE', 'Das wäre wundervoll! Vielen Dank, Meridith.', 'Das wäre wundervoll! Vielen Dank, Meridith.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11089', 'deDE', null, 'Ein schönes Lied, nicht wahr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11090', 'deDE', 'Ah, $C, schön Euch zu sehen. Wir könnten etwas Hilfe beim Zusammentragen der Vorräte für den Krieg in Ahn\'Qiraj gebrauchen. Ich wurde beauftragt, soviele Sanitätskästen wie möglich zusammenzustellen. Zu diesem Zweck werde ich eine ganze Menge Wollverbände brauchen. Momentan konnte ich bereits $2079w Stück auftreiben, allerdings brauche ich insgesamt $2080w Wollverbände.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11091', 'deDE', 'Danke der Nachfrage, aber ich habe bereits sämtliche, für die Kriegsvorbereitungen notwendigen, Wollverbände zusammengetragen. Ich würde vorschlagen, dass Ihr Euch ein wenig umhört. Soweit ich weiß, brauchen die anderen Sammler ebenfalls Hilfe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11092', 'deDE', null, 'Hallo, $C, wie nett von Euch, vorbeizuschauen. Erlaubt mir, es auf den Punkt zu bringen. Wir sammeln hier Vorräte für den Krieg von Ahn\'Qiraj. Wir wollen schließlich nicht mehr Verluste erleiden als nötig, meint Ihr nicht? Ich bin dafür verantwortlich, die Sammlung von $2086w Magiestoffverbänden zu koordinieren. Bisher haben wir $2085w beisammen. Wollt Ihr aushelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11093', 'deDE', null, 'Wie bedauerlich, es scheint, als hätten wir schon sämtliche Magiestoffverbände, die die Allianz für den Krieg benötigt. Vielleicht könnt Ihr sie ja im Auktionshaus verkaufen? Bevor Ihr das macht, solltet Ihr allerdings mal mit den anderen Sammlern reden. Die könnten vielleicht noch Eure Hilfe gebrauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11094', 'deDE', 'Ich werde alle meine Geheimnisse mit ins Grab nehmen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11095', 'deDE', null, 'Die Erdenmutter versorgt uns mit fast allem, was wir benötigen. Manchmal brauchen wir allerdings etwas zusätzliche Hilfe, um zu überleben, $C. Deshalb sammle ich hier so viele Runenstoffverbände wie möglich, als Vorbereitung auf den Krieg von Ahn\'Qiraj. Bisher haben wir $2089w beisammen. Wir brauchen allerdings insgesamt $2090w.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11096', 'deDE', null, 'Es ist sehr nett von Euch, uns nach unseren Bedürfnissen zu fragen, $C. Wie es aussieht, haben wir bereits all die Runenstoffverbände beisammen, die wir glauben, für den Krieg zu benötigen. Dürfte ich Euch darum bitten, mal bei den anderen Sammlern nachzufragen und zu sehen, ob Ihr denen eventuell weiterhelfen könnt? Mögen Eure Vorfahren über Euch wachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11097', 'deDE', null, 'Grrr, was wollt Ihr denn, $R? Oh, Ihr müsst wegen der mageren Wolfsteaks für den Krieg von Ahn\'Qiraj hier sein. Ja, Ihr habt richtig gehört, ich sammele die hier, und? Habt Ihr einen frischen Stapel für mich? Ich brauche nur $2096w und bisher haben wir $2095w.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11098', 'deDE', null, 'Wir haben jetzt anscheinend so viele magere Wolfsteaks beisammen, wie die Horde angefordert hat. Mir wurde aufgetragen hier alles zusammenzupacken und es für den Transport nach Burg Cenarius in Silithus vorzubereiten. Einige der anderen hier sammeln aber eventuell immer noch verschiedene Dinge. Geht und nervt die.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11099', 'deDE', 'Ja, Mann, ich sammel hier die Fischlein für die Horde. Die bereiten sich voll auf den Krieg in der Wüste bei Ahn\'Qiraj vor und ham mich gefragt, $2103w Tüpfelgelbschwänze zu besorgen. Aber bis jetzt hamma nur $2102w. Seid Ihr hier, um zu helfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11100', 'deDE', 'Hallo, $C. Sieht ganz so aus, als hätten wir genügend Tüpfelgelbschwänze für den kommenden Krieg. Wenn Ihr genug Zeit habt, geht und helft doch den anderen Sammlern, die hier \'rum stehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11101', 'deDE', 'Seid gegrüßt, $C. Es freut mich zu sehen, dass Ihr uns bei den Kriegsvorbereitungen zur Hand gehen wollt. All die Sammler hier sind für die Beschaffung der unterschiedlichsten Waren verantwortlich. Ich zum Beispiel bin beauftragt worden, $2106w gebackene Lachse für unsere Truppen in Ahn\'Qiraj einzulagern. Nach meiner letzten Zählung zu urteilen, konnten wir bereits $2105w Stück beschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11102', 'deDE', 'Gut, dass Ihr fragt, $C. Wie es aussieht, haben wir die für den Krieg notwendige Anzahl an gebackenen Lachsen erreicht. Seid doch so gütig und sprecht mit einem der anderen Sammler, sie können sicherlich Eure Hilfe gebrauchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11103', 'deDE', 'Ich habe keine Zeit zu verlieren, $C, es gilt sich auf einen Krieg vorzubereiten. Solltet Ihr uns helfen wollen: Wir sammeln sengende Kernsteine aus dem geschmolzenen Kern. Von $2110w benötigten sengenden Kernsteinen konnten wir bereits $2109w beschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11104', 'deDE', 'Ah, $C! Sämtliche Gilden der Horde haben sich zusammengetan und ihre Aufgabe mit Bravour abgeschlossen. Wir haben nun genug sengende Kernsteine, um ausreichend Ausrüstung für den Krieg herzustellen. Macht Euch doch noch ein wenig nützlich und fragt die anderen Sammler, ob sie nicht noch Eure Hilfe brauchen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11105', 'deDE', 'Die Zeit ist gekommen, nun da das neue Jahr angebrochen ist und der Mond hell scheint.$B$BEs ist nun unsere Zeit... wenn die Alten erwachen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11106', 'deDE', 'Kein Hallo für Euren alten Freund, Narain? Wen habt Ihr denn erwartet???', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11107', 'deDE', '%s beäugt Euch misstrauisch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11108', 'deDE', 'So... Ihr habt also gedacht, Ihr könntet mich reinlegen, wie? Mich, das größte kriminelle Genie von ganz Azeroth???', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11109', 'deDE', 'Eure Tarnung täuscht mich nicht, $R! Nummer Zwei! Nummer Zwei, töte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11110', 'deDE', 'TÖTE!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11111', 'deDE', 'In längst vergangenen Tagen lebten wir für die Jagd. Wir jagten nach Ruhm, nach Ehre…$B$BIst jetzt alles anders?', 'In längst vergangenen Tagen lebten wir für die Jagd. Wir jagten nach Ruhm, nach Ehre…$B$BIst jetzt alles anders?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11112', 'deDE', null, 'Hunde des Zirkels des Cenarius. Schlachtet sie all ab!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11113', 'deDE', 'Eure Bemühungen Drachisch zu erlernen sind sinnlos, Sterblicher. Und weshalb strebt Ihr überhaubt danach, diese uralte Sprache zu erlernen? In der Tat verwunderlich…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11114', 'deDE', '<Nimm dieses Buch und benutze es zum Besten von Azeroth!>', '<Nehmt dieses Buch zum Wohle von Azeroth!>', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11115', 'deDE', '%s wurde verschlungen!', '%s wurde verschlungen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11116', 'deDE', 'Alles ist in Nebel gehüllt, $N. Ich kann fast nichts erkennen! Moment... jetzt wird es klarer. Ich sehe Doktor Weavils Versteck. Ich glaube... JA! Er ist im Besitz eines der Buchkapitel!$B$BLasst mich sehen, ob ich nicht noch etwas weiter weggehen kann, um einen besseren Überblick zu bekommen.$B$B<Narain beginnt zu schielen.>$B$BDie Insel Alcaz! Dieser teuflische Bastard ist auf der Insel Alcaz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11117', 'deDE', 'Böse zu sein, macht mich so müde...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11118', 'deDE', '%s gähnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11119', 'deDE', 'Lok\'tar $R. Ich bin General Zog, Abgesandter der Horde. Ich werde Euch über die Anstrengungen der mächtigen Horde, was den Krieg von Ahn\'Qiraj angeht, auf dem Laufenden halten. Wie kann ich Euch weiterhelfen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11120', 'deDE', 'Worum geht es bei den Kriegsanstrengungen in Ahn\'Qiraj?', 'Worum geht es bei den Kriegsanstrengungen in Ahn\'Qiraj?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11121', 'deDE', 'Diese Zeile ist hauptsächlich Füllmaterial...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11122', 'deDE', 'Wie viele Metallbarren hat die Horde bisher gesammelt?', 'Wie viele Metallbarren hat die Horde bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11123', 'deDE', 'Wieviele Metallbarren, fragt Ihr? Meinen letzten Informationen zur Folge, sieht die Lage wie folgt aus: Von $1998w benötigten Kupferbarren wurden bisher $2018w zusammengetragen, von $2006w Zinnbarren wurden $2005w eingesammelt und von $2009w Mithrilbarren wurden bis jetzt $2008w gesammelt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11124', 'deDE', 'Wie viele Pflanzen hat die Horde bisher gesammelt?', 'Wie viele Pflanzen hat die Horde bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11125', 'deDE', 'Das Letzte, was ich gehört habe ist, dass die Horde $2021w von $2020w Friedensblumen, $2050w von $2051w Feuerblüten und $2054w von $2055w lila Lotuspflanzen angesammelt hat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11126', 'deDE', 'Wie viele Einheiten Leder hat die Horde bisher gesammelt?', 'Wie viele Einheiten Leder hat die Horde bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11127', 'deDE', 'Die Ledervorräte? Ich habe gerade die neuesten Zahlen erhalten. Es sieht so aus, als ob die Horde bisher $2066w Einheiten schweres Leder von insgesamt von $2067w erforderlichen Einheiten eingesammelt hat. Was dickes Leder betrifft, wurden $2070w Einheiten von $2071w benötigten Einheiten gesammelt. Außerdem verfügen wir über $2073w Einheiten unverwüstliches Leder, wobei wir insgesamt $2074w Einheiten davon benötigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11128', 'deDE', 'Wie viele Verbände hat die Horde bisher gesammelt?', 'Wie viele Verbände hat die Horde bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11129', 'deDE', 'Die Horde stellt momentan einen großen Verbandsvorrat zusammen. Bisher wurden $2079w Wollverbände von $2080w Verbänden eingesammelt. Wir verfügen über $2085w von $2086w benötigten Magiestoffverbänden. Es wurden $2089w Runenstoffverbände von $2090w erforderlichen Verbänden hergestellt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11130', 'deDE', 'Wie viel zubereitetes Essen hat die Horde bisher gesammelt?', 'Wie viel zubereitetes Essen hat die Horde bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11131', 'deDE', 'Ihr wollt mehr über die Rationssammlung erfahren? Kein Problem! Nach meinen aktuellsten Informationen, hat die Horde $2095w von $2096w mageren Wolfsteaks zur Verarbeitung vorbereitet. Zusätzlich konnten wir bis jetzt $2102w von $2103w Tüpfelgelbschwänzen und $2105w von $2106w gebackene Lachse einlagern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11132', 'deDE', 'Ich möchte Euch noch etwas anderes fragen.', 'Ich möchte Euch noch etwas anderes fragen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11133', 'deDE', 'Seid gegrüßt, $C. Ich bin Kommandant Stronghammer, der Abgesandte und Repräsentant der Allianz. Wenn Ihr irgendwelche Fragen über den Stand der Sammelbemühungen der Allianz hinsichtlich des Krieges von Ahn\'Qiraj habt, $G Junge:Mädel;, zögert nicht mich zu fragen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11134', 'deDE', 'Das hier ist hauptsächlich Füllmaterial...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11135', 'deDE', 'Wie viele Metallbarren hat die Allianz bisher gesammelt?', 'Wie viele Metallbarren hat die Allianz bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11136', 'deDE', 'Eine sehr gute Frage, $C. Soweit ich informiert bin, haben die Schmiede der Allianz bereits $1997w von $1998w Kupferbarren, $2002w von $2003w Eisenbarren und $2011w von $2012w Thoriumbarren im Rahmen der Kriegsbemühungen erhalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11137', 'deDE', 'Wie viele Pflanzen hat die Allianz bisher gesammelt?', 'Wie viele Pflanzen hat die Allianz bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11138', 'deDE', 'Lasst mich sehen, lasst mich sehen. Wo habe ich die Listen denn nur wieder hingetan? Ah ja, hier sind sie ja! Bis heute sammelte die Allianz $2047w von $2048w benötigten Würgetangpflanzen. Laut den anderen Berichten, verfügen wir bereits über $2053w von $2055w lila Lotuspflanzen und über $2057w von $2058w Arthas\' Tränen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11139', 'deDE', 'Wie viele Einheiten Leder hat die Allianz bisher gesammelt?', 'Wie viele Einheiten Leder hat die Allianz bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11140', 'deDE', 'Was für ein Zufall, dass Ihr gerade fragt… Ich habe erst kürzlich die neuesten Berichte erhalten. Die Allianz verfügt momentan über $2060w von $2061w benötigten Einheiten leichtes Leder, $2063w von $2064w Einheiten mittleres Leder und $2069w von $2071w Einheiten dickes Leder.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11141', 'deDE', 'Wie viele Verbände hat die Allianz bisher gesammelt?', 'Wie viele Verbände hat die Allianz bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11142', 'deDE', 'Verbände, sagt Ihr? Nun ja, wenn ich mich recht erinnere, hat man mir zuletzt gesagt, dass die Allianz bisher $2076w von $2077w Leinenverbänden, $2082w von $2083w Seidenverbänden und $2088w von $2090w Runenstoffverbänden hergestellt hat.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11143', 'deDE', 'Wie viel zubereitetes Essen hat die Allianz bisher gesammelt?', 'Wie viel zubereitetes Essen hat die Allianz bisher gesammelt?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11144', 'deDE', 'Das erinnert mich daran, dass ich mir langsam mal etwas zum Essen suchen sollte. Also, die Allianz konnte bisher $2092w von $2093w benötigten Regenbogenflossenthunfischen einsammeln. Dazu kommen $2098w von $2099w gerösteten Raptoren und $2101w Tüpfelgelbschwänze, wobei wir eine Gesamtzahl von $2103w Stück benötigen. Die bisher erhaltenen Lieferungen wurden eingelagert und für den Transport nach Burg Cenarius in Silithus vorbereitet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11145', 'deDE', 'Die Sterne bewegen sich nun auf neuen Bahnen fort. Fortan scheinen sie als Zeichen der Hoffnung und der Stärke für kommende Zeiten.', 'Die Sterne bewegen sich nun auf neuen Bahnen fort. Fortan scheinen sie als Zeichen der Hoffnung und der Stärke für kommende Zeiten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11146', 'deDE', 'Haha, $G Junge : Mädel;! Die Allianz hat jetzt alles beisammen, was wir für den Krieg hier brauchen. Vielleicht holt die Horde ja bald auf?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11147', 'deDE', 'Für\'s Erste ist alles erledigt. Alle Kriegsmateriallieferungen befinden sich auf dem Weg nach Burg Cenarius in Silithus. Sie sollten dort in weniger als $2113w Tagen eintreffen. Es dauert nicht mehr lange bevor das Szepter wieder zusammengesetzt ist und der Krieg beginnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11148', 'deDE', 'Na, wer hätte das gedacht, wir haben\'s geschafft. Alles ist beisammen und bereit abtransportiert zu werden. Jetzt müssen wir nur noch abwarten bis diese Spinner von der Horde es endlich mal gebacken kriegen, ihren Kram zusammenzusammeln, und dann geht\'s ab nach Ahn\'Qiraj!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11149', 'deDE', 'Juhu, es geht los! Alle haben ihre Kriegsvorräte vervollständigt und noch während wir sprechen, wird alles per Zeppelin nach Silithus gebracht. Die letzten Lieferungen sollten dort in weniger als $2113w Tagen eintreffen. Wenn erst einmal alles vor Ort ist und das Szepter wieder zusammengesetzt wurde, kann der Krieg gegen diese verdammten Insekten endlich beginnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11150', 'deDE', null, 'Es scheint so, als hätte die Allianz jetzt alles, was für den Krieg benötigt wird, beisammen. Jetzt warten wir nur noch darauf, dass diese armseligen Kreaturen der Horde ebenfalls fertig werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11151', 'deDE', null, 'Hallo, $C. Nach dem, was mir berichtet wurde, wurde alles Benötigte zusammengesammelt und sämtliche Kriegsmaterialien werden momentan nach Silithus transportiert. Innerhalb von weniger als $2113w Tagen sollte alles dort ankommen. Wenn es soweit ist und der Gong mit dem Szepter der Sandstürme geschlagen wurde, wird der Krieg beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11152', 'deDE', 'Tja, sieht so aus, als wenn die Allianz alle nötigen Vorräte eingesammelt hat, nun warten wir nur noch auf die Horde. Ich werde jetzt erst einmal zusehen, ob ich hier in der Nähe nicht etwas von Skippis und Gunnis Eiscreme auftreiben kann.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11153', 'deDE', 'Das war\'s, beide Seiten haben alle nötigen Vorräte beisammen. Jetzt wird alles nach Silithus transportiert und sollte in weniger als $2113w Tagen da sein. Sobald sie eingetroffen sind und jemand mit dem Szepter der Sandstürme den Gong auf der Skarabäushöhe schlägt, geht der Krieg los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11154', 'deDE', 'Willkommen in Booty Bay, Partner. Ich hoffe, Euer Aufenthalt hier wird angenehm und ereignislos verlaufen - und ich betone dabei \"ereignislos\". Wir haben hier mehr als genug zu Trinken, solltet Ihr durstig sein, und mehr als genug Rausschmeißer, solltet Ihr auf Ärger aus sein.$B$BJetzt wo wir das geklärt haben, was kann dieser bescheidene Diener des Steamwheedle-Kartells für Euch tun? Oder was noch viel interessanter ist, was könnt Ihr denn für mich tun, hä?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11155', 'deDE', 'Alle benötigten Vorräte, die die Allianz den Kriegsvorbereitungen beizusteuern vermag sind bereits eingetroffen, $C.Wir warten jetzt darauf, dass die Horde ihren Teil beiträgt, und dann werden sämtliche Materialien nach Silithus transportiert werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11156', 'deDE', 'Die Vorbereitungen sind abgeschlossen, junger $C. Während wir uns hier unterhalten, ist sowohl die Allianz, als auch die Horde, fieberhaft damit beschäftigt, sämtliche Kriegsvorräte nach Burg Cenarius in Silithus zu transportieren, was weniger als $2113w Tage in Anspruch nehmen sollte. Danach kann der große Gong nahe den Toren von Ahn\'Qiraj geschlagen werden und der Krieg wird beginnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11157', 'deDE', null, 'Grüße, $C. Wie Ihr eventuell schon wisst, haben wir bereits alles beisammen, was die Allianz für den kommenden Krieg benötigt. Jetzt hängt es nur noch von der Horde ab, Ihre Vorbereitungen abzuschließen, und wir können alles nach Silithus schaffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11158', 'deDE', null, 'Bist hier um deinen Dienst anzutreten, $C? Sämtliche Kriegsvorräte der Horde und Allianz wurden zusammengetragen und werden in diesem Moment nach Silithus transportiert. Es sollte alles in weniger als $2113w Tagen ankommen. Ich hoffe nur, dass jemand die verschiedenen Teile des Szepters der Sandstürme gefunden und es zusammengesetzt hat.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11159', 'deDE', 'Ein Wesen von großer Macht ist erwacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11160', 'deDE', 'Das Biest ist dorthin zurückgekehrt, von wo es kam. Der Zorn von Neptulon hat sich gelegt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11161', 'deDE', null, 'Na dann, $G Junge : Mädel;. Es sieht so aus, als hätte die Allianz alles beisammen, was sie für den Krieg benötigt. Ich weiß, dass die Schmiede, Ingenieure und jedermann sonst mit den letzten Vorbereitungen beschäftigt ist. Sobald die Horde mit ihren Sammlungen fertig ist, wird alles für den Krieg nach Silithus transportiert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11162', 'deDE', null, 'So wie ich das verstehe, wurde alles benötigte zusammengetragen und wird jetzt verwaltet und zur Burg Cenarius transportiert. Der letzte Rest sollte in weniger als $2113w Tagen ankommen. Sobald all unsere Vorräte und Armeen dort eingetroffen sind, kann der Träger des Szepters der Sandstürme den Gong nahe Ahn\'Qiraj schlagen, um die Tore zu öffnen. Dann wird der Krieg beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11163', 'deDE', 'Wir sind hier so gut wie fertig. Die gesamten Kriegsvorräte werden momentan nach Burg Cenarius in Silithus transportiert und sollten dort in weniger als einem Tag eintreffen. Es wird nicht mehr lange dauern, bis das Szepter zusammengesetzt ist und der Krieg endlich beginnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11164', 'deDE', 'Juhu, jetzt geht\'s los! Alle Vorräte sind beisammen und werden mittels Zeppelin nach Silithus verfrachtet. Sie sollten alle in weniger als einem Tag eintreffen. Sobald da alles unter Dach und Fach ist und das Szepter zusammengesetzt wurde, kann der Krieg gegen diese abartigen Insekten losgehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11165', 'deDE', null, 'Hallo, $C. Nach dem, was mir berichtet wurde, wurde alles Benötigte zusammengesammelt und sämtliche Kriegsmaterialien werden momentan nach Silithus transportiert. Innerhalb von weniger als einem Tag sollte alles dort ankommen. Wenn es soweit ist und der Gong mit dem Szepter der Sandstürme geschlagen wurde, wird der Krieg beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11166', 'deDE', 'Das war\'s, beide Seiten haben alle nötigen Vorräte beisammen. Jetzt wird alles nach Silithus transportiert und sollte in weniger als einem Tag da sein. Sobald sie eingetroffen sind und jemand mit dem Szepter der Sandstürme den Gong auf der Skarabäushöhe schlägt, geht der Krieg los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11167', 'deDE', 'Die Vorbereitungen sind abgeschlossen, junger $C. Während wir uns hier unterhalten, ist sowohl die Allianz, als auch die Horde, fieberhaft damit beschäftigt, sämtliche Kriegsvorräte nach Burg Cenarius in Silithus zu transportieren, was weniger als einen Tag in Anspruch nehmen sollte. Danach kann der große Gong nahe den Toren von Ahn\'Qiraj geschlagen werden und der Krieg wird beginnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11168', 'deDE', null, 'Bist hier um deinen Dienst anzutreten, $C? Sämtliche Kriegsvorräte der Horde und Allianz wurden zusammengetragen und werden in diesem Moment nach Silithus transportiert. Es sollte alles in weniger als einem Tag ankommen. Ich hoffe nur, dass jemand die verschiedenen Teile des Szepters der Sandstürme gefunden und es zusammengesetzt hat.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11169', 'deDE', null, 'So wie ich das verstehe, wurde alles benötigte zusammengetragen und wird jetzt verwaltet und zur Burg Cenarius transportiert. Der letzte Rest sollte in weniger als einem Tag ankommen. Sobald all unsere Vorräte und Armeen dort eingetroffen sind, kann der Träger des Szepters der Sandstürme den Gong nahe Ahn\'Qiraj schlagen, um die Tore zu öffnen. Dann wird der Krieg beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11170', 'deDE', 'Jawohl, $G Sire : holde Dame;! Mir wurde aufgetragen Euch zu berichten, dass die Allianz nun alle benötigten Vorräte für den Krieg beisammen hat. Es wurde mir zugetragen, dass es jetzt nur noch eine Frage der Zeit ist, bis die Horde ebenfalls ihre Vorbereitungen abgeschlossen hat. Dann werden sämtliche nach Silithus transportiert werden. Danke, $G Sire : holde Dame;.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11171', 'deDE', 'Aaaachtung! Sämtliche Vorräte für den Krieg in Ahn\'Qiraj sind beisammen und dabei, nach Burg Cenarius transportiert zu werden $G Sire : holde Dame;. Mir wurde berichtet, dass die letzten Vorräte in weniger als $2113w Tagen eintreffen sollten. Sobald sie an Ort und Stelle sind und jemand diesen Gong nahe den Toren von Ahn\'Qiraj schlägt, kann ich Euch garantieren, dass wir diesen Insekten die Hölle heiß machen werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11172', 'deDE', 'Aaaachtung! Sämtliche Vorräte für den Krieg in Ahn\'Qiraj sind beisammen und dabei, nach Burg Cenarius transportiert zu werden $G Sire : holde Dame;. Mir wurde berichtet, dass die letzten Vorräte in weniger als einem Tag eintreffen sollten. Sobald sie an Ort und Stelle sind und jemand diesen Gong nahe den Toren von Ahn\'Qiraj schlägt, kann ich Euch garantieren, dass wir diesen Insekten die Hölle heiß machen werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11173', 'deDE', null, 'Hey, $C. Sieht ganz so aus, als hätte die Horde den letzten Rest von Ihrem Kram beisammen. Jetzt werden sowohl unsere als auch ihre Vorräte und Materialien nach Silithus transportiert. Mir wurde gesagt, alles werde dort innerhalb von weniger als einem Tag ankommen. Dann kann jemand den großen Gong nahe den Toren von Ahn\'Qiraj schlagen und den Krieg einläuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11174', 'deDE', null, 'Hallo, $C! Sieht so aus, als hätte die Horde jetzt die letzten Vorräte zusammengetragen und jetzt werden sowohl ihre als auch unsere Vorräte nach Silithus geschafft. Es mir berichtet, dass alles dort in weniger als $2113w Tagen eintreffen sollte. Dann kann jemand den großen Gong nahe den Toren von Ahn\'Qiraj schlagen und der Krieg wird losgehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11175', 'deDE', null, 'Hallo, $C! Ich habe mal ein wenig herumgefragt und wie es aussieht, haben wir jetzt alles beisammen, was die Allianz zur Vorbereitung auf den Krieg von Ahn\'Qiraj benötigt. Jetzt müssen wir nur noch die Horde ermutigen, dasselbe zu leisten, und dann kann alles nach Silithus geschafft werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11176', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11177', 'deDE', 'Narain, ich brauche noch ein Spezialset.', 'Narain, ich brauche noch ein Spezialset.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11178', 'deDE', 'Narain, wenn du noch genug Materialien hast, könnte ich eine weitere Arkanitboje brauchen.', 'Narain, wenn du genug Materialien hast, könnte ich noch eine Arkanitboje gebrauchen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11179', 'deDE', null, 'Während wir uns hier unterhalten, $C, werden schon sämtliche Kriegsvorräte nach Silithus transportiert. So wie es aussieht, sollte der letzte Rest in weniger als einem Tag dort ankommen. Danach wird $G ein Held : eine Heldin; den Gong auf der Skarabäushöhe schlagen müssen. Dann geht der Krieg los.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11180', 'deDE', null, '$C, die Kriegsmaterialien werden, während wir uns hier unterhalten, nach Silithus transportiert. So wie es aussieht, sollte dort alles innerhalb von weniger als $2113w Tagen eintreffen. Danach muss $G ein Held : eine Heldin; kommen und den Gong auf der Skarabäushöhe schlagen. Dann wird der Krieg richtig losgehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11181', 'deDE', null, 'Ihr solltet Stolz darauf sein, $C, dass die Allianz die Aufgabe erfüllt hat, Vorräte und Materialien für den Krieg zusammenzusammeln. All diese Materialien und Vorräte werden nun haltbar gemacht und gelagert. Unsere Handwerker sind momentan schwer damit beschäftigt, daraus Kriegsmaterial herzustellen, das nach Silithus transportiert werden wird sobald die Horde ihre Vorbereitungen abgschlossen hat.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11182', 'deDE', null, 'Sieht aus als würden die letzten Güter, die wir zusammen haben, innerhalb des nächsten Tages nach Silithus transportiert werden. Und wenn wir damit einmal durch sind, wird jemand daherkommen müssen und den Gong auf der Skarabäushöhe, außerhalb von Ahn\'Qiraj, schlagen müssen um die Tore zu öffnen. Dann werden sich die ganzen Verbände sicherlich als nützlich erweisen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11183', 'deDE', null, 'Sieht so aus, als würden die letzten Güter, die wir zusammengesammelt haben, über die nächsten $2113w Tage hinweg nach Silithus transportiert werden. Wenn wir damit fertig sind, muss jemand den Gong auf der Skarabäushöhe außerhalb von Ahn\'Qiraj schlagen, um die Tore zu öffnen. Dann werden sich unsere Verbände sicherlich als nützlich erweisen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11184', 'deDE', null, 'Uns wurde von den Generälen berichtet, dass wir alles beisammen haben, was für den bevorstehenden Krieg von Ahn\'Qiraj benötigt wird. Jetzt warten wir nur noch darauf, dass die Horde ihre Vorräte aufstockt, und dann kann der Transport der Vorräte und Materialien beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11185', 'deDE', 'Alles ist beisammen, $C. Die letzten Vorbereitungen stehen kurz davor abgeschlossen zu werden, die Vorräte sollten innerhalb eines Tages in Silithus eintreffen. All unsere vereinten Kräfte erwarten nun $G den Helden : die Heldin;, $G der : die; das Szepter der Sandstürme zusammensetzt, den Gong schlägt und somit den Krieg von Ahn\'Qiraj einläutet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11186', 'deDE', 'Alles ist beisammen, $C. Die letzten Vorbereitungen stehen kurz davor abgeschlossen zu werden, die Vorräte sollten innerhalb von $2113w Tagen in Silithus eintreffen. All unsere vereinten Kräfte erwarten nun $G den Helden : die Heldin;, $G der : die; das Szepter der Sandstürme zusammensetzt, den Gong schlägt und somit den Krieg von Ahn\'Qiraj einläutet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11187', 'deDE', 'Die Allianz hat das Sammeln der Vorräte abgeschlossen, $C. Wir treffen jetzt hier die letzten Vorbereitungen und warten darauf, dass die Horde ebenfalls fertig wird. Danach transportieren wir alles nach Silithus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11188', 'deDE', 'Die ganzen Fische und auch alles andere sind auf dem Weg nach Silithus und sollte dort in weniger als einem Tag eintreffen. Danach muss nur noch jemand mit dem Szepter der Sandstürme den Gong schlagen, um den Krieg einzuläuten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11189', 'deDE', 'Die ganzen Fische und auch alles andere sind auf dem Weg nach Silithus und sollte dort in weniger als $2113w Tagen eintreffen. Danach muss nur noch jemand mit dem Szepter der Sandstürme den Gong schlagen, um den Krieg einzuläuten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11190', 'deDE', 'So wie\'s aussieht, haben wir alles zusammengetragen, was wir brauchen. Jetzt kommt es nur noch darauf an, dass die Horde ebenfalls ihr Zeug zusammenkriegt. Sobald die damit durch sind, können wir alle unseren Kram nach Silithus schaffen und der Krieg geht los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11191', 'deDE', 'Die Wände der Kammer erbeben. Etwas geschieht…', 'Die Wände der Halle erzittern. Irgendetwas geschieht...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11192', 'deDE', '%s verbeugt sich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11193', 'deDE', 'Seid standhaft, $G Held : Heldin;. Ich weiß, warum Ihr hierher gekommen seid und ich weiß, wonach Ihr sucht. Eranikus wird den Splitter nicht freiwillig aufgeben. Er wurde korrumpiert… Korrupiert von genau der Macht, die Ihr zu zerstören versucht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11194', 'deDE', 'Seid Ihr überrascht? Fällt es so schwer zu glauben, dass die Macht eines alten Gottes selbst das Innere des Traumes erreichen könnte? Es ist wahr, Eranikus, der Tyrann des Traumes, schlägt eine Schlacht gegen uns alle. Ein Alptraum folgt seiner Spur der Zerstörung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11195', 'deDE', 'Ihr müsst Euch über eines im klaren sein, Eranikus begehrt nichts mehr, als aus dem Traum zurück nach Azeroth zu gelangen. Wenn er dies einmal vollbracht hat, wird er mit allen Mitteln versuchen meine physische Manifestation zu vernichten. Allerdings ist dies der einzige Weg, den Szeptersplitter wiederzubeschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11196', 'deDE', 'Ihr werdet ihn zurück in diese Welt führen, $G Held : Heldin;.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11197', 'deDE', null, 'Sämtliche Vorräte und Materialien, die wir gesammelt haben, sind jetzt auf dem Weg nach Silithus und sollten dort in weniger als einem Tag eintreffen. Mir wurde berichtet, dass sobald alles eingetroffen ist, jemand den Gong auf der Skarabäushöhe schlagen muss, um die Tore zu öffnen und den Krieg einzuläuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11198', 'deDE', null, 'Sämtliche Vorräte und Kriegsmaterialien, die wir zusammengesammelt haben, sind jetzt auf dem Weg nach Silithus und sollten dort in weniger als $2113w Tagen eintreffen. Jemand hat mir gesagt, dass jemand den Gong auf der Skarabäushöhe schlagen muss, sobald alles auf Burg Cenarius angekommen ist. Dann beginnt der Krieg.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11199', 'deDE', null, 'Hallo, $C. Ich habe mich mal mit den Leuten hier unterhalten und wie es aussieht, haben wir alles beisammen, was die Allianz für den Krieg benötigt. Dieser fiese Botschafter meinte allerdings, dass die Horde immer noch mit dem zusammensammeln beschäftigt ist. Von daher haben wir noch etwas Zeit, bevor wir alles nach Silithus schaffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11200', 'deDE', null, 'Grüße, $R. Die Kriegsmaterialien sind auf ihrem Weg und sollten innerhalb eines Tages auf der Burg Cenarius eintreffen. Wir können nur darauf hoffen, dass sich jemand, der das Szepter der Sandstürme zusammengesetzt hat, zeigen wird sobald alles eingetroffen ist und die Tore zu Ahn\'Qiraj öffnet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11201', 'deDE', null, 'Seid gegrüßt, $R. Die Kriegsmaterialien werden nach und nach zur Burg Cenarius transportiert und werden dort innerhalb von $2113w Tagen eintreffen. Wir können nur hoffen, dass sich jemand, der das Szepter der Sandstürme zusammengesetzt hat, zeigen wird und den Gong schlägt um die Tore zu öffnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11202', 'deDE', null, 'Wir alle haben durch unsere Zusammenarbeit eine große Leistung vollbracht. Jetzt stehen wir einer noch größeren Herausforderung gegenüber. Wir warten nun ab, bis die Horde ihre Sammlungen abgeschlossen hat, und stellen uns dann gemeinsam den Silithiden und ihren Meistern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11203', 'deDE', 'Das Ihr alle zu einer so gut durchorganisierten Operation fähig seid, kommt unerwartet. Sowohl die Allianz, als auch die Horde, haben ihre Vorbereitungen für den Krieg von Ahn\'Qiraj abgeschlossen. Während wir diese Worte wechseln, werden die Vorräte bereits nach Silithus transportiert und sollten innerhalb eines Tages eintreffen. Dann liegt es in den Händen dessen, der das Szepter der Sandstürme zusammensetzt, den Gong auf der Skarabäushöhe zu schlagen und die Tore nach Ahn\'Qiraj zu öffnen. Erst dann wird dieser Krieg wahrhaft beginnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11204', 'deDE', 'Das ihr alle zu einer so gut durchorganisierten Operation fähig seid, kommt unerwartet. Sowohl die Allianz, als auch die Horde, haben ihre Vorbereitungen für den Krieg von Ahn\'Qiraj abgeschlossen. Während wir diese Worte wechseln, werden die Vorräte bereits nach Silithus transportiert und sollten innerhalb von $2113w Tagen eintreffen. Dann liegt es in den Händen dessen, der das Szepter der Sandstürme zusammensetzt, den Gong auf der Skarabäushöhe zu schlagen und die Tore nach Ahn\'Qiraj zu öffnen. Erst dann wird dieser Krieg wahrhaft beginnen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11205', 'deDE', 'Wie erwartet, hat die Horde ihre Vorbereitungen längst vor Euch und Eurer sogenannten Allianz abgeschlossen. Ihr haltet uns auf, da wir die letzten Vorbereitungen zum Beginn dieses Krieges nicht treffen können, bevor Ihr nicht Eure versprochenen Verantwortungen erfüllt.$B$BGeht nun, und macht Euch nützlich, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11206', 'deDE', '%s erkennt Eure Ehrerbietung an und winkt Euch herablassend fort.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11207', 'deDE', 'Angriff! Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11208', 'deDE', 'Ich habe gehört, dass die letzten Vorräte jetzt nach Silithus geschafft werden und dort in weniger als einem Tag ankommen sollten. Ich kann darauf verzichten, dass diese Viecher sich in meine Mine durchbuddeln, deshalb hoffe ich, dass schon jemand damit beschäftigt ist das Szepter der Sandstürme zusammenzusetzen, damit der Gong geschlagen werden kann, die Tore sich öffnen und der Krieg beginnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11209', 'deDE', 'Ich habe gehört, dass die letzten Vorräte jetzt nach Silithus geschafft werden und dort in weniger als $2113w Tagen ankommen sollten. Ich kann darauf verzichten, dass diese Viecher sich in meine Mine durchbuddeln, deshalb hoffe ich, dass schon jemand damit beschäftigt ist das Szepter der Sandstürme zusammenzusetzen, damit der Gong geschlagen werden kann, die Tore sich öffnen und der Krieg beginnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11210', 'deDE', 'Unsere Arbeit ist getan. Jetzt warten wir darauf, dass die Allianz ihre tut, bevor wir alles nach Silithus schaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11211', 'deDE', 'Wenn jemand das magische Szepter der Sandstürme zusammengesetzt hat, dann wird der Kampf mit den Käfern bald beginnen. Ich freue mich schon darauf, $C! Unsere letzten Vorräte befinden sich schon auf dem Weg nach Silithus und sollten dort innerhalb eines Tages eintreffen. Geht und bringt der Horde Ehre!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11212', 'deDE', 'Wenn jemand das magische Szepter der Sandstürme zusammengesetzt hat, dann wird der Kampf mit den Käfern bald beginnen. Ich freue mich schon darauf, $C! Unsere letzten Vorräte befinden sich schon auf dem Weg nach Silithus und sollten dort innerhalb von $2113w Tagen eintreffen. Geht und bringt der Horde Ehre!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11213', 'deDE', 'Unser Weg ist der Weg des Ruhmes, $R. Die schwächliche Allianz ist immer noch dabei ihre kläglichen Vorräte zusammenzukratzen, während wir bereits alles beisammen haben und bereit sind, es nach Silithus zu schicken.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11214', 'deDE', 'Ich frage mich was ihr tun werdet, Helden, wenn ich diesen Splitter zerstöre? Wird die Welt dem sicheren Untergang geweiht sein?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11215', 'deDE', 'Was ist das? Ein Sprung im Splitter? Endlich hat sich eine Schwachstelle offenbart… Nun wird es nicht mehr lange dauern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11216', 'deDE', 'Wäre es nicht eine gar vortreffliche Ironie, solltet ihr es schaffen mich zu besiegen, allerdings nicht, bevor ich bereits Kalimdors letzte Hoffnung zerstört habe? Mir wird vor Vorfreude ganz schwindelig. Bald… Ja schon bald wird davon nur Staub übrig sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11217', 'deDE', 'Nicht einmal mein Vater hätte eine solche Tragödie, ein solches Chaos, planen können. Ihr könnt mich nicht aufhalten! Euch läuft die Zeit davon!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11218', 'deDE', 'Ah, die Schutzschicht ist fast fort. Hört ihr das, Helden? Das… Das ist der Klang von Unausweichlichkeit. All eure Hoffnungen und Träume werden unter meinem Druck zerstört.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11219', 'deDE', 'Den Splitter gibt es nicht mehr! Euer geliebtes Kalimdor wird schon bald einem trostlosem Ödland gleichen. Und die östlichen Königreiche werde ich in eine von Asche erfüllte, lebenslose Leere verwandeln.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11220', 'deDE', null, 'Der Krieg kommt jetzt bald. Wir müssen nur noch diese ganzen Waffen und Vorräte zur Burg Cenarius in Silithus schaffen und das wird nur noch einen Tag dauern. Danach wird eine mutige Seele den Gong nahe den Toren von Ahn\'Qiraj schlagen müssen, um sie zu öffnen und den Krieg einzuläuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11221', 'deDE', null, 'Der Krieg kommt jetzt bald. Wir müssen nur noch diese ganzen Waffen und Vorräte zur Burg Cenarius in Silithus schaffen und das wird nur noch $2113w Tage dauern. Danach wird eine mutige Seele den Gong nahe den Toren von Ahn\'Qiraj schlagen müssen, um sie zu öffnen und den Krieg einzuläuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11222', 'deDE', null, 'Ich hab jetz\' nicht nur alle Mithrilbarren, die wir brauchen, die Horde is\' sogar komplett mit den Sammlungen durch, Mann. Wir warten nur noch auf die Allianz und dann geht der Krieg los!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11223', 'deDE', null, 'Es ist wahr, junger $R. Wir und die Allianz haben bereits alles beisammen, was für den Krieg in Ahn\'Qiraj benötigt wird. Diese Dinge werden momentan zur Burg Cenarius transportiert und sollten dort innerhalb eines Tages eintreffen. Wenn alles an seiner rechten Stelle ist, wird jemand den Gong auf der Skarabäushöhe schlagen müssen, um die Tore zu öffnen und den Krieg einzuläuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11224', 'deDE', null, 'Es ist wahr, $G junger : junge; $N. Wir und die Allianz haben bereits alles beisammen, was für den Krieg in Ahn\'Qiraj benötigt wird. Diese Dinge werden momentan zur Burg Cenarius transportiert und sollten dort in $2113w Tagen eintreffen. Wenn alles an seiner rechten Stelle ist, wird jemand den Gong auf der Skarabäushöhe schlagen müssen, um die Tore zu öffnen und den Krieg einzuläuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11225', 'deDE', null, 'Die Sammlungen der Horde sind abgeschlossen, $C. Jetzt vertreiben wir uns die Zeit mit weiteren Vorbereitungen, während die Allianz ihre Kriegsanstrengungen abschließt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11226', 'deDE', 'Hey, immer mit der Ruhe, $C. Pele\'keiki glaubt, dass Ihr überhaupt keinen Plan davon habt, dass der ganze Kram hier auf seinem Weg nach Silithus ist. Pele\'keiki wurde verklickert, dass alles in weniger als einem Tag da ankommt. Danach wird irgendso\'n irrer Trottel den Gong vor den Toren von Ahn\'Qiraj schlagen und sie werden sich öffnen. Dann geht der Krieg los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11227', 'deDE', 'Hey, immer mit der Ruhe, $C. Pele\'keiki glaubt, dass Ihr überhaupt keinen Plan davon habt, dass der ganze Kram hier auf seinem Weg nach Silithus ist. Pele\'keiki wurde verklickert, dass alles in weniger als $2113w Tagen da ankommt. Danach wird irgendso\'n irrer Trottel den Gong vor den Toren von Ahn\'Qiraj schlagen und sie werden sich öffnen. Dann geht der Krieg los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11228', 'deDE', 'Haha, Ihr haltet Pele\'keiki wohl für bescheuert, Mann. Pele\'keiki weiß genau, dass Ihr wisst, dass wir von der Horde schon die ganzen Kriegsvorräte haben, die wir brauchen. Jetzt warten wir nur noch darauf, dass die Allianz es mal gebacken kriegt ihre Vorräte zusammenzukratzen, und dann geht der Krieg los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11229', 'deDE', 'Wenn es mein Schicksal ist, hier zu fallen, dann werde ich euch mit meinem letzten Atem mit in den Schlund der Verdammnis reissen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11230', 'deDE', 'Das Lachen von %s hallt durch die Hallen des Pechschwingenhorts.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11231', 'deDE', 'Das Lachen von %s ertönt erneut in den Hallen des Pechschwingenhortes.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11232', 'deDE', null, 'Das Ende ist nah! Die Vorräte und Materialien werden momentan nach Silithus transportiert und sollten dort innerhalb eines Tages eintreffen. Sobald alles dort ist, wird es sicherlich schnellstmöglich fertig gemacht werden. Dann müssen wir nur noch auf das Erklingen des Gongs an den Toren von Ahn\'Qiraj warten und der Krieg wird beginnen. Ich freue mich schon darauf, analysieren zu können, wie erfolgreich unsere Vorbereitungen sein werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11233', 'deDE', null, 'Das Ende ist nah! Die Vorräte und Materialien werden momentan nach Silithus transportiert und sollten dort innerhalb von $2113w Tagen eintreffen. Sobald alles dort ist, wird es sicherlich schnellstmöglich fertig gemacht werden. Dann müssen wir nur noch auf das Erklingen des Gongs an den Toren von Ahn\'Qiraj warten und der Krieg wird beginnen. Ich freue mich schon darauf, analysieren zu können, wie erfolgreich unsere Vorbereitungen sein werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11234', 'deDE', null, 'Es ist sehr pflichtbewusst von Euch, nachzufragen, $C, aber die Sammlungen der Horde sind abgeschlossen. Wir warten jetzt darauf, dass die Allianz fertig wird, und dann wird alles nach Silithus zur Burg Cenarius transportiert.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11235', 'deDE', null, 'Mit dem Sammeln sind wir jetzt fertig und die Güter werden nach Silithus transportiert. Die sollten in weniger als einem Tag da ankommen. Danach brauchen wir nur noch jemanden, der den Gong schlagen und die Tore nach Ahn\'Qiraj öffnen kann. Dann geht der Spaß so richtig los.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11236', 'deDE', null, 'Mit dem Sammeln sind wir jetzt fertig und die Güter werden nach Silithus transportiert. Die sollten in weniger als $2113w Tagen da ankommen. Danach brauchen wir nur noch jemanden, der den Gong schlagen und die Tore nach Ahn\'Qiraj öffnen kann. Dann geht der Spaß so richtig los.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11237', 'deDE', null, 'Hey, $C, habt Ihr\'s noch nicht gewusst? Die Horde ist fertig mit dem Sammeln und nimmt jetzt nur noch die letzten Vorbereitungen vor während wir darauf warten, dass die Allianz fertig wird. Dann wird\'s Zeit alles für den Krieg nach Silithus zu schicken.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11238', 'deDE', null, 'Throm\'ka, $C. Es werden gerade die letzten Vorbereitungen am Kriegsmaterial vorgenommen, wonach es nach Silithus transportiert wird. Es sollte dort in weniger als einem Tag eintreffen. Wenn alles bereit ist und unsere Truppen aufgestellt sind, warten wir nur noch auf jemanden, der den Gong auf der Skarabäushöhe schlagen wird, was die Tore von Ahn\'Qiraj öffnen und den Krieg einläuten sollte.$B$BLok\'tar Ogar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11239', 'deDE', null, 'Throm\'ka, $C. Es werden gerade die letzten Vorbereitungen am Kriegsmaterial vorgenommen, wonach es nach Silithus transportiert wird. Es sollte dort im Verlaufe der nächsten $2113w Tage eintreffen. Wenn alles bereit ist und unsere Truppen aufgestellt sind, warten wir nur noch auf jemanden, der den Gong auf der Skarabäushöhe schlagen wird, was die Tore von Ahn\'Qiraj öffnen und den Krieg einläuten sollte.$B$BLok\'tar Ogar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11240', 'deDE', null, 'Die Horde ist bereit, jetzt wo wir alle Vorräte und Materialien für den Krieg beisammen haben. Wir warten jetzt nur noch auf die lahme Allianz und dann auf den glorreichen Kampf!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11241', 'deDE', 'Wir haben alle benötigten Vorräte angesammelt, $C. Die gesamten Materialien werden bereits zur Burg Cenarius transportiert und wir nehmen an, dass die letzten Lieferungen im Laufe des Tages dort eintreffen werden. Dann steht dem Krieg in Ahn\'Qiraj nichts mehr im Wege und irgendein schwachsinniger Idiot kann losgeschickt werden, um den Gong an den Toren von Ahn\'Qiraj zu schlagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11242', 'deDE', 'Wir haben alle benötigten Vorräte angesammelt, $C. Die gesamten Materialien werden bereits zur Burg Cenarius transportiert und wir nehmen an, dass die letzten Lieferungen im Laufe von $2113w Tagen dort eintreffen werden. Dann steht dem Krieg in Ahn\'Qiraj nichts mehr im Wege und irgendein schwachsinniger Idiot kann losgeschickt werden, um den Gong an den Toren von Ahn\'Qiraj zu schlagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11243', 'deDE', 'Tja, sieht so aus, als ob wir erst einmal eine kleine Atempause einlegen können. Die Horde hat alle nötigen Vorräte eingesammelt. Momentan werden die letzten Lieferungen nach Silithus vorbereitet. Wir warten jetzt nur noch auf die Allianz. Vielleicht solltet Ihr mal ihren einfältigen, kleinen Gesandten fragen, wie weit die Sammelbemühungen der Allianz bereits gediehen sind.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11244', 'deDE', 'Sämtliche Kriegsmaterialien wurden in ausreichender Zahl eingesammelt und werden nun nach Silithus transportiert, $C. Die letzten Ladungen werden dort innerhalb eines Tages eintreffen. Ich hoffe nur, dass, wenn der Gong geschlagen wurde und sich die Tore öffnen, unsere Mühen wenigstens einigen unserer Soldaten das Leben retten werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11245', 'deDE', 'Sämtliche Kriegsmaterialien wurden in ausreichender Zahl eingesammelt und werden nun nach Silithus transportiert, $C. Die letzten Ladungen werden dort in $2113w Tagen eintreffen. Ich hoffe nur, dass, wenn der Gong geschlagen wurde und sich die Tore öffnen, unsere Mühen wenigstens einigen unserer Soldaten das Leben retten werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11246', 'deDE', 'Wir von der Horde haben alles getan, was in unserer Macht steht, um die Vorbereitungen für den kommenden Krieg in Ahn\'Qiraj abzuschließen. Jetzt warten wir darauf, dass die Allianz es uns gleichtut, $C. Sobald sie fertig sind, werden wir alle unsere Kriegsmaterialien nach Burg Cenarius in Silithus transportieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11247', 'deDE', null, 'Ja, Ihr habt richtig gehört, $C, alle nötigen Vorräte wurden eingesammelt und die letzten Kriegsmaterialien sollten innerhalb eines Tages in Silithus eintreffen. Mir wurde gesagt, dass, sobald dort alles entladen wurde und unsere Truppen einsatzbereit sind, einer von Euch Helden losziehen wird, um den Gong vor Ahn\'Qiraj zu schlagen. Diesem Auserwählten wird es bis dahin gelungen sein, das Szepter der Sandstürme wieder zusammenzusetzen. Ich kann nur hoffen, dass all das nicht zu lange dauern wird.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11248', 'deDE', null, 'Ja, Ihr habt recht gehört, $C. All unsere Sammlungen sind vollständig und der letzte Rest der Kriegsmaterialien sollte innerhalb von $2113w Tagen in Silithus eintreffen. Mir wurde berichtet, dass, sobald dort alles bereit und unsere Truppen aufgestellt sind, jemand aus Euren heldenhaften Reihen mittels des bereits zusammengesetzten Szepters der Sandstürme den Gong außerhalb von Ahn\'Qiraj schlagen wird. Ich hoffe nur, dass wir darauf nicht zu lange warten müssen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11249', 'deDE', null, 'Was gibt es jetzt schon wieder, $C? Ich habe nicht den ganzen Tag Zeit. Die Horde hat ihre Vorräte vervollständigt. Jetzt gilt es nur noch darauf zu warten, dass die Allianz ihre Vorbereitungen abschließt. Vielleicht solltet Ihr Euch selbst auf die Suche nach den Splittern des Szepters der Sandstürme machen, so dass Ihr den Gong auf der Skarabäushöhe schlagen könnt, um die Tore nach Ahn\'Qiraj zu öffnen und den Krieg zu beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11250', 'deDE', null, 'Die Vorbereitungen sind nun abgeschlossen, $C. Wir erwarten nur noch die Ankunft der letzten Materialien in Silithus, welche in weniger als einem Tag ankommen sollten. Sobald dort alles organisiert ist, wird jemand mit dem Szepter der Sandstürme den Gong auf der Skarabäushöhe schlagen müssen. Dann wird der Krieg von Ahn\'Qiraj beginnen. Möge die Erdenmutter über uns wachen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11251', 'deDE', null, 'Alle Vorbereitungen sind abgeschlossen, $C. Wir erwarten, dass die letzten Materiallieferungen in weniger als $2113w Tagen in Silithus eintreffen werden. Dann, sobald die letzten Vorbereitungen abgeschlossen sind, wird jemand vorausgehen und den Gong auf der Skarabäushöhe mit dem Szepter der Sandstürme schlagen müssen. Wenn das geschehen ist, beginnt der Krieg um Ahn\'Qiraj. Möge die Erdenmutter uns beistehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11252', 'deDE', null, 'Als Diener der Erdenmutter und der Horde, kann ich nicht anders, als ein wenig nervös zu werden, nun da wir alle erforderlichen Vorräte beisammen haben und der Krieg kurz bevor steht. Wenigstens bleibt uns noch etwas Zeit, bis die Allianz ihre Vorräte vervollständigt hat. Wenn es dann soweit ist, wird alles nach Burg Cenarius in Silithus transportiert und kurz darauf wird der Krieg beginnen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11253', 'deDE', null, 'Ja, ja, sämtliche Kriegsmaterialien der Horde und Allianz werden nach Silithus transportiert und sollten dort innerhalb eines Tages eintreffen. Wisst Ihr, was dann passiert, $C? Wir beeilen uns zuerst und warten dann ab. Hoffentlich ist schon jemand damit beschäftigt, die Einzelteile des Szepters der Sandstürme zusammenzusammeln. Nur damit kann nämlich der Gong auf der Skarabäushöhe geschlagen und so die Tore von Ahn\'Qiraj geöffnet werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11254', 'deDE', null, 'Natürlich, ja, sämtliche Kriegsvorräte der Horde und der Allianz befinden sich auf dem Weg nach Silithus und sollten dort in den nächsten $2113w Tagen eintreffen. Wisst Ihr, was dann passiert, $C? Wir bereiten uns vor und dann warten wir. Hoffentlich gelingt es bis dahin irgendwem, die Splitter des Szepters der Sandstürme zusammenzuführen, denn um die Tore von Ahn\'Qiraj zu öffnen, ist es notwendig, den Gong auf der Skarabäushöhe mit dem Szepter zu läuten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11255', 'deDE', null, 'Was ist, $C? Die Horde hat ihre Sammlungen abgeschlossen und die letzten Vorbereitungen werden nun abgeschlossen, während die Allianz noch versucht, aufzuholen. Kann ich Euch irgendwie weiterhelfen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11256', 'deDE', 'Es sieht so aus, als bliebe uns nur noch ein Tag zum Angeln, bevor das ganze Kriegsmaterial hier nach Silithus abtransportiert wird. Anschließend werden sie die Schlacht vorbereiten und wenn das getan ist, wird jemand den Gong südlich von Burg Cenarius läuten müssen, um die Tore zu öffnen und den Krieg zu beginnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11257', 'deDE', 'Wie\'s aussieht, hab\'n wir nur noch $2113w Tage zum Fischen übrig, bevor der ganze Kram hier in die Wüste von Silithus geschippert wird, Mann. Dann wird das ganze Zeug klargemacht werden und jemand wird den Gong im Süden von Burg Cenarius schlagen müssen, um die Tore zu öffnen und den Krieg losgeh\'n zu lassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11258', 'deDE', 'Zeit, etwas auszuspannen, Kumpel. Die Allianz ist noch lange nicht soweit wie wir. Unsere Sammelei hier hat ein Ende. Während wir sprechen, werden die letzten Vorbereitungen abgeschlossen. Sobald die Jungs in Eisenschmiede fertig sind, werden sie schon dafür sorgen, dass all das Zeug nach Silithus geschafft wird. Bis dahin, lasst uns noch etwas ausspannen oder fischen gehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11259', 'deDE', 'Ihr liegt richtig, $C, wenn Ihr gehört habt, dass die Horde und die Allianz ihre Vorratssammlung abgeschlossen haben. Alle für den Krieg wichtigen Materialien wurden in ausreichender Zahl gesammelt und werden momentan für den Transport nach Burg Cenarius in Silithus vorbereitet. Die letzten Lieferungen sollten dort in weniger als einem Tag eintreffen. Danach heißt es abwarten, bis es jemandem gelingt, dass Szepter wieder zusammenzusetzen, um damit den Gong zu läuten, welcher die Tore nach Ahn\'Qiraj öffnet und den Krieg einläutet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11260', 'deDE', 'Ihr liegt richtig, $C, wenn Ihr gehört habt, dass die Horde und die Allianz ihre Vorratssammlung abgeschlossen haben. Alle für den Krieg wichtigen Materialien wurden in ausreichender Zahl gesammelt und werden momentan für den Transport nach Burg Cenarius in Silithus vorbereitet. Die letzten Lieferungen sollten dort in weniger als $2113w Tagen eintreffen. Danach heißt es abwarten, bis es jemandem gelingt, dass Szepter wieder zusammenzusetzen, um damit den Gong zu läuten, welcher die Tore nach Ahn\'Qiraj öffnet und den Krieg einläutet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11261', 'deDE', 'Die Horde hat ihre Aufgaben für die Kriegsvorbereitungen erfüllt, junger $C. Jetzt müssen wir nur noch abwarten, bis die Allianz es uns gleichtut, und alles ist für den Krieg in Ahn\'Qiraj bereit. Außer vielleicht dem Zusammensammeln und Vereinen der Teile des Szepters der Sandstürme?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11262', 'deDE', 'Das ist richtig, $G Junge : Mädel;! Sämtliches Kriegsmaterial wird in diesem Moment nach Silithus transportiert. Die letzten Ladungen werden dort innerhalb eines Tages eintreffen. Anschließend heißt es abwarten und sehen, aus wessen Reihen ein Held hervorgeht, der das Szepter der Sandstürme wiederherstellt und den Gong auf der Skarabäushöhe läutet. Nur dann werden sich die Tore nach Ahn\'Qiraj öffnen und der Krieg wird beginnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11263', 'deDE', 'Das ist richtig, $G Junge : Mädel;! Sämtliches Kriegsmaterial wird in diesem Moment nach Silithus transportiert. Die letzten Ladungen werden dort innerhalb von $2113w Tagen eintreffen. Anschließend heißt es abwarten und sehen, aus wessen Reihen ein Held hervorgeht, der das Szepter der Sandstürme wiederherstellt und den Gong auf der Skarabäushöhe läutet. Nur dann wird der Krieg an den Toren von Ahn\'Qiraj beginnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11264', 'deDE', 'Lasst Euch nicht entmutigen, $R, auch wenn es war ist, dass die Allianz ihre Kriegsvorbereitungen bereits abgeschlossen hat. Zwar ist Zeit ein wesentlicher Faktor, doch es ist weitaus wichtiger, dass Ihr beim Vorrätesammeln für die Horde gewissenhaft und gründlich vorgeht. Das erscheint mir besser, als versehentlich einen Fehler zu machen, der vielleicht einem unserer Soldaten in Ahn\'Qiraj das Leben kostet, wenn der Krieg erst einmal richtig losgeht. Meint Ihr nicht auch, $C?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11265', 'deDE', 'Ihr müsst den Szeptersplitter finden, $N. Nefarius darf es nicht gelingen, den Splitter zu zerst… zerstören.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11266', 'deDE', 'Ich werde den Splitter aus seinem geschundenen Körper ziehen.', 'Ich werde den Splitter aus seinem geschundenen Körper ziehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11267', 'deDE', 'Sucht Ihr hiernach: [Roter Szeptersplitter]? Kommt und holt ihn Euch…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11268', 'deDE', 'Ihr habt Euch das letzte Mal in die Angelegenheiten von Dämonen eingemischt, Sterblicher! Nun werdet Ihr durch die Hand von Xandivious den Tod finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11269', 'deDE', 'Niemand kann den Alptraum aufhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11270', 'deDE', '%s saugt die letzten Reste von Leben aus $N und verschlingt seine Seele.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11271', 'deDE', 'Kommt, $N. Seht die Macht des Alptraums…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11272', 'deDE', 'Ein Hauch von Kälte liegt in der Luft.', 'Ein Hauch von Kälte liegt in der Luft.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11273', 'deDE', 'Wir dienen Thrall mit Stolz. Keine Entfernung ist zu groß, um den Feind zu vernichten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11274', 'deDE', 'Melden uns zum Dienst, Sir.', 'Melden uns zum Dienst, Sir.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11275', 'deDE', 'Ihr kommt zur rechten Zeit, $C. Fühlt Ihr, wie der Boden unter Euch bebt?$B$BIrgendetwas nähert sich aus dem Schwarmbau.$B$BBleibt bei den Grunzern, $N. Ich zähle auf Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11276', 'deDE', 'Die Silithiden sind keine ebenbürtigen Gegner für die Söhne und Töchter Ogrimmars. Bald wird von ihnen nicht mehr übrig sein, als ein paar zerquetschte Gliedmaßen und Flügel, die unter unseren Stiefeln kleben, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11277', 'deDE', 'Eranikus, der Tyrann des Traums, hat unsere Welt betreten!', 'Eranikus, der Tyrann des Traums, hat unsere Welt betreten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11278', 'deDE', null, 'Also gut, $N. Ich werde das Alptraumobjekt dazu verwenden, Eranikus zu beschwören.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11279', 'deDE', null, 'Folgt mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11280', 'deDE', null, 'Bereitet Euch vor! Die Beschwörung einer derartig starken Kreatur hat bisher noch nie ein gutes Ende gefunden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11281', 'deDE', null, 'Eranikus ist hier! Geht in Deckung! Sucht Schutz!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11282', 'deDE', 'Wir werden die Quelle des Alptraums über die von Euch gesammelten Fragmente ausfindig machen, $N. Von dort aus, werden wir Eranikus durch einen Spalt im Traum in unsere Welt ziehen. Bereitet Euch vor, $C. Wir holen jetzt die Verkörperung des Alptraums in diese Welt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11283', 'deDE', 'Nach Nighthaven! Sorgt dafür, dass Eure Armee mit Euch Schritt hält, Held!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11284', 'deDE', 'Ich werde Eranikus beschwören, sobald Ihr mir das Zeichen dazu gebt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11285', 'deDE', 'Ich bin bereit, Remulos.', 'Ich bin bereit, Remulos.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11286', 'deDE', 'Der Feind nähert sich! Haltet Euch bereit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11287', 'deDE', 'Merok! Shai! An meine Seite!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11288', 'deDE', 'Für die Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11289', 'deDE', '%s trinkt auf die Gefallenen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11290', 'deDE', 'Der Spalt wird sich hier über dem Elune\'ara See öffnen. Macht Euch bereit, $N. Eranikus\' Übergang in diese Welt wird von Chaos und Hass gekennzeichnet sein.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11291', 'deDE', 'Er wird sich von Nichts und Niemanden aufhalten lassen, um Malfurions körperliche Manifestation zu zerstören. Das darf auf keinen Fall geschehen... Wir müssen das Biest solange beschäftigen, bis Tyrande eingetroffen ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11292', 'deDE', 'Verteidigt Nighthaven, Held...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11293', 'deDE', 'Dämon! Spürt die Macht von Cenarius!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11294', 'deDE', 'In der Tat seid ihr nicht Euer Vater, Insekt. Wenn ich wollte, könnte ich Euch mit einem einzigen Schlag vernichten. Übergebt mir Shan\'do Stormrage und ich werde Euer erbärmliches Leben und das Leben Eurer Männer verschonen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11295', 'deDE', 'Wer ist hier der Narr, Untier? Ihr habt doch nicht wirklich geglaubt, dass wir Euch in Malfurions Nähe beschwören würden? Eure Erlösung naht, Eranikus. Ihr werdet endgültig von Eurem Wahnsinn befreit werden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11296', 'deDE', '%s lacht teuflisch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11297', 'deDE', 'Meine Erlösung? Ihr habt Mut, kleines Wesen. Nur der Wille meines Gottes kann mir Erlösung verschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11298', 'deDE', '%s brüllt vor Wut.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11299', 'deDE', 'Erhebt euch, ihr Diener des Alptraums! Zeigt euch und zerstört diese Welt! Niemand soll überleben...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11300', 'deDE', 'Beeilt Euch, $N! Wir müssen Schutz finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11301', 'deDE', 'Bitte, oh Held, beschützt Eure Gefolgsleute.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11302', 'deDE', 'Wer werden diese Dämonen gemeinsam bekämpfen! Die Verteidiger von Nighthaven stehen uns zur Seite, wenn es sein muss, bis in den Tod. Schnell jetzt! Wir müssen diese Kreaturen in den Alptraum zurücktreiben. Vernichtet sie alle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11303', 'deDE', 'Es wird geschehen, Eranikus. Lebt wohl, oh Weiser!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11304', 'deDE', 'Wo ist Euer Retter jetzt? Wie lange könnt Ihr noch gegen meine Angriffe bestehen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11305', 'deDE', 'Ihr habt meine Wächter besiegt? Nun werdet Ihr gegen mich bestehen müssen, Sterbliche!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11306', 'deDE', 'Remulos, seht wie schnell sie durch meine Hand fallen! Ihr könnt all dem ein Ende bereiten. Übergebt mir den Druiden und alles wird vorbei sein...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11307', 'deDE', 'Lasst es alle wissen, dass $N, ein $C der Horde, sich den ewigen Dank und Respekt unseres Kriegshäuptlings verdient hat! $G Er:Sie; hat sich in glorreicher diplomatischer Mission mit den Einwohnern der Holzschlundfeste bewährt. Dort vollbrachte $G er:sie; große Taten in unserem Namen. $G Seine:Ihre; Hingabe für die Horde geht weit über bloße Pflichterfüllung hinaus. Ein dreifaches Hurra für $N! $G Ein wahrer Held:Eine wahre Heldin; der Horde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11308', 'deDE', 'Lasst es alle wissen, dass $N, ein $C der Allianz, sich den ewigen Dank und Respekt aller Einwohner von Eisenschmiede und der gesamten Allianz verdient hat! $G Er:Sie; hat sich in glorreicher diplomatischer Mission mit den Einwohnern der Holzschlundfeste bewährt. Dort vollbrachte $G er:sie; große Taten in unserem Namen. $G Seine:Ihre; Hingabe für die Allianz geht weit über bloße Pflichterfüllung hinaus. Ein dreifaches Hurra für $N! $G Ein wahrer Held:Eine wahre Heldin; der Allianz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11309', 'deDE', null, 'Elune, erhöre meine Gebete. Verleihe uns Klarheit! Behüte unsere Gefallenen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11310', 'deDE', null, 'Befreie dich von deinen Sünden, Eranikus. Alles wird vergeben werden...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11311', 'deDE', null, 'Ihr werdet Vergebung erfahren, Eranikus. Elune wird Euch immer lieben. Befreit Euch von den Fesseln, die sich Euer bemächtigt haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11312', 'deDE', null, 'Die Umklammerung der alten Götter ist unlösbar. Er wird von ihren dunklen Gedanken verzehrt... Ich... Ich kann nicht... Kann nicht länger durchhalten... Elune steh mir bei...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11313', 'deDE', 'Eranikus, der Tyrann des Traums, ist vollständig von Elunes Licht eingehüllt. Eine nie dagewesene Stille legt sich über Moonglade.', 'Eranikus, der Tyrann des Traums, ist vollständig von Elunes Licht eingehüllt. Eine nie dagewesene Stille legt sich über Moonglade.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11314', 'deDE', 'ES BRENNT! DER SCHMERZ… SENGEND…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11315', 'deDE', 'Wieso? Wie konnte all das mit mir geschehen? Wo wart Ihr, Tyrande? Wo wart Ihr, als ich vor Elune in Ungnade fiel?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11316', 'deDE', 'Ich... ich kann Elunes Berührung erneut auf meiner Seele spüren... Sie lächelt mich an... ja... ich...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11317', 'deDE', null, 'Kümmert euch um die Verwundeten, Schwestern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11318', 'deDE', 'Es bedarf schon etwas Übung, so nahe an die Silithiden heranzukommen und dabei unbemerkt zu bleiben. Ich würde nicht empfehlen, es zu versuchen, solange es sich vermeiden lässt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11319', 'deDE', null, '%s beugt das Knie.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11320', 'deDE', null, 'Gepriesen sei Elune... Eranikus ist erlöst.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11321', 'deDE', 'Versteh\' mich nicht falsch, ich bin nicht gerade scharf drauf, mich in diesem tagein und tagaus mit Silithiden vollgestopften Tunnel zu verstecken. Aber wenn ich so die Bewegungen dieser Kreaturen beobachten kann, helfen uns diese Informationen eventuell den Krieg zu gewinnen. Also werde ich so lange hier bleiben, wie es nötig ist.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11322', 'deDE', 'Bald schon werden wir Azeroth von der grauenhaften Präsenz der Silithiden säubern. Die potentielle Zerstörung, die sie anrichten könnten, ist weit größer, als es Euch zunächst erscheinen mag, $N. Weit mächtigere und düstere Kräfte ziehen die Fäden hinter ihren Aktivitäten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11323', 'deDE', 'So lange war ich verloren... Die Verderbnis des Alptraums hatte sich meiner bemächtigt... und jetzt habt ihr... ihr alle... ihr alle habt mich erlöst. Mich aus ihrer Umklammerung befreit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11324', 'deDE', 'Aber... Malfurion, Cenarius, Ysera... sie kämpfen weiter. Sie brauchen mich. Ich muss mich sofort zurück in den Traum begeben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11325', 'deDE', 'Ich bin hier, um Euren Bericht zu empfangen.', 'Ich bin hier, um Euren Bericht zu empfangen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11326', 'deDE', 'Ich bin Eurer Gebete nicht würdig. Ihr seid wahrhaft ein Engel des Lichts. Bitte lasst mir Eure Unterstützung zuteil werden, auf dass ich zum Grabhügel zurückkehren und erneut in den Traum gelangen kann. Auch ich, wie Malfurion, habe eine Liebste, die mich erwartet... Ich muss zu ihr zurückkehren... Ich muss sie beschützen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11327', 'deDE', 'Und Helden… Ich habe das, wonach es Euch begehrt. Auf dass es erneut das Böse vernichten möge. Remulos, kümmere dich darum, dass $G unser Held : unsere Heldin; den Splitter des grünen Schwarms erhält.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11328', 'deDE', null, '%s nickt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11329', 'deDE', 'Lasst uns Nighthaven verlassen, $G Held : Heldin;. Sucht mich am Hain auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11330', 'deDE', 'Das Szepter der Sandstürme ist erneut vervollständigt, $N.$B$BIhr müsst derjenige sein, der dieses Szepter benutzt. Ihr müsst es sein, der das neue Zeitalter der Völker einläutet.$B$BWenn Ihr bereit seid, den Skarabäusgong zu schlagen und die Tore von Ahn\'Qiraj erneut zu öffnen, werde ich Euch das Szepter überreichen. Ihr müsst nur danach fragen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11331', 'deDE', 'Ich bin bereit, Anachronos. Bitte gewährt mir das Szepter der Sandstürme.', 'Ich bin bereit, Anachronos. Bitte gewährt mir das Szepter der Sandstürme.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11332', 'deDE', 'Das Szepter der Sandstürme ist erneut vervollständigt, $N.$B$BNur Ihr könnt dieses Szepter benutzen. Ihr werdet ein neues Zeitalter für Euer Volk einläuten.$B$BIhr müsst das Eintreffen der Armeen der Horde und der Allianz in Silithus abwarten, bevor Ihr den Skarabäusgong schlagen könnt. Die Zeit des Krieges steht kurz bevor.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11333', 'deDE', 'Das Szepter der Sandstürme ist erneut vervollständigt, $N.$B$BIhr müsst derjenige sein, der seine Armeen in die verfluchte Stadfeste von Ahn\'Qiraj führt und ihren Einwohnern ihre gerechte Strafe zukommen lässt.$B$BBegebt Euch nach Silithus und zeigt das Szepter der Sandstürme Jonathan dem Weisen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11334', 'deDE', 'Ich bin bereit!', 'Ich bin bereit!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11335', 'deDE', null, 'Diese Zeile ist hauptsächlich Füllmaterial.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11336', 'deDE', 'Was ist ein Belobigungsabzeichen der Allianz?', 'Was ist ein Belobigungsabzeichen der Allianz?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11337', 'deDE', null, 'Ein Belobigungsabzeichen der Allianz wird $G einem Helden : einer Heldin; überreicht, $G der : die; nicht nur $G seine : ihre; Pflicht erfüllt, sondern Leistungen selbst darüber hinaus erbringt. In allen Hauptstädten der Allianz kann man Belobigungsoffiziere der verschiedenen Fraktionen antreffen. Sprecht einfach mit dem Offizier der Fraktion, mit der Ihr Euren Ruf erhöhen wollt und gebt ihm so viele Abzeichen, wie er verlangt.$B$BÜbrigens, solltet Ihr während der Kriegsanstrengungen materielle Belohnungen einem besseren Ruf vorziehen, so sprecht mit Feldmarschall Snowfall.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11338', 'deDE', 'Wir sind in unseren Bestrebungen vereint, $R. Eine uralte böse Präsenz droht, alles Leben auf Kalimdor auszulöschen und die Welt ins Verderben zu stürzen... Diese Präsenz hegt einen abgrundtiefen Hass gegen alles, das von den großen Formern geschaffen wurde: den ewigen Titanen.$B$BWir sind die erste und letzte Verteidigungslinie gegen die Armeen von C\'Thun. Wenn wir nicht aufbegehren und uns unseren Feinden entgegenstellen, werden sie uns mit Gewissheit vernichten.$B$BDie Kriegsanstrengungen benötigen Eure Hilfe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11339', 'deDE', 'Ein Belobigungsabzeichen der Allianz wird $G einem Helden : einer Heldin; überreicht, $G der : die; nicht nur $G seine : ihre; Pflicht erfüllt, sondern Leistungen selbst darüber hinaus erbringt. In allen Hauptstädten der Allianz kann man Belobigungsoffiziere der verschiedenen Fraktionen antreffen. Sprecht einfach mit dem Offizier der Fraktion, mit der Ihr Euren Ruf erhöhen wollt und gebt ihm so viele Abzeichen, wie er verlangt.$B$BÜbrigens, solltet Ihr während der Kriegsanstrengungen materielle Belohnungen einem besseren Ruf vorziehen, so sprecht mit Feldmarschall Snowfall.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11340', 'deDE', 'Was ist ein Belobigungsabzeichen der Horde?', 'Was ist ein Belobigungsabzeichen der Horde?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11341', 'deDE', 'Ein Belobigungsabzeichen der Horde wird $G einem Helden : einer Heldin; überreicht, $G der : die; nicht nur $G seine : ihre; Pflicht erfüllt, sondern Leistungen selbst darüber hinaus erbringt. In allen Hauptstädten der Horde kann man Belobigungsoffiziere der verschiedenen Fraktionen antreffen. Sprecht einfach mit dem Offizier der Fraktion, mit der Ihr Euren Ruf erhöhen wollt und gebt ihm so viele Abzeichen, wie er verlangt.$B$BÜbrigens, solltet Ihr während der Kriegsanstrengungen materielle Belohnungen einem besseren Ruf vorziehen, so sprecht mit Kriegsherr Gorchuk.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11342', 'deDE', null, 'Ein Belobigungsabzeichen der Horde wird $G einem Helden : einer Heldin; überreicht, $G der : die; nicht nur $G seine : ihre; Pflicht erfüllt, sondern Leistungen selbst darüber hinaus erbringt. In allen Hauptstädten der Horde kann man Belobigungsoffiziere der verschiedenen Fraktionen antreffen. Sprecht einfach mit dem Offizier der Fraktion, mit der Ihr Euren Ruf erhöhen wollt und gebt ihm so viele Abzeichen, wie er verlangt.$B$BÜbrigens, solltet Ihr während der Kriegsanstrengungen materielle Belohnungen einem besseren Ruf vorziehen, so sprecht mit Kriegsherr Gorchuk.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11343', 'deDE', 'wird langsamer!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11345', 'deDE', 'friert ein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11346', 'deDE', 'geht die Puste aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11347', 'deDE', 'ist kurz davor, zu zerspringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11348', 'deDE', 'Anachronos, Ich habe den Siegelring verloren. Könntet Ihr mir einen Ersatzring geben?', 'Anachronos, Ich habe den Siegelring verloren. Könntet Ihr mir einen Ersatzring geben?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11349', 'deDE', 'Um den Hals des Rentiers hängt ein Schildchen mit dem Emblem von Kokelwälder und einem Namen - \"Metzen\".$B$BMetzen sieht Euch mit traurigen, hoffnungslosen Augen an. Es scheint fast, als ob das Rentier sich auf Grund einer unsichtbaren Kraft nicht bewegen könnte.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11350', 'deDE', 'Berieselt Metzen mit etwas Rentierstaub.', 'Berieselt Metzen mit etwas Rentierstaub.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11351', 'deDE', 'Metzens Augen leuchten voller Rentierglück auf, als Ihr ihn mit Staub berieselt. Er schüttelt sein Geweih und atmet mit einem schweren Seufzer aus, der wie eine Erleichterung klingt.$B$BMagische Energie beginnt sich um Euch herum auszudehnen. Es scheint ganz so, als ob etwas passieren würde!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11352', 'deDE', 'Selbst wenn Ihr es irgendwie schaffen solltet uns zu befreien ohne C\'Thun zu besiegen, wir sollten all unsere Bemühungen darauf konzentrieren, diese Bestie zurück in die Hölle zu schicken, aus der sie gekommen ist - KOSTE ES, WAS ES WOLLE!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11353', 'deDE', null, 'Helden der Brut? C\'Thun muss verbannt werden! Wir können Euch unterstützen $G Sterblicher : Sterbliche;.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11354', 'deDE', 'Ich werde an diesem Tag der Zeuge eines leibhaftigen Wunders! Während meiner eintausendjährigen Gefangenschaft, habe ich nicht ein lebendes Wesen, außer diesen Qiraji und Silithiden, durch diese Kammern schreiten gesehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11355', 'deDE', 'Soldaten von Kalimdor! Helden von Silithus! Einer von euch hat unseren Todfeinden einen fatalen Stoß versetzt! $N und $G seine : ihre; Verbündeten haben die Ruinen von Ahn\'Qiraj von ihrem gnadenlosen Anführer befreit! Ossirian der Narbenlose, die Geißel von Silithus, wurde endlich vernichtet und seine Armeen unschädlich gemacht!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11356', 'deDE', null, 'Stramm gestanden! Der Feind wurde gesichtet! Sofort Formation einnehmen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11357', 'deDE', 'Seid für das nächste Feuerwerk auf jeden Fall in der Nähe! Ab 18 Uhr finden die Vorführungen zu jeder vollen Stunde statt!', 'Seid für das nächste Feuerwerk auf jeden Fall in der Nähe! Ab 18 Uhr finden die Vorführungen zu jeder vollen Stunde statt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11358', 'deDE', 'Ich weiß nicht, was sie in diese Fässchen füllen, aber es geht runter wie Öl!', 'Ich weiß nicht, was sie in diese Fässchen füllen, aber es geht runter wie Öl!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11359', 'deDE', 'Nach dem Feuerwerk geben sie Becher aus, damit wir gemeinsam anstoßen können!', 'Nach dem Feuerwerk geben sie Becher aus, damit wir gemeinsam anstoßen können!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11360', 'deDE', 'Seid gegrüßt, mein Freund!', 'Seid gegrüßt, mein Freund!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11361', 'deDE', 'Seid gegrüßt, $C. Ich bin Feldmarschall Snowfall, der Kommandant der Kriegsanstrengungen. Wir haben einen langen und harten Weg vor uns, $G Junge : Mädel;, also helft bitte, wo Ihr könnt. Und scheut Euch nicht, Fragen zu stellen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11362', 'deDE', 'Gut gemacht, $G Junge : Mädel;! Die Allianz und die Horde haben jetzt alles beisammen, was wir glauben für den Krieg von Ahn\'Qiraj zu benötigen. Die letzten Vorräte werden jetzt nach Burg Cenarius in Silithus transportiert und dort innerhalb eines Tages eintreffen. Danach wird $G ein mutiger Held : eine mutige Heldin; mit dem Szepter der Sandstürme den Skarabäusgong schlagen und so die Tore von Ahn\'Qiraj öffnen. Wenn diese Tore sich öffnen, beginnt der Krieg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11363', 'deDE', 'Gut gemacht, $G Junge : Mädel;! Die Allianz und die Horde haben jetzt alles beisammen, was wir glauben für den Krieg von Ahn\'Qiraj zu benötigen. Die letzten Vorräte werden jetzt nach Burg Cenarius in Silithus transportiert und dort innerhalb von $2113w Tagen eintreffen. Danach wird $G ein mutiger Held : eine mutige Heldin; mit dem Szepter der Sandstürme den Skarabäusgong schlagen und so die Tore von Ahn\'Qiraj öffnen. Wenn diese Tore sich öffnen, beginnt der Krieg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11364', 'deDE', 'Habt Ihr schon die guten Neuigkeiten gehört, $C? Die Allianz hat nun sämtliche Materialien und Vorräte für den Krieg beisammen! Jetzt warten wir nur noch darauf, dass die Horde auch alles zusammenkriegt. Wenn es soweit ist, werden wir sämtliche Materialien und Vorräte nach Silithus transportieren. Danach sollte es nicht mehr lange dauern, bis der Krieg von Ahn\'Qiraj losgeht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11365', 'deDE', 'Gute Frage, $C. Nach meinem letzten Informationsstand haben wir bisher $1997w der benötigten $1998w Kupferbarren zusammen. Außerdem wurde ich davon unterrichtet, dass wir $2002w der benötigten $2003w Eisenbarren und $2011w der benötigten $2012w Thoriumbarren zusammen haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11366', 'deDE', 'Kräuter, Kräuter, hmmm, lasst mich mal sehen. Ah, ja, hier sind sie. Bis jetzt haben wir $2047w von $2048w Einheiten Würgetang, $2053w von $2055w lila Lotussen und $2057w von $2058w Einheiten Arthas Tränen, die wir benötigen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11367', 'deDE', 'Ich habe gerade erst die letzten Zahlen erhalten. Scheint, als hätten wir bisher $2060w der benötigten $2061w leichten Lederstücke. Was den Rest angeht, deutet der Bericht darauf hin, dass wir bisher $2063w von $2064w mittleren Lederstücken und $2069w von $2071w dicken Lederstücken beisammen haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11368', 'deDE', 'Das Sammeln von Verbänden verläuft nach Plan. Was Leinenverbände angeht, haben wir bisher $2076w von $2077w. Seidenverbände haben wir bisher $2082w von $2083w gezählt und wir haben $2088w der benötigten $2090w Runenstoffverbände.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11369', 'deDE', 'Was gekochte Güter angeht, haben wir bisher $2092w Regenbogenthunfische von den angefragten $2093w beisammen und eingelagert. Außerdem haben wir $2098w von $2099w gerösteten Raptoren und $2101w von $2103w Tüpfelgelbschwänzen auf Eis gelagert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11370', 'deDE', 'Throm\'ka, $C der Horde, steht aufrecht und stolz! Ich bin Kriegsherr Gorchuk, der Kommandeur der Kriegsanstrengungen der Horde. Es liegen schwierige Zeiten vor uns, aber wir werden sie meistern, wie immer.$B$BWenn Ihr Fragen habt, stellt sie jetzt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11371', 'deDE', 'Die Zeit des Krieges wird bald beginnen, $C. Sowohl die Horde als auch die Allianz haben ihre Vorbereitungen abgeschlossen. Sämtliche Kriegsvorräte werden nach Silithus transportiert, während wir uns hier unterhalten. Ich schätze, dass alles innerhalb eines Tages dort eintreffen sollte. Wenn die Vorbereitungen dort abgeschlossen sind, wird derjenige, der die schwere Aufgabe erfüllt hat das Szepter der Sandstürme wieder zusammenzusetzen, damit den Skarabäusgong schlagen und die Tore öffnen. Dann wird eine Schlacht beginnen, wie sie dieses Zeitalter noch nie erlebt hat.$B$BLok\'tar Ogar!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11372', 'deDE', 'Die Zeit des Krieges wird bald beginnen, $C. Sowohl die Horde als auch die Allianz haben ihre Vorbereitungen abgeschlossen. Sämtliche Kriegsvorräte werden nach Silithus transportiert, während wir uns hier unterhalten. Ich schätze, dass alles innerhalb von $2113w Tagen dort eintreffen sollte. Wenn die Vorbereitungen dort abgeschlossen sind, wird derjenige, der die schwere Aufgabe erfüllt hat das Szepter der Sandstürme wieder zusammenzusetzen, damit den Skarabäusgong schlagen und die Tore öffnen. Dann wird eine Schlacht beginnen, wie sie dieses Zeitalter noch nie erlebt hat.$B$BLok\'tar Ogar!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11373', 'deDE', '$C, die Horde hat bereits alles beisammen, was wir für den Krieg von Ahn\'Qiraj benötigen. Jetzt warten wir nur noch darauf, dass die Schwächlinge von der Allianz es uns gleichtun. Wenn die dass endlich mal schaffen, fangen wir mit dem Transport nach Silithus an. Es sollte danach nicht mehr lange dauern, bis der Krieg beginnt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11374', 'deDE', 'Von den $1998w Kupferbarren, die wir benötigen, haben wir bisher $2018w. Was Zinnbarren angeht, haben wir bisher $2005w von $2006w benötigten. Es wurden bisher $2008w von $2009w benötigten Mithrilbarren hergestellt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11375', 'deDE', 'Ihr habe diesbezüglich gerade Neuigkeiten erhalten. Wir haben momentan $2021w von $2020w Friedensblumen, $2050w von $2051w Feuerblüten und $2054w von $2055w lila Lotussen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11376', 'deDE', 'Warte mal einen Moment, ich muss mir diese Nummern wieder ins Gedächtnis rufen. Sieht aus, als hätten wir bisher $2066w von $2067w schweren Lederstücken beisammen. Außerdem haben wir $2070w von $2071w dicken Lederstücken und $2073w von $2074w unverwüstlichen Lederstücken auf Lager.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11377', 'deDE', 'Verbände? Glaubt Ihr etwa, Ihr würdet lange genug überleben, um die zu brauchen? Ha! Na gut, $R, ich werde es Euch sagen. Von den $2080w benötigten Wollverbänden haben wir bisher $2079w beisammen. Außerdem haben wir bisher $2085w der benötigten $2086w Magiestoffverbände und $2089w der benötigten $2090w Runenstoffverbände, um die wir gebeten wurden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11378', 'deDE', 'Essen, das ist mal ein Thema, in das ich mich so richtig verbeißen kann. Ich habe gehört, dass wir $2095w von $2096w mageren Wolfsteaks, $2102w von $2103w Tüpfelgelbschwänzen und $2105w von $2106w gebackenen Lachsen gelagert haben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11379', 'deDE', 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengungen sind vereinte Bemühungen der Allianz und der Horde, um uns auf den Krieg gegen die Silithiden und ihre Meister in Ahn\'Qiraj vorzubereiten. Im Militärviertel von Eisenschmiede sammeln wir verschiedene Vorräte und Materialien für den Krieg und dabei benötigen wir Eure Hilfe.$B$BDürfte ich Euch darum bitten, mit unserem Kommandanten zu reden und einen Teil beizusteuern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11380', 'deDE', null, 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengungen sind vereinte Bemühungen der Allianz und der Horde, um uns auf den Krieg gegen die Silithiden und ihre Meister in Ahn\'Qiraj vorzubereiten. Im Militärviertel von Eisenschmiede sammeln wir verschiedene Vorräte und Materialien für den Krieg und dabei benötigen wir Eure Hilfe.$B$BDürfte ich Euch darum bitten, mit unserem Kommandanten zu reden und einen Teil beizusteuern?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11381', 'deDE', null, 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengungen sind vereinte Bemühungen der Allianz und der Horde, um uns auf den Krieg gegen die Silithiden und ihre Meister in Ahn\'Qiraj vorzubereiten. Im Militärviertel von Eisenschmiede sammeln wir verschiedene Vorräte und Materialien für den Krieg und dabei benötigen wir Eure Hilfe.$B$BDürfte ich Euch darum bitten, mit unserem Kommandanten zu reden und einen Teil beizusteuern?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11382', 'deDE', 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengungen sind vereinte Bemühungen der Allianz und der Horde, um uns auf den Krieg gegen die Silithiden und ihre Meister in Ahn\'Qiraj vorzubereiten. Im Tal der Geister von Orgrimmar sammeln wir verschiedene Vorräte und Materialien für den Krieg und dabei benötigen wir Eure Hilfe.$B$BDürfte ich Euch darum bitten, mit unserem Kommandanten zu reden und einen Teil beizusteuern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11383', 'deDE', null, 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengungen sind vereinte Bemühungen der Allianz und der Horde, um uns auf den Krieg gegen die Silithiden und ihre Meister in Ahn\'Qiraj vorzubereiten. Im Tal der Geister von Orgrimmar sammeln wir verschiedene Vorräte und Materialien für den Krieg und dabei benötigen wir Eure Hilfe.$B$BDürfte ich Euch darum bitten, mit unserem Kommandanten zu reden und einen Teil beizusteuern?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11384', 'deDE', null, 'Seid gegrüßt, $C. Ich hoffe Ihr seid gekommen, um Euch über den Stand der Kriegsanstrengungen für den Krieg von Ahn\'Qiraj zu informieren. Wir werden eine Menge Hilfe benötigen.$B$BDie Kriegsanstrengungen sind vereinte Bemühungen der Allianz und der Horde, um uns auf den Krieg gegen die Silithiden und ihre Meister in Ahn\'Qiraj vorzubereiten. Im Tal der Geister von Orgrimmar sammeln wir verschiedene Vorräte und Materialien für den Krieg und dabei benötigen wir Eure Hilfe.$B$BDürfte ich Euch darum bitten, mit unserem Kommandanten zu reden und einen Teil beizusteuern?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11385', 'deDE', 'Seid gegrüßt, $C. Ich bin Leoren, Stallmeister der Burg Cenarius.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11386', 'deDE', 'Da ist ein Stapel verpackter Geschenke.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11387', 'deDE', 'Auf dem Schild dieses Geschenks steht: \"Ab 40 Jahren und älter.\"', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11388', 'deDE', 'Nehmt Euer Geschenk von dem Stapel.', 'Nehmt Euer Geschenk von dem Stapel.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11389', 'deDE', 'Seid gegrüßt, Bürger.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11390', 'deDE', 'Die ganze Welt steht in Eurer Schuld und der Eurer Verbündeten, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11391', 'deDE', 'Wenn C\'Thun überlebt hätte, wäre unsere Freiheit bedeutungslos gewesen. Ihr seid wahrhaft $G der Held : die Heldin; der Prophezeiung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11392', 'deDE', null, 'Jeder wird erfahren, was in diesem verfluchten Tempel vorgefallen ist, $G Held : Heldin;. Es wird in den Geschichtsbüchern niedergeschrieben werden. Die Geschichten werden zu Legenden werden und überall, von Azeroth bis hin zum Nether selbst, zu hören sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11393', 'deDE', 'Jetzt, wo der Krieg voll im Gange ist, fehlt es uns nicht an Nachschub von fähigen Kämpfern für die Allianz und Horde.$B$BWenn wir sie nur davon abhalten könnten, sich gegenseitig an die Kehle zu gehen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11394', 'deDE', null, 'Wartet! Es scheint, als hätte unser Späher hat etwas gesichtet!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11395', 'deDE', 'Auf dem Schild dieses Geschenks steht: \"Ab 20 Jahren und älter.\"', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11396', 'deDE', 'Auf dem Schild dieses Geschenks steht: \"Ab 10 Jahren und älter.\"', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11397', 'deDE', 'Dieses Winterhauchfest werdet Ihr meine Klinge schmecken! Yeargh!', 'Dieses Winterhauchfest werdet Ihr meine Klinge schmecken! Yeargh!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11398', 'deDE', 'Kein lausiger $C reißt sich unsere Winterhauchbeute unter den Nagel! Angriff!', 'Kein lausiger $C reißt sich unsere Winterhauchbeute unter den Nagel! Angriff!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11399', 'deDE', 'Diese Landratten sind wegen dem Rentier hier! Zeigt\'s ihnen!', 'Diese Landratten sind wegen dem Rentier hier! Zeigt\'s ihnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11400', 'deDE', 'Es ist ein Befreiungsversuch! Last niemanden entkommen!', 'Es ist ein Befreiungsversuch! Last niemanden entkommen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11401', 'deDE', 'Überbringt Altvater Winter meine Grüße, Abschaum...', 'Überbringt Altvater Winter meine Grüße, Abschaum...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11402', 'deDE', 'Humbug! Sterbt!', 'Humbug! Sterbt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11403', 'deDE', 'Es ist ein Befreiungsversuch! Tötet sie alle!', 'Es ist ein Befreiungsversuch! Tötet sie alle!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11404', 'deDE', 'Das Einzige, was Metzen dieses Winterhauchfest tun wird, ist sich an einem Bratspieß zu drehen!', 'Das Einzige, was Metzen dieses Winterhauchfest tun wird, ist sich an einem Bratspieß zu drehen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11405', 'deDE', null, 'Das Tagebuch von Jandice Barov ist voll von irrem Geschreibsel über Untote. Gegen Ende des Tagebuchs ist etwas verzeichnet, das wie ein Muster für die Herstellung einer Art Tasche aussieht. Ihr versteht nicht genug von der Schneiderei, um es zu entziffern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11406', 'deDE', null, 'Das Tagebuch von Jandice Barov ist voll von irrem Geschreibsel über Untote. Gegen Ende des Tagebuchs ist etwas verzeichnet, das wie ein Muster für die Herstellung einer Art Tasche aussieht. Diese Art von Tasche wird von Hexenmeistern benutzt, um Seelensplitter aufzubewahren.Wollt Ihr das Muster in euer Buch übertragen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11407', 'deDE', '<Kopiere das Muster in mein Buch.>', '<Kopiere das Muster in mein Buch.>', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11408', 'deDE', null, '%s löst ihre Umklammerung des Tagebuchs.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11409', 'deDE', '%s lässt einen Kampfschrei los!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11410', 'deDE', null, 'Das Tagebuch von Jandice Barov ist voll von irrem Geschreibsel über Untote. Gegen Ende des Tagebuchs ist etwas verzeichnet, das wie ein Muster für die Herstellung einer Art Tasche aussieht. Diese Art von Tasche wird von Hexenmeistern benutzt, um Seelensplitter aufzubewahren. Ihr wisst schon, wie man diese Tasche herstellt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11411', 'deDE', 'Nur jene, die bewiesen haben, dass sie den Furbolgs der Holzschlundfeste nicht feindlich gesinnt sind, können sich gefahrlos durch diese Feste bewegen. Habt ihr Euch darum bemüht, unser Vertrauen zu gewinnen, $G Fremder : Fremde;?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11412', 'deDE', 'Wonach begehrt es Euch, $C?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11413', 'deDE', 'Ich werde warten.', 'Ich werde warten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11414', 'deDE', 'Neu dieses Jahr: der PX-238 Winterwundervolt. Tretet hervor, scheut Euch nicht!$B$BFüllt aber diese Formulare aus, bevor Ihr es versucht, wir beheben noch immer ein paar Macken an diesem Ding...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11415', 'deDE', 'Seid gegrüßt, $C! Ich bin ein Belobigungsoffizier im Auftrag von Darnassus. Es ist meine Aufgabe, Abenteurern, die Belobigungsabzeichen erhalten haben, behilflich zu sein.$B$BIhr könnt mir verschiedene Mengen von Abzeichen bringen, aber es ist am besten für Euch, wenn Ihr mir 10 auf einmal bringt. Ich werde Eure Taten in unsere Aufzeichnungen aufnehmen, wenn Ihr mir die Abzeichen bringt. Ihr könnt Euch so die Anerkennung von Darnassus für Eure Dienste verdienen.', 'Seid gegrüßt, $C! Ich bin ein Belobigungsoffizier im Auftrag von Darnassus. Es ist meine Aufgabe, Abenteurern, die Belobigungsabzeichen erhalten haben, behilflich zu sein.$B$BIhr könnt mir verschiedene Mengen von Abzeichen bringen, aber es ist am besten für Euch, wenn Ihr mir 10 auf einmal bringt. Ich werde Eure Taten in unsere Aufzeichnungen aufnehmen, wenn Ihr mir die Abzeichen bringt. Ihr könnt Euch so die Anerkennung von Darnassus für Eure Dienste verdienen.', '31882');
