INSERT INTO `broadcast_text_locale` VALUES ('11423', 'deDE', '%s lässt eine mächtige Attacke los.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11424', 'deDE', '%s hört den Schlachtruf und kommt seinem Meister zuhilfe.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11425', 'deDE', '%s wirft seinen mächtigen Schatten über ganz Silithus.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11426', 'deDE', '%s befreit sich aus seinem Kokon unter dem Bau des Ashischwarms.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11427', 'deDE', '$N, Champion des bronzenen Drachenschwarms, hat den Skarabäusgong in Silithus geläutet. Die uralten Tore von Ahn\'Qiraj öffnen sich und offenbaren das Grauen eines längst vergessenen Krieges.', '$N, Champion des bronzenen Drachenschwarms, hat den Skarabäusgong in Silithus geläutet. Die uralten Tore von Ahn\'Qiraj öffnen sich und offenbaren das Grauen eines längst vergessenen Krieges.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11428', 'deDE', 'Schlachtet sie alle ab. Ihre Überbleibsel wird der Meister verzehren...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11429', 'deDE', 'Schon bald werdet ihr den Preis für euer Einmischen bezahlen, Sterbliche... Der Meister ist fast bereit... Und wenn er aufersteht, wird diese Welt aufhören zu existieren.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11430', 'deDE', 'Geschenke für jedermann! Altvater Winter hat für alle Geschenke unter den Baum gelegt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11431', 'deDE', 'Glaubt Ihr etwa, ihr hättet gewonnen? So einfach? Die Legion hat ihren Marsch gerade erst begonnen. C\'Thuns Armeen werden diese Welt auslöschen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11432', 'deDE', 'Riesige Qirajiresonanzkristalle brechen aus der Erde Kalimdors an die Oberfläche durch. Giganten aus Obsidiangestein steigen aus der frisch aufgebrochenen Erde hervor.', 'Riesige Qirajiresonanzkristalle brechen aus der Erde Kalimdors an die Oberfläche durch. Giganten aus Obsidiangestein steigen aus der frisch aufgebrochenen Erde hervor.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11433', 'deDE', 'Haucht Euren letzten Atemzug aus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11434', 'deDE', 'Soldaten, Hauptmänner, gebt den Kampf nicht auf!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11435', 'deDE', 'Unverschämter Narr! Ich werde Euch höchstpersönlich töten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11436', 'deDE', 'Fandral! Deine Zeit ist gekommen! Geh und verstecke dich im Smaragdgrünen Traum, und bete, dass wir dich nie finden werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11437', 'deDE', 'Staghelm wird winseln und um sein Leben betteln, genau wie sein räudiger Sohn! Eintausend Jahre der Ungerechtigkeit werden heute enden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11438', 'deDE', 'Wir kennen keine Furcht! Und wir werden unseren Feinden den Tod bringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11439', 'deDE', 'Wir werden nicht länger hinter verbarrikadierten Toren und Mauern aus Stein ausharren! Die Rache wird unser sein! Selbst die Drachen werden im Angesicht unseres Zornes erzittern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11440', 'deDE', 'Die Zeit der Vergeltung ist gekommen! Lasst uns die Herzen unserer Feinde mit Dunkelheit füllen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11441', 'deDE', '%s spürt Eure Angst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11442', 'deDE', 'Ihr habt heiligen Boden entweiht; seid gewiss, dass Ihr dafür gerichtet werdet! Eindringlinge werden vernichtet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11443', 'deDE', 'Ich verurteile Euch zum Tode!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11444', 'deDE', 'Ich diene bis zum letzten Atemzug!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11445', 'deDE', 'Seid ihr so begierig darauf zu sterben? Ich werde Euch mit Freuden dabei behilflich sein…', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11446', 'deDE', 'Möge Euer Tod als ein Beispiel dienen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11447', 'deDE', 'Ihr zögert das Unvermeidliche nur hinaus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11448', 'deDE', 'spürt Eure Angst.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11449', 'deDE', 'Sande der Wüste, erhebt euch und verdunkelt die Sonne!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11450', 'deDE', 'Ihr wurdet vernichtet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11451', 'deDE', 'Ich... habe... versagt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11452', 'deDE', 'Mein Bruder, nein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11453', 'deDE', 'Vor dem Tod gibt es kein Entrinnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11454', 'deDE', 'Vek\'lor, ich spüre deinen Schmerz!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11455', 'deDE', 'Euer Schicksal ist besiegelt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11456', 'deDE', 'Ab 18 Uhr wird von der höchsten Klippe aus ein herrliches Feuerwerk abgeschossen!', 'Ab 18 Uhr wird von der höchsten Klippe aus ein herrliches Feuerwerk abgeschossen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11457', 'deDE', 'Diese Fässchen sind voll des besten Starkbieres! Trinkt, mein Freund, trinkt und feiert!', 'Diese Fässchen sind voll des besten Starkbieres! Trinkt, mein Freund, trinkt und feiert!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11458', 'deDE', 'Ich habe gehört, dass nach dem Feuerwerk Trinkbecher an die Tische gebracht werden. Wenn es soweit ist, lasst uns aufeinander anstoßen!', 'Ich habe gehört, dass nach dem Feuerwerk Trinkbecher an die Tische gebracht werden. Wenn es soweit ist, lasst uns aufeinander anstoßen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11459', 'deDE', 'Grüße, $C! Willkommen zu den Festlichkeiten!', 'Grüße, $C! Willkommen zu den Festlichkeiten!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11460', 'deDE', 'Die ganze Nacht lang wird es immer wieder Feuerwerke geben. Von 18 Uhr an bis zum Morgengrauen wird der Himmel über Booty Bay in den schönsten Farben erstrahlen!', 'Die ganze Nacht lang wird es immer wieder Feuerwerke geben. Von 18 Uhr an bis zum Morgengrauen wird der Himmel über Booty Bay in den schönsten Farben erstrahlen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11461', 'deDE', 'Wow! Diese Fässchen sind mit hartem Zeug gefüllt! Haut mich doch glatt aus den Socken!', 'Wow! Diese Fässchen sind mit hartem Zeug gefüllt! Haut mich doch glatt aus den Socken!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11462', 'deDE', 'Bleibt nach dem Feuerwerk noch etwas hier. Sucht Euch einen Trinkbecher und stoßt mit Euren besten Freunden an!', 'Bleibt nach dem Feuerwerk noch etwas hier. Sucht Euch einen Trinkbecher und stoßt mit Euren besten Freunden an!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11463', 'deDE', 'Auf geht\'s! Zeit zu feiern!', 'Auf geht\'s! Zeit zu feiern!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11464', 'deDE', 'Tage wie dieser lassen mich immer ganz gefühlsduselig werden…', 'Tage wie dieser lassen mich immer ganz gefühlsduselig werden…', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11465', 'deDE', 'Ich könnte jetzt einen Kuss vertragen. Irgendeine Ahnung, wo ich einen bekommen könnte?', 'Ich könnte jetzt einen Kuss vertragen. Irgendeine Ahnung, wo ich einen bekommen könnte?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11466', 'deDE', 'Hah! Dieser Tauren möchte einen Kuss!', 'Hah! Dieser Tauren möchte einen Kuss!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11467', 'deDE', 'Ein fröhliches Winterhauchfest, mein Freund!', 'Ein fröhliches Winterhauchfest, mein Freund!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11468', 'deDE', 'Frohe Feiertage und Frieden wünsche ich Euch!', 'Frohe Feiertage und Frieden wünsche ich Euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11469', 'deDE', 'Frohe Feiertage, Freund!', 'Frohe Feiertage, Freund!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11470', 'deDE', 'Ah, ein Mistelzweig…', 'Ah, ein Mistelzweig…', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11471', 'deDE', 'Kniet vor mir nieder, Sterblicher! Kniet nieder vor Zod!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11472', 'deDE', 'Warum... steeeeerbt... Ihr nicht einfach?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11473', 'deDE', '%s strotzt vor Energie.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11474', 'deDE', '%s entzieht Euch Euer Mana und versteinert Euch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11475', 'deDE', 'Verbrennt im Hass, $R.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11476', 'deDE', '%s ist geschwächt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11477', 'deDE', 'Ich habe gelogen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11478', 'deDE', 'Jetzt gibt\'s Prügel!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11479', 'deDE', null, 'Tolles Wetter hier, oder was meint Ihr?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11480', 'deDE', 'Oh Umi, bitte lehrt mich noch einmal, wie man einen ruhigen mechanischen Yeti herstellt. Ich vermisse ihn so.', 'Oh Umi, bitte lehrt mich noch einmal, wie man einen ruhigen mechanischen Yeti herstellt. Ich vermisse ihn so.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11481', 'deDE', null, 'Na gut, $N. Ich sehe, dass Ihr Euren Fehler eingesehen und zur Euch erneut der Ingenieurskunst zugewandt habt.$B$BViel Spaß mit Eurem Yeti!!!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11482', 'deDE', null, 'Warum kann man nie ein Gyrochronatom finden, wenn man mal eins braucht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11483', 'deDE', null, 'Ich werde zuletzt lachen... die werden schon sehen! Oh, hallo $C, habe dich gar nicht bemerkt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11484', 'deDE', null, 'Erfinderei, $G mein Lieber : meine Liebe;, die besteht zu 2% aus in Flaschen abgefüllter Eingebung, zu 4% aus Sonneneinwirkung, zu 6% aus Gewittern, die sich bis zum Nachmittag legen, und zu 93% aus Rumplesnicker.$B$BWartet mal, kommt das insgesamt auf hundert raus? Ich muss hier doch irgendwo meine Notizen rumliegen haben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11485', 'deDE', 'Wir müssen herausfinden, was die Qiraji an diese Monster verfüttern. Die sind ja riesig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11486', 'deDE', 'Wenn wir den Kolossus studieren und verstehen können, wird uns das einen wichtigen Vorteil gegenüber den Qiraji verschaffen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11487', 'deDE', null, 'Das Geheimnis der Macht der Kolosse könnte zu allen möglichen Innovationen in der Wissenschaft führen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11488', 'deDE', 'Wo ist Urahne Graveborn?', 'Wo ist Urahne Graveborn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11489', 'deDE', 'Die inneren Tore von Ahn\'Qiraj brechen auf.', 'Die inneren Tore von Ahn\'Qiraj brechen auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11490', 'deDE', 'Urahne Graveborn verbringt seine Zeit in der Nähe von Brill in Tirisfal.', 'Urahne Graveborn verbringt seine Zeit in der Nähe von Brill in Tirisfal.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11491', 'deDE', 'Wo ist Urahnin Moonstrike?', 'Wo ist Urahnin Moonstrike?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11492', 'deDE', 'Urahnin Moonstrike kann in Scholomance in den Westlichen Pestländern aufgefunden werden.', 'Urahnin Moonstrike kann in Scholomance in den Westlichen Pestländern aufgefunden werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11493', 'deDE', 'Wo ist Urahne Darkcore?', 'Wo ist Urahne Darkcore?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11494', 'deDE', 'Urahne Darkcore ist in Unterstadt.', 'Urahne Darkcore ist in Unterstadt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11495', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11496', 'deDE', 'Zeigt ihnen, was ihr drauf habt! Angriff!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11497', 'deDE', 'Wo ist Urahne Obsidian?', 'Wo ist Urahne Obsidian?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11498', 'deDE', 'Urahnin Obsidian wandert nahe des Grabmals im Silberwald umher.', 'Urahnin Obsidian wandert nahe des Grabmals im Silberwald umher.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11499', 'deDE', 'Wo ist Urahne Snowcrown?', 'Wo ist Urahne Snowcrown?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11500', 'deDE', 'Urahne Snowcrown kann in der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern angetroffen werden.', 'Urahne Snowcrown kann in der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11501', 'deDE', 'Wo ist Urahne Highpeak?', 'Wo ist Urahne Highpeak?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11502', 'deDE', 'Urahne Highpeak wacht auf einem Hügel in der Mitte des Hinterlands über die Region.', 'Urahne Highpeak wacht auf einem Hügel in der Mitte des Hinterlands über die Region.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11503', 'deDE', 'Wo ist Urahne Windrun?', 'Wo ist Urahne Windrun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11504', 'deDE', 'Urahne Windrun hält sich im Turm der Kronenwache in den Östlichen Pestländern auf.', 'Urahne Windrun hält sich im Turm der Kronenwache in den Östlichen Pestländern auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11505', 'deDE', 'Wo ist Urahne Farwhisper?', 'Wo ist Urahne Farwhisper?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11506', 'deDE', 'Urahne Farwhisper wandert die Straßen von Stratholme entlang.', 'Urahne Farwhisper kann auf den Straßen von Stratholme angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11507', 'deDE', 'Wo ist Urahne Ironband?', 'Wo ist Urahne Ironband?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11508', 'deDE', 'Urahne Ironband lebt in der Blackcharhöhle in der sengenden Schlucht.', 'Urahne Ironband lebt in der Blackcharhöhle in der sengenden Schlucht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11509', 'deDE', 'Wo ist Urahne Goldwell?', 'Wo ist Urahne Goldwell?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11510', 'deDE', 'Urahne Goldwell verbringt seine Zeit im Dorf Kharanos in Dun Morogh.', 'Urahne Goldwell verbringt seine Zeit in Kharanos in Dun Morogh.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11511', 'deDE', null, 'Fort mit euch, elende Kreaturen! Euresgleichen hat in dieser Welt nichts verloren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11512', 'deDE', 'Wo ist Urahne Bronzebeard?', 'Wo ist Urahne Bronzebeard?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11513', 'deDE', 'Zeigt euren Mut, Männer und Frauen von Azeroth! Schlagt die Qirajiinvasion zurück!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11514', 'deDE', 'Urahne Bronzebeard kann im Zuhause der Ahnen der Bronzebeards angetroffen werden, also in Eisenschmiede.', 'Urahne Bronzebeard kann im Zuhause der Ahnen der Bronzebeards angetroffen werden, also in Eisenschmiede.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11515', 'deDE', null, 'Zeigt keine Schwäche! Vernichtet diese widerwärtigen Kreaturen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11516', 'deDE', 'Wo ist Urahne Silvervein?', 'Wo ist Urahne Silvervein?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11517', 'deDE', null, 'Elune sei mit Euch, $C.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11518', 'deDE', null, 'Die Hohepriesterin konnte Darnassus nicht verlassen. Fandral hat sehr schlechte Laune.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11519', 'deDE', null, 'Tyrande schickt ihre Gebete. Die Mondmutter segnet uns alle.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11520', 'deDE', 'Der Entschluss ist gefallen. Ich werde die Macht von Kalimdor anführen. Stellt sicher, dass eure Soldaten diesen Fakt verstehen und akzeptieren. Es gibt schon genug Blutvergießen. Lasst es kein weiteres zwischen unseren Fraktionen geben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11521', 'deDE', 'Schon bald, Frischlinge. Schon bald werden die Armeen der Horde eintreffen. Wir werden marschieren. Auf die verfluchten Tore werden wir zumarschieren und alle vernichten, die es wagen, sich uns in den Weg zu stellen. Wir werden ihnen zeigen, was es heißt, sich der Macht von Kalimdor entgegenzustellen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11522', 'deDE', null, 'Ihr seid sehr wortgewand, Hochfürst Saurfang.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11523', 'deDE', null, 'Was Eure Warnung angeht - sorgt Euch nicht. Unsere Truppen sind sich der Befehlsfolge bewusst. Wir kämpfen Seite an Seite. Lasst uns hoffen, dass Eure eigenen Truppen dies nicht vergessen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11524', 'deDE', 'Urahne Silvervein ist in der Nähe von Thelsamar, in Loch Modan.', 'Urahne Silvervein kann nahe Thelsamar in Loch Modan angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11525', 'deDE', '%s verfällt beim Anblick seiner gefallenen Truppen in Raserei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11526', 'deDE', 'Dafür werdet ihr bezahlen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11527', 'deDE', 'FÜR DIE MACHT VON KALIMDOR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11528', 'deDE', 'DOOMHAMMER! FÜHRE MEINE HAND!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11529', 'deDE', 'Ich werde euch vernichten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11530', 'deDE', 'Wo ist Urahne Morndeep?', 'Wo ist Urahne Morndeep?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11531', 'deDE', 'Urahne Morndeep verbringt seine Zeit im Ring des Gesetzes der Blackrocktiefen.', 'Urahne Morndeep verbringt seine Zeit im Ring des Gesetzes der Blackrocktiefen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11532', 'deDE', 'Wo ist Urahne Stonefort?', 'Wo ist Urahne Stonefort?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11533', 'deDE', 'Urahne Stonefort hat sich in die Verliese der Blackrockspitze begeben.', 'Urahne Stonefort hat sich in die Verliese der Blackrockspitze begeben.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11534', 'deDE', 'Wo ist Urahne Rumblerock?', 'Wo ist Urahne Rumblerock?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11535', 'deDE', 'Urahne Rumblerock verbringt die Jahre in einer Höhle am Gipfel des Schreckensfelsens in der brennenden Steppe.', 'Urahne Rumblerock verbringt die Jahre in einer Höhle am Gipfel des Schreckensfelsens in der brennenden Steppe.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11536', 'deDE', 'Kolosse im Anmarsch! Mindestens 10 Einheiten! SIE KOMMEN VON SÜDEN HER!', 'Kolosse im Anmarsch! Mindestens 10 Einheiten! SIE KOMMEN VON SÜDEN HER!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11537', 'deDE', 'Wo ist Urahnin Starsong?', 'Wo ist Urahnin Starsong?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11538', 'deDE', 'HALTET DIE LINIE! LASST NICHTS PASSIEREN!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11539', 'deDE', 'Soweit ich weiß, hält sich Urahnin Starsong im Versunkenen Tempel in den Sümpfen des Elends auf.', 'Soweit ich weiß, hält sich Urahnin Starsong im Versunkenen Tempel in den Sümpfen des Elends auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11540', 'deDE', 'FÜR KALIMDOR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11541', 'deDE', 'WIR KENNEN KEINE FURCHT, SOLDATEN! HEUTE STERBEN WIR FÜR KALIMDOR!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11542', 'deDE', 'Wo ist Urahne Stormbrow?', 'Wo ist Urahne Stormbrow?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11543', 'deDE', 'Urahne Stormbrow ist höchstwahrscheinlich im Dorf Goldhain im Wald von Elwynn.', 'Urahne Stormbrow ist höchstwahrscheinlich im Dorf Goldhain im Wald von Elwynn.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11544', 'deDE', 'Wo ist Urahne Winterhoof?', 'Wo ist Urahne Winterhoof?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11545', 'deDE', 'Winterhoof? Ich hörte, er lebt in Booty Bay.', 'Winterhoof? Ich hörte, er lebt in Booty Bay.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11546', 'deDE', 'Wo ist Urahnin Skychaser?', 'Wo ist Urahnin Skychaser?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11547', 'deDE', 'Urahnin Skychaser wacht von der Späherkuppe aus über Westfall.', 'Urahnin Skychaser wacht von der Späherkuppe aus über Westfall.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11548', 'deDE', 'Wo ist Urahnin Dawnstrider?', 'Wo ist Urahnin Dawnstrider?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11549', 'deDE', 'Urahnin Dawnstrider kann in der brennenden Steppe am Flammenkamm angetroffen werden.', 'Urahnin Dawnstrider kann in der brennenden Steppe am Flammenkamm angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11550', 'deDE', 'Wo ist Urahne Hammershout?', 'Wo ist Urahne Hammershout?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11551', 'deDE', 'Urahne Hammershout hält sich innerhalb der Mauern von Sturmwind auf.', 'Urahne Hammershout hält sich innerhalb der Mauern von Sturmwind auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11552', 'deDE', 'Wo ist Urahne Bellowrage?', 'Wo ist Urahne Bellowrage?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11553', 'deDE', 'Urahne Bellowrage wacht von einem Felsgrat aus über das Dunkle Portal in den verwüsteten Landen.', 'Urahne Bellowrage wacht von einem Felsgrat aus über das Dunkle Portal in den verwüsteten Landen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11554', 'deDE', 'Wo ist Urahne Bladeswift?', 'Wo ist Urahne Bladeswift?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11555', 'deDE', 'Urahne Bladeswift wandert die Pfade von Darnassus in Teldrassil entlang.', 'Urahne Bladeswift wandert die Pfade von Darnassus in Teldrassil entlang.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11556', 'deDE', 'Wo ist Urahne Skygleam?', 'Wo ist Urahne Skygleam?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11557', 'deDE', 'Urahne Skygleam kann nahe des Ravencrest-Monuments in Azshara angetroffen werden.', 'Urahne Skygleam kann nahe des Ravencrest-Monuments in Azshara angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11558', 'deDE', 'Wo ist Urahne Bladeleaf?', 'Wo ist Urahne Bladeleaf?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11559', 'deDE', 'Das Dorf Dolanaar auf Teldrassil ist das Zuhause des Urahnen Bladeleaf.', 'Das Dorf Dolanaar auf Teldrassil ist das Zuhause des Urahnen Bladeleaf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11560', 'deDE', 'Meine Arbeit hier ist getan. Ich werde Mar\'alith und den anderen auf Burg Cenarius auf jeden Fall mitteilen, dass Ihr mir hier etwas ausgeholfen habt. Seid hier drinnen aber vorsichtig, von jetzt an seid Ihr auf Euch allein gestellt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11561', 'deDE', 'Wo ist Urahnin Starweave?', 'Wo ist Urahnin Starweave?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11562', 'deDE', 'Urahnin Moonstrike kann in der Küstenstadt Auberdine an der Dunkelküste angetroffen werden.', 'Urahnin Moonstrike kann in der Küstenstadt Auberdine an der Dunkelküste angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11563', 'deDE', 'Die Augen von %s leuchten für einen Moment rot auf.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11564', 'deDE', 'Ist das alles, was Ihr draufhabt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11565', 'deDE', 'Wo ist Urahne Nightwind?', 'Wo ist Urahne Nightwind?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11566', 'deDE', 'Urahne Nightwind verbringt seine Zeit in dem Schrein in Jaedenar im Teufelswald.', 'Urahne Nightwind verbringt seine Zeit in dem Schrein in Jaedenar im Teufelswald.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11567', 'deDE', 'Wo ist Urahnin Riversong?', 'Wo ist Urahnin Riversong?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11568', 'deDE', 'Das Dorf Astranaar in Ashenvale ist Urahnin Riversongs momentanes Zuhause.', 'Das Dorf Astranaar in Ashenvale ist Urahnin Riversongs momentanes Zuhause.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11570', 'deDE', 'Wo ist Urahne Stonespire?', 'Wo ist Urahne Stonespire?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11571', 'deDE', 'Soweit ich weiß, hält sich Urahne Stonespire in Everlook in Winterspring auf.', 'Soweit ich weiß, hält sich Urahne Stonespire in Everlook in Winterspring auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11572', 'deDE', 'Wo ist Urahnin Brightspear?', 'Wo ist Urahnin Brightspear?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11573', 'deDE', 'Urahnin Brightspear wandert in den Ruinen von Kel\'Theril in Winterspring umher.', 'Urahnin Brightspear wandert in den Ruinen von Kel\'Theril in Winterspring umher.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11574', 'deDE', 'Wo ist Urahne Bloodhoof?', 'Wo ist Urahne Bloodhoof?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11575', 'deDE', 'Urahne Bloodhoof? Na, den könnt Ihr in Bloodhoof treffen, in Mulgore.', 'Urahne Bloodhoof? Na, den könnt Ihr in Bloodhoof treffen, in Mulgore.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11576', 'deDE', 'Wo ist Urahnin Windtotem?', 'Wo ist Urahnin Windtotem?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11577', 'deDE', 'Urahnin Windtotem leistet den Goblins in Ratchet, an der Küste des Brachlands, Gesellschaft.', 'Urahnin Windtotem leistet den Goblins in Ratchet, an der Küste des Brachlands, Gesellschaft.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11578', 'deDE', 'Wo ist Urahne High Mountain?', 'Wo ist Urahne High Mountain?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11579', 'deDE', 'Urahne High Mountain ist im Camp Taurajo im Brachland.', 'Urahne High Mountain ist im Camp Taurajo im Brachland.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11580', 'deDE', 'Wo ist Urahne Moonwarden?', 'Wo ist Urahne Moonwarden?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11581', 'deDE', 'Urahne Moonwarden wacht über Crossroads im Brachland.', 'Urahne Moonwarden wacht über Crossroads im Brachland.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11582', 'deDE', 'Wo ist Urahne Proudhorn?', 'Wo ist Urahne Proudhorn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11583', 'deDE', 'Urahne Proudhorn genießt die Aussicht auf Mulgore von Donnerfels aus.', 'Urahne Proudhorn genießt die Aussicht auf Mulgore von Donnerfels aus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11584', 'deDE', 'Wo ist Urahnin Darkhorn?', 'Wo ist Urahnin Darkhorn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11585', 'deDE', 'Urahnin Darkhorn kann an den roten Felsen von Orgrimmar angetroffen werden.', 'Urahnin Darkhorn kann an den roten Felsen von Orgrimmar angetroffen werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11586', 'deDE', 'Wo ist Urahnin Morningdew?', 'Wo ist Urahnin Morningdew?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11587', 'deDE', 'Urahnin Morningdew wurde dabei beobachtet, sich die Rennen auf der Illusionenrennbahn in Tausend Nadeln anzusehen.', 'Urahnin Morningdew wurde dabei beobachtet, sich die Rennen auf der Illusionenrennbahn in Tausend Nadeln anzusehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11588', 'deDE', 'Wo ist Urahnin Runetotem?', 'Wo ist Urahnin Runetotem?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11589', 'deDE', 'Ich habe zuletzt gehört, dass Urahnin Runetotem sich auf den Weg zum Klingenhügel in Durotar gemacht hat.', 'Ich habe zuletzt gehört, dass Urahnin Runetotem sich auf den Weg zum Klingenhügel in Durotar gemacht hat.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11590', 'deDE', 'Wo ist Urahne Grimmtotem?', 'Wo ist Urahne Grimmtotem?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11591', 'deDE', 'Ihr werdet den Urahnen Grimmtotem an Lariss\' Pavillion in Feralas antreffen.', 'Ihr werdet den Urahnen Grimmtotem an Lariss\' Pavillion in Feralas antreffen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11592', 'deDE', 'Wo ist Urahnin Mistwalker?', 'Wo ist Urahnin Mistwalker?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11593', 'deDE', 'Urahnin Mistwalker schaut sich gerne die Kämpfe in der Schlägergrube des Düsterbruchs in Feralas an.', 'Urahnin Mistwalker schaut sich gerne die Kämpfe in der Schlägergrube des Düsterbruchs in Feralas an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11594', 'deDE', 'Wo ist Urahne Skyseer?', 'Wo ist Urahne Skyseer?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11595', 'deDE', 'Geht zum Freiwindposten in Tausend Nadeln und Ihr werdet den Urahnen Skyseer finden.', 'Geht zum Freiwindposten in Tausend Nadeln und Ihr werdet den Urahnen Skyseer finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11596', 'deDE', 'Wo ist Urahne Splitrock?', 'Wo ist Urahne Splitrock?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11597', 'deDE', 'Ich glaube Urahne Splitrock ist nach Maraudon in Desolace gereist.', 'Ich glaube Urahne Splitrock ist nach Maraudon in Desolace gereist.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11598', 'deDE', 'Wo ist Urahne Dreamseer?', 'Wo ist Urahne Dreamseer?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11599', 'deDE', 'Urahne Dreamseer wohnt in Gadgetzan in der Tanariswüste.', 'Urahne Dreamseer wohnt in Gadgetzan in der Tanariswüste.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11600', 'deDE', 'Wo ist Urahnin Ragetotem?', 'Wo ist Urahnin Ragetotem?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11601', 'deDE', 'Urahnin Ragetotem verbringt Ihre Tage im Tal der Behüter in der Tanariswüste.', 'Urahnin Ragetotem verbringt Ihre Tage im Tal der Behüter in der Tanariswüste.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11602', 'deDE', 'Wo ist Urahnin Wildmane?', 'Wo ist Urahnin Wildmane?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11603', 'deDE', 'Zul\'Farrak, in der Tanariswüste, ist die Heimat von Urahnin Wildmane.', 'Zul\'Farrak, in der Tanariswüste, ist die Heimat von Urahnin Wildmane.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11604', 'deDE', 'Wo ist Urahne Thunderhorn?', 'Wo ist Urahne Thunderhorn?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11605', 'deDE', 'Aus mir unverständlichen Gründen, hält sich Urahne Thunderhorn momentan in der wuchernden Narbe im Krater von Un\'Goro auf.', 'Aus mir unverständlichen Gründen, hält sich Urahne Thunderhorn momentan in der wuchernden Narbe im Krater von Un\'Goro auf.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11606', 'deDE', 'Wo ist Urahnin Bladesing?', 'Wo ist Urahnin Bladesing?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11607', 'deDE', 'Wenn Ihr zur Burg Cenarius in Silithus reist, werdet Ihr Urahnin Bladesing finden.', 'Wenn Ihr zur Burg Cenarius in Silithus reist, werdet Ihr Urahnin Bladesing finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11608', 'deDE', 'Wo ist Urahne Primestone?', 'Wo ist Urahne Primestone?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11609', 'deDE', 'Urahne Primestone hat viele Jahre im Kristalltal in Silithus verbracht.', 'Urahne Primestone hat viele Jahre im Kristalltal in Silithus verbracht.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11610', 'deDE', 'Seine Ahnen zu ehren, bedeutet, der eigenen Kultur und dem eigenen Volk Ehre zu zollen, $C. Die Münzen der Urahnen dienen dem Zweck, jene anzuerkennen, die dem ehrenhaften Pfad des Respektes folgen.$B$BWährend des Mondfests werdet Ihr die Möglichkeit haben, vielen Urahnen Euren Respekt zu erweisen. Dadurch werdet Ihr die Münzen der Urahnen erhalten. Bringt mir diese Münzen und ich werde Euren Eifer mit einer Auswahl von einzigartigen Gegenständen belohnen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11611', 'deDE', 'Qirajikampftrupp entdeckt!', 'Qirajikampftrupp entdeckt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11612', 'deDE', 'Ein Sandsturm zieht aus dem Süden auf, in De... Das ist kein Sandsturm! ANGRIFF DER QIRAJIARMEE!', 'Ein Sandsturm zieht aus dem Süden auf, in De... Das ist kein Sandsturm! ANGRIFF DER QIRAJIARMEE!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11613', 'deDE', 'Angreifer nähern sich aus dem Süden!', 'Angreifer nähern sich aus dem Süden!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11614', 'deDE', 'Vernichtet sie alle.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11615', 'deDE', 'Die Macht von Kalimdor wird ihren Vormarsch aufhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11616', 'deDE', 'Soldaten von Kalimdor, Verteidigt die Basis.', 'Soldaten von Kalimdor, Verteidigt die Basis.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11617', 'deDE', 'Ich hätte gerne eine neue Einladung zum Mondfest.', 'Ich hätte gerne eine neue Einladung zum Mondfest.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11618', 'deDE', null, 'Seid gegrüßt, $N. Wir sind den ganzen Weg von Moonglade gekommen, um die Kunde vom Mondfest zu verbreiten.$B$BLeistet uns doch bei den Feierlichkeiten Gesellschaft.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11619', 'deDE', 'Die finale Schlacht um Kalimdor naht. Der Oberkommandierende der Macht von Kalimdor wird seinen Truppen den Marsch auf Ahn\'Qiraj befehlen und sie werden jeden zerstören, der sich ihnen in den Weg stellt.', 'Die finale Schlacht um Kalimdor steht kurz bevor. Der Oberkommandierende der Macht von Kalimdor befiehlt seinen Truppen, den Marsch auf Ahn\'Qiraj anzutreten und alle zu vernichten, die sich ihnen in den Weg stellen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11620', 'deDE', '%s steht vor der Macht von Kalimdor', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11621', 'deDE', 'Ich bin Saurfang. Bruder von Broxigar.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11622', 'deDE', 'Ihr wißt, dass ich der Oberkommandierende der Macht von Kalimdor bin.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11623', 'deDE', '%s hält inne und betrachtet das Heer von Soldaten, dass sich vor ihm ausbreitet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11624', 'deDE', 'Ein Orc - ein wahrer Orckrieger - wünscht sich nur eins: einen glorreichen Tod im Kampf gegen einen verhassten Feind zu sterben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11625', 'deDE', 'Manche von euch haben bisher noch nicht in einer Schlacht gekämpft. Seit vielen Jahren haben wir nun Frieden genossen. Viele Jahre waren wir untätig aber wir haben auch viele Jahre gekämpft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11626', 'deDE', 'In diesen Jahren - die von Leid und Blutvergießen bestimmt waren, als die Legion und die Geißel unsere Häuser niederbrannten und unsere Familien töteten - haben diese Insekten unter unseren Füßen verweilt. Unter unseren Häusern - und haben abgewartet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11627', 'deDE', 'Darauf gewartet, das Leben aus unseren Kindern zu quetschen. Alles zu vernichten, dass in ihrem Weg steht. Dies tuen sie für ihren Gott.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11628', 'deDE', 'Und was ist mit unseren Göttern?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11629', 'deDE', 'Für unsere Götter verteidigen wir uns. Wir stellen uns ihnen entgegen. Wir werden ihnen zeigen, dass wir, vereinigt unter einem Banner, alles vernichten können. Ihr Gott wird fallen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11630', 'deDE', 'Heute zu sterben, auf diesem Schlachtfeld, heißt einen orcischen Tod zu sterben! Heute zu sterben, heißt für unsere Kinder zu sterben, für unsere Eltern. Und für die... die wir lieben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11631', 'deDE', 'Würde auch nur einer von euch sich so einen Tod entsagen? Eine solche Ehre?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11632', 'deDE', 'FÜR KALIMDOR!', 'FÜR KALIMDOR!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11633', 'deDE', 'FÜR EHRE!', 'FÜR DIE EHRE!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11634', 'deDE', 'FÜR DEN KÖNIG!', 'FÜR DEN KÖNIG', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11635', 'deDE', 'FÜR MAGNI!', 'FÜR MAGNI!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11636', 'deDE', 'FÜR KHAZ\'MODAN', 'FÜR KHAZ\'MODAN', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11637', 'deDE', 'FÜR IRONFORGE!', 'FÜR IRONFORGE!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11638', 'deDE', 'Ich werde bis zu meinem letzten Atemzug an Eurer Seite stehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11639', 'deDE', null, '%s sagt ein Gebet.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11640', 'deDE', null, null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11641', 'deDE', 'Für den Kriegshäuptling!', 'Für den Kriegshäuptling!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11642', 'deDE', 'FÜR DIE HORDE!', 'FÜR DIE HORDE!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11643', 'deDE', 'Für Broxigar!', 'Für Broxigar!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11644', 'deDE', 'Für Kalimdor!', 'Für Kalimdor!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11645', 'deDE', 'FÜR DIE HORDE! FÜR CAIRNE! FÜR THUNDER BLUFF!', 'FÜR DIE HORDE! FÜR CAIRNE! FÜR THUNDER BLUFF!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11646', 'deDE', 'VORWÄRTS! NACH AHN\'QIRAJ!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11647', 'deDE', 'Heute werden sich unser alle Schicksale erfüllen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11648', 'deDE', 'Stellt euch der Macht von Kalimdor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11649', 'deDE', 'Die Macht von Kalimdor hat gesiegt! Die letzten Streitkräfte der Qiraji werden in die uralte Stadtfeste von Ahn\'Qiraj zurückgetrieben. Die Qirajiresonanzkristalle, die Kalimdor bedeckten, verschwinden so schnell, wie sie erschienen sind, und lassen nichts als verbrannte Erde zurück.', 'Die Macht von Kalimdor ist siegreich! Die letzten Streitkräfte der Qiraji sind besiegt und ziehen sich in die die uralte Stadtfeste von Ahn\'Qiraj zurück. Die Qirajiresonanzkristalle, die Kalimdor bedeckten, verschwinden so schnell sie gekommen sind und lassen nichts als verbrannte Erde zurück.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11650', 'deDE', 'Wir haben den Befehl von ganz oben erhalten, Rajaxx zu vernichten. Termination mit extremer Voreingenommenheit, um es mal so zu sagen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11651', 'deDE', 'Die Schlacht ist gewonnen! Schaut, wie sie sich in die Sicherheit ihres Tempels flüchten. Bald schon wird hier alles dem Erdboden gleichgemacht sein... Ihre Anführer vernichtet!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11652', 'deDE', 'Wir stehen Seite an Seite mit der Allianz, $R. Ein so altes Böses, dass die Welt es vergessen hat, versucht uns alle zu verschlingen. Zuerst Kalimdor und dann den Rest von Azeroth. Der Hass auf alles, das von den großen Titanenformern geschaffen wurde, treibt es an.$B$BWir sind die Einzigen, die C\'Thuns Armeen aufhalten können. Wir werden zuerst zuschlagen und die Armeen von Ahn\'Qiraj in die Knie zwingen. Ganz Azeroth wird unter der vereinten Macht der Allianz und der Horde erzittern.$B$BIhr müsst bei den Kriegsanstrengungen behilflich sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11653', 'deDE', 'Wo ist Urahnin Meadowrun?', 'Wo ist Urahnin Meadowrun?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11654', 'deDE', 'Das letzte Mal habe ich Urahnin Meadowrun in der weinenden Höhle in den westlichen Pestländern gesehen.', 'Das letzte Mal habe ich Urahnin Meadowrun in der weinenden Höhle in den westlichen Pestländern gesehen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11656', 'deDE', 'Wo ist Urahne Starglade?', 'Wo ist Urahne Starglade?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11657', 'deDE', 'Ihr werdet den Urahnen Starglade vor den Toren von Zul\'Gurub im Schlingendorntal finden.', 'Ihr werdet den Urahnen Starglade vor den Toren von Zul\'Gurub im Schlingendorntal finden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11658', 'deDE', '\"Ein klasse Job in der Kriegsressourcenbeschaffung\" hieß es, \"Wie wär\'s mit einer Beförderung?\", hieß es. Die Übersetzung von dem ganzen Quatsch lautet: \"Du wirst gleich neben \'nem Käferbau in Silithus zusammen mit \'nem Haufen schwitzender Zwerge stationiert, Carnes.\"$B$BVielen Dank für die Beförderung, Captain!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11659', 'deDE', null, 'Ich, genau wie mein Bruder, ehre die, die ihre Ahnen ehren. Es bereitet mir außerordentliches Vergnügen, meine Handwerksgeheimnisse mit Jüngern des Mondfestes zu teilen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11660', 'deDE', 'Flüchtet nur, ihr Feiglinge! Es wird euch nichts bringen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11661', 'deDE', 'Ich, wie viele andere, bin gekommen um meinen Brüdern, den Druiden, im Krieg beizustehen.$B$BJetzt, da sich die Tore von Ahn\'Qiraj geöffnet haben, muss ich feststellen, dass es noch weit mehr zu tun gibt. Ich habe beschlossen in Silithus zu bleiben und zu meinen besten Fähigkeiten auszuhelfen, $N.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11662', 'deDE', 'So tödlich die Silithiden auch zu sein scheinen, verglichen mit ihren Meistern, den Qiraji, sind sie nur zahme Haustiere.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11663', 'deDE', 'Dieser Krieg ist noch lange nicht vorbei, er hat gerade erst begonnen. Unsere tapferen Männer und Frauen kämpfen Tag und Nacht hinter den Mauern von Ahn\'Qiraj, $N. Es wird nicht mehr lange dauern, bis die Stadt der Qiraji fällt.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11664', 'deDE', null, 'Unser guter Fortschritt im Kampf gegen die Qiraji ist all der Hilfe zuzuschreiben, die wir aus fast allen Ecken von Kalimdor erhalten haben.$B$BUm Krieg zu führen wird mehr benötigt als nur Soldaten, $N. Die unbeachteten Helden dieses Krieges sind die Händler und Handwerker, die unsere Truppen weiterhin mit den benötigten Ressourcen versorgen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11665', 'deDE', 'Die hier in Moonglade wissen echt wie man \'ne gute Party schmeisst. Wer hätte das gedacht?', 'Die hier in Moonglade wissen echt wie man \'ne gute Party schmeisst. Wer hätte das gedacht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11666', 'deDE', 'Probiert mal diese Feuerwerkszünder aus, die sind echt der Knaller!', 'Probiert mal diese Feuerwerkszünder aus, die sind echt der Knaller!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11667', 'deDE', 'Dieser Mondlichtkreis teleportiert Euch nach Sturmwind. Benutzt Eure Einladung, wenn Ihr bereit seid.', 'Dieser Mondlichtkreis teleportiert Euch nach Sturmwind. Benutzt Eure Einladung, wenn Ihr bereit seid.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11668', 'deDE', 'Versucht bloß nicht, Euch in eine feindliche Stadt zu teleportieren. Das funktioniert nicht - wir mögen hier alle Freunde sein, aber Zuhause halten die alten Streitigkeiten an.', 'Versucht bloß nicht, Euch in eine feindliche Stadt zu teleportieren. Das funktioniert nicht - wir mögen hier alle Freunde sein, aber Zuhause halten die alten Streitigkeiten an.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11669', 'deDE', 'Die Leute in Nighthaven wissen echt, wie man \'ne gute Party schmeisst.', 'Die Leute in Nighthaven wissen echt, wie man \'ne gute Party schmeisst.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11670', 'deDE', 'Diese Feuerwerke knallen ganz schön, meint Ihr nicht?', 'Diese Feuerwerke knallen ganz schön, meint Ihr nicht?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11672', 'deDE', 'Dieser Mondlichtkreis teleportiert Euch nach Eisenschmiede. Aber wer würde schon von dieser Party weg wollen?', 'Dieser Mondlichtkreis teleportiert Euch nach Eisenschmiede. Aber wer würde schon von dieser Party weg wollen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11673', 'deDE', 'Das Mondfest ist dieses Jahr einfach fantastisch.', 'Das Mondfest ist dieses Jahr einfach fantastisch.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11674', 'deDE', 'Man sagt, diese Feuerwerke wären dank der Macht des Mondes so wundervoll.', 'Man sagt, diese Feuerwerke wären dank der Macht des Mondes so wundervoll.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11675', 'deDE', 'Dieser Mondlichtkreis teleportiert Euch nach Darnassus.', 'Dieser Mondlichtkreis teleportiert Euch nach Darnassus.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11676', 'deDE', 'Wenn ich es nicht besser wüsste, würde ich annehmen, dass diese Party von Orcs organisiert wurde.', 'Wenn ich es nicht besser wüsste, würde ich annehmen, dass diese Party von Orcs organisiert wurde.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11677', 'deDE', 'Habt Ihr gesehen? Das letzte Feuerwerk hätte fast das Hemd von dem Zwerg da in Brand gesetzt! Hah!', 'Habt Ihr gesehen? Das letzte Feuerwerk hätte fast das Hemd von dem Zwerg da in Brand gesetzt! Hah!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11678', 'deDE', 'Dieser Mondlichtkreis teleportiert Euch nach Orgrimmar. Benutzt Eure Einladung, wenn Ihr bereit seid.', 'Dieser Mondlichtkreis teleportiert Euch nach Orgrimmar. Benutzt Eure Einladung, wenn Ihr bereit seid.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11679', 'deDE', 'Es geht nichts über das Mondfest, um sich die Knochen aufzuwärmen!', 'Es geht nichts über das Mondfest, um sich die Knochen aufzuwärmen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11680', 'deDE', 'Wenn man seine Finger nicht mehr fühlen kann, muss man mit dem Abfackeln von Feuerwerk ganz schön vorsichtig sein...', 'Wenn man seine Finger nicht mehr fühlen kann, muss man mit dem Abfackeln von Feuerwerk ganz schön vorsichtig sein...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11681', 'deDE', 'Dieser Mondlichtkreis teleportiert Euch zur Unterstadt. Zieht Euch dorthin zurück, wenn Ihr bereit seid.', 'Dieser Mondlichtkreis teleportiert Euch zur Unterstadt. Zieht Euch dorthin zurück, wenn Ihr bereit seid.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11682', 'deDE', 'Ich liebe das Mondfest! Es ist eine klasse Gelegenheit, mal so richtig mit den Hufen zu stampfen!', 'Ich liebe das Mondfest! Es ist eine klasse Gelegenheit, mal so richtig mit den Hufen zu stampfen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11683', 'deDE', 'Ich habe keine Ahnung wie diese Feuerwerke funktionieren, aber sie sind sehr hübsch!', 'Ich habe keine Ahnung wie diese Feuerwerke funktionieren, aber sie sind sehr hübsch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11684', 'deDE', 'Dieser Mondlichtkreis teleportiert Euch nach Donnerfels. Benutzt ihn, wenn ihr das Fest verlassen wollt.', 'Dieser Mondlichtkreis teleportiert Euch nach Donnerfels. Benutzt ihn, wenn ihr das Fest verlassen wollt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11685', 'deDE', 'Es wird die ganze Nacht lang Feuerwerke geben. Von 18 Uhr bis zum Morgengrauen wird der Himmel über dem Elune\'ara See in den schönsten Farben glitzern.', 'Es wird die ganze Nacht lang Feuerwerke geben. Von 18 Uhr bis zum Morgengrauen wird der Himmel über dem Elune\'ara See in den schönsten Farben glitzern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11686', 'deDE', 'Diese Lampen werden aufgestellt, um das Ende des Mondfestes zu zelebrieren.', 'Diese Lampen werden aufgestellt, um das Ende des Mondfestes zu zelebrieren.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11687', 'deDE', 'Es ist die letzte Nacht des Mondfests. Möge das Glück mit Euch sein.', 'Es ist die letzte Nacht des Mondfests. Möge das Glück mit Euch sein.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11688', 'deDE', 'Mögen Eure Ahnen Euch segnen, $N, und Euch im nächsten Jahr Glück bescheren.', 'Mögen Eure Urahnen Euch segnen, $N, und Euch im neuen Jahr Glück bringen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11689', 'deDE', 'Wie kann ich Omen herbeirufen?', 'Wie kann ich Omen herbeirufen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11690', 'deDE', 'Omen schläft in den Tiefen des Elune\'ara Sees, kann allerdings vom südöstlichen Ufer aus, nahe den Stormrage Grabhügeln, herbeigerufen werden. Dort werdet Ihr spezielle Zünder für Raketenbündel finden...$B$BFeuert Eure Raketen mit diesem Zünder ab und Omens Untergebene werden kommen. Feuert dann mehr Raketen, bis zu drei Dutzend, oder Elunes Kerzen auf seine Untergebenen ab, und er wird bald erwachen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11691', 'deDE', 'Ich würde gerne noch eine von Elunes Kerzen kaufen.', 'Ich würde gerne noch eine von Elunes Kerzen kaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11692', 'deDE', '%s teilt seine Macht mit seinen Brüdern.', '%s teilt seine Macht mit seinen Brüdern.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11693', 'deDE', '%s wird wütend!', '%s wird wütend!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11694', 'deDE', '%s verfällt in Berserkerwut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11695', 'deDE', 'ist tiefgefroren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11700', 'deDE', 'Der riesige, fliegende Augapfel im Zentrum des Raumes dreht sich in Eure Richtung und starrt Euch an. Ihr steht im Angesicht eines Gottes.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11702', 'deDE', 'Nur Fleisch und Knochen. Sterbliche sind solch eine leichte Beute...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11703', 'deDE', 'und Knochen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11704', 'deDE', 'Sterbliche sind...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11705', 'deDE', 'solch eine leichte Beute.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11706', 'deDE', 'Wo sind nur deine Manieren, Bruder. Wir sollten unsere Gäste angemessen wilkommen heißen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11707', 'deDE', 'Euch erwartet Schmerz...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11708', 'deDE', 'So viel Schmerz...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11709', 'deDE', 'Kommt, ihr Kleinen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11710', 'deDE', 'Das Festmahl der Seelen beginnt jetzt...', null, '31882');
