INSERT INTO `broadcast_text_locale` VALUES ('11723', 'deDE', 'Hier, ich würde Euch gerne dieses Zeichen meiner Liebe überreichen.', 'Hier, ich würde Euch gerne dieses Zeichen meiner Liebe überreichen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11724', 'deDE', 'Wir waren alle so mit diesem Krieg beschäftigt, dass wir vergessen haben, uns Zeit für die Liebe zu nehmen.', 'Wir waren alle so mit diesem Krieg beschäftigt, dass wir vergessen haben, uns Zeit für die Liebe zu nehmen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11725', 'deDE', 'Wofür sind diese Kisten?', 'Wofür sind diese Kisten?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11726', 'deDE', 'Ich habe ein paar Ladungen mit neuen Parfüms und Duftwässerchen aus der Stadt bestellt. Sobald ich sie ausgepackt habe, solltet Ihr nochmal vorbeischauen, dann werde ich sie zum Kauf anbieten.', 'Ich habe ein paar Ladungen mit neuen Parfüms und Duftwässerchen aus der Stadt bestellt. Sobald ich sie ausgepackt habe, solltet Ihr nochmal vorbeischauen, dann werde ich sie zum Kauf anbieten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11727', 'deDE', 'Ihr gehört nicht hierher! Ihr Uralten, erhebt euch gegen diese elenden Eindringlinge!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11728', 'deDE', null, 'Was für ein Kampf! Lasst uns ein wenig verschnaufen.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11729', 'deDE', 'Hey, $Gmein Herr:meine Dame;. Ihr seht aus wie ein $C, der einen guten Weingeschmack hat. Ich möchte Euch um etwas bitten und denke, dass ich Euch ein Angebot machen kann, das Ihr nicht ablehnen könnt... ach, vergesst es!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('11736', 'deDE', 'Wir waren alle so mit diesem Krieg beschäftigt, dass wir vergessen haben, uns Zeit für die Liebe zu nehmen.', null, '31882');
