INSERT INTO `broadcast_text_locale` VALUES ('12854', 'deDE', null, 'Ihr habt versagt!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12855', 'deDE', null, 'Erbärmliche Kreatur!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12856', 'deDE', null, 'Tötet sie im Namen des Meisters!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12857', 'deDE', null, 'Ihr könnt euch nicht vor mir verstecken!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12858', 'deDE', null, 'Kniet nieder, Wurm!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12859', 'deDE', null, 'Flieht, solange ihr noch könnt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12860', 'deDE', 'Genießt Ihr das Fest, $R?$B$BWährend die Flammenbewahrer sich den Feuern der Gegenwart widmen, bin ich eher ein Historiker, der sich der Vergangenheit dieses Festes verschrieben hat. Natürlich dokumentiere ich auch die diesjährigen Feierlichkeiten. Alles scheint seinen gewohnten Gang zu gehen, meint Ihr nicht?$B$BWisst Ihr, $N, jedem Freudenfeuer wohnt während dieses Fests eine besondere Kraft inne. Wir tragen Sorge für unsere, doch ich bin sicher, dass tief in den Städten unserer Feinde ebenfalls heilige Flammen die Nacht erhellen...', 'Genießt Ihr das Fest, $R?$B$BWährend die Flammenbewahrer sich den Feuern der Gegenwart widmen, bin ich eher ein Historiker, der sich der Vergangenheit dieses Festes verschrieben hat. Natürlich dokumentiere ich auch die diesjährigen Feierlichkeiten. Alles scheint seinen gewohnten Gang zu gehen, meint Ihr nicht?$B$BWisst Ihr, $N, jedem Freudenfeuer wohnt während dieses Fests eine besondere Kraft inne. Wir tragen Sorge für unsere, doch ich bin sicher, dass tief in den Städten unserer Feinde ebenfalls heilige Flammen die Nacht erhellen...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12984', 'deDE', 'Wer wagt es in mein Reich einzudringen? Seid gewarnt: Jeder Eindringling ist verloren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12985', 'deDE', 'Narren! Ihr wähnt euch siegreich? Ihr seid dem Abgrund lediglich einen Schritt näher gekommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12986', 'deDE', 'Eure Spielchen langweilen mich. Fahrt fort, und ich werde eure Seelen der Vergessenheit überantworten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12987', 'deDE', 'Ihr ahnt ja nicht, welche Schrecken noch vor euch liegen. Das Erlebte war erst der Anfang! Das eisige Herz Naxxramas erwartet Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12988', 'deDE', 'Eure Treue ist lobenswert. Schon bald wird jeder dem Lichkönig dienen. Und am Ende auch ihr. Wenn Ihr nicht scheitert.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12989', 'deDE', 'Eure Vorkehrungen waren nutzlos. Ihr wisst, was Ihr zu tun habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12990', 'deDE', 'Unsere Vorbereitungen verlaufen wie vorhergesehen, Meister.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12991', 'deDE', 'Es scheint bisher keine  Komplikationen... Was, was ist das?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12992', 'deDE', 'Ja, Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12993', 'deDE', 'Kommt nur, ihr Helden... Es ist der Wille des Lichkönigs, dass Ihr vernichtet werdet.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12994', 'deDE', 'Also gut. Erhebt euch, Krieger der eisigen Weiten! Ich befehle euch zu kämpfen, zu töten und für euren Meister zu sterben! Lasst keinen am Leben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12995', 'deDE', 'Fleht um Gnade!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12996', 'deDE', 'Macht Euren letzten Atemzug!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12997', 'deDE', 'Euer Ende ist nah!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12998', 'deDE', 'Meister, helft mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('12999', 'deDE', 'Diener, Jünger, Soldaten der eisigen Finsternis! Folgt dem Ruf von Kel\'Thuzad!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13000', 'deDE', 'Nur einmal kosten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13001', 'deDE', '%s eilt Stalagg zu Hilfe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13002', 'deDE', 'Es gibt kein Entkommen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13003', 'deDE', 'Rennt! Das bringt das Blut in Wallung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13004', 'deDE', 'Ahh... willkommen in meinem Reich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13005', 'deDE', 'Schhh... bald ist alles vorbei.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13006', 'deDE', 'Ich höre das Klopfen kleiner Herzen. Ja...  sie schlagen schneller... bald schon werden sie verstummen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13007', 'deDE', 'Wohin gehen? Was tun? So viele Möglichkeiten, und am Ende wartet doch nur der Tod.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13008', 'deDE', 'Welchen soll ich nur zuerst verspeisen? Keine leichte Wahl. Sie alle duften so köstlich...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13009', 'deDE', 'Kommt näher, Ihr kleinen Leckerbissen. Ich habe mich solange nach Nahrung gesehnt!... Nach Blut, um meinen Durst zu stillen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13010', 'deDE', null, 'Verteidigt euch!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13011', 'deDE', null, 'Touche...', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13012', 'deDE', null, 'Wer ist der Nächste?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13013', 'deDE', null, 'Euer Leben gehört mir!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13014', 'deDE', null, 'Zeliek, tötet sie noch nicht. Erst haben wir unseren Spass mit ihnen!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13015', 'deDE', null, 'Ich hoffe, sie bleiben lange genug am Leben... damit ich mich vorstellen kann.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13016', 'deDE', null, 'Ich töte den Ersten! Wer wettet dagegen?', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13017', 'deDE', 'Eure Seele gehört jetzt mir!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13018', 'deDE', 'Es gibt kein Entkommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13019', 'deDE', 'Arggggh! Freut Euch nicht zu früh! Euer Sieg ist bedeutungslos, denn ich werde mit Kräften zurückkehren, die weit über eurer Vorstellungskraft hinausgehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13020', 'deDE', 'Ich werde das Blut in euren Adern gefrieren lassen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13021', 'deDE', 'Die Dunkelheit der Leere erwartet Euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13022', 'deDE', '%s lacht höhnisch.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13023', 'deDE', 'Verfüttere euch an Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13024', 'deDE', 'Kein... Feugen... mehr.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13025', 'deDE', 'Feugen macht Meister glücklich!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13026', 'deDE', 'Das ... ist ... mein ... Ende.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13027', 'deDE', 'Tod ist der einzige Ausweg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13028', 'deDE', 'Ich habe lange genug gewartet. Stellt euch dem Seelenjäger.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13029', 'deDE', 'Ihr Narren habt euren eigenen Untergang heraufbeschworen. Voller Leichtsinn habt ihr euch mit Kräften angelegt, die weit über euren Verstand hinausgehen. Tapfer seid ihr in das Reich des Seelenjägers vorgedrungen. Doch jetzt gibt es nur noch einen Ausweg  - den einsamen Pfad der Verdammten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13030', 'deDE', 'Ihr Narren habt euren eigenen Untergang heraufbeschworen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13031', 'deDE', 'Voller Leichtsinn habt ihr euch mit Kräften angelegt, die weit über euren Verstand hinausgehen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13032', 'deDE', 'Tapfer seid ihr in das Reich des Seelenjägers vorgedrungen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13033', 'deDE', 'Doch jetzt gibt es nur noch einen Ausweg  - den einsamen Pfad der Verdammten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13034', 'deDE', 'Kommt und kämpft, ihr Wichte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13035', 'deDE', 'Was für eine verdammte Verschwendung!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13036', 'deDE', 'Bringt das nächste Mal mehr Freunde mit!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13037', 'deDE', 'Ich mag mein Fleisch besonders knusprig!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13038', 'deDE', 'Zu den Waffen, Ihr Schmarotzer! Wir haben Gesellschaft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13039', 'deDE', 'Ich hab\' genug von Eurem Geflenne! Haltet die Klappe oder ich stopf Euch das Maul!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13040', 'deDE', 'Ich werd\' es genießen, diese Waschlappen aus dem Weg zu räumen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13041', 'deDE', 'Ihr gehört mir...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13042', 'deDE', 'Ihr entgeht mir nicht...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13043', 'deDE', 'Ihr seid.... als nächstes dran.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13044', 'deDE', '%s macht seinen letzten Atemzug.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13045', 'deDE', 'Schließt eure Augen. Schlaft.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13046', 'deDE', 'Die Völker dieser Welt werden zu Grunde gehen. Es ist nur eine Frage der Zeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13047', 'deDE', 'Ich sehe endloses Leiden. Ich sehe Qualen. Ich sehe Zorn. Ich sehe alles...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13048', 'deDE', 'Schon bald wird die Welt erzittern.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13049', 'deDE', 'Euer Ende naht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13050', 'deDE', 'Maden werden sich an eurem faulenden Fleisch laben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13051', 'deDE', 'Ihr sucht den Tod?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13052', 'deDE', 'Ihr kommt nicht vorbei!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13053', 'deDE', 'Schweigt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13054', 'deDE', 'Ich... bin... wieder frei.... vielleicht ist es noch nicht zu spät - nein! Ich brauche... mehr... Zeit...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13055', 'deDE', 'Auch im Tod werdet ihr keine Ruhe finden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13056', 'deDE', 'Der Wille des Meisters wurde erhört.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13057', 'deDE', 'Beugt euch der Macht des Hochlords!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13058', 'deDE', 'Genug mit dem Geschwätz. Lasst sie nur kommen. Wir werden ihre Knochen zu Staub zermalmen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13059', 'deDE', 'Bewahrt euren Hass. Zügelt euren Zorn. Schon bald könnt ihr eurem Argwohn freien Lauf lassen.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13060', 'deDE', 'Das Leben ist bedeutungslos. Der Tod ist die wahre Prüfung.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13061', 'deDE', 'Ehre unserem Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13062', 'deDE', 'Euer Leben ist verwirkt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13063', 'deDE', 'Sterbt, Eindringling!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13064', 'deDE', 'Ich werde dem Meister über den Tod hinaus dienen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13065', 'deDE', 'Meine Pflicht ist getan!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13066', 'deDE', 'Sterbt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13067', 'deDE', 'Erhebt euch, Soldaten! Erhebt euch und kämpft erneut!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13068', 'deDE', 'Flickwerk spielen möchte!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13069', 'deDE', 'Kel’thuzad macht Flickwerk zu seinem Abgesandten von Krieg!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13070', 'deDE', 'Was... sein geschehen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13071', 'deDE', 'Nicht mehr spielen?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13072', 'deDE', 'Ich fange gerade erst an!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13073', 'deDE', 'Stellt euch und kämpft!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13074', 'deDE', 'Zeigt mir, was ihr könnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13075', 'deDE', 'Befolgt meine Befehle!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13076', 'deDE', 'Lasst keine Gnade walten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13077', 'deDE', 'Ihr enttäuscht mich, meine Schüler!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13078', 'deDE', 'Die Zeit des Übens ist vorbei! Zeigt mir, was ihr gelernt habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13079', 'deDE', 'Ein ehrenhafter... Tod.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13080', 'deDE', 'Streckt sie nieder... oder habt ihr ein Problem damit?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13081', 'deDE', 'Ihr hättet daheim bleiben sollen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13082', 'deDE', '%s gibt einen Triumphschrei von sich.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13083', 'deDE', 'Stalagg zerquetschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13084', 'deDE', 'Meister, mich retten!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13085', 'deDE', 'Stalagg töten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13086', 'deDE', 'Töten...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13087', 'deDE', 'Eure... Knochen... zermalmen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13088', 'deDE', 'Euch... zerquetschen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13089', 'deDE', 'Euch.... dankbar sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13090', 'deDE', 'Jetzt spürt ihr den Schmerz...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13091', 'deDE', 'Ihr seid zu spät!! Ich... muss.. gehorchen!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13092', 'deDE', 'Beeilt euch! Beeilt euch biiiiiitte!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13093', 'deDE', 'Aufhören! Bitte aufhöööören!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13094', 'deDE', 'Hilfe! So helft mir doch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13095', 'deDE', 'Neeiiiiiiin!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13096', 'deDE', 'Ihr werdet jetzt.... sterben!!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13097', 'deDE', 'Flieht, bevor es zu spät ist!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13098', 'deDE', 'Es verläuft... ganz nach Plan.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13099', 'deDE', 'Vergebt mir.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13100', 'deDE', 'Ich habe keine Wahl!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13101', 'deDE', 'Eindringlinge, macht diesem törichten Unterfangen ein für alle mal ein Ende! Kehrt um!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13102', 'deDE', 'Vielleicht kommen sie ja noch zur Vernunft... und flüchten so schnell sie können.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13103', 'deDE', 'Hört auf! Kehrt zurück! Kehrt zurück solange ihr noch könnt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13119', 'deDE', 'Genießt Ihr das Fest, $C? Meine Waren werden Eurem Tag das gewisse Etwas verleihen. Es geht nichts über eine Portion warmes Essen oder etwas zu trinken.$B$BUnd falls Ihr es noch nicht getan habt, schnappt Euch ein oder zwei Freunde und tanzt eine Runde am Sonnenbaum; ich bin sicher, Ihr fühlt Euch danach wie neu geboren!', 'Genießt Ihr das Fest, $C? Meine Waren werden Eurem Tag das gewisse Etwas verleihen. Es geht nichts über eine Portion warmes Essen oder etwas zu trinken.$B$BUnd falls Ihr es noch nicht getan habt, schnappt Euch ein oder zwei Freunde und tanzt eine Runde am Sonnenbaum; ich bin sicher, Ihr fühlt Euch danach wie neu geboren!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13120', 'deDE', 'Zeigt mir Euren Eintritt.', 'Zeigt mir Euren Eintritt.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13121', 'deDE', 'Fühlt Ihr es, schwächliche Kreatur? Die Dunkelheit über diesem Land ist nur ein Vorbote dessen, was kommen wird. Schon bald wird alles, was einst Euch gehörte, unser sein! Ihr werdet vor der Macht der Geißel auf die Knie fallen und Euer Ende finden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13122', 'deDE', 'Verzweifelt, Sterbliche! Euer Untergang ist nahe!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13123', 'deDE', 'Möge diese Dunkelheit Euch an eines erinnern: Der Schatten der Geißel ist allumfassend! Es gibt kein Entkommen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13124', 'deDE', 'Mit Eurem Tod in der Schlacht, werden wir nur stärker. Das Ende ist absehbar und unausweichlich. Bereitet Euch auf Euer Ende vor!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13125', 'deDE', 'Vorwärts, meine Kämpfer! Vorwärts! Lasst euch von Niemanden aufhalten.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13126', 'deDE', 'Erzitter vor unserer Macht! Flieht! Für die Lebenden wird es keinen Sieg geben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13129', 'deDE', 'Zeigt mir, was ihr könnt, Anfänger!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13130', 'deDE', 'Ist das alles, was Ihr drauf habt?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13131', 'deDE', 'Ihr verschwendet nicht nur die Zeit meiner Schüler, ihr verschwendet auch meine kostbare Zeit.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13132', 'deDE', 'Jämmerlich...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13133', 'deDE', 'Meine Großmutter, drüben im Rotkammgebirge schlägt härter zu als Ihr, Anfäger!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13134', 'deDE', 'Ihr habt Euch für die falsche Aufgabe entschieden, Initiand. Kel\'thuzad braucht keine Blumenmädchen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13135', 'deDE', 'Ist das eine Art Scherz?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13136', 'deDE', 'Tut endlich etwas, bevor ich anfange meine Übungspuppe durch Euch zu ersetzen und ich meine neuesten Schlagkombinationen an Euch ausprobiere!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13137', 'deDE', 'Der Schüler ist nutzlos, Meister! Der Schüler entschuldigt sich für sein Fehlbetragen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13138', 'deDE', 'Ich bin unwürdig, Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13139', 'deDE', 'Der Schüler wird härter arbeiten, Meister!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13140', 'deDE', 'Sire, der Schüler erbittet für seinen Mangel an Verständnis geschlagen zu werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13150', 'deDE', 'Nein!!! Ich verfluche euch, Eindringlinge! Die Armeen des Lichkönigs werden euch jagen und vernichten. Ihr werdet eurem Schicksal nicht entrinnen...', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13151', 'deDE', 'Greetings, $N. I am Johnny McWeaksauce. I am used as a tool by developers to carry out specific functions for the purpose of testing. For this test period, I am set to do two specific things:$B$B(1) Flag you as having done the Naxxramas attunement quest so that you are able to accept the next quest, \"Echoes of War\" from Commander Eligor Dawnbringer. You must first complete that quest to be able to access the quests for your Tier 3 armor set.$B$B(2) Teleport you to Naxxramas.$B$BWhat is it that you require?', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13153', 'deDE', 'I require flagging.', 'I require flagging.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13154', 'deDE', 'Ich möchte nach Naxxramas teleportiert werden.', 'Ich möchte nach Naxxramas teleportiert werden.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13156', 'deDE', 'Hallo, $N. Wie ich sehe, seid Ihr bereits so markiert, als hättet Ihr die Quest, die Euch Zugang zu Naxxramas gewährt, bereits abgeschlossen. Sprecht mit Kommandant Eligor Dawnbringer bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern, um die Quest \"Echo des Krieges\" zu erhalten. Ihr müsst diese Quest zunächst abschließen, um die Folgequest für Euer Rang 3-Rüstungsset starten zu können.$B$BIch kann Euch direkt nach Naxxramas teleportieren. Alternativ könnt Ihr auch das Portal im Westen des Pestwalds verwenden.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13159', 'deDE', 'Ihr habt also ein kleines Gebiet zurückerobert. Und wenn schon, es ist nur ein kleiner Rückschlag! Die Geißel wird zurückkehren!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13160', 'deDE', 'Genießt Euren kleinen Sieg, solange Ihr es noch könnt, Sterbliche. Diese Niederlage wird unseren entgültigen Triumph über dieses Land nicht verhindern!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13161', 'deDE', 'Für Euch gibt es hier keinen Sieg! Dieser Flecken Land wird schon in Kürze von den Armeen der Geißel wie ein Insekt verschluckt werden!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13162', 'deDE', 'Der Schatten der Geißel liegt noch immer über diesem Land, ihr Narren! Euer Widerstand ist zwecklos!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13163', 'deDE', 'Nun, wie es scheint, sind die Lebenden nicht so schwach wie wir angenommen hatten. Gut, nehmt diesen Ort, fürs Erste. Wir werden zurückkehren, zweimal so zahlreich wie heute. An diesem Tage werden eure lächerlichen Armeen dem Erdboden gleichgemacht.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13164', 'deDE', 'Ihr wagt es unsere Bemühungen hier zu unterbrechen?! Der Meister wird darüber nicht sehr erfreut sein! Nutzt diese Verschnaufpause und bereitet euch auf das Kommende vor... Wenn wir zurückkehren, wird im Sog unserer Armeen nichts außer Leichen und brennende Erde übrig bleiben.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13165', 'deDE', 'Ihr glaubt vielleicht gewonnen zu haben, Würmer, doch gegen die Macht der Geißel kann es keinen Sieg der Lebenden geben! Das war nicht das Letzte, was ihr von uns gehört habt!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13169', 'deDE', 'Glaubt ihr wirklich, eure Bemühungen würden Früchte tragen? Hah! Die Anzahl unserer Kämpfer ist grenzenlos!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13170', 'deDE', 'Auch wenn ihr euch auf dem Schlachtfeld bewährt... werdet ihr niemals den Sieg gegen die Geißel davontragen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13172', 'deDE', 'Ihr habt also ein paar schäbige Landstriche zurückerobert. Lächerlich! Schon in Kürze, werden sie wieder unser sein!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13173', 'deDE', 'Glaubt ihr tatsächlich, Fortschritte zu machen? Niemals werdet ihr die Geißel besiegen! Diese vielen kleinen Siege sind nichts weiter, als ein Komplott, um Euch in Sicherheit zu wiegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13174', 'deDE', 'Glaubt ihr tatsächlich, ihr könntet den Meister mit euren kleinen Spielchen beeindrucken? Niemals könnt ihr uns besiegen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13175', 'deDE', 'Ihr Narren! Kämpft nur weiter, wenn es das ist, was ihr wollt! Mit jeder Schlacht fallen mehr von euch und die Reihen der Untoten füllen sich weiter.', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13176', 'deDE', 'Die Bemühungen der Lebenden haben uns noch nie beeindruckt. Die Zahl eurer Truppen ist beschränkt und schwindet mit jeder weiteren Schlacht. Ihr werdet nicht mehr lange genug Leben, um das Ende dieses Konflikts zu erleben!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13177', 'deDE', 'Glaubt ihr ernsthaft daran, gegen die Legionen von Kel\'Thuzad bestehen zu können? Nicht die Armeen der Lebenden, noch irgendeine Macht über die ihr möglicherweise verfügt, kann uns aufhalten!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13178', 'deDE', 'Dieser andauernde, störende Widerstand ist sinnlos, ihr Maden! Am Ende wartet nur der Tod auf euch!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13253', 'deDE', 'Ich würde gerne den geheimen Code eingeben, um meinen Zerglingbegleiter zu erhalten.', 'Ich würde gerne den geheimen Code eingeben, um meinen Zerglingbegleiter zu erhalten.', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13254', 'deDE', 'Seid gegrüßt! Ihr kommt mir irgendwie bekannt vor... Kennen wir uns von irgendwoher?$B$BNun, wenn wir uns schon einmal getroffen haben..., dann habt Ihr ja sicherlich einen geheimen Code erhalten. Sagt mir den Code und ich werde Euch ein Geschenk geben!', 'Seid gegrüßt! Ihr kommt mir irgendwie bekannt vor... Kennen wir uns von irgendwoher?$B$BNun, wenn wir uns schon einmal getroffen haben..., dann habt Ihr ja sicherlich einen geheimen Code erhalten. Sagt mir den Code und ich werde Euch ein Geschenk geben!', '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13405', 'deDE', '%s nimmt die Energien des Totems in sich auf und wirft sie als Antimagieimpuls zurück.', null, '31882');
