INSERT INTO `broadcast_text_locale` VALUES ('13431', 'deDE', 'Eine riesige, wurmgleiche Kreatur ist gerade eben aus dem Boden nahe des Ashischwarmbaus hervorgebrochen!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13432', 'deDE', 'Seht, da! Beim Bau des Regalschwarms! Ist das... ein Sandwurm?!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13433', 'deDE', 'Seid auf der Hut! Es gibt Berichte über einen gigantischen Wurm in der Nähe des Zoraschwarmbaus!', null, '31882');
INSERT INTO `broadcast_text_locale` VALUES ('13434', 'deDE', 'Etwas rührt sich beim Südwindposten! Es... es ist eine dieser Sandwurmkreaturen! Gebt Acht!', null, '31882');
