UPDATE creature_ai_texts SET content_loc3='Unsere Aufgabe ist es, so viel von seinem Kanonenfutter zu beseitigen wie möglich, während wir die Spezialisten unterstützen, die von den höheren Mächten gesandt wurden.' WHERE entry=-398;
