UPDATE dbscript_string SET content_loc3='Es ist lange her, Bethor, mein Freund.' WHERE entry=2000000208;
UPDATE dbscript_string SET content_loc3='Wenn wir Zeit haben, müssen wir ausführlicher reden. Wir haben viel zu besprechen.' WHERE entry=2000000209;
