UPDATE dbscript_string SET content_loc3='Ahhh... mir läuft es eiskalt den Rücken herunter.' WHERE entry=2000000484;
UPDATE dbscript_string SET content_loc3='Ich hoffe, dass sie das Licht erblicken werden.' WHERE entry=2000000485;
