UPDATE dbscript_string SET content_loc3='Einwohner von Sturmwind! Bürger der Allianz! Euer König spricht!' WHERE entry=2000000563;
UPDATE dbscript_string SET content_loc3='Der heutige Tag bringt die erste von vielen Niederlagen der Geißel! Todesritter, einst im Dienste des Lichkönigs, haben sich von seinem Griff befreit und eine neue Allianz gegen seine Tyrannei gebildet!' WHERE entry=2000000564;
UPDATE dbscript_string SET content_loc3='Ihr werdet diese einstigen Helden der Allianz willkommen heißen und sie mit demselben Respekt behandeln, den Ihr auch jedem anderen Verbündeten Sturmwinds entgegenbringen würdet!' WHERE entry=2000000565;
UPDATE dbscript_string SET content_loc3='Ruhm der Allianz!' WHERE entry=2000000566;
