UPDATE dbscript_string SET content_loc3='Ich werde dieser Spielchen langsam müde, Mensch. Erzählt mir vom \"Scharlachroten Morgen\" oder ich reiße Euch das Rückgrat mit bloßen Händen raus!' WHERE entry=2000001468;
