UPDATE dbscript_string SET content_loc3='Scharfseher! Ich bin so froh, Euch wiederzusehen!' WHERE entry=2000001510;
UPDATE dbscript_string SET content_loc3='Ich bin zu den Lebenden zurückgekehrt.' WHERE entry=2000001511;
UPDATE dbscript_string SET content_loc3='Und nun muss ich ruhen. Habt Dank für alles, was Ihr getan habt.' WHERE entry=2000001512;
UPDATE dbscript_string SET content_loc3='Ich freue mich darauf, die Ausbildung fortzusetzen, wenn Ihr bereit seid.' WHERE entry=2000001513;
UPDATE dbscript_string SET content_loc3='Scharfseher, es ist gut, zu sehen, dass Ihr noch lebt.' WHERE entry=2000001514;
UPDATE dbscript_string SET content_loc3='Scharfseher, Ihr lebt!' WHERE entry=2000001515;
