UPDATE dbscript_string SET content_loc3='Ich kann Bescheres zusammenpantschen, Jaschon.' WHERE entry=2000001789;
UPDATE dbscript_string SET content_loc3='Ihr scholltet... Ihr solltet Petrov diesch Zeuch für seine Bomben schicken!' WHERE entry=2000001790;
UPDATE dbscript_string SET content_loc3='Das is\'n hübschesch Kleid, Scheleschte. Ihr seid sooo hübsch. Ich liebe Euch, Leude... Hicks!' WHERE entry=2000001791;
UPDATE dbscript_string SET content_loc3='Ich denke, dass Ihr genug habt.' WHERE entry=2000001792;
