UPDATE dbscript_string SET content_loc3='Opus will etwas Gold von Cuergo... mit Wurm.' WHERE entry=2000005001;
UPDATE dbscript_string SET content_loc3='Bijou ist verloren! KRÄCHZ! *hust*' WHERE entry=2000005002;
UPDATE dbscript_string SET content_loc3='Kibler wird derzeit von der G.E.T.A. geprüft.' WHERE entry=2000005003;
UPDATE dbscript_string SET content_loc3='Bijou weiß, wo die Schätze versteckt sind.' WHERE entry=2000005004;
UPDATE dbscript_string SET content_loc3='Großdrachen kontrollieren Orcs!? KRÄCHZ!' WHERE entry=2000005005;
UPDATE dbscript_string SET content_loc3='Alle gehen zur Spitze, aber keiner kommt zurück... genau wie Bijou.' WHERE entry=2000005006;
UPDATE dbscript_string SET content_loc3='Fragt Kibler nach dem Schatz! KRÄCHZ!' WHERE entry=2000005007;
UPDATE dbscript_string SET content_loc3='KRÄCHZ!! *hust* *hust* Ich muss mit dem Rauchen aufhören!' WHERE entry=2000005008;
UPDATE dbscript_string SET content_loc3='Gut gemacht, Soldaten! Rührt euch!' WHERE entry=2000005009;
UPDATE dbscript_string SET content_loc3='Zug zug! Freut sich zu helfen!' WHERE entry=2000005010;
UPDATE dbscript_string SET content_loc3='Ja auf der Stelle Mama!' WHERE entry=2000005011;
UPDATE dbscript_string SET content_loc3='Geheimnisvolle Hufabdrücke, versengte Schilde, seltsame Abzeichen... wie passt das nur alles zusammen?' WHERE entry=2000005012;
UPDATE dbscript_string SET content_loc3='Ich glaube kaum, dass Drazzit jemanden finden wird, der seine verdammten Raptoren jagt. Ich meine, er hat die Belohnung schon zweimal erhöht...' WHERE entry=2000005013;
UPDATE dbscript_string SET content_loc3='Aber es ist das Einzige, in das Brogg hineinpasst! Ihr Goblins baut viel zu kleine Unterkünfte!' WHERE entry=2000005014;
UPDATE dbscript_string SET content_loc3='Wir haben schon oft genug darüber gesprochen, Brogg. Ihr könnt nicht in diesem Gebäude bleiben. Sicher, es bietet Euch genug Platz, doch es passt sonst niemand mehr hinein!' WHERE entry=2000005015;
UPDATE dbscript_string SET content_loc3='Ja, wir haben gesagt, dass Ihr hier bleiben könnt, aber dieses Gebäude ist unser Hauptquartier! All unsere Unterlagen und Pläne werden hier aufbewahrt!' WHERE entry=2000005016;
UPDATE dbscript_string SET content_loc3='Ich widerspreche nur ungern, aber wenn Ihr mich fragt, seid ihr Oger einfach nur zu groß! Ich sehe schon, heute werde ich Euch wohl nicht zu einem Umzug bewegen können. Aber glaubt nicht, dass Ihr auf ewig hier wohnen könnt!' WHERE entry=2000005017;
UPDATE dbscript_string SET content_loc3='Ah, das muss er jetzt sein... nein?' WHERE entry=2000005018;
UPDATE dbscript_string SET content_loc3='Ihr solltet zurücktreten. Das Ausnehmen von Fischen ist eine schmutzige Arbeit.' WHERE entry=2000005019;
UPDATE dbscript_string SET content_loc3='Es ist gut zu sehen, dass Ihr Theramore mit Stolz verteidigt, Freund.' WHERE entry=2000005020;
UPDATE dbscript_string SET content_loc3='Lasst uns zum Gasthaus gehen, wenn Ihr Dienstschluss habt. Ich gebe eine Runde aus und wir können uns über die gute alte Zeit unterhalten.' WHERE entry=2000005021;
UPDATE dbscript_string SET content_loc3='Sehnt Ihr Euch nicht auch die gute alte Zeit herbei? Ich habe da ein paar Freunde, die uns bei der Erfüllung dieses Wunsches behilflich sein können...' WHERE entry=2000005022;
UPDATE dbscript_string SET content_loc3='Jeden Gegenstand, den ich verkaufe, habe ich selbst erlangt. Ihr habt meine persönliche Garantie auf Echtheit.' WHERE entry=2000005023;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005024; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Gewinne! Holt Eure Gewinne direkt hier ab! Kommt her und löst Eure Gewinnlose des Dunkelmond-Jahrmarkts gegen wertvolle Gewinne ein!' WHERE entry=2000005025;
UPDATE dbscript_string SET content_loc3='Kommt sprecht mit mir, und was einst verschwommen war soll kristallklar werden.' WHERE entry=2000005026;
UPDATE dbscript_string SET content_loc3='Die Macht des Lichkönigs wird stärker. Es ist von äußerster Wichtigkeit, dass unser Zeitplan seinem Vorhaben entspricht!' WHERE entry=2000005027;
UPDATE dbscript_string SET content_loc3='Morgen werden wir mit dem Training unserer vielversprechenden Drachen beginnen, also vergesst Eure Kauspielzeuge nicht.' WHERE entry=2000005028;
UPDATE dbscript_string SET content_loc3='Unser ältesten Gelege von Drachen sind noch immer weit von der Reife entfernt, aber mit Geduld und Studien sind wir überzeugt, dass die Dachen bald bereit sein werden.' WHERE entry=2000005029;
UPDATE dbscript_string SET content_loc3='Wenn Ihr wartet um mich zu sehen, dann wird vielleicht nichts übrig sein!' WHERE entry=2000005030;
UPDATE dbscript_string SET content_loc3='Ich komme aus dem Land weit im Süden... wo die Frauen umtreiben und die Männer sich zerreisen.' WHERE entry=2000005031;
UPDATE dbscript_string SET content_loc3='...Und dann sagte der Taure, \"33 ZENTIMETER\"!' WHERE entry=2000005032;
UPDATE dbscript_string SET content_loc3='Herbei, herbei! Willkommen auf dem Dunkelmond-Jahrmarkt! Ist euch nach Abenteuern zumute? Seid ihr auf der Suche nach mystischen, exotischen Schätzen? Dann sucht nicht weiter, denn wir haben alles was ihr begehrt! Taucht ein in eine Welt voller Wunder und erlebt was der Jahrmarkt zu bieten hat! Wir gastieren hier noch die ganze Woche über. Vergesst also nicht euren Freunden und Lieben Bescheid zu sagen!' WHERE entry=2000005033;
UPDATE dbscript_string SET content_loc3='Der einzigartige Dunkelmond-Jahrmarkt heißt euch willkommen! Hier findet ihr alles was das Herz begehrt... herzhaftes Essen, gutes Bier, exotische Artefakte und Tränke! Vielleicht sogar ein Blick in die Zukunft! Fantastische Preise und Vergnügen ohne Ende erwarten euch! Vergesst nicht eure Dunkelmond-Gewinnlose bei Gelvas Rußgatter einzulösen! Mit nur fünf Losen oder mehr seid ihr dabei und könnt zwischen den wunderbarsten Preisen in ganz Azeroth wählen. Bei uns ist jeder ein Gewinner!' WHERE entry=2000005034;
UPDATE dbscript_string SET content_loc3='Er entkommt!' WHERE entry=2000005035;
UPDATE dbscript_string SET content_loc3='Bitte! Ich brauche etwas... einen Manakristall.... einen Splitter... irgendetwas! Die Schmerzen sind unerträglich!' WHERE entry=2000005036;
UPDATE dbscript_string SET content_loc3='Ich werde wohl Jagd auf diesen Getriebenen machen, jetzt, da eine Belohnung auf seinen Kopf aussteht.' WHERE entry=2000005037;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005038; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Der Wind berichtet von den Bewegungen unserer Gegner, Ashyen. Die Naga werden erneut angreifen.' WHERE entry=2000005039;
UPDATE dbscript_string SET content_loc3='Sehr gut. Wenn diese Entscheidung getroffen wurde, werde ich bereit sein. Lasst uns hoffen, dass es nicht zu spät sein wird.' WHERE entry=2000005040;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005041;
UPDATE dbscript_string SET content_loc3='Es gibt eine neue Bekanntmachung! Abenteurer und Helden, versammelt Euch um den Aushang!' WHERE entry=2000005042;
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005043; -- ungenutzt
-- UPDATE dbscript_string SET content_loc3='' WHERE entry=2000005044; -- ungenutzt
UPDATE dbscript_string SET content_loc3='Fragt mich nicht, wo ich es her habe...' WHERE entry=2000005045;
UPDATE dbscript_string SET content_loc3='Nach dieser Schicht werde ich dieser Kopfgeldsache mal nachgehen.' WHERE entry=2000005046;
UPDATE dbscript_string SET content_loc3='Ihr wollt also nach der Schicht das Kopfgeld für diesen Steckbrief sammeln?' WHERE entry=2000005047;
