UPDATE dbscript_string SET content_loc3='Ich habe keine Lust mehr, Euch nett zu fragen, Fischmann!' WHERE entry=2000005426;
UPDATE dbscript_string SET content_loc3='Wo ist die Litanei der Verdammnis? Ich weiß, dass sich ein Drittel davon in diesem Dorf befindet.' WHERE entry=2000005427;
UPDATE dbscript_string SET content_loc3='Wasss... wovon ssssprecht Ihr, Weichhaut?' WHERE entry=2000005428;
UPDATE dbscript_string SET content_loc3='Die Naga haben... diessse Litanei... nicht...' WHERE entry=2000005429;
UPDATE dbscript_string SET content_loc3='Nein... Natürlich nicht. Wie komme ich nur darauf. Warum solltet Ihr wohl ein Drittel des Worts der Verdammnis besitzen?' WHERE entry=2000005430;
UPDATE dbscript_string SET content_loc3='Ich bin ja so dumm. Vielleicht sollte ich Euch losketten und freilassen?' WHERE entry=2000005431;
UPDATE dbscript_string SET content_loc3='Ja... Ja... lasssst mich frei...' WHERE entry=2000005432;
UPDATE dbscript_string SET content_loc3='HALTET IHR MICH WIRKLICH FÜR SO DUMM, NAGA? JETZT FÜHLT DEN SCHMERZ!' WHERE entry=2000005433;
UPDATE dbscript_string SET content_loc3='%s spuckt verächtlich auf den Naga der Echsennarbe.' WHERE entry=2000005434;
UPDATE dbscript_string SET content_loc3='STEH AUF DU STINKENDES STÜCK DRECK! STEH AUF, BEVOR ICH DICH VERNICHTE!' WHERE entry=2000005435;
UPDATE dbscript_string SET content_loc3='%s kommt wieder zu sich.' WHERE entry=2000005436;
UPDATE dbscript_string SET content_loc3='Ich höre...' WHERE entry=2000005437;
UPDATE dbscript_string SET content_loc3='Schlagt den Kommandanten der Echsssennarbe nicht länger, Orc! Ich werde reden! Die Litanei... ist in einer der Truhen in der Echsssennarbe versssteckt... Die Naga dort haben die Schlüssssel...' WHERE entry=2000005438;
UPDATE dbscript_string SET content_loc3='ECHSSSENNARBE!' WHERE entry=2000005439;
