UPDATE dbscript_string SET content_loc3='Versteckt Euch! Versteckt Euch, bevor sie Euch sehen können! Zurück!' WHERE entry=2000005478;
UPDATE dbscript_string SET content_loc3='Halt!' WHERE entry=2000005479;
UPDATE dbscript_string SET content_loc3='Was ist, mein Herr?' WHERE entry=2000005480;
UPDATE dbscript_string SET content_loc3='Zarath, Ihr müsst sofort zum Schwarzen Tempel zurückkehren! Ich... Ich habe wie es scheint Fürst Illidans Befehle verlegt. Schnell!' WHERE entry=2000005481;
UPDATE dbscript_string SET content_loc3='Mein Herr, Ihr erwartet doch nicht, dass ich Euch unbewacht lasse. Fürst Illidan würde mich köpfen lassen, wenn Euch etwas zustieße.' WHERE entry=2000005482;
UPDATE dbscript_string SET content_loc3='Zarath, ich bin sehr wohl in der Lage, die Straße zur Stätte der Mondfinsternis zu bewältigen. Wenn wir den Brief nicht abliefern, wird Fürst Illidan uns beide töten lassen! Und nun fort mit Euch!' WHERE entry=2000005483;
UPDATE dbscript_string SET content_loc3='Wie Ihr wünscht, mein Herr.' WHERE entry=2000005484;
UPDATE dbscript_string SET content_loc3='Ah, süße, süße Blutdistel... Wahrscheinlich hat sie einer der verdammten Abhängigen von der Stätte der Mondfinsternis verloren.' WHERE entry=2000005485;
UPDATE dbscript_string SET content_loc3='Ihr Verlust ist mein Gewinn...' WHERE entry=2000005486;
UPDATE dbscript_string SET content_loc3='Es ist an der Zeit zu handeln. Vernichtet Icarius.' WHERE entry=2000005487;
UPDATE dbscript_string SET content_loc3='%s verstaut das Blutdistelbündel in seiner Robe.' WHERE entry=2000005488;
UPDATE dbscript_string SET content_loc3='Tretet zurück, $N. Die Geister könnten um sich schlagen.' WHERE entry=2000005489;
UPDATE dbscript_string SET content_loc3='Warum habt Ihr uns gefangen?' WHERE entry=2000005490;
UPDATE dbscript_string SET content_loc3='Was hofft Ihr dadurch zu erreichen?' WHERE entry=2000005491;
UPDATE dbscript_string SET content_loc3='%s verneigt sich.' WHERE entry=2000005492;
UPDATE dbscript_string SET content_loc3='Wir wollen Euch helfen, Geist. Wir suchen Eure Weisheit.' WHERE entry=2000005493;
UPDATE dbscript_string SET content_loc3='Weisheit? Es war Euer Volk, das den unauslöschlichen Zorn von Cyrukh erregte.' WHERE entry=2000005494;
UPDATE dbscript_string SET content_loc3='Warum sollten wir Euch helfen? Die Welt liegt in Trümmern. Zerstört.' WHERE entry=2000005495;
UPDATE dbscript_string SET content_loc3='%s nickt.' WHERE entry=2000005496;
UPDATE dbscript_string SET content_loc3='Ihr habt Recht, Geist. Es war mein Volk, welches Euch dies angetan hat. Erlaubt mir, es wiedergutzumachen.' WHERE entry=2000005497;
UPDATE dbscript_string SET content_loc3='Bitte sagt uns: Wer oder was ist Cyrukh?' WHERE entry=2000005498;
UPDATE dbscript_string SET content_loc3='Narr! Cyrukh der Feuerlord! Als die Verbindung durchtrennt wurde, wachte Cyrukh auf. Es sind die Tränen von Cyrukh, die dieses Land mit geschmolzenem Stein füllen.' WHERE entry=2000005499;
UPDATE dbscript_string SET content_loc3='Der... der Feuerlord? Ein Elementarlord wurde empor gerufen? Natürlich... Gul\'dan, Ihr wart ein verdammter Narr!' WHERE entry=2000005500;
UPDATE dbscript_string SET content_loc3='Er ist verrückt. Die Zersplitterung hat ihn in den Wahnsinn getrieben. Es gibt keine Ruhe für die Elemente.' WHERE entry=2000005501;
UPDATE dbscript_string SET content_loc3='Es muss einen Weg geben.' WHERE entry=2000005502;
UPDATE dbscript_string SET content_loc3='Ja, einen Weg. Zerstört Cyrukh. Zerstört einen Elementarlord.' WHERE entry=2000005503;
UPDATE dbscript_string SET content_loc3='Zweifelt nicht an mir und meinen Kameraden, Geist. Sagt uns, wie wir den Feuerlord beschwören können. Meine Verbündeten werden ihn zerstören.' WHERE entry=2000005504;
UPDATE dbscript_string SET content_loc3='Ihr müsst die Worte sagen...' WHERE entry=2000005505;
UPDATE dbscript_string SET content_loc3='Ja, ja... die Worte. Die Worte der Verdammnis. Die Worte des Dunklen. Eures Meisters.' WHERE entry=2000005506;
UPDATE dbscript_string SET content_loc3='Ja... Gul\'dan...' WHERE entry=2000005507;
UPDATE dbscript_string SET content_loc3='Wir werden diese Worte finden. Wir werden Euren Meister beschwören und ihn zerstören.' WHERE entry=2000005508;
UPDATE dbscript_string SET content_loc3='Gerede... nur Gerede. Lasst uns frei, Sterblicher.' WHERE entry=2000005509;
UPDATE dbscript_string SET content_loc3='Und nun zur Wiederentdeckung der verlorenen Worte!' WHERE entry=2000005510;
UPDATE dbscript_string SET content_loc3='...es soll ein Fond für den Kauf von Haargel eingerichtet werden? Vielen Dank, aber ich mag meine Haare auch so!' WHERE entry=2000005511;
UPDATE dbscript_string SET content_loc3='...vergnügt Euch als Deserteur im Kinderplanschbecken oder bei der Arbeit im Hundesalon? Wie lächerlich!' WHERE entry=2000005512;
UPDATE dbscript_string SET content_loc3='Ich würde mich einer Sache wie dieser niemals anschließen. Behaltet das mal lieber. Es könnte Euch auf dem stillen Örtchen noch von Nutzen sein...' WHERE entry=2000005513;
UPDATE dbscript_string SET content_loc3='...die Deserteure wünschen sich ein Theramore, in dem die Bürger die Freiheit besitzen, jederzeit und überall zu jonglieren? Was ist das für ein Quatsch?' WHERE entry=2000005514;
UPDATE dbscript_string SET content_loc3='Das ist das witzigste was ich seit langem gelesen habe. Ihr versüßt mir den Tag!' WHERE entry=2000005515;
UPDATE dbscript_string SET content_loc3='Das... das ist ein Scherz, nicht wahr?' WHERE entry=2000005516;
UPDATE dbscript_string SET content_loc3='Hey, danke.' WHERE entry=2000005517;
UPDATE dbscript_string SET content_loc3='%s liest den Handzettel.' WHERE entry=2000005518;
UPDATE dbscript_string SET content_loc3='...bekommt Unterwäsche der Deserteure jetzt zum halben Preis? Was ist das für ein Mist?' WHERE entry=2000005519;
UPDATE dbscript_string SET content_loc3='Was für ein Haufen Irrer! Glaubt Ihr denn wirklich an das?' WHERE entry=2000005520;
UPDATE dbscript_string SET content_loc3='Seid bedankt, $R! Euren Dienst an den Mag\'har werden wir nicht vergessen.' WHERE entry=2000005521;
UPDATE dbscript_string SET content_loc3='Vielen Dank, $GHeld:Heldin;.' WHERE entry=2000005522;
UPDATE dbscript_string SET content_loc3='Die Geister mögen über Euch wachen, $R!' WHERE entry=2000005523;
UPDATE dbscript_string SET content_loc3='Tod den Ogern!' WHERE entry=2000005524;
UPDATE dbscript_string SET content_loc3='Habt Dank dafür, dass Ihr uns befreit habt, Sterbliche. Seid auf der Hut: der blaue Schwarm weiß von Eurer Anweisenheit und ist gewarnt. In diesem Moment entsendet Malygos Varos Wolkenwanderer und seine Ringwächter, um das Oculus zu verteidigen. Ihr werdet unsere Hilfe brauchen, um eine Chance zu haben.' WHERE entry=2000005525;
UPDATE dbscript_string SET content_loc3='Eindringlinge, euer Sieg währt nur kurz. Ich bin Kommandant Varos Wolkenwanderer. Meine Drachen kontrollieren den Himmel und schützen diese Energiezufuhr. Ich persönlich werde dafür sorgen, dass das Oculus euch nicht in die Hände fällt!' WHERE entry=2000005526;
UPDATE dbscript_string SET content_loc3='Der schwindlerische Magierlord Urom schützt den dritten Ring. Es wird so aussehen, als sei er allein und wehrlos, doch lasst Euch durch den Schein nicht trügen! Urom ist ein mächtiger Beschwörer, der eine ganze Menagerie geisterhafter Kreaturen befehligt. Ihr findet ihn oben.' WHERE entry=2000005527;
UPDATE dbscript_string SET content_loc3='Eure größte Herausforderung liegt vor Euch. Leywächter Eregos ist ein blauer Drache mit gewaltigen Kräften. Ihr könnt ihn über dem höchsten Ring fliegen sehen.' WHERE entry=2000005528;
