UPDATE dbscript_string SET content_loc3='Meinen Beinen gehts schon besser, das Gegengift wirkt schon. Wenn Ihr mich jetzt entschuldigen würdet, ich muss Meldung an meine Vorgesetzten machen über das was hier passiert ist.' WHERE entry=2000005630;
UPDATE dbscript_string SET content_loc3='Bitte sprecht mit Schildwache Onaeya in Maestras Posten im Eschental, sie wird sich darum kümmern, das Ihr angemessen entlohnt werdet für Eure heutige Tapferkeit.' WHERE entry=2000005631;
